(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["docs/algorithm/complexity"],{cHO4:function(n,p,i){var u=i("Rxnk"),s=i("Oiii"),t=u(s),l='<h1 id="\u590d\u6742\u5ea6">\u590d\u6742\u5ea6</h1> <h2 id="1-\u4e3a\u4ec0\u4e48\u9700\u8981\u590d\u6742\u5ea6\u5206\u6790">1 \u4e3a\u4ec0\u4e48\u9700\u8981\u590d\u6742\u5ea6\u5206\u6790</h2> <p>\u628a\u4ee3\u7801\u8dd1\u4e00\u904d\uff0c\u901a\u8fc7\u7edf\u8ba1\u3001\u76d1\u63a7\uff0c\u5c31\u80fd\u5f97\u5230\u7b97\u6cd5\u6267\u884c\u7684\u65f6\u95f4\u548c\u5360\u7528\u7684\u5185\u5b58\u5927\u5c0f\u3002\u4e3a\u4ec0\u4e48\u8fd8\u8981\u505a\u65f6\u95f4\u3001\u7a7a\u95f4\u590d\u6742\u5ea6\u5206\u6790\u5462\uff1f\u8fd9\u79cd\u5206\u6790\u65b9\u6cd5\u80fd\u6bd4\u5b9e\u5b9e\u5728\u5728\u8dd1\u4e00\u904d\u5f97\u5230\u7684\u6570\u636e\u66f4\u51c6\u786e\u5417\uff1f</p> <p>\u9996\u5148\uff0c\u8fd9\u79cd\u8bc4\u4f30\u7b97\u6cd5\u6267\u884c\u6548\u7387\u7684\u65b9\u6cd5\u662f\u6b63\u786e\u7684\u3002\u5f88\u591a\u6570\u636e\u7ed3\u6784\u548c\u7b97\u6cd5\u4e66\u7c4d\u8fd8\u7ed9\u8fd9\u79cd\u65b9\u6cd5\u8d77\u4e86\u4e00\u4e2a\u540d\u5b57\uff0c\u53eb\u4e8b\u540e\u7edf\u8ba1\u6cd5\u3002\u4f46\u662f\uff0c\u8fd9\u79cd\u7edf\u8ba1\u65b9\u6cd5\u6709\u975e\u5e38\u5927\u7684\u5c40\u9650\u6027\u3002</p> <p><strong>1 \u6d4b\u8bd5\u7ed3\u679c\u975e\u5e38\u4f9d\u8d56\u6d4b\u8bd5\u73af\u5883</strong></p> <p>\u6d4b\u8bd5\u73af\u5883\u4e2d\u786c\u4ef6\u7684\u4e0d\u540c\u4f1a\u5bf9\u6d4b\u8bd5\u7ed3\u679c\u6709\u5f88\u5927\u7684\u5f71\u54cd\u3002\u6bd4\u5982\uff0c\u6211\u4eec\u62ff\u540c\u6837\u4e00\u6bb5\u4ee3\u7801\uff0c\u5206\u522b\u7528 Intel Core i9 \u5904\u7406\u5668\u548c Intel Core i3 \u5904\u7406\u5668\u6765\u8fd0\u884c\uff0c\u4e0d\u7528\u8bf4\uff0ci9 \u5904\u7406\u5668\u8981\u6bd4 i3 \u5904\u7406\u5668\u6267\u884c\u7684\u901f\u5ea6\u5feb\u5f88\u591a\u3002\u8fd8\u6709\uff0c\u6bd4\u5982\u539f\u672c\u5728\u8fd9\u53f0\u673a\u5668\u4e0a a \u4ee3\u7801\u6267\u884c\u7684\u901f\u5ea6\u6bd4 b \u4ee3\u7801\u8981\u5feb\uff0c\u7b49\u6211\u4eec\u6362\u5230\u53e6\u4e00\u53f0\u673a\u5668\u4e0a\u65f6\uff0c\u53ef\u80fd\u4f1a\u6709\u622a\u7136\u76f8\u53cd\u7684\u7ed3\u679c\u3002</p> <p><strong>2 \u6d4b\u8bd5\u7ed3\u679c\u53d7\u6570\u636e\u89c4\u6a21\u7684\u5f71\u54cd\u5f88\u5927</strong></p> <p>\u5bf9\u540c\u4e00\u4e2a\u6392\u5e8f\u7b97\u6cd5\uff0c\u5f85\u6392\u5e8f\u6570\u636e\u7684\u6709\u5e8f\u5ea6\u4e0d\u4e00\u6837\uff0c\u6392\u5e8f\u7684\u6267\u884c\u65f6\u95f4\u5c31\u4f1a\u6709\u5f88\u5927\u7684\u5dee\u522b\u3002\u6781\u7aef\u60c5\u51b5\u4e0b\uff0c\u5982\u679c\u6570\u636e\u5df2\u7ecf\u662f\u6709\u5e8f\u7684\uff0c\u90a3\u6392\u5e8f\u7b97\u6cd5\u4e0d\u9700\u8981\u505a\u4efb\u4f55\u64cd\u4f5c\uff0c\u6267\u884c\u65f6\u95f4\u5c31\u4f1a\u975e\u5e38\u77ed\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u5982\u679c\u6d4b\u8bd5\u6570\u636e\u89c4\u6a21\u592a\u5c0f\uff0c\u6d4b\u8bd5\u7ed3\u679c\u53ef\u80fd\u65e0\u6cd5\u771f\u5b9e\u5730\u53cd\u5e94\u7b97\u6cd5\u7684\u6027\u80fd\u3002\u6bd4\u5982\uff0c\u5bf9\u4e8e\u5c0f\u89c4\u6a21\u7684\u6570\u636e\u6392\u5e8f\uff0c\u63d2\u5165\u6392\u5e8f\u53ef\u80fd\u53cd\u5012\u4f1a\u6bd4\u5feb\u901f\u6392\u5e8f\u8981\u5feb\uff01</p> <p>\u6240\u4ee5\uff0c\u6211\u4eec\u9700\u8981\u4e00\u4e2a\u4e0d\u7528\u5177\u4f53\u7684\u6d4b\u8bd5\u6570\u636e\u6765\u6d4b\u8bd5\uff0c\u5c31\u53ef\u4ee5\u7c97\u7565\u5730\u4f30\u8ba1\u7b97\u6cd5\u7684\u6267\u884c\u6548\u7387\u7684\u65b9\u6cd5\u3002</p> <h2 id="2-\u5927-o-\u590d\u6742\u5ea6\u8868\u793a\u6cd5">2 \u5927 O \u590d\u6742\u5ea6\u8868\u793a\u6cd5</h2> <p>\u7b97\u6cd5\u7684\u6267\u884c\u6548\u7387\uff0c\u7c97\u7565\u5730\u8bb2\uff0c\u5c31\u662f\u7b97\u6cd5\u4ee3\u7801\u6267\u884c\u7684\u65f6\u95f4\u3002\u4f46\u662f\uff0c\u5982\u4f55\u5728\u4e0d\u8fd0\u884c\u4ee3\u7801\u7684\u60c5\u51b5\u4e0b\uff0c\u7528\u201c\u8089\u773c\u201d\u5f97\u5230\u4e00\u6bb5\u4ee3\u7801\u7684\u6267\u884c\u65f6\u95f4\u5462\uff1f</p> <p>\u5047\u8bbe\u6bcf\u4e2a\u8bed\u53e5\u7684\u6267\u884c\u65f6\u95f4\u662f unit_time</p> <p>\u4ee3\u7801 1\uff0c\u6c42 1,2,3\u2026n \u7684\u7d2f\u52a0\u548c\uff1a</p> <pre><code class="language-java">int cal(int n) {\n  int sum = 0;\n  int i = 1;\n  for (; i &lt;= n; ++i) {\n    sum = sum + i;\n  }\n  return sum;\n}</code></pre> <ul> <li>\u7b2c 2\u30013 \u884c\u4ee3\u7801\u5206\u522b\u9700\u8981 1 \u4e2a unit_time \u7684\u6267\u884c\u65f6\u95f4</li> <li>\u7b2c 4\u30015 \u884c\u90fd\u8fd0\u884c\u4e86 n \u904d\uff0c\u6240\u4ee5\u9700\u8981 2n*unit_time \u7684\u6267\u884c\u65f6\u95f4</li> </ul> <p>\u8fd9\u6bb5\u4ee3\u7801\u603b\u7684\u6267\u884c\u65f6\u95f4 T(n) = (2n+2)*unit_time</p> <p>\u4ee3\u7801 2\uff1a</p> <pre><code class="language-java">int cal(int n) {\n  int sum = 0;\n  int i = 1;\n  int j = 1;\n  for (; i &lt;= n; ++i) {\n    j = 1;\n    for (; j &lt;= n; ++j) {\n      sum = sum + i * j;\n    }\n  }\n}</code></pre> <ul> <li>\u7b2c 2\u30013\u30014 \u884c\u4ee3\u7801\uff0c\u6bcf\u884c\u90fd\u9700\u8981 1 \u4e2a unit_time \u7684\u6267\u884c\u65f6\u95f4</li> <li>\u7b2c 5\u30016 \u884c\u4ee3\u7801\u5faa\u73af\u6267\u884c\u4e86 n \u904d\uff0c\u9700\u8981 2n * unit_time \u7684\u6267\u884c\u65f6\u95f4</li> <li>\u7b2c 7\u30018 \u884c\u4ee3\u7801\u5faa\u73af\u6267\u884c\u4e86 n<sup>2</sup> \u904d\uff0c\u6240\u4ee5\u9700\u8981 2n<sup>2</sup> * unit_time \u7684\u6267\u884c\u65f6\u95f4</li> </ul> <p>\u6574\u6bb5\u4ee3\u7801\u603b\u7684\u6267\u884c\u65f6\u95f4 T(n) = (2n<sup>2</sup>+2n+3)*unit_time</p> <p>\u5c3d\u7ba1\u6211\u4eec\u4e0d\u77e5\u9053 unit_time \u7684\u5177\u4f53\u503c\uff0c\u4f46\u662f\u901a\u8fc7\u8fd9\u4e24\u6bb5\u4ee3\u7801\u6267\u884c\u65f6\u95f4\u7684\u63a8\u5bfc\u8fc7\u7a0b\uff0c\u6211\u4eec\u53ef\u4ee5\u5f97\u5230\u4e00\u4e2a\u975e\u5e38\u91cd\u8981\u7684\u89c4\u5f8b\uff0c\u90a3\u5c31\u662f\uff0c\u6240\u6709\u4ee3\u7801\u7684\u6267\u884c\u65f6\u95f4 T(n) \u4e0e\u6bcf\u884c\u4ee3\u7801\u7684\u6267\u884c\u6b21\u6570 n \u6210\u6b63\u6bd4\u3002</p> <p>\u6211\u4eec\u53ef\u4ee5\u628a\u8fd9\u4e2a\u89c4\u5f8b\u603b\u7ed3\u6210\u4e00\u4e2a\u516c\u5f0f\uff1a</p> <pre><code>T(n) = O(f(n))</code></pre><p>\u5176\u4e2d\uff0cT(n) \u6211\u4eec\u5df2\u7ecf\u8bb2\u8fc7\u4e86\uff0c\u5b83\u8868\u793a\u4ee3\u7801\u6267\u884c\u7684\u65f6\u95f4\uff1bn \u8868\u793a\u6570\u636e\u89c4\u6a21\u7684\u5927\u5c0f\uff1bf(n) \u8868\u793a\u6bcf\u884c\u4ee3\u7801\u6267\u884c\u7684\u6b21\u6570\u603b\u548c\u3002\u56e0\u4e3a\u8fd9\u662f\u4e00\u4e2a\u516c\u5f0f\uff0c\u6240\u4ee5\u7528 f(n) \u6765\u8868\u793a\u3002\u516c\u5f0f\u4e2d\u7684 O\uff0c\u8868\u793a\u4ee3\u7801\u7684\u6267\u884c\u65f6\u95f4 T(n) \u4e0e f(n) \u8868\u8fbe\u5f0f\u6210\u6b63\u6bd4\u3002</p> <p>\u6240\u4ee5\uff0c\u7b2c\u4e00\u4e2a\u4f8b\u5b50\u4e2d\u7684 T(n) = O(2n+2)\uff0c\u7b2c\u4e8c\u4e2a\u4f8b\u5b50\u4e2d\u7684 T(n) = O(2n<sup>2</sup>+2n+3)\u3002\u8fd9\u5c31\u662f\u5927 O \u65f6\u95f4\u590d\u6742\u5ea6\u8868\u793a\u6cd5\u3002\u5927 O \u65f6\u95f4\u590d\u6742\u5ea6\u5b9e\u9645\u4e0a\u5e76\u4e0d\u5177\u4f53\u8868\u793a\u4ee3\u7801\u771f\u6b63\u7684\u6267\u884c\u65f6\u95f4\uff0c\u800c\u662f\u8868\u793a\u4ee3\u7801\u6267\u884c\u65f6\u95f4\u968f\u6570\u636e\u89c4\u6a21\u589e\u957f\u7684\u53d8\u5316\u8d8b\u52bf\uff0c\u6240\u4ee5\uff0c\u4e5f\u53eb\u4f5c\u6e10\u8fdb\u65f6\u95f4\u590d\u6742\u5ea6\uff08asymptotic time complexity\uff09\uff0c\u7b80\u79f0\u65f6\u95f4\u590d\u6742\u5ea6\u3002</p> <p>\u5f53 n \u5f88\u5927\u65f6\uff0c\u4f60\u53ef\u4ee5\u628a\u5b83\u60f3\u8c61\u6210 10000\u3001100000\u3002\u800c\u516c\u5f0f\u4e2d\u7684\u4f4e\u9636\u3001\u5e38\u91cf\u3001\u7cfb\u6570\u4e09\u90e8\u5206\u5e76\u4e0d\u5de6\u53f3\u589e\u957f\u8d8b\u52bf\uff0c\u6240\u4ee5\u90fd\u53ef\u4ee5\u5ffd\u7565\u3002\u6211\u4eec\u53ea\u9700\u8981\u8bb0\u5f55\u4e00\u4e2a\u6700\u5927\u91cf\u7ea7\u5c31\u53ef\u4ee5\u4e86</p> <blockquote> <p>\u4ee3\u7801 1 <br> T(n) = (2n+2)*unit_time <br> T(n) = O(2n+2) <br> T(n) = O(n)</p> </blockquote> <blockquote> <p>\u4ee3\u7801 2 <br> T(n) = (2n<sup>2</sup>+2n+3)*unit_time <br> T(n) = O(2n<sup>2</sup>+2n+3) <br> T(n) = O(n<sup>2</sup>)</p> </blockquote> <h2 id="3-\u65f6\u95f4\u590d\u6742\u5ea6\u5206\u6790">3 \u65f6\u95f4\u590d\u6742\u5ea6\u5206\u6790</h2> <h3 id="31-\u53ea\u5173\u6ce8\u5faa\u73af\u6267\u884c\u6b21\u6570\u6700\u591a\u7684\u4e00\u6bb5\u4ee3\u7801">3.1 \u53ea\u5173\u6ce8\u5faa\u73af\u6267\u884c\u6b21\u6570\u6700\u591a\u7684\u4e00\u6bb5\u4ee3\u7801</h3> <p>\u5927 O \u8fd9\u79cd\u590d\u6742\u5ea6\u8868\u793a\u65b9\u6cd5\u53ea\u662f\u8868\u793a\u4e00\u79cd\u53d8\u5316\u8d8b\u52bf\u3002\u6211\u4eec\u901a\u5e38\u4f1a\u5ffd\u7565\u6389\u516c\u5f0f\u4e2d\u7684\u5e38\u91cf\u3001\u4f4e\u9636\u3001\u7cfb\u6570\uff0c\u53ea\u9700\u8981\u8bb0\u5f55\u4e00\u4e2a\u6700\u5927\u9636\u7684\u91cf\u7ea7\u5c31\u53ef\u4ee5\u4e86\u3002\u6240\u4ee5\uff0c\u6211\u4eec\u5728\u5206\u6790\u4e00\u4e2a\u7b97\u6cd5\u3001\u4e00\u6bb5\u4ee3\u7801\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u7684\u65f6\u5019\uff0c\u4e5f\u53ea\u5173\u6ce8\u5faa\u73af\u6267\u884c\u6b21\u6570\u6700\u591a\u7684\u90a3\u4e00\u6bb5\u4ee3\u7801\u5c31\u53ef\u4ee5\u4e86\u3002\u8fd9\u6bb5\u6838\u5fc3\u4ee3\u7801\u6267\u884c\u6b21\u6570\u7684 n \u7684\u91cf\u7ea7\uff0c\u5c31\u662f\u6574\u6bb5\u8981\u5206\u6790\u4ee3\u7801\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u3002</p> <pre><code class="language-java">int cal(int n) {\n  int sum = 0;\n  int i = 1;\n  for (; i &lt;= n; ++i) {\n    sum = sum + i;\n  }\n  return sum;\n}</code></pre> <p>\u5176\u4e2d\u7b2c 2\u30013 \u884c\u4ee3\u7801\u90fd\u662f\u5e38\u91cf\u7ea7\u7684\u6267\u884c\u65f6\u95f4\uff0c\u4e0e n \u7684\u5927\u5c0f\u65e0\u5173\uff0c\u6240\u4ee5\u5bf9\u4e8e\u590d\u6742\u5ea6\u5e76\u6ca1\u6709\u5f71\u54cd\u3002\u5faa\u73af\u6267\u884c\u6b21\u6570\u6700\u591a\u7684\u662f\u7b2c 4\u30015 \u884c\u4ee3\u7801\uff0c\u6240\u4ee5\u8fd9\u5757\u4ee3\u7801\u8981\u91cd\u70b9\u5206\u6790\u3002\u8fd9\u4e24\u884c\u4ee3\u7801\u88ab\u6267\u884c\u4e86 n \u6b21\uff0c\u6240\u4ee5\u603b\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u5c31\u662f O(n)\u3002</p> <h3 id="32-\u52a0\u6cd5\u6cd5\u5219\uff1a\u603b\u590d\u6742\u5ea6\u7b49\u4e8e\u91cf\u7ea7\u6700\u5927\u7684\u90a3\u6bb5\u4ee3\u7801\u7684\u590d\u6742\u5ea6">3.2 \u52a0\u6cd5\u6cd5\u5219\uff1a\u603b\u590d\u6742\u5ea6\u7b49\u4e8e\u91cf\u7ea7\u6700\u5927\u7684\u90a3\u6bb5\u4ee3\u7801\u7684\u590d\u6742\u5ea6</h3> <pre><code class="language-java">int cal(int n) {\n  int sum_1 = 0;\n  int p = 1;\n  for (; p &lt; 100; ++p) {\n    sum_1 = sum_1 + p;\n  }\n\n  int sum_2 = 0;\n  int q = 1;\n  for (; q &lt; n; ++q) {\n    sum_2 = sum_2 + q;\n  }\n\n  int sum_3 = 0;\n  int i = 1;\n  int j = 1;\n  for (; i &lt;= n; ++i) {\n    j = 1;\n    for (; j &lt;= n; ++j) {\n      sum_3 = sum_3 + i \\* j;\n    }\n  }\n\n  return sum_1 + sum_2 + sum_3;\n}</code></pre> <p>\u8fd9\u4e2a\u4ee3\u7801\u5206\u4e3a\u4e09\u90e8\u5206\uff0c\u5206\u522b\u662f\u6c42 sum_1\u3001sum_2\u3001sum_3\u3002\u6211\u4eec\u53ef\u4ee5\u5206\u522b\u5206\u6790\u6bcf\u4e00\u90e8\u5206\u7684\u65f6\u95f4\u590d\u6742\u5ea6\uff0c\u7136\u540e\u628a\u5b83\u4eec\u653e\u5230\u4e00\u5757\u513f\uff0c\u518d\u53d6\u4e00\u4e2a\u91cf\u7ea7\u6700\u5927\u7684\u4f5c\u4e3a\u6574\u6bb5\u4ee3\u7801\u7684\u590d\u6742\u5ea6\u3002</p> <p>\u7b2c\u4e00\u6bb5\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u662f\u591a\u5c11\u5462\uff1f\u8fd9\u6bb5\u4ee3\u7801\u5faa\u73af\u6267\u884c\u4e86 100 \u6b21\uff0c\u6240\u4ee5\u662f\u4e00\u4e2a\u5e38\u91cf\u7684\u6267\u884c\u65f6\u95f4\uff0c\u8ddf n \u7684\u89c4\u6a21\u65e0\u5173\u3002</p> <p>\u5373\u4fbf\u8fd9\u6bb5\u4ee3\u7801\u5faa\u73af 10000 \u6b21\u3001100000 \u6b21\uff0c\u53ea\u8981\u662f\u4e00\u4e2a\u5df2\u77e5\u7684\u6570\uff0c\u8ddf n \u65e0\u5173\uff0c\u7167\u6837\u4e5f\u662f\u5e38\u91cf\u7ea7\u7684\u6267\u884c\u65f6\u95f4\u3002\u5f53 n \u65e0\u9650\u5927\u7684\u65f6\u5019\uff0c\u5c31\u53ef\u4ee5\u5ffd\u7565\u3002\u5c3d\u7ba1\u5bf9\u4ee3\u7801\u7684\u6267\u884c\u65f6\u95f4\u4f1a\u6709\u5f88\u5927\u5f71\u54cd\uff0c\u4f46\u662f\u56de\u5230\u65f6\u95f4\u590d\u6742\u5ea6\u7684\u6982\u5ff5\u6765\u8bf4\uff0c\u5b83\u8868\u793a\u7684\u662f\u4e00\u4e2a\u7b97\u6cd5\u6267\u884c\u6548\u7387\u4e0e\u6570\u636e\u89c4\u6a21\u589e\u957f\u7684\u53d8\u5316\u8d8b\u52bf\uff0c\u6240\u4ee5\u4e0d\u7ba1\u5e38\u91cf\u7684\u6267\u884c\u65f6\u95f4\u591a\u5927\uff0c\u6211\u4eec\u90fd\u53ef\u4ee5\u5ffd\u7565\u6389\u3002\u56e0\u4e3a\u5b83\u672c\u8eab\u5bf9\u589e\u957f\u8d8b\u52bf\u5e76\u6ca1\u6709\u5f71\u54cd\u3002</p> <p>\u90a3\u7b2c\u4e8c\u6bb5\u4ee3\u7801\u548c\u7b2c\u4e09\u6bb5\u4ee3\u7801\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u662f O(n) \u548c O(n<sup>2</sup>)</p> <p>\u7efc\u5408\u8fd9\u4e09\u6bb5\u4ee3\u7801\u7684\u65f6\u95f4\u590d\u6742\u5ea6\uff0c\u6211\u4eec\u53d6\u5176\u4e2d\u6700\u5927\u7684\u91cf\u7ea7\u3002\u6240\u4ee5\uff0c\u6574\u6bb5\u4ee3\u7801\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u5c31\u4e3a O(n<sup>2</sup>)\u3002\u4e5f\u5c31\u662f\u8bf4\uff1a\u603b\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u5c31\u7b49\u4e8e\u91cf\u7ea7\u6700\u5927\u7684\u90a3\u6bb5\u4ee3\u7801\u7684\u65f6\u95f4\u590d\u6742\u5ea6</p> <h3 id="33-\u4e58\u6cd5\u6cd5\u5219\uff1a\u5d4c\u5957\u4ee3\u7801\u7684\u590d\u6742\u5ea6\u7b49\u4e8e\u5d4c\u5957\u5185\u5916\u4ee3\u7801\u590d\u6742\u5ea6\u7684\u4e58\u79ef">3.3 \u4e58\u6cd5\u6cd5\u5219\uff1a\u5d4c\u5957\u4ee3\u7801\u7684\u590d\u6742\u5ea6\u7b49\u4e8e\u5d4c\u5957\u5185\u5916\u4ee3\u7801\u590d\u6742\u5ea6\u7684\u4e58\u79ef</h3> <p>\u5982\u679c T1(n) = O(f(n))\uff0cT2(n) = O(g(n))</p> <p>\u90a3\u4e48 T(n) = T1(n) * T2(n) = O(f(n)) * O(g(n)) = O(f(n)*g(n))</p> <p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u5047\u8bbe T1(n) = O(n)\uff0cT2(n) = O(n<sup>2</sup>)\uff0c\u5219 T1(n) * T2(n) = O(n<sup>2</sup>)</p> <p>\u4ee3\u7801\u4e0a\uff0c\u6211\u4eec\u53ef\u4ee5\u628a\u4e58\u6cd5\u6cd5\u5219\u770b\u6210\u662f\u5d4c\u5957\u5faa\u73af\uff0c\u4f8b\u5982\uff1a</p> <pre><code class="language-java">int cal(int n) {\n  int ret = 0;\n  int i = 1;\n  for (; i &lt; n; ++i) {\n    ret = ret + f(i);\n  }\n}\n\nint f(int n) {\n  int sum = 0;\n  int i = 1;\n  for (; i &lt; n; ++i) {\n    sum = sum + i;\n  }\n  return sum;\n}</code></pre> <p>\u6211\u4eec\u5355\u72ec\u770b cal() \u51fd\u6570\u3002\u5047\u8bbe f() \u53ea\u662f\u4e00\u4e2a\u666e\u901a\u7684\u64cd\u4f5c\uff0c\u90a3\u7b2c 4 \uff5e 6 \u884c\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u5c31\u662f\uff0cT1(n) = O(n)</p> <p>\u4f46 f() \u51fd\u6570\u672c\u8eab\u4e0d\u662f\u4e00\u4e2a\u7b80\u5355\u7684\u64cd\u4f5c\uff0c\u5b83\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u662f T2(n) = O(n)</p> <p>\u6240\u4ee5\uff0c\u6574\u4e2a cal() \u51fd\u6570\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u5c31\u662f\uff0cT(n) = T1(n) * T2(n) = O(n*n) = O(n<sup>2</sup>)</p> <h2 id="4-\u51e0\u79cd\u5e38\u89c1\u65f6\u95f4\u590d\u6742\u5ea6\u5b9e\u4f8b\u5206\u6790">4 \u51e0\u79cd\u5e38\u89c1\u65f6\u95f4\u590d\u6742\u5ea6\u5b9e\u4f8b\u5206\u6790</h2> <p>\u867d\u7136\u4ee3\u7801\u5343\u5dee\u4e07\u522b\uff0c\u4f46\u662f\u5e38\u89c1\u7684\u590d\u6742\u5ea6\u91cf\u7ea7\u5e76\u4e0d\u591a</p> <ul> <li>\u5e38\u91cf\u9636 O(1)</li> <li>\u5bf9\u6570\u9636 O(log n)</li> <li>\u7ebf\u6027\u9636 O(n)</li> <li>\u7ebf\u6027\u5bf9\u6570\u9636 O(n log n)</li> <li>\u5e73\u65b9\u9636 O(n<sup>2</sup>)\u3001\u7acb\u65b9\u9636 O(n<sup>3</sup>)...k \u6b21\u65b9\u9636 O(n<sup>k</sup>)</li> <li>\u6307\u6570\u9636 O(2<sup>n</sup>)</li> <li>\u9636\u4e58\u9636 O(n!)</li> </ul> <p>\u590d\u6742\u5ea6\u91cf\u7ea7\u53ef\u4ee5\u7c97\u7565\u5730\u5206\u4e3a\u4e24\u7c7b\uff0c\u591a\u9879\u5f0f\u91cf\u7ea7\u548c\u975e\u591a\u9879\u5f0f\u91cf\u7ea7\u3002\u5176\u4e2d\uff0c\u975e\u591a\u9879\u5f0f\u91cf\u7ea7\u53ea\u6709\u4e24\u4e2a\uff1aO(2n) \u548c O(n!)\u3002</p> <p>\u5f53\u6570\u636e\u89c4\u6a21 n \u8d8a\u6765\u8d8a\u5927\u65f6\uff0c\u975e\u591a\u9879\u5f0f\u91cf\u7ea7\u7b97\u6cd5\u7684\u6267\u884c\u65f6\u95f4\u4f1a\u6025\u5267\u589e\u52a0\uff0c\u6c42\u89e3\u95ee\u9898\u7684\u6267\u884c\u65f6\u95f4\u4f1a\u65e0\u9650\u589e\u957f\u3002\u6240\u4ee5\uff0c\u975e\u591a\u9879\u5f0f\u65f6\u95f4\u590d\u6742\u5ea6\u7684\u7b97\u6cd5\u5176\u5b9e\u662f\u975e\u5e38\u4f4e\u6548\u7684\u7b97\u6cd5\u3002\u6211\u4eec\u4e3b\u8981\u6765\u770b\u51e0\u79cd\u5e38\u89c1\u7684\u591a\u9879\u5f0f\u65f6\u95f4\u590d\u6742\u5ea6\u3002</p> <h3 id="41-o1">4.1 O(1)</h3> <p>\u9996\u5148\u5fc5\u987b\u660e\u786e\u4e00\u4e2a\u6982\u5ff5\uff0cO(1) \u53ea\u662f\u5e38\u91cf\u7ea7\u65f6\u95f4\u590d\u6742\u5ea6\u7684\u4e00\u79cd\u8868\u793a\u65b9\u6cd5\uff0c\u5e76\u4e0d\u662f\u6307\u53ea\u6267\u884c\u4e86\u4e00\u884c\u4ee3\u7801\u3002\u6bd4\u5982\u8fd9\u6bb5\u4ee3\u7801\uff0c\u5373\u4fbf\u6709 3 \u884c\uff0c\u5b83\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u4e5f\u662f O(1\uff09\uff0c\u800c\u4e0d\u662f O(3)</p> <pre><code class="language-java">int i = 8;\nint j = 6;\nint sum = i + j;</code></pre> <p>\u6211\u7a0d\u5fae\u603b\u7ed3\u4e00\u4e0b\uff0c\u53ea\u8981\u4ee3\u7801\u7684\u6267\u884c\u65f6\u95f4\u4e0d\u968f n \u7684\u589e\u5927\u800c\u589e\u957f\uff0c\u8fd9\u6837\u4ee3\u7801\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u6211\u4eec\u90fd\u8bb0\u4f5c O(1)</p> <p>\u6216\u8005\u8bf4\uff0c\u4e00\u822c\u60c5\u51b5\u4e0b\uff0c\u53ea\u8981\u7b97\u6cd5\u4e2d\u4e0d\u5b58\u5728\u5faa\u73af\u8bed\u53e5\u3001\u9012\u5f52\u8bed\u53e5\uff0c\u5373\u4f7f\u6709\u6210\u5343\u4e0a\u4e07\u884c\u7684\u4ee3\u7801\uff0c\u5176\u65f6\u95f4\u590d\u6742\u5ea6\u4e5f\u662f \u039f(1)</p> <h3 id="42-olog-n\u3001on-log-n">4.2 O(log n)\u3001O(n log n)</h3> <p>\u5bf9\u6570\u9636\u65f6\u95f4\u590d\u6742\u5ea6\u975e\u5e38\u5e38\u89c1\uff0c\u540c\u65f6\u4e5f\u662f\u6700\u96be\u5206\u6790\u7684\u4e00\u79cd\u65f6\u95f4\u590d\u6742\u5ea6</p> <pre><code class="language-java">int i=1;\nwhile (i &lt;= n) {\n  i = i * 2;\n}</code></pre> <p>\u6839\u636e\u524d\u9762\u8bb2\u7684\u590d\u6742\u5ea6\u5206\u6790\u65b9\u6cd5\uff0c\u7b2c\u4e09\u884c\u4ee3\u7801\u662f\u5faa\u73af\u6267\u884c\u6b21\u6570\u6700\u591a\u7684\u3002\u6240\u4ee5\uff0c\u6211\u4eec\u53ea\u8981\u80fd\u8ba1\u7b97\u51fa\u8fd9\u884c\u4ee3\u7801\u88ab\u6267\u884c\u4e86\u591a\u5c11\u6b21\uff0c\u5c31\u80fd\u77e5\u9053\u6574\u6bb5\u4ee3\u7801\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u3002</p> <p>\u4ece\u4ee3\u7801\u4e2d\u53ef\u4ee5\u770b\u51fa\uff0c\u53d8\u91cf i \u7684\u503c\u4ece 1 \u5f00\u59cb\u53d6\uff0c\u6bcf\u5faa\u73af\u4e00\u6b21\u5c31\u4e58\u4ee5 2\u3002\u5f53\u5927\u4e8e n \u65f6\uff0c\u5faa\u73af\u7ed3\u675f\u3002</p> <p>\u53d8\u91cf i \u7684\u53d6\u503c\u5c31\u662f\u4e00\u4e2a\u7b49\u6bd4\u6570\u5217\u3002\u5982\u679c\u6211\u628a\u5b83\u4e00\u4e2a\u4e00\u4e2a\u5217\u51fa\u6765\uff0c\u5c31\u5e94\u8be5\u662f\u8fd9\u4e2a\u6837\u5b50\u7684\uff1a</p> <blockquote> <p>2<sup>0</sup> 2<sup>1</sup> 2<sup>2</sup> ... 2<sup>k</sup> ... 2<sup>x</sup> = n</p> </blockquote> <p>\u6240\u4ee5\uff0c\u6211\u4eec\u53ea\u8981\u77e5\u9053 x \u503c\u662f\u591a\u5c11\uff0c\u5c31\u77e5\u9053\u8fd9\u884c\u4ee3\u7801\u6267\u884c\u7684\u6b21\u6570\u4e86</p> <p>\u901a\u8fc7 2<sup>x</sup> = n \u6c42\u89e3 x</p> <p>x = log<sub>2</sub>n\uff0c\u6240\u4ee5\uff0c\u8fd9\u6bb5\u4ee3\u7801\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u5c31\u662f O(log<sub>2</sub>n)</p> <p>\u73b0\u5728\u7a0d\u5fae\u6539\u52a8\u4ee3\u7801\uff1a</p> <pre><code class="language-java">int i=1;\nwhile (i &lt;= n) {\n  i = i * 3;\n}</code></pre> <p>\u8fd9\u6bb5\u4ee3\u7801\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u4e3a O(log<sub>3</sub>n)</p> <p>\u5b9e\u9645\u4e0a\uff0c\u4e0d\u7ba1\u662f\u4ee5 2 \u4e3a\u5e95\u3001\u4ee5 3 \u4e3a\u5e95\uff0c\u8fd8\u662f\u4ee5 10 \u4e3a\u5e95\uff0c\u6211\u4eec\u53ef\u4ee5\u628a\u6240\u6709\u5bf9\u6570\u9636\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u90fd\u8bb0\u4e3a O(log n)</p> <p>\u4e3a\u4ec0\u4e48\u5462\uff1f\u6211\u4eec\u77e5\u9053\uff0c\u5bf9\u6570\u4e4b\u95f4\u662f\u53ef\u4ee5\u4e92\u76f8\u8f6c\u6362\u7684\uff0clog<sub>3</sub>n = log<sub>3</sub>2 * log<sub>2</sub>n</p> <p>\u6240\u4ee5 O(log<sub>3</sub>n) = O(C * log<sub>2</sub>n)\uff0c\u5176\u4e2d C = log<sub>3</sub>2 \u662f\u4e00\u4e2a\u5e38\u91cf</p> <p>\u57fa\u4e8e\u6211\u4eec\u524d\u9762\u7684\u4e00\u4e2a\u7406\u8bba\uff1a\u5728\u91c7\u7528\u5927 O \u6807\u8bb0\u590d\u6742\u5ea6\u7684\u65f6\u5019\uff0c\u53ef\u4ee5\u5ffd\u7565\u7cfb\u6570\uff0c\u5373 O(Cf(n)) = O(f(n))\u3002\u6240\u4ee5\uff0cO(log<sub>2</sub>n) \u5c31\u7b49\u4e8e O(log<sub>3</sub>n)\u3002\u56e0\u6b64\uff0c\u5728\u5bf9\u6570\u9636\u65f6\u95f4\u590d\u6742\u5ea6\u7684\u8868\u793a\u65b9\u6cd5\u91cc\uff0c\u6211\u4eec\u5ffd\u7565\u5bf9\u6570\u7684\u201c\u5e95\u201d\uff0c\u7edf\u4e00\u8868\u793a\u4e3a O(log n)</p> <h3 id="43-omn\u3001omn">4.3 O(m+n)\u3001O(m*n)</h3> <p>\u6211\u4eec\u518d\u6765\u770b\u4e00\u79cd\u8ddf\u524d\u9762\u90fd\u4e0d\u4e00\u6837\u7684\u65f6\u95f4\u590d\u6742\u5ea6\uff0c\u4ee3\u7801\u7684\u590d\u6742\u5ea6\u7531\u4e24\u4e2a\u6570\u636e\u7684\u89c4\u6a21\u6765\u51b3\u5b9a\uff1a</p> <pre><code class="language-java">int cal(int m, int n) {\n  int sum_1 = 0;\n  int i = 1;\n  for (; i &lt; m; ++i) {\n    sum_1 = sum_1 + i;\n  }\n\n  int sum_2 = 0;\n  int j = 1;\n  for (; j &lt; n; ++j) {\n    sum_2 = sum_2 + j;\n  }\n\n  return sum_1 + sum_2;\n}</code></pre> <p>\u4ece\u4ee3\u7801\u4e2d\u53ef\u4ee5\u770b\u51fa\uff0cm \u548c n \u662f\u8868\u793a\u4e24\u4e2a\u6570\u636e\u89c4\u6a21\u3002\u6211\u4eec\u65e0\u6cd5\u4e8b\u5148\u8bc4\u4f30 m \u548c n \u8c01\u7684\u91cf\u7ea7\u5927\uff0c\u6240\u4ee5\u6211\u4eec\u5728\u8868\u793a\u590d\u6742\u5ea6\u7684\u65f6\u5019\uff0c\u5c31\u4e0d\u80fd\u7b80\u5355\u5730\u5229\u7528\u52a0\u6cd5\u6cd5\u5219\uff0c\u7701\u7565\u6389\u5176\u4e2d\u4e00\u4e2a</p> <p>\u6240\u4ee5\uff0c\u4e0a\u9762\u4ee3\u7801\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u5c31\u662f O(m+n)</p> <p>\u9488\u5bf9\u8fd9\u79cd\u60c5\u51b5\uff0c\u539f\u6765\u7684\u52a0\u6cd5\u6cd5\u5219\u5c31\u4e0d\u6b63\u786e\u4e86\uff0c\u6211\u4eec\u9700\u8981\u5c06\u52a0\u6cd5\u89c4\u5219\u6539\u4e3a\uff1aT1(m) + T2(n) = O(f(m) + g(n))\u3002\u4f46\u662f\u4e58\u6cd5\u6cd5\u5219\u7ee7\u7eed\u6709\u6548\uff1aT1(m) _ T2(n) = O(f(m) _ f(n))</p> <h2 id="5-\u7a7a\u95f4\u590d\u6742\u5ea6\u5206\u6790">5 \u7a7a\u95f4\u590d\u6742\u5ea6\u5206\u6790</h2> <p>\u7a7a\u95f4\u590d\u6742\u5ea6\u5168\u79f0\u5c31\u662f\u6e10\u8fdb\u7a7a\u95f4\u590d\u6742\u5ea6\uff08asymptotic space complexity\uff09\uff0c\u8868\u793a\u7b97\u6cd5\u7684\u5b58\u50a8\u7a7a\u95f4\u4e0e\u6570\u636e\u89c4\u6a21\u4e4b\u95f4\u7684\u589e\u957f\u5173\u7cfb\u3002</p> <pre><code class="language-java">void print(int n) {\n  int i = 0;\n  int[] a = new int[n];\n  for (i; i &lt;n; ++i) {\n    a[i] = i * i;\n  }\n\n  for (i = n-1; i &gt;= 0; --i) {\n    print out a[i];\n  }\n}</code></pre> <p>\u8ddf\u65f6\u95f4\u590d\u6742\u5ea6\u5206\u6790\u4e00\u6837\uff0c\u6211\u4eec\u53ef\u4ee5\u770b\u5230\uff0c\u7b2c 2 \u884c\u4ee3\u7801\u4e2d\uff0c\u6211\u4eec\u7533\u8bf7\u4e86\u4e00\u4e2a\u7a7a\u95f4\u5b58\u50a8\u53d8\u91cf i\uff0c\u4f46\u662f\u5b83\u662f\u5e38\u91cf\u9636\u7684\uff0c\u8ddf\u6570\u636e\u89c4\u6a21 n \u6ca1\u6709\u5173\u7cfb\uff0c\u6240\u4ee5\u6211\u4eec\u53ef\u4ee5\u5ffd\u7565\u3002\u7b2c 3 \u884c\u7533\u8bf7\u4e86\u4e00\u4e2a\u5927\u5c0f\u4e3a n \u7684 int \u7c7b\u578b\u6570\u7ec4\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u5269\u4e0b\u7684\u4ee3\u7801\u90fd\u6ca1\u6709\u5360\u7528\u66f4\u591a\u7684\u7a7a\u95f4\uff0c\u6240\u4ee5\u6574\u6bb5\u4ee3\u7801\u7684\u7a7a\u95f4\u590d\u6742\u5ea6\u5c31\u662f O(n)\u3002</p> <p>\u6211\u4eec\u5e38\u89c1\u7684\u7a7a\u95f4\u590d\u6742\u5ea6\u5c31\u662f O(1)\u3001O(n)\u3001O(n<sup>x</sup>)\uff0c\u50cf O(log n)\u3001O(n log n) \u8fd9\u6837\u7684\u5bf9\u6570\u9636\u590d\u6742\u5ea6\u5e73\u65f6\u90fd\u7528\u4e0d\u5230\u3002\u7a7a\u95f4\u590d\u6742\u5ea6\u5206\u6790\u6bd4\u65f6\u95f4\u590d\u6742\u5ea6\u5206\u6790\u8981\u7b80\u5355\u5f88\u591a</p> <h2 id="6-\u6700\u597d\u3001\u6700\u574f\u60c5\u51b5\u65f6\u95f4\u590d\u6742\u5ea6">6 \u6700\u597d\u3001\u6700\u574f\u60c5\u51b5\u65f6\u95f4\u590d\u6742\u5ea6</h2> <pre><code class="language-java">// n \u8868\u793a\u6570\u7ec4 array \u7684\u957f\u5ea6\nint find(int[] array, int n, int x) {\n  int i = 0;\n  int pos = -1;\n  for (; i &lt; n; ++i) {\n    if (array[i] == x) pos = i;\n  }\n  return pos;\n}</code></pre> <p>\u8fd9\u6bb5\u4ee3\u7801\u8981\u5b9e\u73b0\u7684\u529f\u80fd\u662f\uff0c\u5728\u4e00\u4e2a\u65e0\u5e8f\u7684\u6570\u7ec4\uff08array\uff09\u4e2d\uff0c\u67e5\u627e\u53d8\u91cf x \u51fa\u73b0\u7684\u4f4d\u7f6e\u3002\u5982\u679c\u6ca1\u6709\u627e\u5230\uff0c\u5c31\u8fd4\u56de -1</p> <p>\u6309\u7167\u4e0a\u9762\u7684\u5206\u6790\u65b9\u6cd5\uff0c\u8fd9\u6bb5\u4ee3\u7801\u7684\u590d\u6742\u5ea6\u662f O(n)\uff0c\u5176\u4e2d\uff0cn \u4ee3\u8868\u6570\u7ec4\u7684\u957f\u5ea6\u3002</p> <p>\u6211\u4eec\u5728\u6570\u7ec4\u4e2d\u67e5\u627e\u4e00\u4e2a\u6570\u636e\uff0c\u5e76\u4e0d\u9700\u8981\u6bcf\u6b21\u90fd\u628a\u6574\u4e2a\u6570\u7ec4\u90fd\u904d\u5386\u4e00\u904d\uff0c\u56e0\u4e3a\u6709\u53ef\u80fd\u4e2d\u9014\u627e\u5230\u5c31\u53ef\u4ee5\u63d0\u524d\u7ed3\u675f\u5faa\u73af\u4e86\u3002\u4f46\u662f\uff0c\u8fd9\u6bb5\u4ee3\u7801\u5199\u5f97\u4e0d\u591f\u9ad8\u6548\u3002\u6211\u4eec\u53ef\u4ee5\u8fd9\u6837\u4f18\u5316\u4e00\u4e0b\u8fd9\u6bb5\u67e5\u627e\u4ee3\u7801\u3002</p> <pre><code class="language-java">// n \u8868\u793a\u6570\u7ec4 array \u7684\u957f\u5ea6\nint find(int[] array, int n, int x) {\n  int i = 0;\n  int pos = -1;\n  for (; i &lt; n; ++i) {\n    if (array[i] == x) {\n       pos = i;\n       break;\n    }\n  }\n  return pos;\n}</code></pre> <p>\u8981\u67e5\u627e\u7684\u53d8\u91cf x \u53ef\u80fd\u51fa\u73b0\u5728\u6570\u7ec4\u7684\u4efb\u610f\u4f4d\u7f6e\u3002\u5982\u679c\u6570\u7ec4\u4e2d\u7b2c\u4e00\u4e2a\u5143\u7d20\u6b63\u597d\u662f\u8981\u67e5\u627e\u7684\u53d8\u91cf x\uff0c\u90a3\u5c31\u4e0d\u9700\u8981\u7ee7\u7eed\u904d\u5386\u5269\u4e0b\u7684 n-1 \u4e2a\u6570\u636e\u4e86\uff0c\u90a3\u65f6\u95f4\u590d\u6742\u5ea6\u5c31\u662f O(1)</p> <p>\u4f46\u5982\u679c\u6570\u7ec4\u4e2d\u4e0d\u5b58\u5728\u53d8\u91cf x\uff0c\u90a3\u6211\u4eec\u5c31\u9700\u8981\u628a\u6574\u4e2a\u6570\u7ec4\u90fd\u904d\u5386\u4e00\u904d\uff0c\u65f6\u95f4\u590d\u6742\u5ea6\u5c31\u6210\u4e86 O(n)\u3002\u6240\u4ee5\uff0c\u4e0d\u540c\u7684\u60c5\u51b5\u4e0b\uff0c\u8fd9\u6bb5\u4ee3\u7801\u7684\u65f6\u95f4\u590d\u6742\u5ea6\u662f\u4e0d\u4e00\u6837\u7684\u3002</p> <p>\u4e3a\u4e86\u8868\u793a\u4ee3\u7801\u5728\u4e0d\u540c\u60c5\u51b5\u4e0b\u7684\u4e0d\u540c\u65f6\u95f4\u590d\u6742\u5ea6\uff0c\u6211\u4eec\u9700\u8981\u5f15\u5165\u4e09\u4e2a\u6982\u5ff5\uff1a</p> <blockquote> <p>\u6700\u597d\u60c5\u51b5\u65f6\u95f4\u590d\u6742\u5ea6\u3001\u6700\u574f\u60c5\u51b5\u65f6\u95f4\u590d\u6742\u5ea6\u548c\u5e73\u5747\u60c5\u51b5\u65f6\u95f4\u590d\u6742\u5ea6</p> </blockquote> <h2 id="7-\u5e73\u5747\u60c5\u51b5\u65f6\u95f4\u590d\u6742\u5ea6">7 \u5e73\u5747\u60c5\u51b5\u65f6\u95f4\u590d\u6742\u5ea6</h2> <p>\u6211\u4eec\u90fd\u77e5\u9053\uff0c\u6700\u597d\u60c5\u51b5\u65f6\u95f4\u590d\u6742\u5ea6\u548c\u6700\u574f\u60c5\u51b5\u65f6\u95f4\u590d\u6742\u5ea6\u5bf9\u5e94\u7684\u90fd\u662f\u6781\u7aef\u60c5\u51b5\u4e0b\u7684\u4ee3\u7801\u590d\u6742\u5ea6\uff0c\u53d1\u751f\u7684\u6982\u7387\u5176\u5b9e\u5e76\u4e0d\u5927\u3002\u4e3a\u4e86\u66f4\u597d\u5730\u8868\u793a\u5e73\u5747\u60c5\u51b5\u4e0b\u7684\u590d\u6742\u5ea6\uff0c\u6211\u4eec\u9700\u8981\u5f15\u5165\u53e6\u4e00\u4e2a\u6982\u5ff5\uff1a\u5e73\u5747\u60c5\u51b5\u65f6\u95f4\u590d\u6742\u5ea6\uff0c\u540e\u9762\u6211\u7b80\u79f0\u4e3a\u5e73\u5747\u65f6\u95f4\u590d\u6742\u5ea6\u3002</p> <p>\u6211\u4eec\u77e5\u9053\uff0c\u8981\u67e5\u627e\u7684\u53d8\u91cf x\uff0c\u8981\u4e48\u5728\u6570\u7ec4\u91cc\uff0c\u8981\u4e48\u5c31\u4e0d\u5728\u6570\u7ec4\u91cc\u3002\u8fd9\u4e24\u79cd\u60c5\u51b5\u5bf9\u5e94\u7684\u6982\u7387\u7edf\u8ba1\u8d77\u6765\u5f88\u9ebb\u70e6\uff0c\u4e3a\u4e86\u65b9\u4fbf\u7406\u89e3\uff0c\u6211\u4eec\u5047\u8bbe\u5728\u6570\u7ec4\u4e2d\u4e0e\u4e0d\u5728\u6570\u7ec4\u4e2d\u7684\u6982\u7387\u90fd\u4e3a 1/2\u3002\u53e6\u5916\uff0c\u8981\u67e5\u627e\u7684\u6570\u636e\u51fa\u73b0\u5728 0\uff5en-1 \u8fd9 n \u4e2a\u4f4d\u7f6e\u7684\u6982\u7387\u4e5f\u662f\u4e00\u6837\u7684\uff0c\u4e3a 1/n\u3002\u6240\u4ee5\uff0c\u6839\u636e\u6982\u7387\u4e58\u6cd5\u6cd5\u5219\uff0c\u8981\u67e5\u627e\u7684\u6570\u636e\u51fa\u73b0\u5728 0\uff5en-1 \u4e2d\u4efb\u610f\u4f4d\u7f6e\u7684\u6982\u7387\u5c31\u662f 1/(2n)\u3002</p> <p>\u90a3\u5e73\u5747\u65f6\u95f4\u590d\u6742\u5ea6\u7684\u8ba1\u7b97\u8fc7\u7a0b\u5c31\u53d8\u6210\u4e86\u8fd9\u6837\uff1a</p> <p><img src="'+t+'" alt=""></p> <p>\u8fd9\u4e2a\u503c\u5c31\u662f\u6982\u7387\u8bba\u4e2d\u7684\u52a0\u6743\u5e73\u5747\u503c\uff0c\u4e5f\u53eb\u4f5c\u671f\u671b\u503c\uff0c\u6240\u4ee5\u5e73\u5747\u65f6\u95f4\u590d\u6742\u5ea6\u7684\u5168\u79f0\u5e94\u8be5\u53eb\u52a0\u6743\u5e73\u5747\u65f6\u95f4\u590d\u6742\u5ea6\u6216\u8005\u671f\u671b\u65f6\u95f4\u590d\u6742\u5ea6\u3002</p> <p>\u5f15\u5165\u6982\u7387\u4e4b\u540e\uff0c\u524d\u9762\u90a3\u6bb5\u4ee3\u7801\u7684\u52a0\u6743\u5e73\u5747\u503c\u4e3a (3n+1)/4\u3002\u7528\u5927 O \u8868\u793a\u6cd5\u6765\u8868\u793a\uff0c\u53bb\u6389\u7cfb\u6570\u548c\u5e38\u91cf\uff0c\u8fd9\u6bb5\u4ee3\u7801\u7684\u52a0\u6743\u5e73\u5747\u65f6\u95f4\u590d\u6742\u5ea6\u4ecd\u7136\u662f O(n)\u3002</p> <p>\u5b9e\u9645\u4e0a\uff0c\u5728\u5927\u591a\u6570\u60c5\u51b5\u4e0b\uff0c\u6211\u4eec\u5e76\u4e0d\u9700\u8981\u533a\u5206\u6700\u597d\u3001\u6700\u574f\u3001\u5e73\u5747\u60c5\u51b5\u65f6\u95f4\u590d\u6742\u5ea6\u4e09\u79cd\u60c5\u51b5\u3002\u50cf\u6211\u4eec\u4e0a\u4e00\u8282\u8bfe\u4e3e\u7684\u90a3\u4e9b\u4f8b\u5b50\u90a3\u6837\uff0c\u5f88\u591a\u65f6\u5019\uff0c\u6211\u4eec\u4f7f\u7528\u4e00\u4e2a\u590d\u6742\u5ea6\u5c31\u53ef\u4ee5\u6ee1\u8db3\u9700\u6c42\u4e86\u3002\u53ea\u6709\u540c\u4e00\u5757\u4ee3\u7801\u5728\u4e0d\u540c\u7684\u60c5\u51b5\u4e0b\uff0c\u65f6\u95f4\u590d\u6742\u5ea6\u6709\u91cf\u7ea7\u7684\u5dee\u8ddd\uff0c\u6211\u4eec\u624d\u4f1a\u4f7f\u7528\u8fd9\u4e09\u79cd\u590d\u6742\u5ea6\u8868\u793a\u6cd5\u6765\u533a\u5206\u3002</p> <h2 id="8-\u5747\u644a\u65f6\u95f4\u590d\u6742\u5ea6">8 \u5747\u644a\u65f6\u95f4\u590d\u6742\u5ea6</h2> ';n.exports=l}}]);