(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["docs/blockChain/btc"],{gRYp:function(o,l,i){var t=i("Rxnk"),p=i("d9IR"),e=i("os2Z"),s=i("pHIk"),r=i("ThzR"),n=i("RzER"),g=i("WazT"),c=i("uop0"),h=t(p),d=t(e),a=t(s),u=t(r),k=t(n),P=t(g),b=t(c),D='<h1 id="\u6bd4\u7279\u5e01">\u6bd4\u7279\u5e01</h1> <h2 id="1-\u6bd4\u7279\u5e01\u6982\u5ff5">1 \u6bd4\u7279\u5e01\u6982\u5ff5</h2> <h3 id="11-\u6bd4\u7279\u5e01\u7684\u7279\u6027">1.1 \u6bd4\u7279\u5e01\u7684\u7279\u6027</h3> <ol> <li><strong>\u53bb\u4e2d\u5fc3\u5316</strong>\u3002\u8fd9\u610f\u5473\u7740\u6ca1\u6709\u4e2d\u5fc3\u7684\u670d\u52a1\u5668\uff0c\u4e0d\u53d7\u67d0\u4e2a\u4eba\u7684\u63a7\u5236\uff0c\u6574\u4e2a\u7cfb\u7edf\u76f4\u63a5\u7531\u7528\u6237\u7aef\u7684\u7535\u8111\u6784\u6210\u3002\u8fd9\u6837\u7684\u6280\u672f\u96be\u5ea6\u662f\u975e\u5e38\u5927\u7684\uff0c\u5e76\u4e0d\u50cf\u624b\u673a App \u6216\u662f\u5c0f\u7f51\u7ad9\u4e00\u6837\uff0c\u4f60\u60f3\u53d1\u5e03\u5c31\u53d1\u5e03\uff0c\u8fd9\u9700\u8981\u6709\u4eba\u6765\u8ddf\u4f60\u4e00\u8d77\u73a9</li> <li><strong>\u6570\u636e\u9632\u7be1\u6539</strong>\u3002\u6240\u6709\u4ea4\u6613\u8bb0\u5f55\u5168\u91cf\u4fdd\u5b58\uff0c\u5e76\u516c\u5f00\u7ed9\u6240\u6709\u7684\u4eba\uff0c\u800c\u4e14\u8fd8\u88ab\u52a0\u5bc6\u548c\u6821\u9a8c\u3002\u5e76\u4e0d\u662f\u6570\u636e\u4e0d\u80fd\u88ab\u7be1\u6539\uff0c\u800c\u662f\u6570\u636e\u88ab\u7be1\u6539\u7684\u6210\u672c\u975e\u5e38\u5927</li> <li><strong>\u56fa\u5b9a\u7684\u53d1\u884c\u91cf</strong>\u3002\u4e0d\u4f1a\u50cf\u56fd\u5bb6\u4e2d\u592e\u94f6\u884c\u90a3\u6837\u4e71\u5370\u949e\u7968\uff0c\u9020\u6210\u901a\u8d27\u81a8\u80c0</li> </ol> <h3 id="12-\u53bb\u4e2d\u5fc3\u5316\u4ea4\u6613\u6d41\u7a0b">1.2 \u53bb\u4e2d\u5fc3\u5316\u4ea4\u6613\u6d41\u7a0b</h3> <ol> <li>\u9996\u5148\uff0c\u9700\u8981\u4ea4\u6613\u7684\u7528\u6237\u628a\u4ea4\u6613\u4f20\u5230\u7f51\u7edc\u4e2d</li> <li>\u7136\u540e\uff0c\u7f51\u7edc\u4e0a\u6709\u4e9b\u673a\u5668\u53eb\u8bb0\u8d26\u7ed3\u70b9\uff0c\u5b83\u4eec\u901a\u8fc7\u6bd4\u62fc\u8ba1\u7b97\u529b\u7684\u65b9\u5f0f\u7ade\u4e89\u8bb0\u8d26\u6743\u3002\u8fd9\u4e5f\u53eb\u201c\u6316\u77ff\u201d</li> <li>\u83b7\u5f97\u8bb0\u8d26\u6743\u7684\u7ed3\u70b9\uff0c\u4f1a\u628a\u5f85\u8bb0\u8d26\u7684\u4ea4\u6613\u8fdb\u884c\u8ba1\u7b97\u6253\u5305\uff0c\u5e76\u5411\u5168\u7f51\u5e7f\u64ad\u3002\u6536\u5230\u65b0\u7684\u8bb0\u8d26\u5305\u7684\u7ed3\u70b9\u4f1a\u5bf9\u5176\u8fdb\u884c\u9a8c\u8bc1\uff0c\u9a8c\u8bc1\u901a\u8fc7\u540e\u52a0\u5165\u81ea\u5df1\u7684\u533a\u5757</li> </ol> <blockquote> <p>\u6ce8\u610f\uff0c\u6574\u4e2a\u6bd4\u7279\u5e01\u7684\u4e16\u754c\u662f\u6ca1\u6709\u670d\u52a1\u5668\u7684\uff0c\u8fd9\u79cd P2P \u7684\u53bb\u4e2d\u5fc3\u5316\u7f51\u7edc<strong>\u4efb\u4f55\u4eba\u90fd\u80fd\u4fee\u6539\u6570\u636e</strong>\u3002\u4e3a\u4e86\u5e94\u5bf9\u8fd9\u4e2a\u98ce\u9669\uff0c\u6570\u636e\u8981\u80fd <strong>\u5f88\u5bb9\u6613\u88ab\u9a8c\u8bc1\u662f\u5408\u6cd5\u7684\u6ca1\u6709\u88ab\u4fee\u6539\u8fc7\u7684</strong>\uff0c\u800c\u4e14\u4e5f\u8981\u662f <strong>\u5f88\u96be\u88ab\u4eba\u4fee\u6539\u7684</strong></p> </blockquote> <blockquote> <p>\u57fa\u4e8e\u8fd9\u4e2a\u8bbe\u8ba1\uff0c\u6bd4\u7279\u5e01\u4f7f\u7528\u4e86\u4e24\u4e2a\u6bd4\u8f83\u5927\u7684\u6280\u672f\uff1a<strong>\u533a\u5757\u94fe\u6280\u672f</strong> \u548c <strong>\u5de5\u4f5c\u91cf\u8bc1\u660e\u5171\u8bc6\u673a\u5236</strong></p> </blockquote> <h3 id="13-\u6570\u636e\u9632\u7be1\u6539-\u533a\u5757\u94fe\u6280\u672f">1.3 \u6570\u636e\u9632\u7be1\u6539-\u533a\u5757\u94fe\u6280\u672f</h3> <p><strong>\u4ec0\u4e48\u662f\u533a\u5757\u94fe\uff1a</strong></p> <p>\u533a\u5757\u94fe\u53c8\u53eb blockchain\uff0c\u5176\u4e2d\u6709\u4e00\u4e2a\u4e00\u4e2a\u7684\u533a\u5757\uff0c\u6bcf\u4e2a\u533a\u5757\u4e2d\u5305\u62ec\u7740\u4e00\u7ec4\u4ea4\u6613\u4fe1\u606f\uff0c\u7136\u540e\uff0c\u6bcf\u4e00\u4e2a\u533a\u5757\u90fd\u4f1a\u6709\u4e00\u4e2a ID\uff08\u6216\u662f\u4e00\u4e2a\u5730\u5740\uff09\uff0c\u8fd9\u4e9b\u533a\u5757\u901a\u8fc7\u8bb0\u5f55\u524d\u4e00\u4e2a\u533a\u5757\u7684 ID \u6765\u5f62\u6210\u4e00\u6761\u94fe</p> <p><img src="'+h+'" alt=""></p> <ul> <li><strong>\u6bcf\u4e2a\u5757\u7684 ID \u90fd\u662f\u901a\u8fc7\u5176\u5185\u5bb9\u751f\u6210\u7684</strong>\uff0c\u6240\u4ee5\uff0c\u53ea\u8981\u662f\u5185\u5bb9\u6709\u4e00\u4e01\u70b9\u513f\u7684\u53d8\u5316\uff0c\u8fd9\u4e2a ID \u90fd\u4f1a\u5b8c\u5168\u4e0d\u4e00\u6837</li> <li>\u800c\u751f\u6210 ID \u7684\u5185\u5bb9\u4e2d\u8fd8\u5305\u62ec\u4e0a\u4e00\u4e2a\u5757\u7684 ID\u3002\u4e8e\u662f\u53ea\u8981\u4e0a\u4e00\u4e2a\u5757\u7684\u5185\u5bb9\u53d8\u4e86\uff0c\u5176 ID \u4e5f\u8981\u8ddf\u7740\u53d8\uff08\u4e0d\u7136\u5c31\u4e0d\u5408\u6cd5\u4e86\uff09\uff0c\u90a3\u4e48\u540e\u9762\u6307\u5411\u8fd9\u4e2a\u5757\u7684 ID \u4e5f\u8981\u53d8\u3002\u8fd9\u6837\u5c31\u4f1a\u5f62\u6210\u4e00\u4e2a\u8fde\u9501\u6548\u5e94\u2014\u2014\u4e00\u4e2a\u5757\u88ab\u4fee\u6539\uff0c\u540e\u7eed\u7684\u6240\u6709\u5757\u90fd\u8981\u8ddf\u7740\u4e00\u8d77\u6539\u3002\u4e8e\u662f\u5bfc\u81f4\u4e86\u4fee\u6539\u6210\u672c\u7684\u63d0\u5347</li> <li>\u8fd9\u79cd\u4e00\u5904\u6539\uff0c\u5904\u5904\u6539\u7684\u65b9\u5f0f\uff0c\u5e76\u4e0d\u4ee3\u8868\u4e0d\u80fd\u7be1\u6539\uff0c\u800c\u53ea\u662f\u8ba9\u4fee\u6539\u9762\u6bd4\u8f83\u5927\uff0c\u8ba9\u4f60\u7684\u6539\u52a8\u9ebb\u70e6\u4e00\u70b9</li> <li>\u8d8a\u65e7\u7684\u533a\u5757\u7684\u7be1\u6539\u4f1a\u9020\u6210\u8d8a\u5927\u9762\u79ef\u7684\u4fee\u6539\uff0c\u4e8e\u662f\u8d8a\u65e7\u7684\u533a\u5757\u5c31\u4e0d\u5bb9\u6613\u7be1\u6539\uff0c\u5c31\u8d8a\u5b89\u5168\u3002\u53cd\u4e4b\uff0c\u8d8a\u65b0\u7684\u533a\u5757\u5c31\u8d8a\u4e0d\u5b89\u5168</li> </ul> <h3 id="14-\u6570\u636e\u653e\u7be1\u6539-\u5171\u8bc6\u673a\u5236">1.4 \u6570\u636e\u653e\u7be1\u6539-\u5171\u8bc6\u673a\u5236</h3> <p>\u771f\u6b63\u8ba9\u533a\u5757\u94fe\u505a\u5230\u975e\u5e38\u96be\u7be1\u6539\u7684\u662f\u5de5\u4f5c\u91cf\u8bc1\u660e\u7684\u5171\u8bc6\u673a\u5236</p> <p><strong>\u89e3\u51b3\u6570\u636e\u4e00\u81f4\u6027\u76f8\u5173\u7684\u95ee\u9898\uff1a</strong></p> <ol> <li><strong>\u4ee5\u8c01\u7684\u6570\u636e\u4e3a\u51c6\u3002</strong> \u4efb\u4f55\u7ed3\u70b9\u90fd\u53ef\u4ee5\u4fee\u6539\u81ea\u5df1\u6240\u4e0b\u8f7d\u7684\u8d26\u672c\uff0c\u4e5f\u5c31\u662f\u4efb\u4f55\u4e00\u4e2a\u4eba\u90fd\u53ef\u4ee5\u4f2a\u9020\u8d26\u672c\u3002\u90a3\u4e48\uff0c\u8c01\u7684\u6570\u636e\u624d\u662f\u5bf9\u7684\uff1f\u5728\u53bb\u4e2d\u5fc3\u5316\u7684\u7f51\u7edc\u4e0b\uff0c\u6211\u4eec\u53ea\u80fd\u8ba4\u4e3a\uff0c\u5927\u591a\u6570\u4eba\u8ba4\u8bc6\u7684\u6570\u636e\u662f\u5bf9\u7684</li> <li><strong>\u201c\u5927\u591a\u6570\u4eba\u201d\u7684\u95ee\u9898\u3002</strong> \u5728\u7f51\u7edc\u4e16\u754c\u91cc\uff0c\u6211\u53ef\u4ee5\u7528\u7a0b\u5e8f\u6a21\u62df\u51fa\u65e0\u7a77\u591a\u7684\u201c\u4eba\u201d\u51fa\u6765\u6295\u7968\uff0c\u6240\u4ee5\uff0c\u7528\u4eba\u6570\u6765\u89e3\u51b3\u53bb\u4e2d\u5fc3\u5316\u7684\u95ee\u9898\uff0c\u5728\u5206\u4e0d\u6e05\u662f\u4eba\u8fd8\u662f\u8ba1\u7b97\u673a</li> <li><strong>\u610f\u89c1\u5206\u6b67\u95ee\u9898\u3002</strong> \u5982\u679c\u5728\u540c\u4e00\u4e2a\u65f6\u523b\uff0c\u6709\u591a\u4e2a\u4eba\u90fd\u5728\u544a\u8bc9\u5176\u4ed6\u4eba\uff0c\u8fd9\u8d26\u5e94\u8be5\u8fd9\u4e48\u8bb0\u3002\u800c\u4e14\uff0c\u4ed6\u4eec\u7684\u6570\u636e\u90fd\u5408\u6cd5\uff0c\u90a3\u4e48\uff0c\u6574\u4e2a\u7f51\u7edc\u5e94\u8be5\u542c\u8c01\u7684\uff1f</li> </ol> <blockquote> <p>\u4e3a\u4e86\u89e3\u51b3\u8fd9\u51e0\u4e2a\u95ee\u9898\uff0c\u6bd4\u7279\u5e01\u4f7f\u7528\u4e86 <code>Proof-of-Work</code> \u5de5\u4f5c\u91cf\u8bc1\u660e\u673a\u5236\uff0c\u4e5f\u5c31\u662f\u201c\u6316\u77ff\u201d\u3002\u6240\u8c13\u7684\u201c\u6316\u77ff\u201d\u5176\u5b9e\u5c31\u662f\u7528\u5927\u89c4\u6a21\u7684\u8ba1\u7b97\u6765\u627e\u5230\u4e00\u4e2a\u7b26\u5408\u7cfb\u7edf\u8981\u6c42\u7684\u533a\u5757 ID\u3002\u8981\u627e\u5230\u7b26\u5408\u6761\u4ef6\u7684\u533a\u5757 ID \u53ea\u80fd\u901a\u8fc7\u66b4\u529b\u7a77\u4e3e\u7684\u65b9\u5f0f\uff0c\u6240\u4ee5\u8981\u4ed8\u51fa\u5927\u91cf\u7684\u7cfb\u7edf\u8ba1\u7b97\u8d44\u6e90\u548c\u7535\u529b\u3002</p> </blockquote> <p><strong>\u8fd9\u79cd\u6781\u5ea6\u6d88\u8017\u8ba1\u7b97\u529b\u7684\u65b9\u5f0f\u6709\u6548\u7684\u89e3\u51b3\u4e0b\u9762\u51e0\u4e2a\u95ee\u9898\uff1a</strong></p> <ol> <li><strong>\u4fee\u6539\u51e0\u4e4e\u53d8\u5f97\u4e0d\u53ef\u80fd\u3002</strong> \u5982\u679c\u751f\u6210\u4e00\u4e2a\u533a\u5757\u9700\u8981\u5927\u91cf\u7684\u957f\u65f6\u95f4\u7684\u8ba1\u7b97\u529b\uff0c\u4e5f\u5c31\u662f\u5728\u4e16\u754c\u4e0a\u6700\u597d\u7684\u7535\u8111\u96c6\u7fa4\u4e0b\u8ba1\u7b97 10 \u5206\u949f\u624d\u80fd\u6253\u597d\u4e00\u4e2a\u5305\u3002\u90a3\u4e48\u4fee\u6539\u4e00\u4e2a\u5757\u6570\u636e\u5c31\u5f88\u56f0\u96be\uff0c\u5e76\u4e14\u5982\u679c\u8981\u4f2a\u9020\u4e00\u4e2a\u5757\uff0c\u90a3\u4e48\u4f60\u5c31\u8981\u4fee\u6539\u540e\u9762\u6240\u6709\u7684\u5757</li> <li><strong>\u80fd\u638c\u63e1 51% \u7684\u7b97\u529b\u7684\u4eba\u53d8\u5f97\u51e0\u4e4e\u4e0d\u53ef\u80fd\u3002</strong> \u9664\u4e86\u4f2a\u9020\u4e00\u6761\u94fe\u7684\u6210\u672c\u5f88\u9ad8\uff0c\u8fd8\u8981\u63a7\u5236\u5927\u591a\u6570\u4eba\u7684\u7b97\u529b\uff0c\u8fd9\u610f\u5473\u7740\u9700\u8981\u5de8\u989d\u7684\u8d44\u91d1\u6295\u5165\u3002\u8fd9\u4e24\u4e2a\u96be\u5ea6\u52a0\u8d77\u6765\uff0c\u51e0\u4e4e\u4e0d\u592a\u53ef\u80fd</li> <li><strong>\u89e3\u51b3\u5206\u6b67\u3002</strong> \u5c31\u7b97\u662f\u51fa\u73b0\u4e86\u5408\u6cd5\u51b2\u7a81\u7684\u533a\u5757\uff08\u540c\u65f6\u51fa\u73b0\u4e86\u591a\u4e2a\u5408\u7406\u7684\u533a\u5757\uff0c\u5373\u533a\u5757\u94fe\u51fa\u73b0\u5206\u652f / \u5206\u53c9\uff09\uff0c\u4e5f\u5c31\u662f\u591a\u4e2a\u5408\u6cd5\u7684\u8d26\u672c\u3002\u800c\u56e0\u4e3a\u6316\u77ff\u7684\u6210\u672c\u592a\u9ad8\uff0c\u5bfc\u81f4\u8981\u540c\u65f6\u8ddf\u8fdb\u591a\u4e2a\u8d26\u672c\u662f\u4e0d\u53ef\u80fd\u7684\uff0c\u6240\u4ee5\u77ff\u5de5\u4eec\u53ea\u80fd\u8d4c\u8ddf\u5176\u4e2d\u4e00\u4e2a\u3002\u5927\u591a\u6570\u4eba\u6240\u9009\u62e9\u7684\u90a3\u4e00\u4e2a\u5206\u652f\u7684\u94fe\u5c31\u4f1a\u8d8a\u6765\u8d8a\u591a\uff0c\u4e8e\u662f\u53e6\u5916\u4e00\u8fb9\u4e5f\u5c31\u65e0\u4eba\u95ee\u6d25\uff0c\u4ece\u800c\u4f5c\u5e9f\u4e86</li> </ol> <h2 id="2-\u533a\u5757\u683c\u5f0f">2 \u533a\u5757\u683c\u5f0f</h2> <h3 id="21-\u533a\u5757\u94fe\u7684\u534f\u8bae\u683c\u5f0f">2.1 \u533a\u5757\u94fe\u7684\u534f\u8bae\u683c\u5f0f</h3> <p>\u533a\u5757\u94fe\u5c31\u50cf\u4e00\u4e2a\u5355\u5411\u94fe\u8868\uff0c\u4e00\u4e2a\u6570\u636e\u5757\u4e2d\u4fdd\u5b58\u7740\u4e09\u4e2a\u4fe1\u606f\uff1a</p> <ol> <li>\u771f\u6b63\u7684\u6570\u636e</li> <li>\u81ea\u5df1\u7684\u5730\u5740\uff08\u6216\u662f ID\uff09</li> <li>\u524d\u4e00\u4e2a\u6570\u636e\u5757\u7684\u5730\u5740</li> </ol> <blockquote> <p>\u6570\u636e\u5757\u7684\u5730\u5740\u4f7f\u7528\u54c8\u5e0c\u7b97\u6cd5\u7f16\u7801</p> </blockquote> <p><strong>\u4e0b\u56fe\u662f\u533a\u5757\u94fe\u7684\u534f\u8bae\u683c\u5f0f\uff1a</strong></p> <p><img src="'+d+'" alt=""></p> <ul> <li><strong>\u533a\u5757\u6570\u636e\u534f\u8bae\u5934\uff1a</strong> <code>Version</code> <code>Previous Block Hash</code> <code>Merkle Root</code> <code>Timestamp</code> <code>Difficulty Target</code> <code>Nonce</code></li> <li><strong>\u4ea4\u6613\u6570\u636e\uff1a</strong> \u5206\u522b\u662f\uff1a\u672c\u5757\u4e2d\u7684\u4ea4\u6613\u7b14\u6570 H \u548c\u4ea4\u6613\u5217\u8868\uff08\u6700\u591a\u4e0d\u80fd\u8d85\u8fc7 1MB\uff0c\u4e3a\u4ec0\u4e48\u662f 1MB\uff0c\u540e\u9762\u4f1a\u8bf4\uff09</li> </ul> <p><strong>\u533a\u5757\u5934\uff1a</strong></p> <ul> <li><code>Version</code>\uff1a\u5f53\u524d\u533a\u5757\u94fe\u534f\u8bae\u7684\u7248\u672c\u53f7\uff0c4 \u4e2a\u5b57\u8282\u3002\u5982\u679c\u5347\u7ea7\u4e86\uff0c\u8fd9\u4e2a\u7248\u672c\u53f7\u4f1a\u53d8</li> <li><code>Previous Block Hash</code>\uff1a\u524d\u9762\u90a3\u4e2a\u533a\u5757\u7684 hash \u5730\u5740\u300232 \u4e2a\u5b57\u8282</li> <li><code>Merkle Root</code>\uff1a\u8fd9\u4e2a\u5b57\u6bb5\u53ef\u4ee5\u7b80\u5355\u7406\u89e3\u4e3a\u662f\u540e\u9762\u4ea4\u6613\u4fe1\u606f\u7684 hash \u503c \u300232 \u4e2a\u5b57\u8282</li> <li><code>Timestamp</code>\uff1a\u533a\u5757\u751f\u6210\u7684\u65f6\u95f4\u3002\u8fd9\u4e2a\u65f6\u95f4\u4e0d\u80fd\u65e9\u4e8e\u524d\u9762 11 \u4e2a\u533a\u5757\u7684\u4e2d\u4f4d\u65f6\u95f4\uff0c\u4e0d\u80fd\u665a\u4e8e <code>\u7f51\u7edc\u534f\u8c03\u65f6\u95f4</code> \u2014\u2014\u4f60\u6240\u8fde\u63a5\u7684\u6240\u6709\u7ed3\u70b9\u65f6\u95f4\u7684\u4e2d\u4f4d\u6570\u30024 \u4e2a\u5b57\u8282</li> <li><code>Bits</code>\uff1a\u4e5f\u5c31\u662f\u4e0a\u56fe\u4e2d\u7684 Difficulty Tagrget\uff0c\u8868\u660e\u4e86\u5f53\u524d\u7684 hash \u751f\u6210\u7684\u96be\u5ea6\u30024 \u4e2a\u5b57\u8282</li> <li><code>Nonce</code>\uff1a\u4e00\u4e2a\u968f\u673a\u503c\uff0c\u7528\u4e8e\u627e\u5230\u6ee1\u8db3\u67d0\u4e2a\u6761\u4ef6\u7684 hash \u503c\u30024 \u5b57\u8282</li> </ul> <blockquote> <p>\u5bf9\u8fd9\u516d\u5b57\u6bb5\u8fdb\u884c hash \u8ba1\u7b97\uff0c\u5c31\u53ef\u4ee5\u5f97\u5230\u672c\u533a\u5757\u7684 hash \u503c\uff0c\u4e5f\u5c31\u662f\u5176 ID \u6216\u662f\u5730\u5740</p> </blockquote> <pre><code>SHA-256(SHA-256 (Block Header))</code></pre><blockquote> <p>\u5f53\u7136\uff0c\u4e8b\u60c5\u5e76\u6ca1\u6709\u8fd9\u4e48\u7b80\u5355\u3002\u6bd4\u7279\u5e01\u5bf9\u8fd9\u4e2a <code>hash</code> \u503c\u662f\u6709\u8981\u6c42\u7684\uff0c\u5176\u8981\u6c42\u662f\u90a3\u4e2a <code>Bits</code> \u5b57\u6bb5\u63a7\u5236\u7684\uff0c\u7136\u540e\u4f60\u53ef\u4ee5\u8c03\u6574 <code>Nonce</code> \u8fd9\u4e2a 32 \u4f4d\u6574\u578b\u7684\u503c\u6765\u627e\u5230\u7b26\u5408\u6761\u4ef6\u7684 <code>hash</code> \u503c\u3002\u6211\u4eec\u628a\u8fd9\u4e2a\u4e8b\u60c5\u53eb\u505a <strong>\u6316\u77ff</strong></p> </blockquote> <h3 id="22-merkle-root">2.2 Merkle Root</h3> <p><strong>\u6bd4\u7279\u5e01\u7684 Merkle Root \u662f\u600e\u4e48\u8ba1\u7b97\u51fa\u6765\u7684\uff1a</strong></p> <ol> <li>\u6bd4\u7279\u5e01\u7684\u6bcf\u4e00\u7b14\u4ea4\u6613\u4f1a\u6709\u4e09\u4e2a\u5b57\u6bb5\uff0c\u4e00\u4e2a\u662f\u8f6c\u51fa\u65b9\uff0c\u4e00\u4e2a\u662f\u8f6c\u5165\u65b9\uff0c\u8fd8\u6709\u4e00\u4e2a\u662f\u91d1\u989d</li> <li>\u5bf9\u6bcf\u4e2a\u4ea4\u6613\u7684\u8fd9\u4e09\u4e2a\u5b57\u6bb5\u6c42 hash</li> <li>\u7136\u540e\u628a\u4ea4\u6613\u7684 hash \u505a\u4e24\u4e24\u5408\u5e76\uff0c\u518d\u6c42\u5176 hash</li> <li>\u76f4\u5230\u7b97\u51fa\u6700\u540e\u4e00\u4e2a hash \u503c\uff0c\u8fd9\u5c31\u662f\u6211\u4eec\u7684 <code>Merkle Root</code></li> </ol> <p><img src="'+a+'" alt=""></p> <p>\u4e0b\u56fe\u662f\u4e00\u4e2a\u533a\u5757\u94fe\u7684\u793a\u610f\u56fe\uff0c\u6765\u81ea\u6bd4\u7279\u5e01\u7684\u767d\u76ae\u4e66\uff1a</p> <p><img src="'+u+'" alt=""></p> <blockquote> <p>\u4e3a\u4ec0\u4e48\u8981\u8fd9\u6837\u505a\u5462\uff1f\u4e3a\u4ec0\u4e48\u4e0d\u662f\u628a\u6240\u6709\u7684\u4ea4\u6613\u90fd\u653e\u5728\u4e00\u8d77\u505a\u4e00\u6b21 hash \u5462\uff1f\u8fd9\u4e0d\u4e5f\u53ef\u4ee5\u8ba9\u4eba\u65e0\u6cd5\u7be1\u6539\u5417\uff1f\u8fd9\u6837\u505a\u7684\u597d\u5904\u662f\u2014\u2014\u6211\u4eec\u628a\u4ea4\u6613\u6570\u636e\u5206\u6210\u4e86\u82e5\u5e72\u4e2a\u7ec4\u3002\u5c31\u50cf\u4e0a\u9762\u90a3\u4e2a\u4e8c\u53c9\u6811\u6240\u8868\u793a\u7684\u4e00\u6837\uff0c\u6211\u4eec\u53ef\u4ee5\u4e0d\u65ad\u5730\u628a\u8fd9\u4e2a\u6811\u5206\u6210\u5de6\u8fb9\u7684\u5206\u652f\u548c\u53f3\u8fb9\u7684\u5206\u652f\uff0c\u56e0\u4e3a\u5b83\u4eec\u90fd\u88ab\u8ba1\u7b97\u8fc7 hash \u503c\uff0c\u6240\u4ee5\u53ef\u4ee5\u5f88\u5feb\u5730\u6821\u9a8c\u5176\u4e2d\u7684\u5185\u5bb9\u6709\u6ca1\u6709\u88ab\u4fee\u6539\u8fc7</p> </blockquote> <p><strong>\u8fd9\u81f3\u5c11\u5e26\u6765\u4e09\u4e2a\u597d\u5904\uff1a</strong></p> <ol> <li>\u5927\u91cf\u7684\u4ea4\u6613\u6570\u636e\u53ef\u4ee5\u88ab\u5206\u6210\u5404\u79cd\u5c3a\u5bf8\u7684\u5c0f\u7ec4\uff0c\u8fd9\u6837\u6709\u5229\u4e8e\u6211\u4eec\u6574\u5408\u6570\u636e\u548c\u6821\u9a8c\u6570\u636e</li> <li>\u8fd9\u6837\u7684\u5f00\u9500\u5728\u5b58\u50a8\u548c\u5185\u5b58\u4e0a\u5e76\u4e0d\u5927\uff0c\u7136\u800c\u6211\u4eec\u53ef\u4ee5\u63d0\u9ad8\u6821\u9a8c\u4e00\u7ec4\u6570\u636e\u7684\u96be\u6613\u7a0b\u5ea6</li> <li>\u5728 P2P \u7684\u65e0\u4e2d\u5fc3\u5316\u7f51\u7edc\u4e0a\uff0c\u6211\u4eec\u53ef\u4ee5\u628a\u5927\u91cf\u6570\u636e\u62c6\u6210\u4e00\u4e2a\u4e00\u4e2a\u5c0f\u6570\u636e\u7247\u4f20\u8f93\uff0c\u53ef\u4ee5\u63d0\u9ad8\u7f51\u7edc\u7684\u4f20\u8f93\u901f\u5ea6</li> </ol> <h3 id="23-\u6bd4\u7279\u5e01\u7684\u4ea4\u6613\u6a21\u578b">2.3 \u6bd4\u7279\u5e01\u7684\u4ea4\u6613\u6a21\u578b</h3> <p>\u6bd4\u7279\u5e01\u4ea4\u6613\u4e2d\u7684\u4e24\u4e2a\u672f\u8bed\uff1a</p> <ul> <li><p><code>input</code> \u652f\u51fa\u65b9</p> </li> <li><p><code>output</code> \u6536\u5165\u65b9</p> </li> <li><p>\u4e00\u4e2a\u4ea4\u6613\u53ef\u4ee5\u6709\u591a\u4e2a output\uff0c\u4e5f\u5c31\u662f\u8bf4\u6211\u53ef\u4ee5\u628a\u4e00\u7b14\u94b1\u6c47\u7ed9\u591a\u4e2a\u4eba</p> </li> <li><p>output \u8ddf input \u7684\u603b\u6570\u8981\u543b\u5408</p> </li> </ul> <blockquote> <p><strong>UTXO\uff1a</strong> \uff08Unspent Transaction Output\uff09\u6ca1\u6709\u82b1\u51fa\u53bb\u7684\u4ea4\u6613\u3002\u5047\u8bbe\uff0cFred \u7ed9\u4e86 Alice 2 \u4e2a\u6bd4\u7279\u5e01\uff0cTed \u7ed9\u4e86 Alice 3 \u4e2a\u6bd4\u7279\u5e01\uff0c\u8fd9\u4e2a\u65f6\u5019\uff0cAlice \u6709 5 \u4e2a\u6bd4\u7279\u5e01\u3002\u6bd4\u7279\u5e01\u91cc\u662f\u6ca1\u6709\u4f59\u989d\u7684\uff0c\u6240\u4ee5\uff0c\u5bf9\u4e8e Alice \u6765\u8bf4\uff0c\u5979\u53ea\u6709\u4e24\u4e2a\u6ca1\u6709\u82b1\u51fa\u53bb\u7684\u4ea4\u6613\uff0c\u4e00\u4e2a\u662f 2 \u4e2a\u6bd4\u7279\u5e01\uff0c\u4e00\u4e2a\u662f 3 \u4e2a\u6bd4\u7279\u5e01\u3002\u7cfb\u7edf\u8ba1\u7b97\u4f59\u989d\u4e8b\u5c31\u662f\u8ba1\u7b97 UTXO</p> </blockquote> <p>\u5982\u679c Alice \u60f3\u8981\u8f6c\u7ed9 Bob 4 \u4e2a\u6bd4\u7279\u5e01\uff0c\u5979\u53d1\u73b0\u81ea\u5df1\u7684\u4e24\u4e2a\u4ea4\u6613\u4e2d\u90fd\u4e0d\u591f\uff0c\u4e5f\u4e0d\u80fd\u62c6\u5f00\u4e4b\u524d\u7684\u90a3\u4e24\u4e2a\u6bd4\u7279\u5e01\u4ea4\u6613\uff0c\u90a3\u4e48\u5979\u53ea\u80fd\u628a\u4ea4\u6613 2 \u548c\u4ea4\u6613 3 \u5f53\u6210 input\uff0c\u7136\u540e\u628a\u81ea\u5df1\u548c Bob \u5f53\u6210 output\uff0cBob \u5206\u5f97 4 \u4e2a\uff0c\u5979\u81ea\u5df1\u5206 1 \u4e2a</p> <p><img src="'+k+'" alt=""></p> <p>UTXO \u56e0\u4e3a\u6ca1\u6709\u8d26\u6237\u548c\u4f59\u989d\u7684\u6982\u5ff5\uff0c\u6240\u4ee5\u53ef\u4ee5\u5e76\u884c\u8fdb\u884c\u591a\u7b14\u4ea4\u6613\u3002\u5047\u5982\u4f60\u6709\u591a\u4e2a UTXO\uff0c\u4f60\u53ef\u4ee5\u8fdb\u884c\u591a\u7b14\u4ea4\u6613\u800c\u4e0d\u9700\u8981\u5e76\u884c\u9501\u3002\u7136\u540e\u5176\u8fd8\u6709\u533f\u540d\u6027\u7684\u7279\u5f81\uff0c\u4f60\u53ef\u4ee5\u9690\u85cf\u81ea\u5df1\u7684\u4ea4\u6613\u76ee\u7684\u5730\uff08\u901a\u8fc7\u8bbe\u7f6e\u7684\u591a\u4e2a output\uff09\uff0c\u800c\u4e14\u6ca1\u6709\u4f59\u989d\u610f\u5473\u7740\u662f\u6ca1\u6709\u72b6\u6001\u7684\u3002\u8981\u77e5\u9053\u4f60\u6709\u591a\u5c11\u4e2a\u6bd4\u7279\u5e01\uff0c\u53ea\u9700\u8981\u628a UTXO \u7684\u4ea4\u6613\u8bb0\u5f55\u7edf\u8ba1\u4e00\u4e0b\u5c31\u53ef\u4ee5\u77e5\u9053\u4e86</p> <h2 id="3-\u52a0\u5bc6\u65b9\u5f0f">3 \u52a0\u5bc6\u65b9\u5f0f</h2> <h3 id="31-\u5bc6\u94a5\u5bf9">3.1 \u5bc6\u94a5\u5bf9</h3> <p>\u6240\u8c13\u5bc6\u94a5\u5bf9\uff0c\u4e5f\u5c31\u662f\u4e00\u79cd\u975e\u5bf9\u79f0\u52a0\u5bc6\u6280\u672f\u3002\u5728\u5bf9\u4fe1\u606f\u8fdb\u884c\u52a0\u5bc6\u548c\u89e3\u5bc6\u65f6\uff0c\u4f7f\u7528\u4e24\u4e2a\u4e0d\u540c\u7684\u5bc6\u94a5</p> <ul> <li><strong>\u516c\u94a5</strong>\uff1a\u516c\u5e03\u51fa\u53bb\uff0c\u4efb\u4f55\u4eba\u90fd\u80fd\u770b\u5230\u3002\u53ea\u80fd\u52a0\u5bc6\uff0c\u89e3\u5bc6\u9700\u8981\u79c1\u94a5\uff0c\u4fe1\u606f\u4f7f\u7528\u516c\u94a5\u52a0\u5bc6\u540e\u53ea\u80fd\u7528\u79c1\u94a5\u89e3\u5bc6\uff0c\u5176\u4ed6\u4eba\u65e0\u6cd5\u89e3\u5bc6 (\u53ef\u4ee5\u4f7f\u7528\u516c\u94a5\u52a0\u5bc6\uff0c\u79c1\u94a5\u89e3\u5bc6\uff0c\u4e5f\u6709\u53cd\u8fc7\u6765\u7528\u79c1\u94a5\u52a0\u5bc6\uff0c\u516c\u94a5\u89e3\u5bc6)</li> <li><strong>\u79c1\u94a5</strong>\uff1a\u53ea\u80fd\u81ea\u5df1\u770b\u5230\u3002\u7528\u6765\u89e3\u5bc6</li> </ul> <h3 id="32-\u6570\u5b57\u7b7e\u540d">3.2 \u6570\u5b57\u7b7e\u540d</h3> <p><strong>\u975e\u5bf9\u79f0\u52a0\u5bc6\u7684\u7f3a\u9677\uff1a</strong></p> <p>\u6bcf\u4e2a\u4eba\u90fd\u6709\u6211\u7684\u516c\u94a5\uff0c\u522b\u4eba\u53ef\u4ee5\u622a\u83b7\u53d1\u4ef6\u4eba\u53d1\u7ed9\u6211\u7684\u4fe1\u606f\uff0c\u7136\u540e\u81ea\u5df1\u7528\u6211\u7684\u516c\u94a5\u52a0\u5bc6\u4e00\u4e2a\u522b\u7684\u4fe1\u606f\uff0c\u4f2a\u88c5\u6210\u53d1\u4ef6\u4eba\u53d1\u7ed9\u6211\uff0c\u8fd9\u6837\u6211\u5c31\u88ab\u9ed1\u4e86</p> <p>\u6240\u4ee5\uff0c\u6211\u4eec\u9700\u8981\u5bf9\u53d1\u4ef6\u4eba\u7684\u8eab\u4efd\u8fdb\u884c\u9a8c\u8bc1\uff0c\u6b64\u65f6\u5c31\u9700\u8981\u7528\u5230 <strong>\u6570\u5b57\u7b7e\u540d</strong> \u7684\u6982\u5ff5\u4e86</p> <p><strong>\u6570\u5b57\u7b7e\u540d\u7684\u52a0\u5bc6\u8fc7\u7a0b\uff1a</strong></p> <ol> <li>Mike(\u53d1\u4ef6\u4eba) \u4e5f\u6709\u4e00\u5bf9\u5bc6\u94a5\u5bf9\uff0c\u4e00\u4e2a\u516c\u94a5\u7ed9\u4e86 Jack\uff0c\u79c1\u94a5\u81ea\u5df1\u4fdd\u7559</li> <li>Mike(\u53d1\u4ef6\u4eba) \u628a\u9700\u8981\u53d1\u9001\u7684\u4fe1\u606f\u505a\u4e2a hash\uff0c\u5f97\u5230\u4e00\u4e2a hash \u4e32\uff0c\u53c8\u53eb <strong>Digest</strong></li> <li>Mike(\u53d1\u4ef6\u4eba) \u7528\u81ea\u5df1\u7684\u79c1\u94a5\uff0c\u628a Digest \u52a0\u5bc6\uff0c\u5f97\u5230\u4e00\u6bb5 Digest \u7684\u5bc6\u6587\u3002\u6211\u4eec\u628a\u8fd9\u4e2a\u8fc7\u7a0b\u53eb <strong>\u6570\u5b57\u7b7e\u540d</strong>\uff0cSignature</li> <li>\u7136\u540e\uff0cMike(\u53d1\u4ef6\u4eba) \u628a\u9700\u8981\u53d1\u9001\u7684\u4fe1\u606f\u7528\u6211\u7684\u516c\u94a5\u52a0\u5bc6\u540e\uff0c\u8fde\u540c\u4ed6\u7684\u6570\u5b57\u7b7e\u540d\u4e00\u540c\u53d1\u7ed9 Jack</li> <li>Jack \u7528\u81ea\u5df1\u7684\u79c1\u94a5\u89e3\u5bc6 Mike \u53d1\u7ed9\u4ed6\u7684\u5bc6\u6587\uff0c\u7136\u540e\u7528 Mike \u7684\u516c\u94a5\u89e3\u5bc6\u5176\u6570\u5b57\u7b7e\u540d\u5f97\u5230 Digest</li> <li>\u7136\u540e\uff0c\u5bf9\u89e3\u5f00\u7684\u5bc6\u6587\u505a Hash\u3002\u5982\u679c\u7ed3\u679c\u548c Digest \u4e00\u81f4\uff0c\u5c31\u8bf4\u660e\uff0c\u8fd9\u4e2a\u4fe1\u606f\u662f Mike \u53d1\u9001\u7684\uff0c\u6ca1\u6709\u4eba\u66f4\u6539\u8fc7</li> </ol> <p><img src="'+P+'" alt=""></p> <h3 id="33-\u8bc1\u4e66">3.3 \u8bc1\u4e66</h3> <p><strong>\u4e2d\u95f4\u4eba\u653b\u51fb\uff1a</strong></p> <ol> <li>\u5047\u8bbe\u6709\u4e2a\u9ed1\u5ba2\u5077\u5077\u5730\u628a Jack(\u6536\u4ef6\u4eba) \u7535\u8111\u4e0a\u7684 Mike \u7684\u516c\u94a5\u7ed9\u6362\u4e86\uff0c\u6362\u6210\u81ea\u5df1\u7684</li> <li>\u7136\u540e\u622a\u83b7 Mike \u53d1\u51fa\u6765\u7684\u4fe1\u606f\uff0c\u7528\u81ea\u5df1\u7684\u5bc6\u94a5\u52a0\u5bc6\u4e00\u6bb5\u81ea\u5df1\u7684\u4fe1\u606f\uff0c\u4ee5\u53ca\u81ea\u5df1\u7684\u6570\u5b57\u7b7e\u540d</li> <li>\u5bf9\u4e8e Jack(\u6536\u4ef6\u4eba) \u6765\u770b\uff0c\u56e0\u4e3a\u4ed6\u7528\u4e86\u9ed1\u5ba2\u7684\u516c\u94a5\uff0c\u800c\u4e0d\u662f Mike \u7684</li> <li>\u90a3\u4e48\u5bf9\u4ed6\u6765\u8bf4\uff0c\u4ed6\u5c31\u4ee5\u4e3a\u4fe1\u606f\u6765\u81ea Mike\uff0c\u4e8e\u662f\u9ed1\u5ba2\u53ef\u4ee5\u7528\u81ea\u5df1\u7684\u79c1\u94a5\u4f2a\u88c5\u6210 Mike \u7ed9 Jack \u901a\u4fe1</li> </ol> <p><strong>\u8bc1\u4e66\uff1a</strong></p> <ol> <li>\u4e3a\u4e86\u5e94\u5bf9\u4e2d\u95f4\u4eba\u653b\u51fb\uff0c\u9700\u8981\u4fdd\u8bc1\u81ea\u5df1\u7684\u516c\u94a5\u5319\u4e0d\u88ab\u7be1\u6539</li> <li>\u8fd9\u5c31\u9700\u8981\u4e00\u4e2a\u6743\u5a01\u673a\u6784\u6765\u8ba4\u8bc1\u516c\u94a5\u3002\u6743\u5a01\u673a\u6784\uff0c\u7528\u81ea\u5df1\u7684\u79c1\u94a5\u628a Mike(\u53d1\u4ef6\u4eba) \u7684\u516c\u94a5\u548c\u5176\u76f8\u5173\u4fe1\u606f\u4e00\u8d77\u52a0\u5bc6\uff0c\u751f\u6210\u4e00\u4e2a <strong>\u8bc1\u4e66</strong></li> <li>Mike(\u53d1\u4ef6\u4eba) \u53ea\u9700\u8981\u5728\u53d1\u9001\u4fe1\u606f\u7684\u65f6\u5019\u653e\u4e0a\u8fd9\u4e2a\u6743\u5a01\u673a\u6784\u53d1\u7684\u6570\u5b57\u8bc1\u4e66</li> <li>\u7136\u540e Jack(\u6536\u4ef6\u4eba) \u7528\u8fd9\u4e2a <code>\u6743\u5a01\u673a\u6784\u7684\u516c\u94a5</code> \u89e3\u5bc6\u8fd9\u4e2a\u8bc1\u4e66\uff0c\u5f97\u5230 Mike \u7684\u516c\u94a5</li> <li>\u518d\u7528 Mike \u7684\u516c\u94a5\u6765\u9a8c\u8bc1 Mike \u7684\u6570\u5b57\u7b7e\u540d</li> </ol> <h2 id="4-\u6bd4\u7279\u5e01\u7684\u52a0\u5bc6">4 \u6bd4\u7279\u5e01\u7684\u52a0\u5bc6</h2> <p>\u5728\u6bd4\u7279\u5e01\u4e2d\uff0c\u6bcf\u4e00\u7b14\u4ea4\u6613\u7684 From \u548c To \u90fd\u662f\u6bcf\u4e2a\u7528\u6237\u7684\u516c\u94a5\uff08Public Key\uff09\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u4f7f\u7528\u7528\u6237\u7684\u516c\u94a5\u6765\u505a\u4ea4\u6613\u7684\u8d26\u6237</p> <p>\u4ea4\u6613\u7684\u53d1\u8d77\u65b9\u53ea\u80fd\u662f\u652f\u4ed8\u65b9\uff0c\u652f\u4ed8\u65b9\u9700\u8981\u7528\u81ea\u5df1\u7684\u79c1\u94a5\u6765\u52a0\u5bc6\u4ea4\u6613\u4fe1\u606f\u5e76\u5236\u4f5c\u76f8\u5173\u7684\u4ea4\u6613\u7b7e\u540d</p> <p>\u7f51\u7edc\u4e0a\u5176\u4ed6\u4eba\u4f1a\u7528\u652f\u4ed8\u65b9\u7684\u516c\u94a5\uff08\u4e5f\u5c31\u662f\u4ea4\u6613\u7684\u652f\u51fa\u65b9\uff09\u6765\u505a\u89e3\u5bc6\u6765\u9a8c\u8bc1</p> <blockquote> <p>\u4e3a\u4ec0\u4e48\u4e0d\u9700\u8981\u90a3\u4e2a\u8bc1\u4e66\u673a\u6784\u5462\uff1f\u4e0d\u6015\u4e2d\u95f4\u4eba\u653b\u51fb\u5417\uff1f\u8fd9\u662f\u56e0\u4e3a\uff0c\u5982\u679c\u9ed1\u5ba2\u60f3\u8981\u4f2a\u9020\u4e00\u7b14\u522b\u4eba\u7684\u4ea4\u6613\uff0c\u90a3\u4e48\u4ed6\u9700\u8981\u6362\u6389\u534a\u6570\u4ee5\u4e0a\u7ed3\u70b9\u4e0a\u7684\u88ab\u653b\u51fb\u8005\u7684\u516c\u94a5\uff0c\u8fd9\u4e0d\u592a\u73b0\u5b9e</p> </blockquote> <p>\u4e0b\u9762\u662f\u4e00\u4e2a\u4ea4\u6613\u94fe\u7684\u56fe\u793a\u3002\u8fd9\u4e2a\u4ea4\u6613\u94fe\u7684\u94b1\u4ece A -&gt; B -&gt; C -&gt; D\uff0c\u4e00\u5171 3 \u7b14\u4ea4\u6613\uff1a</p> <p><img src="'+b+'" alt=""></p> <ul> <li><strong>\u53d1\u8d77\u4ea4\u6613</strong>\uff1a\u6211\u4eec\u4ece\u7b2c\u4e00\u7b14\u4ea4\u6613\u53ef\u4ee5\u770b\u5230\uff0cA \u7528\u81ea\u5df1\u7684\u79c1\u94a5\u4e3a\u4ea4\u6613\u4fe1\u606f\u548c\u81ea\u5df1\u7684\u5730\u5740\u751f\u6210\u4e86\u4ea4\u6613\u7684\u7b7e\u540d\uff0c\u7136\u540e\u628a\u4ea4\u6613\u4fe1\u606f\u3001\u81ea\u5df1\u7684\u5730\u5740\u3001\u4ea4\u6613\u7b7e\u540d\u548c\u81ea\u5df1\u7684\u516c\u94a5\u653e\u51fa\u53bb\uff0c\u8fd9\u6837\u65b9\u4fbf\u522b\u4eba\u6765\u9a8c\u8bc1\u7684\u786e\u662f A \u53d1\u8d77\u7684</li> <li><strong>\u9a8c\u8bc1\u4ea4\u6613</strong>\uff1a\u5728\u9a8c\u8bc1\u65f6\uff0c\u4f7f\u7528 A \u7684\u516c\u94a5\u89e3\u5bc6\u4ea4\u6613\u7b7e\u540d\uff0c\u5f97\u5230\u4ea4\u6613\u7684 hash \u503c\u3002\u628a\u4ea4\u6613\u4fe1\u606f\u548c\u81ea\u5df1\u7684\u5730\u5740\u505a hash\uff0c\u770b\u770b\u662f\u4e0d\u662f\u548c\u7b7e\u540d\u89e3\u5bc6\u540e\u7684 hash \u503c\u4e00\u81f4</li> </ul> <h2 id="5-\u6bd4\u7279\u5e01\u7684\u6316\u77ff">5 \u6bd4\u7279\u5e01\u7684\u6316\u77ff</h2> <h3 id="51-\u6316\u77ff\u7684\u6982\u5ff5">5.1 \u6316\u77ff\u7684\u6982\u5ff5</h3> <p>\u524d\u9762\u8bf4\u5230\uff0c\u5728\u6bd4\u7279\u5e01\u7684\u533a\u5757 hash \u7b97\u6cd5\u4e2d\uff0c\u8981\u786e\u4fdd\u4e0b\u9762\u8fd9\u4e2a\u516c\u5f0f\u6210\u7acb\uff1a</p> <pre><code>SHA-256(SHA-256 (Block Header)) &lt; Target</code></pre><p>\u800c\u5728\u533a\u5757\u5934\u4e2d\uff0c\u53ef\u4ee5\u5b8c\u5168\u81ea\u7531\u4fee\u6539\u7684\u53ea\u6709\u4e00\u4e2a\u5b57\u6bb5\uff0c\u5c31\u662f Nonce\uff0c\u5176\u4ed6\u7684 Timestamp \u53ef\u4ee5\u5728\u7279\u5b9a\u8303\u56f4\u5185\u4fee\u6539\uff0cMerkle Root \u548c\u4f60\u9700\u8981\u8bb0\u5f55\u7684\u4ea4\u6613\u4fe1\u606f\u6709\u5173\u7cfb <strong>\u6240\u6709\u7684\u77ff\u5de5\u53ef\u4ee5\u81ea\u7531\u5730\u4ece\u5f85\u786e\u8ba4\u4ea4\u6613\u5217\u8868\u4e2d\u6311\u9009\u81ea\u5df1\u60f3\u8981\u7684\u4ea4\u6613\u6253\u5305</strong></p> <blockquote> <p>Target \u662f\u4e00\u4e2a\u6570\uff0c\u8868\u793a\u8ba1\u7b97\u51fa\u6765\u7684 hash \u503c\u7684\u5b57\u7b26\u4e32\u6700\u524d\u9762\u6709\u51e0\u4e2a\u96f6\u3002\u6211\u4eec\u628a\u8fd9\u4e2a\u64cd\u4f5c\u53eb\u201c\u54c8\u5e0c\u78b0\u649e\u201d(hash collision)\uff0c\u78b0\u649e\u524d\u51e0\u4e2a\u4f4d\u90fd\u662f 0 \u7684\u54c8\u5e0c\u503c\u3002\u8fd9\u4e2a Target \u662f\u7531\u533a\u5757\u5934\u4e2d\u7684 Bits \u8fd9\u4e2a\u5b57\u6bb5\u63a7\u5236\u7684\uff0c\u4e5f\u5c31\u662f\u96be\u5ea6\u7cfb\u6570</p> </blockquote> <p>\u8fd9\u4e2a\u96be\u5ea6\u7cfb\u6570\uff0c\u4f1a\u5728\u6bcf\u51fa 2016 \u4e2a\u533a\u5757\u540e\u5c31\u8c03\u6574\u4e00\u6b21\u3002\u73b0\u5728\uff0c\u8fd9\u4e2a\u96be\u5ea6\u662f\u8981\u5728\u524d\u9762\u627e\u5230\u6709 18 \u4e2a\u96f6\u3002\u5982\u4e0b\u6240\u793a (\u4e00\u4e2a\u771f\u5b9e\u7684\u533a\u5757\u94fe\u7684 Hash \u503c)\uff1a</p> <pre><code>000000000000000000424118cc80622cb26c07b69fbe2bdafe57fea7d5f59d68</code></pre><blockquote> <p>\u5f88\u6709\u53ef\u80fd\u7a77\u4e3e\u5b8c Nonce \u540e\u8fd8\u627e\u4e0d\u5230\uff0c\u90a3\u5c31\u53ea\u80fd\u8c03\u6574 Timestamp \u548c Merkle Root\uff08\u8c03\u6574\u4e0d\u540c\u7684\u8bb0\u8d26\u4ea4\u6613\uff09\u4e86</p> </blockquote> <h3 id="52-\u6316\u77ff\u7684\u6d41\u7a0b">5.2 \u6316\u77ff\u7684\u6d41\u7a0b</h3> <p><strong>\u4e00\u822c\u7684\u6316\u77ff\u6d41\u7a0b\u5982\u4e0b\uff1a</strong></p> <ol> <li>\u4ece\u7f51\u7edc\u4e0a\u53d6\u5f97\u4e4b\u524d\u7684\u533a\u5757\u4fe1\u606f</li> <li>\u4ece <code>\u5f85\u8bb0\u8d26\u533a</code> \u4e2d\u83b7\u53d6\u4e00\u7ec4\u4ea4\u6613\u6570\u636e\uff08\u6709\u4f18\u5148\u7ea7\uff0c\u6bd4\u5982\u6210\u957f\u65f6\u95f4\u3001\u77ff\u5de5\u5c0f\u8d39\u7b49\uff09</li> <li>\u5f62\u6210\u533a\u5757\u5934\uff08\u8ba1\u7b97 <code>Merkle Root</code> \u5e76\u8bbe\u8ba1\u8bb0\u8d26\u65f6\u95f4 <code>Timestamp</code> \u7b49\uff09</li> <li>\u5f00\u59cb\u7a77\u4e3e <code>Nonce</code>\uff0c\u6765\u8ba1\u7b97\u533a\u5757\u5934\u7684 <code>hash</code> \u503c\u3002\u5982\u679c\u524d\u9762\u6709 18 \u4e2a\u96f6\uff08\u5c0f\u4e8e Target\uff09\uff0c\u90a3\u4e48\u8bb0\u8d26\u6210\u529f\u3002\u5982\u679c\u6ca1\u6709\uff0c\u5219\u4ece\u7b2c\u4e00\u6b65\u91cd\u65b0\u5f00\u59cb</li> <li>\u4e00\u65e6\u67d0\u77ff\u5de5\u6210\u529f\u6253\u5305\u4e00\u4e2a\u533a\u5757\uff0c\u4ed6\u5c31\u4f1a\u544a\u8bc9\u5176\u4ed6\u77ff\u5de5\u3002\u6536\u5230\u6d88\u606f\u7684\u77ff\u5de5\u4f1a\u505c\u4e0b\u624b\u4e0a\u7684\u5de5\u4f5c\uff0c\u5f00\u59cb\u9a8c\u8bc1\uff0c\u9a8c\u8bc1\u901a\u8fc7\u540e\uff0c\u5e7f\u64ad\u7ed9\u5176\u4ed6\u77ff\u5de5</li> </ol> <p>\u8bbe\u7f6e\u8fd9\u4e2a\u96be\u5ea6\u7cfb\u6570\u5c31\u662f\u4e3a\u4e86\u8ba9\u5168\u7f51\u4ea7\u751f\u7684\u533a\u57df\u540d\u5e73\u5747\u5728 10 \u5206\u949f\u4e00\u5757\u3002\u800c\u6839\u636e\u6bd4\u7279\u5e01\u65e0\u4e2d\u5fc3\u670d\u52a1\u5668\u7684\u67b6\u6784\uff0c\u4e5f\u5c31\u662f\u5176\u6316\u77ff\u7684\u673a\u5668\u6570\u91cf\u662f\u60f3\u6765\u5c31\u6765\u60f3\u8d70\u5c31\u8d70\u7684\uff0c\u8ba1\u7b97\u529b\u53ef\u80fd\u4f1a\u4e0d\u4e00\u6837\u3002\u56e0\u6b64\uff0c\u4e3a\u4e86\u4fdd\u8bc1\u6bcf 10 \u5206\u949f\u4ea7\u751f\u4e00\u4e2a\u533a\u5757\uff0c\u5f53\u7b97\u529b\u4e0d\u8db3\u7684\u65f6\u5019\uff0c\u96be\u5ea6\u4e0b\u964d\uff0c\u5f53\u7b97\u529b\u5145\u8db3\u7684\u65f6\u5019\uff0c\u96be\u5ea6\u63d0\u9ad8</p> <h2 id="6-\u5171\u8bc6\u673a\u5236">6 \u5171\u8bc6\u673a\u5236</h2> <h3 id="61-\u62dc\u5360\u5ead\u95ee\u9898">6.1 \u62dc\u5360\u5ead\u95ee\u9898</h3> <p>\u62dc\u5360\u5ead\u5c06\u519b\u95ee\u9898\uff08The Byzantine Generals Problem\uff09\uff0c\u5b83\u662f\u83b1\u65af\u5229\xb7\u5170\u4f2f\u7279\uff08Leslie Lamport\uff09\u4e8e 1982 \u5e74\u63d0\u51fa\u6765\u7684\uff0c\u7528\u6765\u89e3\u91ca\u4e00\u81f4\u6027\u95ee\u9898\u7684\u4e00\u4e2a\u865a\u6784\u6a21\u578b\u3002\u540c\u65f6\uff0c\u5b83\u4e5f\u662f\u5206\u5e03\u5f0f\u9886\u57df\u4e2d\u6700\u590d\u6742\u3001\u6700\u4e25\u683c\u7684\u5bb9\u9519\u6a21\u578b</p> <p>\u65e0\u8bba\u4f60\u662f\u641e\u533a\u5757\u94fe\uff0c\u8fd8\u662f\u641e\u5206\u5e03\u5f0f\uff0c\u4f60\u90fd\u9700\u8981\u77e5\u9053\u62dc\u5360\u5ead\u5bb9\u9519\u7cfb\u7edf\u7814\u7a76\u4e2d\u7684\u4e09\u4e2a\u91cd\u8981\u7406\u8bba\uff1aCAP\u3001FLP \u548c DLS</p> <ul> <li><strong>CAP</strong>\uff1a\u5728\u7f51\u7edc\u53d1\u751f\u963b\u65ad\uff08partition\uff09\u65f6\uff0c\u53ea\u80fd\u9009\u62e9\u6570\u636e\u7684\u4e00\u81f4\u6027\uff08consistency\uff09\u6216\u53ef\u7528\u6027\uff08availability\uff09\uff0c\u65e0\u6cd5\u4e24\u8005\u517c\u5f97</li> <li><strong>FLP</strong>\uff1a\u5728\u5f02\u6b65\u73af\u5883\u4e2d\uff0c\u5982\u679c\u8282\u70b9\u95f4\u7684\u7f51\u7edc\u5ef6\u8fdf\u6ca1\u6709\u4e0a\u9650\uff0c\u53ea\u8981\u6709\u4e00\u4e2a\u6076\u610f\u8282\u70b9\u5b58\u5728\uff0c\u5c31\u6ca1\u6709\u7b97\u6cd5\u80fd\u5728\u6709\u9650\u7684\u65f6\u95f4\u5185\u8fbe\u6210\u5171\u8bc6</li> <li><strong>DLS</strong>\uff1a\u5bb9\u9519\u7684\u4e0a\u9650\uff1a<ul> <li>\u5728\u90e8\u5206\u540c\u6b65\u7684\u7f51\u7edc\u73af\u5883\u4e2d\uff08\u5373\u7f51\u7edc\u5ef6\u8fdf\u6709\u4e00\u5b9a\u7684\u4e0a\u9650\uff0c\u4f46\u6211\u4eec\u65e0\u6cd5\u4e8b\u5148\u77e5\u9053\u4e0a\u9650\u662f\u591a\u5c11\uff09\uff0c\u534f\u8bae\u53ef\u4ee5\u5bb9\u5fcd\u6700\u591a 1/3 \u7684\u62dc\u5360\u5ead\u6545\u969c</li> <li>\u5728\u5f02\u6b65\u7f51\u7edc\u73af\u5883\u4e2d\uff0c\u5177\u786e\u5b9a\u6027\u8d28\u7684\u534f\u8bae\u65e0\u6cd5\u5bb9\u5fcd\u4efb\u4f55\u9519\u8bef</li> <li>\u5728\u540c\u6b65\u7f51\u7edc\u73af\u5883\u4e2d\uff08\u7f51\u7edc\u5ef6\u8fdf\u6709\u4e0a\u9650\u4e14\u4e0a\u9650\u662f\u5df2\u77e5\u7684\uff09\uff0c\u534f\u8bae\u53ef\u4ee5\u5bb9\u5fcd 100% \u7684\u62dc\u5360\u5ead\u6545\u969c\u3002\u4f46\u5f53\u8d85\u8fc7 1/2 \u7684\u8282\u70b9\u4e3a\u6076\u610f\u8282\u70b9\u65f6\uff0c\u4f1a\u6709\u4e00\u4e9b\u9650\u5236\u6761\u4ef6</li> </ul> </li> </ul> <h3 id="62-\u5206\u5e03\u5f0f\u4e00\u81f4\u6027\u7b97\u6cd5">6.2 \u5206\u5e03\u5f0f\u4e00\u81f4\u6027\u7b97\u6cd5</h3> <ul> <li><strong>PoW</strong>\uff1aProof of Work\uff0c\u6bd4\u7279\u5e01\u7684\u533a\u5757\u94fe\u7f51\u7edc\u4f7f\u7528 PoW</li> </ul> <h3 id="63-pow-\u5de5\u4f5c\u91cf\u8bc1\u660e">6.3 Pow \u5de5\u4f5c\u91cf\u8bc1\u660e</h3> <p><strong>\u6982\u5ff5\uff1a</strong></p> <p>\u6bd4\u7279\u5e01\u7684\u6316\u77ff\u7b97\u6cd5\u5e76\u4e0d\u662f\u6bd4\u7279\u5e01\u5f00\u521b\u7684\uff0c\u5176\u539f\u578b\u53eb Hashcash\uff0c\u4e00\u5f00\u59cb\u8fd9\u4e2a\u7b97\u6cd5\u662f\u7528\u6765\u9650\u5236\u5783\u573e\u90ae\u4ef6\u7684\u3002Hashcash \u4e00\u5f00\u59cb\u8981\u6c42\u90ae\u4ef6\u53d1\u9001\u65b9\u5bf9\u90ae\u4ef6\u5934\uff08\u5176\u4e2d\u5305\u62ec\u65f6\u95f4\u548c\u6536\u4ef6\u4eba\u5730\u5740\uff09\u8ba1\u7b97\u4e00\u4e2a 160bit \u7684 SHA-1 \u54c8\u5e0c\u503c\u3002\u5176\u524d\u9762\u9700\u8981\u6709 5 \u4e2a\u96f6\uff0c\u4e5f\u5c31\u662f 20bit \u7684 0</p> <p>\u4e3a\u4ec0\u4e48\u8981\u8bbe\u8ba1\u6210\u8fd9\u4e2a\u6837\u5b50\uff1f\u56e0\u4e3a\u5982\u679c\u6211\u4eec\u53d1\u6b63\u5e38\u90ae\u4ef6\uff0c\u8fd9\u70b9\u7b97\u529b\u5bf9\u4e8e\u53d1\u9001\u65b9\u6765\u8bf4\uff0c\u6ca1\u6709\u4ec0\u4e48\u3002\u4f46\u5bf9\u4e8e\u9700\u8981\u5927\u91cf\u53d1\u9001\u5783\u573e\u90ae\u4ef6\u7684\u4eba\u6765\u8bf4\uff0c\u8fd9\u5c31\u662f\u4e00\u4e2a\u5f88\u5927\u7684\u6210\u672c\u4e86\u3002\u90a3\u4e9b\u63a7\u5236\u7740\u7528\u6237\u7684\u50f5\u5c38\u7f51\u7edc\u7684\u9ed1\u5ba2\u6765\u8bf4\uff0c\u53d1\u9001\u5783\u573e\u90ae\u4ef6\u65f6\uff0c\u5bfc\u81f4 CPU \u4f7f\u7528\u7387\u8fc7\u9ad8\uff0c\u4f1a\u9a6c\u4e0a\u5f15\u8d77\u7535\u8111\u6240\u6709\u8005\u7684\u8b66\u89c9\uff0c\u800c\u4e14\u8fd8\u5f88\u5bb9\u6613\u5b9a\u4f4d\u76f8\u5e94\u7684\u6076\u610f\u7a0b\u5e8f</p> <p><strong>\u5de5\u4f5c\u91cf\u8bc1\u660e\u7684\u4f5c\u7528\uff1a</strong></p> <ul> <li><strong>\u63d0\u9ad8\u5bf9\u6570\u636e\u7be1\u6539\u7684\u6210\u672c</strong>\uff1a\u8ba9\u4f60\u4fee\u6539\u6570\u636e\u9700\u8981\u4ed8\u51fa\u5927\u91cf\u7684\u7b97\u529b\uff0c\u800c\u533a\u5757\u94fe\u7684\u6570\u636e\u76f8\u4e92\u4f9d\u8d56\uff0c\u5bfc\u81f4\u4e00\u5904\u6539\u5904\u5904\u6539\uff0c\u56e0\u6b64\u4f60\u8981\u5b8c\u5168\u4fee\u6539\u5c31\u9700\u8981\u4ed8\u51fa\u5927\u91cf\u7684\u7b97\u529b</li> <li><strong>\u63d0\u9ad8\u7f51\u7edc\u4e2d\u6709\u4e0d\u540c\u58f0\u97f3\u7684\u6210\u672c</strong>\uff1a\u5982\u679c\u4e00\u4e2a\u7f51\u7edc\u6709\u4e0d\u540c\u7684\u4eba\u7ed9\u51fa\u6765\u4e86\u4e0d\u540c\u7684\u8d26\u672c\u800c\u4e14\u90fd\u5408\u6cd5\uff0c\u90a3\u4e48\u5c31\u65e0\u6cd5\u786e\u5b9a\u9700\u8981\u76f8\u4fe1\u8c01\u3002\u6240\u4ee5\u6316\u77ff\u53ef\u4ee5\u89e3\u51b3\u8fd9\u4e2a\u95ee\u9898\uff0c\u8ba9\u4f60\u8981\u505a\u4e00\u4e2a\u4f2a\u9020\u8d26\u672c\u7684\u6210\u672c\u6781\u5176\u5730\u5927\uff0c\u800c\u6821\u9a8c\u8d26\u672c\u7684\u6210\u672c\u5f88\u5c0f</li> <li><strong>\u89e3\u51b3\u5206\u6b67</strong>\uff1a\u5f53\u6709\u4e0d\u540c\u58f0\u97f3\u7684\u65f6\u5019\uff0c\u5373\u533a\u5757\u94fe\u51fa\u73b0\u5206\u53c9\u65f6\uff0c\u6240\u6709\u7684\u77ff\u5de5\u53ea\u80fd\u9009\u62e9\u5176\u4e2d\u4e00\u4e2a\u5206\u652f\uff08\u56e0\u4e3a\u6ca1\u4eba\u6709\u7b97\u529b\u53ef\u4ee5\u540c\u65f6\u53d1\u51fa\u4e24\u4e2a\u4e0d\u540c\u7684\u58f0\u97f3)\u3002\u4e8e\u662f\uff0c\u5927\u591a\u6570\u4eba\u9009\u62e9\u7684\u90a3\u4e2a\u5206\u652f\u5c31\u4f1a\u6210\u4e3a\u4e8b\u5b9e\uff0c\u5c11\u6570\u4eba\u9009\u7684\u90a3\u5934\u5c31\u88ab\u9057\u5fd8\u4e86\u3002\u8fd9\u8ba9\u6574\u4e2a\u53bb\u4e2d\u5fc3\u5316\u7cfb\u7edf\u7684\u4e00\u81f4\u6027\uff0c\u4e0d\u518d\u4ee5\u4eba\u6570\u591a\u8ba4\u53ef\u7684\u6570\u636e\u4e3a\u51c6\uff0c\u800c\u662f\u4ee5\u7b97\u529b\u591a\u7684\u4eba\u8ba4\u53ef\u7684\u6570\u636e\u4e3a\u51c6</li> </ul> <p>\u6bd4\u7279\u5e01\u7684\u533a\u5757\u94fe\u7f51\u7edc\u5728\u8bbe\u8ba1\u65f6\u4f7f\u7528\u7684 PoW\uff08Proof of Work\uff09 \u7b97\u6cd5\u601d\u8def\u3002\u4e00\u4e2a\u662f\u9650\u5236\u4e00\u6bb5\u65f6\u95f4\u5185\u6574\u4e2a\u7f51\u7edc\u4e2d\u51fa\u73b0\u63d0\u6848\u7684\u4e2a\u6570\uff08\u589e\u52a0\u63d0\u6848\u6210\u672c\uff09\uff0c\u53e6\u5916\u4e00\u4e2a\u662f\u653e\u5bbd\u5bf9\u6700\u7ec8\u4e00\u81f4\u6027\u786e\u8ba4\u7684\u9700\u6c42\uff0c\u7ea6\u5b9a\u597d\u5927\u5bb6\u90fd\u786e\u8ba4\u5e76\u6cbf\u7740\u5df2\u77e5\u6700\u957f\u7684\u94fe\u8fdb\u884c\u62d3\u5bbd\u3002</p> <p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u5982\u679c\u6bd4\u7279\u5e01\u7cfb\u7edf\u5728\u67d0\u4e00\u4e2a\u65f6\u523b\u540c\u65f6\u51fa\u73b0\u4e86\u4e24\u4e2a\u90fd\u5408\u6cd5\u7684\u533a\u5757\uff0c\u90a3\u4e48\u4e24\u4e2a\u90fd\u627f\u8ba4\u3002\u4e8e\u662f\uff0c\u533a\u5757\u94fe\u4e0a\u4f1a\u51fa\u73b0\u4e24\u4e2a\u5408\u6cd5\u7684\u5206\u652f\uff08\u672f\u8bed\u53eb &quot;\u5206\u53c9&quot;\uff09\u3002\u6b64\u65f6\u77ff\u5de5\u53ef\u4ee5\u9009\u62e9\u4efb\u4f55\u4e00\u4e2a\u5206\u652f\u7ee7\u7eed\uff0c\u5728\u67d0\u4e2a\u5206\u652f\u7684\u957f\u5ea6\u8d85\u8fc7\u4e86\u53e6\u4e00\u4e2a\u5206\u652f\u65f6\uff0c\u77ed\u7684\u90a3\u4e2a\u5206\u652f\u9a6c\u4e0a\u4f5c\u5e9f</p> <p>\u5f53\u7136\uff0cPoW \u7684\u521d\u8877\u662f\u901a\u8fc7\u6d88\u8017\u8d44\u6e90\u7684\u65b9\u5f0f\u6765\u963b\u6b62\u4e00\u4e9b\u6076\u610f\u653b\u51fb\u3002\u7136\u800c\u5728\u533a\u5757\u94fe\u7684\u53bb\u4e2d\u5fc3\u5316\u7684\u4e16\u754c\u91cc\uff0cPoW \u8fd8\u6709\u53e6\u4e00\u4e2a\u529f\u80fd\uff0c\u90a3\u5c31\u662f\u8ba9\u8fd9\u4e9b\u4e0d\u53d7\u63a7\u5236\u7684\u5206\u5e03\u5f0f P2P \u7f51\u7edc\u91cc\u7684\u7ed3\u70b9\u7edf\u4e00\u601d\u60f3\u3002\u4e5f\u5c31\u662f\u8bf4\u6211\u4eec\u5e38\u8bf4\u7684\uff0c\u5206\u5e03\u5f0f\u4e00\u81f4\u6027\u3002\u8fd9\u5bf9\u5206\u5e03\u5f0f\u7cfb\u7edf\u4e2d\u7684\u4ea4\u6613\u7cfb\u7edf\u6765\u8bf4\u662f\u4e00\u4ef6\u975e\u5e38\u91cd\u8981\u7684\u4e8b</p> <h3 id="64-pos-\u80a1\u6743\u8bc1\u660e\u534f\u8bae">6.4 PoS \u80a1\u6743\u8bc1\u660e\u534f\u8bae</h3> <p><strong>\u80cc\u666f\uff1a</strong></p> <p>PoW \u8017\u8d39\u7684\u7535\u529b\u548c\u65f6\u95f4\u6210\u672c\u662f\u8d8a\u6765\u8d8a\u5927\u4e86\u3002\u6240\u4ee5\uff0c\u4e3a\u4e86\u6bcf\u4e2a Block \u66f4\u5feb\u7684\u751f\u6210\uff0c\u51fa\u73b0\u4e86 PoS \uff08Proof of Stake\uff09\u534f\u8bae\uff0c\u4e2d\u6587\u7ffb\u8bd1\u4e3a\u80a1\u6743\u8bc1\u660e\u534f\u8bae</p> <p><strong>\u6982\u5ff5\uff1a</strong></p> <ul> <li>\u5728 PoS \u673a\u5236\u4e0b\uff0c\u77ff\u5de5\u4e0d\u5728\u53eb\u77ff\u5de5\uff0c\u800c\u662f\u53eb Validator\uff08\u6821\u9a8c\u8005\uff09</li> <li>\u5047\u8bbe\u73b0\u5728\u6709\u4e00\u4e2a\u533a\u57df\u9700\u8981\u88ab\u751f\u6210\uff0c\u800c\u73b0\u5728\u6709 4 \u4e2a Validator\uff0c\u6bcf\u4e00\u4e2a Validator \u9700\u8981\u4ee5 &quot;\u4ea4\u62bc\u91d1&quot; \u7684\u65b9\u5f0f\u6765\u53d6\u5f97\u8bb0\u8d26\u6743</li> <li>\u5047\u5982\uff0cA \u4ea4\u7684\u62bc\u91d1\u5360\u4e86 38%\uff0cB \u5360 25%\uff0cC \u70b9 21%\uff0cD \u5360 16%\u3002\u90a3\u4e48\uff0c\u4ed6\u4eec\u6309\u7167\u80a1\u6743\u7684\u6bd4\u6743\u6765\u83b7\u5f97\u8bb0\u8d26\u6743</li> <li>\u6bd4\u5982\uff0cA \u6709 38% \u7684\u6982\u7387\u53ef\u4ee5\u83b7\u5f97\u8bb0\u8d26\u6743\uff08\u4e0d\u662f\u7531\u7cfb\u7edf\u968f\u673a\u5206\u914d\uff0c\u8fd8\u662f\u8981\u7b97 hash \u503c\uff0c\u53ea\u4e0d\u8fc7\u662f\u8d22\u5bcc\u8d8a\u591a\u7684\u4eba\u6316\u77ff\u7684\u96be\u5ea6\u8d8a\u5c0f\uff09</li> <li>\u800c\u5982\u679c\u4f60\u53d1\u8d77\u6076\u610f\u653b\u51fb\u7684\u8bdd\uff0c\u4f60\u7684\u94b1\u5c31\u4f1a\u88ab\u7cfb\u7edf\u6ca1\u6536\u5145\u516c\u3002Validator \u8bb0\u8d26\u540e\u6ca1\u6709\u5956\u91d1\uff0c\u53ea\u6709\u624b\u7eed\u8d39</li> </ul> <blockquote> <p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u5728 PoS \u673a\u5236\u4e0b\uff0c\u8bb0\u8d26\u6743\u4e0d\u518d\u50cf PoW \u90a3\u6837\u7531\u8c01\u7684\u7b97\u529b\u5927\u8c01\u5c31\u8d8a\u6709\u673a\u4f1a\u6765\u8bb0\u8d26\uff0c\u800c\u662f\u7531\u8c01\u7684\u8d22\u5bcc\u591a\uff0c\u8c01\u5c31\u8d8a\u6709\u53ef\u80fd\u6765\u8bb0\u8d26\u3002\u4e8e\u662f\uff0c\u8bb0\u8d26\u6743\u6309\u5927\u5bb6\u8d22\u5bcc\u7684\u6bd4\u4f8b\u6765\u5206\u914d</p> </blockquote> <p><strong>PoS \u7684\u4f18\u52bf\uff1a</strong></p> <ol> <li>\u4e0d\u9700\u8981\u90a3\u4e48\u8d39\u52b2\u7684\u6316\u77ff\u4e86\u3002PoS \u5f88\u660e\u663e\u5730\u89e3\u51b3\u4e86\u8fd9\u4e2a\u95ee\u9898</li> <li>\u5728 P2P \u8fd9\u79cd\u65e0\u4e2d\u5fc3\u5316\u7684\u7f51\u7edc\u4e0b\uff0c\u5982\u679c\u4f60\u8981\u63a7\u5236\u6574\u4e2a\u7f51\u7edc\uff0c\u5c31\u9700\u8981\u8d85\u8fc7\u534a\u6570\u4ee5\u4e0a\u7684\u80fd\u529b\u3002\u5728 PoW \u4e0b\uff0c\u9700\u8981 51% \u7684\u7b97\u529b\u3002\u800c\u5728 PoS \u4e0b\uff0c\u4f60\u9700\u8981\u6709 51% \u7684\u8d22\u5bcc\uff0c\u4f60\u624d\u53ef\u4ee5\u53d1\u8d77\u653b\u51fb</li> </ol> <p><strong>PoS \u6f5c\u5728\u7684\u95ee\u9898\uff1a</strong></p> <p>\u5f53\u4e0d\u9700\u8981\u592a\u591a\u7b97\u529b\u7684\u65f6\u5019\uff0c\u5982\u679c\u8d26\u672c\u51fa\u73b0\u5206\u53c9\u7684\u60c5\u51b5\uff0c\u4e5f\u5c31\u662f\u7cfb\u7edf\u51fa\u73b0\u4e24\u4e2a\u51b2\u7a81\u4e14\u5408\u6cd5\u7684\u533a\u5757\u7684\u65f6\u5019\uff0c\u5728\u6bd4\u7279\u5e01\u8fd9\u79cd\u7b97\u529b\u5bc6\u96c6\u7684 PoW \u673a\u5236\u4e0b\uff0c\u6240\u6709\u7684\u77ff\u5de5\u5fc5\u9700\u8d4c\u5176\u4e2d\u4e00\u4e2a\u5206\u652f\u5f80\u4e0b\u8d70\u3002\u56e0\u4e3a\u7b97\u529b\u7684\u95ee\u9898\uff0c\u6240\u4ee5\u57fa\u672c\u4e0a\u6765\u8bf4\u4e0d\u592a\u53ef\u80fd\u540c\u65f6\u5728\u4e24\u4e2a\u5206\u652f\u4e0a\u53d1\u5c55\u3002\u800c\u5176\u4e2d\u4e00\u4e2a\u5206\u652f\u5982\u679c\u957f\u4e8e\u53e6\u4e00\u4e2a\u5206\u652f\uff0c\u8f83\u77ed\u7684\u90a3\u4e2a\u5206\u652f\u5c31\u4f1a\u88ab\u5b64\u7acb\u51fa\u53bb\uff0c\u5176\u4e0a\u7684\u8d26\u672c\u5c31\u90fd\u4e0d\u4f5c\u6570\u4e86\uff0c\u800c\u77ff\u5de5\u7684\u5956\u52b1\u4e5f\u6ca1\u6709\u4e86\u3002\u8fd9\u662f PoW \u673a\u5236\u7684\u597d\u5904</p> <p>\u800c\u5728 PoS \u8fd9\u79cd\u4e0d\u9700\u8981\u7b97\u529b\u7684\u673a\u5236\u4e0b\uff0c\u5c31\u53ef\u4ee5\u8ba9\u8bb0\u8d26\u4eba\u4eec\u5728\u4e24\u4e2a\u5206\u652f\u4e0a\u540c\u65f6\u8fdb\u884c\uff0c\u4ee5\u4e89\u53d6\u5b9e\u73b0\u5229\u76ca\u7684\u6700\u5927\u5316\uff08\u65e0\u8bba\u54ea\u4e2a\u5206\u652f\u6700\u7ec8\u80dc\u51fa\uff0c\u6211\u90fd\u53ef\u4ee5\u6709\u5229\uff09\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u653b\u51fb\u8005\u5c31\u53ef\u4ee5\u5229\u7528\u8fd9\u79cd\u60c5\u51b5\u6765\u53d1\u8d77 Nothin-At-Stake \u653b\u51fb\u3002</p> <p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u5982\u679c\u7edd\u5927\u591a\u6570\u4eba\u90fd\u5728\u53d1\u5c55\u4e24\u4e2a\u5206\u652f\uff0c\u5047\u8bbe\u6709 99% \u7684\u4eba\u53d1\u5c55 A \u5206\u652f\uff0c99% \u7684\u4eba\u4e5f\u540c\u65f6\u53d1\u5c55 B \u5206\u652f\uff0c\u800c\u6709 1% \u80a1\u4efd\u7684\u4eba\u5728\u5206\u652f A \u4e2d\u5199\u4e00\u7b14\u4ea4\u6613\uff0c\u7136\u540e\u5728 B \u5206\u652f\u6ca1\u6709\u8fd9\u7b14\u4ea4\u6613\uff0c\u5f53\u5176\u5728 A \u5206\u652f\u4e0a\u8fbe\u6210\u5408\u7ea6\u540e\uff08\u6bd4\u5982\uff0c\u6536\u5230\u5546\u54c1\uff09\uff0c\u52a0\u5165 B \u5206\u652f\uff0c\u7136\u540e B \u5206\u652f\u80dc\u51fa\uff0c\u5bfc\u81f4\u5176\u6ca1\u6709\u4ea4\u6613</p> <h3 id="65-dpos-\u59d4\u6258\u80a1\u6743\u8bc1\u660e">6.5 DPoS \u59d4\u6258\u80a1\u6743\u8bc1\u660e</h3> <p>PoS \u7684\u95ee\u9898\u4e5f\u5f88\u591a\uff0c\u6240\u4ee5\u6709\u4eba\u53c8\u63d0\u51fa\u6765\u4e86\u4e00\u4e2a\u8fdb\u5316\u7248\uff0c\u53eb DPoS\uff08Delegated Proof of Stake\uff0c\u59d4\u6258\u80a1\u6743\u8bc1\u660e\uff09</p> <p>\u5728\u5e38\u89c4 PoW \u548c PoS \u4e2d\uff0c\u4e00\u5927\u5f71\u54cd\u6548\u7387\u4e4b\u5904\u5728\u4e8e\u4efb\u4f55\u4e00\u4e2a\u65b0\u52a0\u5165\u7684\u533a\u5757\uff0c\u90fd\u9700\u8981\u88ab\u6574\u4e2a\u7f51\u7edc\u6240\u6709\u8282\u70b9\u505a\u786e\u8ba4\u3002DPoS \u4f18\u5316\u65b9\u6848\u5728\u4e8e\uff1a\u901a\u8fc7\u4e0d\u540c\u7684\u7b56\u7565\uff0c\u4e0d\u5b9a\u65f6\u5730\u9009\u4e2d\u4e00\u5c0f\u7fa4\u8282\u70b9\uff0c\u8fd9\u4e00\u5c0f\u7fa4\u8282\u70b9\u505a\u65b0\u533a\u5757\u7684\u521b\u5efa\u3001\u9a8c\u8bc1\u3001\u7b7e\u540d\u548c\u76f8\u4e92\u76d1\u7763\u3002\u8fd9\u6837\u5c31\u5927\u5e45\u5ea6\u51cf\u5c11\u4e86\u533a\u5757\u521b\u5efa\u548c\u786e\u8ba4\u6240\u9700\u8981\u6d88\u8017\u7684\u65f6\u95f4\u548c\u7b97\u529b\u6210\u672c\u3002</p> <p>DPoS \u4e0b\u6bcf\u4e2a token \u90fd\u662f\u9009\u7968\uff0c\u5927\u5bb6\u7968\u9009 20 \u4e2a\u9009\u4e3e\u4eba\u56e2 +1 \u4e2a\u968f\u673a\u9009\u4e3e\u4eba =21 \u4e2a\u9009\u4e3e\u4eba\u4ee3\u8868\u7f51\u7edc\u3002\u7136\u540e\u6bcf\u9694\u4e00\u6bb5\u65f6\u95f4\uff0c\u5728\u8fd9 21 \u4e2a\u4eba\u4e2d\u6311\u9009\u4e00\u4e2a\u51fa\u6765\u7ef4\u62a4\u8d26\u672c\u5e76\u83b7\u5f97\u6536\u76ca</p> <h2 id="7-\u667a\u80fd\u5408\u7ea6">7 \u667a\u80fd\u5408\u7ea6</h2> <h3 id="71-\u4ee5\u592a\u574a\u7684\u667a\u80fd\u5408\u7ea6">7.1 \u4ee5\u592a\u574a\u7684\u667a\u80fd\u5408\u7ea6</h3> <p>\u5bf9\u4e8e\u4ee5\u592a\u574a\u6765\u8bf4\uff0c\u667a\u80fd\u5408\u7ea6\u5176\u5b9e\u5c31\u662f\u4e00\u6bb5\u53ef\u6267\u884c\u7684\u7a0b\u5e8f\u7247\u6bb5\uff0c\u7531\u53d1\u5e03\u4eba\u4f7f\u7528\u4e00\u79cd\u7c7b\u4f3c\u4e8e JavaScript \u6216\u662f Python \u7684\u7f16\u7a0b\u8bed\u8a00\u6765\u7f16\u5199</p> <p>\u6211\u4eec\u628a\u5408\u7ea6\u4ee3\u7801\u5728\u672c\u5730\u7f16\u8bd1\u6210\u529f\u540e\u53d1\u5e03\u5230\u533a\u5757\u94fe\u4e0a\uff0c\u53ef\u4ee5\u7406\u89e3\u4e3a\u4e00\u4e2a\u7279\u6b8a\u7684\u4ea4\u6613\uff08\u5305\u62ec\u53ef\u6267\u884c\u4ee3\u7801\uff09\uff0c\u7136\u540e\u4f1a\u88ab\u77ff\u5de5\u6253\u5305\u8bb0\u5f55\u5728\u67d0\u4e00\u4e2a\u533a\u5757\u4e2d\u3002\u5f53\u9700\u8981\u8c03\u7528\u8fd9\u4e2a\u667a\u80fd\u5408\u7ea6\u7684\u65b9\u6cd5\u65f6\uff0c\u53ea\u9700\u8981\u5411\u8fd9\u4e2a\u667a\u80fd\u5408\u7ea6\u7684\u5730\u5740\u53d1\u9001\u4e00\u7b14\u4ea4\u6613\u5373\u53ef\u3002</p> <p>\u6bcf\u4e2a\u8282\u70b9\u7684\u7535\u8111\u90fd\u9700\u8981\u5b89\u88c5\u4ee5\u592a\u574a\u5ba2\u6237\u7aef\uff0c\u5ba2\u6237\u7aef\u81ea\u5e26\u4e86\u4e00\u4e2a\u548c JVM \u7c7b\u4f3c\u7684\u4e00\u4e2a EVM\u3002\u901a\u8fc7\u4ea4\u6613\u89e6\u53d1\u667a\u80fd\u5408\u7ea6\u540e\uff0c\u667a\u80fd\u5408\u7ea6\u7684\u4ee3\u7801\u5c31\u4f1a\u5728 EVM \u4e2d\u6267\u884c\u4e86\u3002\u8fd9\u79cd\u65b9\u5f0f\u76f8\u5f53\u4e8e\u628a\u7a0b\u5e8f\u90e8\u7f72\u5230\u4e86\u975e\u5e38\u975e\u5e38\u591a\u7684\u7535\u8111\u4e0a\uff0c\u968f\u65f6\u90fd\u53ef\u4ee5\u901a\u8fc7\u4ea4\u6613\u6765\u89e6\u53d1\u8fd9\u4e9b\u667a\u80fd\u5408\u7ea6\u7684\u6267\u884c\uff0c\u4e5f\u4ece\u800c\u5b8c\u6210\u4e86\u5206\u5e03\u5f0f\u7a0b\u5e8f\u7684\u90e8\u7f72\u548c\u8c03\u7528</p> <h3 id="72-\u5408\u540c\u7684-bug">7.2 \u5408\u540c\u7684 Bug</h3> <p>\u6709\u7a0b\u5e8f\u7684\u5730\u65b9\u5c31\u4f1a\u6709 Bug\uff0c\u73b0\u5b9e\u751f\u6d3b\u4e2d\u4f1a\u6709 Bug\uff0c\u5408\u540c\u4e5f\u4f1a\u6709 Bug\u3002\u51fa\u73b0\u4e86 Bug \u540e\uff0c\u5927\u5bb6\u53ef\u4ee5\u76f8\u4e92\u534f\u5546\uff0c\u7ed9\u5408\u540c\u6253\u8865\u4e01\uff08\u9644\u52a0\u6761\u6b3e\uff0c\u6216\u662f\u91cd\u65b0\u7b7e\u5408\u540c\uff09\u3002\u7136\u800c\uff0c\u4ee3\u7801\u5408\u540c\u5219\u4e0d\u4e00\u6837\uff0cBug \u4e5f\u4f1a\u88ab\u6b8b\u9177\u65e0\u60c5\u5730\u6267\u884c\uff0c\u4e00\u65e6\u6267\u884c\u5c31\u5f88\u96be\u8865\u6551\u4e86</p> <p><strong>\u6848\u4f8b The DAO\uff1a</strong></p> <p>\u6700\u8457\u540d\u7684\u4f8b\u5b50\u5c31\u662f\u4ee5\u592a\u574a\u4e00\u4e2a\u53eb The DAO \u7684\u5e94\u7528\uff0c\u5b83\u662f\u4e00\u4e2a\u53bb\u4e2d\u5fc3\u5316\u7684\u98ce\u9669\u6295\u8d44\u57fa\u91d1\uff0c\u4ee5\u667a\u80fd\u5408\u7ea6\u7684\u5f62\u5f0f\u8fd0\u884c\u5728\u4ee5\u592a\u574a\u533a\u5757\u94fe\u4e0a\u3002\u5b83\u4e5f\u662f\u4e00\u4e2a\u76c8\u5229\u6027\u7684\u53bb\u4e2d\u5fc3\u5316\u81ea\u6cbb\u7ec4\u7ec7\uff0c\u5b83\u5c06\u5229\u7528\u81ea\u5df1\u638c\u63a7\u7684\u4ee5\u592a\u5e01\u8d44\u91d1\u901a\u8fc7\u6295\u8d44\u4ee5\u592a\u574a\u4e0a\u7684\u5e94\u7528\u4e3a\u5176\u6210\u5458\u521b\u9020\u4ef7\u503c\u3002\u5728 The DAO \u521b\u5efa\u671f\uff0c\u4efb\u4f55\u4eba\u90fd\u53ef\u4ee5\u5411\u5b83\u7684\u4f17\u7b79\u5408\u7ea6\u53d1\u9001\u4ee5\u592a\u5e01\uff0c\u83b7\u5f97 DAO \u4ee3\u5e01\u3002</p> <p>\u56e0\u4e3a The DAO \u8fd9\u4e2a\u7a0b\u5e8f\u5199\u5f97\u4e0d\u597d\uff0c\u9ed1\u5ba2\u5728\u5176\u667a\u80fd\u5408\u7ea6\u91cc\u627e\u5230 Bug\uff0c\u628a\u6240\u6709\u7684\u94b1\u7ed9\u8c03\u8d70\u4e86\uff0c\u5927\u7ea6 7000 \u591a\u4e07\u7f8e\u5200\u3002\u8fd9\u6210\u4e3a\u6709\u53f2\u4ee5\u6765\u6700\u5927\u5b97\u7684\u6570\u5b57\u52ab\u6848\uff0c\u800c\u4e14 FBI \u4e5f\u627e\u4e0d\u5230\u4eba\u3002\u8fd9\u4e2a\u9879\u76ee\u56e0\u4e3a\u94b1\u88ab\u5077\u8d70\u800c\u5012\u95ed\u4ee5\u540e\uff0c\u5f15\u8d77\u4e86\u4ee5\u592a\u574a\u7684\u5f3a\u884c\u5206\u53c9\uff0c\u53d8\u6210 ETH \u548c ETC</p> <p><strong>\u6848\u4f8b Parity \u88ab\u76d7\uff1a</strong></p> <p>\u8fd8\u6709\u4e00\u4e2a\u6848\u4f8b\uff0c\u662f 2017 \u5e74\u53d1\u751f\u7684\u667a\u80fd\u94b1\u5305\uff08\u591a\u7b7e\u540d\u94b1\u5305\uff09Parity \u88ab\u76d7\u4e8b\u4ef6\u3002\u5b83\u53f7\u79f0\u81ea\u5df1\u7684\u667a\u80fd\u5408\u7ea6\u88ab\u5f88\u591a\u5f88\u5389\u5bb3\u7684\u5b89\u5168\u4eba\u5458\u90fd\u5ba1\u67e5\u8fc7\uff0c\u90fd\u8ba4\u4e3a\u6ca1\u95ee\u9898\u3002\u4f46\u6700\u540e\u8fd8\u662f\u88ab\u9ed1\u5ba2\u5229\u7528\u4e86\u4e00\u4e2a\u53eb\u505a initwallet \u7684\u51fd\u6570\uff0c\u53cd\u590d\u8c03\u7528\u5b83\uff0c\u8f6c\u8d70\u4e86 3000 \u4e07\u7f8e\u91d1</p> ';o.exports=D}}]);