(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["docs/blockChain/eth"],{a6Nn:function(e,o,c){var d=c("Rxnk"),t=c("aGwq"),n=c("BbM/"),p=c("72wP"),r=c("RTCd"),s=c("+QXI"),i=c("ld7g"),a=c("WE8L"),l=c("qRpH"),g=c("kFgp"),h=c("hO+r"),u=c("tksw"),b=c("Wx7G"),m=c("GjP5"),f=c("BADG"),k=c("n34F"),w=c("pp+W"),x=c("RGrl"),v=c("j9KD"),M=d(t),P=d(n),q=d(p),y=d(r),E=d(s),T=d(i),A=d(a),D=d(l),G=d(g),R=d(h),S=d(u),V=d(b),H=d(m),B=d(f),U=d(k),j=d(w),C=d(x),I=d(v),W='<h1 id="\u4ee5\u592a\u574a">\u4ee5\u592a\u574a</h1> <h2 id="1-\u6982\u89c8">1 \u6982\u89c8</h2> <h3 id="11-\u6982\u5ff5">1.1 \u6982\u5ff5</h3> <p>\u4ee5\u592a\u574a\u662f\u4e00\u4e2a\u4e3a\u53bb\u4e2d\u5fc3\u5316\u5e94\u7528\uff08DApp\uff09\u800c\u751f\u7684\u5168\u7403\u5f00\u6e90\u5e73\u53f0\u3002\u540d\u5b57\u6765\u6e90\u4e0e Ether (\u4ee5\u592a) \u548c\u540e\u7f00 ruem (\u5e0c\u814a\u8bed\u4e49\u662f\u201c\u77f3\u6cb9\u201d)\uff0c\u56fd\u5185\u7684\u65e9\u671f\u793e\u533a\u6210\u5458\u5c06\u5176\u7ffb\u8bd1\u6210\u201c\u4ee5\u592a\u574a\u201d</p> <p>\u4ee5\u592a\u574a\u662f\u53bb\u4e2d\u5fc3\u5316\u7684\u3001\u53ef\u4ee5\u8fd0\u884c\u53ef\u8f6c\u79fb\u8ba1\u7b97\u548c\u6570\u636e\u7684\u81ea\u6d3d\u7ecf\u6d4e\u7cfb\u7edf\uff0c\u4e00\u4e2a\u53bb\u4e2d\u5fc3\u5316\u7684\u533a\u5757\u94fe\u5e73\u53f0\uff0c\u7528\u4e8e\u6784\u5efa\u53bb\u4e2d\u5fc3\u5316\u5e94\u7528\u7a0b\u5e8f\uff08DApp\uff09\uff0c\u4ee5\u592a\u5e01\uff08Ether\uff09\u662f\u8be5\u5e73\u53f0\u6240\u4f7f\u7528\u7684\u52a0\u5bc6\u8d27\u5e01\u3002</p> <p>\u4ee5\u592a\u574a\u53ef\u4ee5\u8f7b\u677e\u521b\u5efa\u667a\u80fd\u5408\u7ea6\uff0c\u81ea\u884c\u6267\u884c\u4ee3\u7801\uff0c\u5f00\u53d1\u4eba\u5458\u53ef\u4ee5\u5229\u7528\u8fd9\u4e9b\u4ee3\u7801\u6765\u5904\u7406\u5404\u79cd\u5e94\u7528\u7a0b\u5e8f</p> <p>\u4ee5\u592a\u574a\u51e0\u4e4e\u53ef\u4ee5\u7528\u4e8e\u4efb\u4f55\u7c7b\u578b\u7684\u4ea4\u6613\u6216\u534f\u8bae\uff0c\u5176\u6210\u672c\u6bd4\u4f20\u7edf\u7684\u66ff\u4ee3\u65b9\u6848\u8981\u4f4e\uff0c\u4f8b\u5982\u94f6\u884c\u5361\u652f\u4ed8\uff0cPayPal \u548c\u6295\u7968\u7b49\uff0c\u5e76\u4e14\u5168\u7a0b\u91c7\u7528\u53bb\u4e2d\u5fc3\u5316\u3001\u53bb\u4fe1\u4efb\uff08\u65e0\u9700\u4e2d\u4ecb\uff09\u3001\u5b89\u5168\u6709\u6548\u5e76\u4e14\u6297\u5ba1\u67e5\u7684\u5f62\u5f0f</p> <h3 id="12-\u4ee5\u592a\u574a\u7684\u4ef7\u503c">1.2 \u4ee5\u592a\u574a\u7684\u4ef7\u503c</h3> <p>\u4ee5\u592a\u574a\u6bd4\u5355\u7eaf\u7684\u52a0\u5bc6\u8d27\u5e01\u66f4\u6709\u6548\u7528\u3002\u901a\u8fc7\u4ee5\u592a\u574a\uff0c\u7528\u6237\u53ef\u4ee5\u521b\u5efa\u4efb\u4f55\u53bb\u4e2d\u5fc3\u5316\u5e94\u7528\u7a0b\u5e8f\uff08\u5373 DApp\uff09\u548c\u667a\u80fd\u5408\u7ea6\uff0c\u7406\u8bba\u4e0a\u53ef\u7f16\u7a0b\u7684\u4ee5\u592a\u574a\u53ef\u4ee5\u7528\u4e8e\u4efb\u4f55\u7ecf\u6d4e\u6216\u6cbb\u7406\u6d3b\u52a8</p> <p>\u5728\u4ee5\u592a\u574a\u4e0a\uff0c\u4f60\u53ef\u4ee5\u901a\u8fc7\u7f16\u5199\u4ee3\u7801\u7ba1\u7406\u6570\u5b57\u8d44\u4ea7\u3001\u8fd0\u884c\u7a0b\u5e8f\uff0c\u4e0d\u53d7\u5730\u57df\u9650\u5236</p> <p>\u4ee5\u592a\u574a\u4e3a\u5168\u7403\u91d1\u878d\u7cfb\u7edf\u6253\u5f00\u4e86\u4e00\u6247\u5927\u95e8\uff0c\u7528\u6237\u53ea\u9700\u8981\u901a\u8fc7\u4e92\u8054\u7f51\u5c31\u80fd\u591f\u4ee5\u53bb\u4fe1\u4efb\u5316\u7684\u65b9\u5f0f\u8bbf\u95ee\u5e94\u7528\u7a0b\u5e8f\u3001\u4ea7\u54c1\u548c\u670d\u52a1\u3002\u6bcf\u4e00\u4e2a\u4eba\u90fd\u53ef\u4ee5\u4e0e\u4ee5\u592a\u574a\u7f51\u7edc\u8fdb\u884c\u4ea4\u4e92\uff0c\u4eb2\u81ea\u53c2\u4e0e\u5230\u8fd9\u79cd\u6570\u5b57\u7ecf\u6d4e\u5f62\u5f0f\u4e2d\uff0c\u800c\u4e0d\u9700\u8981\u7b2c\u4e09\u65b9</p> <p>\u6211\u4eec\u53ef\u4ee5\u4f7f\u7528\u667a\u80fd\u5408\u7ea6\u6765\u4e3a\u9879\u76ee\u4f17\u7b79\u8d44\u91d1\u3001\u516c\u53f8\u53ef\u4ee5\u5728\u533a\u5757\u94fe\u4e0a\u8fd0\u7528\u667a\u80fd\u5408\u7ea6\u8fdb\u884c\u80a1\u7968\u53d1\u884c\u3001\u94f6\u884c\u7b49\u91d1\u878d\u673a\u6784\u53ef\u4ee5\u7528\u5b83\u6765\u7ba1\u7406\u8d37\u6b3e\u6536\u53d1\u3001\u4fdd\u9669\u516c\u53f8\u53ef\u4ee5\u7528\u5b83\u6765\u5b9e\u73b0\u81ea\u52a8\u7406\u8d54\u7b49\u7b49\u7b49\u7b49\uff0c\u751a\u81f3\u4efb\u4f55\u73b0\u5b9e\u91d1\u878d\u4ea4\u6613\u90fd\u53ef\u4ee5\u88ab\u6539\u9020\u5230\u533a\u5757\u94fe\u4e0a\u8fdb\u884c</p> <h3 id="13-\u4ee5\u592a\u574a\u8fd0\u4f5c">1.3 \u4ee5\u592a\u574a\u8fd0\u4f5c</h3> <p>\u4ee5\u592a\u574a\u533a\u5757\u94fe\u7684\u7ed3\u6784\u4e0e\u6bd4\u7279\u5e01\u5341\u5206\u76f8\u4f3c\uff0c\u56e0\u4e3a\u5b83\u662f\u6574\u4e2a\u4ea4\u6613\u5386\u53f2\u7684\u5171\u4eab\u8bb0\u5f55\u3002\u7f51\u7edc\u4e0a\u7684\u6bcf\u4e2a\u8282\u70b9\u90fd\u5b58\u50a8\u4e86\u8fd9\u4e2a\u5386\u53f2\u8bb0\u5f55\u7684\u526f\u672c</p> <p>\u4ee5\u592a\u574a\u4e0e\u6bd4\u7279\u5e01\u7684\u6700\u5927\u533a\u522b\u5728\u4e8e\uff0c\u9664\u4e86\u6240\u6709\u4ee5\u592a\u574a\u4ea4\u6613\u5916\uff0c\u5b83\u7684\u8282\u70b9\u8fd8\u5b58\u50a8\u7740\u6bcf\u4e2a\u667a\u80fd\u5408\u7ea6\u7684\u6700\u65b0\u72b6\u6001\u3002\u5bf9\u4e8e\u6bcf\u4e2a\u4ee5\u592a\u574a\u5e94\u7528\u7a0b\u5e8f\uff0c\u7f51\u7edc\u90fd\u9700\u8981\u8ddf\u8e2a\u201c\u72b6\u6001\u201d\u6216\u6240\u6709\u8fd9\u4e9b\u5e94\u7528\u7a0b\u5e8f\u7684\u5f53\u524d\u4fe1\u606f\uff0c\u5305\u62ec\u6bcf\u4e2a\u7528\u6237\u7684\u4f59\u989d\u3001\u6240\u6709\u667a\u80fd\u5408\u7ea6\u4ee3\u7801\u4ee5\u53ca\u5b83\u4eec\u7684\u5b58\u50a8\u4f4d\u7f6e\u7b49</p> <h3 id="14-dapp">1.4 DApp</h3> <p>\u5f97\u76ca\u4e8e\u533a\u5757\u94fe\u6280\u672f\uff0c\u4ee5\u592a\u574a\u6ca1\u6709\u4e2d\u5fc3\u670d\u52a1\u5668\uff0c\u6240\u4ee5\u5728\u4ee5\u592a\u574a\u4e0a\u8fd0\u884c\u7684 APP \u4e0d\u5b58\u5728\u5d29\u6e83\u3001\u6b7b\u673a\u7684\u53ef\u80fd\uff0c\u6570\u636e\u4e5f\u4e0d\u4f1a\u6cc4\u9732\uff0c\u751a\u81f3\u4e0d\u80fd\u88ab\u5173\u95ed\uff01\u5b83\u8ddf\u533a\u5757\u94fe\u4e00\u6837\u4e5f\u662f\u5206\u5e03\u5f0f\u548c\u53bb\u4e2d\u5fc3\u5316\u7684\uff0c\u6240\u4ee5\u53c8\u53eb\u5206\u5e03\u5f0f\u5e94\u7528\uff08Decentralized Application\uff0c\u7b80\u79f0 DApp\uff09</p> <p>DApp \u4e00\u65e6\u88ab \u201c\u4e0a\u4f20\u201d \u5230\u4ee5\u592a\u574a\uff0c\u5b83\u4eec\u5c06\u59cb\u7ec8\u6309\u7167\u7f16\u597d\u7684\u7a0b\u5e8f\u8fd0\u884c\u3002\u8fd9\u4e9b\u5e94\u7528\u7a0b\u5e8f\u53ef\u4ee5\u63a7\u5236\u6570\u5b57\u8d44\u4ea7\uff1b\u540c\u65f6\u8fd8\u662f\u53bb\u4e2d\u5fc3\u5316\u7684\uff0c\u8fd9\u610f\u5473\u7740\u6ca1\u6709\u4efb\u4f55\u5355\u4e00\u5b9e\u4f53\u6216\u4e2a\u4eba\u53ef\u4ee5\u63a7\u5236\u5b83\u4eec</p> <p>\u76ee\u524d\uff0c\u5168\u4e16\u754c\u6709\u6210\u5343\u4e0a\u4e07\u540d\u5f00\u53d1\u8005\u6b63\u5728\u4ee5\u592a\u574a\u4e0a\u6784\u5efa\u5e94\u7528\u7a0b\u5e8f\uff0c\u5176\u4e2d\u6709\u8bb8\u591a\u73b0\u5728\u5df2\u7ecf\u53ef\u4ee5\u4f7f\u7528\uff1a</p> <ul> <li><strong>\u52a0\u5bc6\u8d27\u5e01\u94b1\u5305\uff1a</strong>\u8ba9\u4f60\u53ef\u4ee5\u4f7f\u7528 ETH \u6216\u5176\u4ed6\u6570\u5b57\u8d44\u4ea7\u8fdb\u884c\u4f4e\u6210\u672c\u7684\u5373\u65f6\u652f\u4ed8</li> <li><strong>\u91d1\u878d\u5e94\u7528\u7a0b\u5e8f\uff1a</strong>\u8ba9\u4f60\u53ef\u4ee5\u501f\u8d37\u3001\u6295\u8d44\u6570\u5b57\u8d44\u4ea7</li> <li><strong>\u53bb\u4e2d\u5fc3\u5316\u5e02\u573a\uff1a</strong>\u8ba9\u4f60\u53ef\u4ee5\u4ea4\u6613\u6570\u5b57\u8d44\u4ea7\uff0c\u751a\u81f3\u5c31\u73b0\u5b9e\u4e16\u754c\u4e8b\u4ef6\u7684 \u201c\u9884\u6d4b\u201d \u8fdb\u884c\u4ea4\u6613</li> <li><strong>\u6e38\u620f\uff1a</strong>\u4f60\u53ef\u4ee5\u62e5\u6709\u6e38\u620f\u5185\u7684\u8d44\u4ea7\uff0c\u751a\u81f3\u53ef\u4ee5\u7531\u6b64\u83b7\u5f97\u73b0\u5b9e\u6536\u76ca</li> </ul> <h3 id="15-\u667a\u80fd\u5408\u7ea6">1.5 \u667a\u80fd\u5408\u7ea6</h3> <p>\u6839\u636e\u533a\u5757\u94fe\u53ef\u7f16\u7a0b\u7684\u7279\u70b9\uff0c\u4eba\u4eec\u53ef\u4ee5\u5c06\u5408\u540c\u53d8\u6210\u4ee3\u7801\u7684\u5f62\u5f0f\u653e\u5230\u533a\u5757\u94fe\u4e0a\uff0c\u5e76\u5728\u7ea6\u5b9a\u7684\u6761\u4ef6\u4e0b\u81ea\u52a8\u6267\u884c\uff0c \u8fd9\u5c31\u662f\u6240\u8c13\u7684\u667a\u80fd\u5408\u7ea6</p> <p>\u5f53\u6ee1\u8db3\u67d0\u4e9b\u6761\u4ef6\u65f6\uff0c\u5b83\u5219\u6267\u884c\u7279\u5b9a\u4efb\u52a1\u3002\u56e0\u6b64\uff0c\u667a\u80fd\u5408\u7ea6\u7cfb\u7edf\u901a\u5e38\u9075\u5faa <code>if ... then ...</code> \u8bed\u53e5\u3002\u4f46\u662f\uff0c\u5373\u4fbf\u667a\u80fd\u5408\u7ea6\u6210\u4e3a\u4e00\u79cd\u4e3b\u6d41\u7684\u6280\u672f\uff0c\u4f46\u667a\u80fd\u5408\u7ea6\u65e2\u4e0d\u662f\u6cd5\u5b9a\u7684\u4e5f\u4e0d\u662f\u667a\u80fd\u5316\u7684\u3002\u5b83\u4eec\u53ea\u662f\u5728\u5206\u5e03\u5f0f\u7cfb\u7edf\uff08\u533a\u5757\u94fe\uff09\u4e0a\u8fd0\u884c\u7684\u4e00\u6bb5\u4ee3\u7801</p> <h3 id="16-\u4ee5\u592a\u574a\u548c\u6bd4\u7279\u5e01\u533a\u522b">1.6 \u4ee5\u592a\u574a\u548c\u6bd4\u7279\u5e01\u533a\u522b</h3> <p>\u4e0e\u6bd4\u7279\u5e01\u76f8\u4f3c\uff0c\u4ee5\u592a\u574a\u4e5f\u91c7\u7528 POW \u673a\u5236\uff0c\u901a\u8fc7\u77ff\u5de5\u6316\u77ff\u6765\u4ea7\u751f\u65b0\u7684\u4ee5\u592a\u574a\u3002\u4e0d\u540c\u7684\u662f\uff0c\u4ee5\u592a\u574a\u662f\u4e00\u4e2a\u53ef\u4ee5\u7f16\u7a0b\u7684\u533a\u5757\u94fe\uff0c\u5b83\u5141\u8bb8\u4efb\u4f55\u4eba\u5728\u4e0a\u9762\u521b\u5efa\u53bb\u4e2d\u5fc3\u5316\u7684 APP\u3001\u7f16\u5199\u667a\u80fd\u5408\u7ea6\u548c\u53d1\u884c\u5c5e\u4e8e\u81ea\u5df1\u7684\u6570\u5b57\u8d27\u5e01</p> <p>\u4e0d\u540c\u4e8e\u6bd4\u7279\u5e01\u7684\u8d27\u5e01\u5b9a\u4e49\uff0c\u4ee5\u592a\u574a\u91ca\u653e\u4e86\u533a\u5757\u94fe\u524d\u6240\u672a\u6709\u7684\u6f5c\u529b\uff0c\u901a\u8fc7\u4e00\u7cfb\u5217\u7684\u91cd\u65b0\u8bbe\u8ba1\uff0c\u5305\u62ec\u865a\u62df\u673a(EVM, Etheruem Virtual Machine)\u3001\u8d26\u6237\u7cfb\u7edf\uff0c\u5728\u5176\u4e0a\u53ef\u4ee5\u8fd0\u884c\u667a\u80fd\u5408\u7ea6</p> <h3 id="17-\u4ee5\u592a\u574a\u7684\u4f18\u7f3a\u70b9">1.7 \u4ee5\u592a\u574a\u7684\u4f18\u7f3a\u70b9</h3> <p><strong>\u4f18\u70b9\uff1a</strong></p> <ul> <li>\u4ee5\u592a\u574a\u5141\u8bb8\u7528\u6237\u5728\u533a\u5757\u94fe\u4e0a\u4f7f\u7528\u5b8c\u6574\u7684\u7f16\u7a0b\u8bed\u8a00\uff0c\u5728\u7f51\u7edc\u4e0a\u6267\u884c\u66f4\u590d\u6742\u7684\u667a\u80fd\u5408\u7ea6\uff0c\u800c\u4e0d\u9700\u8981\u4f9d\u9760\u4efb\u4f55\u7b2c\u4e09\u65b9\u670d\u52a1</li> <li>\u4ee5\u592a\u574a\u53ef\u4e3a\u5176\u4ed6\u4ea7\u54c1\u548c\u670d\u52a1\u5e73\u53f0\u63d0\u4f9b\u5f3a\u5927\u7684\u751f\u6001\u7cfb\u7edf</li> <li>\u4ee5\u592a\u574a\u62e5\u6709\u4e00\u4e2a\u5f3a\u5927\u7684\u53d1\u5c55\u8def\u7ebf\u56fe\uff083-5 \u5e74\uff09\uff0c\u8fd9\u610f\u5473\u7740\u793e\u533a\u5bf9\u672a\u6765\u51e0\u5e74\u7684\u9884\u671f\u6709\u660e\u786e\u7684\u6807\u51c6</li> <li>\u6709\u5f88\u591a\u516c\u53f8\u53c2\u4e0e\u6539\u5584\u4ee5\u592a\u574a\u4ee5\u5916\u7684\u57fa\u7840\u3002\u5728\u4ee5\u592a\u574a\u4f01\u4e1a\u8054\u76df\uff08EEA\uff09\u548c Hyperledger \u56e2\u961f\u6279\u51c6\u5176\u7b2c\u4e00\u4e2a\u4ee5\u592a\u574a\u9879\u76ee\u4e4b\u95f4\uff0c\u6709\u6570\u5341\u5bb6\u4f01\u4e1a\u7ec4\u7ec7\u81f4\u529b\u4e8e\u4ee5\u592a\u574a\u7684\u6539\u5584\u751f\u6001\u7cfb\u7edf\u3002\u9664\u4e86\u6bd4\u7279\u5e01\u4e4b\u5916\uff0c\u5176\u4ed6\u533a\u5757\u94fe\u8fd8\u6ca1\u6709\u5f97\u5230\u5982\u6b64\u591a\u5546\u4e1a\u793e\u533a\u7684\u5927\u529b\u652f\u6301</li> </ul> <p><strong>\u7f3a\u70b9\uff1a</strong></p> <ul> <li>\u56e0\u4e3a\u4ee5\u592a\u574a\u662f\u4e00\u4e2a\u5e73\u53f0\uff0c\u5b83\u6c38\u8fdc\u4e0d\u4f1a\u50cf\u4e13\u95e8\u8bbe\u8ba1\u6210\u52a0\u5bc6\u8d27\u5e01\u7684\u5176\u4ed6\u4e00\u6837\u6709\u6548\u3002\u6bd4\u7279\u5e01\u53ea\u662f\u771f\u6b63\u7684\u5206\u7c7b\u5e10\uff0c\u4f46\u662f\u4ee5\u592a\u574a\u6b63\u5728\u8bd5\u56fe\u6210\u4e3a\u4e00\u4e2a\u5206\u7c7b\u8d26/\u8d85\u7ea7\u8ba1\u7b97\u673a/\u667a\u80fd\u5408\u7ea6\u4ea7\u751f\u5668/\u7b49\u7b49\uff0c\u4e3a\u66f4\u591a\u7684\u7528\u6237\u63d0\u4f9b\u670d\u52a1\u3002\u8fd9\u79cd\u590d\u6742\u6027\u4f7f\u5176\u5177\u6709\u4e86\u7075\u6d3b\u6027\uff0c\u4f46\u662f\u5bf9\u4e8e\u4ee5\u4e0a\u4efb\u4f55\u4e00\u79cd\u7528\u4f8b\u800c\u8a00\uff0c\u90fd\u6ca1\u6709\u8fdb\u884c\u8fc7\u6df1\u5ea6\u4f18\u5316</li> <li>\u7531\u4e8e\u4ee5\u592a\u574a\u53d1\u5c55\u8def\u7ebf\u7684\u539f\u56e0\uff0c\u4ee5\u592a\u574a\u7684\u4e00\u4e9b\u91cd\u5927\u5347\u7ea7\u5305\u62ec\u5c06\u5de5\u4f5c\u8bc1\u660e\u8f6c\u79fb\u5230\u8bc1\u660e\u6743\u76ca\u7684\u5e73\u53f0\u3002\u5982\u679c\u8fd9\u79cd\u8f6c\u6362\u4e0d\u987a\u5229\uff0c\u53ef\u80fd\u4f1a\u5728\u67b6\u6784\u4e2d\u5f15\u5165\u4e00\u4e9b\u5173\u952e\u95ee\u9898\u5e76\u5bfc\u81f4\u7cfb\u7edf\u5d29\u6e83\u3002\u76f8\u4fe1\u4ee5\u592a\u574a\u56e2\u961f\u53ef\u4ee5\u786e\u4fdd\u8fd9\u4e00\u5207\u90fd\u4e0d\u4f1a\u53d1\u751f\uff0c\u4f46\u8fd9\u662f\u4e00\u79cd\u98ce\u9669</li> <li>\u7f3a\u4e4f\u5e2e\u52a9\u5f00\u53d1\u4eba\u5458\u7684\u6559\u7a0b\u6216\u6587\u6863\u662f\u6210\u4e3a\u4ee5\u592a\u574a\u5f00\u53d1\u4eba\u5458\u7684\u4e25\u91cd\u969c\u788d\u3002\u4f60\u6240\u641c\u7d22\u5230\u7684\u5927\u591a\u6570\u6559\u7a0b\u90fd\u662f\u8fc7\u65f6\u7684\uff0c\u6216\u8005\u53ea\u662f\u6559\u6388\u5bf9\u6280\u672f\u7684\u6982\u5ff5\u6027\u7406\u89e3\uff0c\u6216\u8005\u53ea\u662f\u4e00\u4e2a\u57fa\u672c\u7684 <code>hello world</code> \u6559\u7a0b\uff0c\u6ca1\u6709\u4efb\u4f55\u5b9e\u8d28\u6027\u5185\u5bb9</li> </ul> <h2 id="2-\u8d26\u6237">2 \u8d26\u6237</h2> <h3 id="21-\u6982\u5ff5">2.1 \u6982\u5ff5</h3> <p>\u4ee5\u592a\u574a\u8d26\u6237\u8d1f\u8d23\u5b58\u50a8\u7528\u6237\u7684\u4ee5\u592a\u574a\u4f59\u989d\u3002\u5bf9\u5927\u591a\u6570\u666e\u901a\u7528\u6237\u6765\u8bf4\uff0c<strong>\u4ee5\u592a\u574a\u8d26\u6237\u548c\u94f6\u884c\u8d26\u6237\u975e\u5e38\u7c7b\u4f3c</strong>\uff0c\u901a\u5e38\u53ea\u9700\u8981\u4e00\u4e2a\u8d26\u6237\u5373\u53ef</p> <p><strong>\u4ee5\u592a\u574a\u8d26\u6237\u5206\u4e3a\u5916\u90e8\u8d26\u6237\u548c\u5408\u7ea6\u8d26\u6237\u4e24\u7c7b\uff1a</strong></p> <ul> <li><strong>\u5916\u90e8\u8d26\u6237\uff1a</strong>\u5373\u666e\u901a\u7528\u6237\u7528\u79c1\u94a5\u63a7\u5236\u7684\u8d26\u6237</li> <li><strong>\u5408\u7ea6\u8d26\u6237\uff1a</strong>\u4e00\u79cd\u62e5\u6709\u5408\u7ea6\u4ee3\u7801\u7684\u8d26\u6237\uff0c\u5b83\u4e0d\u5c5e\u4e8e\u4efb\u4f55\u4eba\uff0c\u4e5f\u6ca1\u6709\u79c1\u94a5\u4e0e\u4e4b\u5bf9\u5e94</li> </ul> <h3 id="22-\u4ee5\u592a\u574a\u5730\u5740\u7b97\u6cd5">2.2 \u4ee5\u592a\u574a\u5730\u5740\u7b97\u6cd5</h3> <p>\u548c\u6bd4\u7279\u5e01\u7c7b\u4f3c\uff0c\u4e00\u4e2a\u4ee5\u592a\u574a\u8d26\u6237\u5c31\u662f\u4e00\u4e2a\u516c\u94a5\u54c8\u5e0c\u540e\u5f97\u5230\u7684\u5730\u5740\uff0c\u5b83\u662f\u7531\u4e00\u4e2a\u79c1\u94a5\u63a8\u5bfc\u51fa\u5bf9\u5e94\u7684\u516c\u94a5\uff0c\u7136\u540e\u518d\u8ba1\u7b97\u51fa\u5730\u5740\u3002\u5176\u4e2d\uff0c\u79c1\u94a5\u4e0e\u516c\u94a5\u7b97\u6cd5\u4e0e\u6bd4\u7279\u5e01\u5b8c\u5168\u76f8\u540c\uff0c\u5747\u4e3a <code>secp256k1</code> \u692d\u5706\u66f2\u7ebf\uff0c\u4f46\u548c\u6bd4\u7279\u5e01\u4e0d\u540c\u7684\u662f\uff0c\u4ee5\u592a\u574a\u91c7\u7528\u975e\u538b\u7f29\u516c\u94a5\uff0c\u7136\u540e\u76f4\u63a5\u5bf9\u516c\u94a5\u505a <code>keccak256</code> \u54c8\u5e0c\uff0c\u5f97\u5230 32 \u5b57\u8282\u7684\u54c8\u5e0c\u503c\uff0c\u53d6\u540e 20 \u5b57\u8282\u52a0\u4e0a <code>0x</code> \u524d\u7f00\u5373\u4e3a\u5730\u5740\uff1a</p> <p><img src="'+M+'" alt=""></p> <pre><code class="language-js">const randomBytes = require(&#39;randombytes&#39;)\uff1b\nconst ethUtil = require(&#39;ethereumjs-util&#39;);\n\n// \u751f\u6210 256bit \u7684\u968f\u673a\u6570\u4f5c\u4e3a\u79c1\u94a5:\nlet priKey = randomBytes(32).toString(&#39;hex&#39;);\n// \u8ba1\u7b97\u516c\u94a5(\u975e\u538b\u7f29\u683c\u5f0f):\nlet pubKey = ethUtil.privateToPublic(new Buffer(priKey, &#39;hex&#39;)).toString(&#39;hex&#39;);\n// \u8ba1\u7b97\u5730\u5740:\nlet addr = ethUtil.pubToAddress(new Buffer(pubKey, &#39;hex&#39;)).toString(&#39;hex&#39;);\n\nconsole.log(&#39;Private key: 0x&#39; + priKey);\nconsole.log(&#39;Public key: 0x&#39; + pubKey);\nconsole.log(&#39;Address: 0x&#39; + addr);</code></pre> <blockquote> <p>\u548c\u6bd4\u7279\u5e01\u91c7\u7528 <code>Base58</code> \u6216 <code>Bech32</code> \u7f16\u7801\u4e0d\u540c\uff0c\u4ee5\u592a\u574a\u5bf9\u79c1\u94a5\u548c\u5730\u5740\u5747\u91c7\u7528\u5341\u516d\u8fdb\u5236\u7f16\u7801\uff0c\u56e0\u6b64\u5b83\u6ca1\u6709\u4efb\u4f55\u6821\u9a8c\uff0c\u5982\u679c\u67d0\u4e00\u4f4d\u5199\u9519\u4e86\uff0c\u4ecd\u7136\u662f\u4e00\u4e2a\u6709\u6548\u7684\u79c1\u94a5\u6216\u5730\u5740</p> </blockquote> <blockquote> <p><code>keccak256</code> \u54c8\u5e0c\u7b97\u6cd5\u5728\u4ee5\u592a\u574a\u4e2d\u4e5f\u88ab\u79f0\u4e3a <code>SHA3</code> \u7b97\u6cd5\u3002<strong>\u4f46\u662f\u8981\u6ce8\u610f</strong>\uff0c<code>keccak</code> \u7b97\u6cd5\u539f\u672c\u662f <code>SHA3</code> \u7684\u5019\u9009\u7b97\u6cd5\uff0c\u7136\u800c\u5728 <code>SHA3</code> \u6700\u540e\u7684\u6807\u51c6\u5316\u65f6\uff0c\u5bf9 <code>keccak</code> \u505a\u4e86\u6539\u8fdb\uff0c\u56e0\u6b64\uff0c\u6807\u51c6\u7684 <code>SHA3</code> \u7b97\u6cd5\u548c <code>keccak</code> \u662f\u4e0d\u540c\u7684\uff0c\u53ea\u662f\u4ee5\u592a\u574a\u5728\u5f00\u53d1\u65f6\u5c31\u9009\u62e9\u4e86\u5c1a\u672a\u6210\u4e3a <code>SHA3</code> \u6807\u51c6\u7684 <code>keccak</code> \u7b97\u6cd5\u3002\u540e\u7eed\u6211\u4eec\u5728\u8ba8\u8bba\u4ee5\u592a\u574a\u7684\u54c8\u5e0c\u7b97\u6cd5\u65f6\uff0c\u4e00\u5f8b\u4f7f\u7528 <code>keccak256</code> \u800c\u4e0d\u662f <code>SHA3-256</code></p> </blockquote> <h3 id="23-\u5e26\u6821\u9a8c\u7684\u5730\u5740">2.3 \u5e26\u6821\u9a8c\u7684\u5730\u5740</h3> <p>\u56e0\u4e3a\u4ee5\u592a\u574a\u7684\u5730\u5740\u5c31\u662f\u539f\u59cb\u54c8\u5e0c\u7684\u540e 20 \u5b57\u8282\uff0c\u5e76\u4e14\u4ee5\u5341\u516d\u8fdb\u5236\u8868\u793a\uff0c\u8fd9\u79cd\u65b9\u6cd5\u7b80\u5355\u7c97\u66b4\uff0c\u4f46\u6ca1\u6709\u6821\u9a8c\u3002\u5730\u5740\u4e2d\u4efb\u4f55\u6570\u5b57\u51fa\u9519\u90fd\u4ecd\u662f\u4e00\u4e2a\u6709\u6548\u5730\u5740</p> <p>\u4e3a\u4e86\u9632\u6b62\u6284\u9519\uff0c\u4ee5\u592a\u574a\u901a\u8fc7 <code>EIP-55</code> \u5b9e\u73b0\u4e86\u4e00\u4e2a\u5e26\u6821\u9a8c\u7684\u5730\u5740\u683c\u5f0f\uff0c\u5b83\u7684\u5b9e\u73b0\u975e\u5e38\u7b80\u5355\uff0c\u5373\u5bf9\u5730\u5740\u505a\u4e00\u4e2a <code>keccak256</code> \u54c8\u5e0c\uff0c\u7136\u540e\u6309\u4f4d\u5bf9\u9f50\uff0c\u5c06\u54c8\u5e0c\u503c <code>&gt;=8</code> \u7684\u5b57\u6bcd\u53d8\u6210\u5927\u5199\uff1a</p> <pre><code>original addr = 0x29717bf51d8afca452459936d395668a576bce66\nkeccak hash = e72ecce2eb2ed0ffab5e05f043ee68fab3df759d...\nchecksum addr = 0x29717BF51D8AFcA452459936d395668A576Bce66</code></pre><p>\u4ee5\u592a\u574a\u5730\u5740\u5c31\u662f\u4f9d\u9760\u90e8\u5206\u53d8\u6210\u5927\u5199\u7684\u5b57\u6bcd\u8fdb\u884c\u6821\u9a8c\uff0c\u5b83\u7684\u597d\u5904\u662f\u5e26\u6821\u9a8c\u7684\u5730\u5740\u548c\u4e0d\u5e26\u6821\u9a8c\u7684\u5730\u5740\u5bf9\u94b1\u5305\u8f6f\u4ef6\u90fd\u662f\u4e00\u6837\u7684\u683c\u5f0f\uff0c\u7f3a\u70b9\u662f\u6709\u5f88\u5c0f\u7684\u6982\u7387\u65e0\u6cd5\u6821\u9a8c\u5168\u90e8\u5c0f\u5199\u7684\u5730\u5740</p> <h3 id="24-hd-\u94b1\u5305">2.4 HD \u94b1\u5305</h3> <p>\u56e0\u4e3a\u4ee5\u592a\u574a\u548c\u6bd4\u7279\u5e01\u7684\u975e\u5bf9\u79f0\u52a0\u5bc6\u7b97\u6cd5\u662f\u5b8c\u5168\u76f8\u540c\u7684\uff0c\u4e0d\u540c\u7684\u4ec5\u4ec5\u662f\u516c\u94a5\u548c\u5730\u5740\u7684\u8868\u793a\u683c\u5f0f\uff0c\u56e0\u6b64\uff0c\u6bd4\u7279\u5e01\u7684 HD \u94b1\u5305\u4f53\u7cfb\u4e5f\u5b8c\u5168\u9002\u7528\u4e8e\u4ee5\u592a\u574a\u3002\u7528\u6237\u901a\u8fc7\u4e00\u5957\u52a9\u8bb0\u8bcd\uff0c\u65e2\u53ef\u4ee5\u7ba1\u7406\u6bd4\u7279\u5e01\u94b1\u5305\uff0c\u4e5f\u53ef\u4ee5\u7ba1\u7406\u4ee5\u592a\u574a\u94b1\u5305</p> <p>\u4ee5\u592a\u574a\u94b1\u5305\u7684\u6d3e\u751f\u8def\u5f84\u662f <code>m/44&#39;/60&#39;/0&#39;/0/0</code></p> <p><strong>\u7528\u4ee3\u7801\u5b9e\u73b0\u5982\u4e0b\uff1a</strong></p> <pre><code class="language-js">const bitcoin = require(&#39;bitcoinjs-lib&#39;);\nconst bip39 = require(&#39;bip39&#39;);\nconst ethUtil = require(&#39;ethereumjs-util&#39;);\n\n// \u52a9\u8bb0\u8bcd\u548c\u53e3\u4ee4:\nlet words =\n  &#39;bleak version runway tell hour unfold donkey defy digital abuse glide please omit much cement sea sweet tenant demise taste emerge inject cause link&#39;;\nlet password = &#39;bitcoin&#39;;\n\n// \u8ba1\u7b97 seed:\nlet seedHex = bip39.mnemonicToSeedHex(words, password);\nconsole.log(&#39;seed: &#39; + seedHex); // b59a8078...c9ebfaaa\n\n// \u751f\u6210 root:\nlet root = bitcoin.HDNode.fromSeedHex(seedHex);\nconsole.log(&#39;xprv: &#39; + root.toBase58()); // xprv9s21ZrQH...uLgyr9kF\nconsole.log(&#39;xpub: &#39; + root.neutered().toBase58()); // xpub661MyMwA...oy32fcRG\n\n// \u751f\u6210\u6d3e\u751f key:\nlet child0 = root.derivePath(&quot;m/44&#39;/60&#39;/0&#39;/0/0&quot;);\nlet prvKey = child0.keyPair.d.toString(16);\nlet pubKey = ethUtil.privateToPublic(new Buffer(prvKey, &#39;hex&#39;)).toString(&#39;hex&#39;);\nlet address = &#39;0x&#39; + ethUtil.pubToAddress(new Buffer(pubKey, &#39;hex&#39;)).toString(&#39;hex&#39;);\nlet checksumAddr = ethUtil.toChecksumAddress(address);\n\nconsole.log(&quot; prv m/44&#39;/60&#39;/0&#39;/0/0: 0x&quot; + prvKey); // 0x6c03e50ae20af44b9608109fc978bdc8f081e7b0aa3b9d0295297eb20d72c1c2\nconsole.log(&quot; pub m/44&#39;/60&#39;/0&#39;/0/0: 0x&quot; + pubKey); // 0xff10c2376a9ff0974b28d97bc70daa42cf85826ba83e985c91269e8c975f75f7d56b9f5071911fb106e48b2dbb2b30e0558faa2fc687a813113632c87c3b051c\nconsole.log(&quot; addr m/44&#39;/60&#39;/0&#39;/0/0: &quot; + address); // 0x9759be9e1f8994432820739d7217d889918f2f07\nconsole.log(&quot;check-addr m/44&#39;/60&#39;/0&#39;/0/0: &quot; + checksumAddr); // 0x9759bE9e1f8994432820739D7217D889918f2f07</code></pre> <p>\u56e0\u4e3a\u4ee5\u592a\u574a\u91c7\u7528\u8d26\u6237\u4f59\u989d\u6a21\u578b\uff0c\u901a\u5e38\u60c5\u51b5\u4e0b\u4e00\u4e2a\u4ee5\u592a\u574a\u5730\u5740\u5df2\u591f\u7528\u3002\u5982\u679c\u8981\u751f\u6210\u591a\u4e2a\u5730\u5740\uff0c\u53ef\u7ee7\u7eed\u6d3e\u751f <code>m/44&#39;/60&#39;/0&#39;/0/1</code>\u3001<code>m/44&#39;/60&#39;/0&#39;/0/2</code> \u7b49</p> <h2 id="3-\u533a\u5757\u7ed3\u6784">3 \u533a\u5757\u7ed3\u6784</h2> <h3 id="31-\u548c-btc-\u7684\u533a\u522b">3.1 \u548c BTC \u7684\u533a\u522b</h3> <p>\u6bd4\u7279\u5e01\u7684\u533a\u5757\u94fe\u662f\u7531 <code>PoW</code> \u4fdd\u8bc1\u6bcf\u4e2a\u533a\u5757\u90fd\u6307\u5411\u524d\u4e00\u4e2a\u533a\u5757\uff0c\u800c\u5728\u6bcf\u4e00\u4e2a\u533a\u5757\u5185\u90e8\uff0c\u7531\u4e00\u4e2a\u72ec\u7acb\u7684 <code>Merkle Tree</code> \u6765\u4fdd\u8bc1\u6240\u6709\u4ea4\u6613\u7684\u4e0d\u53ef\u7be1\u6539\u3002\u7528\u6237\u7684\u6bd4\u7279\u5e01\u662f\u4ee5 <code>UTXO</code> \u7684\u65b9\u5f0f\u5b58\u50a8\u7684\uff0c\u56e0\u6b64\uff0c\u6bd4\u7279\u5e01\u7684\u4ea4\u6613\u5c31\u662f\u4e0d\u65ad\u5730\u6d88\u8017\u73b0\u6709\u7684 <code>UTXO</code>\uff0c\u5e76\u4ea7\u751f\u65b0\u7684 <code>UTXO</code></p> <p>\u800c<strong>\u4ee5\u592a\u574a\u91c7\u7528\u7684\u662f\u8d26\u6237\u6a21\u578b</strong>\uff0c\u6240\u4ee5\u4ee5\u592a\u574a\u7684\u533a\u5757\u7ed3\u6784\u5fc5\u987b\u80fd\u5728\u6bcf\u4e2a\u533a\u5757\u6301\u7eed\u5730\u8ddf\u8e2a\u5e76\u8bb0\u5f55\u7528\u6237\u7684\u8d26\u6237\u4f59\u989d\u53d8\u52a8\u3002\u56e0\u6b64\u548c\u6bd4\u7279\u5e01\u76f8\u6bd4\uff0c\u4ee5\u592a\u574a\u7684\u533a\u5757\u6570\u636e\u7ed3\u6784\u66f4\u52a0\u590d\u6742</p> <h3 id="32-merkle-patricia-tree">3.2 Merkle Patricia Tree</h3> <p>\u4ee5\u592a\u574a\u5b58\u50a8\u8d26\u6237\u6570\u636e\u7684\u6570\u636e\u7ed3\u6784\u662f <code>MPT\uff1aMerkle Patricia Tree</code>\uff0c\u5b83\u662f\u4e00\u79cd\u6539\u8fdb\u7684 <code>Merkle Tree</code>\u3002\u5f53 <code>MPT</code> \u7684\u6bcf\u4e2a\u53f6\u5b50\u7ed3\u70b9\u7684\u503c\u786e\u5b9a\u540e\uff0c\u8ba1\u7b97\u51fa\u7684 <code>Root Hash</code> \u5c31\u662f\u5b8c\u5168\u786e\u5b9a\u7684</p> <p>\u4f8b\u5982\uff0c\u5728\u7b2c\u4e00\u4e2a\u533a\u5757\u4e2d\uff0c4 \u4e2a\u8d26\u6237\u7684\u4f59\u989d\u786e\u5b9a\u540e\uff0c\u5373\u53ef\u786e\u5b9a Root1\uff1a</p> <p><img src="'+P+'" alt=""></p> <p>\u5728\u7b2c\u4e8c\u4e2a\u533a\u5757\u4e2d\uff0c\u5982\u679c\u53d1\u751f\u4e86\u8f6c\u8d26\uff0c\u5c06\u8ba1\u7b97\u51fa\u4e00\u4e2a\u65b0\u7684 Root2\uff1a</p> <p><img src="'+q+'" alt=""></p> <p>\u6bcf\u4e00\u4e2a\u533a\u5757\u901a\u8fc7 <code>Root Hash</code> \u5c06\u5b8c\u5168\u786e\u5b9a\u6240\u6709\u8d26\u6237\u7684\u72b6\u6001\uff0c\u6240\u4ee5\uff0c\u4ece\u5168\u5c40\u770b\uff0c\u4ee5\u592a\u574a\u5c31\u662f\u4e00\u4e2a\u72b6\u6001\u673a\uff0c\u6bcf\u4e2a\u533a\u5757\u901a\u8fc7\u8bb0\u5f55\u4e00\u4e2a <code>stateRoot</code> \u6765\u8868\u793a\u4e00\u4e2a\u65b0\u72b6\u6001\u3002\u5982\u679c\u7ed9\u5b9a\u67d0\u4e2a\u533a\u5757\u7684 <code>stateRoot</code>\uff0c\u6211\u4eec\u80af\u5b9a\u80fd\u5b8c\u5168\u786e\u5b9a\u6240\u6709\u8d26\u6237\u7684\u6240\u6709\u4f59\u989d\u7b49\u4fe1\u606f\u3002\u56e0\u6b64\uff0c<strong>stateRoot \u5c31\u88ab\u79f0\u4e3a\u5f53\u524d\u7684\u4e16\u754c\u72b6\u6001</strong></p> <p>\u5982\u679c\u7b2c\u4e00\u4e2a\u533a\u5757\u53ea\u6709\u51e0\u4e2a\u8d26\u6237\uff0c\u968f\u7740\u8d26\u6237\u7684\u589e\u52a0\uff0c\u5982\u679c\u6709\u6570\u767e\u4e07\u4e2a\u8d26\u6237\uff0c\u5230\u540e\u9762\u5c82\u4e0d\u662f\u533a\u5757\u5b58\u50a8\u7684\u6570\u636e\u91cf\u4f1a\u8d8a\u6765\u8d8a\u5927\uff1f</p> <blockquote> <p>\u5b9e\u9645\u4e0a\uff0c\u6bcf\u4e2a\u533a\u5757\u7684 <code>stateRoot</code> \u8868\u793a\u7684\u662f\u4e00\u4e2a\u5b8c\u5168\u72b6\u6001\u7684\u903b\u8f91\u6811\uff0c\u4f46\u6bcf\u4e2a\u533a\u5757\u8bb0\u5f55\u7684\u6570\u636e\u53ea\u5305\u62ec\u4fee\u6539\u7684\u90e8\u5206\uff0c\u5982\u679c\u6211\u4eec\u89c2\u5bdf\u7b2c\u4e8c\u4e2a\u533a\u5757\u7684\u6811\uff0c\u5b83\u5b9e\u9645\u4e0a\u53ea\u8bb0\u5f55\u4fee\u6539\u7684\u4e24\u4e2a\u8d26\u6237\uff0c\u4ee5\u53ca\u4e24\u4e2a\u8d26\u6237\u56e0\u4fee\u6539\u540e\u5bfc\u81f4\u7684\u4e0a\u5c42\u8def\u5f84\u7684 Hash \u53d1\u751f\u7684\u53d8\u5316\uff1a</p> </blockquote> <p><img src="'+y+'" alt=""></p> <p>\u60f3\u8981\u5c06\u4e00\u4e2a\u6709\u51e0\u767e\u4e07\u8282\u70b9\u7684\u6811\u5b8c\u6574\u5730\u653e\u5165\u5185\u5b58\u9700\u8981\u6d88\u8017\u5927\u91cf\u7684\u5185\u5b58\uff0c\u800c\u4ee5\u592a\u574a\u5168\u8282\u70b9\u4e5f\u5e76\u4e0d\u4f1a\u5c06\u6574\u9897\u903b\u8f91\u6811\u653e\u5165\u5185\u5b58\u3002\u5b9e\u9645\u4e0a\uff0c\u6bcf\u4e2a\u8282\u70b9\u7684\u6570\u636e\u88ab\u5b58\u653e\u5230 <code>LevelDB</code> \u4e2d\uff0c\u8282\u70b9\u4ec5\u5728\u5185\u5b58\u4e2d\u5b58\u50a8\u5f53\u524d\u6d3b\u52a8\u7684\u4e00\u4e9b\u8d26\u6237\u4fe1\u606f\u3002\u5982\u679c\u9700\u8981\u64cd\u4f5c\u67d0\u4e2a\u4e0d\u5728\u5185\u5b58\u7684\u8d26\u6237\uff0c\u5219\u4f1a\u5c06\u5176\u4ece <code>LevelDB</code> \u52a0\u8f7d\u5230\u5185\u5b58\u3002\u5982\u679c\u5185\u5b58\u4e0d\u591f\uff0c\u4e5f\u4f1a\u5c06\u957f\u671f\u4e0d\u6d3b\u52a8\u7684\u8282\u70b9\u4ece\u5185\u5b58\u4e2d\u79fb\u9664\uff0c\u56e0\u4e3a\u5c06\u6765\u53ef\u4ee5\u901a\u8fc7\u8282\u70b9\u7684\u8def\u5f84\u518d\u6b21\u4ece <code>LevelDB</code> \u52a0\u8f7d</p> <h3 id="33-\u8d26\u6237\u6570\u636e">3.3 \u8d26\u6237\u6570\u636e</h3> <p><strong>\u4e00\u4e2a\u4ee5\u592a\u574a\u8d26\u6237\u7531 4 \u90e8\u5206\u6570\u636e\u6784\u6210\uff1a</strong></p> <ul> <li><strong>nonce:</strong> \u4e00\u4e2a\u9012\u589e\u7684\u6574\u6570\uff0c\u6bcf\u53d1\u9001\u4e00\u6b21\u4ea4\u6613\uff0cnonce \u9012\u589e 1\uff0c\u56e0\u6b64\uff0cnonce \u8bb0\u5f55\u7684\u5c31\u662f\u4ea4\u6613\u6b21\u6570</li> <li><strong>balance:</strong> \u8bb0\u5f55\u7684\u5c31\u662f\u8d26\u6237\u4f59\u989d\uff0c\u4ee5 wei \u4e3a\u5355\u4f4d\uff0c1 Ether \u7b49\u4e8e <code>10^18 wei</code></li> <li><strong>storageRoot:</strong> storageRoot \u5b58\u50a8\u5408\u7ea6\u76f8\u5173\u7684\u72b6\u6001\u6570\u636e\u3002\u5408\u7ea6\u8d26\u6237\u624d\u6709\u8fd9\u4e2a\u6570\u636e</li> <li><strong>codeHash:</strong> \u5b58\u50a8\u5408\u7ea6\u4ee3\u7801\u7684 Hash\u3002\u5408\u7ea6\u8d26\u6237\u624d\u6709\u8fd9\u4e2a\u6570\u636e</li> </ul> <h3 id="34-\u533a\u5757\u6570\u636e">3.4 \u533a\u5757\u6570\u636e</h3> <p>\u4e00\u4e2a\u4ee5\u592a\u574a\u533a\u5757\u7531\u533a\u5757\u5934\u548c\u4e00\u7cfb\u5217\u4ea4\u6613\u6784\u6210\u3002\u533a\u5757\u5934\u9664\u4e86\u8bb0\u5f55 <code>parentHash</code>\uff08\u4e0a\u4e00\u4e2a\u533a\u5757\u7684 Hash\uff09\u3001<code>stateRoot</code>\uff08\u4e16\u754c\u72b6\u6001\uff09\u5916\uff0c\u8fd8\u5305\u62ec\uff1a</p> <ul> <li><strong>sha3Uncles\uff1a</strong>\u8bb0\u5f55\u5f15\u7528\u7684\u53d4\u5757</li> <li><strong>transactionRoot\uff1a</strong>\u8bb0\u5f55\u5f53\u524d\u533a\u5757\u6240\u6709\u4ea4\u6613\u7684 Root Hash</li> <li><strong>receiptsRoot\uff1a</strong>\u8bb0\u5f55\u5f53\u524d\u533a\u5757\u6240\u6709\u4ea4\u6613\u56de\u6267\u7684 Root Hash</li> <li><strong>logsBloom\uff1a</strong>\u4e00\u4e2a Bloom Filter\uff0c\u7528\u4e8e\u5feb\u901f\u67e5\u627e Log</li> <li><strong>difficulty\uff1a</strong>\u6316\u77ff\u96be\u5ea6\u503c</li> <li><strong>number\uff1a</strong>\u533a\u5757\u9ad8\u5ea6\uff0c\u4e25\u683c\u9012\u589e\u7684\u6574\u6570</li> <li><strong>timestamp\uff1a</strong>\u533a\u5757\u7684\u65f6\u95f4\u6233\uff08\u4ee5\u79d2\u4e3a\u5355\u4f4d\uff09</li> </ul> <p><code>transactionRoot</code> \u548c <code>receiptsRoot</code> \u4e5f\u662f\u4e24\u4e2a <code>MPT</code> \u6811\uff0c\u4f46\u4ed6\u4e24\u548c <code>stateRoot</code> \u4e0d\u540c\uff0c\u4ed6\u4eec\u4ec5\u8868\u793a\u5f53\u524d\u533a\u5757\u7684\u4e24\u68f5\u6811\uff0c\u4e0e\u524d\u9762\u7684\u533a\u5757\u72b6\u6001\u65e0\u5173</p> <h3 id="35-\u53d4\u5757">3.5 \u53d4\u5757</h3> <p>\u4ee5\u592a\u574a\u91c7\u7528\u548c\u6bd4\u7279\u5e01\u7c7b\u4f3c\u7684 <code>PoW</code> \u6316\u77ff\uff0c\u53ea\u662f\u7b97\u6cd5\u4e3a\u6539\u8fdb\u7684 <code>Ethash</code> \u7b97\u6cd5\u3002<code>PoW</code> \u6316\u77ff\u80af\u5b9a\u4f1a\u4ea7\u751f\u5206\u53c9\uff0c\u4f46\u7531\u4e8e\u6700\u957f\u94fe\u5171\u8bc6\uff0c\u6700\u7ec8\u67d0\u4e2a\u5206\u53c9\u5c06\u80dc\u51fa\uff1a</p> <p><img src="'+E+'" alt=""></p> <p>\u4f46\u662f\u548c\u6bd4\u7279\u5e01\u4e0d\u540c\u7684\u662f\uff0c\u867d\u7136 <code>#4</code> \u7684\u7ade\u4e89\u5bfc\u81f4\u4e00\u4e2a\u80dc\u51fa\u53e6\u4e00\u4e2a\u5931\u8d25\uff0c\u4f46\u4ee5\u592a\u574a\u9f13\u52b1\u540e\u7eed\u7684 <code>#5</code> \u533a\u5757\u5f15\u7528\u53e6\u4e00\u4e2a\u5e9f\u5f03\u7684 <code>#4</code> \u5757\uff0c\u8fd9\u79cd\u5f15\u7528\u7684\u5e9f\u5f03\u5757\u88ab\u79f0\u4e3a\u53d4\u5757\uff08Uncle Block\uff09\uff1a</p> <p><img src="'+T+'" alt=""></p> <p>\u533a\u5757\u5934\u8bb0\u5f55\u7684 <code>sha3Uncles</code> \u5c31\u662f\u53d4\u5757\uff0c\u4e00\u4e2a\u533a\u5757\u53ef\u5f15\u7528 <code>0 \uff5e 2</code> \u4e2a\u53d4\u5757\uff0c\u4e14\u53d4\u5757\u9ad8\u5ea6\u5fc5\u987b\u5728\u524d 7 \u5c42\u4e4b\u5185\u3002</p> <p>\u53d4\u5757\u7684\u76ee\u7684\u662f\u7ed9\u4e88\u7ade\u4e89\u5931\u8d25\u7684\u77ff\u5de5\u90e8\u5206\u5956\u52b1\uff0c\u907f\u514d\u51fa\u73b0\u8f83\u957f\u7684\u5206\u53c9</p> <h2 id="4-\u4ea4\u6613">4 \u4ea4\u6613</h2> <h3 id="41-\u624b\u7eed\u8d39-gas">4.1 \u624b\u7eed\u8d39 Gas</h3> <p>\u5728\u6bd4\u7279\u5e01\u4e2d\uff0c\u4ea4\u6613\u5c31\u662f\u6d88\u8017\u5df2\u6709 <code>UTXO</code>\uff0c\u5e76\u901a\u8fc7\u6267\u884c\u811a\u672c\u4ea7\u751f\u65b0\u7684 <code>UTXO</code>\uff0c\u5176\u4e2d\u9690\u542b\u7684\u65b0\u65e7\u5dee\u989d\u5373\u4e3a\u77ff\u5de5\u624b\u7eed\u8d39\u3002</p> <p>\u5728\u4ee5\u592a\u574a\u4e2d\uff0c\u4ea4\u6613\u4e5f\u9700\u8981\u624b\u7eed\u8d39\uff0c\u624b\u7eed\u8d39\u88ab\u79f0\u4e3a <code>Gas</code>\uff0c\u5b83\u7684\u8ba1\u7b97\u6bd4\u6bd4\u7279\u5e01\u8981\u590d\u6742\u5f97\u591a</p> <p>\u4ee5\u592a\u574a\u9664\u4e86\u6700\u57fa\u672c\u7684\u8f6c\u8d26\uff08\u5373\u4ece\u4e00\u4e2a\u8d26\u6237\u652f\u4ed8 Ether \u5230\u53e6\u4e00\u4e2a\u8d26\u6237\uff09\uff0c\u8fd8<strong>\u652f\u6301\u6267\u884c\u5408\u7ea6\u4ee3\u7801</strong>\u3002\u5408\u7ea6\u4ee3\u7801\u662f\u56fe\u7075\u5b8c\u5907\u7684\u7f16\u7a0b\u8bed\u8a00\uff0c\u901a\u8fc7 EVM\uff08\u4ee5\u592a\u574a\u865a\u62df\u673a\uff09\u6267\u884c</p> <blockquote> <p>\u5982\u679c\u67d0\u4e2a\u5408\u7ea6\u7f16\u5199\u4e86\u4e00\u4e2a\u65e0\u9650\u5faa\u73af\uff0c\u90a3\u4e48\u6240\u6709\u8282\u70b9\u6267\u884c\u8be5\u5408\u7ea6\u4ee3\u7801\uff0c\u90a3\u4e0d\u662f\u6c38\u8fdc\u65e0\u6cd5\u7ed3\u675f\uff1f</p> </blockquote> <p><strong>\u5408\u7ea6\u6267\u884c\u9700\u8981\u6d88\u8017 Gas\uff1a</strong></p> <p>\u4e3a\u4e86\u4fdd\u8bc1\u5408\u7ea6\u4ee3\u7801\u7684\u53ef\u9760\u6267\u884c\uff0c\u4ee5\u592a\u574a\u7ed9\u6bcf\u4e00\u4e2a\u865a\u62df\u673a\u6307\u4ee4\u90fd\u6807\u8bb0\u4e86\u4e00\u4e2a <code>Gas</code> \u57fa\u672c\u8d39\u7528\uff0c\u79f0\u4e3a <code>gasUsed</code></p> <p>\u4f8b\u5982\uff0c\u52a0\u51cf\u8ba1\u7b97\u7684\u8d39\u7528\u662f 3\uff0c\u8ba1\u7b97 <code>SHA3</code> \u7684\u8d39\u7528\u662f 30\uff0c\u8f93\u51fa\u65e5\u5fd7\u7684\u8d39\u7528\u662f 375\uff0c\u5199\u5165\u5b58\u50a8\u7684\u8d39\u7528\u9ad8\u8fbe 20000\u3002\u603b\u7684\u6765\u8bf4\uff0c\u6d88\u8017 CPU \u6bd4\u6d88\u8017\u5b58\u50a8\u4fbf\u5b9c\uff0c\u7b80\u5355\u8ba1\u7b97\u6bd4\u590d\u6742\u8ba1\u7b97\u4fbf\u5b9c\uff0c\u8bfb\u53d6\u6bd4\u5199\u5165\u4fbf\u5b9c</p> <p>\u9664\u4e86 <code>gasUsed</code> \u5916\uff0c\u7528\u6237\u8fd8\u9700\u8981\u63d0\u4f9b\u4e00\u4e2a <code>gasPrice</code>\uff0c\u4ee5 <code>Gwei\uff081Gwei = 10^9 Wei\uff09</code>\u4e3a\u5355\u4f4d\u3002\u901a\u8fc7\u7ade\u4ef7\u5f97\u5230\u4e00\u4e2a\u77ff\u5de5\u613f\u610f\u63a5\u53d7\u7684 <code>gasPrice</code>\u3002\u5982\u679c\u4e00\u4e2a\u4ea4\u6613\u6d88\u8017\u4e86 120000 \u7684 <code>gasUsed</code>\uff0c\u800c <code>gasPrice</code> \u662f 50 Gwei\uff0c\u5219\u4ea4\u6613\u8d39\u7528\u662f\uff1a</p> <pre><code>120000 x 50 Gwei = 6000000 Gwei = 0.006 Ether</code></pre><p><strong>\u9884\u652f Gas\uff1a</strong></p> <p>\u5728\u6267\u884c\u4ee3\u7801\u7684\u65f6\u5019\uff0c\u5b58\u5728\u6761\u4ef6\u5224\u65ad\u3001\u5faa\u73af\u7b49\u8bed\u53e5\uff0c\u540c\u4e00\u6bb5\u4ee3\u7801\uff0c\u6267\u884c\u7684\u7ed3\u679c\u4e5f\u53ef\u80fd\u4e0d\u540c\uff0c\u56e0\u6b64\uff0c\u4e8b\u524d\u9884\u8ba1\u4e00\u4e2a\u5408\u7ea6\u6267\u884c\u8981\u82b1\u8d39\u591a\u5c11 Gas\uff0c\u4e0d\u73b0\u5b9e</p> <p>\u6240\u4ee5\u4ee5\u592a\u574a\u89c4\u5b9a\uff0c\u4e00\u7b14\u4ea4\u6613\uff0c\u5148\u7ed9\u51fa <code>gasPrice</code> \u548c <code>gasLimit</code>\uff0c\u5982\u679c\u6267\u884c\u5b8c\u6210\u540e\u6709\u5269\u4f59\uff0c\u5269\u4f59\u7684\u9000\u8fd8\uff0c\u5982\u679c\u6267\u884c\u8fc7\u7a0b\u4e2d\u6d88\u8017\u6b86\u5c3d\uff0c\u90a3\u4e48\u4ea4\u6613\u6267\u884c\u5931\u8d25\uff0c\u4f46\u5df2\u6267\u884c\u7684 Gas \u4e0d\u4f1a\u9000</p> <p>\u5047\u5b9a\u67d0\u4e2a\u8d26\u6237\u60f3\u6267\u884c\u4e00\u7b14\u4ea4\u6613\uff0c\u4ed6\u7ed9\u51fa gasPrice \u4e3a 50 Gwei\uff0c\u9884\u4f30 <code>gasUsed</code> \u7ea6 120000\uff0c\u8bbe\u5b9a <code>gasLimit</code> \u4e3a 150000\uff0c\u5219\u9884\u652f\u4ed8\u7684 Ether \u4e3a\uff1a</p> <pre><code>150000 x 50 Gwei = 7500000 Gwei = 0.0075 Ether</code></pre><p>\u5982\u679c\u8d26\u6237\u7684 <code>Ether</code> \u4f59\u989d\u4e0d\u8db3 0.0075\uff0c\u5219\u8be5\u4ea4\u6613\u6839\u672c\u65e0\u6cd5\u53d1\u9001\u3002\u5982\u679c\u8d26\u6237\u4f59\u989d\u7b49\u4e8e\u6216\u8d85\u8fc7 0.0075\uff0c\u4f8b\u5982\u4f59\u989d\u4e3a 0.008\uff0c\u5219\u77ff\u5de5\u53ef\u4ee5\u5c06\u8be5\u4ea4\u6613\u6253\u5305\u3002\u5047\u8bbe\u5b9e\u9645\u6267\u884c\u6d88\u8017\u7684 <code>gasUsed</code> \u4e3a 120000\uff0c\u5219\u4ea4\u6613\u8d39\u7528 0.006\uff0c\u8d26\u6237\u5269\u4f59 0.002</p> <blockquote> <p>\u5f88\u5c11\u6709\u4ea4\u6613\u80fd\u51c6\u786e\u9884\u4f30 <code>gasUsed</code>\uff0c\u53ea\u6709\u6807\u51c6\u8f6c\u8d26\u4ea4\u6613\u662f 21000\uff0c\u56e0\u6b64\uff0c\u6807\u51c6\u7684\u8f6c\u8d26\u4ea4\u6613 <code>gasLimit</code> \u53ef\u4ee5\u8bbe\u7f6e\u4e3a 21000\uff08\u5373\u6070\u597d\u6d88\u8017\u5b8c\u6bd5\u65e0\u5269\u4f59\uff09</p> </blockquote> <blockquote> <p>Gas Price \u662f\u5168\u7f51\u7528\u6237\u7ade\u4ef7\u4ea7\u751f\u7684\uff0c\u5b83\u65f6\u523b\u5728\u6ce2\u52a8\u3002\u5982\u679c\u4ea4\u6613\u5c11\uff0cGas Price \u5c06\u4e0b\u964d\uff0c\u5982\u679c\u4ea4\u6613\u591a\uff0c\u7f51\u7edc\u62e5\u5835\uff0c\u5219 Gas Price \u5c06\u4e0a\u5347\u3002\u4ee5\u592a\u574a\u7684 Gas \u4ef7\u683c\u53ef\u4ee5\u5728 <code>Etherscan</code> \u8ddf\u8e2a</p> </blockquote> <h3 id="42-\u4ea4\u6613\u56de\u6267">4.2 \u4ea4\u6613\u56de\u6267</h3> <p>\u4ee5\u592a\u574a\u533a\u5757\u4e3a\u6bcf\u4e00\u7b14\u4ea4\u6613\u90fd\u4f1a\u4ea7\u751f\u4e00\u7b14\u56de\u6267\uff08Recipt\uff09\uff0c\u8868\u793a\u4ea4\u6613\u7684\u6700\u7ec8\u72b6\u6001</p> <p><strong>\u4e00\u4e2a\u56de\u6267\u4fe1\u606f\u4e3b\u8981\u5305\u62ec\uff1a</strong></p> <ul> <li><strong>status\uff1a</strong>\u6267\u884c\u7ed3\u679c\uff0c1 \u8868\u793a\u6210\u529f\uff0c0 \u8868\u793a\u5931\u8d25</li> <li><strong>gasUsed\uff1a</strong>\u5df2\u6d88\u8017\u7684 Gas \u6570\u91cf</li> <li><strong>txHash\uff1a</strong>\u4ea4\u6613 Hash</li> <li><strong>logs\uff1a</strong>\u4ea4\u6613\u4ea7\u751f\u7684\u65e5\u5fd7</li> <li>\u2026\u2026</li> </ul> <h3 id="43-\u8f6c\u8d26\u4ea4\u6613">4.3 \u8f6c\u8d26\u4ea4\u6613</h3> <p>\u8f6c\u8d26\u4ea4\u6613\u662f\u6307\u4e24\u4e2a\u5916\u90e8\u8d26\u53f7\u8f6c\u79fb <code>Ether</code></p> <p>\u6211\u4eec\u4ee5\u6d4b\u8bd5\u7f51\u7684\u4ea4\u6613 <a href="https://ropsten.etherscan.io/tx/0xf04dfc4f3fe7d7623abbc3ae1e4a6e4f20d14b9db134d573f224dc2994feb7ba">0xf04d...b7ba</a> \u4e3a\u4f8b\uff1a</p> <ul> <li><strong>Transaction Hash:</strong> <code>0xf04d...b7ba</code>\uff0c\u8fd9\u662f\u4ea4\u6613 Hash\uff0c\u5373\u4ea4\u6613\u7684\u552f\u4e00\u6807\u8bc6</li> <li><strong>Status:</strong> <code>Success</code>\uff0c\u8868\u793a\u4ea4\u6613\u6210\u529f</li> <li><strong>From:</strong> <code>0x0c46...2978</code>\uff0c\u4ea4\u6613\u7684\u53d1\u9001\u65b9</li> <li><strong>To:</strong> <code>0x77e4...e34c</code>\uff0c\u4ea4\u6613\u7684\u63a5\u6536\u65b9</li> <li><strong>Value:</strong> 0.02759796846726396 Ether\uff0c\u4ea4\u6613\u53d1\u9001\u7684 Ether</li> <li><strong>Gas Price:</strong> 1.500000788 Gwei\uff0cGas \u7684\u4ef7\u683c</li> <li><strong>Gas Limit:</strong> 21,000\uff0c\u8f6c\u8d26\u4ea4\u6613\u6070\u597d\u6d88\u8017 21000 Gas\uff0c\u56e0\u6b64\u603b\u662f 21000</li> <li><strong>Gas Used by Transaction:</strong> 21,000 (100%)\uff0c\u6d88\u8017\u7684 Gas \u5360\u6bd4\uff0c\u8fd9\u91cc\u6070\u597d\u5168\u90e8\u6d88\u8017\u5b8c</li> <li><strong>Nonce:</strong> 1\uff0c\u53d1\u9001\u65b9\u7684 nonce\uff0c1 \u8868\u793a\u7b2c 2 \u7b14\u4ea4\u6613</li> <li><strong>Input Data:</strong> <code>0x</code>\uff0c\u56e0\u4e3a\u662f\u8f6c\u8d26\u4ea4\u6613\uff0c\u6ca1\u6709\u8f93\u5165\u6570\u636e\uff0c\u56e0\u6b64\u4e3a\u7a7a</li> </ul> <h3 id="44-\u5408\u7ea6\u4ea4\u6613">4.4 \u5408\u7ea6\u4ea4\u6613</h3> <p>\u5408\u7ea6\u4ea4\u6613\u5c31\u662f\u6307\u4e00\u4e2a\u5916\u90e8\u8d26\u53f7\u8c03\u7528\u67d0\u4e2a\u5408\u7ea6\u7684\u67d0\u4e2a <code>public</code> \u51fd\u6570</p> <p>\u6211\u4eec\u4ee5\u6d4b\u8bd5\u7f51\u7684\u4ea4\u6613 <a href="https://ropsten.etherscan.io/tx/0x223572134e999599c24d255e1beee78c8c33dcc52262a9ef5524f8f2a552132e">0x2235...132e</a> \u4e3a\u4f8b\uff1a</p> <ul> <li><strong>From:</strong> <code>0x0c46...2978</code>\uff0c\u4ea4\u6613\u7684\u53d1\u8d77\u65b9\uff0c\u8be5\u5730\u5740\u4e00\u5b9a\u662f\u5916\u90e8\u8d26\u6237</li> <li><strong>To:</strong> <code>0x5b2a...5a46</code>\uff0c\u4ea4\u6613\u7684\u63a5\u6536\u65b9\uff0c\u8fd9\u91cc\u5730\u5740\u662f\u4e00\u4e2a\u5408\u7ea6\u5730\u5740</li> <li><strong>Value:</strong> 0 Ether\uff0c\u8fd9\u91cc\u53d1\u9001\u7684 Ether \u662f 0\uff0c\u5373\u6ca1\u6709\u5411\u5408\u7ea6\u53d1\u9001 Ether</li> <li><strong>Gas Limit:</strong> 105,406\uff0c\u8fd9\u662f\u4ea4\u6613\u53d1\u8d77\u524d\u8bbe\u5b9a\u7684\u6700\u5927 Gas</li> <li><strong>Gas Used by Transaction:</strong> 70,271 (66.67%)\uff0c\u8fd9\u662f\u4ea4\u6613\u5b9e\u9645\u6d88\u8017\u7684 Gas</li> <li><strong>Input Data:</strong> <code>0xb3f98adc...</code>\uff0c\u8fd9\u662f\u4ea4\u6613\u7684\u8f93\u5165\u6570\u636e\uff0c\u5176\u4e2d\u5305\u542b\u4e86\u8c03\u7528\u54ea\u4e2a\u51fd\u6570\uff0c\u4ee5\u53ca\u4f20\u9012\u7684\u53c2\u6570</li> </ul> <p>\u53ef\u89c1\uff0c\u8f6c\u8d26\u4ea4\u6613\u7684 Gas \u8d39\u7528\u662f\u56fa\u5b9a\u7684\uff0c\u800c\u5408\u7ea6\u4ea4\u6613\u53ea\u80fd\u9884\u4f30\uff0c\u5177\u4f53\u8d39\u7528\u4ee5\u5b9e\u9645\u6267\u884c\u540e\u6d88\u8017\u7684\u4e3a\u51c6</p> <h2 id="5-\u667a\u80fd\u5408\u7ea6">5 \u667a\u80fd\u5408\u7ea6</h2> <p>\u4ee5\u592a\u574a\u76f8\u6bd4\u6bd4\u7279\u5e01\u7684\u4e00\u4e2a\u91cd\u5927\u521b\u65b0\u5c31\u662f\u5b83\u652f\u6301\u667a\u80fd\u5408\u7ea6\uff08Smart Contract\uff09</p> <h3 id="51-\u4ec0\u4e48\u662f\u667a\u80fd\u5408\u7ea6">5.1 \u4ec0\u4e48\u662f\u667a\u80fd\u5408\u7ea6</h3> <p>\u6240\u8c13\u667a\u80fd\u5408\u7ea6\uff0c\u5c31\u662f\u4e00\u79cd\u8fd0\u884c\u5728\u533a\u5757\u94fe\u4e0a\u7684\u7a0b\u5e8f\u3002\u548c\u666e\u901a\u7a0b\u5e8f\u4e0d\u540c\u7684\u662f\uff0c\u667a\u80fd\u5408\u7ea6\u8981\u4fdd\u8bc1\u5728\u533a\u5757\u94fe\u7f51\u7edc\u7684\u6bcf\u4e00\u4e2a\u8282\u70b9\u4e2d\u8fd0\u884c\u7684\u7ed3\u679c\u5b8c\u5168\u76f8\u540c\uff0c\u8fd9\u6837\u624d\u80fd\u4f7f\u4efb\u4f55\u4e00\u4e2a\u8282\u70b9\u90fd\u53ef\u4ee5\u9a8c\u8bc1\u667a\u80fd\u5408\u7ea6\u6267\u884c\u7684\u7ed3\u679c\u5bf9\u4e0d\u5bf9\u3002\u56e0\u6b64\uff0c\u4ee5\u592a\u574a\u63d0\u4f9b\u4e86\u4e00\u4e2a EVM\uff08Ethereum Virtual Machine\uff09\u865a\u62df\u673a\u6765\u6267\u884c\u667a\u80fd\u5408\u7ea6\u7684\u5b57\u8282\u7801</p> <blockquote> <p>\u548c\u666e\u901a\u7a0b\u5e8f\u76f8\u6bd4\uff0c\u4e3a\u4e86\u6d88\u9664\u7a0b\u5e8f\u8fd0\u884c\u7684\u4e0d\u786e\u5b9a\u6027\uff0c\u667a\u80fd\u5408\u7ea6\u6709\u5f88\u591a\u9650\u5236\uff0c\u4f8b\u5982\uff0c\u4e0d\u652f\u6301\u6d6e\u70b9\u8fd0\u7b97\uff08\u56e0\u4e3a\u6d6e\u70b9\u6570\u6709\u4e0d\u540c\u7684\u8868\u793a\u65b9\u6cd5\uff0c\u4e0d\u540c\u67b6\u6784\u7684 CPU \u8fd0\u884c\u7684\u6d6e\u70b9\u8ba1\u7b97\u7cbe\u5ea6\u90fd\u4e0d\u540c\uff09\uff0c\u4e0d\u652f\u6301\u968f\u673a\u6570\uff0c\u4e0d\u652f\u6301\u4ece\u5916\u90e8\u8bfb\u53d6\u8f93\u5165\u7b49\u7b49</p> </blockquote> <p>\u7c7b\u4f3c\u4e8e Java \u6e90\u7801\u88ab\u7f16\u8bd1\u4e3a JVM \u53ef\u6267\u884c\u7684\u5b57\u8282\u7801\uff0c\u6211\u4eec\u4e5f\u9700\u8981\u4e00\u79cd\u9ad8\u7ea7\u8bed\u8a00\u6765\u7f16\u5199\u667a\u80fd\u5408\u7ea6\uff0c\u7136\u540e\u7f16\u8bd1\u6210 EVM \u7684\u5b57\u8282\u7801\u3002\u6700\u5e38\u7528\u7684\u5f00\u53d1\u667a\u80fd\u5408\u7ea6\u7684\u8bed\u8a00\u662f\u4ee5\u592a\u574a\u4e13\u95e8\u4e3a\u5176\u5b9a\u5236\u7684 Solidity \u8bed\u8a00</p> <h3 id="52-\u90e8\u7f72\u548c\u6267\u884c">5.2 \u90e8\u7f72\u548c\u6267\u884c</h3> <p>\u4e00\u4e2a\u667a\u80fd\u5408\u7ea6\u88ab\u7f16\u8bd1\u540e\u5c31\u662f\u4e00\u6bb5 EVM \u5b57\u8282\u7801\uff0c\u5c06\u5b83\u90e8\u7f72\u5728\u4ee5\u592a\u574a\u7684\u533a\u5757\u94fe\u65f6\uff0c\u4f1a\u6839\u636e\u90e8\u7f72\u8005\u7684\u5730\u5740\u548c\u8be5\u5730\u5740\u7684 <code>nonce</code> \u5206\u914d\u4e00\u4e2a\u5408\u7ea6\u5730\u5740\uff0c\u5408\u7ea6\u5730\u5740\u548c\u8d26\u6237\u5730\u5740\u7684\u683c\u5f0f\u662f\u6ca1\u6709\u533a\u522b\u7684\uff0c\u4f46\u5408\u7ea6\u5730\u5740\u6ca1\u6709\u79c1\u94a5\uff0c\u4e5f\u5c31\u6ca1\u6709\u4eba\u80fd\u76f4\u63a5\u64cd\u4f5c\u8be5\u5730\u5740\u7684\u5408\u7ea6\u6570\u636e\u3002<strong>\u8981\u8c03\u7528\u5408\u7ea6\uff0c\u552f\u4e00\u7684\u65b9\u6cd5\u662f\u8c03\u7528\u5408\u7ea6\u7684\u516c\u5171\u51fd\u6570</strong></p> <blockquote> <p>\u8fd9\u4e5f\u662f\u5408\u7ea6\u7684\u4e00\u4e2a\u9650\u5236\uff1a\u5408\u7ea6\u4e0d\u80fd\u4e3b\u52a8\u6267\u884c\uff0c\u5b83\u53ea\u80fd\u88ab\u5916\u90e8\u8d26\u6237\u53d1\u8d77\u8c03\u7528\u3002\u5982\u679c\u4e00\u4e2a\u5408\u7ea6\u8981\u5b9a\u671f\u6267\u884c\uff0c\u90a3\u53ea\u80fd\u7531\u7ebf\u4e0b\u670d\u52a1\u5668\u5b9a\u671f\u53d1\u8d77\u5408\u7ea6\u8c03\u7528</p> </blockquote> <p>\u6b64\u5916\uff0c\u5408\u7ea6\u4f5c\u4e3a\u5730\u5740\uff0c\u53ef\u4ee5\u63a5\u6536 <code>Ether</code>\uff0c\u4e5f\u53ef\u4ee5\u53d1\u9001 <code>Ether</code>\u3002\u5408\u7ea6\u5185\u90e8\u4e5f\u53ef\u4ee5\u5b58\u50a8\u6570\u636e\u3002\u5408\u7ea6\u7684\u6570\u636e\u5b58\u50a8\u5728\u5408\u7ea6\u5730\u5740\u5173\u8054\u7684\u5b58\u50a8\u4e0a\uff0c\u8fd9\u5c31\u4f7f\u5f97\u5408\u7ea6\u5177\u6709\u4e86\u72b6\u6001\uff0c\u53ef\u4ee5\u5b9e\u73b0\u6bd4\u8f83\u590d\u6742\u7684\u903b\u8f91\uff0c\u5305\u62ec\u5b58\u6b3e\u3001\u53d6\u6b3e\u7b49</p> <p>\u5408\u7ea6\u5728\u6267\u884c\u7684\u8fc7\u7a0b\u4e2d\uff0c<strong>\u53ef\u4ee5\u8c03\u7528\u5176\u4ed6\u5df2\u90e8\u7f72\u7684\u5408\u7ea6</strong>\uff0c\u524d\u63d0\u662f\u77e5\u9053\u5176\u4ed6\u5408\u7ea6\u7684\u5730\u5740\u548c\u51fd\u6570\u7b7e\u540d\uff0c\u8fd9\u5c31\u5927\u5927\u6269\u5c55\u4e86\u5408\u7ea6\u7684\u529f\u80fd</p> <p>\u4f8b\u5982\uff0c\u4e00\u4e2a\u5408\u7ea6\u53ef\u4ee5\u8c03\u7528\u53e6\u4e00\u4e2a\u501f\u8d37\u5408\u7ea6\u7684\u501f\u6b3e\u65b9\u6cd5\uff0c\u518d\u8c03\u7528\u4ea4\u6613\u5408\u7ea6\uff0c\u6700\u540e\u518d\u8c03\u7528\u8fd8\u6b3e\u65b9\u6cd5\uff0c\u5b9e\u73b0\u6240\u8c13\u7684\u201c\u95ea\u7535\u8d37\u201d\uff08\u5373\u5728\u4e00\u4e2a\u5408\u7ea6\u8c03\u7528\u4e2d\u5b9e\u73b0\u501f\u6b3e-\u4ea4\u6613-\u8fd8\u6b3e\uff09\u529f\u80fd\u3002\u591a\u4e2a\u5408\u7ea6\u7684\u5d4c\u5957\u8c03\u7528\u4e5f\u4f7f\u5f97\u56e0\u4e3a\u4ee3\u7801\u7f16\u5199\u7684\u6f0f\u6d1e\u5bfc\u81f4\u9ed1\u5ba2\u653b\u51fb\u7684\u53ef\u80fd\u6027\u5927\u5927\u589e\u52a0\u3002\u4e3a\u4e86\u907f\u514d\u6f0f\u6d1e\uff0c\u7f16\u5199\u5408\u7ea6\u65f6\u9700\u8981\u66f4\u52a0\u5c0f\u5fc3</p> <h2 id="6-\u7f16\u5199\u5408\u7ea6">6 \u7f16\u5199\u5408\u7ea6</h2> <p>\u4ee5\u592a\u574a\u7684\u667a\u80fd\u5408\u7ea6\u5c31\u662f\u4e00\u6bb5\u7531 EVM \u865a\u62df\u673a\u6267\u884c\u7684\u5b57\u8282\u7801\uff0c\u7c7b\u4f3c\u4e8e Java \u865a\u62df\u673a\u6267\u884c Java \u5b57\u8282\u7801\u3002\u76f4\u63a5\u7f16\u5199\u5b57\u8282\u7801\u975e\u5e38\u56f0\u96be\uff0c\u901a\u5e38\u90fd\u662f\u7531\u7f16\u8bd1\u5668\u8d1f\u8d23\u628a\u9ad8\u7ea7\u8bed\u8a00\u7f16\u8bd1\u4e3a\u5b57\u8282\u7801</p> <p>\u7f16\u5199\u4ee5\u592a\u574a\u5408\u7ea6\u6700\u5e38\u7528\u7684\u9ad8\u7ea7\u8bed\u8a00\u662f <code>Solidity</code>\uff0c\u8fd9\u662f\u4e00\u79cd\u7c7b\u4f3c JavaScript \u8bed\u6cd5\u7684\u9ad8\u7ea7\u8bed\u8a00\u3002\u901a\u8fc7 Solidity \u7684<a href="https://soliditylang.org/">\u5b98\u65b9\u7f51\u7ad9</a>\u53ef\u4ee5\u5feb\u901f\u4e0a\u624b</p> <h3 id="61-\u5b9e\u4f8b">6.1 \u5b9e\u4f8b</h3> <p><strong>\u7b80\u5355\u7684\u6295\u7968\u5408\u7ea6\uff1a</strong></p> <pre><code class="language-js">// \u6700\u597d\u9075\u5faa\u4e00\u4e2a\u6587\u4ef6\u4e00\u4e2a\u5408\u7ea6\uff0c\u4e14\u6587\u4ef6\u540d\u4fdd\u6301\u4e0e\u5408\u7ea6\u4e00\u81f4\u3002\u8fd9\u91cc\u7684\u5408\u7ea6\u540d\u662f Vote\n\n// \u7b2c\u4e00\u884c\u901a\u5e38\u662f\u7248\u6743\u58f0\u660e\u7684\u6ce8\u91ca\n// SPDX-License-Identifier: GPL-3.0\n\n// \u58f0\u660e\u7f16\u8bd1\u5668\u7248\u672c\npragma solidity = 0.8.7;\n\n// \u4e5f\u53ef\u4ee5\u6307\u5b9a\u7248\u672c\u8303\u56f4\n// pragma solidity &gt;= 0.8.0 &lt; 0.9.0;\n\n// \u7531\u5173\u952e\u5b57 contract \u58f0\u660e\u4e00\u4e2a\u5408\u7ea6\ncontract Vote {\n  // \u4e00\u4e2a\u5408\u7ea6\u5c31\u76f8\u5f53\u4e8e\u4e00\u4e2a\u7c7b\uff0c\u5408\u7ea6\u5185\u90e8\u53ef\u4ee5\u6709\u6210\u5458\u53d8\u91cf\n\n  // \u5408\u7ea6\u53ef\u4ee5\u5b9a\u4e49\u4e8b\u4ef6\uff08Event\uff09\uff0c\u6211\u4eec\u5728 Vote \u5408\u7ea6\u4e2d\u5b9a\u4e49\u4e86\u4e00\u4e2a Voted \u4e8b\u4ef6\n  // \u53ea\u5b9a\u4e49\u4e8b\u4ef6\u8fd8\u4e0d\u591f\uff0c\u89e6\u53d1\u4e8b\u4ef6\u5fc5\u987b\u5728\u5408\u7ea6\u7684\u5199\u51fd\u6570\u4e2d\u901a\u8fc7 emit \u5173\u952e\u5b57\u5b9e\u73b0\n  event Voted(address indexed voter, uint8 proposal);\n\n  // \u8bb0\u5f55\u5df2\u6295\u7968\u7684\u5730\u5740\n  mapping(address =&gt; bool) public voted;\n\n  // \u8bb0\u5f55\u6295\u7968\u7ec8\u6b62\u65f6\u95f4\n  uint256 public endTime;\n\n  // \u8bb0\u5f55\u5f97\u7968\u6570\u91cf\n  uint256 public proposalA;\n  uint256 public proposalB;\n  uint256 public proposalC;\n\n  // \u6240\u6709\u7684\u6210\u5458\u53d8\u91cf\u90fd\u9ed8\u8ba4\u521d\u59cb\u5316\u4e3a 0 \u6216 false\uff08\u9488\u5bf9 bool\uff09\u6216\u7a7a\uff08\u9488\u5bf9 mapping\uff09\n  // \u5982\u679c\u67d0\u4e2a\u6210\u5458\u53d8\u91cf\u8981\u6307\u5b9a\u521d\u59cb\u503c\uff0c\u90a3\u4e48\u9700\u8981\u5728\u6784\u9020\u51fd\u6570\u4e2d\u8d4b\u503c\n  constructor(uint256 _endTime) {\n    endTime = _endTime;\n  }\n\n  // \u4ee5\u592a\u574a\u5408\u7ea6\u652f\u6301\u8bfb\u3001\u5199\u4e24\u79cd\u7c7b\u578b\u7684\u6210\u5458\u51fd\u6570\n  // \u6ca1\u6709 view \u4fee\u9970\u7684\u51fd\u6570\u662f\u5199\u5165\u51fd\u6570\uff0c\u5b83\u4f1a\u4fee\u6539\u6210\u5458\u53d8\u91cf\uff0c\u5373\u6539\u53d8\u4e86\u5408\u7ea6\u7684\u72b6\u6001\n  function vote(uint8 _proposal) public {\n    require(block.timestamp &lt; endTime, &quot;Vote expired.&quot;);\n    require(_proposal &gt;= 1 &amp;&amp; _proposal &lt;= 3, &quot;Invalid proposal.&quot;);\n    require(!voted[msg.sender], &quot;Cannot vote again.&quot;);\n\n    // \u7ed9mapping\u589e\u52a0\u4e00\u4e2akey-value\n    voted[msg.sender] = true;\n\n    if (_proposal == 1) {\n      // \u4fee\u6539proposalA\n      proposalA ++;\n    } else if (_proposal == 2) {\n      // \u4fee\u6539proposalB\n      proposalB ++;\n    } else if (_proposal == 3) {\n      // \u4fee\u6539proposalC\n      proposalC ++;\n    }\n\n    // \u5f53\u8c03\u7528 vote() \u5199\u65b9\u6cd5\u65f6\uff0c\u4f1a\u89e6\u53d1 Voted \u4e8b\u4ef6\n    emit Voted(msg.sender, _proposal);\n  }\n\n  // \u4ee5 view \u4fee\u9970\u7684\u51fd\u6570\u662f\u53ea\u8bfb\u51fd\u6570\uff0c\u5b83\u4e0d\u4f1a\u4fee\u6539\u6210\u5458\u53d8\u91cf\uff0c\u5373\u4e0d\u4f1a\u6539\u53d8\u5408\u7ea6\u7684\u72b6\u6001\n  function votes() public view returns (uint256) {\n    return proposalA + proposalB + proposalC;\n  }\n}</code></pre> <p><code>Solidity</code> \u652f\u6301\u6574\u578b\uff08\u7ec6\u5206\u4e3a <code>uint256</code>\u3001<code>uint128</code>\u3001<code>uint8</code> \u7b49\uff09\u3001<code>bytes32</code> \u7c7b\u578b\u3001\u6620\u5c04\u7c7b\u578b\uff08\u76f8\u5f53\u4e8e <code>Java</code> \u7684 <code>Map</code>\uff09\u3001\u5e03\u5c14\u578b\uff08<code>true</code> \u6216 <code>false</code>\uff09\u548c\u7279\u6b8a\u7684 <code>address</code> \u7c7b\u578b\u8868\u793a\u4e00\u4e2a\u4ee5\u592a\u574a\u5730\u5740</p> <blockquote> <p>\u4ee5\u592a\u574a\u5408\u7ea6\u4e0d\u652f\u6301\u6d6e\u70b9\u6570\u7c7b\u578b\uff0c\u662f\u4e3a\u4e86\u4fdd\u8bc1\u6bcf\u4e2a\u8282\u70b9\u8fd0\u884c\u5408\u7ea6\u90fd\u80fd\u5f97\u5230\u5b8c\u5168\u76f8\u540c\u7684\u7ed3\u679c\u3002\u6d6e\u70b9\u6570\u8fd0\u7b97\u5728\u4e0d\u540c\u7684 ISA \u4f53\u7cfb\u4e0b\u5b58\u5728\u8868\u793a\u65b9\u5f0f\u3001\u8fd0\u7b97\u7cbe\u5ea6\u7684\u4e0d\u540c\uff0c\u65e0\u6cd5\u4fdd\u8bc1\u4e24\u4e2a\u8282\u70b9\u6267\u884c\u6d6e\u70b9\u8fd0\u7b97\u4f1a\u5f97\u5230\u76f8\u540c\u7684\u7ed3\u679c</p> </blockquote> <p>\u4e8b\u4ef6\u53ef\u7528\u6765\u901a\u77e5\u5916\u90e8\u611f\u5174\u8da3\u7684\u7b2c\u4e09\u65b9\uff0c\u4ed6\u4eec\u53ef\u4ee5\u5728\u533a\u5757\u94fe\u4e0a\u76d1\u542c\u4ea7\u751f\u7684\u4e8b\u4ef6\uff0c\u4ece\u800c\u786e\u8ba4\u5408\u7ea6\u67d0\u4e9b\u72b6\u6001\u53d1\u751f\u4e86\u6539\u53d8</p> <p><strong>\u7528 Solidity \u7f16\u5199\u4e00\u4e2a\u5b8c\u6574\u7684\u5408\u7ea6\u6240\u6d89\u53ca\u7684\u51e0\u4e2a\u8981\u7d20\uff1a</strong></p> <ul> <li>\u58f0\u660e\u7248\u6743\uff08\u53ef\u9009\uff09</li> <li>\u58f0\u660e\u7f16\u8bd1\u5668\u7248\u672c</li> <li>\u4ee5 <code>contract</code> \u5173\u952e\u5b57\u7f16\u5199\u4e00\u4e2a\u5408\u7ea6</li> <li>\u53ef\u4ee5\u5305\u542b\u82e5\u5e72\u6210\u5458\u53d8\u91cf</li> <li>\u53ef\u4ee5\u5728\u6784\u9020\u51fd\u6570\u4e2d\u5bf9\u6210\u5458\u53d8\u91cf\u521d\u59cb\u5316\uff08\u53ef\u9009\uff09</li> <li>\u53ef\u4ee5\u7f16\u5199\u53ea\u8bfb\u65b9\u6cd5</li> <li>\u53ef\u4ee5\u7f16\u5199\u5199\u5165\u65b9\u6cd5</li> <li>\u53ef\u4ee5\u58f0\u660e <code>Event</code> \u5e76\u5728\u5199\u5165\u65b9\u6cd5\u4e2d\u89e6\u53d1</li> <li>\u51fd\u6570\u53c8\u53ef\u4ee5\u7528 <code>public</code> \u6216 <code>private</code> \u4fee\u9970\u3002\u987e\u540d\u601d\u4e49\uff0c<code>public</code> \u51fd\u6570\u53ef\u4ee5\u88ab\u5916\u90e8\u8c03\u7528\uff0c\u800c <code>private</code> \u51fd\u6570\u4e0d\u80fd\u88ab\u5916\u90e8\u8c03\u7528\uff0c\u4ed6\u4eec\u53ea\u80fd\u88ab <code>public</code> \u51fd\u6570\u5728\u5185\u90e8\u8c03\u7528</li> </ul> <h3 id="62-\u5408\u7ea6\u6267\u884c\u6d41\u7a0b">6.2 \u5408\u7ea6\u6267\u884c\u6d41\u7a0b</h3> <p>\u5f53\u4e00\u4e2a\u5408\u7ea6\u7f16\u5199\u5b8c\u6210\u5e76\u6210\u529f\u7f16\u8bd1\u540e\uff0c\u6211\u4eec\u5c31\u53ef\u4ee5\u628a\u5b83\u90e8\u7f72\u5230\u4ee5\u592a\u574a\u4e0a\u3002\u5408\u7ea6\u90e8\u7f72\u540e\u5c06\u81ea\u52a8\u83b7\u5f97\u4e00\u4e2a\u5730\u5740\uff0c\u901a\u8fc7\u8be5\u5730\u5740\u5373\u53ef\u8bbf\u95ee\u5408\u7ea6</p> <p>\u628a <code>contract Vote {...}</code> \u770b\u4f5c\u4e00\u4e2a\u7c7b\uff0c<strong>\u90e8\u7f72\u5c31\u76f8\u5f53\u4e8e\u4e00\u4e2a\u5b9e\u4f8b\u5316</strong>\u3002\u5982\u679c\u90e8\u7f72\u4e24\u6b21\uff0c\u5c06\u5f97\u5230\u4e24\u4e2a\u4e0d\u540c\u7684\u5730\u5740\uff0c\u76f8\u5f53\u4e8e\u5b9e\u4f8b\u5316\u4e24\u6b21\uff0c\u4e24\u4e2a\u90e8\u7f72\u540e\u7684\u5408\u7ea6\u5bf9\u5e94\u7684\u6210\u5458\u53d8\u91cf\u662f\u5b8c\u5168\u72ec\u7acb\u7684\uff0c\u4e92\u4e0d\u5f71\u54cd</p> <p>\u6784\u9020\u51fd\u6570\u5728\u90e8\u7f72\u5408\u7ea6\u65f6\u5c31\u4f1a\u7acb\u523b\u6267\u884c\uff0c\u4e14\u4ec5\u6267\u884c\u4e00\u6b21\u3002\u5408\u7ea6\u90e8\u7f72\u540e\u5c31\u65e0\u6cd5\u8c03\u7528\u6784\u9020\u51fd\u6570</p> <p>\u4efb\u4f55\u5916\u90e8\u8d26\u6237\u90fd\u53ef\u4ee5\u53d1\u8d77\u5bf9\u5408\u7ea6\u7684\u51fd\u6570\u8c03\u7528\u3002\u5982\u679c\u8c03\u7528\u53ea\u8bfb\u65b9\u6cd5\uff0c\u56e0\u4e3a\u4e0d\u6539\u53d8\u5408\u7ea6\u72b6\u6001\uff0c\u6240\u4ee5\u4efb\u4f55\u65f6\u523b\u90fd\u53ef\u4ee5\u8c03\u7528\uff0c\u4e14\u4e0d\u9700\u8981\u7b7e\u540d\uff0c\u4e5f\u4e0d\u9700\u8981\u6d88\u8017 Gas\u3002\u4f46\u5982\u679c\u8c03\u7528\u5199\u5165\u65b9\u6cd5\uff0c\u5c31\u9700\u8981\u7b7e\u540d\u63d0\u4ea4\u4e00\u4e2a\u4ea4\u6613\uff0c\u5e76\u6d88\u8017\u4e00\u5b9a\u7684 Gas</p> <p>\u5728\u4e00\u4e2a\u4ea4\u6613\u4e2d\uff0c\u53ea\u80fd\u8c03\u7528\u4e00\u4e2a\u5408\u7ea6\u7684\u4e00\u4e2a\u5199\u5165\u65b9\u6cd5\u3002\u65e0\u9700\u8003\u8651\u5e76\u53d1\u548c\u540c\u6b65\u7684\u95ee\u9898\uff0c\u56e0\u4e3a\u4ee5\u592a\u574a\u4ea4\u6613\u7684\u5199\u5165\u662f\u4e25\u683c\u4e32\u884c\u7684</p> <h3 id="63-\u9a8c\u8bc1">6.3 \u9a8c\u8bc1</h3> <p>\u7531\u4e8e\u4efb\u4f55\u5916\u90e8\u8d26\u6237\u90fd\u53ef\u4ee5\u53d1\u8d77\u5bf9\u5408\u7ea6\u7684\u51fd\u6570\u8c03\u7528\uff0c\u6240\u4ee5\u4efb\u4f55\u9a8c\u8bc1\u5de5\u4f5c\u90fd\u5fc5\u987b\u5728\u51fd\u6570\u5185\u90e8\u81ea\u884c\u5b8c\u6210\u3002\u6700\u5e38\u7528\u7684 <code>require()</code> \u53ef\u4ee5\u65ad\u8a00\u4e00\u4e2a\u6761\u4ef6\uff0c\u5982\u679c\u65ad\u8a00\u5931\u8d25\uff0c\u5c06\u629b\u51fa\u9519\u8bef\u5e76\u4e2d\u65ad\u6267\u884c</p> <p><strong>\u5e38\u7528\u7684\u68c0\u67e5\u5305\u62ec\u51e0\u7c7b\uff1a</strong></p> <p>\u53c2\u6570\u68c0\u67e5\uff1a</p> <pre><code class="language-js">// \u53c2\u6570\u5fc5\u987b\u4e3a 1,2,3:\nrequire(_proposal &gt;= 1 &amp;&amp; _proposal &lt;= 3, &#39;Invalid proposal.&#39;);</code></pre> <p>\u6761\u4ef6\u68c0\u67e5\uff1a</p> <pre><code class="language-js">// \u5f53\u524d\u533a\u5757\u65f6\u95f4\u5fc5\u987b\u5c0f\u4e8e\u8bbe\u5b9a\u7684\u7ed3\u675f\u65f6\u95f4:\nrequire(block.timestamp &lt; endTime, &#39;Vote expired.&#39;);</code></pre> <p>\u8c03\u7528\u65b9\u68c0\u67e5\uff1a</p> <pre><code class="language-js">// msg.sender \u8868\u793a\u8c03\u7528\u65b9\u5730\u5740:\nrequire(!voted[msg.sender], &#39;Cannot vote again.&#39;);</code></pre> <p>\u4ee5\u592a\u574a\u5408\u7ea6\u5177\u5907\u7c7b\u4f3c\u6570\u636e\u5e93\u4e8b\u52a1\u7684\u7279\u70b9\uff0c\u5982\u679c\u4e2d\u9014\u6267\u884c\u5931\u8d25\uff0c\u5219\u6574\u4e2a\u5408\u7ea6\u7684\u72b6\u6001\u4fdd\u6301\u4e0d\u53d8\uff0c\u4e0d\u5b58\u5728\u4fee\u6539\u67d0\u4e2a\u6210\u5458\u53d8\u91cf\u540e\uff0c\u540e\u7eed\u65ad\u8a00\u5931\u8d25\u5bfc\u81f4\u90e8\u5206\u4fee\u6539\u751f\u6548\u7684\u95ee\u9898\uff1a</p> <pre><code class="language-js">function increment() {\n  // \u5047\u8bbe a,b \u5747\u4e3a\u6210\u5458\u53d8\u91cf:\n  a++;\n  emit AChanged(a);\n\n  // \u5982\u679c\u4e0b\u9762\u7684\u9a8c\u8bc1\u5931\u8d25\uff0ca \u4e0d\u4f1a\u88ab\u66f4\u65b0\uff0c\u4e5f\u6ca1\u6709 AChanged \u4e8b\u4ef6\u53d1\u751f:\n  require(b &lt; 10, &#39;b &gt;= 10&#39;);\n  b++;\n}</code></pre> <p>\u5373\u5408\u7ea6\u5982\u679c\u6267\u884c\u5931\u8d25\uff0c\u5176\u72b6\u6001\u4e0d\u4f1a\u53d1\u751f\u4efb\u4f55\u53d8\u5316\uff0c\u4e5f\u4e0d\u4f1a\u6709\u4efb\u4f55\u4e8b\u4ef6\u53d1\u751f\uff0c\u4ec5\u4ec5\u662f\u8c03\u7528\u65b9\u767d\u767d\u6d88\u8017\u4e86\u4e00\u5b9a\u7684 Gas</p> <h2 id="7-\u90e8\u7f72\u5408\u7ea6">7 \u90e8\u7f72\u5408\u7ea6</h2> <p>\u5b9e\u9645\u4e0a\uff0c\u90e8\u7f72\u5408\u7ea6\u4e5f\u662f\u4e00\u4e2a\u4ea4\u6613\uff0c\u9700\u8981\u4e00\u4e2a\u5916\u90e8\u8d26\u6237\uff0c\u82b1\u8d39\u4e00\u5b9a\u7684 Gas\uff0c\u5c31\u53ef\u4ee5\u628a\u5408\u7ea6\u90e8\u7f72\u5230\u94fe\u4e0a</p> <p>\u56e0\u6b64\uff0c\u6211\u4eec\u9996\u5148\u9700\u8981\u4e00\u4e2a\u4fbf\u4e8e\u5f00\u53d1\u548c\u6d4b\u8bd5\u7684\u94b1\u5305\uff0c\u624d\u80fd\u521b\u5efa\u4e00\u4e2a\u5916\u90e8\u8d26\u6237\uff0c\u5e76\u4e14\u8d26\u6237\u4e0a\u8981\u6709\u4e00\u5b9a\u7684 <code>Ether</code></p> <h3 id="71-metamask">7.1 MetaMask</h3> <p><code>MetaMask</code> \u94b1\u5305\uff0c\u8fd9\u662f\u4e00\u4e2a\u57fa\u4e8e\u6d4f\u89c8\u5668\u63d2\u4ef6\u7684\u94b1\u5305\uff0c\u652f\u6301 Chrome\u3001FireFox \u7b49\u6d4f\u89c8\u5668\u3002\u4f7f\u7528\u7684\u65f6\u5019\uff0c\u901a\u8fc7 <code>Dapp</code> \u7f51\u7ad9\u7684 <code>JavaScript</code> \u53ef\u4ee5\u53d1\u8d77\u4ea4\u6613\uff0c\u7528\u6237\u901a\u8fc7 <code>MetaMask</code> \u786e\u8ba4\u540e\u5373\u53ef\u5c06\u4ea4\u6613\u53d1\u9001\u81f3\u94fe\u4e0a</p> <p>\u5b89\u88c5\u5b8c\u6210\u540e\uff0c\u7b2c\u4e00\u6b21\u542f\u52a8 <code>MetaMask</code> \u9700\u8981\u521b\u5efa\u6216\u5bfc\u5165\u4e00\u4e2a\u94b1\u5305\uff0c\u8bbe\u7f6e\u4e00\u4e2a\u89e3\u9501\u53e3\u4ee4\uff0c<code>MetaMask</code> \u5141\u8bb8\u521b\u5efa\u591a\u4e2a\u8d26\u53f7\uff0c\u53ef\u968f\u65f6\u5207\u6362\u8d26\u53f7\uff0c\u8fd8\u53ef\u5207\u6362\u4e0d\u540c\u7684\u94fe\uff0c\u4f8b\u5982\uff0c\u4ee5\u592a\u574a\u4e3b\u7f51\u3001Ropsten \u6d4b\u8bd5\u7f51\u3001Rinkeby \u6d4b\u8bd5\u7f51\u7b49</p> <p>\u5728\u5f00\u53d1\u9636\u6bb5\uff0c\u76f4\u63a5\u4f7f\u7528\u4e3b\u7f51\u592a\u8d39\u94b1\uff0c\u53ef\u4ee5\u4f7f\u7528 Ropsten \u6d4b\u8bd5\u7f51\uff0c\u5e76\u4ece <a href="https://faucet.ropsten.be">faucet.ropsten.be</a> \u6216 <a href="https://faucet.dimensions.network/">faucet.dimensions.network</a> \u83b7\u53d6\u4e00\u4e9b\u6d4b\u8bd5\u7f51\u7684 Ether</p> <h3 id="72-\u90e8\u7f72\u5408\u7ea6">7.2 \u90e8\u7f72\u5408\u7ea6</h3> <p>\u4ee5\u592a\u574a\u5b98\u65b9\u63d0\u4f9b\u4e86\u4e00\u4e2a <code>Remix</code> \u7684\u5728\u7ebf IDE\uff0c\u7528\u4e8e\u7f16\u5199\u3001\u7f16\u8bd1\u548c\u90e8\u7f72\u4ee5\u592a\u574a\u5408\u7ea6\u3002\u8fd9\u662f\u4ece\u96f6\u5f00\u59cb\u90e8\u7f72\u4e00\u4e2a\u5408\u7ea6\u7684\u6700\u7b80\u5355\u7684\u65b9\u5f0f</p> <p>\u6211\u4eec\u8bbf\u95ee <a href="http://remix.ethereum.org/">Remix</a>\uff08\u6ce8\u610f\uff1a\u8981\u90e8\u7f72\u5408\u7ea6\uff0c\u53ea\u80fd\u901a\u8fc7 http \u8bbf\u95ee\uff0c\u4e0d\u80fd\u4f7f\u7528 https\uff09\uff0c\u5728\u5de6\u4fa7\u9009\u62e9 \u201cFile explorers\u201d\uff0c\u5728\u9ed8\u8ba4\u7684 <code>Workspace</code> \u7684 <code>contracts</code> \u76ee\u5f55\u4e0b\u65b0\u5efa\u6587\u4ef6 <code>Vote.sol</code>\uff0c\u7136\u540e\u8d34\u5165\u4e0a\u9762\u7684\u6295\u7968\u4ee3\u7801\uff1a</p> <p><img src="'+A+'" alt=""></p> <p>\u63a5\u4e0b\u6765\u662f\u7f16\u8bd1\u8be5\u5408\u7ea6\u3002\u9009\u62e9\u5de6\u4fa7\u7684 \u201cSolidity compiler\u201d\uff0c\u70b9\u51fb \u201cCompile Vote.sol\u201d \u5f00\u59cb\u7f16\u8bd1\uff1a</p> <p><img src="'+D+'" alt=""></p> <p>\u5982\u679c\u6ca1\u6709\u7f16\u8bd1\u9519\u8bef\uff0c\u53ef\u4ee5\u770b\u5230\u7f16\u8bd1\u6210\u529f\u7684\u6807\u5fd7\u3002\u63a5\u4e0b\u6765\u9009\u62e9 \u201cDeploy &amp; run transactions\u201d\uff1a</p> <p><img src="'+G+'" alt=""></p> <p>\u5728 \u201cENVIRONMENT\u201d \u4e2d\uff0c\u9009\u62e9 \u201cInjected Web3\u201d\uff0c\u8868\u793a\u6211\u4eec\u8981\u4f7f\u7528 <code>MetaMask</code> \u6ce8\u5165\u7684 <code>Web3</code> \u73af\u5883\uff0c\u5982\u679c\u5df2\u6b63\u786e\u8fde\u63a5 <code>MetaMask</code>\uff0c\u53ef\u4ee5\u770b\u5230 \u201cRopsten (3) network\u201d\uff0c\u8868\u793a\u5df2\u8fde\u63a5\u5230 <code>Ropsten</code> \u6d4b\u8bd5\u7f51</p> <p>\u5728 \u201cCONTRACT\u201d \u4e2d\uff0c\u9009\u62e9 \u201cVote - contracts/Vote.sol\u201d\uff0c\u8fd9\u662f\u6211\u4eec\u5c06\u8981\u90e8\u7f72\u7684\u5408\u7ea6</p> <p>\u5728 \u201cDeploy\u201d \u6309\u94ae\u5de6\u4fa7\uff0c\u586b\u5165\u6784\u9020\u51fd\u6570\u7684\u53c2\u6570\uff0c\u4f8b\u5982 <code>1735719000</code>\uff0c\u7136\u540e\u70b9\u51fb \u201cDeploy\u201d \u6309\u94ae\u5f00\u59cb\u90e8\u7f72\uff0c\u6b64\u65f6\u4f1a\u5f39\u51fa <code>MetaMask</code> \u7684\u4ea4\u6613\u7b7e\u540d\u786e\u8ba4\uff0c\u786e\u8ba4\u540e\u90e8\u7f72\u5408\u7ea6\u7684\u4ea4\u6613\u5373\u88ab\u53d1\u9001\u81f3\u6d4b\u8bd5\u94fe\u3002\u5728 <code>MetaMask</code> \u7684\u8d26\u6237 - \u6d3b\u52a8\u4e2d\u53ef\u4ee5\u770b\u5230\u6b63\u5728\u53d1\u9001\u7684\u4ea4\u6613\uff0c\u67e5\u770b\u8be6\u60c5\u53ef\u4ee5\u5728 <code>Etherscan</code> \u67e5\u770b\u8be5\u4ea4\u6613\u7684\u8be6\u7ec6\u4fe1\u606f\u3002\u5f53\u4ea4\u6613\u88ab\u6253\u5305\u786e\u8ba4\u540e\uff0c\u5373\u53ef\u83b7\u5f97\u90e8\u7f72\u540e\u5408\u7ea6\u7684\u5730\u5740 <code>0x5b2a...5a46</code></p> <p>\u81f3\u6b64\uff0c\u6211\u4eec\u5c31\u6210\u529f\u5730\u90e8\u7f72\u4e86\u4e00\u4e2a\u4ee5\u592a\u574a\u5408\u7ea6</p> <p>\u5bf9\u4e8e\u719f\u7ec3\u7684 <code>Solidity</code> \u5f00\u53d1\u8005\uff0c\u53ef\u4ee5\u4f7f\u7528 <code>Truffle</code> \u8fd9\u4e2a <code>JavaScript</code> \u5de5\u5177\u901a\u8fc7 <code>JavaScript</code> \u811a\u672c\u5168\u81ea\u52a8\u90e8\u7f72\u5408\u7ea6\uff0c\u51cf\u5c11\u624b\u52a8\u64cd\u4f5c\u5bfc\u81f4\u7684\u51fa\u9519\u7684\u53ef\u80fd</p> <h2 id="8-\u8c03\u7528\u5408\u7ea6">8 \u8c03\u7528\u5408\u7ea6</h2> <p>\u5f53\u6211\u4eec\u5df2\u7ecf\u6210\u529f\u5730\u5c06\u4e00\u4e2a\u5408\u7ea6\u90e8\u7f72\u5230\u4ee5\u592a\u574a\u94fe\u4e0a\u65f6\uff0c\u5982\u4f55\u8c03\u7528\u8be5\u5408\u7ea6\uff1f</p> <p>\u9996\u5148\uff0c\u6211\u4eec\u901a\u8fc7 <code>Etherscan</code> \u8fd9\u4e2a\u7f51\u7ad9\u53ef\u4ee5\u67e5\u770b\u5df2\u90e8\u7f72\u5408\u7ea6\u7684\u8be6\u7ec6\u4fe1\u606f\uff0c\u4f8b\u5982 <a href="https://ropsten.etherscan.io/address/0x5b2a057e1db47463695b4629114cbdae99235a46">0x5b2a...5a46</a></p> <p>\u5728\u8c03\u7528\u5408\u7ea6\u524d\uff0c\u6211\u4eec\u53ef\u4ee5\u901a\u8fc7 \u201cVerify and Publish\u201d \u8fd9\u4e2a\u94fe\u63a5\u5c06\u6e90\u7801\u4e0a\u4f20\u5230 <code>Etherscan</code> \u5e76\u9a8c\u8bc1\u3002\u9996\u5148\u9009\u62e9\u6b63\u786e\u7684 <code>Solidity</code> \u7f16\u8bd1\u5668\u7248\u672c\uff0c\u8d34\u5165\u6e90\u7801\uff0c<code>Etherscan</code> \u81ea\u52a8\u7f16\u8bd1\u540e\uff0c\u5982\u679c\u4e8c\u8fdb\u5236\u7684\u5b57\u8282\u7801\u5b8c\u5168\u5339\u914d\u5df2\u90e8\u7f72\u7684\u5408\u7ea6\uff0c\u5219\u9a8c\u8bc1\u901a\u8fc7\uff0c\u8be5\u5408\u7ea6\u6709\u4e00\u4e2a\u7eff\u8272\u5c0f\u52fe\u7684\u6807\u5fd7\uff1a</p> <p><img src="'+R+'" alt=""></p> <h3 id="81-\u8bfb\u53d6\u5408\u7ea6">8.1 \u8bfb\u53d6\u5408\u7ea6</h3> <p>\u8bbf\u95ee\u5408\u7ea6\u7684\u53ea\u8bfb\u51fd\u6570\u65f6\uff0c\u65e0\u9700\u6d88\u8017 Gas\uff0c\u4e5f\u65e0\u9700\u8fde\u63a5\u94b1\u5305\uff0c\u76f4\u63a5\u5207\u6362\u5230 \u201cRead\u201d \u9762\u677f\uff0c\u5373\u53ef\u770b\u5230\u53ea\u8bfb\u51fd\u6570\u7684\u8fd4\u56de\u503c\uff1a</p> <p><img src="'+S+'" alt=""></p> <p>\u4ece\u7ed3\u679c\u53ef\u77e5\uff0c<code>endTime()</code> \u8fd4\u56de 1735719000\uff0c<code>proposalA()</code> \u8fd4\u56de 1</p> <p>\u6211\u4eec\u5728 <code>Vote</code> \u5408\u7ea6\u53ea\u6709 <code>endTime</code> \u5b57\u6bb5\u5e76\u6ca1\u6709 <code>endTime()</code> \u51fd\u6570\uff0c\u4e3a\u4ec0\u4e48\u53ef\u4ee5\u8bbf\u95ee <code>endTime()</code>\uff1f\u539f\u56e0\u662f <strong>public \u5b57\u6bb5\u4f1a\u81ea\u52a8\u5bf9\u5e94\u4e00\u4e2a\u540c\u540d\u7684\u53ea\u8bfb\u51fd\u6570</strong>\uff0c\u5373\uff1a</p> <pre><code class="language-js">contract Vote {\n  uint256 public endTime;\n}</code></pre> <p>\u5b8c\u5168\u7b49\u4ef7\u4e8e\uff1a</p> <pre><code class="language-js">contract Vote {\nuint256 private _endTime;\n  function endTime() public view returns (uint256) {\n    return _endTime;\n  }\n}</code></pre> <p>\u5bf9\u4e8e\u9700\u8981\u586b\u5165\u53c2\u6570\u7684\u53ea\u8bfb\u51fd\u6570\uff0c\u53ef\u4ee5\u76f4\u63a5\u5728\u5bf9\u5e94\u7684\u8f93\u5165\u6846\u586b\u5165\u53c2\u6570\uff0c\u7136\u540e\u70b9\u51fb \u201cQuery\u201d \u8c03\u7528\u5e76\u83b7\u53d6\u7ed3\u679c\uff1a</p> <p><img src="'+V+'" alt=""></p> <h3 id="82-\u5199\u5165\u5408\u7ea6">8.2 \u5199\u5165\u5408\u7ea6</h3> <p>\u5f53\u6211\u4eec\u8981\u5199\u5165\u5408\u7ea6\u65f6\uff0c\u5c31<strong>\u5fc5\u987b\u63d0\u4ea4\u4e00\u4e2a\u7b7e\u540d\u7684\u4ea4\u6613</strong>\uff0c\u5e76\u6d88\u8017\u4e00\u5b9a\u7684 Gas\u3002\u6211\u4eec\u5728 <code>Etherscan</code> \u7684\u5408\u7ea6\u9875\u9009\u62e9\u201cWrite\u201d\uff0c\u4f1a\u51fa\u73b0\u4e00\u4e2a \u201cConnect to Web3\u201d \u7684\u94fe\u63a5\uff1a</p> <p><img src="'+H+'" alt=""></p> <p>\u70b9\u51fb\u5e76\u8fde\u63a5 <code>MetaMask</code> \u540e\uff0c\u6211\u4eec\u5c31\u53ef\u4ee5\u9009\u62e9\u4e00\u4e2a\u5199\u5165\u51fd\u6570\uff0c\u586b\u5165\u53c2\u6570\uff0c\u7136\u540e\u70b9\u51fb \u201cWrite\u201d\uff1a</p> <p><img src="'+B+'" alt=""></p> <p>\u5728 <code>MetaMask</code> \u4e2d\u786e\u8ba4\u8be5\u4ea4\u6613\u540e\uff0c\u4ea4\u6613\u88ab\u53d1\u9001\u81f3\u94fe\u4e0a\u3002\u7b49\u5f85\u6253\u5305\u6210\u529f\u540e\uff0c\u6211\u4eec\u5c31\u53ef\u4ee5\u8bfb\u53d6\u5230\u5408\u7ea6\u5185\u90e8\u66f4\u65b0\u540e\u7684\u72b6\u6001</p> <h2 id="9-\u7f16\u5199-dapp">9 \u7f16\u5199 Dapp</h2> <h3 id="91-dapp-\u7684\u4f5c\u7528">9.1 Dapp \u7684\u4f5c\u7528</h3> <p>\u901a\u8fc7 Etherscan \u8fd9\u4e2a\u7f51\u7ad9\u4e0d\u4ec5\u53ef\u4ee5\u67e5\u770b\u5408\u7ea6\u4ee3\u7801\uff0c\u8fd8\u53ef\u4ee5\u8c03\u7528\u5408\u7ea6\u7684\u8bfb\u53d6\u548c\u5199\u5165\u65b9\u6cd5\u3002\u53ef\u89c1\uff0c\u8c03\u7528\u4e00\u4e2a\u5408\u7ea6\u662f\u975e\u5e38\u7b80\u5355\u7684</p> <p>\u4f46\u662f\u5bf9\u4e8e\u666e\u901a\u7528\u6237\u6765\u8bf4\uff0c\u8fd9\u79cd\u8c03\u7528\u65b9\u5f0f\u8fd8\u662f\u8fc7\u4e8e\u590d\u6742\u3002\u6bd4\u5982\u53d1\u4e2a\u5fae\u535a\uff0c\u5982\u679c\u9700\u8981\u7528\u6237\u81ea\u5df1\u53bb\u8c03\u63a5\u53e3\u7684\u8bdd\uff0c\u95e8\u69db\u5c31\u592a\u9ad8\u4e86</p> <p>\u6240\u4ee5\uff0c\u8981\u6c42\u666e\u901a\u7528\u6237\u81ea\u5df1\u53bb <code>Etherscan</code> \u8c03\u7528\u5408\u7ea6\u4e0d\u73b0\u5b9e\u3002\u5f00\u53d1\u4e00\u4e2a Dapp\uff0c\u5c31\u53ef\u4ee5\u8ba9\u7528\u6237\u901a\u8fc7\u9875\u9762\uff0c\u70b9\u51fb\u6309\u94ae\u6765\u8c03\u7528\u5408\u7ea6</p> <h3 id="92-dapp-\u67b6\u6784">9.2 Dapp \u67b6\u6784</h3> <p>\u4e00\u4e2a Dapp \u7684\u67b6\u6784\u5b9e\u9645\u4e0a\u5305\u542b\u4ee5\u4e0b\u90e8\u5206\uff1a</p> <p><img src="'+U+'" alt=""></p> <p>\u4ee5\u592a\u574a\u7684\u533a\u5757\u94fe\u7f51\u7edc\u5b9e\u9645\u4e0a\u662f\u4e00\u4e2a\u7531\u82e5\u5e72\u8282\u70b9\u6784\u6210\u7684 <code>P2P</code> \u7f51\u7edc\uff0c\u6240\u8c13\u8bfb\u5199\u5408\u7ea6\uff0c\u5b9e\u9645\u4e0a\u662f\u5411\u7f51\u7edc\u4e2d\u7684<strong>\u67d0\u4e2a\u8282\u70b9</strong>\u53d1\u9001 <code>JSON-PRC</code> \u8bf7\u6c42\u3002\u5f53\u6211\u4eec\u60f3\u8981\u505a\u4e00\u4e2a\u57fa\u4e8e <code>Vote</code> \u5408\u7ea6\u7684 <code>Dapp</code> \u65f6\uff0c\u6211\u4eec\u9700\u8981\u5f00\u53d1\u4e00\u4e2a\u9875\u9762\uff0c\u5e76\u8fde\u63a5\u5230\u6d4f\u89c8\u5668\u7684 <code>MetaMask</code> \u94b1\u5305\uff0c\u8fd9\u6837\uff0c\u9875\u9762\u7684 JavaScript \u5c31\u53ef\u4ee5\u901a\u8fc7 <code>MetaMask</code> \u8bfb\u5199 <code>Vote</code> \u5408\u7ea6\uff0c\u9875\u9762\u6548\u679c\u5982\u4e0b\uff1a</p> <p><img src="'+j+'" alt=""></p> <h3 id="93-\u7f16\u5199-dapp">9.3 \u7f16\u5199 Dapp</h3> <p><strong>\u7b2c\u4e00\u6b65</strong>\uff0c\u5f15\u5165\u76f8\u5173\u5e93\uff0c\u8fd9\u91cc\u6211\u4eec\u5f15\u5165 ethers.js \u8fd9\u4e2a\u5e93\uff0c\u5b83\u5c01\u88c5\u4e86\u8bfb\u5199\u5408\u7ea6\u7684\u903b\u8f91\u3002\u5728\u9875\u9762\u4e2d\u7528 <code>&lt;script&gt;</code> \u5f15\u5165\u5982\u4e0b\uff1a</p> <pre><code class="language-html">&lt;script src=&quot;https://cdn.jsdelivr.net/npm/ethers@5.0.32/dist/ethers.umd.min.js&quot;&gt;&lt;/script&gt;</code></pre> <p><strong>\u7b2c\u4e8c\u6b65</strong>\uff0c\u6211\u4eec\u9700\u8981\u83b7\u53d6 <code>MetaMask</code> \u6ce8\u5165\u7684 <code>Web3</code>\uff0c\u53ef\u4ee5\u901a\u8fc7\u4e00\u4e2a\u7b80\u5355\u7684\u51fd\u6570\u5b9e\u73b0\uff1a</p> <pre><code class="language-js">function getWeb3Provider() {\n  if (!window.web3Provider) {\n    if (!window.ethereum) {\n      console.error(&#39;there is no web3 provider.&#39;);\n      return null;\n    }\n\n    window.web3Provider = new ethers.providers.Web3Provider(window.ethereum, &#39;any&#39;);\n  }\n\n  return window.web3Provider;\n}</code></pre> <p><strong>\u7b2c\u4e09\u6b65</strong>\uff0c\u5728\u7528\u6237\u70b9\u51fb\u9875\u9762 \u201cConnect Wallet\u201d \u6309\u94ae\u65f6\uff0c\u5c1d\u8bd5\u8fde\u63a5 <code>MetaMask</code>\uff1a</p> <pre><code class="language-js">async function connect() {\n  if (window.getWeb3Provider() === null) {\n    console.error(&#39;there is no web3 provider.&#39;);\n    return false;\n  }\n\n  try {\n    // \u83b7\u53d6\u5f53\u524d\u8fde\u63a5\u7684\u8d26\u6237\u5730\u5740:\n    let account = await window.ethereum.request({\n      method: &#39;eth_requestAccounts&#39;\n    });\n\n    // \u83b7\u53d6\u5f53\u524d\u8fde\u63a5\u7684\u94fe ID:\n    let chainId = await window.ethereum.request({\n      method: &#39;eth_chainId&#39;\n    });\n\n    console.log(&#39;wallet connected.&#39;);\n\n    return true;\n  } catch (e) {\n    console.error(&#39;could not get a wallet connection.&#39;, e);\n    return false;\n  }\n}</code></pre> <p><strong>\u7b2c\u56db\u6b65</strong>\uff0c\u5f53\u6211\u4eec\u5df2\u7ecf\u8fde\u63a5\u5230 <code>MetaMask</code> \u94b1\u5305\u540e\uff0c\u5373\u53ef\u5199\u5165\u5408\u7ea6\u3002\u5199\u5165\u5408\u7ea6\u9700\u8981\u5408\u7ea6\u7684 <code>ABI\uff08Application Binary Interface\uff09</code>\u4fe1\u606f\uff0c\u5373\u5408\u7ea6\u51fd\u6570\u8c03\u7528\u7684\u63a5\u53e3\u4fe1\u606f\uff0c\u8fd9\u4e9b\u4fe1\u606f\u5728 <code>Remix</code> \u90e8\u7f72\u65f6\u4ea7\u751f\u3002\u6211\u4eec\u9700\u8981\u56de\u5230 <code>Remix</code>\uff0c\u5728 <code>contracts-artifacts</code> \u76ee\u5f55\u4e0b\u627e\u5230 <code>Vote.json</code> \u6587\u4ef6\uff0c\u5b83\u662f\u4e00\u4e2a <code>JSON</code>\uff0c\u53f3\u4fa7\u627e\u5230 <code>&quot;abi&quot;: [...]</code>\uff0c\u628a <code>abi</code> \u5bf9\u5e94\u7684\u90e8\u5206\u590d\u5236\u51fa\u6765\uff1a</p> <p><img src="'+C+'" alt=""></p> <p>\u4ee5\u5e38\u91cf\u7684\u5f62\u5f0f\u5f15\u5165 <code>Vote</code> \u5408\u7ea6\u7684\u5730\u5740\u548c <code>ABI</code>\uff1a</p> <pre><code class="language-js">const VOTE_ADDR = &#39;0x5b2a057e1db47463695b4629114cbdae99235a46&#39;;\nconst VOTE_ABI = [&#39;...&#39;];</code></pre> <p><strong>\u7b2c\u4e94\u6b65</strong>\uff0c\u73b0\u5728\u6211\u4eec\u5c31\u53ef\u4ee5\u5728\u9875\u9762\u8c03\u7528 <code>vote()</code> \u5199\u5165\u51fd\u6570\u4e86\uff1a</p> <pre><code class="language-js">async function vote(proposal) {\n  // TODO: \u68c0\u67e5 MetaMask \u8fde\u63a5\u4fe1\u606f\n  // \u6839\u636e\u5730\u5740\u548c ABI \u521b\u5efa\u4e00\u4e2a Contract \u5bf9\u8c61:\n  let contract = new ethers.Contract(VOTE_ADDR, VOTE_ABI, window.getWeb3Provider().getSigner());\n\n  // \u8c03\u7528 vote()\u51fd\u6570\uff0c\u5e76\u8fd4\u56de\u4e00\u4e2a tx \u5bf9\u8c61:\n  let tx = await contract.vote(proposal);\n\n  // \u7b49\u5f85 tx \u843d\u5757\uff0c\u5e76\u81f3\u5c11 1 \u4e2a\u533a\u5757\u786e\u8ba4:\n  await tx.wait(1);\n}</code></pre> <p>\u4ee5\u4e0a\u5c31\u662f\u8c03\u7528\u5408\u7ea6\u7684\u5168\u90e8\u6d41\u7a0b</p> <h3 id="94-\u670d\u52a1\u5668\u7aef">9.4 \u670d\u52a1\u5668\u7aef</h3> <p><strong>\u6211\u4eec\u9700\u8981\u660e\u786e\u51e0\u4e2a\u8981\u70b9\uff1a</strong></p> <ol> <li>\u9875\u9762\u7684 <code>JavaScript</code> \u4ee3\u7801\u65e0\u6cd5\u76f4\u63a5\u8bbf\u95ee\u4ee5\u592a\u574a\u7f51\u7edc\u7684 <code>P2P</code> \u8282\u70b9\uff0c\u53ea\u80fd\u95f4\u63a5\u901a\u8fc7 <code>MetaMask</code> \u94b1\u5305\u8bbf\u95ee</li> <li>\u94b1\u5305\u4e4b\u6240\u4ee5\u80fd\u8bbf\u95ee\u4ee5\u592a\u574a\u7f51\u7edc\u7684\u8282\u70b9\uff0c\u662f\u56e0\u4e3a\u5b83\u4eec\u5185\u7f6e\u4e86\u67d0\u4e9b\u516c\u5171\u8282\u70b9\u7684\u57df\u540d\u4fe1\u606f</li> <li>\u5982\u679c\u7528\u6237\u7684\u6d4f\u89c8\u5668\u6ca1\u6709\u5b89\u88c5 <code>MetaMask</code> \u94b1\u5305\uff0c\u5219\u9875\u9762\u65e0\u6cd5\u901a\u8fc7\u94b1\u5305\u8bfb\u53d6\u5408\u7ea6\u6216\u5199\u5165\u5408\u7ea6</li> </ol> <p><strong>\u56e0\u6b64\u5f15\u51fa\u4e86\u4e00\u4e2a\u4e2a\u95ee\u9898\uff1a\u4e00\u4e2a Dapp \u5230\u5e95\u9700\u4e0d\u9700\u8981\u670d\u52a1\u5668\u7aef\uff1f</strong></p> <p>\u5bf9\u4e8e\u5927\u591a\u6570\u7684 <code>Dapp</code> \u6765\u8bf4\uff0c\u662f\u9700\u8981\u670d\u52a1\u5668\u7aef\u7684\uff0c\u8fd9\u662f\u56e0\u4e3a\uff0c\u5f53\u7528\u6237\u6d4f\u89c8\u5668\u6ca1\u6709\u5b89\u88c5\u94b1\u5305\uff0c\u6216\u8005\u94b1\u5305\u5e76\u6ca1\u6709\u8fde\u63a5\u5230 <code>Dapp</code> \u671f\u5f85\u7684\u7f51\u7edc\u65f6\uff0c\u9875\u9762\u5c06\u65e0\u6cd5\u83b7\u5f97\u5408\u7ea6\u7684\u4efb\u4f55\u6570\u636e\u3002\u4f8b\u5982\uff0c\u4e0a\u8ff0 <code>Dapp</code> \u5c31\u65e0\u6cd5\u8bfb\u53d6\u5230\u4e09\u79cd\u6295\u7968\u7684\u6570\u91cf\uff0c\u56e0\u6b64\u65e0\u6cd5\u5728\u9875\u9762\u4e0a\u7ed8\u5236\u5bf9\u6bd4\u56fe</p> <p>\u5982\u679c\u90e8\u7f72\u4e00\u4e2a\u670d\u52a1\u5668\u7aef\uff0c\u7531\u670d\u52a1\u5668\u8fde\u63a5 <code>P2P</code> \u7f51\u7edc\u7684\u8282\u70b9\u5e76\u8bfb\u53d6\u5408\u7ea6\uff0c\u7136\u540e\u4ee5 <code>JSON API</code> \u7684\u5f62\u5f0f\u7ed9\u524d\u7aef\u63d0\u4f9b\u76f8\u5173\u6570\u636e\uff0c\u5219\u53ef\u4ee5\u5b9e\u73b0\u4e00\u4e2a\u66f4\u5b8c\u5584\u7684 <code>Dapp</code>\u3002\u56e0\u6b64\uff0c\u5b8c\u6574\u7684 <code>Dapp</code> \u67b6\u6784\u5982\u4e0b\uff1a</p> <p><img src="'+I+'" alt=""></p> <p><strong>\u4e3a Dapp \u642d\u5efa\u540e\u7aef\u670d\u52a1\u5668\u65f6\u8981\u4e25\u683c\u9075\u5faa\u4ee5\u4e0b\u89c4\u8303\uff1a</strong></p> <ol> <li>\u540e\u7aef\u670d\u52a1\u5668\u53ea\u8bfb\u53d6\u5408\u7ea6\uff0c\u4e0d\u5b58\u50a8\u4efb\u4f55\u79c1\u94a5\uff0c\u56e0\u6b64\u65e0\u6cd5\u5199\u5165\u5408\u7ea6\uff0c\u4fdd\u8bc1\u4e86\u5b89\u5168\u6027</li> <li>\u540e\u7aef\u670d\u52a1\u5668\u8981\u8bfb\u53d6\u5408\u7ea6\uff0c\u5c31\u5fc5\u987b\u8fde\u63a5\u5230 <code>P2P</code> \u8282\u70b9\uff0c\u8981\u4e48\u9009\u62e9\u516c\u5171\u7684\u8282\u70b9\u670d\u52a1\uff08\u4f8b\u5982 Infura\uff09\uff0c\u8981\u4e48\u81ea\u5df1\u642d\u5efa\u4e00\u4e2a\u4ee5\u592a\u574a\u8282\u70b9\uff08\u7ef4\u62a4\u7684\u5de5\u4f5c\u91cf\u8f83\u5927\uff09</li> <li>\u540e\u7aef\u670d\u52a1\u5668\u5e94\u8be5\u901a\u8fc7\u5408\u7ea6\u4ea7\u751f\u7684\u65e5\u5fd7\uff08\u5373\u5408\u7ea6\u8fd0\u884c\u65f6\u89e6\u53d1\u7684 <code>event</code>\uff09\u76d1\u542c\u5408\u7ea6\u7684\u72b6\u6001\u53d8\u5316\uff0c\u800c\u4e0d\u662f\u5b9a\u671f\u626b\u63cf\u3002\u76d1\u542c\u65e5\u5fd7\u9700\u8981\u901a\u8fc7 <code>P2P</code> \u8282\u70b9\u521b\u5efa <code>Filter</code> \u5e76\u83b7\u53d6 <code>Filter</code> \u8fd4\u56de\u7684\u65e5\u5fd7</li> <li>\u540e\u7aef\u670d\u52a1\u5668\u5e94\u8be5\u5c06\u4ece\u65e5\u5fd7\u83b7\u53d6\u7684\u6570\u636e\u505a\u805a\u5408\u3001\u7f13\u5b58\uff0c\u4ee5\u4fbf\u524d\u7aef\u9875\u9762\u80fd\u5feb\u901f\u5c55\u793a\u76f8\u5173\u6570\u636e</li> <li>\u56e0\u6b64\uff0c\u8bbe\u8ba1 <code>Dapp</code> \u65f6\uff0c\u65e2\u8981\u8003\u8651\u5c06\u5173\u952e\u4e1a\u52a1\u903b\u8f91\u5199\u5165\u5408\u7ea6\uff0c\u53c8\u8981\u8003\u8651\u65e5\u5fd7\u8f93\u51fa\u6709\u8db3\u591f\u7684\u4fe1\u606f\u8ba9\u540e\u7aef\u670d\u52a1\u5668\u80fd\u805a\u5408\u5386\u53f2\u6570\u636e\u3002\u524d\u7aef\u3001\u540e\u7aef\u548c\u5408\u7ea6\u4e09\u65b9\u5f00\u53d1\u5fc5\u987b\u7d27\u5bc6\u914d\u5408</li> </ol> <p>\u4e0d\u540c\u7684\u7f16\u7a0b\u8bed\u8a00\u5b9e\u73b0\u540e\u7aef\u670d\u52a1\u65f6\uff0c\u53ef\u4ee5\u9009\u62e9\u5c01\u88c5\u597d\u7684\u7b2c\u4e09\u65b9\u5e93\uff0c\u4f8b\u5982 <code>Java</code> \u53ef\u4ee5\u4f7f\u7528 <code>Web3j</code>\uff0c<code>Python</code> \u53ef\u4ee5\u4f7f\u7528 <code>Web3.py</code>\uff0c\u8be6\u7ec6\u7684\u8bf4\u660e\u53ef\u4ee5\u53c2\u8003\u5b98\u65b9\u6587\u6863</p> ';e.exports=W}}]);