(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["docs/browser/browser"],{NE0d:function(o,e,c){var d=c("Rxnk"),l=c("NoCu"),i=c("E0W2"),t=d(l),r=d(i),p='<h1 id="\u6d4f\u89c8\u5668\u5de5\u4f5c\u539f\u7406">\u6d4f\u89c8\u5668\u5de5\u4f5c\u539f\u7406</h1> <p>\u6587\u7ae0\u642c\u8fd0\u81ea\uff1a</p> <p>\u4f5c\u8005\uff1adailc</p> <p>\u94fe\u63a5\uff1a<a href="https://juejin.cn/post/6844903553795014663">https://juejin.cn/post/6844903553795014663</a></p> <p>\u6765\u6e90\uff1a\u6398\u91d1</p> <h2 id="1-\u8fdb\u7a0b\u548c\u7ebf\u7a0b">1 \u8fdb\u7a0b\u548c\u7ebf\u7a0b</h2> <p><strong>\u4e0b\u9762\u901a\u8fc7\u6bd4\u55bb\u63cf\u8ff0\u8fdb\u7a0b\u548c\u7ebf\u7a0b\uff1a</strong></p> <ul> <li>\u8fdb\u7a0b\u662f\u4e00\u4e2a\u5de5\u5382\uff0c\u5de5\u5382\u6709\u5b83\u7684\u72ec\u7acb\u8d44\u6e90 -&gt; \u7cfb\u7edf\u5206\u914d\u7684\u5185\u5b58\uff08\u72ec\u7acb\u7684\u4e00\u5757\u5185\u5b58\uff09</li> <li>\u5de5\u5382\u4e4b\u95f4\u7684\u76f8\u4e92\u72ec\u7acb -&gt; \u8fdb\u7a0b\u4e4b\u95f4\u76f8\u4e92\u72ec\u7acb</li> <li>\u591a\u4e2a\u5de5\u4eba\u534f\u4f5c\u5b8c\u6210\u4efb\u52a1 -&gt; \u591a\u4e2a\u7ebf\u7a0b\u5728\u8fdb\u7a0b\u4e2d\u534f\u4f5c\u5b8c\u6210\u4efb\u52a1</li> <li>\u5de5\u5382\u5185\u6709\u4e00\u4e2a\u6216\u591a\u4e2a\u5de5\u4eba -&gt; \u4e00\u4e2a\u8fdb\u7a0b\u7531\u4e00\u4e2a\u6216\u591a\u4e2a\u7ebf\u7a0b\u7ec4\u6210</li> <li>\u5de5\u4eba\u4e4b\u95f4\u5171\u4eab\u7a7a\u95f4 -&gt; \u540c\u4e00\u8fdb\u7a0b\u4e0b\u7684\u5404\u4e2a\u7ebf\u7a0b\u4e4b\u95f4\u5171\u4eab\u7a0b\u5e8f\u7684\u5185\u5b58\u7a7a\u95f4\uff08\u5305\u62ec\u4ee3\u7801\u6bb5\u3001\u6570\u636e\u96c6\u3001\u5806\u7b49\uff09</li> </ul> <p><strong>\u8f83\u4e3a\u5b98\u65b9\u7684\u672f\u8bed\u63cf\u8ff0\u8fdb\u7a0b\u548c\u7ebf\u7a0b\uff1a</strong></p> <ul> <li>\u8fdb\u7a0b\u662f cpu \u8d44\u6e90\u5206\u914d\u7684\u6700\u5c0f\u5355\u4f4d\uff08\u662f\u80fd\u62e5\u6709\u8d44\u6e90\u548c\u72ec\u7acb\u8fd0\u884c\u7684\u6700\u5c0f\u5355\u4f4d\uff09</li> <li>\u7ebf\u7a0b\u662f cpu \u8c03\u5ea6\u7684\u6700\u5c0f\u5355\u4f4d\uff08\u7ebf\u7a0b\u662f\u5efa\u7acb\u5728\u8fdb\u7a0b\u7684\u57fa\u7840\u4e0a\u7684\u4e00\u6b21\u7a0b\u5e8f\u8fd0\u884c\u5355\u4f4d\uff0c\u4e00\u4e2a\u8fdb\u7a0b\u4e2d\u53ef\u4ee5\u6709\u591a\u4e2a\u7ebf\u7a0b\uff09</li> </ul> <p><strong>\u6ce8\u610f\u70b9\uff1a</strong></p> <ul> <li>\u4e0d\u540c\u8fdb\u7a0b\u4e4b\u95f4\u4e5f\u53ef\u4ee5\u901a\u4fe1\uff0c\u4e0d\u8fc7\u4ee3\u4ef7\u8f83\u5927</li> <li>\u73b0\u5728\uff0c\u4e00\u822c\u901a\u7528\u7684\u53eb\u6cd5\uff1a\u5355\u7ebf\u7a0b\u4e0e\u591a\u7ebf\u7a0b\uff0c\u90fd\u662f\u6307\u5728\u4e00\u4e2a\u8fdb\u7a0b\u5185\u7684\u5355\u548c\u591a\uff08\u6240\u4ee5\u6838\u5fc3\u8fd8\u662f\u5f97\u5c5e\u4e8e\u4e00\u4e2a\u8fdb\u7a0b\u624d\u884c\uff09</li> </ul> <h2 id="2-\u6d4f\u89c8\u5668\u662f\u591a\u8fdb\u7a0b\u7684">2 \u6d4f\u89c8\u5668\u662f\u591a\u8fdb\u7a0b\u7684</h2> <p><strong>\u6d4f\u89c8\u5668\u8fdb\u7a0b\u4e0e\u7ebf\u7a0b\u7684\u7b80\u5316\u7406\u89e3\uff1a</strong></p> <ul> <li>\u6d4f\u89c8\u5668\u662f\u591a\u8fdb\u7a0b\u7684</li> <li>\u6d4f\u89c8\u5668\u4e4b\u6240\u4ee5\u80fd\u591f\u8fd0\u884c\uff0c\u662f\u56e0\u4e3a\u7cfb\u7edf\u7ed9\u5b83\u7684\u8fdb\u7a0b\u5206\u914d\u4e86\u8d44\u6e90\uff08cpu\u3001\u5185\u5b58\uff09</li> <li>\u7b80\u5355\u70b9\u7406\u89e3\uff0c\u6bcf\u6253\u5f00\u4e00\u4e2a <code>Tab</code> \u9875\uff0c\u5c31\u76f8\u5f53\u4e8e\u521b\u5efa\u4e86\u4e00\u4e2a\u72ec\u7acb\u7684\u6d4f\u89c8\u5668\u8fdb\u7a0b\u3002</li> </ul> <blockquote> <p><strong>\u6ce8\u610f\uff1a</strong>\u5728\u8fd9\u91cc\u6d4f\u89c8\u5668\u5e94\u8be5\u4e5f\u6709\u81ea\u5df1\u7684\u4f18\u5316\u673a\u5236\uff0c\u6709\u65f6\u5019\u6253\u5f00\u591a\u4e2a <code>tab</code> \u9875\u540e\uff0c\u53ef\u4ee5\u5728 <code>Chrome</code> \u4efb\u52a1\u7ba1\u7406\u5668\u4e2d\u770b\u5230\uff0c\u6709\u4e9b\u8fdb\u7a0b\u88ab\u5408\u5e76\u4e86</p> </blockquote> <h3 id="21-\u6d4f\u89c8\u5668\u90fd\u5305\u542b\u54ea\u4e9b\u8fdb\u7a0b">2.1 \u6d4f\u89c8\u5668\u90fd\u5305\u542b\u54ea\u4e9b\u8fdb\u7a0b</h3> <p><strong>\u6d4f\u89c8\u5668\u5305\u542b\u54ea\u4e9b\u8fdb\u7a0b\uff1a\uff08\u4e3a\u4e86\u7b80\u5316\u7406\u89e3\uff0c\u4ec5\u5217\u4e3e\u4e3b\u8981\u8fdb\u7a0b\uff09</strong></p> <ul> <li><code>Browser</code> \u8fdb\u7a0b\uff1a\u6d4f\u89c8\u5668\u7684\u4e3b\u8fdb\u7a0b\uff08\u8d1f\u8d23\u534f\u8c03\u3001\u4e3b\u63a7\uff09\uff0c\u53ea\u6709\u4e00\u4e2a\u3002\u4f5c\u7528\u6709\uff1a<ol> <li>\u8d1f\u8d23\u6d4f\u89c8\u5668\u754c\u9762\u663e\u793a\uff0c\u4e0e\u7528\u6237\u4ea4\u4e92\u3002\u5982\u524d\u8fdb\uff0c\u540e\u9000\u7b49</li> <li>\u8d1f\u8d23\u5404\u4e2a\u9875\u9762\u7684\u7ba1\u7406\uff0c\u521b\u5efa\u548c\u9500\u6bc1\u5176\u4ed6\u8fdb\u7a0b</li> <li>\u5c06 <code>Renderer</code> \u8fdb\u7a0b\u5f97\u5230\u7684\u5185\u5b58\u4e2d\u7684 <code>Bitmap</code>\uff0c\u7ed8\u5236\u5230\u7528\u6237\u754c\u9762\u4e0a</li> <li>\u7f51\u7edc\u8d44\u6e90\u7684\u7ba1\u7406\uff0c\u4e0b\u8f7d\u7b49</li> </ol> </li> <li>\u7b2c\u4e09\u65b9\u63d2\u4ef6\u8fdb\u7a0b\uff1a\u6bcf\u79cd\u7c7b\u578b\u7684\u63d2\u4ef6\u5bf9\u5e94\u4e00\u4e2a\u8fdb\u7a0b\uff0c\u4ec5\u5f53\u4f7f\u7528\u8be5\u63d2\u4ef6\u65f6\u624d\u521b\u5efa</li> <li><code>GPU</code> \u8fdb\u7a0b\uff1a\u6700\u591a\u4e00\u4e2a\uff0c\u7528\u4e8e 3D \u7ed8\u5236\u7b49</li> <li>\u6d4f\u89c8\u5668\u6e32\u67d3\u8fdb\u7a0b\uff08\u6d4f\u89c8\u5668\u5185\u6838\uff09\uff08<code>Renderer</code> \u8fdb\u7a0b\uff0c\u5185\u90e8\u662f\u591a\u7ebf\u7a0b\u7684\uff09\uff1a\u9ed8\u8ba4\u6bcf\u4e2a Tab \u9875\u9762\u4e00\u4e2a\u8fdb\u7a0b\uff0c\u4e92\u4e0d\u5f71\u54cd\u3002\u4e3b\u8981\u4f5c\u7528\u4e3a\uff1a<ol> <li>\u9875\u9762\u6e32\u67d3</li> <li>\u811a\u672c\u6267\u884c</li> <li>\u4e8b\u4ef6\u5904\u7406\u7b49</li> <li>\u5f02\u6b65\u8bf7\u6c42</li> </ol> </li> </ul> <h3 id="22-\u6d4f\u89c8\u5668\u591a\u8fdb\u7a0b\u7684\u4f18\u52bf">2.2 \u6d4f\u89c8\u5668\u591a\u8fdb\u7a0b\u7684\u4f18\u52bf</h3> <p><strong>\u76f8\u6bd4\u4e8e\u5355\u8fdb\u7a0b\u6d4f\u89c8\u5668\uff0c\u591a\u8fdb\u7a0b\u6709\u5982\u4e0b\u4f18\u70b9\uff1a</strong></p> <ul> <li>\u907f\u514d\u5355\u4e2a\u9875\u9762\u5d29\u6e83\u5f71\u54cd\u6574\u4e2a\u6d4f\u89c8\u5668</li> <li>\u907f\u514d\u7b2c\u4e09\u65b9\u63d2\u4ef6\u5d29\u6e83\u5f71\u54cd\u6574\u4e2a\u6d4f\u89c8\u5668</li> <li>\u591a\u8fdb\u7a0b\u5145\u5206\u5229\u7528\u591a\u6838\u4f18\u52bf</li> <li>\u65b9\u4fbf\u4f7f\u7528\u6c99\u76d2\u6a21\u578b\u9694\u79bb\u63d2\u4ef6\u7b49\u8fdb\u7a0b\uff0c\u63d0\u9ad8\u6d4f\u89c8\u5668\u7a33\u5b9a\u6027</li> </ul> <blockquote> <p>\u7b80\u5355\u70b9\u7406\u89e3\uff1a\u5982\u679c\u6d4f\u89c8\u5668\u662f\u5355\u8fdb\u7a0b\uff0c\u90a3\u4e48\u67d0\u4e2a <code>Tab</code> \u9875\u5d29\u6e83\u4e86\uff0c\u5c31\u5f71\u54cd\u4e86\u6574\u4e2a\u6d4f\u89c8\u5668\uff0c\u4f53\u9a8c\u6709\u591a\u5dee\uff1b\u540c\u7406\u5982\u679c\u662f\u5355\u8fdb\u7a0b\uff0c\u63d2\u4ef6\u5d29\u6e83\u4e86\u4e5f\u4f1a\u5f71\u54cd\u6574\u4e2a\u6d4f\u89c8\u5668\uff1b\u800c\u4e14\u591a\u8fdb\u7a0b\u8fd8\u6709\u5176\u5b83\u7684\u8bf8\u591a\u4f18\u52bf\u3002\u3002\u3002</p> </blockquote> <p>\u5f53\u7136\uff0c\u5185\u5b58\u7b49\u8d44\u6e90\u6d88\u8017\u4e5f\u4f1a\u66f4\u5927\uff0c\u6709\u70b9\u7a7a\u95f4\u6362\u65f6\u95f4\u7684\u610f\u601d\u3002</p> <h3 id="23-\u91cd\u70b9\u662f\u6d4f\u89c8\u5668\u5185\u6838\uff08\u6e32\u67d3\u8fdb\u7a0b\uff09">2.3 \u91cd\u70b9\u662f\u6d4f\u89c8\u5668\u5185\u6838\uff08\u6e32\u67d3\u8fdb\u7a0b\uff09</h3> <p>\u91cd\u70b9\u6765\u4e86\uff0c\u6211\u4eec\u53ef\u4ee5\u770b\u5230\uff0c\u4e0a\u9762\u63d0\u5230\u4e86\u8fd9\u4e48\u591a\u7684\u8fdb\u7a0b\uff0c\u90a3\u4e48\uff0c\u5bf9\u4e8e\u666e\u901a\u7684\u524d\u7aef\u64cd\u4f5c\u6765\u8bf4\uff0c\u6700\u7ec8\u8981\u7684\u662f\u4ec0\u4e48\u5462\uff1f\u7b54\u6848\u662f\u6e32\u67d3\u8fdb\u7a0b</p> <blockquote> <p>\u53ef\u4ee5\u8fd9\u6837\u7406\u89e3\uff0c\u9875\u9762\u7684\u6e32\u67d3\uff0cJS \u7684\u6267\u884c\uff0c\u4e8b\u4ef6\u7684\u5faa\u73af\uff0c\u90fd\u5728\u8fd9\u4e2a\u8fdb\u7a0b\u5185\u8fdb\u884c</p> </blockquote> <p><strong>\u6d4f\u89c8\u5668\u8fdb\u7a0b\u5305\u542b\u4e86\u54ea\u4e9b\u7ebf\u7a0b\uff1a</strong></p> <ul> <li><code>UI \u7ebf\u7a0b</code> \u8d1f\u8d23\u7ed8\u5236\u5de5\u5177\u680f\u4e2d\u7684\u6309\u94ae\u3001\u5730\u5740\u680f\u7b49</li> <li><code>\u7f51\u7edc\u7ebf\u7a0b</code> \u8d1f\u8d23\u4ece\u7f51\u7edc\u4e2d\u83b7\u53d6\u6570\u636e</li> <li><code>\u5b58\u50a8\u7ebf\u7a0b</code> \u8d1f\u8d23\u6587\u4ef6\u7b49\u529f\u80fd</li> </ul> <p><strong>\u6e32\u67d3\u8fdb\u7a0b\u5305\u542b\u4e86\u54ea\u4e9b\u7ebf\u7a0b\uff08\u5217\u4e3e\u4e00\u4e9b\u4e3b\u8981\u5e38\u9a7b\u7ebf\u7a0b\uff09\uff1a</strong></p> <ul> <li><code>GUI</code> \u6e32\u67d3\u7ebf\u7a0b<ol> <li>\u8d1f\u8d23\u6e32\u67d3\u6d4f\u89c8\u5668\u754c\u9762\uff0c\u89e3\u6790 <code>HTML</code>\uff0c<code>CSS</code>\uff0c\u6784\u5efa <code>DOM</code> \u6811\u548c <code>RenderObject</code> \u6811\uff0c\u5e03\u5c40\u548c\u7ed8\u5236\u7b49</li> <li>\u5f53\u754c\u9762\u9700\u8981\u91cd\u7ed8\uff08Repaint\uff09\u6216\u7531\u4e8e\u67d0\u79cd\u64cd\u4f5c\u5f15\u53d1\u56de\u6d41 (reflow) \u65f6\uff0c\u8be5\u7ebf\u7a0b\u5c31\u4f1a\u6267\u884c</li> <li>\u6ce8\u610f\uff0c<strong>GUI \u6e32\u67d3\u7ebf\u7a0b\u4e0e JS \u5f15\u64ce\u7ebf\u7a0b\u662f\u4e92\u65a5\u7684</strong>\uff0c\u5f53 <code>JS</code> \u5f15\u64ce\u6267\u884c\u65f6 <code>GUI</code> \u7ebf\u7a0b\u4f1a\u88ab\u6302\u8d77\uff08\u76f8\u5f53\u4e8e\u88ab\u51bb\u7ed3\u4e86\uff09\uff0c<code>GUI</code> \u66f4\u65b0\u4f1a\u88ab\u4fdd\u5b58\u5728\u4e00\u4e2a\u961f\u5217\u4e2d\u7b49\u5230 <code>JS</code> \u5f15\u64ce\u7a7a\u95f2\u65f6\u7acb\u5373\u88ab\u6267\u884c</li> </ol> </li> <li><code>JS</code> \u5f15\u64ce\u7ebf\u7a0b<ol> <li>\u4e5f\u79f0\u4e3a <code>JS</code> \u5185\u6838\uff0c\u8d1f\u8d23\u5904\u7406 <code>Javascript</code> \u811a\u672c\u7a0b\u5e8f\u3002\uff08\u4f8b\u5982 V8 \u5f15\u64ce\uff09</li> <li><code>JS</code> \u5f15\u64ce\u7ebf\u7a0b\u8d1f\u8d23\u89e3\u6790 <code>Javascript</code> \u811a\u672c\uff0c\u8fd0\u884c\u4ee3\u7801</li> <li><code>JS</code> \u5f15\u64ce\u4e00\u76f4\u7b49\u5f85\u7740\u4efb\u52a1\u961f\u5217\u4e2d\u4efb\u52a1\u7684\u5230\u6765\uff0c\u7136\u540e\u52a0\u4ee5\u5904\u7406\uff0c\u4e00\u4e2a <code>Tab</code> \u9875\uff08<code>renderer</code> \u8fdb\u7a0b\uff09\u4e2d\u65e0\u8bba\u4ec0\u4e48\u65f6\u5019\u90fd\u53ea\u6709\u4e00\u4e2a <code>JS</code> \u7ebf\u7a0b\u5728\u8fd0\u884c <code>JS</code> \u7a0b\u5e8f</li> <li>\u540c\u6837\u6ce8\u610f\uff0c<strong><code>GUI</code> \u6e32\u67d3\u7ebf\u7a0b\u4e0e <code>JS</code> \u5f15\u64ce\u7ebf\u7a0b\u662f\u4e92\u65a5\u7684</strong>\uff0c\u6240\u4ee5\u5982\u679c <code>JS</code> \u6267\u884c\u7684\u65f6\u95f4\u8fc7\u957f\uff0c\u8fd9\u6837\u5c31\u4f1a\u9020\u6210\u9875\u9762\u7684\u6e32\u67d3\u4e0d\u8fde\u8d2f\uff0c\u5bfc\u81f4\u9875\u9762\u6e32\u67d3\u52a0\u8f7d\u963b\u585e</li> </ol> </li> <li>\u4e8b\u4ef6\u89e6\u53d1\u7ebf\u7a0b<ol> <li>\u5f52\u5c5e\u4e8e\u6d4f\u89c8\u5668\u800c\u4e0d\u662f <code>JS</code> \u5f15\u64ce\uff0c\u7528\u6765\u63a7\u5236\u4e8b\u4ef6\u5faa\u73af\uff08\u53ef\u4ee5\u7406\u89e3\uff0c<code>JS</code> \u5f15\u64ce\u81ea\u5df1\u90fd\u5fd9\u4e0d\u8fc7\u6765\uff0c\u9700\u8981\u6d4f\u89c8\u5668\u53e6\u5f00\u7ebf\u7a0b\u534f\u52a9\uff09</li> <li>\u5f53 <code>JS</code> \u5f15\u64ce\u6267\u884c\u4ee3\u7801\u5757\u5982 <code>setTimeOut</code> \u65f6\uff08\u4e5f\u53ef\u6765\u81ea\u6d4f\u89c8\u5668\u5185\u6838\u7684\u5176\u4ed6\u7ebf\u7a0b,\u5982\u9f20\u6807\u70b9\u51fb\u3001<code>AJAX</code> \u5f02\u6b65\u8bf7\u6c42\u7b49\uff09\uff0c\u4f1a\u5c06\u5bf9\u5e94\u4efb\u52a1\u6dfb\u52a0\u5230\u4e8b\u4ef6\u7ebf\u7a0b\u4e2d</li> <li>\u5f53\u5bf9\u5e94\u7684\u4e8b\u4ef6\u7b26\u5408\u89e6\u53d1\u6761\u4ef6\u88ab\u89e6\u53d1\u65f6\uff0c\u8be5\u7ebf\u7a0b\u4f1a\u628a\u4e8b\u4ef6\u6dfb\u52a0\u5230\u5f85\u5904\u7406\u961f\u5217\u7684\u961f\u5c3e\uff0c\u7b49\u5f85 <code>JS</code> \u5f15\u64ce\u7684\u5904\u7406</li> <li>\u6ce8\u610f\uff0c\u7531\u4e8e <code>JS</code> \u7684\u5355\u7ebf\u7a0b\u5173\u7cfb\uff0c\u6240\u4ee5\u8fd9\u4e9b\u5f85\u5904\u7406\u961f\u5217\u4e2d\u7684\u4e8b\u4ef6\u90fd\u5f97\u6392\u961f\u7b49\u5f85 <code>JS</code> \u5f15\u64ce\u5904\u7406\uff08\u5f53 <code>JS</code> \u5f15\u64ce\u7a7a\u95f2\u65f6\u624d\u4f1a\u53bb\u6267\u884c\uff09</li> </ol> </li> <li>\u5b9a\u65f6\u89e6\u53d1\u5668\u7ebf\u7a0b<ol> <li>\u4f20\u8bf4\u4e2d\u7684 <code>setInterval</code> \u4e0e <code>setTimeout</code> \u6240\u5728\u7ebf\u7a0b</li> <li>\u6d4f\u89c8\u5668\u5b9a\u65f6\u8ba1\u6570\u5668\u5e76\u4e0d\u662f\u7531 <code>JavaScript</code> \u5f15\u64ce\u8ba1\u6570\u7684,\uff08\u56e0\u4e3a <code>JavaScript</code> \u5f15\u64ce\u662f\u5355\u7ebf\u7a0b\u7684, \u5982\u679c\u5904\u4e8e\u963b\u585e\u7ebf\u7a0b\u72b6\u6001\u5c31\u4f1a\u5f71\u54cd\u8bb0\u8ba1\u65f6\u7684\u51c6\u786e\uff09</li> <li>\u56e0\u6b64\u901a\u8fc7\u5355\u72ec\u7ebf\u7a0b\u6765\u8ba1\u65f6\u5e76\u89e6\u53d1\u5b9a\u65f6\uff08\u8ba1\u65f6\u5b8c\u6bd5\u540e\uff0c\u6dfb\u52a0\u5230\u4e8b\u4ef6\u961f\u5217\u4e2d\uff0c\u7b49\u5f85 <code>JS</code> \u5f15\u64ce\u7a7a\u95f2\u540e\u6267\u884c\uff09</li> <li>\u6ce8\u610f\uff0c<code>W3C</code> \u5728 <code>HTML</code> \u6807\u51c6\u4e2d\u89c4\u5b9a\uff0c\u89c4\u5b9a\u8981\u6c42 <code>setTimeout</code> \u4e2d\u4f4e\u4e8e <code>4ms</code> \u7684\u65f6\u95f4\u95f4\u9694\u7b97\u4e3a <code>4ms</code></li> </ol> </li> <li>\u5f02\u6b65 http \u8bf7\u6c42\u7ebf\u7a0b<ol> <li>\u5728 <code>XMLHttpRequest</code> \u5728\u8fde\u63a5\u540e\u662f\u901a\u8fc7\u6d4f\u89c8\u5668\u65b0\u5f00\u4e00\u4e2a\u7ebf\u7a0b\u8bf7\u6c42</li> <li>\u5c06\u68c0\u6d4b\u5230\u72b6\u6001\u53d8\u66f4\u65f6\uff0c\u5982\u679c\u8bbe\u7f6e\u6709\u56de\u8c03\u51fd\u6570\uff0c\u5f02\u6b65\u7ebf\u7a0b\u5c31\u4ea7\u751f\u72b6\u6001\u53d8\u66f4\u4e8b\u4ef6\uff0c\u5c06\u8fd9\u4e2a\u56de\u8c03\u518d\u653e\u5165\u4e8b\u4ef6\u961f\u5217\u4e2d\u3002\u518d\u7531 <code>JavaScript</code> \u5f15\u64ce\u6267\u884c\u3002</li> </ol> </li> </ul> <h3 id="24-\u4e3b\u8fdb\u7a0b\u548c\u6e32\u67d3\u8fdb\u884c\u7684\u901a\u4fe1\u8fc7\u7a0b">2.4 \u4e3b\u8fdb\u7a0b\u548c\u6e32\u67d3\u8fdb\u884c\u7684\u901a\u4fe1\u8fc7\u7a0b</h3> <p><strong>\u6574\u4e2a\u7684\u8fc7\u7a0b\uff1a(\u7b80\u5316\u4e86\u5f88\u591a)</strong></p> <ul> <li><code>Browser</code> \u8fdb\u7a0b\u6536\u5230\u7528\u6237\u8bf7\u6c42\uff0c\u9996\u5148\u9700\u8981\u83b7\u53d6\u9875\u9762\u5185\u5bb9\uff08\u8b6c\u5982\u901a\u8fc7\u7f51\u7edc\u4e0b\u8f7d\u8d44\u6e90\uff09\uff0c\u968f\u540e\u5c06\u8be5\u4efb\u52a1\u901a\u8fc7 <code>RendererHost</code> \u63a5\u53e3\u4f20\u9012\u7ed9 <code>Render</code> \u8fdb\u7a0b (http \u8bf7\u6c42\u83b7\u53d6\u9875\u9762\u8d44\u6e90\u662f\u5728\u4e3b\u8fdb\u7a0b)</li> <li><code>Renderer</code> \u8fdb\u7a0b\u7684 <code>Renderer</code> \u63a5\u53e3\u6536\u5230\u6d88\u606f\uff0c\u7b80\u5355\u89e3\u91ca\u540e\uff0c\u4ea4\u7ed9\u6e32\u67d3\u7ebf\u7a0b\uff0c\u7136\u540e\u5f00\u59cb\u6e32\u67d3<ol> <li>\u6e32\u67d3\u7ebf\u7a0b\u63a5\u6536\u8bf7\u6c42\uff0c\u52a0\u8f7d\u7f51\u9875\u5e76\u6e32\u67d3\u7f51\u9875\uff0c\u8fd9\u5176\u4e2d\u53ef\u80fd\u9700\u8981 <code>Browser</code> \u8fdb\u7a0b\u83b7\u53d6\u8d44\u6e90\u548c\u9700\u8981 <code>GPU</code> \u8fdb\u7a0b\u6765\u5e2e\u52a9\u6e32\u67d3</li> <li>\u5f53\u7136\u53ef\u80fd\u4f1a\u6709 <code>JS</code> \u7ebf\u7a0b\u64cd\u4f5c <code>DOM</code>\uff08\u8fd9\u6837\u53ef\u80fd\u4f1a\u9020\u6210\u56de\u6d41\u5e76\u91cd\u7ed8\uff09</li> <li>\u6700\u540e <code>Render</code> \u8fdb\u7a0b\u5c06\u7ed3\u679c\u4f20\u9012\u7ed9 <code>Browser</code> \u8fdb\u7a0b</li> </ol> </li> <li><code>Browser</code> \u8fdb\u7a0b\u63a5\u6536\u5230\u7ed3\u679c\u5e76\u5c06\u7ed3\u679c\u7ed8\u5236\u51fa\u6765</li> </ul> <h2 id="3-\u6d4f\u89c8\u5668\u5185\u6838\u4e2d\u7ebf\u7a0b\u4e4b\u95f4\u7684\u5173\u7cfb">3 \u6d4f\u89c8\u5668\u5185\u6838\u4e2d\u7ebf\u7a0b\u4e4b\u95f4\u7684\u5173\u7cfb</h2> <h3 id="31-gui-\u6e32\u67d3\u7ebf\u7a0b\u4e0e-js-\u7ebf\u7a0b\u4e92\u65a5">3.1 GUI \u6e32\u67d3\u7ebf\u7a0b\u4e0e JS \u7ebf\u7a0b\u4e92\u65a5</h3> <p>\u7531\u4e8e <code>JavaScript</code> \u662f\u53ef\u64cd\u7eb5 <code>DOM</code> \u7684\uff0c\u5982\u679c\u5728\u4fee\u6539\u8fd9\u4e9b\u5143\u7d20\u5c5e\u6027\u540c\u65f6\u6e32\u67d3\u754c\u9762\uff08\u5373 <code>JS</code> \u7ebf\u7a0b\u548c <code>UI</code> \u7ebf\u7a0b\u540c\u65f6\u8fd0\u884c\uff09\uff0c\u90a3\u4e48\u6e32\u67d3\u7ebf\u7a0b\u524d\u540e\u83b7\u5f97\u7684\u5143\u7d20\u6570\u636e\u5c31\u53ef\u80fd\u4e0d\u4e00\u81f4\u4e86</p> <p>\u56e0\u6b64\u4e3a\u4e86\u9632\u6b62\u6e32\u67d3\u51fa\u73b0\u4e0d\u53ef\u9884\u671f\u7684\u7ed3\u679c\uff0c\u6d4f\u89c8\u5668\u8bbe\u7f6e <code>GUI</code> \u6e32\u67d3\u7ebf\u7a0b\u4e0e <code>JS</code> \u5f15\u64ce\u4e3a\u4e92\u65a5\u7684\u5173\u7cfb\uff0c\u5f53 <code>JS</code> \u5f15\u64ce\u6267\u884c\u65f6 <code>GUI</code> \u7ebf\u7a0b\u4f1a\u88ab\u6302\u8d77\uff0c<code>GUI</code> \u66f4\u65b0\u5219\u4f1a\u88ab\u4fdd\u5b58\u5728\u4e00\u4e2a\u961f\u5217\u4e2d\u7b49\u5230 <code>JS</code> \u5f15\u64ce\u7ebf\u7a0b\u7a7a\u95f2\u65f6\u7acb\u5373\u88ab\u6267\u884c</p> <h3 id="32-js-\u963b\u585e\u9875\u9762\u6e32\u67d3">3.2 JS \u963b\u585e\u9875\u9762\u6e32\u67d3</h3> <p>\u4ece\u4e0a\u8ff0\u7684\u4e92\u65a5\u5173\u7cfb\uff0c\u53ef\u4ee5\u63a8\u5bfc\u51fa\uff0c<code>JS</code> \u5982\u679c\u6267\u884c\u65f6\u95f4\u8fc7\u957f\u5c31\u4f1a\u963b\u585e\u9875\u9762</p> <p>\u8b6c\u5982\uff0c\u5047\u8bbe <code>JS</code> \u5f15\u64ce\u6b63\u5728\u8fdb\u884c\u5de8\u91cf\u7684\u8ba1\u7b97\uff0c\u6b64\u65f6\u5c31\u7b97 <code>GUI</code> \u6709\u66f4\u65b0\uff0c\u4e5f\u4f1a\u88ab\u4fdd\u5b58\u5230\u961f\u5217\u4e2d\uff0c\u7b49\u5f85 <code>JS</code> \u5f15\u64ce\u7a7a\u95f2\u540e\u6267\u884c\u3002\u7136\u540e\uff0c\u7531\u4e8e\u5de8\u91cf\u8ba1\u7b97\uff0c\u6240\u4ee5 JS \u5f15\u64ce\u5f88\u53ef\u80fd\u5f88\u4e45\u5f88\u4e45\u540e\u624d\u80fd\u7a7a\u95f2\uff0c\u81ea\u7136\u4f1a\u611f\u89c9\u5230\u5de8\u5361\u65e0\u6bd4</p> <p>\u6240\u4ee5\uff0c\u8981\u5c3d\u91cf\u907f\u514d JS \u6267\u884c\u65f6\u95f4\u8fc7\u957f\uff0c\u8fd9\u6837\u5c31\u4f1a\u9020\u6210\u9875\u9762\u7684\u6e32\u67d3\u4e0d\u8fde\u8d2f\uff0c\u5bfc\u81f4\u9875\u9762\u6e32\u67d3\u52a0\u8f7d\u963b\u585e\u7684\u611f\u89c9</p> <h3 id="33-webworker">3.3 WebWorker</h3> <p>\u524d\u6587\u4e2d\u6709\u63d0\u5230 <code>JS</code> \u5f15\u64ce\u662f\u5355\u7ebf\u7a0b\u7684\uff0c\u800c\u4e14 <code>JS</code> \u6267\u884c\u65f6\u95f4\u8fc7\u957f\u4f1a\u963b\u585e\u9875\u9762\uff0c\u90a3\u4e48 <code>JS</code> \u5c31\u771f\u7684\u5bf9 <code>cpu</code> \u5bc6\u96c6\u578b\u8ba1\u7b97\u65e0\u80fd\u4e3a\u529b\u4e48\uff1f</p> <p>\u6240\u4ee5\uff0c\u540e\u6765 HTML5 \u4e2d\u652f\u6301\u4e86 Web Worker</p> <p><strong>MDN \u7684\u5b98\u65b9\u89e3\u91ca\u662f\uff1a</strong></p> <p><code>Web Worker</code> \u4e3a <code>Web</code> \u5185\u5bb9\u5728\u540e\u53f0\u7ebf\u7a0b\u4e2d\u8fd0\u884c\u811a\u672c\u63d0\u4f9b\u4e86\u4e00\u79cd\u7b80\u5355\u7684\u65b9\u6cd5\u3002\u7ebf\u7a0b\u53ef\u4ee5\u6267\u884c\u4efb\u52a1\u800c\u4e0d\u5e72\u6270\u7528\u6237\u754c\u9762</p> <p>\u4e00\u4e2a <code>worker</code> \u662f\u4f7f\u7528\u4e00\u4e2a\u6784\u9020\u51fd\u6570\u521b\u5efa\u7684\u4e00\u4e2a\u5bf9\u8c61(<code>e.g. Worker()</code>) \u8fd0\u884c\u4e00\u4e2a\u547d\u540d\u7684 <code>JavaScript</code> \u6587\u4ef6</p> <p>\u8fd9\u4e2a\u6587\u4ef6\u5305\u542b\u5c06\u5728\u5de5\u4f5c\u7ebf\u7a0b\u4e2d\u8fd0\u884c\u7684\u4ee3\u7801; <code>workers</code> \u8fd0\u884c\u5728\u53e6\u4e00\u4e2a\u5168\u5c40\u4e0a\u4e0b\u6587\u4e2d\uff0c\u4e0d\u540c\u4e8e\u5f53\u524d\u7684 <code>window</code></p> <p>\u56e0\u6b64\uff0c\u4f7f\u7528 <code>window</code> \u5feb\u6377\u65b9\u5f0f\u83b7\u53d6\u5f53\u524d\u5168\u5c40\u7684\u8303\u56f4 (\u800c\u4e0d\u662f <code>self</code>) \u5728\u4e00\u4e2a <code>Worker</code> \u5185\u5c06\u8fd4\u56de\u9519\u8bef</p> <p><strong>\u8fd9\u6837\u7406\u89e3\u4e0b\uff1a</strong></p> <ul> <li>\u521b\u5efa <code>Worker</code> \u65f6\uff0c<code>JS</code> \u5f15\u64ce\u5411\u6d4f\u89c8\u5668\u7533\u8bf7\u5f00\u4e00\u4e2a\u5b50\u7ebf\u7a0b\uff08\u5b50\u7ebf\u7a0b\u662f\u6d4f\u89c8\u5668\u5f00\u7684\uff0c\u5b8c\u5168\u53d7\u4e3b\u7ebf\u7a0b\u63a7\u5236\uff0c\u800c\u4e14\u4e0d\u80fd\u64cd\u4f5c <code>DOM</code>\uff09</li> <li><code>JS</code> \u5f15\u64ce\u7ebf\u7a0b\u4e0e <code>worker</code> \u7ebf\u7a0b\u95f4\u901a\u8fc7\u7279\u5b9a\u7684\u65b9\u5f0f\u901a\u4fe1\uff08<code>postMessage API</code>\uff0c\u9700\u8981\u901a\u8fc7\u5e8f\u5217\u5316\u5bf9\u8c61\u6765\u4e0e\u7ebf\u7a0b\u4ea4\u4e92\u7279\u5b9a\u7684\u6570\u636e\uff09</li> </ul> <p>\u6240\u4ee5\uff0c\u5982\u679c\u6709\u975e\u5e38\u8017\u65f6\u7684\u5de5\u4f5c\uff0c\u8bf7\u5355\u72ec\u5f00\u4e00\u4e2a <code>Worker</code> \u7ebf\u7a0b\uff0c\u8fd9\u6837\u91cc\u9762\u4e0d\u7ba1\u5982\u4f55\u7ffb\u5929\u8986\u5730\u90fd\u4e0d\u4f1a\u5f71\u54cd <code>JS</code> \u5f15\u64ce\u4e3b\u7ebf\u7a0b\uff0c\u53ea\u5f85\u8ba1\u7b97\u51fa\u7ed3\u679c\u540e\uff0c\u5c06\u7ed3\u679c\u901a\u4fe1\u7ed9\u4e3b\u7ebf\u7a0b\u5373\u53ef\uff0cperfect!</p> <p>\u800c\u4e14\u6ce8\u610f\u4e0b\uff0c<strong>JS \u5f15\u64ce\u662f\u5355\u7ebf\u7a0b\u7684</strong>\uff0c\u8fd9\u4e00\u70b9\u7684\u672c\u8d28\u4ecd\u7136\u672a\u6539\u53d8\uff0cWorker \u53ef\u4ee5\u7406\u89e3\u662f\u6d4f\u89c8\u5668\u7ed9 JS \u5f15\u64ce\u5f00\u7684\u5916\u6302\uff0c\u4e13\u95e8\u7528\u6765\u89e3\u51b3\u90a3\u4e9b\u5927\u91cf\u8ba1\u7b97\u95ee\u9898</p> <h3 id="34-webworker-\u4e0e-sharedworker">3.4 WebWorker \u4e0e SharedWorker</h3> <p>\u8fd9\u91cc\u518d\u63d0\u4e00\u4e0b <code>SharedWorker</code>\uff08\u907f\u514d\u540e\u7eed\u5c06\u8fd9\u4e24\u4e2a\u6982\u5ff5\u641e\u6df7\uff09</p> <ul> <li><code>WebWorker</code> \u53ea\u5c5e\u4e8e\u67d0\u4e2a\u9875\u9762\uff0c\u4e0d\u4f1a\u548c\u5176\u4ed6\u9875\u9762\u7684 <code>Render</code> \u8fdb\u7a0b\uff08\u6d4f\u89c8\u5668\u5185\u6838\u8fdb\u7a0b\uff09\u5171\u4eab<ul> <li>\u6240\u4ee5 <code>Chrome</code> \u5728 <code>Render</code> \u8fdb\u7a0b\u4e2d\uff08\u6bcf\u4e00\u4e2a <code>Tab</code> \u9875\u5c31\u662f\u4e00\u4e2a <code>render</code> \u8fdb\u7a0b\uff09\u521b\u5efa\u4e00\u4e2a\u65b0\u7684\u7ebf\u7a0b\u6765\u8fd0\u884c <code>Worker</code> \u4e2d\u7684 <code>JavaScript</code> \u7a0b\u5e8f</li> </ul> </li> <li><code>SharedWorker</code> \u662f\u6d4f\u89c8\u5668\u6240\u6709\u9875\u9762\u5171\u4eab\u7684\uff0c\u4e0d\u80fd\u91c7\u7528\u4e0e <code>Worker</code> \u540c\u6837\u7684\u65b9\u5f0f\u5b9e\u73b0\uff0c\u56e0\u4e3a\u5b83\u4e0d\u96b6\u5c5e\u4e8e\u67d0\u4e2a <code>Render</code> \u8fdb\u7a0b\uff0c\u53ef\u4ee5\u4e3a\u591a\u4e2a <code>Render</code> \u8fdb\u7a0b\u5171\u4eab\u4f7f\u7528<ul> <li>\u6240\u4ee5 <code>Chrome</code> \u6d4f\u89c8\u5668\u4e3a <code>SharedWorker</code> \u5355\u72ec\u521b\u5efa\u4e00\u4e2a\u8fdb\u7a0b\u6765\u8fd0\u884c <code>JavaScript</code> \u7a0b\u5e8f\uff0c\u5728\u6d4f\u89c8\u5668\u4e2d\u6bcf\u4e2a\u76f8\u540c\u7684 <code>JavaScript</code> \u53ea\u5b58\u5728\u4e00\u4e2a <code>SharedWorker</code> \u8fdb\u7a0b\uff0c\u4e0d\u7ba1\u5b83\u88ab\u521b\u5efa\u591a\u5c11\u6b21</li> </ul> </li> </ul> <p>\u770b\u5230\u8fd9\u91cc\uff0c\u5e94\u8be5\u5c31\u5f88\u5bb9\u6613\u660e\u767d\u4e86\uff0c\u672c\u8d28\u4e0a\u5c31\u662f\u8fdb\u7a0b\u548c\u7ebf\u7a0b\u7684\u533a\u522b\u3002<code>SharedWorker</code> \u7531\u72ec\u7acb\u7684\u8fdb\u7a0b\u7ba1\u7406\uff0c<code>WebWorker</code> \u53ea\u662f\u5c5e\u4e8e <code>render</code> \u8fdb\u7a0b\u4e0b\u7684\u4e00\u4e2a\u7ebf\u7a0b</p> <h2 id="4-\u6d4f\u89c8\u5668\u6e32\u67d3\u6d41\u7a0b">4 \u6d4f\u89c8\u5668\u6e32\u67d3\u6d41\u7a0b</h2> <p><strong>\u4e3a\u4e86\u7b80\u5316\u7406\u89e3\uff0c\u524d\u671f\u5de5\u4f5c\u76f4\u63a5\u7701\u7565\u6210\uff1a</strong></p> <ol> <li>\u6d4f\u89c8\u5668\u8f93\u5165 <code>url</code>\uff0c\u6d4f\u89c8\u5668\u4e3b\u8fdb\u7a0b\u63a5\u7ba1</li> <li>\u6309\u4e0b\u56de\u8f66\u5f00\u59cb\u8bbf\u95ee\u65f6\uff0c\u5f00\u4e00\u4e2a\u4e0b\u8f7d\u7ebf\u7a0b\uff0c\u4e3b\u8fdb\u7a0b\u7684 <code>UI</code> \u7ebf\u7a0b\u5c06\u501f\u52a9\u7f51\u7edc\u7ebf\u7a0b\u8bbf\u95ee\u7ad9\u70b9\u8d44\u6e90</li> <li>\u540c\u65f6\u7f51\u7edc\u7ebf\u7a0b\u4f1a\u6839\u636e\u9002\u5f53\u7684\u7f51\u7edc\u534f\u8bae\uff0c\u4f8b\u5982 <code>DNS lookup</code> \u548c <code>TLS</code> \u4e3a\u8fd9\u6b21\u8bf7\u6c42\u5efa\u7acb\u8fde\u63a5</li> <li>\u7136\u540e\u8fdb\u884c <code>http</code> \u8bf7\u6c42\uff08\u7565\u53bb <code>DNS</code> \u67e5\u8be2\uff0c<code>IP</code> \u5bfb\u5740\u7b49\u7b49\u64cd\u4f5c\uff09\uff0c\u7136\u540e\u7b49\u5f85\u54cd\u5e94\uff0c\u83b7\u53d6\u5185\u5bb9\uff0c\u968f\u540e\u5c06\u5185\u5bb9\u901a\u8fc7 <code>RendererHost</code> \u63a5\u53e3\u8f6c\u4ea4\u7ed9 <code>Renderer</code> \u8fdb\u7a0b</li> </ol> <ul> <li>\u6d4f\u89c8\u5668\u6e32\u67d3\u6d41\u7a0b\u5f00\u59cb</li> </ul> <p><strong>\u989d\u5916\u8d44\u6e90\u7684\u52a0\u8f7d:</strong></p> <ol> <li>\u4e00\u4e2a\u7f51\u7ad9\u901a\u5e38\u8fd8\u4f1a\u4f7f\u7528\u7c7b\u4f3c\u56fe\u7247\uff0c\u6837\u5f0f\u6587\u4ef6\u548c <code>JavaScript</code> \u4ee3\u7801\u7b49\u989d\u5916\u7684\u8d44\u6e90</li> <li>\u8fd9\u4e9b\u8d44\u6e90\u4e5f\u9700\u8981\u4ece\u7f51\u7edc\u6216\u7f13\u5b58\u4e2d\u83b7\u53d6\uff0c\u4e3b\u7ebf\u7a0b\u5728\u8f6c\u5316 <code>HTML</code> \u7684\u8fc7\u7a0b\u4e2d\u7406\u5e94\u6328\u4e2a\u52a0\u8f7d\u5b83\u4eec</li> <li>\u4e3a\u4e86\u63d0\u9ad8\u6548\u7387\uff0c\u9884\u52a0\u8f7d\u626b\u63cf\uff08Preload Scanner\uff09\u4e0e\u8f6c\u6362\u8fc7\u7a0b\u4f1a\u540c\u65f6\u8fd0\u884c\u7740</li> <li>\u5f53\u9884\u52a0\u8f7d\u626b\u63cf\u5728\u5206\u6790\u5668\u5206\u6790 <code>HTML</code> \u8fc7\u7a0b\u4e2d\u53d1\u73b0\u4e86\u7c7b\u4f3c <code>img</code> \u6216 <code>link</code> \u8fd9\u6837\u7684\u6807\u7b7e\u65f6\uff0c\u5c31\u4f1a\u53d1\u9001\u8bf7\u6c42\u7ed9\u6d4f\u89c8\u5668\u8fdb\u7a0b\u7684\u7f51\u7edc\u7ebf\u7a0b</li> <li>\u4e3b\u7ebf\u7a0b\u4f1a\u6839\u636e\u8fd9\u4e9b\u989d\u5916\u8d44\u6e90\u662f\u5426\u4f1a\u963b\u585e\u8f6c\u5316\u8fc7\u7a0b\u800c\u51b3\u5b9a\u662f\u5426\u7b49\u5f85\u8d44\u6e90\u52a0\u8f7d\u5b8c\u6bd5</li> </ol> <p><strong>\u6d4f\u89c8\u5668\u5668\u5185\u6838\u62ff\u5230\u5185\u5bb9\u540e\uff0c\u6e32\u67d3\u5927\u6982\u53ef\u4ee5\u5212\u5206\u6210\u4ee5\u4e0b\u51e0\u4e2a\u6b65\u9aa4\uff1a</strong></p> <ol> <li>\u89e3\u6790 <code>html</code> \u5efa\u7acb <code>dom</code> \u6811</li> <li>\u89e3\u6790 <code>css</code> \u6784\u5efa <code>render</code> \u6811\uff08\u5c06 <code>CSS</code> \u4ee3\u7801\u89e3\u6790\u6210\u6811\u5f62\u7684\u6570\u636e\u7ed3\u6784\uff0c\u7136\u540e\u7ed3\u5408 <code>DOM</code> \u5408\u5e76\u6210 <code>render</code> \u6811\uff09</li> <li>\u5e03\u5c40 <code>render</code> \u6811\uff08Layout/reflow\uff09\uff0c\u8d1f\u8d23\u5404\u5143\u7d20\u5c3a\u5bf8\u3001\u4f4d\u7f6e\u7684\u8ba1\u7b97</li> <li>\u7ed8\u5236 <code>render</code> \u6811\uff08paint\uff09\uff0c\u7ed8\u5236\u9875\u9762\u50cf\u7d20\u4fe1\u606f</li> <li>\u6d4f\u89c8\u5668\u4f1a\u5c06\u5404\u5c42\u7684\u4fe1\u606f\u53d1\u9001\u7ed9 <code>GPU</code>\uff0c<code>GPU</code> \u4f1a\u5c06\u5404\u5c42\u5408\u6210\uff08<code>composite</code>\uff09\uff0c\u663e\u793a\u5728\u5c4f\u5e55\u4e0a</li> </ol> <p>\u6240\u6709\u8be6\u7ec6\u6b65\u9aa4\u90fd\u5df2\u7ecf\u7565\u53bb\uff0c\u6e32\u67d3\u5b8c\u6bd5\u540e\u5c31\u662f <code>load</code> \u4e8b\u4ef6\u4e86\uff0c\u4e4b\u540e\u5c31\u662f\u81ea\u5df1\u7684 <code>JS</code> \u903b\u8f91\u5904\u7406\u4e86</p> <p><img src="'+t+'" alt=""></p> <h3 id="41-load-\u4e0e-domcontentloaded">4.1 load \u4e0e DOMContentLoaded</h3> <p>\u4e0a\u9762\u63d0\u5230\uff0c\u6e32\u67d3\u5b8c\u6bd5\u540e\u4f1a\u89e6\u53d1 <code>load</code> \u4e8b\u4ef6\uff0c\u90a3\u4e48\u4f60\u80fd\u5206\u6e05\u695a <code>load</code> \u4e8b\u4ef6\u4e0e <code>DOMContentLoaded</code> \u4e8b\u4ef6\u7684\u5148\u540e\u4e48\uff1f</p> <ul> <li>\u5f53 <code>DOMContentLoaded</code> \u4e8b\u4ef6\u89e6\u53d1\u65f6\uff0c\u4ec5\u5f53 <code>DOM</code> \u52a0\u8f7d\u5b8c\u6210\uff0c\u4e0d\u5305\u62ec\u6837\u5f0f\u8868\uff0c\u56fe\u7247\u3002(\u8b6c\u5982\u5982\u679c\u6709 <code>async</code> \u52a0\u8f7d\u7684\u811a\u672c\u5c31\u4e0d\u4e00\u5b9a\u5b8c\u6210)</li> <li>\u5f53 <code>onload</code> \u4e8b\u4ef6\u89e6\u53d1\u65f6\uff0c\u9875\u9762\u4e0a\u6240\u6709\u7684 <code>DOM</code>\uff0c\u6837\u5f0f\u8868\uff0c\u811a\u672c\uff0c\u56fe\u7247\u90fd\u5df2\u7ecf\u52a0\u8f7d\u5b8c\u6210\u4e86\u3002\uff08\u6e32\u67d3\u5b8c\u6bd5\u4e86\uff09</li> </ul> <blockquote> <p>\u6240\u4ee5\uff0c\u987a\u5e8f\u662f\uff1a<code>DOMContentLoaded -&gt; load</code></p> </blockquote> <h3 id="42-css-\u52a0\u8f7d\u4f1a\u963b\u585e\u6e32\u67d3">4.2 css \u52a0\u8f7d\u4f1a\u963b\u585e\u6e32\u67d3</h3> <p>\u8fd9\u91cc\u8bf4\u7684\u662f\u5934\u90e8\u5f15\u5165 <code>css</code> \u7684\u60c5\u51b5</p> <blockquote> <p>\u9996\u5148\uff0c\u6211\u4eec\u90fd\u77e5\u9053\uff1acss \u662f\u7531\u5355\u72ec\u7684\u4e0b\u8f7d\u7ebf\u7a0b\u5f02\u6b65\u4e0b\u8f7d\u7684</p> </blockquote> <p><strong>\u7136\u540e\u518d\u8bf4\u4e0b\u51e0\u4e2a\u73b0\u8c61\uff1a</strong></p> <ul> <li><code>css</code> \u52a0\u8f7d\u4e0d\u4f1a\u963b\u585e <code>DOM</code> \u6811\u89e3\u6790\uff08\u5f02\u6b65\u52a0\u8f7d\u65f6 <code>DOM</code> \u7167\u5e38\u6784\u5efa\uff09</li> <li>\u4f46\u4f1a\u963b\u585e <code>render</code> \u6811\u6e32\u67d3\uff08\u6e32\u67d3\u65f6\u9700\u7b49 <code>css</code> \u52a0\u8f7d\u5b8c\u6bd5\uff0c\u56e0\u4e3a <code>render</code> \u6811\u9700\u8981 <code>css</code> \u4fe1\u606f\uff09</li> </ul> <p>\u8fd9\u4e5f\u662f\u6d4f\u89c8\u5668\u7684\u4e00\u79cd\u4f18\u5316\u673a\u5236:</p> <p>\u56e0\u4e3a\u4f60\u52a0\u8f7d <code>css</code> \u7684\u65f6\u5019\uff0c\u53ef\u80fd\u4f1a\u4fee\u6539\u4e0b\u9762 <code>DOM</code> \u8282\u70b9\u7684\u6837\u5f0f\uff0c\u5982\u679c <code>css</code> \u52a0\u8f7d\u4e0d\u963b\u585e <code>render</code> \u6811\u6e32\u67d3\u7684\u8bdd\uff0c\u90a3\u4e48\u5f53 <code>css</code> \u52a0\u8f7d\u5b8c\u4e4b\u540e\uff0c<code>render</code> \u6811\u53ef\u80fd\u53c8\u5f97\u91cd\u65b0\u91cd\u7ed8\u6216\u8005\u56de\u6d41\u4e86\uff0c\u8fd9\u5c31\u9020\u6210\u4e86\u4e00\u4e9b\u6ca1\u6709\u5fc5\u8981\u7684\u635f\u8017\u3002\u6240\u4ee5\u5e72\u8106\u5c31\u5148\u628a <code>DOM</code> \u6811\u7684\u7ed3\u6784\u5148\u89e3\u6790\u5b8c\uff0c\u628a\u53ef\u4ee5\u505a\u7684\u5de5\u4f5c\u505a\u5b8c\uff0c\u7136\u540e\u7b49\u4f60 <code>css</code> \u52a0\u8f7d\u5b8c\u4e4b\u540e\uff0c\u5728\u6839\u636e\u6700\u7ec8\u7684\u6837\u5f0f\u6765\u6e32\u67d3 <code>render</code> \u6811\uff0c\u8fd9\u79cd\u505a\u6cd5\u6027\u80fd\u65b9\u9762\u786e\u5b9e\u4f1a\u6bd4\u8f83\u597d\u4e00\u70b9</p> <h3 id="43-\u666e\u901a\u56fe\u5c42\u548c\u590d\u5408\u56fe\u5c42">4.3 \u666e\u901a\u56fe\u5c42\u548c\u590d\u5408\u56fe\u5c42</h3> <p>\u6e32\u67d3\u6b65\u9aa4\u4e2d\u5c31\u63d0\u5230\u4e86 <code>composite</code> \u6982\u5ff5</p> <p>\u53ef\u4ee5\u7b80\u5355\u7684\u8fd9\u6837\u7406\u89e3\uff0c\u6d4f\u89c8\u5668\u6e32\u67d3\u7684\u56fe\u5c42\u4e00\u822c\u5305\u542b\u4e24\u5927\u7c7b\uff1a<code>\u666e\u901a\u56fe\u5c42</code> \u4ee5\u53ca <code>\u590d\u5408\u56fe\u5c42</code></p> <p>\u9996\u5148\uff0c\u666e\u901a\u6587\u6863\u6d41\u5185\u53ef\u4ee5\u7406\u89e3\u4e3a\u4e00\u4e2a\u590d\u5408\u56fe\u5c42\uff08\u8fd9\u91cc\u79f0\u4e3a<code>\u9ed8\u8ba4\u590d\u5408\u5c42</code>\uff0c\u91cc\u9762\u4e0d\u7ba1\u6dfb\u52a0\u591a\u5c11\u5143\u7d20\uff0c\u5176\u5b9e\u90fd\u662f\u5728\u540c\u4e00\u4e2a\u590d\u5408\u56fe\u5c42\u4e2d\uff09</p> <p>\u5176\u6b21\uff0c<code>absolute</code> \u5e03\u5c40\uff08<code>fixed</code> \u4e5f\u4e00\u6837\uff09\uff0c\u867d\u7136\u53ef\u4ee5\u8131\u79bb\u666e\u901a\u6587\u6863\u6d41\uff0c\u4f46\u5b83\u4ecd\u7136\u5c5e\u4e8e\u9ed8\u8ba4\u590d\u5408\u5c42</p> <p>\u7136\u540e\uff0c\u53ef\u4ee5\u901a\u8fc7 <code>\u786c\u4ef6\u52a0\u901f</code> \u7684\u65b9\u5f0f\uff0c\u58f0\u660e\u4e00\u4e2a <code>\u65b0\u7684\u590d\u5408\u56fe\u5c42</code>\uff0c\u5b83\u4f1a\u5355\u72ec\u5206\u914d\u8d44\u6e90\uff08\u5f53\u7136\u4e5f\u4f1a\u8131\u79bb\u666e\u901a\u6587\u6863\u6d41\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u4e0d\u7ba1\u8fd9\u4e2a\u590d\u5408\u56fe\u5c42\u4e2d\u600e\u4e48\u53d8\u5316\uff0c\u4e5f\u4e0d\u4f1a\u5f71\u54cd <code>\u9ed8\u8ba4\u590d\u5408\u5c42</code> \u91cc\u7684\u56de\u6d41\u91cd\u7ed8\uff09</p> <p>\u53ef\u4ee5\u7b80\u5355\u7406\u89e3\u4e0b\uff1a<strong>GPU \u4e2d\uff0c\u5404\u4e2a\u590d\u5408\u56fe\u5c42\u662f\u5355\u72ec\u7ed8\u5236\u7684\uff0c\u6240\u4ee5\u4e92\u4e0d\u5f71\u54cd</strong>\uff0c\u8fd9\u4e5f\u662f\u4e3a\u4ec0\u4e48\u67d0\u4e9b\u573a\u666f\u786c\u4ef6\u52a0\u901f\u6548\u679c\u4e00\u7ea7\u68d2</p> <p><strong>\u5982\u4f55\u53d8\u6210\u590d\u5408\u56fe\u5c42\uff08\u786c\u4ef6\u52a0\u901f\uff09:</strong></p> <p>\u5c06\u8be5\u5143\u7d20\u53d8\u6210\u4e00\u4e2a\u590d\u5408\u56fe\u5c42\uff0c\u5c31\u662f\u4f20\u8bf4\u4e2d\u7684\u786c\u4ef6\u52a0\u901f\u6280\u672f</p> <ul> <li>\u6700\u5e38\u7528\u7684\u65b9\u5f0f\uff1a<code>translate3d</code>\u3001<code>translateZ</code></li> <li><code>opacity</code> \u5c5e\u6027/\u8fc7\u6e21\u52a8\u753b\uff08\u9700\u8981\u52a8\u753b\u6267\u884c\u7684\u8fc7\u7a0b\u4e2d\u624d\u4f1a\u521b\u5efa\u5408\u6210\u5c42\uff0c\u52a8\u753b\u6ca1\u6709\u5f00\u59cb\u6216\u7ed3\u675f\u540e\u5143\u7d20\u8fd8\u4f1a\u56de\u5230\u4e4b\u524d\u7684\u72b6\u6001\uff09</li> <li><code>will-chang</code> \u5c5e\u6027\uff08\u8fd9\u4e2a\u6bd4\u8f83\u504f\u50fb\uff09\uff0c\u4e00\u822c\u914d\u5408 <code>opacity</code> \u4e0e <code>translate</code> \u4f7f\u7528\uff08\u800c\u4e14\u7ecf\u6d4b\u8bd5\uff0c\u9664\u4e86\u4e0a\u8ff0\u53ef\u4ee5\u5f15\u53d1\u786c\u4ef6\u52a0\u901f\u7684\u5c5e\u6027\u5916\uff0c\u5176\u5b83\u5c5e\u6027\u5e76\u4e0d\u4f1a\u53d8\u6210\u590d\u5408\u5c42\uff09</li> <li><code>&lt;video&gt;</code> <code>&lt;iframe&gt;</code> <code>&lt;canvas&gt;</code> <code>&lt;webgl&gt;</code> \u7b49\u5143\u7d20</li> </ul> <p><strong>absolute \u548c\u786c\u4ef6\u52a0\u901f\u7684\u533a\u522b\uff1a</strong></p> <p>\u53ef\u4ee5\u770b\u5230\uff0c<code>absolute</code> \u867d\u7136\u53ef\u4ee5\u8131\u79bb\u666e\u901a\u6587\u6863\u6d41\uff0c\u4f46\u662f\u65e0\u6cd5\u8131\u79bb\u9ed8\u8ba4\u590d\u5408\u5c42\u3002\u6240\u4ee5\uff0c\u5c31\u7b97 <code>absolute</code> \u4e2d\u4fe1\u606f\u6539\u53d8\u65f6\u4e0d\u4f1a\u6539\u53d8\u666e\u901a\u6587\u6863\u6d41\u4e2d render \u6811\uff0c\u4f46\u662f\uff0c\u6d4f\u89c8\u5668\u6700\u7ec8\u7ed8\u5236\u65f6\uff0c\u662f\u6574\u4e2a\u590d\u5408\u5c42\u7ed8\u5236\u7684\uff0c\u6240\u4ee5 <code>absolute</code> \u4e2d\u4fe1\u606f\u7684\u6539\u53d8\uff0c\u4ecd\u7136\u4f1a\u5f71\u54cd\u6574\u4e2a\u590d\u5408\u5c42\u7684\u7ed8\u5236\u3002\uff08\u6d4f\u89c8\u5668\u4f1a\u91cd\u7ed8\u5b83\uff0c\u5982\u679c\u590d\u5408\u5c42\u4e2d\u5185\u5bb9\u591a\uff0c<code>absolute</code> \u5e26\u6765\u7684\u7ed8\u5236\u4fe1\u606f\u53d8\u5316\u8fc7\u5927\uff0c\u8d44\u6e90\u6d88\u8017\u662f\u975e\u5e38\u4e25\u91cd\u7684\uff09</p> <p>\u800c\u786c\u4ef6\u52a0\u901f\u76f4\u63a5\u5c31\u662f\u5728\u53e6\u4e00\u4e2a\u590d\u5408\u5c42\u4e86\uff08\u53e6\u8d77\u7089\u7076\uff09\uff0c\u6240\u4ee5\u5b83\u7684\u4fe1\u606f\u6539\u53d8\u4e0d\u4f1a\u5f71\u54cd\u9ed8\u8ba4\u590d\u5408\u5c42\uff08\u5f53\u7136\u4e86\uff0c\u5185\u90e8\u80af\u5b9a\u4f1a\u5f71\u54cd\u5c5e\u4e8e\u81ea\u5df1\u7684\u590d\u5408\u5c42\uff09\uff0c\u4ec5\u4ec5\u662f\u5f15\u53d1\u6700\u540e\u7684\u5408\u6210\uff08\u8f93\u51fa\u89c6\u56fe\uff09</p> <p><strong>\u590d\u5408\u56fe\u5c42\u7684\u4f5c\u7528\uff1a</strong></p> <p>\u4e00\u822c\u4e00\u4e2a\u5143\u7d20\u5f00\u542f\u786c\u4ef6\u52a0\u901f\u540e\u4f1a\u53d8\u6210\u590d\u5408\u56fe\u5c42\uff0c\u53ef\u4ee5\u72ec\u7acb\u4e8e\u666e\u901a\u6587\u6863\u6d41\u4e2d\uff0c\u6539\u52a8\u540e\u53ef\u4ee5\u907f\u514d\u6574\u4e2a\u9875\u9762\u91cd\u7ed8\uff0c\u63d0\u5347\u6027\u80fd</p> <blockquote> <p>\u4f46\u662f\u5c3d\u91cf\u4e0d\u8981\u5927\u91cf\u4f7f\u7528\u590d\u5408\u56fe\u5c42\uff0c\u5426\u5219\u7531\u4e8e\u8d44\u6e90\u6d88\u8017\u8fc7\u5ea6\uff0c\u9875\u9762\u53cd\u800c\u4f1a\u53d8\u7684\u66f4\u5361</p> </blockquote> <p><strong>\u786c\u4ef6\u52a0\u901f\u65f6\u8bf7\u4f7f\u7528 index\uff1a</strong></p> <p>\u4f7f\u7528\u786c\u4ef6\u52a0\u901f\u65f6\uff0c\u5c3d\u53ef\u80fd\u7684\u4f7f\u7528 <code>index</code>\uff0c\u9632\u6b62\u6d4f\u89c8\u5668\u9ed8\u8ba4\u7ed9\u540e\u7eed\u7684\u5143\u7d20\u521b\u5efa\u590d\u5408\u5c42\u6e32\u67d3</p> <p>\u5177\u4f53\u7684\u539f\u7406\u65f6\u8fd9\u6837\u7684\uff1a<code>webkit CSS3</code> \u4e2d\uff0c\u5982\u679c\u8fd9\u4e2a\u5143\u7d20\u6dfb\u52a0\u4e86\u786c\u4ef6\u52a0\u901f\uff0c\u5e76\u4e14 <code>index</code> \u5c42\u7ea7\u6bd4\u8f83\u4f4e\uff0c\u90a3\u4e48\u5728\u8fd9\u4e2a\u5143\u7d20\u7684\u540e\u9762\u5176\u5b83\u5143\u7d20\uff08\u5c42\u7ea7\u6bd4\u8fd9\u4e2a\u5143\u7d20\u9ad8\u7684\uff0c\u6216\u8005\u76f8\u540c\u7684\uff0c\u5e76\u4e14 <code>releative</code> \u6216 <code>absolute</code> \u5c5e\u6027\u76f8\u540c\u7684\uff09\uff0c \u4f1a\u9ed8\u8ba4\u53d8\u4e3a\u590d\u5408\u5c42\u6e32\u67d3\uff0c\u5982\u679c\u5904\u7406\u4e0d\u5f53\u4f1a\u6781\u5927\u7684\u5f71\u54cd\u6027\u80fd</p> <p>\u7b80\u5355\u70b9\u7406\u89e3\uff0c\u5176\u5b9e\u53ef\u4ee5\u8ba4\u4e3a\u662f\u4e00\u4e2a\u9690\u5f0f\u5408\u6210\u7684\u6982\u5ff5\uff1a<strong>\u5982\u679c <code>a</code> \u662f\u4e00\u4e2a\u590d\u5408\u56fe\u5c42\uff0c\u800c\u4e14 <code>b</code> \u5728 <code>a</code> \u4e0a\u9762\uff0c\u90a3\u4e48 <code>b</code> \u4e5f\u4f1a\u88ab\u9690\u5f0f\u8f6c\u4e3a\u4e00\u4e2a\u590d\u5408\u56fe\u5c42</strong>\uff0c\u8fd9\u70b9\u9700\u8981\u7279\u522b\u6ce8\u610f</p> <h2 id="5-\u4ece-event-loop-\u8c08-js-\u7684\u8fd0\u884c\u673a\u5236">5 \u4ece Event Loop \u8c08 JS \u7684\u8fd0\u884c\u673a\u5236</h2> <p>\u6ce8\u610f\uff0c\u8fd9\u91cc\u4e0d\u8c08 <strong>\u53ef\u6267\u884c\u4e0a\u4e0b\u6587</strong>\uff0c<strong>VO</strong>\uff0c<strong>scop chain</strong> \u7b49\u6982\u5ff5\uff0c\u8fd9\u91cc\u4e3b\u8981\u662f\u7ed3\u5408 Event Loop \u6765\u8c08 JS \u4ee3\u7801\u662f\u5982\u4f55\u6267\u884c\u7684</p> <p><strong>\u540c\u6b65\u4efb\u52a1\u548c\u5f02\u6b65\u4efb\u52a1\uff1a</strong></p> <ol> <li>JS \u5206\u4e3a\u540c\u6b65\u4efb\u52a1\u548c\u5f02\u6b65\u4efb\u52a1</li> <li>\u540c\u6b65\u4efb\u52a1\u90fd\u5728\u4e3b\u7ebf\u7a0b\u4e0a\u6267\u884c\uff0c\u5f62\u6210\u4e00\u4e2a\u6267\u884c\u6808</li> <li>\u4e3b\u7ebf\u7a0b\u4e4b\u5916\uff0c\u4e8b\u4ef6\u89e6\u53d1\u7ebf\u7a0b\u7ba1\u7406\u7740\u4e00\u4e2a\u4efb\u52a1\u961f\u5217\uff0c\u53ea\u8981\u5f02\u6b65\u4efb\u52a1\u6709\u4e86\u8fd0\u884c\u7ed3\u679c\uff0c\u5c31\u5728\u4efb\u52a1\u961f\u5217\u4e4b\u4e2d\u653e\u7f6e\u4e00\u4e2a\u4e8b\u4ef6</li> <li>\u4e00\u65e6\u6267\u884c\u6808\u4e2d\u7684\u6240\u6709\u540c\u6b65\u4efb\u52a1\u6267\u884c\u5b8c\u6bd5\uff08\u6b64\u65f6 <code>JS</code> \u5f15\u64ce\u7a7a\u95f2\uff09\uff0c\u7cfb\u7edf\u5c31\u4f1a\u8bfb\u53d6\u4efb\u52a1\u961f\u5217\uff0c\u5c06\u53ef\u8fd0\u884c\u7684\u5f02\u6b65\u4efb\u52a1\u6dfb\u52a0\u5230\u53ef\u6267\u884c\u6808\u4e2d\uff0c\u5f00\u59cb\u6267\u884c</li> </ol> <blockquote> <p>\u770b\u5230\u8fd9\u91cc\uff0c\u5e94\u8be5\u5c31\u53ef\u4ee5\u7406\u89e3\u4e86\uff1a\u4e3a\u4ec0\u4e48\u6709\u65f6\u5019 setTimeout \u63a8\u5165\u7684\u4e8b\u4ef6\u4e0d\u80fd\u51c6\u65f6\u6267\u884c\uff1f\u56e0\u4e3a\u53ef\u80fd\u5728\u5b83\u63a8\u5165\u5230\u4e8b\u4ef6\u5217\u8868\u65f6\uff0c\u4e3b\u7ebf\u7a0b\u8fd8\u4e0d\u7a7a\u95f2\uff0c\u6b63\u5728\u6267\u884c\u5176\u5b83\u4ee3\u7801\uff0c\u6240\u4ee5\u81ea\u7136\u6709\u8bef\u5dee</p> </blockquote> <h3 id="51-\u4e8b\u4ef6\u5faa\u73af\u673a\u5236\u8fdb\u4e00\u6b65\u8865\u5145">5.1 \u4e8b\u4ef6\u5faa\u73af\u673a\u5236\u8fdb\u4e00\u6b65\u8865\u5145</h3> <p>\u5f15\u7528\u4e00\u5f20\u56fe\u7247\u6765\u534f\u52a9\u7406\u89e3\uff1a\uff08\u53c2\u8003\u81ea Philip Roberts \u7684\u6f14\u8bb2\u300aHelp, I&#39;m stuck in an event-loop\u300b\uff09</p> <p><img src="'+r+'" alt=""></p> <p><strong>\u4e0a\u56fe\u5927\u81f4\u63cf\u8ff0\u5c31\u662f\uff1a</strong></p> <ol> <li>\u4e3b\u7ebf\u7a0b\u8fd0\u884c\u65f6\u4f1a\u4ea7\u751f\u6267\u884c\u6808</li> <li>\u6808\u4e2d\u7684\u4ee3\u7801\u8c03\u7528\u67d0\u4e9b <code>api</code> \u65f6\uff0c\u5b83\u4eec\u4f1a\u5728\u4e8b\u4ef6\u961f\u5217\u4e2d\u6dfb\u52a0\u5404\u79cd\u4e8b\u4ef6\uff08\u5f53\u6ee1\u8db3\u89e6\u53d1\u6761\u4ef6\u540e\uff0c\u5982 <code>ajax</code> \u8bf7\u6c42\u5b8c\u6bd5\uff09</li> <li>\u800c\u6808\u4e2d\u7684\u4ee3\u7801\u6267\u884c\u5b8c\u6bd5\uff0c\u5c31\u4f1a\u8bfb\u53d6\u4e8b\u4ef6\u961f\u5217\u4e2d\u7684\u4e8b\u4ef6\uff0c\u53bb\u6267\u884c\u90a3\u4e9b\u56de\u8c03</li> <li>\u5982\u6b64\u5faa\u73af</li> <li>\u6ce8\u610f\uff0c\u603b\u662f\u8981\u7b49\u5f85\u6808\u4e2d\u7684\u4ee3\u7801\u6267\u884c\u5b8c\u6bd5\u540e\u624d\u4f1a\u53bb\u8bfb\u53d6\u4e8b\u4ef6\u961f\u5217\u4e2d\u7684\u4e8b\u4ef6</li> </ol> <h3 id="52-\u5b9a\u65f6\u5668">5.2 \u5b9a\u65f6\u5668</h3> <p><strong>\u4e0a\u8ff0\u4e8b\u4ef6\u5faa\u73af\u673a\u5236\u7684\u6838\u5fc3\u662f\uff1a</strong>JS \u5f15\u64ce\u7ebf\u7a0b\u548c\u4e8b\u4ef6\u89e6\u53d1\u7ebf\u7a0b</p> <p>\u4f46\u4e8b\u4ef6\u4e0a\uff0c\u91cc\u9762\u8fd8\u6709\u4e00\u4e9b\u9690\u85cf\u7ec6\u8282\uff0c\u8b6c\u5982\u8c03\u7528 <code>setTimeout</code> \u540e\uff0c\u662f\u5982\u4f55\u7b49\u5f85\u7279\u5b9a\u65f6\u95f4\u540e\u624d\u6dfb\u52a0\u5230\u4e8b\u4ef6\u961f\u5217\u4e2d\u7684\uff1f</p> <p>\u662f <code>JS</code> \u5f15\u64ce\u68c0\u6d4b\u7684\u4e48\uff1f\u5f53\u7136\u4e0d\u662f\u4e86\u3002\u5b83\u662f\u7531\u5b9a\u65f6\u5668\u7ebf\u7a0b\u63a7\u5236\uff08\u56e0\u4e3a <code>JS</code> \u5f15\u64ce\u81ea\u5df1\u90fd\u5fd9\u4e0d\u8fc7\u6765\uff0c\u6839\u672c\u65e0\u6687\u5206\u8eab\uff09</p> <p>\u4e3a\u4ec0\u4e48\u8981\u5355\u72ec\u7684\u5b9a\u65f6\u5668\u7ebf\u7a0b\uff1f\u56e0\u4e3a <code>JavaScript</code> \u5f15\u64ce\u662f\u5355\u7ebf\u7a0b\u7684, \u5982\u679c\u5904\u4e8e\u963b\u585e\u7ebf\u7a0b\u72b6\u6001\u5c31\u4f1a\u5f71\u54cd\u8bb0\u8ba1\u65f6\u7684\u51c6\u786e\uff0c\u56e0\u6b64\u5f88\u6709\u5fc5\u8981\u5355\u72ec\u5f00\u4e00\u4e2a\u7ebf\u7a0b\u7528\u6765\u8ba1\u65f6</p> <p>\u4ec0\u4e48\u65f6\u5019\u4f1a\u7528\u5230\u5b9a\u65f6\u5668\u7ebf\u7a0b\uff1f\u5f53\u4f7f\u7528 <code>setTimeout</code> \u6216 <code>setInterval</code> \u65f6\uff0c\u5b83\u9700\u8981\u5b9a\u65f6\u5668\u7ebf\u7a0b\u8ba1\u65f6\uff0c\u8ba1\u65f6\u5b8c\u6210\u540e\u5c31\u4f1a\u5c06\u7279\u5b9a\u7684\u4e8b\u4ef6\u63a8\u5165\u4e8b\u4ef6\u961f\u5217\u4e2d</p> <p>\u8b6c\u5982:</p> <pre><code class="language-js">setTimeout(function() {\n  console.log(&#39;hello!&#39;);\n}, 1000);</code></pre> <p>\u8fd9\u6bb5\u4ee3\u7801\u7684\u4f5c\u7528\u662f\u5f53 <code>1000</code> \u6beb\u79d2\u8ba1\u65f6\u5b8c\u6bd5\u540e\uff08\u7531\u5b9a\u65f6\u5668\u7ebf\u7a0b\u8ba1\u65f6\uff09\uff0c\u5c06\u56de\u8c03\u51fd\u6570\u63a8\u5165\u4e8b\u4ef6\u961f\u5217\u4e2d\uff0c\u7b49\u5f85\u4e3b\u7ebf\u7a0b\u6267\u884c</p> <pre><code class="language-js">setTimeout(function() {\n  console.log(&#39;hello!&#39;);\n}, 0);\n\nconsole.log(&#39;begin&#39;);</code></pre> <p>\u8fd9\u6bb5\u4ee3\u7801\u7684\u6548\u679c\u662f\u6700\u5feb\u7684\u65f6\u95f4\u5185\u5c06\u56de\u8c03\u51fd\u6570\u63a8\u5165\u4e8b\u4ef6\u961f\u5217\u4e2d\uff0c\u7b49\u5f85\u4e3b\u7ebf\u7a0b\u6267\u884c</p> <p><strong>\u6ce8\u610f\uff1a</strong></p> <ul> <li>\u6267\u884c\u7ed3\u679c\u662f\uff1a\u5148 <code>begin</code> \u540e <code>hello!</code></li> <li>\u867d\u7136\u4ee3\u7801\u7684\u672c\u610f\u662f <code>0</code> \u6beb\u79d2\u540e\u5c31\u63a8\u5165\u4e8b\u4ef6\u961f\u5217\uff0c\u4f46\u662f <code>W3C</code> \u5728 <code>HTML</code> \u6807\u51c6\u4e2d\u89c4\u5b9a\uff0c\u89c4\u5b9a\u8981\u6c42 <code>setTimeout</code> \u4e2d\u4f4e\u4e8e <code>4ms</code> \u7684\u65f6\u95f4\u95f4\u9694\u7b97\u4e3a <code>4ms</code></li> </ul> <h3 id="53-settimeout-\u800c\u4e0d\u662f-setinterval">5.3 setTimeout \u800c\u4e0d\u662f setInterval</h3> <blockquote> <p>\u7528 <code>setTimeout</code> \u6a21\u62df\u5b9a\u671f\u8ba1\u65f6\u548c\u76f4\u63a5\u7528 <code>setInterval</code> \u662f\u6709\u533a\u522b\u7684</p> </blockquote> <p>\u56e0\u4e3a\u6bcf\u6b21 <code>setTimeout</code> \u8ba1\u65f6\u5230\u540e\u5c31\u4f1a\u53bb\u6267\u884c\uff0c\u7136\u540e\u6267\u884c\u4e00\u6bb5\u65f6\u95f4\u540e\u624d\u4f1a\u7ee7\u7eed <code>setTimeout</code>\uff0c\u4e2d\u95f4\u5c31\u591a\u4e86\u8bef\u5dee \uff08\u8bef\u5dee\u591a\u5c11\u4e0e\u4ee3\u7801\u6267\u884c\u65f6\u95f4\u6709\u5173\uff09</p> <p>\u800c <code>setInterval</code> \u5219\u662f\u6bcf\u6b21\u90fd\u7cbe\u786e\u7684\u9694\u4e00\u6bb5\u65f6\u95f4\u63a8\u5165\u4e00\u4e2a\u4e8b\u4ef6\uff08\u4f46\u662f\uff0c\u4e8b\u4ef6\u7684\u5b9e\u9645\u6267\u884c\u65f6\u95f4\u4e0d\u4e00\u5b9a\u5c31\u51c6\u786e\uff0c\u8fd8\u6709\u53ef\u80fd\u662f\u8fd9\u4e2a\u4e8b\u4ef6\u8fd8\u6ca1\u6267\u884c\u5b8c\u6bd5\uff0c\u4e0b\u4e00\u4e2a\u4e8b\u4ef6\u5c31\u6765\u4e86\uff09</p> <p><strong><code>setInterval</code> \u6709\u4e00\u4e9b\u6bd4\u8f83\u81f4\u547d\u7684\u95ee\u9898\u5c31\u662f\uff1a</strong></p> <ul> <li>\u7d2f\u8ba1\u6548\u5e94\uff08\u4e0a\u9762\u63d0\u5230\u7684\uff09\uff0c\u5982\u679c <code>setInterval</code> \u4ee3\u7801\u5728\uff08setInterval\uff09\u518d\u6b21\u6dfb\u52a0\u5230\u961f\u5217\u4e4b\u524d\u8fd8\u6ca1\u6709\u5b8c\u6210\u6267\u884c\uff0c</li> </ul> <p>\u5c31\u4f1a\u5bfc\u81f4\u5b9a\u65f6\u5668\u4ee3\u7801\u8fde\u7eed\u8fd0\u884c\u597d\u51e0\u6b21\uff0c\u800c\u4e4b\u95f4\u6ca1\u6709\u95f4\u9694\u3002\u5c31\u7b97\u6b63\u5e38\u95f4\u9694\u6267\u884c\uff0c\u591a\u4e2a <code>setInterval</code> \u7684\u4ee3\u7801\u6267\u884c\u65f6\u95f4\u53ef\u80fd\u4f1a\u6bd4\u9884\u671f\u5c0f\uff08\u56e0\u4e3a\u4ee3\u7801\u6267\u884c\u9700\u8981\u4e00\u5b9a\u65f6\u95f4\uff09</p> <p><strong>\u76ee\u524d\u4e00\u822c\u8ba4\u4e3a\u7684\u6700\u4f73\u65b9\u6848\u662f\uff1a</strong> \u7528 <code>setTimeout</code> \u6a21\u62df <code>setInterval</code>\uff0c\u6216\u8005\u7279\u6b8a\u573a\u5408\u76f4\u63a5\u7528 <code>requestAnimationFrame</code></p> <h2 id="6-\u5b8f\u4efb\u52a1\u4e0e\u5fae\u4efb\u52a1">6 \u5b8f\u4efb\u52a1\u4e0e\u5fae\u4efb\u52a1</h2> <p>\u4e0a\u6587\u4e2d\u5c06 JS \u4e8b\u4ef6\u5faa\u73af\u673a\u5236\u68b3\u7406\u4e86\u4e00\u904d\uff0c\u5728 ES5 \u7684\u60c5\u51b5\u662f\u591f\u7528\u4e86\uff0c\u4f46\u662f\u5728 ES6 \u76db\u884c\u7684\u73b0\u5728\uff0c\u4ecd\u7136\u4f1a\u9047\u5230\u4e00\u4e9b\u95ee\u9898\uff0c\u8b6c\u5982\u4e0b\u9762\u8fd9\u9898\uff1a</p> <pre><code class="language-js">console.log(&#39;script start&#39;);\n\nsetTimeout(function() {\n  console.log(&#39;setTimeout&#39;);\n}, 0);\n\nPromise.resolve()\n  .then(function() {\n    console.log(&#39;promise1&#39;);\n  })\n  .then(function() {\n    console.log(&#39;promise2&#39;);\n  });\n\nconsole.log(&#39;script end&#39;);</code></pre> <p>\u5b83\u7684\u6b63\u786e\u6267\u884c\u987a\u5e8f\u662f\u8fd9\u6837\u5b50\u7684\uff1a</p> <pre><code>script start\nscript end\npromise1\npromise2\nsetTimeout</code></pre><p>\u4e3a\u4ec0\u4e48\u5462\uff1f\u56e0\u4e3a <code>Promise</code> \u91cc\u6709\u4e86\u4e00\u4e2a\u4e00\u4e2a\u65b0\u7684\u6982\u5ff5\uff1a<code>microtask</code></p> <p>\u6216\u8005\uff0c\u8fdb\u4e00\u6b65\uff0c<code>JS</code> \u4e2d\u5206\u4e3a\u4e24\u79cd\u4efb\u52a1\u7c7b\u578b\uff1a<code>macrotask</code> \u548c <code>microtask</code>\uff0c\u5728 <code>ECMAScript</code> \u4e2d\uff0c<code>microtask</code> \u79f0\u4e3a <code>jobs</code>\uff0c<code>macrotask</code> \u53ef\u79f0\u4e3a <code>task</code></p> <p><strong>\u5b83\u4eec\u7684\u5b9a\u4e49\u548c\u533a\u522b\uff0c\u7b80\u5355\u70b9\u53ef\u4ee5\u6309\u5982\u4e0b\u7406\u89e3\uff1a</strong></p> <ul> <li><code>macrotask</code>\uff08\u53c8\u79f0\u4e4b\u4e3a\u5b8f\u4efb\u52a1\uff09\uff0c\u53ef\u4ee5\u7406\u89e3\u662f\u6bcf\u6b21\u6267\u884c\u6808\u6267\u884c\u7684\u4ee3\u7801\u5c31\u662f\u4e00\u4e2a\u5b8f\u4efb\u52a1\uff08\u5305\u62ec\u6bcf\u6b21\u4ece\u4e8b\u4ef6\u961f\u5217\u4e2d\u83b7\u53d6\u4e00\u4e2a\u4e8b\u4ef6\u56de\u8c03\u5e76\u653e\u5230\u6267\u884c\u6808\u4e2d\u6267\u884c\uff09<ol> <li>\u6bcf\u4e00\u4e2a <code>task</code> \u4f1a\u4ece\u5934\u5230\u5c3e\u5c06\u8fd9\u4e2a\u4efb\u52a1\u6267\u884c\u5b8c\u6bd5\uff0c\u4e0d\u4f1a\u6267\u884c\u5176\u5b83</li> <li>\u6d4f\u89c8\u5668\u4e3a\u4e86\u80fd\u591f\u4f7f\u5f97 <code>JS</code> \u5185\u90e8 <code>task</code> \u4e0e <code>DOM</code> \u4efb\u52a1\u80fd\u591f\u6709\u5e8f\u7684\u6267\u884c\uff0c\u4f1a\u5728\u4e00\u4e2a <code>task</code> \u6267\u884c\u7ed3\u675f\u540e\uff0c\u5728\u4e0b\u4e00\u4e2a <code>task</code> \u6267\u884c\u5f00\u59cb\u524d\uff0c\u5bf9\u9875\u9762\u8fdb\u884c\u91cd\u65b0\u6e32\u67d3</li> </ol> </li> <li><code>microtask</code>\uff08\u53c8\u79f0\u4e3a\u5fae\u4efb\u52a1\uff09\uff0c\u53ef\u4ee5\u7406\u89e3\u662f\u5728\u5f53\u524d <code>task</code> \u6267\u884c\u7ed3\u675f\u540e\u7acb\u5373\u6267\u884c\u7684\u4efb\u52a1<ol> <li>\u4e5f\u5c31\u662f\u8bf4\uff0c\u5728\u5f53\u524d <code>task</code> \u4efb\u52a1\u540e\uff0c\u4e0b\u4e00\u4e2a <code>task</code> \u4e4b\u524d\uff0c\u5728\u6e32\u67d3\u4e4b\u524d\uff0c\u4f1a\u6267\u884c <strong>\u6240\u6709\u7684</strong> \u5fae\u4efb\u52a1</li> <li>\u6240\u4ee5\u5b83\u7684\u54cd\u5e94\u901f\u5ea6\u76f8\u6bd4 <code>setTimeout</code>\uff08<code>setTimeout</code> \u662f <code>task</code>\uff09\u4f1a\u66f4\u5feb\uff0c\u56e0\u4e3a\u65e0\u9700\u7b49\u6e32\u67d3</li> </ol> </li> </ul> <blockquote> <p>\u4e5f\u5c31\u662f\u8bf4\uff0c\u5728\u67d0\u4e00\u4e2a <code>macrotask</code> \u6267\u884c\u5b8c\u540e\uff0c\u5c31\u4f1a\u5c06\u5728\u5b83\u6267\u884c\u671f\u95f4\u4ea7\u751f\u7684\u6240\u6709 microtask \u90fd\u6267\u884c\u5b8c\u6bd5\uff08\u5728\u6e32\u67d3\u524d\uff09</p> </blockquote> <p><strong>\u5206\u522b\u5f88\u4e48\u6837\u7684\u573a\u666f\u4f1a\u5f62\u6210 <code>macrotask</code> \u548c <code>microtask</code>\uff1a</strong></p> <ul> <li><code>macrotask</code>\uff1a\u4e3b\u4ee3\u7801\u5757\uff0c<code>setTimeout</code>\uff0c<code>setInterval</code> \u7b49\uff08\u53ef\u4ee5\u770b\u5230\uff0c\u4e8b\u4ef6\u961f\u5217\u4e2d\u7684\u6bcf\u4e00\u4e2a\u4e8b\u4ef6\u90fd\u662f\u4e00\u4e2a <code>macrotask</code>\uff09</li> <li><code>microtask</code>\uff1a<code>Promise</code>\uff0c<code>process.nextTick</code> \u7b49</li> </ul> <p><strong>\u518d\u6839\u636e\u7ebf\u7a0b\u6765\u7406\u89e3\u4e0b\uff1a</strong></p> <ul> <li><code>macrotask</code> \u4e2d\u7684\u4e8b\u4ef6\u90fd\u662f\u653e\u5728\u4e00\u4e2a\u4e8b\u4ef6\u961f\u5217\u4e2d\u7684\uff0c\u800c\u8fd9\u4e2a\u961f\u5217\u7531 <strong>\u4e8b\u4ef6\u89e6\u53d1\u7ebf\u7a0b\u7ef4\u62a4</strong></li> <li><code>microtask</code> \u4e2d\u7684\u6240\u6709\u5fae\u4efb\u52a1\u90fd\u662f\u6dfb\u52a0\u5230\u5fae\u4efb\u52a1\u961f\u5217\uff08Job Queues\uff09\u4e2d\uff0c\u7b49\u5f85\u5f53\u524d <code>macrotask</code> \u6267\u884c\u5b8c\u6bd5\u540e\u6267\u884c\uff0c\u800c\u8fd9\u4e2a\u961f\u5217\u7531 <strong>JS \u5f15\u64ce\u7ebf\u7a0b\u7ef4\u62a4</strong></li> </ul> <p>\uff08\u8fd9\u70b9\u7531\u81ea\u5df1\u7406\u89e3+\u63a8\u6d4b\u5f97\u51fa\uff0c\u56e0\u4e3a\u5b83\u662f\u5728\u4e3b\u7ebf\u7a0b\u4e0b\u65e0\u7f1d\u6267\u884c\u7684\uff09</p> <p><strong>\u6240\u4ee5\uff0c\u603b\u7ed3\u4e0b\u8fd0\u884c\u673a\u5236\uff1a</strong></p> <ol> <li>\u6267\u884c\u4e00\u4e2a\u5b8f\u4efb\u52a1\uff08\u6808\u4e2d\u6ca1\u6709\u5c31\u4ece\u4e8b\u4ef6\u961f\u5217\u4e2d\u83b7\u53d6\uff09</li> <li>\u6267\u884c\u8fc7\u7a0b\u4e2d\u5982\u679c\u9047\u5230\u5fae\u4efb\u52a1\uff0c\u5c31\u5c06\u5b83\u6dfb\u52a0\u5230\u5fae\u4efb\u52a1\u7684\u4efb\u52a1\u961f\u5217\u4e2d</li> <li>\u5b8f\u4efb\u52a1\u6267\u884c\u5b8c\u6bd5\u540e\uff0c\u7acb\u5373\u6267\u884c\u5f53\u524d\u5fae\u4efb\u52a1\u961f\u5217\u4e2d\u7684\u6240\u6709\u5fae\u4efb\u52a1\uff08\u4f9d\u6b21\u6267\u884c\uff09</li> <li>\u5f53\u524d\u5b8f\u4efb\u52a1\u6267\u884c\u5b8c\u6bd5\uff0c\u5f00\u59cb\u68c0\u67e5\u6e32\u67d3\uff0c\u7136\u540e <code>GUI</code> \u7ebf\u7a0b\u63a5\u7ba1\u6e32\u67d3</li> <li>\u6e32\u67d3\u5b8c\u6bd5\u540e\uff0c<code>JS</code> \u7ebf\u7a0b\u7ee7\u7eed\u63a5\u7ba1\uff0c\u5f00\u59cb\u4e0b\u4e00\u4e2a\u5b8f\u4efb\u52a1\uff08\u4ece\u4e8b\u4ef6\u961f\u5217\u4e2d\u83b7\u53d6\uff09</li> </ol> <p><strong>\u53e6\u5916\uff0c\u8bf7\u6ce8\u610f\u4e0b Promise \u7684 polyfill \u4e0e\u5b98\u65b9\u7248\u672c\u7684\u533a\u522b\uff1a</strong></p> <ul> <li>\u5b98\u65b9\u7248\u672c\u4e2d\uff0c\u662f\u6807\u51c6\u7684 <code>microtask</code> \u5f62\u5f0f</li> <li><code>polyfill</code>\uff0c\u4e00\u822c\u90fd\u662f\u901a\u8fc7 <code>setTimeout</code> \u6a21\u62df\u7684\uff0c\u6240\u4ee5\u662f <code>macrotask</code> \u5f62\u5f0f</li> </ul> <p><strong>MutationObserver \u53ef\u4ee5\u7528\u6765\u5b9e\u73b0 microtask\uff1a</strong></p> <p>\uff08\u5b83\u5c5e\u4e8e <code>microtask</code>\uff0c\u4f18\u5148\u7ea7\u5c0f\u4e8e <code>Promise</code>\uff0c\u4e00\u822c\u662f <code>Promise</code> \u4e0d\u652f\u6301\u65f6\u624d\u4f1a\u8fd9\u6837\u505a\uff09</p> <p>\u5b83\u662f <code>HTML5</code> \u4e2d\u7684\u65b0\u7279\u6027\uff0c\u4f5c\u7528\u662f\uff1a\u76d1\u542c\u4e00\u4e2a <code>DOM</code> \u53d8\u52a8\uff0c\u5f53 <code>DOM</code> \u5bf9\u8c61\u6811\u53d1\u751f\u4efb\u4f55\u53d8\u52a8\u65f6\uff0c<code>Mutation Observer</code> \u4f1a\u5f97\u5230\u901a\u77e5</p> ';o.exports=p}}]);