(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["docs/browser/document"],{WrMY:function(e,o,t){var d=t("Rxnk"),c=t("00yk"),l=t("pv9x"),i=t("5nkk"),n=t("t+I5"),p=t("OpqV"),r=t("zSwZ"),s=t("1QTm"),a=t("7YbX"),u=d(c),g=d(l),m=d(i),h=d(n),b=d(p),M=d(r),q=d(s),f=d(a),y='<h1 id="document">Document</h1> <h2 id="1-\u6d4f\u89c8\u5668\u73af\u5883">1 \u6d4f\u89c8\u5668\u73af\u5883</h2> <p>JavaScript \u5728\u6d4f\u89c8\u5668\u4e2d\u8fd0\u884c\u65f6\u7684\u73af\u5883\uff1a</p> <p><img src="'+u+'" alt=""></p> <h3 id="11-\u6587\u6863\u5bf9\u8c61\u6a21\u578b\uff08dom\uff09">1.1 \u6587\u6863\u5bf9\u8c61\u6a21\u578b\uff08DOM\uff09</h3> <p>\u6587\u6863\u5bf9\u8c61\u6a21\u578b\uff08Document Object Model\uff09\uff0c\u7b80\u79f0 DOM\uff0c\u5c06\u6240\u6709\u9875\u9762\u5185\u5bb9\u8868\u793a\u4e3a\u53ef\u4ee5\u4fee\u6539\u7684\u5bf9\u8c61</p> <p><code>document</code> \u5bf9\u8c61\u662f\u9875\u9762\u7684\u4e3b\u8981\u201c\u5165\u53e3\u70b9\u201d</p> <blockquote> <p>\u53e6\u5916\u4e5f\u6709\u4e00\u4efd\u9488\u5bf9 <code>CSS</code> \u89c4\u5219\u548c\u6837\u5f0f\u8868\u7684\u3001\u5355\u72ec\u7684\u89c4\u8303 <code>CSS Object Model (CSSOM)</code></p> </blockquote> <h3 id="12-\u6d4f\u89c8\u5668\u5bf9\u8c61\u6a21\u578b\uff08bom\uff09">1.2 \u6d4f\u89c8\u5668\u5bf9\u8c61\u6a21\u578b\uff08BOM\uff09</h3> <p>\u6d4f\u89c8\u5668\u5bf9\u8c61\u6a21\u578b\uff08Browser Object Model\uff09\uff0c\u7b80\u79f0 <code>BOM</code>\uff0c\u8868\u793a\u7531\u6d4f\u89c8\u5668\uff08\u4e3b\u673a\u73af\u5883\uff09\u63d0\u4f9b\u7684\u7528\u4e8e\u5904\u7406\u6587\u6863\uff08document\uff09\u4e4b\u5916\u7684\u6240\u6709\u5185\u5bb9\u7684\u5176\u4ed6\u5bf9\u8c61\u3002\u4f8b\u5982\uff1a<code>navigator</code> \u5bf9\u8c61\uff0c<code>location</code> \u5bf9\u8c61</p> <h2 id="2-dom-\u6811">2 DOM \u6811</h2> <blockquote> <p>DOM \u6811\u4e2d\u7684\u5bf9\u8c61\u53eb\u505a\u8282\u70b9\uff0c\u8282\u70b9\u5305\u62ec\u5143\u7d20\u8282\u70b9\u3001\u6587\u672c\u8282\u70b9\u7b49</p> </blockquote> <p>\u5e38\u7528\u5230\u7684\u8282\u70b9\u7c7b\u578b\uff1a</p> <ul> <li>document \u2014 DOM \u7684\u201c\u5165\u53e3\u70b9\u201d</li> <li>\u5143\u7d20\u8282\u70b9 \u2014 HTML \u6807\u7b7e\uff0c\u6811\u6784\u5efa\u5757</li> <li>\u6587\u672c\u8282\u70b9 \u2014 \u5305\u542b\u6587\u672c</li> <li>\u6ce8\u91ca \u2014 \u6709\u65f6\u6211\u4eec\u53ef\u4ee5\u5c06\u4e00\u4e9b\u4fe1\u606f\u653e\u5165\u5176\u4e2d\uff0c\u5b83\u4e0d\u4f1a\u663e\u793a\uff0c\u4f46 JS \u53ef\u4ee5\u4ece DOM \u4e2d\u8bfb\u53d6\u5b83</li> </ul> <h2 id="3-\u904d\u5386-dom">3 \u904d\u5386 DOM</h2> <h3 id="31-\u9876\u5c42\u8282\u70b9">3.1 \u9876\u5c42\u8282\u70b9</h3> <ul> <li><code>&lt;html&gt; = document.documentElement</code></li> <li><code>&lt;body&gt; = document.body</code></li> <li><code>&lt;head&gt; = document.head</code></li> </ul> <h3 id="32-\u5b50\u8282\u70b9">3.2 \u5b50\u8282\u70b9</h3> <ul> <li><code>childNodes</code> \u96c6\u5408(\u4e0d\u662f\u6570\u7ec4)\u5217\u51fa\u4e86\u6240\u6709\u5b50\u8282\u70b9\uff0c\u5305\u62ec\u6587\u672c\u8282\u70b9</li> <li><code>firstChild</code> \u7b2c\u4e00\u4e2a\u5b50\u5143\u7d20</li> <li><code>lastChild</code> \u6700\u540e\u4e00\u4e2a\u5b50\u5143\u7d20</li> </ul> <p><code>elem.hasChildNodes()</code> \u7528\u4e8e\u68c0\u67e5\u8282\u70b9\u662f\u5426\u6709\u5b50\u8282\u70b9</p> <h3 id="33-\u5144\u5f1f\u8282\u70b9\u548c\u7236\u8282\u70b9">3.3 \u5144\u5f1f\u8282\u70b9\u548c\u7236\u8282\u70b9</h3> <ul> <li><code>nextSibling</code> \u4e0b\u4e00\u4e2a\u5144\u5f1f\u8282\u70b9</li> <li><code>previousSibling</code> \u4e0a\u4e00\u4e2a\u5144\u5f1f\u8282\u70b9</li> <li><code>parentNode</code> \u7236\u8282\u70b9</li> </ul> <h3 id="34-\u5143\u7d20\u8282\u70b9">3.4 \u5143\u7d20\u8282\u70b9</h3> <p>\u4e0a\u9762\u5217\u51fa\u7684\u5bfc\u822a\uff08navigation\uff09\u5c5e\u6027\u5f15\u7528\u6240\u6709\u8282\u70b9\u3002\u4f8b\u5982\uff0c\u5728 <code>childNodes</code> \u4e2d\u6211\u4eec\u53ef\u4ee5\u770b\u5230\u6587\u672c\u8282\u70b9\uff0c\u5143\u7d20\u8282\u70b9\uff0c\u751a\u81f3\u5982\u679c\u6ce8\u91ca\u8282\u70b9\u5b58\u5728\u7684\u8bdd\uff0c\u4e5f\u80fd\u8bbf\u95ee\u5230</p> <p>\u53ea\u8bbf\u95ee\u5143\u7d20\u8282\u70b9\uff1a</p> <p><code>children</code> \u2014 \u4ec5\u90a3\u4e9b\u4f5c\u4e3a\u5143\u7d20\u8282\u70b9\u7684\u5b50\u4ee3\u7684\u8282\u70b9 <code>firstElementChild</code>\uff0c<code>lastElementChild</code> \u2014 \u7b2c\u4e00\u4e2a\u548c\u6700\u540e\u4e00\u4e2a\u5b50\u5143\u7d20 <code>previousElementSibling</code>\uff0c<code>nextElementSibling</code> \u2014 \u5144\u5f1f\u5143\u7d20 <code>parentElement</code> \u2014 \u7236\u5143\u7d20</p> <h2 id="4-\u83b7\u53d6\u5143\u7d20">4 \u83b7\u53d6\u5143\u7d20</h2> <ul> <li><code>getElementById</code> \u901a\u8fc7 id \u83b7\u53d6\uff0c\u5982\u679c id \u4e0d\u552f\u4e00\u5c31\u4f1a\u968f\u673a\u83b7\u53d6</li> <li><code>querySelectorAll</code> \u4f7f\u7528 CSS \u9009\u62e9\u5668</li> <li><code>querySelector</code></li> <li><code>matches</code> \u68c0\u67e5\u5f53\u524d elem \u662f\u5426\u4e0e\u7ed9\u5b9a\u7684 CSS \u9009\u62e9\u5668\u5339\u914d\uff0c\u8fd4\u56de true/false</li> <li><code>closest</code> \u4f7f\u7528 CSS \u9009\u62e9\u5668\uff0c\u67e5\u627e\u4e0a\u7ea7\u5143\u7d20</li> </ul> <blockquote> <p>\u6240\u6709\u7684 <code>getElementsBy*</code> \u65b9\u6cd5\u90fd\u4f1a\u8fd4\u56de\u4e00\u4e2a <code>\u5b9e\u65f6\u7684\uff08live\uff09</code> \u96c6\u5408\u3002\u8fd9\u6837\u7684\u96c6\u5408\u59cb\u7ec8\u53cd\u6620\u7684\u662f\u6587\u6863\u7684\u5f53\u524d\u72b6\u6001\uff0c\u5e76\u4e14\u5728\u6587\u6863\u53d1\u751f\u66f4\u6539\u65f6\u4f1a\u201c\u81ea\u52a8\u66f4\u65b0\u201d</p> </blockquote> <blockquote> <p><code>querySelectorAll</code> \u8fd4\u56de\u7684\u662f\u4e00\u4e2a <code>\u9759\u6001\u7684</code> \u96c6\u5408</p> </blockquote> <h2 id="5-\u8282\u70b9\u5c5e\u6027">5 \u8282\u70b9\u5c5e\u6027</h2> <h3 id="51-dom-\u8282\u70b9\u7c7b">5.1 DOM \u8282\u70b9\u7c7b</h3> <p>DOM \u8282\u70b9\u7684\u7ee7\u627f\u5173\u7cfb\uff1a</p> <p>\u5c42\u6b21\u7ed3\u6784\uff08hierarchy\uff09\u7684\u6839\u8282\u70b9\u662f <code>EventTarget</code>\uff0c<code>Node</code> \u7ee7\u627f\u81ea\u5b83\uff0c\u5176\u4ed6 <code>DOM</code> \u8282\u70b9\u7ee7\u627f\u81ea <code>Node</code></p> <p><img src="'+g+'" alt=""></p> <ul> <li><code>EventTarget</code> \u2014 \u662f\u6839\u7684\u201c\u62bd\u8c61\uff08abstract\uff09\u201d\u7c7b\u3002\u8be5\u7c7b\u7684\u5bf9\u8c61\u4ece\u672a\u88ab\u521b\u5efa\u3002\u5b83\u4f5c\u4e3a\u4e00\u4e2a\u57fa\u7840\uff0c\u4ee5\u4fbf\u8ba9\u6240\u6709 DOM \u8282\u70b9\u90fd\u652f\u6301\u6240\u8c13\u7684\u201c\u4e8b\u4ef6\uff08event\uff09\u201d</li> <li><code>Node</code> \u2014 \u4e5f\u662f\u4e00\u4e2a\u201c\u62bd\u8c61\u201d\u7c7b\uff0c\u5145\u5f53 DOM \u8282\u70b9\u7684\u57fa\u7840\u3002\u5b83\u63d0\u4f9b\u4e86\u6811\u7684\u6838\u5fc3\u529f\u80fd\uff1a<code>parentNode</code>\uff0c<code>nextSibling</code>\uff0c<code>childNodes</code> \u7b49\uff08\u5b83\u4eec\u90fd\u662f <code>getter</code>\uff09\u3002Node \u7c7b\u7684\u5bf9\u8c61\u4ece\u672a\u88ab\u521b\u5efa\u3002\u4f46\u662f\u6709\u4e00\u4e9b\u7ee7\u627f\u81ea\u5b83\u7684\u5177\u4f53\u7684\u8282\u70b9\u7c7b\uff0c\u4f8b\u5982\uff1a\u6587\u672c\u8282\u70b9\u7684 Text\uff0c\u5143\u7d20\u8282\u70b9\u7684 Element\uff0c\u4ee5\u53ca\u66f4\u591a\u5f02\u57df\uff08exotic\uff09\u7c7b\uff0c\u4f8b\u5982\u6ce8\u91ca\u8282\u70b9\u7684 Comment</li> <li><code>Element</code> \u2014 \u662f DOM \u5143\u7d20\u7684\u57fa\u672c\u7c7b\u3002\u5b83\u63d0\u4f9b\u4e86\u5143\u7d20\u7ea7\u7684\u5bfc\u822a\uff08navigation\uff09\uff0c\u4f8b\u5982 <code>nextElementSibling</code>\uff0c<code>children</code>\uff0c\u4ee5\u53ca\u50cf <code>getElementsByTagName</code> \u548c <code>querySelector</code> \u8fd9\u6837\u7684\u641c\u7d22\u65b9\u6cd5\u3002\u6d4f\u89c8\u5668\u4e2d\u4e0d\u4ec5\u6709 HTML\uff0c\u8fd8\u4f1a\u6709 XML \u548c SVG\u3002Element \u7c7b\u5145\u5f53\u66f4\u591a\u7279\u5b9a\u7c7b\u7684\u57fa\u672c\u7c7b\uff1aSVGElement\uff0cXMLElement \u548c HTMLElement</li> <li><code>HTMLElement</code> \u2014 \u6700\u7ec8\u662f\u6240\u6709 HTML \u5143\u7d20\u7684\u57fa\u672c\u7c7b\u3002\u5404\u79cd HTML \u5143\u7d20\u5747\u7ee7\u627f\u81ea\u5b83\uff1a<ul> <li><code>HTMLInputElement</code> \u2014 <code>&lt;input&gt;</code> \u5143\u7d20\u7684\u7c7b</li> <li><code>HTMLBodyElement</code> \u2014 <code>&lt;body&gt;</code> \u5143\u7d20\u7684\u7c7b</li> <li><code>HTMLAnchorElement</code> \u2014 <code>&lt;a&gt;</code> \u5143\u7d20\u7684\u7c7b</li> <li>\u2026\u2026\u7b49\uff0c\u6bcf\u4e2a\u6807\u7b7e\u90fd\u6709\u81ea\u5df1\u7684\u7c7b\uff0c\u8fd9\u4e9b\u7c7b\u53ef\u4ee5\u63d0\u4f9b\u7279\u5b9a\u7684\u5c5e\u6027\u548c\u65b9\u6cd5</li> </ul> </li> </ul> <h3 id="52-nodetype-\u5c5e\u6027">5.2 nodeType \u5c5e\u6027</h3> <p>nodeType \u662f\u4e00\u4e2a\u6570\u503c\u578b\u503c\uff1a</p> <ul> <li>\u5bf9\u4e8e\u5143\u7d20\u8282\u70b9 <code>elem.nodeType == 1</code></li> <li>\u5bf9\u4e8e\u6587\u672c\u8282\u70b9 <code>elem.nodeType == 3</code></li> <li>\u5bf9\u4e8e document \u5bf9\u8c61 <code>elem.nodeType == 9</code></li> </ul> <blockquote> <p>\u8fd9\u4e2a\u5c5e\u6027\u662f\u8fc7\u65f6\u7684\uff0c\u73b0\u5728\u4e00\u822c\u7528 <code>instanceof</code> \u548c\u5176\u4ed6\u57fa\u4e8e\u7c7b\u7684\u68c0\u67e5\u65b9\u6cd5\u6765\u67e5\u770b\u8282\u70b9\u7c7b\u578b</p> </blockquote> <h3 id="53-nodenametagname-\u6807\u7b7e\u540d">5.3 nodeName/tagName \u6807\u7b7e\u540d</h3> <p><code>nodeName</code> \u6216\u8005 <code>tagName</code> \u90fd\u80fd\u83b7\u53d6\u6807\u7b7e\u540d</p> <p>\u533a\u522b\uff1a</p> <ul> <li><code>tagName</code> \u5c5e\u6027\u4ec5\u9002\u7528\u4e8e Element \u8282\u70b9</li> <li><code>nodeName</code> \u662f\u4e3a\u4efb\u610f Node \u5b9a\u4e49\u7684\uff1a<ul> <li>\u5bf9\u4e8e\u5143\u7d20\uff0c\u5b83\u7684\u610f\u4e49\u4e0e tagName \u76f8\u540c</li> <li>\u5bf9\u4e8e\u5176\u4ed6\u8282\u70b9\u7c7b\u578b\uff08text\uff0ccomment \u7b49\uff09\uff0c\u5b83\u62e5\u6709\u4e00\u4e2a\u5bf9\u5e94\u8282\u70b9\u7c7b\u578b\u7684\u5b57\u7b26\u4e32</li> </ul> </li> </ul> <h3 id="54-innerhtmlouterhtml-\u5143\u7d20\u5185\u5bb9">5.4 innerHTML/outerHTML \u5143\u7d20\u5185\u5bb9</h3> <p><strong><code>innerHTML</code> \u53ef\u4ee5 <code>\u83b7\u53d6/\u4fee\u6539</code> \u5143\u7d20\u5185\u5bb9</strong></p> <blockquote> <p>\u811a\u672c\u4e0d\u4f1a\u6267\u884c\uff1a\u5982\u679c <code>innerHTML</code> \u5c06\u4e00\u4e2a <code>&lt;script&gt;</code> \u6807\u7b7e\u63d2\u5165\u5230 document \u4e2d \u2014 \u5b83\u4f1a\u6210\u4e3a HTML \u7684\u4e00\u90e8\u5206\uff0c\u4f46\u662f\u4e0d\u4f1a\u6267\u884c</p> </blockquote> <p><code>innerHTML</code> \u4e0d\u662f\u9644\u52a0\u5185\u5bb9\uff0c\u800c\u662f <code>\u5b8c\u5168\u5730\u91cd\u5199</code></p> <blockquote> <p><code>innerHTML</code> \u4f1a\u5c06\u6240\u6709\u7684\u56fe\u7247\u548c\u5176\u4ed6\u8d44\u6e90\u90fd\u5c06\u91cd\u5199\u52a0\u8f7d\u3002\u5e26\u6709\u7528\u6237\u8f93\u5165\u7684\u6587\u672c\u7684 <code>&lt;input&gt;</code>\uff0c\u88ab\u8f93\u5165\u7684\u6587\u672c\u4e5f\u4f1a\u88ab\u79fb\u9664</p> </blockquote> <p><strong><code>outerHTML</code> \u83b7\u53d6\u5143\u7d20\u7684\u5b8c\u6574 HTML</strong></p> <blockquote> <p>\u4e0e <code>innerHTML</code> \u4e0d\u540c\uff0c\u5199\u5165 <code>outerHTML</code> \u4e0d\u4f1a\u6539\u53d8\u5143\u7d20\uff0c\u800c\u662f\u5728 DOM \u4e2d\u66ff\u6362\u5b83\u3002\u9700\u8981\u901a\u8fc7\u67e5\u8be2 DOM \u6765\u83b7\u53d6\u5bf9\u65b0\u5143\u7d20\u7684\u5f15\u7528</p> </blockquote> <pre><code class="language-js">let div = document.querySelector(&#39;div&#39;);\n\n// \u4f7f\u7528 &lt;p&gt;...&lt;/p&gt; \u66ff\u6362 div.outerHTML\ndiv.outerHTML = &#39;&lt;p&gt;A new element&lt;/p&gt;&#39;; // (*)\n\n// \u86e4\uff01&#39;div&#39; \u8fd8\u662f\u539f\u6765\u90a3\u6837\uff01\nalert(div.outerHTML); // &lt;div&gt;Hello, world!&lt;/div&gt; (**)</code></pre> <ul> <li>div \u88ab\u4ece\u6587\u6863\uff08document\uff09\u4e2d\u79fb\u9664</li> <li>\u53e6\u4e00\u4e2a HTML \u7247\u6bb5 <code>&lt;p&gt;A new element&lt;/p&gt;</code> \u88ab\u63d2\u5165\u5230\u5176\u4f4d\u7f6e\u4e0a</li> <li>div \u4ecd\u62e5\u6709\u5176\u65e7\u7684\u503c\u3002\u65b0\u7684 HTML \u6ca1\u6709\u88ab\u8d4b\u503c\u7ed9\u4efb\u4f55\u53d8\u91cf</li> </ul> <h3 id="55-nodevaluedata-\u6587\u672c\u8282\u70b9\u5185\u5bb9">5.5 nodeValue/data \u6587\u672c\u8282\u70b9\u5185\u5bb9</h3> <p><code>innerHTML</code> \u5c5e\u6027\u4ec5\u5bf9\u5143\u7d20\u8282\u70b9\u6709\u6548\u3002</p> <p>\u5176\u4ed6\u8282\u70b9\u7c7b\u578b\uff0c\u4f8b\u5982\u6587\u672c\u8282\u70b9\uff0c\u5177\u6709\u5b83\u4eec\u7684\u5bf9\u5e94\u9879\uff1a<code>nodeValue</code> \u548c <code>data</code> \u5c5e\u6027\u3002\u8fd9\u4e24\u8005\u5728\u5b9e\u9645\u4f7f\u7528\u4e2d\u51e0\u4e4e\u76f8\u540c\uff0c\u53ea\u6709\u7ec6\u5fae\u89c4\u8303\u4e0a\u7684\u5dee\u5f02</p> <pre><code class="language-html">&lt;body&gt;\n  Hello\n  &lt;!-- Comment --&gt;\n  &lt;script&gt;\n    let text = document.body.firstChild;\n    alert(text.data); // Hello\n\n    let comment = text.nextSibling;\n    alert(comment.data); // Comment\n  &lt;/script&gt;\n&lt;/body&gt;</code></pre> <h3 id="56-textcontent-\u7eaf\u6587\u672c\u65b9\u5f0f\u8bfb\u5199">5.6 textContent \u7eaf\u6587\u672c\u65b9\u5f0f\u8bfb\u5199</h3> <p><code>textContent</code> \u63d0\u4f9b\u4e86\u5bf9\u5143\u7d20\u5185\u7684\u6587\u672c\u7684\u8bbf\u95ee\u6743\u9650\uff1a\u4ec5\u6587\u672c\uff0c\u53bb\u6389\u6240\u6709 <code>&lt;tags&gt;</code></p> <blockquote> <p>\u5199\u5165 <code>textContent</code> \u8981\u6709\u7528\u5f97\u591a\uff0c\u56e0\u4e3a\u5b83\u5141\u8bb8\u4ee5\u201c\u5b89\u5168\u65b9\u5f0f\u201d\u5199\u5165\u6587\u672c</p> </blockquote> <ul> <li>\u4f7f\u7528 <code>innerHTML</code>\uff0c\u6211\u4eec\u5c06\u5176\u201c\u4f5c\u4e3a HTML\u201d\u63d2\u5165\uff0c\u5e26\u6709\u6240\u6709 HTML \u6807\u7b7e</li> <li>\u4f7f\u7528 <code>textContent</code>\uff0c\u6211\u4eec\u5c06\u5176\u201c\u4f5c\u4e3a\u6587\u672c\u201d\u63d2\u5165\uff0c\u6240\u6709\u7b26\u53f7\uff08symbol\uff09\u5747\u6309\u5b57\u9762\u610f\u4e49\u5904\u7406</li> </ul> <h3 id="57-\u5176\u4ed6\u5c5e\u6027">5.7 \u5176\u4ed6\u5c5e\u6027</h3> <ul> <li><code>hidden</code> - \u6307\u5b9a\u5143\u7d20\u662f\u5426\u53ef\u89c1</li> <li><code>value</code> \u2014 <code>&lt;input&gt;</code>\uff0c<code>&lt;select&gt;</code> \u548c <code>&lt;textarea&gt;</code>\uff08<code>HTMLInputElement</code>\uff0c<code>HTMLSelectElement</code>\u2026\u2026\uff09\u7684 <code>value</code></li> <li><code>href</code> \u2014 <code>&lt;a href=&quot;...&quot;&gt;</code>\uff08<code>HTMLAnchorElement</code>\uff09\u7684 <code>href</code>\u3002</li> <li><code>id</code> \u2014 \u6240\u6709\u5143\u7d20\u7684 \u201cid\u201d <code>attribute</code> \u7684\u503c</li> </ul> <h2 id="6-attributes-\u548c-properties">6 attributes \u548c properties</h2> <ul> <li><code>attributes</code> - \u662f\u6307\u5199\u5728 HTML \u6807\u7b7e\u4e2d\u7684\u5c5e\u6027</li> <li><code>properties</code> - JS \u4e2d DOM \u5bf9\u8c61\u7684\u5c5e\u6027</li> </ul> <blockquote> <p>\u5bf9\u4e8e\u5143\u7d20\u8282\u70b9\uff0c\u5927\u591a\u6570 <code>\u6807\u51c6\u7684</code> HTML \u7279\u6027\uff08attributes\uff09\u4f1a\u81ea\u52a8\u53d8\u6210 DOM \u5bf9\u8c61\u7684\u5c5e\u6027\uff08properties\uff09</p> </blockquote> <h3 id="61-dom-\u5c5e\u6027-properties">6.1 DOM \u5c5e\u6027 properties</h3> <p>DOM \u8282\u70b9\u662f\u5e38\u89c4\u7684 JavaScript \u5bf9\u8c61\uff0cDOM \u5c5e\u6027\u548c\u65b9\u6cd5\u7684\u884c\u4e3a\u5c31\u50cf\u5e38\u89c4\u7684 Javascript \u5bf9\u8c61\u4e00\u6837</p> <p>\u53ef\u4ee5\u81ea\u884c\u6dfb\u52a0\u5c5e\u6027\u548c\u65b9\u6cd5\uff0c\u8fd8\u53ef\u4ee5\u4fee\u6539\u5185\u5efa\u5c5e\u6027\u7684\u539f\u578b\uff0c\u4f8b\u5982\u4fee\u6539 <code>Element.prototype</code> \u4e3a\u6240\u6709\u5143\u7d20\u6dfb\u52a0\u4e00\u4e2a\u65b0\u65b9\u6cd5</p> <h3 id="62-html-\u5c5e\u6027-attributes">6.2 HTML \u5c5e\u6027 attributes</h3> <p>\u5f53\u6d4f\u89c8\u5668\u89e3\u6790 HTML \u6587\u672c\uff0c\u5e76\u6839\u636e\u6807\u7b7e\u521b\u5efa DOM \u5bf9\u8c61\u65f6\uff0c\u6d4f\u89c8\u5668\u4f1a\u8fa8\u522b <code>\u6807\u51c6\u7684</code> <code>attributes</code> \u5e76\u4ee5\u6b64\u521b\u5efa DOM \u5c5e\u6027\uff0c\u975e <code>\u6807\u51c6\u7684</code> <code>attributes</code> \u5219\u4e0d\u4f1a</p> <pre><code class="language-html">&lt;!-- \u8fd9\u4e2a myname \u5c31\u662f\u975e\u6807\u51c6\u7684 attributes --&gt;\n&lt;div myname=&quot;div&quot;&gt;&lt;/div&gt;</code></pre> <p>\u5982\u679c\u4e00\u4e2a <code>attributes</code> \u4e0d\u662f\u6807\u51c6\u7684\uff0c\u90a3\u4e48\u5c31\u6ca1\u6709\u76f8\u5bf9\u5e94\u7684 <code>DOM</code> \u5c5e\u6027</p> <p><strong>\u53ef\u4ee5\u4f7f\u7528\u4e0b\u9762\u65b9\u6cd5\u8bbf\u95ee\u975e\u6807\u51c6 attributes\uff1a</strong></p> <ul> <li><code>elem.hasAttribute(name)</code> \u2014 \u68c0\u67e5 <code>attributes</code> \u662f\u5426\u5b58\u5728</li> <li><code>elem.getAttribute(name)</code> \u2014 \u83b7\u53d6\u8fd9\u4e2a <code>attributes</code> \u503c</li> <li><code>elem.setAttribute(name, value)</code> \u2014 \u8bbe\u7f6e\u8fd9\u4e2a <code>attributes</code> \u503c</li> <li><code>elem.removeAttribute(name)</code> \u2014 \u79fb\u9664\u8fd9\u4e2a <code>attributes</code></li> </ul> <p><strong>HTML \u7279\u6027 (attributes) \u6709\u4ee5\u4e0b\u51e0\u4e2a\u7279\u5f81\uff1a</strong></p> <ul> <li>\u5b83\u4eec\u7684\u540d\u5b57\u662f\u5927\u5c0f\u5199\u4e0d\u654f\u611f\u7684\uff08id \u4e0e ID \u76f8\u540c\uff09</li> <li>\u5b83\u4eec\u7684\u503c\u603b\u662f\u5b57\u7b26\u4e32\u7c7b\u578b\u7684</li> </ul> <p><strong>\u6ce8\u610f\u70b9\uff1a</strong></p> <ul> <li><code>getAttribute(&#39;About&#39;)</code> \u2014 \u8fd9\u91cc\u7684\u7b2c\u4e00\u4e2a\u5b57\u6bcd\u662f\u5927\u5199\u7684\uff0c\u4f46\u662f\u5728 HTML \u4e2d\uff0c\u5b83\u4eec\u90fd\u662f\u5c0f\u5199\u7684\u3002\u4f46\u8fd9\u6ca1\u6709\u5f71\u54cd\uff1a\u7279\u6027\u7684\u540d\u79f0\u662f\u5927\u5c0f\u5199\u4e0d\u654f\u611f\u7684</li> <li>\u6211\u4eec\u53ef\u4ee5\u5c06\u4efb\u4f55\u4e1c\u897f\u8d4b\u503c\u7ed9 <code>attributes</code>\uff0c\u4f46\u662f\u8fd9\u4e9b\u4e1c\u897f\u4f1a\u53d8\u6210\u5b57\u7b26\u4e32\u7c7b\u578b\u7684</li> <li>\u6240\u6709 <code>attributes</code>\uff0c\u5305\u62ec\u6211\u4eec\u8bbe\u7f6e\u7684\u90a3\u4e2a <code>attributes</code>\uff0c\u5728 <code>outerHTML</code> \u4e2d\u90fd\u662f\u53ef\u89c1\u7684</li> <li><code>attributes</code> \u96c6\u5408\u662f\u53ef\u8fed\u4ee3\u5bf9\u8c61\uff0c\u8be5\u5bf9\u8c61\u5c06\u6240\u6709\u5143\u7d20\u7684 <code>attributes</code>\uff08\u6807\u51c6\u548c\u975e\u6807\u51c6\u7684\uff09\u4f5c\u4e3a <code>name</code> \u548c <code>value</code> \u5c5e\u6027\u5b58\u50a8\u5728\u5bf9\u8c61\u4e2d</li> </ul> <h3 id="63-properties---attributes-\u540c\u6b65">6.3 properties - attributes \u540c\u6b65</h3> <p>\u5f53\u4e00\u4e2a <code>\u6807\u51c6\u7684</code> <code>attributes</code> \u88ab\u6539\u53d8\uff0c\u5bf9\u5e94\u7684\u5c5e\u6027\u4e5f\u4f1a\u81ea\u52a8\u66f4\u65b0\uff0c\uff08\u9664\u4e86\u51e0\u4e2a\u7279\u4f8b\uff09\u53cd\u4e4b\u4ea6\u7136</p> <blockquote> <p>\u8fd9\u91cc\u4e5f\u6709\u4e9b\u4f8b\u5916\uff0c\u4f8b\u5982 <code>input.value</code> \u53ea\u80fd\u4ece <code>attributes</code> \u540c\u6b65\u5230 <code>properties</code>\uff0c\u53cd\u8fc7\u6765\u5219\u4e0d\u884c</p> </blockquote> <pre><code class="language-html">&lt;input /&gt;\n\n&lt;script&gt;\n  let input = document.querySelector(&#39;input&#39;);\n\n  // attributes =&gt; properties\n  input.setAttribute(&#39;value&#39;, &#39;text&#39;);\n  alert(input.value); // text\n\n  // \u8fd9\u4e2a\u64cd\u4f5c\u65e0\u6548\uff0cproperties =&gt; attributes\n  input.value = &#39;newValue&#39;;\n  alert(input.getAttribute(&#39;value&#39;)); // text\uff08\u6ca1\u6709\u88ab\u66f4\u65b0\uff01\uff09\n&lt;/script&gt;</code></pre> <h3 id="64-dom-\u5c5e\u6027\u662f\u591a\u7c7b\u578b\u7684">6.4 DOM \u5c5e\u6027\u662f\u591a\u7c7b\u578b\u7684</h3> <p>DOM \u5c5e\u6027\u4e0d\u603b\u662f\u5b57\u7b26\u4e32\u7c7b\u578b\u7684\u3002\u4f8b\u5982\uff0c<code>input.checked</code> \u5c5e\u6027\uff08\u5bf9\u4e8e <code>checkbox</code> \u7684\uff09\u662f\u5e03\u5c14\u578b</p> <pre><code class="language-html">&lt;input id=&quot;input&quot; type=&quot;checkbox&quot; checked /&gt; checkbox\n\n&lt;script&gt;\n  alert(input.getAttribute(&#39;checked&#39;)); // attributes \u503c\u662f\uff1a\u7a7a\u5b57\u7b26\u4e32\n  alert(input.checked); // properties \u503c\u662f\uff1atrue\n&lt;/script&gt;</code></pre> <p><strong>\u6ce8\u610f\u70b9\uff1a</strong></p> <ul> <li>HTML \u7279\u6027 (attributes) \u548c DOM \u5c5e\u6027 (properties) \u53d6\u5230\u7684\u503c\u53ef\u80fd\u4f1a\u4e0d\u4e00\u81f4</li> <li>\u5982\u679c\u8981\u53d6\u4e0e HTML \u4e2d\u6240\u5199\u7684\u5b8c\u5168\u76f8\u540c\u7684 <code>attributes</code>\uff0c\u53ef\u4ee5\u4f7f\u7528 <code>getAttribute</code></li> </ul> <h3 id="65-dataset">6.5 dataset</h3> <p>\u81ea\u5b9a\u4e49\u7684 <code>attributes</code> \u4e5f\u5b58\u5728\u95ee\u9898\u3002HTML \u8bed\u8a00\u662f\u5728\u4e0d\u65ad\u53d1\u5c55\u7684\uff0c\u5982\u679c\u6211\u4eec\u51fa\u4e8e\u6211\u4eec\u7684\u76ee\u7684\u4f7f\u7528\u4e86\u975e\u6807\u51c6\u7684 <code>attributes</code>\uff0c\u4e4b\u540e\u5b83\u88ab\u5f15\u5165\u5230\u4e86\u6807\u51c6\u4e2d\u5e76\u6709\u4e86\u5176\u81ea\u5df1\u7684\u7528\u9014\uff0c\u5c31\u4f1a\u6709\u51b2\u7a81</p> <blockquote> <p>\u6240\u4ee5 <code>data-</code> \u5f00\u5934\u7684 <code>attributes</code> \u5747\u88ab\u4fdd\u7559\u4f9b\u7a0b\u5e8f\u5458\u4f7f\u7528</p> </blockquote> <pre><code class="language-html">&lt;body data-about=&quot;Elephants&quot;&gt;\n  &lt;div id=&quot;order&quot; class=&quot;order&quot; data-order-state=&quot;new&quot;&gt;A new order.&lt;/div&gt;\n\n  &lt;script&gt;\n    alert(document.body.dataset.about); // Elephants\n\n    // \u8bfb\u53d6\uff0c\u9a7c\u5cf0\u5f0f\n    alert(order.dataset.orderState); // new\n    // \u4fee\u6539\n    order.dataset.orderState = &#39;pending&#39;;\n  &lt;/script&gt;\n&lt;/body&gt;</code></pre> <h2 id="7-\u4fee\u6539\u6587\u6863">7 \u4fee\u6539\u6587\u6863</h2> <h3 id="71-\u521b\u5efa\u8282\u70b9">7.1 \u521b\u5efa\u8282\u70b9</h3> <ul> <li><code>document.createElement(tag)</code> \u7528\u7ed9\u5b9a\u7684\u6807\u7b7e\u521b\u5efa\u4e00\u4e2a\u65b0 <code>\u5143\u7d20\u8282\u70b9</code></li> <li><code>document.createTextNode(text)</code></li> </ul> <pre><code class="language-js">let div = document.createElement(&#39;div&#39;);\nlet textNode = document.createTextNode(&#39;Here I am&#39;);</code></pre> <p><strong>DocumentFragment \u8282\u70b9\u5305\u88c5\u5668</strong></p> <p>\u8fd9\u4e2a\u65b9\u6cd5\u4e0d\u5e38\u7528</p> <p><code>DocumentFragment</code> \u662f\u4e00\u4e2a\u7279\u6b8a\u7684 <code>DOM</code> \u8282\u70b9\uff0c\u662f\u4e00\u4e2a\u5305\u88c5\u5668</p> <p>\u63d2\u5165\u65f6\u53ea\u4f1a\u5c06\u5185\u5bb9\u63d2\u5165\uff0c\u4e0d\u4f1a\u63d2\u5165\u81ea\u8eab\uff0c\u5b83\u53ea\u662f\u4e00\u4e2a\u5bb9\u5668</p> <pre><code class="language-html">&lt;ul id=&quot;ul&quot;&gt;&lt;/ul&gt;\n\n&lt;script&gt;\n  function getListContent() {\n    let fragment = new DocumentFragment();\n\n    for (let i = 1; i &lt;= 3; i++) {\n      let li = document.createElement(&#39;li&#39;);\n      li.append(i);\n      fragment.append(li);\n    }\n\n    return fragment;\n  }\n\n  ul.append(getListContent()); // (*)\n&lt;/script&gt;</code></pre> <h3 id="72-\u63d2\u5165\u8282\u70b9">7.2 \u63d2\u5165\u8282\u70b9</h3> <p><code>node.append(...nodes or strings)</code> \u2014\u2014 \u5728 <code>node</code> \u672b\u5c3e \u63d2\u5165\u8282\u70b9\u6216\u5b57\u7b26\u4e32 <code>node.prepend(...nodes or strings)</code> \u2014\u2014 \u5728 <code>node</code> \u5f00\u5934 \u63d2\u5165\u8282\u70b9\u6216\u5b57\u7b26\u4e32 <code>node.before(...nodes or strings)</code> \u2014\u2014 \u5728 <code>node</code> \u524d\u9762 \u63d2\u5165\u8282\u70b9\u6216\u5b57\u7b26\u4e32 <code>node.after(...nodes or strings)</code> \u2014\u2014 \u5728 <code>node</code> \u540e\u9762 \u63d2\u5165\u8282\u70b9\u6216\u5b57\u7b26\u4e32 <code>node.replaceWith(...nodes or strings)</code> \u2014\u2014 \u5c06 <code>node</code> \u66ff\u6362\u4e3a\u7ed9\u5b9a\u7684\u8282\u70b9\u6216\u5b57\u7b26\u4e32</p> <pre><code class="language-js">let div = document.createElement(&#39;div&#39;);\ndiv.className = &#39;alert&#39;;\ndiv.innerHTML = &quot;&lt;strong&gt;Hi there!&lt;/strong&gt; You&#39;ve read an important message.&quot;;\n\ndocument.body.append(div);</code></pre> <blockquote> <p>\u5982\u679c\u63d2\u5165\u4e00\u4e2a\u9875\u9762\u4e2d\u5df2\u5b58\u5728\u7684\u8282\u70b9\uff0c\u90a3\u4e48\u539f\u6709\u7684\u8282\u70b9\u4f1a\u81ea\u52a8\u5220\u9664 (\u76f8\u5f53\u4e8e\u632a\u4e00\u4e2a\u4f4d\u7f6e)</p> </blockquote> <p><strong>nsertAdjacentHTML/Text/Element</strong></p> <p>\u4e0a\u9762\u8fd9\u79cd\u65b9\u5f0f\u9700\u8981\u5148\u521b\u5efa\u8282\u70b9\uff0c\u7136\u540e\u518d\u63d2\u5165\uff0c\u662f\u4e00\u79cd\u6bd4\u8f83\u5b89\u5168\u7684\u65b9\u5f0f</p> <p><code>insertAdjacentHTML</code> \u53ef\u4ee5\u76f4\u63a5\u63d2\u5165 HTML \u4ee3\u7801\uff0c\u5c31\u50cf <code>innerHTML</code> \u90a3\u6837\uff0c\u4f46\u662f\u8fd9\u79cd\u65b9\u5f0f\u4e0d\u5b89\u5168</p> <p><strong>\u53c2\u6570\uff1a</strong></p> <ul> <li><code>beforebegin</code> \u2014 \u5c06 html \u63d2\u5165\u5230 elem \u524d\u63d2\u5165</li> <li><code>afterbegin</code> \u2014 \u5c06 html \u63d2\u5165\u5230 elem \u5f00\u5934</li> <li><code>beforeend</code> \u2014 \u5c06 html \u63d2\u5165\u5230 elem \u672b\u5c3e</li> <li><code>afterend</code> \u2014 \u5c06 html \u63d2\u5165\u5230 elem \u540e\u3002</li> </ul> <pre><code class="language-html">&lt;div id=&quot;div&quot;&gt;&lt;/div&gt;\n&lt;script&gt;\n  div.insertAdjacentHTML(&#39;beforebegin&#39;, &#39;&lt;p&gt;Hello&lt;/p&gt;&#39;);\n  div.insertAdjacentHTML(&#39;afterend&#39;, &#39;&lt;p&gt;Bye&lt;/p&gt;&#39;);\n&lt;/script&gt;</code></pre> <h3 id="73-\u79fb\u9664\u8282\u70b9">7.3 \u79fb\u9664\u8282\u70b9</h3> <p><code>node.remove()</code> \u79fb\u9664\u8282\u70b9</p> <blockquote> <p>\u5982\u679c\u6211\u4eec\u8981\u5c06\u4e00\u4e2a\u5143\u7d20 <code>\u79fb\u52a8</code> \u5230\u53e6\u4e00\u4e2a\u5730\u65b9\uff0c\u5219\u65e0\u9700\u5c06\u5176\u4ece\u539f\u6765\u7684\u4f4d\u7f6e\u4e2d\u5220\u9664\u3002\u6240\u6709\u63d2\u5165\u65b9\u6cd5\u90fd\u4f1a\u81ea\u52a8\u4ece\u65e7\u4f4d\u7f6e\u5220\u9664\u8be5\u8282\u70b9</p> </blockquote> <h3 id="74-\u514b\u9686\u8282\u70b9">7.4 \u514b\u9686\u8282\u70b9</h3> <p><code>elem.cloneNode()</code> \u514b\u9686\u8282\u70b9</p> <ul> <li><code>elem.cloneNode(true)</code> \u6df1\u514b\u9686 \u2014 \u5177\u6709\u6240\u6709\u7279\u6027\uff08attribute\uff09\u548c <code>\u5b50\u5143\u7d20</code></li> <li><code>elem.cloneNode(false)</code> \u4e0d\u5305\u62ec\u5b50\u5143\u7d20</li> </ul> <h3 id="75-\u65e7\u5f0f\u65b9\u6cd5">7.5 \u65e7\u5f0f\u65b9\u6cd5</h3> <p>\u6240\u6709\u64cd\u4f5c\u662f\u57fa\u4e8e\u7236\u5143\u7d20\u7684</p> <ul> <li><code>parentElem.appendChild(node)</code> - \u5728\u672b\u5c3e\u63d2\u5165\u4e00\u4e2a\u5b50\u5143\u7d20</li> <li><code>parentElem.insertBefore(node, nextSibling)</code> - <code>nextSibling</code> \u662f\u4e00\u4e2a\u5b50\u5143\u7d20\uff0c\u5728 <code>nextSibling</code> \u524d\u63d2\u5165 <code>node</code></li> <li><code>parentElem.replaceChild(node, oldChild)</code> - \u5c06 <code>parentElem</code> \u7684\u540e\u4ee3\u4e2d\u7684 <code>oldChild</code> \u66ff\u6362\u4e3a <code>node</code></li> <li><code>parentElem.removeChild(node)</code> - \u4ece <code>parentElem</code> \u4e2d\u5220\u9664 <code>node</code>\uff0c\uff08<code>node</code> \u4e3a <code>parentElem</code> \u7684\u540e\u4ee3\uff09</li> </ul> <p><strong>document.write</strong></p> <p>\u4e5f\u662f\u4e00\u4e2a\u8fdc\u53e4\u65b9\u6cd5\uff0c\u73b0\u5728\u4e0d\u5e38\u7528\u3002\u76f4\u63a5\u4ece\u5199 html \u4e0b\u7684\u5185\u5bb9</p> <h2 id="8-\u6837\u5f0f\u548c\u7c7b">8 \u6837\u5f0f\u548c\u7c7b</h2> <h3 id="81-classname-\u548c-classlist">8.1 className \u548c classList</h3> <p><strong>className:</strong></p> <p>\u50cf <code>class</code> \u8fd9\u6837\u7684\u4fdd\u7559\u5b57\u4e0d\u80fd\u7528\u4f5c\u5bf9\u8c61\u7684\u5c5e\u6027\uff0c\u6240\u4ee5\u7528 <code>className</code> \u6765\u8868\u793a</p> <pre><code class="language-html">&lt;body class=&quot;main page&quot;&gt;\n  &lt;script&gt;\n    alert(document.body.className); // main page\n  &lt;/script&gt;\n&lt;/body&gt;</code></pre> <blockquote> <p><code>className</code> \u83b7\u53d6 HTML attributes \u4e2d\u7684 class\uff0c\u7ed9 <code>className</code> \u8d4b\u503c\u4f1a\u66ff\u6362\u6574\u4e2a\u5c5e\u6027</p> </blockquote> <p><strong>classList:</strong></p> <p><code>elem.classList</code> \u662f\u4e00\u4e2a\u7279\u6b8a\u7684\u5bf9\u8c61\uff0c\u53ef\u4ee5\u5bf9\u5355\u4e2a\u7c7b\u8fdb\u884c\u64cd\u4f5c</p> <ul> <li><code>elem.classList.add/remove(class)</code> \u2014 \u6dfb\u52a0/\u79fb\u9664\u7c7b</li> <li><code>elem.classList.toggle(class)</code> \u2014 \u5982\u679c\u7c7b\u4e0d\u5b58\u5728\u5c31\u6dfb\u52a0\u7c7b\uff0c\u5b58\u5728\u5c31\u79fb\u9664\u5b83</li> <li><code>elem.classList.contains(class)</code> \u2014 \u68c0\u67e5\u7ed9\u5b9a\u7c7b\uff0c\u8fd4\u56de <code>true/false</code></li> </ul> <h3 id="82-\u5143\u7d20\u6837\u5f0f">8.2 \u5143\u7d20\u6837\u5f0f</h3> <p><strong>elem.style</strong></p> <p><code>elem.style</code> \u5c5e\u6027\u662f\u4e00\u4e2a\u5bf9\u8c61\uff0c\u5b83\u5bf9\u5e94\u4e8e <code>style</code> \u7279\u6027\uff08attribute\uff09\u4e2d\u6240\u5199\u7684\u5185\u5bb9</p> <blockquote> <p><code>elem.style</code> \u53ea\u80fd\u83b7\u53d6\u884c\u5185\u6837\u5f0f\uff0c\u5176\u4ed6\u5730\u65b9\u8bbe\u7f6e\u7684\u6837\u5f0f\u65e0\u6cd5\u83b7\u53d6\u3002\u8bbe\u7f6e\u7684\u4e5f\u662f\u884c\u5185\u6837\u5f0f</p> </blockquote> <ul> <li>\u5bf9\u4e8e\u591a\u8bcd\u5c5e\u6027\uff0c\u4f7f\u7528\u9a7c\u5cf0\u5f0f <code>elem.style.backgroundColor</code></li> <li>\u524d\u7f00\u5c5e\u6027 <code>-moz-border-radius</code> =&gt; <code>button.style.MozBorderRadius = &#39;5px&#39;</code></li> <li>\u5220\u9664\u67d0\u4e2a\u6837\u5f0f\uff0c\u7f6e\u7a7a\u5c31\u884c <code>elem.style.display = &quot;&quot;</code></li> </ul> <p><strong>style.cssText</strong></p> <p><code>elem.style</code> \u53ea\u80fd\u8bbe\u7f6e\u5f53\u4e2a\u6837\u5f0f\uff0c\u60f3\u8981\u4ee5\u5b57\u7b26\u4e32\u7684\u5f62\u5f0f\u8bbe\u7f6e\u5b8c\u6574\u7684\u6837\u5f0f\uff0c\u53ef\u4ee5\u4f7f\u7528\u7279\u6b8a\u5c5e\u6027 <code>style.cssText</code></p> <p>\u5b83\u4f1a <strong>\u66ff\u6362</strong> \u6574\u4e2a\u5143\u7d20\u4e2d\u6574\u4e2a style \u5c5e\u6027</p> <pre><code class="language-js">ele.style.cssText = `color: red !important;\n    background-color: yellow;\n    width: 100px;\n    text-align: center;\n  `;</code></pre> <h3 id="83-getcomputedstyle">8.3 getComputedStyle</h3> <p><code>style</code> \u5c5e\u6027\u4ec5\u5bf9 &quot;style&quot; \u7279\u6027\uff08attribute\uff09\u503c\u8d77\u4f5c\u7528\uff0c\u800c\u6ca1\u6709\u4efb\u4f55 CSS \u7ea7\u8054\uff0c\u65e0\u6cd5\u83b7\u53d6\u5b9e\u9645\u7684 CSS \u6837\u5f0f</p> <p><code>getComputedStyle</code> \u53ef\u4ee5\u83b7\u53d6\u5143\u7d20\u8ba1\u7b97\u540e\u7684\u6837\u5f0f\uff0c\u5373\u5f53\u524d\u5b9e\u9645\u7684\u6837\u5f0f</p> <pre><code class="language-js">getComputedStyle(element, [pseudo]);</code></pre> <p><code>element</code> - \u9700\u8981\u88ab\u8bfb\u53d6\u6837\u5f0f\u503c\u7684\u5143\u7d20 <code>pseudo</code> - \u4f2a\u5143\u7d20\uff08\u5982\u679c\u9700\u8981\uff09\uff0c\u4f8b\u5982 <code>::before</code>\u3002\u7a7a\u5b57\u7b26\u4e32\u6216\u65e0\u53c2\u6570\u5219\u610f\u5473\u7740\u5143\u7d20\u672c\u8eab</p> <blockquote> <p><code>getComputedStyle</code> \u83b7\u53d6\u7684\u662f\u89e3\u6790\u540e\u7684\u503c\uff0c\u4f7f\u7528\u7684\u5355\u4f4d\u662f\u56fa\u5b9a\u7684\u3002\u6bd4\u5982 <code>height:1em</code> \u6216 <code>font-size:125%</code>\uff0c\u83b7\u53d6\u5230\u7684\u503c\u4e3a <code>height:20px</code> \u6216 <code>font-size:16px</code></p> </blockquote> <h2 id="9-\u5143\u7d20\u5927\u5c0f\u548c\u6eda\u52a8">9 \u5143\u7d20\u5927\u5c0f\u548c\u6eda\u52a8</h2> <p><img src="'+m+'" alt=""></p> <p><img src="'+h+'" alt=""></p> <h3 id="91-offsetparent">9.1 offsetParent</h3> <p><code>offsetParent</code> \u662f\u6700\u63a5\u8fd1\u7684\u7956\u5148\uff08ancestor\uff09\uff0c\u5728\u6d4f\u89c8\u5668\u6e32\u67d3\u671f\u95f4\uff0c\u5b83\u88ab\u7528\u4e8e\u8ba1\u7b97\u5750\u6807</p> <p><strong>\u6700\u8fd1\u7684\u7956\u5148\u4e3a\u4e0b\u5217\u4e4b\u4e00\uff1a</strong></p> <ol> <li>CSS \u5b9a\u4f4d\u7684\uff08<code>position</code> \u4e3a <code>absolute</code>\uff0c<code>relative</code> \u6216 <code>fixed</code>\uff09</li> <li><code>&lt;td&gt;</code>\uff0c<code>&lt;th&gt;</code>\uff0c<code>&lt;table&gt;</code></li> <li><code>&lt;body&gt;</code></li> </ol> <pre><code class="language-html">&lt;main style=&quot;position: relative&quot; id=&quot;main&quot;&gt;\n  &lt;article&gt;\n    &lt;div id=&quot;example&quot; style=&quot;position: absolute; left: 180px; top: 180px&quot;&gt;...&lt;/div&gt;\n  &lt;/article&gt;\n&lt;/main&gt;\n&lt;script&gt;\n  alert(example.offsetParent.id); // main\n  alert(example.offsetLeft); // 180\uff08\u6ce8\u610f\uff1a\u8fd9\u662f\u4e00\u4e2a\u6570\u5b57\uff0c\u4e0d\u662f\u5b57\u7b26\u4e32 &quot;180px&quot;\uff09\n  alert(example.offsetTop); // 180\n&lt;/script&gt;</code></pre> <h3 id="92-\u4f4d\u7f6e\u76f8\u5173\u5c5e\u6027">9.2 \u4f4d\u7f6e\u76f8\u5173\u5c5e\u6027</h3> <ul> <li><code>offsetLeft/offsetTop</code> \u63d0\u4f9b\u76f8\u5bf9\u4e8e <code>offsetParent</code> \u5de6\u4e0a\u89d2\u7684 <code>x/y</code> \u5750\u6807</li> <li><code>offsetWidth/Height</code> \u5305\u542b <code>border</code> \u7684\u5927\u5c0f</li> <li><code>clientTop/Left</code> \u5185\u4fa7\u4e0e\u5916\u4fa7\u7684\u76f8\u5bf9\u5750\u6807\uff0c\u53ef\u4ee5\u7406\u89e3\u4e3a \u8fb9\u6846+\u6eda\u52a8\u6761 \u7684\u5bbd\u5ea6</li> <li><code>clientWidth/Height</code> \u5143\u7d20 border \u5185\u7684\u5927\u5c0f\uff0c\u5373 <code>content width</code> \u548c <code>padding</code>\uff0c\u4f46\u4e0d\u5305\u62ec\u6eda\u52a8\u6761\u5bbd\u5ea6\u3002\u5982\u679c\u6709\u6eda\u52a8\u6761\u7684\u8bdd\uff0c\u83b7\u53d6\u7684\u5230\u503c\u4f1a\u6bd4 CSS \u8bbe\u7f6e\u7684\u5c0f</li> <li><code>scrollWidth/Height</code> \u5305\u542b\u6eda\u52a8\u9690\u85cf\u90e8\u5206\u7684\u5bbd\u9ad8\uff0c\u4e0d\u5305\u62ec\u6eda\u52a8\u6761\u7684\u5bbd\u5ea6</li> </ul> <blockquote> <p>\u5bf9\u4e8e\u672a\u663e\u793a\u7684\u5143\u7d20\uff0c\u5373 <code>display:none</code> \u6216\u4e0d\u5728\u6587\u6863\u4e2d\uff0c<code>offsetParent</code> \u4e3a <code>null</code>\uff0c\u5e76\u4e14 <code>offsetWidth</code> \u548c <code>offsetHeight</code> \u4e3a 0</p> </blockquote> <p><img src="'+b+'" alt=""></p> <p><img src="'+M+'" alt=""></p> <blockquote> <p>\u4e0d\u8981\u4ece <code>CSS</code> \u4e2d\u83b7\u53d6 <code>width/height</code>\uff0c\u56e0\u4e3a\u4f1a\u53d7\u6eda\u52a8\u6761\u548c <code>box-sizing</code> \u5c5e\u6027\u7684\u5f71\u54cd</p> </blockquote> <h2 id="10-window-\u5927\u5c0f\u548c\u6eda\u52a8">10 Window \u5927\u5c0f\u548c\u6eda\u52a8</h2> <h3 id="101-\u7a97\u53e3\u5bbd\u9ad8">10.1 \u7a97\u53e3\u5bbd\u9ad8</h3> <ul> <li><code>document.documentElement.clientWidth/clientHeight</code> \u83b7\u53d6\u7a97\u53e3\u5927\u5c0f\uff0c\u4e0d\u5305\u542b\u6eda\u52a8\u6761\u5bbd\u5ea6</li> <li><code>window.innerWidth/innerHeight</code> \u83b7\u53d6\u7a97\u53e3\u5927\u5c0f\uff0c\u5305\u542b\u6eda\u52a8\u6761\u5bbd\u5ea6</li> </ul> <h3 id="102-\u6587\u6863\u5bbd\u9ad8">10.2 \u6587\u6863\u5bbd\u9ad8</h3> <p><strong>\u901a\u5e38\u60c5\u51b5</strong> \u53ef\u4ee5\u4f7f\u7528 <code>documentElement.scrollWidth/scrollHeight</code> \u6765\u6d4b\u91cf\u6587\u6863\u7684\u5b8c\u6574\u5927\u5c0f</p> <p>\u4f46\u662f\u4f1a\u6709\u517c\u5bb9\u6027\u95ee\u9898\uff0c\u83b7\u53d6\u5230\u7684\u503c\u4f1a\u4e0d\u51c6\u786e</p> <p>\u9700\u8981\u53bb\u4e0b\u9762\u8fd9\u4e9b\u503c\u7684\u6700\u5927\u503c\uff0c\u624d\u80fd\u51c6\u786e\u83b7\u53d6\u6587\u6863\u5bbd\u9ad8</p> <pre><code class="language-js">let scrollHeight = Math.max(\n  document.body.scrollHeight,\n  document.documentElement.scrollHeight,\n  document.body.offsetHeight,\n  document.documentElement.offsetHeight,\n  document.body.clientHeight,\n  document.documentElement.clientHeight\n);</code></pre> <h3 id="103-\u6eda\u52a8\u6761\u4f4d\u7f6e">10.3 \u6eda\u52a8\u6761\u4f4d\u7f6e</h3> <p><strong>\u83b7\u53d6\u548c\u8bbe\u7f6e\u6eda\u52a8\u6761 (\u6709\u517c\u5bb9\u6027\u95ee\u9898)\uff1a</strong></p> <ul> <li><code>document.documentElement.scrollLeft/scrollTop</code> - \u652f\u6301\u5927\u591a\u6570\u6d4f\u89c8\u5668</li> <li><code>document.body.scrollLeft/scrollTop</code> - \u5c11\u6570\u6d4f\u89c8\u5668</li> </ul> <p><strong>\u65b0\u7684\u65b9\u6cd5 (\u65e7\u6d4f\u89c8\u5668\u4e0d\u652f\u6301)\uff1a</strong></p> <ul> <li><code>scrollBy(x,y)</code> \u5c06\u9875\u9762\u6eda\u52a8\u81f3 \u76f8\u5bf9\u4e8e\u5f53\u524d\u4f4d\u7f6e\u7684 <code>(x, y)</code> \u4f4d\u7f6e\u3002\u4f8b\u5982\uff0c<code>scrollBy(0,10)</code> \u4f1a\u5c06\u9875\u9762\u5411\u4e0b\u6eda\u52a8 <code>10px</code></li> <li><code>scrollTo(pageX,pageY)</code> \u5c06\u9875\u9762\u6eda\u52a8\u81f3\u7edd\u5bf9\u5750\u6807</li> <li><code>elem.scrollIntoView(top)</code> \u5c06\u6eda\u52a8\u9875\u9762\u4ee5\u4f7f <code>elem</code> \u53ef\u89c1<ul> <li>\u5982\u679c <code>top=true</code>\uff08\u9ed8\u8ba4\u503c\uff09\uff0c\u9875\u9762\u6eda\u52a8\uff0c\u4f7f <code>elem</code> \u51fa\u73b0\u5728\u7a97\u53e3\u9876\u90e8\u3002\u5143\u7d20\u7684\u4e0a\u8fb9\u7f18\u5c06\u4e0e\u7a97\u53e3\u9876\u90e8\u5bf9\u9f50</li> <li>\u5982\u679c <code>top=false</code>\uff0c\u9875\u9762\u6eda\u52a8\uff0c\u4f7f <code>elem</code> \u51fa\u73b0\u5728\u7a97\u53e3\u5e95\u90e8\u3002\u5143\u7d20\u7684\u5e95\u90e8\u8fb9\u7f18\u5c06\u4e0e\u7a97\u53e3\u5e95\u90e8\u5bf9\u9f50</li> </ul> </li> </ul> <h2 id="11-\u5750\u6807">11 \u5750\u6807</h2> <h3 id="111-\u4e24\u79cd\u65b9\u5f0f">11.1 \u4e24\u79cd\u65b9\u5f0f</h3> <p>JavaScript \u65b9\u6cd5\u5904\u7406\u7684\u662f\u4ee5\u4e0b\u4e24\u79cd\u5750\u6807\u7cfb\u4e2d\u7684\u4e00\u4e2a:</p> <ul> <li><strong>\u76f8\u5bf9\u4e8e\u7a97\u53e3</strong> \u2014 \u4ece\u7a97\u53e3\u7684\u9876\u90e8/\u5de6\u4fa7\u8fb9\u7f18\u8ba1\u7b97\u5f97\u51fa\uff0c\u6211\u4eec\u5c06\u8fd9\u4e9b\u5750\u6807\u8868\u793a\u4e3a <code>clientX/clientY</code></li> <li><strong>\u76f8\u5bf9\u4e8e\u6587\u6863</strong> \u2014 \u4ece\u6587\u6863\u7684\u9876\u90e8/\u5de6\u4fa7\u8fb9\u7f18\u8ba1\u7b97\u5f97\u51fa\uff0c\u6211\u4eec\u5c06\u5b83\u4eec\u8868\u793a\u4e3a <code>pageX/pageY</code></li> </ul> <p><img src="'+q+'" alt=""></p> <h3 id="112-\u5143\u7d20\u5750\u6807">11.2 \u5143\u7d20\u5750\u6807</h3> <p>\u65b9\u6cd5 <code>elem.getBoundingClientRect()</code> \u8fd4\u56de\u6700\u5c0f\u77e9\u5f62\u7684\u7a97\u53e3\u5750\u6807\uff0c\u8be5\u77e9\u5f62\u5c06 <code>elem</code> \u4f5c\u4e3a\u5185\u5efa <code>DOMRect</code> \u7c7b\u7684\u5bf9\u8c61</p> <ul> <li><code>x/y</code> \u2014 \u77e9\u5f62\u539f\u70b9\u76f8\u5bf9\u4e8e\u7a97\u53e3\u7684 X/Y \u5750\u6807</li> <li><code>width/height</code> \u2014 \u77e9\u5f62\u7684 width/height\uff08\u53ef\u4ee5\u4e3a\u8d1f\uff09</li> <li><code>top/bottom</code> \u2014 \u9876\u90e8/\u5e95\u90e8\u77e9\u5f62\u8fb9\u7f18\u7684 Y \u5750\u6807</li> <li><code>left/right</code> \u2014 \u5de6/\u53f3\u77e9\u5f62\u8fb9\u7f18\u7684 X \u5750\u6807</li> </ul> <p><img src="'+f+'" alt=""></p> <blockquote> <p>\u7531\u4e8e\u5386\u53f2\u539f\u56e0\uff0c<strong>IE \u6d4f\u89c8\u5668\u4e0d\u652f\u6301 x/y \u5c5e\u6027</strong></p> </blockquote> <h3 id="113-\u83b7\u53d6\u5750\u6807\u5904\u7684\u5143\u7d20">11.3 \u83b7\u53d6\u5750\u6807\u5904\u7684\u5143\u7d20</h3> <p><code>document.elementFromPoint(x, y)</code> \u4f1a\u8fd4\u56de\u5728\u7a97\u53e3\u5750\u6807 <code>(x, y)</code> \u5904\u5d4c\u5957\u6700\u591a\u7684\u5143\u7d20</p> <pre><code class="language-js">let elem = document.elementFromPoint(x, y);</code></pre> <blockquote> <p>\u5bf9\u4e8e\u5728\u7a97\u53e3\u4e4b\u5916\u7684\u5750\u6807\uff0c<code>elementFromPoint</code> \u8fd4\u56de <code>null</code> <br> \u65b9\u6cd5 <code>document.elementFromPoint(x,y)</code> \u53ea\u5bf9\u5728\u53ef\u89c1\u533a\u57df\u5185\u7684\u5750\u6807 <code>(x,y)</code> \u8d77\u4f5c\u7528</p> </blockquote> <h3 id="114-\u6587\u6863\u5750\u6807">11.4 \u6587\u6863\u5750\u6807</h3> <p>\u6587\u6863\u76f8\u5bf9\u5750\u6807\u4ece\u6587\u6863\u7684\u5de6\u4e0a\u89d2\u5f00\u59cb\u8ba1\u7b97\uff0c\u800c\u4e0d\u662f\u7a97\u53e3</p> <p>\u6ca1\u6709\u6807\u51c6\u65b9\u6cd5\u6765\u83b7\u53d6\u5143\u7d20\u7684\u6587\u6863\u5750\u6807\uff0c\u4f46\u662f\u53ef\u4ee5\u901a\u8fc7\u5176\u4ed6\u65b9\u5f0f\u83b7\u53d6</p> <pre><code>pageY = clientY + \u6587\u6863\u7684\u5782\u76f4\u6eda\u52a8\u51fa\u7684\u90e8\u5206\u7684\u9ad8\u5ea6\npageX = clientX + \u6587\u6863\u7684\u6c34\u5e73\u6eda\u52a8\u51fa\u7684\u90e8\u5206\u7684\u5bbd\u5ea6</code></pre><pre><code class="language-js">// \u83b7\u53d6\u5143\u7d20\u7684\u6587\u6863\u5750\u6807\nfunction getCoords(elem) {\n  let box = elem.getBoundingClientRect();\n\n  return {\n    top: box.top + window.pageYOffset,\n    right: box.right + window.pageXOffset,\n    bottom: box.bottom + window.pageYOffset,\n    left: box.left + window.pageXOffset\n  };\n}</code></pre> ';e.exports=y}}]);