(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["docs/browser/file"],{LEYQ:function(e,o,d){var c=d("Rxnk"),r=d("+Toj"),l=d("cuxH"),t=c(r),n=c(l),a='<h1 id="\u4e8c\u8fdb\u5236\u6570\u636e\u548c\u6587\u4ef6">\u4e8c\u8fdb\u5236\u6570\u636e\u548c\u6587\u4ef6</h1> <h2 id="1-\u4e8c\u8fdb\u5236\u6570\u7ec4">1 \u4e8c\u8fdb\u5236\u6570\u7ec4</h2> <p>\u5904\u7406\u6587\u4ef6\u65f6\uff08\u521b\u5efa\uff0c\u4e0a\u4f20\uff0c\u4e0b\u8f7d\uff09\uff0c\u7ecf\u5e38\u4f1a\u9047\u5230\u4e8c\u8fdb\u5236\u6570\u636e\u3002\u53e6\u4e00\u4e2a\u5178\u578b\u7684\u5e94\u7528\u573a\u666f\u662f\u56fe\u50cf\u5904\u7406</p> <p>\u8fd9\u4e9b\u90fd\u53ef\u4ee5\u901a\u8fc7 JavaScript \u8fdb\u884c\u5904\u7406\uff0c\u800c\u4e14\u4e8c\u8fdb\u5236\u64cd\u4f5c\u6027\u80fd\u66f4\u9ad8</p> <blockquote> <p>\u4e0e\u5176\u4ed6\u8bed\u8a00\u76f8\u6bd4\uff0cJavaScript \u4e2d\u7684\u4e8c\u8fdb\u5236\u6570\u636e\u662f\u4ee5\u975e\u6807\u51c6\u65b9\u5f0f\u5b9e\u73b0\u7684</p> </blockquote> <h3 id="11-arraybuffer">1.1 ArrayBuffer</h3> <p>\u57fa\u672c\u7684\u4e8c\u8fdb\u5236\u5bf9\u8c61\u662f <code>ArrayBuffer</code> \u2014\u2014 \u5bf9\u56fa\u5b9a\u957f\u5ea6\u7684\u8fde\u7eed\u5185\u5b58\u7a7a\u95f4\u7684\u5f15\u7528</p> <pre><code class="language-js">let buffer = new ArrayBuffer(16); // \u521b\u5efa\u4e00\u4e2a\u957f\u5ea6\u4e3a 16 \u7684 buffer\nalert(buffer.byteLength); // 16</code></pre> <p>\u5b83\u4f1a\u5206\u914d\u4e00\u4e2a 16 \u5b57\u8282\u7684\u8fde\u7eed\u5185\u5b58\u7a7a\u95f4\uff0c\u5e76\u7528 0 \u8fdb\u884c\u9884\u586b\u5145</p> <blockquote> <p><code>ArrayBuffer</code> \u4e0d\u662f\u67d0\u79cd\u4e1c\u897f\u7684\u6570\u7ec4\u3002<code>ArrayBuffer</code> \u4e0e <code>Array</code> \u6ca1\u6709\u4efb\u4f55\u5171\u540c\u4e4b\u5904</p> </blockquote> <ul> <li>\u5b83\u7684\u957f\u5ea6\u662f\u56fa\u5b9a\u7684\uff0c\u6211\u4eec\u65e0\u6cd5\u589e\u52a0\u6216\u51cf\u5c11\u5b83\u7684\u957f\u5ea6</li> <li>\u5b83\u6b63\u597d\u5360\u7528\u4e86\u5185\u5b58\u4e2d\u7684\u90a3\u4e48\u591a\u7a7a\u95f4</li> <li>\u8981\u8bbf\u95ee\u5355\u4e2a\u5b57\u8282\uff0c\u9700\u8981\u53e6\u4e00\u4e2a\u89c6\u56fe\u5bf9\u8c61\uff0c\u800c\u4e0d\u662f <code>buffer[index]</code></li> </ul> <p><code>ArrayBuffer</code> \u662f\u4e00\u4e2a\u5185\u5b58\u533a\u57df\u3002\u5b83\u91cc\u9762\u5b58\u50a8\u4e86\u4ec0\u4e48\u65e0\u4ece\u5224\u65ad\u3002\u53ea\u662f\u4e00\u4e2a\u539f\u59cb\u7684\u5b57\u8282\u5e8f\u5217</p> <p>\u5982\u8981\u64cd\u4f5c <code>ArrayBuffer</code>\uff0c\u6211\u4eec\u9700\u8981\u4f7f\u7528\u89c6\u56fe\u5bf9\u8c61</p> <p>\u89c6\u56fe\u5bf9\u8c61\u672c\u8eab\u5e76\u4e0d\u5b58\u50a8\u4efb\u4f55\u4e1c\u897f\u3002\u5b83\u662f\u4e00\u526f\u773c\u955c\uff0c\u900f\u8fc7\u5b83\u6765\u89e3\u91ca\u5b58\u50a8\u5728 <code>ArrayBuffer</code> \u4e2d\u7684\u5b57\u8282</p> <p>\u4f8b\u5982\uff1a</p> <ul> <li><code>Uint8Array</code> \u2014\u2014 \u5c06 <code>ArrayBuffer</code> \u4e2d\u7684\u6bcf\u4e2a\u5b57\u8282\u89c6\u4e3a <code>0</code> \u5230 <code>255</code> \u4e4b\u95f4\u7684\u5355\u4e2a\u6570\u5b57\uff08\u6bcf\u4e2a\u5b57\u8282\u662f 8 \u4f4d\uff0c\u56e0\u6b64\u53ea\u80fd\u5bb9\u7eb3\u90a3\u4e48\u591a\uff09\u3002\u8fd9\u79f0\u4e3a \u201c8 \u4f4d\u65e0\u7b26\u53f7\u6574\u6570\u201d</li> <li><code>Uint16Array</code> \u2014\u2014 \u5c06\u6bcf <code>2</code> \u4e2a\u5b57\u8282\u89c6\u4e3a\u4e00\u4e2a <code>0</code> \u5230 <code>65535</code> \u4e4b\u95f4\u7684\u6574\u6570\u3002\u8fd9\u79f0\u4e3a \u201c16 \u4f4d\u65e0\u7b26\u53f7\u6574\u6570\u201d</li> <li><code>Uint32Array</code> \u2014\u2014 \u5c06\u6bcf <code>4</code> \u4e2a\u5b57\u8282\u89c6\u4e3a\u4e00\u4e2a 0 \u5230 <code>4294967295</code> \u4e4b\u95f4\u7684\u6574\u6570\u3002\u8fd9\u79f0\u4e3a \u201c32 \u4f4d\u65e0\u7b26\u53f7\u6574\u6570\u201d</li> <li><code>Float64Array</code> \u2014\u2014 \u5c06\u6bcf <code>8</code> \u4e2a\u5b57\u8282\u89c6\u4e3a\u4e00\u4e2a <code>5.0x10^-324</code> \u5230 <code>1.8x10^308</code> \u4e4b\u95f4\u7684\u6d6e\u70b9\u6570\u3002</li> </ul> <p><img src="'+t+'" alt=""></p> <blockquote> <p><code>ArrayBuffer</code> \u662f\u6838\u5fc3\u5bf9\u8c61\uff0c\u662f\u6240\u6709\u7684\u57fa\u7840\uff0c\u662f\u539f\u59cb\u7684\u4e8c\u8fdb\u5236\u6570\u636e\u3002\u4f46\u662f\uff0c\u5982\u679c\u6211\u4eec\u8981\u5199\u5165\u503c\u6216\u904d\u5386\u5b83\uff0c\u57fa\u672c\u4e0a\u51e0\u4e4e\u6240\u6709\u64cd\u4f5c \u2014\u2014 \u6211\u4eec\u5fc5\u987b\u4f7f\u7528\u89c6\u56fe\uff08view\uff09</p> </blockquote> <pre><code class="language-js">let buffer = new ArrayBuffer(16); // \u521b\u5efa\u4e00\u4e2a\u957f\u5ea6\u4e3a 16 \u7684 buffer\n\nlet view = new Uint32Array(buffer); // \u5c06 buffer \u89c6\u4e3a\u4e00\u4e2a 32 \u4f4d\u6574\u6570\u7684\u5e8f\u5217\n\nalert(Uint32Array.BYTES_PER_ELEMENT); // \u6bcf\u4e2a\u6574\u6570 4 \u4e2a\u5b57\u8282\n\nalert(view.length); // 4\uff0c\u5b83\u5b58\u50a8\u4e86 4 \u4e2a\u6574\u6570\nalert(view.byteLength); // 16\uff0c\u5b57\u8282\u4e2d\u7684\u5927\u5c0f\n\n// \u5199\u5165\u4e00\u4e2a\u503c\nview[0] = 123456;\n\n// \u904d\u5386\u503c\nfor (let num of view) {\n  alert(num); // 123456\uff0c\u7136\u540e 0\uff0c0\uff0c0\uff08\u4e00\u5171 4 \u4e2a\u503c\uff09\n}</code></pre> <h3 id="12-typedarray">1.2 TypedArray</h3> <p>\u6240\u6709\u8fd9\u4e9b\u89c6\u56fe\uff08<code>Uint8Array</code>\uff0c<code>Uint32Array</code> \u7b49\uff09\u7684\u901a\u7528\u672f\u8bed\u662f <code>TypedArray</code>\u3002\u5b83\u4eec\u90fd\u4eab\u6709\u540c\u4e00\u7ec4\u65b9\u6cd5\u548c\u5c5e\u6027</p> <p><code>TypedArray</code> \u53ea\u662f\u8868\u793a ArrayBuffer \u4e0a\u7684\u89c6\u56fe\u4e4b\u4e00\u7684\u901a\u7528\u603b\u79f0\u672f\u8bed\uff1a<code>Int8Array</code>\uff0c<code>Uint8Array</code> \u53ca\u5176\u4ed6\u3002\u5728\u8fd9\u91cc\u662f\u4e00\u4e2a\u4ee3\u79f0\uff0c\u4e0d\u662f\u6784\u9020\u5668</p> <p><strong>\u7c7b\u578b\u5316\u6570\u7ec4 (<code>TypedArray</code>)\uff1a</strong></p> <ul> <li><code>Uint8Array</code>\uff0c<code>Uint16Array</code>\uff0c<code>Uint32Array</code> \u2014\u2014 \u7528\u4e8e 8\u300116 \u548c 32 \u4f4d\u7684\u6574\u6570<ul> <li><code>Uint8ClampedArray</code> \u2014\u2014 \u7528\u4e8e 8 \u4f4d\u6574\u6570\uff0c\u5728\u8d4b\u503c\u65f6\u4fbf\u201c\u56fa\u5b9a\u201c\u5176\u503c</li> </ul> </li> <li><code>Int8Array</code>\uff0c<code>Int16Array</code>\uff0c<code>Int32Array</code> \u2014\u2014 \u7528\u4e8e\u6709\u7b26\u53f7\u6574\u6570\uff08\u53ef\u4ee5\u4e3a\u8d1f\u6570\uff09</li> <li><code>Float32Array</code>\uff0c<code>Float64Array</code> \u2014\u2014 \u7528\u4e8e 32 \u4f4d\u548c 64 \u4f4d\u7684\u6709\u7b26\u53f7\u6d6e\u70b9\u6570</li> </ul> <p><strong>\u4e00\u4e2a\u7c7b\u578b\u5316\u6570\u7ec4\u7684\u6784\u9020\u5668\u6839\u636e\u4e0d\u540c\u7684\u53c2\u6570\u4f1a\u6709\u4e0d\u540c\u7684\u884c\u4e3a\uff1a</strong></p> <pre><code class="language-js">new TypedArray(buffer, [byteOffset], [length]);\nnew TypedArray(object);\nnew TypedArray(typedArray);\nnew TypedArray(length);\nnew TypedArray();</code></pre> <p><strong>\u53c2\u6570\u4e3a ArrayBuffer\uff1a</strong></p> <p>\u5982\u679c\u7ed9\u5b9a\u7684\u662f <code>ArrayBuffer</code> \u53c2\u6570\uff0c\u5219\u4f1a\u5728\u5176\u4e0a\u521b\u5efa\u89c6\u56fe</p> <p>\u53ef\u4ee5\u7ed9\u5b9a\u8d77\u59cb\u4f4d\u7f6e <code>byteOffset</code>\uff08\u9ed8\u8ba4\u4e3a 0\uff09\u4ee5\u53ca <code>length</code>\uff08\u9ed8\u8ba4\u81f3 <code>buffer</code> \u7684\u672b\u5c3e\uff09\uff0c\u8fd9\u6837\u89c6\u56fe\u5c06\u4ec5\u6db5\u76d6 <code>buffer</code> \u7684\u4e00\u90e8\u5206</p> <p><strong>\u53c2\u6570\u4e3a Array\uff1a</strong></p> <p>\u5982\u679c\u7ed9\u5b9a\u7684\u662f <code>Array</code>\uff0c\u6216\u4efb\u4f55\u7c7b\u6570\u7ec4\u5bf9\u8c61\uff0c\u5219\u4f1a\u521b\u5efa\u4e00\u4e2a\u76f8\u540c\u957f\u5ea6\u7684\u7c7b\u578b\u5316\u6570\u7ec4\uff0c\u5e76\u590d\u5236\u5176\u5185\u5bb9</p> <pre><code class="language-js">let arr = new Uint8Array([0, 1, 2, 3]);\nalert(arr.length); // 4\uff0c\u521b\u5efa\u4e86\u76f8\u540c\u957f\u5ea6\u7684\u4e8c\u8fdb\u5236\u6570\u7ec4\nalert(arr[1]); // 1\uff0c\u7528\u7ed9\u5b9a\u503c\u586b\u5145\u4e86 4 \u4e2a\u5b57\u8282\uff08\u65e0\u7b26\u53f7 8 \u4f4d\u6574\u6570\uff09</code></pre> <p><strong>\u53c2\u6570\u4e3a TypedArray\uff1a</strong></p> <p>\u4f1a\u521b\u5efa\u4e00\u4e2a\u76f8\u540c\u957f\u5ea6\u7684\u7c7b\u578b\u5316\u6570\u7ec4\uff0c\u5e76\u590d\u5236\u5176\u5185\u5bb9\u3002\u5982\u679c\u9700\u8981\u7684\u8bdd\uff0c\u6570\u636e\u5728\u6b64\u8fc7\u7a0b\u4e2d\u4f1a\u88ab\u8f6c\u6362\u4e3a\u65b0\u7684\u7c7b\u578b</p> <pre><code class="language-js">let arr16 = new Uint16Array([1, 1000]);\nlet arr8 = new Uint8Array(arr16);\nalert(arr8[0]); // 1\nalert(arr8[1]); // 232\uff0c\u8bd5\u56fe\u590d\u5236 1000\uff0c\u4f46\u65e0\u6cd5\u5c06 1000 \u653e\u8fdb 8 \u4f4d\u5b57\u8282\u4e2d</code></pre> <p><strong>\u53c2\u6570\u4e3a length\uff1a</strong></p> <p>\u521b\u5efa\u5305\u542b <code>length</code> \u4e2a\u5143\u7d20\u7684\u7c7b\u578b\u5316\u6570\u7ec4\u3002\u5b83\u7684\u5b57\u8282\u957f\u5ea6\u5c06\u662f <code>length</code> \u4e58\u4ee5\u5355\u4e2a <code>TypedArray.BYTES_PER_ELEMENT</code> \u4e2d\u7684\u5b57\u8282\u6570\uff1a</p> <pre><code class="language-js">let arr = new Uint16Array(4); // \u4e3a 4 \u4e2a\u6574\u6570\u521b\u5efa\u7c7b\u578b\u5316\u6570\u7ec4\nalert(Uint16Array.BYTES_PER_ELEMENT); // \u6bcf\u4e2a\u6574\u6570 2 \u4e2a\u5b57\u8282\nalert(arr.byteLength); // 8\uff08\u5b57\u8282\u4e2d\u7684\u5927\u5c0f\uff09</code></pre> <p><strong>\u4e0d\u5e26\u53c2\u6570\uff1a</strong></p> <p>\u4e0d\u5e26\u53c2\u6570\u7684\u60c5\u51b5\u4e0b\uff0c\u521b\u5efa\u957f\u5ea6\u4e3a\u96f6\u7684\u7c7b\u578b\u5316\u6570\u7ec4</p> <blockquote> <p>\u53ef\u4ee5\u76f4\u63a5\u521b\u5efa\u4e00\u4e2a <code>TypedArray</code>\uff0c\u800c\u65e0\u9700\u63d0\u53ca <code>ArrayBuffer</code>\u3002\u9664\u7b2c\u4e00\u79cd\u60c5\u51b5\uff08\u5df2\u63d0\u4f9b <code>ArrayBuffer</code>\uff09\u5916\uff0c\u5176\u4ed6\u6240\u6709\u60c5\u51b5\u90fd\u4f1a\u81ea\u52a8\u521b\u5efa <code>ArrayBuffer</code></p> </blockquote> <p>\u8bbf\u95ee <code>ArrayBuffer</code>\uff0c\u53ef\u4ee5\u7528\u4ee5\u4e0b\u5c5e\u6027\uff1a</p> <ul> <li><code>arr.buffer</code> \u2014\u2014 \u5f15\u7528 <code>ArrayBuffer</code></li> <li><code>arr.byteLength</code> \u2014\u2014 <code>ArrayBuffer</code> \u7684\u957f\u5ea6</li> </ul> <h3 id="13-typedarray-\u65b9\u6cd5">1.3 TypedArray \u65b9\u6cd5</h3> <p><code>TypedArray</code> \u5177\u6709\u5927\u90e8\u5206\u5e38\u89c4\u7684 <code>Array</code> \u65b9\u6cd5</p> <p><strong>\u4e0d\u53ef\u7528\u7684\u65b9\u6cd5\uff1a</strong></p> <ul> <li><code>splice</code> \u2014\u2014 \u65e0\u6cd5\u5220\u9664\u4e00\u4e2a\u503c\uff0c\u56e0\u4e3a\u7c7b\u578b\u5316\u6570\u7ec4\u662f\u7f13\u51b2\u533a\uff08buffer\uff09\u4e0a\u7684\u89c6\u56fe\uff0c\u5e76\u4e14\u7f13\u51b2\u533a\uff08buffer\uff09\u662f\u56fa\u5b9a\u7684\u3001\u8fde\u7eed\u7684\u5185\u5b58\u533a\u57df</li> <li><code>concat</code> \u65b9\u6cd5</li> </ul> <p><strong>\u989d\u5916\u7684\u65b9\u6cd5\uff1a</strong></p> <ul> <li><code>arr.set(fromArr, [offset])</code> \u4ece <code>offset</code>\uff08\u9ed8\u8ba4\u4e3a 0\uff09\u5f00\u59cb\uff0c\u5c06 <code>fromArr</code> \u4e2d\u7684\u6240\u6709\u5143\u7d20\u590d\u5236\u5230 <code>arr</code></li> <li><code>arr.subarray([begin, end])</code> \u521b\u5efa\u4e00\u4e2a\u4ece <code>begin</code> \u5230 <code>end</code>\uff08\u4e0d\u5305\u62ec\uff09\u76f8\u540c\u7c7b\u578b\u7684\u65b0\u89c6\u56fe\u3002\u8fd9\u7c7b\u4f3c\u4e8e <code>slice</code> \u65b9\u6cd5\uff0c\u4f46\u4e0d\u590d\u5236\u4efb\u4f55\u5185\u5bb9 \u2014\u2014 \u53ea\u662f\u521b\u5efa\u4e00\u4e2a\u65b0\u89c6\u56fe\uff0c\u4ee5\u5bf9\u7ed9\u5b9a\u7247\u6bb5\u7684\u6570\u636e\u8fdb\u884c\u64cd\u4f5c</li> </ul> <h3 id="14-dataview">1.4 DataView</h3> <p><code>DataView</code> \u662f\u5728 <code>ArrayBuffer</code> \u4e0a\u7684\u4e00\u79cd\u7279\u6b8a\u7684\u8d85\u7075\u6d3b \u201c\u672a\u7c7b\u578b\u5316\u201d \u89c6\u56fe\u3002\u5b83\u5141\u8bb8\u4ee5\u4efb\u4f55\u683c\u5f0f\u8bbf\u95ee\u4efb\u4f55\u504f\u79fb\u91cf\uff08offset\uff09\u7684\u6570\u636e</p> <ul> <li>\u5bf9\u4e8e\u7c7b\u578b\u5316\u7684\u6570\u7ec4\uff0c\u6784\u9020\u5668\u51b3\u5b9a\u4e86\u5176\u683c\u5f0f\u3002\u6574\u4e2a\u6570\u7ec4\u5e94\u8be5\u662f\u7edf\u4e00\u7684\u3002\u7b2c <code>i</code> \u4e2a\u6570\u5b57\u662f <code>arr[i]</code></li> <li>\u901a\u8fc7 <code>DataView</code>\uff0c\u6211\u4eec\u53ef\u4ee5\u4f7f\u7528 <code>.getUint8(i)</code> \u6216 <code>.getUint16(i)</code> \u4e4b\u7c7b\u7684\u65b9\u6cd5\u8bbf\u95ee\u6570\u636e\u3002\u6211\u4eec\u5728\u8c03\u7528\u65b9\u6cd5\u65f6\u9009\u62e9\u683c\u5f0f\uff0c\u800c\u4e0d\u662f\u5728\u6784\u9020\u7684\u65f6\u5019</li> </ul> <p><strong>\u8bed\u6cd5\uff1a</strong></p> <pre><code class="language-js">new DataView(buffer, [byteOffset], [byteLength]);</code></pre> <ul> <li><code>buffer</code> \u2014\u2014 \u5e95\u5c42\u7684 <code>ArrayBuffer</code>\u3002\u4e0e\u7c7b\u578b\u5316\u6570\u7ec4\u4e0d\u540c\uff0c<code>DataView</code> \u4e0d\u4f1a\u81ea\u884c\u521b\u5efa\u7f13\u51b2\u533a\uff08buffer\uff09\u3002\u6211\u4eec\u9700\u8981\u4e8b\u5148\u51c6\u5907\u597d</li> <li><code>byteOffset</code> \u2014\u2014 \u89c6\u56fe\u7684\u8d77\u59cb\u5b57\u8282\u4f4d\u7f6e\uff08\u9ed8\u8ba4\u4e3a 0\uff09</li> <li><code>byteLength</code> \u2014\u2014 \u89c6\u56fe\u7684\u5b57\u8282\u957f\u5ea6\uff08\u9ed8\u8ba4\u81f3 buffer \u7684\u672b\u5c3e\uff09</li> </ul> <p>\u4f8b\u5982\uff0c\u8fd9\u91cc\u6211\u4eec\u4ece\u540c\u4e00\u4e2a buffer \u4e2d\u63d0\u53d6\u4e0d\u540c\u683c\u5f0f\u7684\u6570\u5b57\uff1a</p> <pre><code class="language-js">// 4 \u4e2a\u5b57\u8282\u7684\u4e8c\u8fdb\u5236\u6570\u7ec4\uff0c\u6bcf\u4e2a\u90fd\u662f\u6700\u5927\u503c 255\nlet buffer = new Uint8Array([255, 255, 255, 255]).buffer;\n\nlet dataView = new DataView(buffer);\n\n// \u5728\u504f\u79fb\u91cf\u4e3a 0 \u5904\u83b7\u53d6 8 \u4f4d\u6570\u5b57\nalert(dataView.getUint8(0)); // 255\n\n// \u73b0\u5728\u5728\u504f\u79fb\u91cf\u4e3a 0 \u5904\u83b7\u53d6 16 \u4f4d\u6570\u5b57\uff0c\u5b83\u7531 2 \u4e2a\u5b57\u8282\u7ec4\u6210\uff0c\u4e00\u8d77\u89e3\u6790\u4e3a 65535\nalert(dataView.getUint16(0)); // 65535\uff08\u6700\u5927\u7684 16 \u4f4d\u65e0\u7b26\u53f7\u6574\u6570\uff09\n\n// \u5728\u504f\u79fb\u91cf\u4e3a 0 \u5904\u83b7\u53d6 32 \u4f4d\u6570\u5b57\nalert(dataView.getUint32(0)); // 4294967295\uff08\u6700\u5927\u7684 32 \u4f4d\u65e0\u7b26\u53f7\u6574\u6570\uff09\n\ndataView.setUint32(0, 0); // \u5c06 4 \u4e2a\u5b57\u8282\u7684\u6570\u5b57\u8bbe\u4e3a 0\uff0c\u5373\u5c06\u6240\u6709\u5b57\u8282\u90fd\u8bbe\u4e3a 0</code></pre> <p>\u5f53\u6211\u4eec\u5c06\u6df7\u5408\u683c\u5f0f\u7684\u6570\u636e\u5b58\u50a8\u5728\u540c\u4e00\u7f13\u51b2\u533a\uff08buffer\uff09\u4e2d\u65f6\uff0c<code>DataView</code> \u975e\u5e38\u6709\u7528\u3002\u4f8b\u5982\uff0c\u5f53\u6211\u4eec\u5b58\u50a8\u4e00\u4e2a\u6210\u5bf9\u5e8f\u5217\uff0816 \u4f4d\u6574\u6570\uff0c32 \u4f4d\u6d6e\u70b9\u6570\uff09\u65f6\uff0c\u7528 <code>DataView</code> \u53ef\u4ee5\u8f7b\u677e\u8bbf\u95ee\u5b83\u4eec</p> <h2 id="2-textdecoder">2 TextDecoder</h2> <p>\u5982\u679c\u4e8c\u8fdb\u5236\u6570\u636e\u5b9e\u9645\u4e0a\u4e3a\u5b57\u7b26\u4e32\uff0c\u4f8b\u5982\u4e00\u4e2a\u5185\u5bb9\u4e3a\u6587\u672c\u7684\u6587\u4ef6\uff0c\u53ef\u4ee5\u7528 <code>TextDecoder</code> \u548c <code>TextEncoder</code> \u5904\u7406</p> <h3 id="21-textdecoder">2.1 TextDecoder</h3> <p>\u5185\u5efa\u7684 <code>TextDecoder</code> \u5bf9\u8c61\u5728\u7ed9\u5b9a\u7f13\u51b2\u533a\uff08buffer\uff09\u548c\u7f16\u7801\u683c\u5f0f\uff08encoding\uff09\u7684\u60c5\u51b5\u4e0b\uff0c\u80fd\u591f\u5c06\u503c\u8bfb\u53d6\u5230\u5b9e\u9645\u7684 <code>JavaScript</code> \u5b57\u7b26\u4e32\u4e2d</p> <pre><code class="language-js">let decoder = new TextDecoder([label], [options]);</code></pre> <ul> <li><code>label</code> \u2014\u2014 \u7f16\u7801\u683c\u5f0f\uff0c\u9ed8\u8ba4\u4e3a <code>utf-8</code>\uff0c\u4f46\u540c\u65f6\u4e5f\u652f\u6301 <code>big5</code>\uff0c<code>windows-1251</code> \u7b49\u8bb8\u591a\u5176\u4ed6\u7f16\u7801\u683c\u5f0f</li> <li><code>options</code> \u2014\u2014 \u53ef\u9009\u5bf9\u8c61\uff1a<ul> <li><code>fatal</code> \u2014\u2014 \u5e03\u5c14\u503c\uff0c\u5982\u679c\u4e3a <code>true</code> \u5219\u4e3a\u65e0\u6548\uff08\u4e0d\u53ef\u89e3\u7801\uff09\u5b57\u7b26\u629b\u51fa\u5f02\u5e38\uff0c\u5426\u5219\uff08\u9ed8\u8ba4\uff09\u7528\u5b57\u7b26 <code>\\uFFFD</code> \u66ff\u6362\u65e0\u6548\u5b57\u7b26</li> <li><code>ignoreBOM</code> \u2014\u2014 \u5e03\u5c14\u503c\uff0c\u5982\u679c\u4e3a <code>true</code> \u5219 <code>BOM</code>\uff08\u53ef\u9009\u7684\u5b57\u8282\u987a\u5e8f unicode \u6807\u8bb0\uff09\uff0c\u5f88\u5c11\u9700\u8981\u4f7f\u7528</li> </ul> </li> </ul> <pre><code class="language-js">let str = decoder.decode([input], [options]);</code></pre> <ul> <li><code>input</code> \u2014\u2014 \u8981\u88ab\u89e3\u7801\u7684 <code>BufferSource</code></li> <li><code>options</code> \u2014\u2014 \u53ef\u9009\u5bf9\u8c61\uff1a<ul> <li><code>stream</code> \u2014\u2014 \u5bf9\u4e8e\u89e3\u7801\u6d41\uff0c\u4e3a <code>true</code>\uff0c\u5219\u5c06\u4f20\u5165\u7684\u6570\u636e\u5757\uff08chunk\uff09\u4f5c\u4e3a\u53c2\u6570\u91cd\u590d\u8c03\u7528 <code>decoder</code>\u3002\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u591a\u5b57\u8282\u7684\u5b57\u7b26\u53ef\u80fd\u5076\u5c14\u4f1a\u5728\u5757\u4e0e\u5757\u4e4b\u95f4\u88ab\u5206\u5272\u3002\u8fd9\u4e2a\u9009\u9879\u544a\u8bc9 <code>TextDecoder</code> \u8bb0\u4f4f\u672a\u5b8c\u6210\u7684\u5b57\u7b26\uff0c\u5e76\u5728\u4e0b\u4e00\u4e2a\u6570\u636e\u5757\u6765\u7684\u65f6\u5019\u8fdb\u884c\u89e3\u7801</li> </ul> </li> </ul> <p><strong>\u4f8b\u5982\uff1a</strong></p> <pre><code class="language-js">let uint8Array = new Uint8Array([72, 101, 108, 108, 111]);\n\nalert(new TextDecoder().decode(uint8Array)); // Hello</code></pre> <pre><code class="language-js">let uint8Array = new Uint8Array([228, 189, 160, 229, 165, 189]);\n\nalert(new TextDecoder().decode(uint8Array)); // \u4f60\u597d</code></pre> <p>\u6211\u4eec\u53ef\u4ee5\u901a\u8fc7\u4e3a\u5176\u521b\u5efa\u5b50\u6570\u7ec4\u89c6\u56fe\u6765\u89e3\u7801\u90e8\u5206\u7f13\u51b2\u533a\uff1a</p> <pre><code class="language-js">let uint8Array = new Uint8Array([0, 72, 101, 108, 108, 111, 0]);\n\n// \u8be5\u5b57\u7b26\u4e32\u4f4d\u4e8e\u4e2d\u95f4\n// \u5728\u4e0d\u590d\u5236\u4efb\u4f55\u5185\u5bb9\u7684\u524d\u63d0\u4e0b\uff0c\u521b\u5efa\u4e00\u4e2a\u65b0\u7684\u89c6\u56fe\nlet binaryString = uint8Array.subarray(1, -1);\n\nalert(new TextDecoder().decode(binaryString)); // Hello</code></pre> <h3 id="22-textencoder">2.2 TextEncoder</h3> <p><code>TextEncoder</code> \u505a\u76f8\u53cd\u7684\u4e8b\u60c5 \u2014\u2014 \u5c06\u5b57\u7b26\u4e32\u8f6c\u6362\u4e3a\u5b57\u8282</p> <p>\u53ea\u652f\u6301 <code>utf-8</code> \u7f16\u7801</p> <pre><code class="language-js">let encoder = new TextEncoder();</code></pre> <p>\u5b83\u6709\u4e24\u79cd\u65b9\u6cd5\uff1a</p> <ul> <li><code>encode(str)</code> \u2014\u2014 \u4ece\u5b57\u7b26\u4e32\u8fd4\u56de <code>Uint8Array</code></li> <li><code>encodeInto(str, destination)</code> \u2014\u2014 \u5c06 <code>str</code> \u7f16\u7801\u5230 <code>destination</code> \u4e2d\uff0c\u8be5\u76ee\u6807\u5fc5\u987b\u4e3a <code>Uint8Array</code></li> </ul> <pre><code class="language-js">let encoder = new TextEncoder();\n\nlet uint8Array = encoder.encode(&#39;Hello&#39;);\nalert(uint8Array); // 72,101,108,108,111</code></pre> <h2 id="3-blob">3 Blob</h2> <p><code>Blob</code> \u7531\u4e00\u4e2a\u53ef\u9009\u7684\u5b57\u7b26\u4e32 <code>type</code>\uff08\u901a\u5e38\u662f <code>MIME</code> \u7c7b\u578b\uff09\u548c <code>blobParts</code> \u7ec4\u6210 \u2014\u2014 \u4e00\u7cfb\u5217\u5176\u4ed6 <code>Blob</code> \u5bf9\u8c61\uff0c\u5b57\u7b26\u4e32\u548c <code>BufferSource</code></p> <p><img src="'+n+'" alt=""></p> <p><strong>\u8bed\u6cd5\uff1a</strong></p> <pre><code class="language-js">new Blob(blobParts, options);</code></pre> <ul> <li><code>blobParts</code> \u662f <code>Blob/BufferSource/String</code> \u7c7b\u578b\u7684\u503c\u7684\u6570\u7ec4</li> <li><code>options</code> \u53ef\u9009\u5bf9\u8c61\uff1a<ul> <li><code>type</code> \u2014\u2014 Blob \u7c7b\u578b\uff0c\u901a\u5e38\u662f MIME \u7c7b\u578b\uff0c\u4f8b\u5982 image/png</li> <li><code>endings</code> \u2014\u2014 \u662f\u5426\u8f6c\u6362\u6362\u884c\u7b26\uff0c\u4f7f Blob \u5bf9\u5e94\u4e8e\u5f53\u524d\u64cd\u4f5c\u7cfb\u7edf\u7684\u6362\u884c\u7b26\uff08\\r\\n \u6216 \\n\uff09\u3002\u9ed8\u8ba4\u4e3a &quot;transparent&quot;\uff08\u5565\u4e5f\u4e0d\u505a\uff09\uff0c\u4e0d\u8fc7\u4e5f\u53ef\u4ee5\u662f &quot;native&quot;\uff08\u8f6c\u6362\uff09</li> </ul> </li> </ul> <p><strong>\u4f8b\u5982\uff1a</strong></p> <pre><code class="language-js">// \u4ece\u5b57\u7b26\u4e32\u521b\u5efa Blob\nlet blob = new Blob([&#39;&lt;html&gt;\u2026&lt;/html&gt;&#39;], { type: &#39;text/html&#39; });\n// \u8bf7\u6ce8\u610f\uff1a\u7b2c\u4e00\u4e2a\u53c2\u6570\u5fc5\u987b\u662f\u4e00\u4e2a\u6570\u7ec4 [...]</code></pre> <pre><code class="language-js">// \u4ece\u7c7b\u578b\u5316\u6570\u7ec4\uff08typed array\uff09\u548c\u5b57\u7b26\u4e32\u521b\u5efa Blob\nlet hello = new Uint8Array([72, 101, 108, 108, 111]); // \u4e8c\u8fdb\u5236\u683c\u5f0f\u7684 &quot;hello&quot;\n\nlet blob = new Blob([hello, &#39; &#39;, &#39;world&#39;], { type: &#39;text/plain&#39; });</code></pre> <p>\u53ef\u4ee5\u7528 <code>slice</code> \u65b9\u6cd5\u6765\u63d0\u53d6 <code>Blob</code> \u7247\u6bb5\uff1a</p> <pre><code class="language-js">blob.slice([byteStart], [byteEnd], [contentType]);</code></pre> <ul> <li><code>byteStart</code> \u2014\u2014 \u8d77\u59cb\u5b57\u8282\uff0c\u9ed8\u8ba4\u4e3a <code>0</code></li> <li><code>byteEnd</code> \u2014\u2014 \u6700\u540e\u4e00\u4e2a\u5b57\u8282\uff08\u4e13\u6709\uff0c\u9ed8\u8ba4\u4e3a\u6700\u540e\uff09</li> <li><code>contentType</code> \u2014\u2014 \u65b0 <code>blob</code> \u7684 <code>type</code>\uff0c\u9ed8\u8ba4\u4e0e\u6e90 <code>blob</code> \u76f8\u540c</li> </ul> <blockquote> <p><code>Blob</code> \u5bf9\u8c61\u662f\u4e0d\u53ef\u6539\u53d8\u7684\uff0c\u4f46\u6211\u4eec\u53ef\u4ee5\u901a\u8fc7 <code>slice</code> \u83b7\u5f97 <code>Blob</code> \u7684\u591a\u4e2a\u90e8\u5206\uff0c\u4ece\u8fd9\u4e9b\u90e8\u5206\u521b\u5efa\u65b0\u7684 <code>Blob</code> \u5bf9\u8c61\uff0c\u5c06\u5b83\u4eec\u7ec4\u6210\u65b0\u7684 <code>Blob</code></p> </blockquote> <h3 id="31-blob-\u7528\u4f5c-url">3.1 Blob \u7528\u4f5c URL</h3> <p>\u4e0b\u8f7d\u4e00\u4e2a\u5177\u6709\u52a8\u6001\u751f\u6210\u7684\u5185\u5bb9\u7684 <code>Blob</code> \u7684\u6587\u4ef6\uff1a</p> <pre><code class="language-html">&lt;!-- download \u7279\u6027\uff08attribute\uff09\u5f3a\u5236\u6d4f\u89c8\u5668\u4e0b\u8f7d\u800c\u4e0d\u662f\u5bfc\u822a --&gt;\n&lt;a download=&quot;hello.txt&quot; href=&quot;#&quot; id=&quot;link&quot;&gt;Download&lt;/a&gt;\n\n&lt;script&gt;\n  let blob = new Blob([&#39;Hello, world!&#39;], { type: &#39;text/plain&#39; });\n\n  link.href = URL.createObjectURL(blob);\n&lt;/script&gt;</code></pre> <p>\u5728 Javascript \u4e2d\u52a8\u6001\u521b\u5efa\u4e00\u4e2a\u94fe\u63a5\uff1a</p> <pre><code class="language-js">let link = document.createElement(&#39;a&#39;);\nlink.download = &#39;hello.txt&#39;;\n\nlet blob = new Blob([&#39;Hello, world!&#39;], { type: &#39;text/plain&#39; });\n\nlink.href = URL.createObjectURL(blob);\n\nlink.click();\n\nURL.revokeObjectURL(link.href);</code></pre> <p><code>URL.createObjectURL</code> \u53d6\u4e00\u4e2a <code>Blob</code>\uff0c\u5e76\u4e3a\u5176\u521b\u5efa\u4e00\u4e2a\u552f\u4e00\u7684 <code>URL</code>\uff0c\u5f62\u5f0f\u4e3a <code>blob:&lt;origin&gt;/&lt;uuid&gt;</code></p> <p>\u4e5f\u5c31\u662f <code>link.href</code> \u7684\u503c\u7684\u6837\u5b50\uff1a</p> <pre><code>blob:https://javascript.info/1e67e00e-860d-40a5-89ae-6ab0cbee6273</code></pre><blockquote> <p>\u6d4f\u89c8\u5668\u5185\u90e8\u4e3a\u6bcf\u4e2a\u901a\u8fc7 <code>URL.createObjectURL</code> \u751f\u6210\u7684 <code>URL</code> \u5b58\u50a8\u4e86\u4e00\u4e2a <code>URL \u2192 Blob</code> \u6620\u5c04\u3002\u56e0\u6b64\uff0c\u6b64\u7c7b <code>URL</code> \u5f88\u77ed\uff0c\u4f46\u53ef\u4ee5\u8bbf\u95ee <code>Blob</code></p> </blockquote> <p>\u4e0d\u8fc7\u5b83\u6709\u4e2a\u526f\u4f5c\u7528\u3002\u867d\u7136\u8fd9\u91cc\u6709 <code>Blob</code> \u7684\u6620\u5c04\uff0c\u4f46 <code>Blob</code> \u672c\u8eab\u53ea\u4fdd\u5b58\u5728\u5185\u5b58\u4e2d\u7684\u3002\u6d4f\u89c8\u5668\u65e0\u6cd5\u91ca\u653e\u5b83</p> <p><code>URL.revokeObjectURL(url)</code> \u4ece\u5185\u90e8\u6620\u5c04\u4e2d\u79fb\u9664\u5f15\u7528\uff0c\u56e0\u6b64\u5141\u8bb8 <code>Blob</code> \u88ab\u5220\u9664\uff08\u5982\u679c\u6ca1\u6709\u5176\u4ed6\u5f15\u7528\u7684\u8bdd\uff09\uff0c\u5e76\u91ca\u653e\u5185\u5b58</p> <h3 id="32-blob-\u8f6c\u6362\u4e3a-base64">3.2 Blob \u8f6c\u6362\u4e3a base64</h3> <p>\u4f7f\u7528\u5185\u5efa\u7684 <code>FileReader</code> \u5bf9\u8c61\u6765\u5c06 <code>Blob</code> \u8f6c\u6362\u4e3a <code>base64</code>\u3002\u5b83\u53ef\u4ee5\u5c06 <code>Blob</code> \u4e2d\u7684\u6570\u636e\u8bfb\u53d6\u4e3a\u591a\u79cd\u683c\u5f0f</p> <p>\u4e0b\u9762\u662f\u4e0b\u8f7d <code>Blob</code> \u7684\u793a\u4f8b\uff0c\u8fd9\u6b21\u662f\u901a\u8fc7 <code>base-64</code>\uff1a</p> <pre><code class="language-js">let link = document.createElement(&#39;a&#39;);\nlink.download = &#39;hello.txt&#39;;\n\nlet blob = new Blob([&#39;Hello, world!&#39;], { type: &#39;text/plain&#39; });\n\nlet reader = new FileReader();\nreader.readAsDataURL(blob); // \u5c06 Blob \u8f6c\u6362\u4e3a base64 \u5e76\u8c03\u7528 onload\n\nreader.onload = function() {\n  link.href = reader.result; // data url\n  link.click();\n};</code></pre> <p><strong>\u7279\u70b9\uff1a</strong></p> <ul> <li>\u65e0\u9700\u64a4\u9500\uff08revoke\uff09\u4efb\u4f55\u64cd\u4f5c</li> <li>\u5bf9\u5927\u7684 <code>Blob</code> \u8fdb\u884c\u7f16\u7801\u65f6\uff0c\u6027\u80fd\u548c\u5185\u5b58\u4f1a\u6709\u635f\u8017</li> </ul> <h3 id="33-image-\u8f6c\u6362\u4e3a-blob">3.3 Image \u8f6c\u6362\u4e3a blob</h3> <p>\u901a\u8fc7 canvas \u5b9e\u73b0\uff1a</p> <pre><code class="language-js">// \u83b7\u53d6\u4efb\u4f55\u56fe\u50cf\nlet img = document.querySelector(&#39;img&#39;);\n\n// \u751f\u6210\u540c\u5c3a\u5bf8\u7684 &lt;canvas&gt;\nlet canvas = document.createElement(&#39;canvas&#39;);\ncanvas.width = img.clientWidth;\ncanvas.height = img.clientHeight;\n\nlet context = canvas.getContext(&#39;2d&#39;);\n\n// \u5411\u5176\u4e2d\u590d\u5236\u56fe\u50cf\uff08\u6b64\u65b9\u6cd5\u5141\u8bb8\u526a\u88c1\u56fe\u50cf\uff09\ncontext.drawImage(img, 0, 0);\n// \u6211\u4eec context.rotate()\uff0c\u5e76\u5728 canvas \u4e0a\u505a\u5f88\u591a\u5176\u4ed6\u4e8b\u60c5\n\n// toBlob \u662f\u5f02\u6b65\u64cd\u4f5c\uff0c\u7ed3\u675f\u540e\u4f1a\u8c03\u7528 callback\ncanvas.toBlob(function(blob) {\n  // blob \u521b\u5efa\u5b8c\u6210\uff0c\u4e0b\u8f7d\u5b83\n  let link = document.createElement(&#39;a&#39;);\n  link.download = &#39;example.png&#39;;\n\n  link.href = URL.createObjectURL(blob);\n  link.click();\n\n  // \u5220\u9664\u5185\u90e8 blob \u5f15\u7528\uff0c\u8fd9\u6837\u6d4f\u89c8\u5668\u53ef\u4ee5\u4ece\u5185\u5b58\u4e2d\u5c06\u5176\u6e05\u9664\n  URL.revokeObjectURL(link.href);\n}, &#39;image/png&#39;);</code></pre> <h3 id="34-blob-\u8f6c\u6362\u4e3a-arraybuffer">3.4 Blob \u8f6c\u6362\u4e3a arrayBuffer</h3> <p><code>Blob</code> \u6784\u9020\u5668\u5141\u8bb8\u4ece\u51e0\u4e4e\u6240\u6709\u4e1c\u897f\u521b\u5efa <code>blob</code>\uff0c\u5305\u62ec\u4efb\u4f55 <code>BufferSource</code></p> <p>\u4f46\u662f\uff0c\u5982\u679c\u6211\u4eec\u9700\u8981\u6267\u884c\u4f4e\u7ea7\u522b\u7684\u64cd\u4f5c\u7684\u8bdd\uff0c\u5219\u53ef\u4ee5\u4f7f\u7528 <code>blob.arrayBuffer()</code> \u4ece <code>blob</code> \u4e2d\u83b7\u53d6\u6700\u4f4e\u7ea7\u522b\u7684 <code>arrayBuffer</code>\uff1a</p> <pre><code class="language-js">// \u4ece blob \u83b7\u53d6 arrayBuffer\nconst bufferPromise = await blob.arrayBuffer();\n\n// \u6216\u8005\nblob.arrayBuffer().then(buffer =&gt; {\n  /* \u5904\u7406 arrayBuffer */\n});</code></pre> <h3 id="35-blob-\u8f6c\u6362\u4e3a-stream">3.5 Blob \u8f6c\u6362\u4e3a stream</h3> <p>\u5f53\u8bfb\u53d6\u548c\u5199\u5165\u8d85\u8fc7 <code>2 GB</code> \u7684 <code>blob</code> \u65f6\uff0c\u5c06\u5176\u8f6c\u6362\u4e3a <code>arrayBuffer</code> \u4f1a\u66f4\u52a0\u5360\u7528\u5185\u5b58\u3002stream \u4f1a\u66f4\u9002\u5408\u5904\u7406</p> <p><code>stream</code> \u662f\u4e00\u79cd\u7279\u6b8a\u7684\u5bf9\u8c61\uff0c\u6211\u4eec\u53ef\u4ee5\u4ece\u5b83\u90a3\u91cc\u9010\u90e8\u5206\u5730\u8bfb\u53d6\uff08\u6216\u5199\u5165\uff09</p> <p><code>Blob</code> \u63a5\u53e3\u91cc\u7684 <code>stream()</code> \u65b9\u6cd5\u8fd4\u56de\u4e00\u4e2a <code>ReadableStream</code>\uff0c\u5728\u88ab\u8bfb\u53d6\u65f6\u53ef\u4ee5\u8fd4\u56de <code>Blob</code> \u4e2d\u5305\u542b\u7684\u6570\u636e</p> <pre><code class="language-js">// \u4ece blob \u83b7\u53d6\u53ef\u8bfb\u6d41\uff08readableStream\uff09\nconst readableStream = blob.stream();\nconst stream = readableStream.getReader();\n\nwhile (true) {\n  // \u5bf9\u4e8e\u6bcf\u6b21\u8fed\u4ee3\uff1adata \u662f\u4e0b\u4e00\u4e2a blob \u6570\u636e\u7247\u6bb5\n  let { done, data } = await stream.read();\n\n  if (done) {\n    // \u8bfb\u53d6\u5b8c\u6bd5\uff0cstream \u91cc\u5df2\u7ecf\u6ca1\u6709\u6570\u636e\u4e86\n    console.log(&#39;all blob processed.&#39;);\n    break;\n  }\n\n  // \u5bf9\u521a\u4ece blob \u4e2d\u8bfb\u53d6\u7684\u6570\u636e\u7247\u6bb5\u505a\u4e00\u4e9b\u5904\u7406\n  console.log(data);\n}</code></pre> <h2 id="4-file-\u548c-filereader">4 File \u548c FileReader</h2> <h3 id="41-file">4.1 File</h3> <p><code>File</code> \u5bf9\u8c61\u7ee7\u627f\u81ea <code>Blob</code>\uff0c\u5e76\u6269\u5c55\u4e86\u4e0e\u6587\u4ef6\u7cfb\u7edf\u76f8\u5173\u7684\u529f\u80fd</p> <p>\u6709\u4e24\u79cd\u65b9\u5f0f\u53ef\u4ee5\u83b7\u53d6\u5b83</p> <p>\u7b2c\u4e00\u79cd\uff0c\u4e0e <code>Blob</code> \u7c7b\u4f3c\uff0c\u6709\u4e00\u4e2a\u6784\u9020\u5668\uff1a</p> <pre><code class="language-js">new File(fileParts, fileName, [options])</code></pre> <ul> <li><code>fileParts</code> \u2014\u2014 <code>Blob/BufferSource/String</code> \u7c7b\u578b\u503c\u7684\u6570\u7ec4</li> <li><code>fileName</code> \u2014\u2014 \u6587\u4ef6\u540d\u5b57\u7b26\u4e32</li> <li><code>options</code> \u2014\u2014 \u53ef\u9009\u5bf9\u8c61\uff1a<ul> <li><code>lastModified</code> \u2014\u2014 \u6700\u540e\u4e00\u6b21\u4fee\u6539\u7684\u65f6\u95f4\u6233\uff08\u6574\u6570\u65e5\u671f\uff09</li> </ul> </li> </ul> <p>\u7b2c\u4e8c\u79cd\uff0c\u4ece <code>&lt;input type=&quot;file&quot;&gt;</code> \u6216\u62d6\u653e\u6216\u5176\u4ed6\u6d4f\u89c8\u5668\u63a5\u53e3\u6765\u83b7\u53d6\u6587\u4ef6\u3002\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c<code>file</code> \u5c06\u4ece\u64cd\u4f5c\u7cfb\u7edf\uff08OS\uff09\u83b7\u5f97 <code>this</code> \u4fe1\u606f</p> <p>\u7531\u4e8e <code>File</code> \u662f\u7ee7\u627f\u81ea <code>Blob</code> \u7684\uff0c\u6240\u4ee5 <code>File</code> \u5bf9\u8c61\u5177\u6709\u76f8\u540c\u7684\u5c5e\u6027\uff0c\u9644\u52a0\uff1a</p> <ul> <li><code>name</code> \u2014\u2014 \u6587\u4ef6\u540d</li> <li><code>lastModified</code> \u2014\u2014 \u6700\u540e\u4e00\u6b21\u4fee\u6539\u7684\u65f6\u95f4\u6233</li> </ul> <h3 id="42-filereader">4.2 FileReader</h3> <p><code>FileReader</code> \u662f\u4e00\u4e2a\u5bf9\u8c61\uff0c\u5176\u552f\u4e00\u76ee\u7684\u662f\u4ece <code>Blob</code>\uff08\u56e0\u6b64\u4e5f\u4ece File\uff09\u5bf9\u8c61\u4e2d\u8bfb\u53d6\u6570\u636e</p> <pre><code class="language-js">let reader = new FileReader(); // \u6ca1\u6709\u53c2\u6570</code></pre> <p><strong>\u4e3b\u8981\u65b9\u6cd5:</strong></p> <ul> <li><code>readAsArrayBuffer(blob)</code> \u2014\u2014 \u5c06\u6570\u636e\u8bfb\u53d6\u4e3a\u4e8c\u8fdb\u5236\u683c\u5f0f\u7684 <code>ArrayBuffer</code></li> <li><code>readAsText(blob, [encoding])</code> \u2014\u2014 \u5c06\u6570\u636e\u8bfb\u53d6\u4e3a\u7ed9\u5b9a\u7f16\u7801\uff08\u9ed8\u8ba4\u4e3a utf-8 \u7f16\u7801\uff09\u7684\u6587\u672c\u5b57\u7b26\u4e32</li> <li><code>readAsDataURL(blob)</code> \u2014\u2014 \u8bfb\u53d6\u4e8c\u8fdb\u5236\u6570\u636e\uff0c\u5e76\u5c06\u5176\u7f16\u7801\u4e3a <code>base64</code> \u7684 data url</li> <li><code>abort()</code> \u2014\u2014 \u53d6\u6d88\u64cd\u4f5c</li> </ul> <p><strong>\u8bfb\u53d6\u8fc7\u7a0b\u4e2d\uff0c\u6709\u4ee5\u4e0b\u4e8b\u4ef6\uff1a</strong></p> <ul> <li><code>loadstart</code> \u2014\u2014 \u5f00\u59cb\u52a0\u8f7d</li> <li><code>progress</code> \u2014\u2014 \u5728\u8bfb\u53d6\u8fc7\u7a0b\u4e2d\u51fa\u73b0</li> <li><code>load</code> \u2014\u2014 \u8bfb\u53d6\u5b8c\u6210\uff0c\u6ca1\u6709 <code>error</code></li> <li><code>abort</code> \u2014\u2014 \u8c03\u7528\u4e86 <code>abort()</code></li> <li><code>error</code> \u2014\u2014 \u51fa\u73b0 <code>error</code></li> <li><code>loadend</code> \u2014\u2014 \u8bfb\u53d6\u5b8c\u6210\uff0c\u65e0\u8bba\u6210\u529f\u8fd8\u662f\u5931\u8d25</li> </ul> <p><strong>\u8bfb\u53d6\u5b8c\u6210\u540e\uff0c\u6211\u4eec\u53ef\u4ee5\u901a\u8fc7\u4ee5\u4e0b\u65b9\u5f0f\u8bbf\u95ee\u8bfb\u53d6\u7ed3\u679c\uff1a</strong></p> <ul> <li><code>reader.result</code> \u662f\u7ed3\u679c\uff08\u5982\u679c\u6210\u529f\uff09</li> <li><code>reader.error</code> \u662f error\uff08\u5982\u679c\u5931\u8d25\uff09</li> </ul> <p>\u8fd9\u662f\u4e00\u4e2a\u8bfb\u53d6\u6587\u4ef6\u7684\u793a\u4f8b\uff1a</p> <pre><code class="language-html">&lt;input type=&quot;file&quot; onchange=&quot;readFile(this)&quot;&gt;\n\n&lt;script&gt;\nfunction readFile(input) {\n  let file = input.files[0];\n\n  let reader = new FileReader();\n\n  reader.readAsText(file);\n\n  reader.onload = function() {\n    console.log(reader.result);\n  };\n\n  reader.onerror = function() {\n    console.log(reader.error);\n  };\n}\n&lt;/script&gt;</code></pre> <p><strong>\u5728 <code>Web Workers</code> \u4e2d\u53ef\u4ee5\u4f7f\u7528 <code>FileReaderSync</code>:</strong></p> <p>\u5bf9\u4e8e <code>Web Worker</code>\uff0c\u8fd8\u6709\u4e00\u79cd\u540c\u6b65\u7684 <code>FileReader</code> \u53d8\u4f53\uff0c\u79f0\u4e3a <code>FileReaderSync</code></p> <p>\u5b83\u7684\u8bfb\u53d6\u65b9\u6cd5 <code>read*</code> \u4e0d\u4f1a\u751f\u6210\u4e8b\u4ef6\uff0c\u4f46\u662f\u4f1a\u50cf\u5e38\u89c4\u51fd\u6570\u90a3\u6837\u8fd4\u56de\u4e00\u4e2a\u7ed3\u679c</p> <p>\u4e0d\u8fc7\uff0c\u8fd9\u4ec5\u5728 <code>Web Worker</code> \u4e2d\u53ef\u7528\uff0c\u56e0\u4e3a\u5728\u8bfb\u53d6\u6587\u4ef6\u7684\u65f6\u5019\uff0c\u540c\u6b65\u8c03\u7528\u4f1a\u6709\u5ef6\u8fdf\uff0c\u800c\u5728 <code>Web Worker</code> \u4e2d\uff0c\u8fd9\u79cd\u5ef6\u8fdf\u5e76\u4e0d\u662f\u5f88\u91cd\u8981\u3002\u5b83\u4e0d\u4f1a\u5f71\u54cd\u9875\u9762</p> ';e.exports=a}}]);