(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["docs/browser/network"],{FtLU:function(e,o,c){var d=c("Rxnk"),t=c("3GZx"),n=c("p0m1"),r=d(t),l=d(n),s='<h1 id="\u7f51\u7edc\u8bf7\u6c42">\u7f51\u7edc\u8bf7\u6c42</h1> <h2 id="1-fetch">1 Fetch</h2> <h3 id="11-\u8bed\u6cd5">1.1 \u8bed\u6cd5</h3> <p>\u5bf9\u4e8e\u6765\u81ea <code>JavaScript</code> \u7684\u7f51\u7edc\u8bf7\u6c42\uff0c\u6709\u4e00\u4e2a\u603b\u79f0\u672f\u8bed <code>AJAX</code>\uff08Asynchronous JavaScript And XML \u7684\u7b80\u79f0\uff09\u3002\u4f46\u662f\u6211\u4eec\u4e0d\u5fc5\u4f7f\u7528 <code>XML</code>,\u8fd9\u4e2a\u672f\u8bed\u8bde\u751f\u4e8e\u5f88\u4e45\u4ee5\u524d</p> <p><code>fetch()</code> \u65b9\u6cd5\u662f\u4e00\u79cd\u73b0\u4ee3\u901a\u7528\u7684\u65b9\u6cd5\uff0c\u65e7\u7248\u672c\u7684\u6d4f\u89c8\u5668\u4e0d\u652f\u6301\u5b83\uff08\u53ef\u4ee5 polyfill\uff09</p> <p><strong>\u57fa\u672c\u8bed\u6cd5\uff1a</strong></p> <pre><code class="language-js">let promise = fetch(url, [options]);</code></pre> <ul> <li><code>url</code> \u2014\u2014 \u8981\u8bbf\u95ee\u7684 <code>URL</code></li> <li><code>options</code> \u2014\u2014 \u53ef\u9009\u53c2\u6570\uff1a<code>method</code>\uff0c<code>header</code> \u7b49</li> </ul> <p><strong>\u83b7\u53d6\u54cd\u5e94\u901a\u5e38\u9700\u8981\u7ecf\u8fc7\u4e24\u4e2a\u9636\u6bb5\uff1a</strong></p> <p><strong>\u7b2c\u4e00\u9636\u6bb5\uff0c\u5f53\u670d\u52a1\u5668\u53d1\u9001\u4e86\u54cd\u5e94\u5934\uff08response header\uff09\uff0cfetch \u8fd4\u56de\u7684 promise \u5c31\u4f7f\u7528\u5185\u5efa\u7684 Response class \u5bf9\u8c61\u6765\u5bf9\u54cd\u5e94\u5934\u8fdb\u884c\u89e3\u6790</strong></p> <p>\u5728\u8fd9\u4e2a\u9636\u6bb5\uff0c\u6211\u4eec\u53ef\u4ee5\u901a\u8fc7\u68c0\u67e5\u54cd\u5e94\u5934\uff0c\u6765\u68c0\u67e5 <code>HTTP</code> \u72b6\u6001\u4ee5\u786e\u5b9a\u8bf7\u6c42\u662f\u5426\u6210\u529f\uff0c\u5f53\u524d\u8fd8\u6ca1\u6709\u54cd\u5e94\u4f53\uff08response body\uff09</p> <p>\u5982\u679c <code>fetch</code> \u65e0\u6cd5\u5efa\u7acb\u4e00\u4e2a <code>HTTP</code> \u8bf7\u6c42\uff0c\u4f8b\u5982\u7f51\u7edc\u95ee\u9898\uff0c\u4ea6\u6216\u662f\u8bf7\u6c42\u7684\u7f51\u5740\u4e0d\u5b58\u5728\uff0c\u90a3\u4e48 <code>promise</code> \u5c31\u4f1a <code>reject</code>\u3002\u5f02\u5e38\u7684 <code>HTTP</code> \u72b6\u6001\uff0c\u4f8b\u5982 404 \u6216 500\uff0c\u4e0d\u4f1a\u5bfc\u81f4\u51fa\u73b0 <code>error</code></p> <p>\u6211\u4eec\u53ef\u4ee5\u5728 <code>response</code> \u7684\u5c5e\u6027\u4e2d\u770b\u5230 <code>HTTP</code> \u72b6\u6001\uff1a</p> <ul> <li><code>status</code> \u2014\u2014 <code>HTTP</code> \u72b6\u6001\u7801\uff0c\u4f8b\u5982 200</li> <li><code>ok</code> \u2014\u2014 \u5e03\u5c14\u503c\uff0c\u5982\u679c <code>HTTP</code> \u72b6\u6001\u7801\u4e3a <code>200-299</code>\uff0c\u5219\u4e3a true</li> </ul> <p>\u4f8b\u5982\uff1a</p> <pre><code class="language-js">let response = await fetch(url);\n\nif (response.ok) {\n  // \u5982\u679c HTTP \u72b6\u6001\u7801\u4e3a 200-299\n  // \u83b7\u53d6 response body\uff08\u6b64\u65b9\u6cd5\u4f1a\u5728\u4e0b\u9762\u89e3\u91ca\uff09\n  let json = await response.json();\n} else {\n  alert(&#39;HTTP-Error: &#39; + response.status);\n}</code></pre> <p><strong>\u7b2c\u4e8c\u9636\u6bb5\uff0c\u4e3a\u4e86\u83b7\u53d6 response body\uff0c\u6211\u4eec\u9700\u8981\u4f7f\u7528\u4e00\u4e2a\u5176\u4ed6\u7684\u65b9\u6cd5\u8c03\u7528\uff1a</strong></p> <p><code>Response</code> \u63d0\u4f9b\u4e86\u591a\u79cd\u57fa\u4e8e <code>promise</code> \u7684\u65b9\u6cd5\uff0c\u6765\u4ee5\u4e0d\u540c\u7684\u683c\u5f0f\u8bbf\u95ee <code>body</code>\uff1a</p> <ul> <li><code>response.text()</code> \u2014\u2014 \u8bfb\u53d6 <code>response</code>\uff0c\u5e76\u4ee5\u6587\u672c\u5f62\u5f0f\u8fd4\u56de <code>response</code></li> <li><code>response.json()</code> \u2014\u2014 \u5c06 <code>response</code> \u89e3\u6790\u4e3a <code>JSON</code></li> <li><code>response.formData()</code> \u2014\u2014 \u4ee5 FormData \u5bf9\u8c61\uff08\u5728 \u4e0b\u4e00\u7ae0 \u6709\u89e3\u91ca\uff09\u7684\u5f62\u5f0f\u8fd4\u56de response\uff0c</li> <li><code>response.blob()</code> \u2014\u2014 \u4ee5 Blob\uff08\u5177\u6709\u7c7b\u578b\u7684\u4e8c\u8fdb\u5236\u6570\u636e\uff09\u5f62\u5f0f\u8fd4\u56de response\uff0c</li> <li><code>response.arrayBuffer()</code> \u2014\u2014 \u4ee5 ArrayBuffer\uff08\u4f4e\u7ea7\u522b\u7684\u4e8c\u8fdb\u5236\u6570\u636e\uff09\u5f62\u5f0f\u8fd4\u56de response\uff0c</li> <li><code>response.body</code> \u662f <code>ReadableStream</code> \u5bf9\u8c61\uff0c\u5b83\u5141\u8bb8\u9010\u5757\u8bfb\u53d6 <code>body</code></li> </ul> <blockquote> <p><strong>\u91cd\u8981\uff1a</strong>\u6211\u4eec\u53ea\u80fd\u9009\u62e9\u4e00\u79cd\u8bfb\u53d6 body \u7684\u65b9\u6cd5\u3002 \u5982\u679c\u6211\u4eec\u5df2\u7ecf\u4f7f\u7528\u4e86 <code>response.text()</code> \u65b9\u6cd5\u6765\u83b7\u53d6 <code>response</code>\uff0c\u90a3\u4e48\u5982\u679c\u518d\u7528 <code>response.json()</code>\uff0c\u5219\u4e0d\u4f1a\u751f\u6548\uff0c\u56e0\u4e3a <code>body</code> \u5185\u5bb9\u5df2\u7ecf\u88ab\u5904\u7406\u8fc7\u4e86</p> </blockquote> <h3 id="11-response-header">1.1 Response header</h3> <p>Response header \u4f4d\u4e8e <code>response.headers</code> \u4e2d\u7684\u4e00\u4e2a\u7c7b\u4f3c\u4e8e <code>Map</code> \u7684 <code>header</code> \u5bf9\u8c61</p> <p>\u5b83\u4e0d\u662f\u771f\u6b63\u7684 <code>Map</code>\uff0c\u4f46\u662f\u5b83\u5177\u6709\u7c7b\u4f3c\u7684\u65b9\u6cd5</p> <pre><code class="language-js">let response = await fetch(&#39;https://api.github.com/repos/javascript-tutorial/en.javascript.info/commits&#39;);\n\n// \u83b7\u53d6\u4e00\u4e2a header\nconst contentType = response.headers.get(&#39;Content-Type&#39;); // application/json; charset=utf-8\n\n// \u8fed\u4ee3\u6240\u6709 header\nfor (let [key, value] of response.headers) {\n  alert(`${key} = ${value}`);\n}</code></pre> <h3 id="13-request-header">1.3 Request header</h3> <p>\u8981\u5728 <code>fetch</code> \u4e2d\u8bbe\u7f6e <code>request header</code>\uff0c\u6211\u4eec\u53ef\u4ee5\u4f7f\u7528 <code>headers</code> \u9009\u9879\u3002\u5b83\u6709\u4e00\u4e2a\u5e26\u6709\u8f93\u51fa <code>header</code> \u7684\u5bf9\u8c61</p> <p>\u5982\u4e0b\u6240\u793a\uff1a</p> <pre><code class="language-js">let response = fetch(protectedUrl, {\n  headers: {\n    Authentication: &#39;secret&#39;\n  }\n});</code></pre> <p><strong>\u65e0\u6cd5\u8bbe\u7f6e\u7684 header\uff1a</strong></p> <ul> <li>Accept-Charset, Accept-Encoding</li> <li>Access-Control-Request-Headers</li> <li>Access-Control-Request-Method</li> <li>Connection</li> <li>Content-Length</li> <li>Cookie, Cookie2</li> <li>Date</li> <li>DNT</li> <li>Expect</li> <li>Host</li> <li>Keep-Alive</li> <li>Origin</li> <li>Referer</li> <li>TE</li> <li>Trailer</li> <li>Transfer-Encoding</li> <li>Upgrade</li> <li>Via</li> <li>Proxy-*</li> <li>Sec-*</li> </ul> <blockquote> <p>\u8fd9\u4e9b <code>header</code> \u4fdd\u8bc1\u4e86 <code>HTTP</code> \u7684\u6b63\u786e\u6027\u548c\u5b89\u5168\u6027\uff0c\u6240\u4ee5\u5b83\u4eec\u4ec5\u7531\u6d4f\u89c8\u5668\u63a7\u5236</p> </blockquote> <h3 id="14-post-\u8bf7\u6c42">1.4 POST \u8bf7\u6c42</h3> <p>\u8981\u521b\u5efa\u4e00\u4e2a <code>POST</code> \u8bf7\u6c42\uff0c\u6216\u8005\u5176\u4ed6\u65b9\u6cd5\u7684\u8bf7\u6c42\uff0c\u9700\u8981\u4f7f\u7528 <code>fetch</code> \u9009\u9879\uff1a</p> <ul> <li><code>method</code> \u2014\u2014 <code>HTTP</code> \u65b9\u6cd5\uff0c\u4f8b\u5982 <code>POST</code></li> <li><code>body</code> \u2014\u2014 <code>request body</code>\uff0c\u5176\u4e2d\u4e4b\u4e00<ul> <li>\u5b57\u7b26\u4e32\uff08\u4f8b\u5982 JSON \u7f16\u7801\u7684\uff09</li> <li><code>FormData</code> \u5bf9\u8c61\uff0c\u4ee5 <code>multipart/form-data</code> \u5f62\u5f0f\u53d1\u9001\u6570\u636e</li> <li><code>Blob/BufferSource</code> \u53d1\u9001\u4e8c\u8fdb\u5236\u6570\u636e</li> <li><code>URLSearchParams</code>\uff0c\u4ee5 <code>x-www-form-urlencoded</code> \u7f16\u7801\u5f62\u5f0f\u53d1\u9001\u6570\u636e\uff0c\u5f88\u5c11\u4f7f\u7528</li> </ul> </li> </ul> <p>\u4f8b\u5982\uff0c\u4ee5 <code>JSON</code> \u5f62\u5f0f\u53d1\u9001 <code>user</code> \u5bf9\u8c61\uff1a</p> <pre><code class="language-js">let user = {\n  name: &#39;John&#39;,\n  surname: &#39;Smith&#39;\n};\n\nlet response = await fetch(&#39;/article/fetch/post/user&#39;, {\n  method: &#39;POST&#39;,\n  headers: {\n    &#39;Content-Type&#39;: &#39;application/json;charset=utf-8&#39;\n  },\n  body: JSON.stringify(user)\n});\n\nlet result = await response.json();\nalert(result.message);</code></pre> <blockquote> <p>\u6ce8\u610f\uff0c\u5982\u679c\u8bf7\u6c42\u7684 <code>body</code> \u662f\u5b57\u7b26\u4e32\uff0c\u5219 <code>Content-Type</code> \u4f1a\u9ed8\u8ba4\u8bbe\u7f6e\u4e3a <code>text/plain;charset=UTF-8</code></p> </blockquote> <h3 id="15-\u53d1\u9001\u56fe\u7247">1.5 \u53d1\u9001\u56fe\u7247</h3> <p>\u6211\u4eec\u53ef\u4ee5\u4f7f\u7528 <code>Blob</code> \u6216 <code>BufferSource</code> \u5bf9\u8c61\u901a\u8fc7 <code>fetch</code> \u63d0\u4ea4\u4e8c\u8fdb\u5236\u6570\u636e</p> <pre><code class="language-js">async function submit() {\n  let blob = await new Promise(resolve =&gt; canvasElem.toBlob(resolve, &#39;image/png&#39;));\n\n  let response = await fetch(&#39;/article/fetch/post/image&#39;, {\n    method: &#39;POST&#39;,\n    body: blob\n  });\n\n  let result = await response.json();\n  alert(result.message);\n}</code></pre> <blockquote> <p>\u6ce8\u610f\uff0c\u8fd9\u91cc\u6ca1\u6709\u624b\u52a8\u8bbe\u7f6e <code>Content-Type header</code>\uff0c\u56e0\u4e3a Blob \u5bf9\u8c61\u5177\u6709\u5185\u5efa\u7684\u7c7b\u578b\uff08\u8fd9\u91cc\u662f <code>image/png</code>\uff0c\u901a\u8fc7 <code>toBlob</code> \u751f\u6210\u7684\uff09\u3002\u5bf9\u4e8e <code>Blob</code> \u5bf9\u8c61\uff0c\u8fd9\u4e2a\u7c7b\u578b\u5c31\u53d8\u6210\u4e86 <code>Content-Type</code> \u7684\u503c</p> </blockquote> <h2 id="2-formdata">2 FormData</h2> <p>\u7f51\u7edc\u65b9\u6cd5\u4f8b\u5982 <code>fetch</code> \u53ef\u4ee5\u63a5\u53d7\u4e00\u4e2a <code>FormData</code> \u5bf9\u8c61\u4f5c\u4e3a <code>body</code>\u3002\u5b83\u4f1a\u5e26\u6709 <code>Content-Type: multipart/form-data</code></p> <p>\u4ece\u670d\u52a1\u5668\u89d2\u5ea6\u6765\u770b\uff0c\u5b83\u5c31\u50cf\u662f\u4e00\u4e2a\u666e\u901a\u7684\u8868\u5355\u63d0\u4ea4</p> <h3 id="21-formdata-\u65b9\u6cd5">2.1 FormData \u65b9\u6cd5</h3> <p>\u6211\u4eec\u53ef\u4ee5\u4f7f\u7528\u4ee5\u4e0b\u65b9\u6cd5\u4fee\u6539 FormData \u4e2d\u7684\u5b57\u6bb5\uff1a</p> <ul> <li><code>formData.append(name, value)</code> \u2014\u2014 \u6dfb\u52a0\u5177\u6709\u7ed9\u5b9a <code>name</code> \u548c <code>value</code> \u7684\u8868\u5355\u5b57\u6bb5</li> <li><code>formData.append(name, blob, fileName)</code> \u2014\u2014 \u6dfb\u52a0\u4e00\u4e2a\u5b57\u6bb5\uff0c\u5c31\u50cf\u5b83\u662f <code>&lt;input type=&quot;file&quot;&gt;</code>\uff0c\u7b2c\u4e09\u4e2a\u53c2\u6570 <code>fileName</code> \u8bbe\u7f6e\u6587\u4ef6\u540d\uff08\u800c\u4e0d\u662f\u8868\u5355\u5b57\u6bb5\u540d\uff09\uff0c\u56e0\u4e3a\u5b83\u662f\u7528\u6237\u6587\u4ef6\u7cfb\u7edf\u4e2d\u6587\u4ef6\u7684\u540d\u79f0</li> <li><code>formData.delete(name)</code> \u2014\u2014 \u79fb\u9664\u5e26\u6709\u7ed9\u5b9a <code>name</code> \u7684\u5b57\u6bb5</li> <li><code>formData.get(name)</code> \u2014\u2014 \u83b7\u53d6\u5e26\u6709\u7ed9\u5b9a <code>name</code> \u7684\u5b57\u6bb5\u503c</li> <li><code>formData.has(name)</code> \u2014\u2014 \u5982\u679c\u5b58\u5728\u5e26\u6709\u7ed9\u5b9a <code>name</code> \u7684\u5b57\u6bb5\uff0c\u5219\u8fd4\u56de <code>true</code>\uff0c\u5426\u5219\u8fd4\u56de <code>false</code></li> </ul> <blockquote> <p>\u4e00\u4e2a\u8868\u5355\u53ef\u4ee5\u5305\u542b\u591a\u4e2a\u5177\u6709\u76f8\u540c <code>name</code> \u7684\u5b57\u6bb5\uff0c\u56e0\u6b64\uff0c\u591a\u6b21\u8c03\u7528 append \u5c06\u4f1a\u6dfb\u52a0\u591a\u4e2a\u5177\u6709\u76f8\u540c\u540d\u79f0\u7684\u5b57\u6bb5</p> </blockquote> <blockquote> <p><code>set</code> \u65b9\u6cd5\uff0c\u8bed\u6cd5\u4e0e <code>append</code> \u76f8\u540c\u3002\u4e0d\u540c\u4e4b\u5904\u5728\u4e8e <code>.set</code> \u79fb\u9664\u6240\u6709\u5177\u6709\u7ed9\u5b9a <code>name</code> \u7684\u5b57\u6bb5\uff0c\u7136\u540e\u9644\u52a0\u4e00\u4e2a\u65b0\u5b57\u6bb5\u3002\u56e0\u6b64\uff0c\u5b83\u786e\u4fdd\u4e86\u53ea\u6709\u4e00\u4e2a\u5177\u6709\u8fd9\u79cd <code>name</code> \u7684\u5b57\u6bb5\uff0c\u5176\u4ed6\u7684\u548c <code>append</code> \u4e00\u6837</p> </blockquote> <h3 id="22-\u53d1\u9001\u5e26\u6709\u6587\u4ef6\u7684\u8868\u5355">2.2 \u53d1\u9001\u5e26\u6709\u6587\u4ef6\u7684\u8868\u5355</h3> <p>\u8868\u5355\u59cb\u7ec8\u4ee5 <code>Content-Type: multipart/form-data</code> \u6765\u53d1\u9001\u6570\u636e\uff0c\u8fd9\u4e2a\u7f16\u7801\u5141\u8bb8\u53d1\u9001\u6587\u4ef6\u3002\u56e0\u6b64 <code>&lt;input type=&quot;file&quot;&gt;</code> \u5b57\u6bb5\u4e5f\u80fd\u88ab\u53d1\u9001\uff0c\u7c7b\u4f3c\u4e8e\u666e\u901a\u7684\u8868\u5355\u63d0\u4ea4</p> <p>\u793a\u4f8b\uff1a</p> <pre><code class="language-html">&lt;form id=&quot;formElem&quot;&gt;\n  &lt;input type=&quot;text&quot; name=&quot;firstName&quot; value=&quot;John&quot; /&gt;\n  &lt;input type=&quot;file&quot; name=&quot;picture&quot; accept=&quot;image/*&quot; /&gt;\n\n  &lt;input type=&quot;submit&quot; /&gt;\n&lt;/form&gt;\n\n&lt;script&gt;\n  formElem.onsubmit = async e =&gt; {\n    e.preventDefault();\n\n    let response = await fetch(&#39;/article/formdata/post/user-avatar&#39;, {\n      method: &#39;POST&#39;,\n      body: new FormData(formElem)\n    });\n\n    let result = await response.json();\n\n    alert(result.message);\n  };\n&lt;/script&gt;</code></pre> <h3 id="23-\u53d1\u9001\u5177\u6709-blob-\u6570\u636e\u7684\u8868\u5355">2.3 \u53d1\u9001\u5177\u6709 Blob \u6570\u636e\u7684\u8868\u5355</h3> <p>\u4f46\u5728\u5b9e\u9645\u4e2d\uff0c\u901a\u5e38\u66f4\u65b9\u4fbf\u7684\u53d1\u9001\u56fe\u7247\u7684\u65b9\u5f0f\u4e0d\u662f\u5355\u72ec\u53d1\u9001\uff0c\u800c\u662f\u5c06\u5176\u4f5c\u4e3a\u8868\u5355\u7684\u4e00\u90e8\u5206\uff0c\u5e76\u5e26\u6709\u9644\u52a0\u5b57\u6bb5\u4e00\u8d77\u53d1\u9001</p> <p>\u4e0b\u9762\u8fd9\u4e2a\u4f8b\u5b50\u4f7f\u7528 <code>FormData</code> \u5c06\u4e00\u4e2a\u6765\u81ea <code>&lt;canvas&gt;</code> \u7684\u56fe\u7247\u548c\u4e00\u4e9b\u5176\u4ed6\u5b57\u6bb5\u4e00\u8d77\u4f5c\u4e3a\u4e00\u4e2a\u8868\u5355\u63d0\u4ea4\uff1a</p> <pre><code class="language-js">let imageBlob = await new Promise(resolve =&gt; canvasElem.toBlob(resolve, &#39;image/png&#39;));\n\nlet formData = new FormData();\nformData.append(&#39;firstName&#39;, &#39;John&#39;);\nformData.append(&#39;image&#39;, imageBlob, &#39;image.png&#39;);</code></pre> <h2 id="3-fetch\uff1a\u4e0b\u8f7d\u8fdb\u5ea6">3 Fetch\uff1a\u4e0b\u8f7d\u8fdb\u5ea6</h2> <p><code>fetch</code> \u65b9\u6cd5\u5141\u8bb8\u53bb\u8ddf\u8e2a\u4e0b\u8f7d\u8fdb\u5ea6</p> <blockquote> <p>\u6ce8\u610f\uff1a\u5230\u76ee\u524d\u4e3a\u6b62\uff0c<code>fetch</code> \u65b9\u6cd5\u65e0\u6cd5\u8ddf\u8e2a\u4e0a\u4f20\u8fdb\u5ea6\u3002\u8bf7\u4f7f\u7528 <code>XMLHttpRequest</code></p> </blockquote> <p>\u8981\u8ddf\u8e2a\u4e0b\u8f7d\u8fdb\u5ea6\uff0c\u6211\u4eec\u53ef\u4ee5\u4f7f\u7528 <code>response.body</code> \u5c5e\u6027\u3002\u5b83\u662f <code>ReadableStream</code> \u2014\u2014 \u4e00\u4e2a\u7279\u6b8a\u7684\u5bf9\u8c61\uff0c\u5b83\u53ef\u4ee5\u9010\u5757\uff08chunk\uff09\u63d0\u4f9b <code>body</code></p> <p><strong>\u4ece <code>response.body</code> \u8bfb\u53d6 <code>response</code> \u7684\u793a\u4f8b\u4ee3\u7801\uff1a</strong></p> <pre><code class="language-js">// \u4ee3\u66ff response.json() \u4ee5\u53ca\u5176\u4ed6\u65b9\u6cd5\nconst reader = response.body.getReader();\n\n// \u5728 body \u4e0b\u8f7d\u65f6\uff0c\u4e00\u76f4\u4e3a\u65e0\u9650\u5faa\u73af\nwhile (true) {\n  // \u5f53\u6700\u540e\u4e00\u5757\u4e0b\u8f7d\u5b8c\u6210\u65f6\uff0cdone \u503c\u4e3a true\n  // value \u662f\u5757\u5b57\u8282\u7684 Uint8Array\n  const { done, value } = await reader.read();\n\n  if (done) {\n    break;\n  }\n\n  console.log(`Received ${value.length} bytes`);\n}</code></pre> <p><strong><code>await reader.read()</code> \u8c03\u7528\u7684\u7ed3\u679c\u662f\u4e00\u4e2a\u5177\u6709\u4e24\u4e2a\u5c5e\u6027\u7684\u5bf9\u8c61\uff1a</strong></p> <ul> <li><code>done</code> \u2014\u2014 \u5f53\u8bfb\u53d6\u5b8c\u6210\u65f6\u4e3a <code>true</code>\uff0c\u5426\u5219\u4e3a <code>false</code></li> <li><code>value</code> \u2014\u2014 \u5b57\u8282\u7684\u7c7b\u578b\u5316\u6570\u7ec4\uff1a<code>Uint8Array</code></li> </ul> <p><strong>\u66f4\u591a\u8bf4\u660e\uff1a</strong></p> <pre><code class="language-js">// Step 1\uff1a\u542f\u52a8 fetch\uff0c\u5e76\u83b7\u5f97\u4e00\u4e2a reader\nlet response = await fetch(&#39;https://api.github.com/repos/javascript-tutorial/en.javascript.info/commits?per_page=100&#39;);\n\nconst reader = response.body.getReader();\n\n// Step 2\uff1a\u83b7\u5f97\u603b\u957f\u5ea6\uff08length\uff09\nconst contentLength = +response.headers.get(&#39;Content-Length&#39;);\n\n// Step 3\uff1a\u8bfb\u53d6\u6570\u636e\nlet receivedLength = 0; // \u5f53\u524d\u63a5\u6536\u5230\u4e86\u8fd9\u4e48\u591a\u5b57\u8282\nlet chunks = []; // \u63a5\u6536\u5230\u7684\u4e8c\u8fdb\u5236\u5757\u7684\u6570\u7ec4\uff08\u5305\u62ec body\uff09\n\nwhile (true) {\n  const { done, value } = await reader.read();\n\n  if (done) break;\n\n  chunks.push(value);\n  receivedLength += value.length;\n\n  console.log(`Received ${receivedLength} of ${contentLength}`);\n}\n\n// Step 4\uff1a\u5c06\u5757\u8fde\u63a5\u5230\u5355\u4e2a Uint8Array\nlet chunksAll = new Uint8Array(receivedLength); // (4.1)\nlet position = 0;\n\nfor (let chunk of chunks) {\n  chunksAll.set(chunk, position); // (4.2)\n  position += chunk.length;\n}\n\n// Step 5\uff1a\u89e3\u7801\u6210\u5b57\u7b26\u4e32\nlet result = new TextDecoder(&#39;utf-8&#39;).decode(chunksAll);\n\n// \u6211\u4eec\u5b8c\u6210\u5566\uff01\nlet commits = JSON.parse(result);\nalert(commits[0].author.login);</code></pre> <h2 id="4-fetch\uff1a\u4e2d\u6b62">4 Fetch\uff1a\u4e2d\u6b62</h2> <p><code>fetch</code> \u8fd4\u56de\u4e00\u4e2a <code>promise</code>\u3002<code>JavaScript</code> \u901a\u5e38\u5e76\u6ca1\u6709\u3010\u4e2d\u6b62\u3011 <code>promise</code> \u7684\u6982\u5ff5</p> <p>\u90a3\u4e48\u600e\u6837\u624d\u80fd\u53d6\u6d88\u4e00\u4e2a\u6b63\u5728\u6267\u884c\u7684 <code>fetch</code> \u5462</p> <p>\u4e3a\u6b64\u6709\u4e00\u4e2a\u7279\u6b8a\u7684\u5185\u5efa\u5bf9\u8c61\uff1a<code>AbortController</code>\u3002\u5b83\u4e0d\u4ec5\u53ef\u4ee5\u4e2d\u6b62 <code>fetch</code>\uff0c\u8fd8\u53ef\u4ee5\u4e2d\u6b62\u5176\u4ed6\u5f02\u6b65\u4efb\u52a1</p> <h3 id="41-abortcontroller-\u5bf9\u8c61">4.1 AbortController \u5bf9\u8c61</h3> <p>\u521b\u5efa\u4e00\u4e2a\u63a7\u5236\u5668\uff08controller\uff09\uff1a</p> <pre><code class="language-js">let controller = new AbortController();</code></pre> <ul> <li>\u5b83\u5177\u6709\u5355\u4e2a\u65b9\u6cd5 <code>abort()</code></li> <li>\u548c\u5355\u4e2a\u5c5e\u6027 <code>signal</code>\uff0c\u6211\u4eec\u53ef\u4ee5\u5728\u8fd9\u4e2a\u5c5e\u6027\u4e0a\u8bbe\u7f6e\u4e8b\u4ef6\u76d1\u542c\u5668</li> </ul> <p><strong>\u5f53 abort() \u88ab\u8c03\u7528\u65f6\uff1a</strong></p> <ul> <li><code>controller.signal</code> \u5c31\u4f1a\u89e6\u53d1 <code>abort</code> \u4e8b\u4ef6</li> <li><code>controller.signal.aborted</code> \u5c5e\u6027\u53d8\u4e3a <code>true</code></li> </ul> <p><strong>\u901a\u5e38\uff0c\u5904\u7406\u5206\u4e3a\u4e24\u90e8\u5206\uff1a</strong></p> <ul> <li>\u4e00\u90e8\u5206\u662f\u4e00\u4e2a\u53ef\u53d6\u6d88\u7684\u64cd\u4f5c\uff0c\u5b83\u5728 <code>controller.signal</code> \u4e0a\u8bbe\u7f6e\u4e00\u4e2a\u76d1\u542c\u5668</li> <li>\u53e6\u4e00\u90e8\u5206\u662f\u53d6\u6d88\uff1a\u5728\u9700\u8981\u7684\u65f6\u5019\u8c03\u7528 <code>controller.abort()</code></li> </ul> <pre><code class="language-js">let controller = new AbortController();\nlet signal = controller.signal;\n\n// \u53ef\u53d6\u6d88\u7684\u64cd\u4f5c\u8fd9\u4e00\u90e8\u5206\n// \u83b7\u53d6 &quot;signal&quot; \u5bf9\u8c61\uff0c\n// \u5e76\u5c06\u76d1\u542c\u5668\u8bbe\u7f6e\u4e3a\u5728 controller.abort() \u88ab\u8c03\u7528\u65f6\u89e6\u53d1\nsignal.addEventListener(&#39;abort&#39;, () =&gt; alert(&#39;abort!&#39;));\n\n// \u53e6\u4e00\u90e8\u5206\uff0c\u53d6\u6d88\uff08\u5728\u4e4b\u540e\u7684\u4efb\u4f55\u65f6\u5019\uff09\uff1a\ncontroller.abort(); // \u4e2d\u6b62\uff01\n\n// \u4e8b\u4ef6\u89e6\u53d1\uff0csignal.aborted \u53d8\u4e3a true\nalert(signal.aborted); // true</code></pre> <p><code>AbortController</code> \u53ea\u662f\u5728 <code>abort()</code> \u88ab\u8c03\u7528\u65f6\u4f20\u9012 <code>abort</code> \u4e8b\u4ef6\u7684\u4e00\u79cd\u65b9\u5f0f\u3002\u6211\u4eec\u53ef\u4ee5\u81ea\u5df1\u5728\u4ee3\u7801\u4e2d\u5b9e\u73b0\u76f8\u540c\u7c7b\u578b\u7684\u4e8b\u4ef6\u76d1\u542c\uff0c\u800c\u4e0d\u9700\u8981 <code>AbortController</code> \u5bf9\u8c61</p> <blockquote> <p>\u4f46\u662f\uff0c<code>fetch</code> \u77e5\u9053\u5982\u4f55\u4e0e <code>AbortController</code> \u5bf9\u8c61\u4e00\u8d77\u5de5\u4f5c\u3002\u5b83\u4eec\u662f\u96c6\u6210\u5728\u4e00\u8d77\u7684</p> </blockquote> <h3 id="42-\u4e0e-fetch-\u4e00\u8d77\u4f7f\u7528">4.2 \u4e0e fetch \u4e00\u8d77\u4f7f\u7528</h3> <p>\u5c06 <code>AbortController</code> \u7684 <code>signal</code> \u5c5e\u6027\u4f5c\u4e3a <code>fetch</code> \u7684\u4e00\u4e2a\u53ef\u9009\u53c2\u6570\u8fdb\u884c\u4f20\u9012</p> <p><code>fetch</code> \u4f1a\u76d1\u542c <code>signal</code> \u4e0a\u7684 <code>abort</code> \u4e8b\u4ef6\uff0c\u8c03\u7528 <code>controller.abort()</code> \u5c31\u53ef\u4ee5\u4e2d\u6b62 <code>fetch</code></p> <pre><code class="language-js">let controller = new AbortController();\n\nfetch(url, {\n  signal: controller.signal\n});\n\ncontroller.abort();</code></pre> <p>\u5f53\u4e00\u4e2a <code>fetch</code> \u88ab\u4e2d\u6b62\uff0c\u5b83\u7684 <code>promise</code> \u5c31\u4f1a\u4ee5\u4e00\u4e2a error <code>AbortError</code> reject</p> <p><strong>\u5b8c\u6574\u7684\u793a\u4f8b\uff1a</strong></p> <pre><code class="language-js">// 1 \u79d2\u540e\u4e2d\u6b62\nlet controller = new AbortController();\nsetTimeout(() =&gt; controller.abort(), 1000);\n\ntry {\n  let response = await fetch(&#39;/article/fetch-abort/demo/hang&#39;, {\n    signal: controller.signal\n  });\n} catch (err) {\n  if (err.name == &#39;AbortError&#39;) {\n    // handle abort()\n    alert(&#39;Aborted!&#39;);\n  } else {\n    throw err;\n  }\n}</code></pre> <blockquote> <p><code>AbortController</code> \u662f\u53ef\u4f38\u7f29\u7684\u3002\u5b83\u5141\u8bb8\u4e00\u6b21\u53d6\u6d88\u591a\u4e2a <code>fetch</code></p> </blockquote> <h2 id="5-fetch-\u8de8\u6e90\u8bf7\u6c42">5 Fetch \u8de8\u6e90\u8bf7\u6c42</h2> <h3 id="51-\u5b89\u5168\u8bf7\u6c42">5.1 \u5b89\u5168\u8bf7\u6c42</h3> <p><strong>\u5b89\u5168\u8bf7\u6c42\u9700\u8981\u6ee1\u8db3\u4e0b\u9762\u4e24\u4e2a\u6761\u4ef6\uff1a</strong></p> <ul> <li>\u5b89\u5168\u7684\u65b9\u6cd5\uff1a<code>GET</code>\uff0c<code>POST</code> \u6216 <code>HEAD</code></li> <li>\u5b89\u5168\u7684 <code>header</code> \u2014\u2014 \u4ec5\u5141\u8bb8\u81ea\u5b9a\u4e49\u4e0b\u5217 <code>header</code><ul> <li><code>Accept</code></li> <li><code>Accept-Language</code></li> <li><code>Content-Language</code></li> <li><code>Content-Type</code> \u7684\u503c\u4e3a <code>application/x-www-form-urlencoded</code>\uff0c<code>multipart/form-data</code> \u6216 <code>text/plain</code></li> </ul> </li> </ul> <p>\u4efb\u4f55\u5176\u4ed6\u8bf7\u6c42\u90fd\u88ab\u8ba4\u4e3a\u662f\u975e\u5b89\u5168\u8bf7\u6c42\u3002\u4f8b\u5982\uff0c\u5177\u6709 <code>PUT</code> \u65b9\u6cd5\u6216 <code>API-Key</code> HTTP-header \u7684\u8bf7\u6c42\u5c31\u4e0d\u662f\u5b89\u5168\u8bf7\u6c42</p> <p>\u5f53\u53d1\u9001\u4e00\u4e2a\u975e\u5b89\u5168\u8bf7\u6c42\u65f6\uff0c\u6d4f\u89c8\u5668\u4f1a\u53d1\u9001\u4e00\u4e2a\u7279\u6b8a\u7684 <code>\u9884\u68c0</code> \u8bf7\u6c42\u5230\u670d\u52a1\u5668 \u2014\u2014 \u8be2\u95ee\u670d\u52a1\u5668\u662f\u5426\u63a5\u53d7\u6b64\u7c7b\u8de8\u6e90\u8bf7\u6c42</p> <p>\u5e76\u4e14\uff0c\u9664\u975e\u670d\u52a1\u5668\u660e\u786e\u901a\u8fc7 <code>header</code> \u8fdb\u884c\u786e\u8ba4\uff0c\u5426\u5219\u975e\u5b89\u5168\u8bf7\u6c42\u4e0d\u4f1a\u88ab\u53d1\u9001</p> <h3 id="52-\u7528\u4e8e\u5b89\u5168\u8bf7\u6c42\u7684-cors">5.2 \u7528\u4e8e\u5b89\u5168\u8bf7\u6c42\u7684 CORS</h3> <p>\u5982\u679c\u4e00\u4e2a\u8bf7\u6c42\u662f\u8de8\u6e90\u7684\uff0c\u6d4f\u89c8\u5668\u59cb\u7ec8\u4f1a\u5411\u5176\u6dfb\u52a0 <code>Origin header</code></p> <pre><code>GET /request\nHost: anywhere.com\nOrigin: https://javascript.info\n...</code></pre><p>\u670d\u52a1\u5668\u53ef\u4ee5\u68c0\u67e5 <code>Origin</code>\uff0c\u5982\u679c\u540c\u610f\u63a5\u53d7\u8fd9\u6837\u7684\u8bf7\u6c42\uff0c\u5c31\u4f1a\u5728\u54cd\u5e94\u4e2d\u6dfb\u52a0\u4e00\u4e2a\u7279\u6b8a\u7684 <code>header</code> <code>Access-Control-Allow-Origin</code>\u3002\u8be5 <code>header</code> \u5305\u542b\u4e86\u5141\u8bb8\u7684\u6e90\u6216\u8005\u4e00\u4e2a\u661f\u53f7 <code>*</code></p> <blockquote> <p>\u6ce8\u610f\uff1a\u9ed8\u8ba4\u4e0d\u8fd4\u56de <code>Content-Length</code> header\uff0c\u56e0\u6b64\uff0c\u5982\u679c\u6211\u4eec\u6b63\u5728\u4e0b\u8f7d\u67d0\u4e9b\u5185\u5bb9\uff0c\u5e76\u5e0c\u671b\u8ddf\u8e2a\u8fdb\u5ea6\u767e\u5206\u6bd4\uff0c\u5219\u9700\u8981\u989d\u5916\u7684\u6743\u9650\u624d\u80fd\u8bbf\u95ee\u8be5 header\u3002\u670d\u52a1\u5668\u5fc5\u987b\u53d1\u9001 <code>Access-Control-Expose-Headers</code> header\u3002\u5b83\u5305\u542b\u4e00\u4e2a\u4ee5\u9017\u53f7\u5206\u9694\u7684\u5e94\u8be5\u88ab\u8bbe\u7f6e\u4e3a\u53ef\u8bbf\u95ee\u7684\u975e\u5b89\u5168 header \u540d\u79f0\u5217\u8868</p> </blockquote> <p><strong>\u4f8b\u5982\uff1a</strong></p> <pre><code>200 OK\nContent-Type:text/html; charset=UTF-8\nContent-Length: 12345\nAPI-Key: 2c9de507f2c54aa1\nAccess-Control-Allow-Origin: https://javascript.info\nAccess-Control-Expose-Headers: Content-Length,API-Key</code></pre><h3 id="53-\u975e\u5b89\u5168\u8bf7\u6c42">5.3 \u975e\u5b89\u5168\u8bf7\u6c42</h3> <p>\u975e\u5b89\u5168\u8bf7\u6c42\u4f1a\u5148\u53d1\u9001\u4e00\u4e2a <code>\u9884\u68c0</code> \u8bf7\u6c42\uff0c\u6765\u8bf7\u6c42\u8bb8\u53ef</p> <p><strong>\u9884\u68c0\u8bf7\u6c42\u4f7f\u7528 <code>OPTIONS</code> \u65b9\u6cd5\uff0c\u5b83\u6ca1\u6709 <code>body</code>\uff0c\u4f46\u662f\u6709\u4e09\u4e2a <code>header</code>\uff1a</strong></p> <ul> <li><code>Access-Control-Request-Method</code> \u5e26\u6709\u975e\u5b89\u5168\u8bf7\u6c42\u7684\u65b9\u6cd5</li> <li><code>Access-Control-Request-Headers</code> \u63d0\u4f9b\u4e00\u4e2a\u4ee5\u9017\u53f7\u5206\u9694\u7684\u975e\u5b89\u5168 <code>HTTP-header</code> \u5217\u8868</li> </ul> <p>\u5982\u679c\u670d\u52a1\u5668\u540c\u610f\u5904\u7406\u8bf7\u6c42\u4f1a\u76f8\u5e94 200\uff0c\u6ca1\u6709 <code>body</code>\uff0c\u5177\u6709 <code>header</code>\uff1a</p> <ul> <li><code>Access-Control-Allow-Origin</code> \u5fc5\u987b\u4e3a <code>*</code> \u6216\u8fdb\u884c\u8bf7\u6c42\u7684\u6e90\u624d\u80fd\u5141\u8bb8\u6b64\u8bf7\u6c42</li> <li><code>Access-Control-Allow-Methods</code> \u5fc5\u987b\u5177\u6709\u5141\u8bb8\u7684\u65b9\u6cd5</li> <li><code>Access-Control-Allow-Headers</code> \u5fc5\u987b\u5177\u6709\u4e00\u4e2a\u5141\u8bb8\u7684 <code>header</code> \u5217\u8868</li> <li><code>Access-Control-Max-Age</code> \u53ef\u4ee5\u6307\u5b9a\u7f13\u5b58\u6b64\u6743\u9650\u7684\u79d2\u6570\u3002\u56e0\u6b64\uff0c\u6d4f\u89c8\u5668\u4e0d\u662f\u5fc5\u987b\u4e3a\u6ee1\u8db3\u7ed9\u5b9a\u6743\u9650\u7684\u540e\u7eed\u8bf7\u6c42\u53d1\u9001\u9884\u68c0</li> </ul> <h3 id="54-\u51ed\u636e\uff08credentials\uff09">5.4 \u51ed\u636e\uff08Credentials\uff09</h3> <p>\u9ed8\u8ba4\u60c5\u51b5\u4e0b\uff0c\u7531 JavaScript \u4ee3\u7801\u53d1\u8d77\u7684\u8de8\u6e90\u8bf7\u6c42\u4e0d\u4f1a\u5e26\u6765\u4efb\u4f55\u51ed\u636e\uff08<code>cookies</code> \u6216\u8005 <code>HTTP</code> \u8ba4\u8bc1\uff09</p> <p>\u8981\u5728 <code>fetch</code> \u4e2d\u53d1\u9001\u51ed\u636e\uff0c\u6211\u4eec\u9700\u8981\u6dfb\u52a0 <code>credentials: &quot;include&quot;</code> \u9009\u9879\uff1a</p> <pre><code class="language-js">fetch(&#39;http://another.com&#39;, {\n  credentials: &#39;include&#39;\n});</code></pre> <p>\u5982\u679c\u670d\u52a1\u5668\u540c\u610f\u63a5\u53d7\u5e26\u6709\u51ed\u636e\u7684\u8bf7\u6c42\uff0c\u5219\u9664\u4e86 <code>Access-Control-Allow-Origin</code> \u5916\uff0c\u670d\u52a1\u5668\u8fd8\u5e94\u8be5\u5728\u54cd\u5e94\u4e2d\u6dfb\u52a0 <code>Access-Control-Allow-Credentials: true</code></p> <h2 id="6-fetch-api">6 Fetch API</h2> <p><strong>\u6240\u6709\u53ef\u80fd\u7684 <code>fetch</code> \u9009\u9879\u53ca\u5176\u9ed8\u8ba4\u503c\u7684\u5b8c\u6574\u5217\u8868\uff1a</strong></p> <pre><code class="language-js">let promise = fetch(url, {\n  method: &quot;GET&quot;, // POST\uff0cPUT\uff0cDELETE\uff0c\u7b49\u3002\n  headers: {\n    // \u5185\u5bb9\u7c7b\u578b header \u503c\u901a\u5e38\u662f\u81ea\u52a8\u8bbe\u7f6e\u7684\n    // \u53d6\u51b3\u4e8e request body\n    &quot;Content-Type&quot;: &quot;text/plain;charset=UTF-8&quot;\n  },\n  body: undefined // string\uff0cFormData\uff0cBlob\uff0cBufferSource\uff0c\u6216 URLSearchParams\n  referrer: &quot;about:client&quot;, // \u6216 &quot;&quot; \u4ee5\u4e0d\u53d1\u9001 Referer header\uff0c\n  // \u6216\u8005\u662f\u5f53\u524d\u6e90\u7684 url\n  referrerPolicy: &quot;no-referrer-when-downgrade&quot;, // no-referrer\uff0corigin\uff0csame-origin...\n  mode: &quot;cors&quot;, // same-origin\uff0cno-cors\n  credentials: &quot;same-origin&quot;, // omit\uff0cinclude\n  cache: &quot;default&quot;, // no-store\uff0creload\uff0cno-cache\uff0cforce-cache\uff0c\u6216 only-if-cached\n  redirect: &quot;follow&quot;, // manual\uff0cerror\n  integrity: &quot;&quot;, // \u4e00\u4e2a hash\uff0c\u50cf &quot;sha256-abcdef1234567890&quot;\n  keepalive: false, // true\n  signal: undefined, // AbortController \u6765\u4e2d\u6b62\u8bf7\u6c42\n  window: window // null\n});</code></pre> <h3 id="61-referrer-referrerpolicy">6.1 referrer referrerPolicy</h3> <p>\u901a\u5e38 <code>referrer</code> \u662f\u88ab\u81ea\u52a8\u8bbe\u7f6e\u7684\uff0c\u5e76\u5305\u542b\u4e86<strong>\u53d1\u51fa\u8bf7\u6c42\u7684\u9875\u9762\u7684 url</strong></p> <p><code>referer</code> \u9009\u9879\u5141\u8bb8\u8bbe\u7f6e\u4efb\u4f55 Referer\uff08\u5728\u5f53\u524d\u57df\u7684\uff09\uff0c\u6216\u8005\u79fb\u9664\u5b83</p> <pre><code class="language-js">// \u4e0d\u53d1\u9001 referer\uff0c\u53ef\u4ee5\u5c06 referer \u8bbe\u7f6e\u4e3a\u7a7a\u5b57\u7b26\u4e32\uff1a\nfetch(&#39;/page&#39;, {\n  referrer: &#39;&#39; // \u6ca1\u6709 Referer header\n});\n\n// \u8bbe\u7f6e\u5728\u5f53\u524d\u57df\u5185\u7684\u53e6\u4e00\u4e2a url\uff1a\nfetch(&#39;/page&#39;, {\n  // \u5047\u8bbe\u6211\u4eec\u5728 https://javascript.info\n  // \u6211\u4eec\u53ef\u4ee5\u8bbe\u7f6e\u4efb\u4f55 Referer header\uff0c\u4f46\u5fc5\u987b\u662f\u5728\u5f53\u524d\u57df\u5185\u7684\n  referrer: &#39;https://javascript.info/anotherpage&#39;\n});</code></pre> <p><code>referrerPolicy</code> \u544a\u8bc9\u6d4f\u89c8\u5668\u9488\u5bf9\u5404\u4e2a\u8bf7\u6c42\u7c7b\u578b\u7684\u4e00\u822c\u7684\u89c4\u5219</p> <p><strong><code>Referrer Policy</code> \u53ef\u80fd\u7684\u503c\uff1a</strong></p> <ul> <li><code>no-referrer-when-downgrade</code> \u2014\u2014 \u9ed8\u8ba4\u503c\uff1a\u9664\u975e\u6211\u4eec\u4ece <code>HTTPS</code> \u53d1\u9001\u8bf7\u6c42\u5230 <code>HTTP</code>\uff08\u5230\u5b89\u5168\u6027\u8f83\u4f4e\u7684\u534f\u8bae\uff09\uff0c\u5426\u5219\u59cb\u7ec8\u4f1a\u53d1\u9001\u5b8c\u6574\u7684 <code>Referer</code></li> <li><code>no-referrer</code> \u2014\u2014 \u4ece\u4e0d\u53d1\u9001 <code>Referer</code></li> <li><code>origin</code> \u2014\u2014 \u53ea\u53d1\u9001\u5728 <code>Referer</code> \u4e2d\u7684\u57df\uff0c\u800c\u4e0d\u662f\u5b8c\u6574\u7684\u9875\u9762 <code>URL</code></li> <li><code>origin-when-cross-origin</code> \u2014\u2014 \u53d1\u9001\u5b8c\u6574\u7684 <code>Referer</code> \u5230\u76f8\u540c\u7684\u6e90\uff0c\u4f46\u5bf9\u4e8e\u8de8\u6e90\u8bf7\u6c42\uff0c\u53ea\u53d1\u9001\u57df\u90e8\u5206\uff08\u540c\u4e0a\uff09</li> <li><code>same-origin</code> \u2014\u2014 \u53d1\u9001\u5b8c\u6574\u7684 <code>Referer</code> \u5230\u76f8\u540c\u7684\u6e90\uff0c\u4f46\u5bf9\u4e8e\u8de8\u6e90\u8bf7\u6c42\uff0c\u4e0d\u53d1\u9001 <code>Referer</code></li> <li><code>strict-origin</code> \u2014\u2014 \u53ea\u53d1\u9001\u57df\uff0c\u5bf9\u4e8e <code>HTTPS\u2192HTTP</code> \u8bf7\u6c42\uff0c\u5219\u4e0d\u53d1\u9001 <code>Referer</code></li> <li><code>strict-origin-when-cross-origin</code> \u2014\u2014 \u5bf9\u4e8e\u540c\u6e90\u60c5\u51b5\u4e0b\u5219\u53d1\u9001\u5b8c\u6574\u7684 <code>Referer</code>\uff0c\u5bf9\u4e8e\u8de8\u6e90\u60c5\u51b5\u4e0b\uff0c\u5219\u53ea\u53d1\u9001\u57df\uff0c\u5982\u679c\u662f <code>HTTPS\u2192HTTP</code> \u8bf7\u6c42\uff0c\u5219\u4ec0\u4e48\u90fd\u4e0d\u53d1\u9001</li> <li><code>unsafe-url</code> \u2014\u2014 \u5728 <code>Referer</code> \u4e2d\u59cb\u7ec8\u53d1\u9001\u5b8c\u6574\u7684 <code>url</code>\uff0c\u5373\u4f7f\u662f <code>HTTPS\u2192HTTP</code> \u8bf7\u6c42</li> </ul> <h3 id="62-mode">6.2 mode</h3> <p><strong><code>mode</code> \u9009\u9879\u662f\u4e00\u79cd\u5b89\u5168\u63aa\u65bd\uff0c\u53ef\u4ee5\u9632\u6b62\u5076\u53d1\u7684\u8de8\u6e90\u8bf7\u6c42\uff1a</strong></p> <ul> <li><code>cors</code> \u2014\u2014 \u9ed8\u8ba4\u503c\uff0c\u5141\u8bb8\u8de8\u6e90\u8bf7\u6c42</li> <li><code>same-origin</code> \u2014\u2014 \u7981\u6b62\u8de8\u6e90\u8bf7\u6c42</li> <li><code>no-cors</code> \u2014\u2014 \u53ea\u5141\u8bb8\u5b89\u5168\u7684\u8de8\u6e90\u8bf7\u6c42</li> </ul> <p>\u5f53 <code>fetch</code> \u7684 <code>URL</code> \u6765\u81ea\u4e8e\u7b2c\u4e09\u65b9\uff0c\u5e76\u4e14\u6211\u4eec\u60f3\u8981\u4e00\u4e2a\u201c\u65ad\u7535\u5f00\u5173\u201d\u6765\u9650\u5236\u8de8\u6e90\u80fd\u529b\u65f6\uff0c\u6b64\u9009\u9879\u53ef\u80fd\u5f88\u6709\u7528</p> <h3 id="63-credentials">6.3 credentials</h3> <p><code>credentials</code> \u9009\u9879\u6307\u5b9a <code>fetch</code> \u662f\u5426\u5e94\u8be5\u968f\u8bf7\u6c42\u53d1\u9001 <code>cookie</code> \u548c <code>HTTP-Authorization</code> header</p> <ul> <li><code>same-origin</code> \u2014\u2014 \u9ed8\u8ba4\u503c\uff0c\u5bf9\u4e8e\u8de8\u6e90\u8bf7\u6c42\u4e0d\u53d1\u9001</li> <li><code>include</code> \u2014\u2014 \u603b\u662f\u53d1\u9001\uff0c\u9700\u8981\u6765\u81ea\u8de8\u6e90\u670d\u52a1\u5668\u7684 <code>Access-Control-Allow-Credentials</code>\uff0c\u624d\u80fd\u4f7f JavaScript \u80fd\u591f\u8bbf\u95ee\u54cd\u5e94</li> <li><code>omit</code> \u2014\u2014 \u4e0d\u53d1\u9001\uff0c\u5373\u4f7f\u5bf9\u4e8e\u540c\u6e90\u8bf7\u6c42</li> </ul> <h3 id="64-cache">6.4 cache</h3> <p>\u9ed8\u8ba4\u60c5\u51b5\u4e0b\uff0c<code>fetch</code> \u8bf7\u6c42\u4f7f\u7528\u6807\u51c6\u7684 HTTP \u7f13\u5b58\u3002\u5c31\u662f\u8bf4\uff0c\u5b83\u9075\u4ece <code>Expires</code>\uff0c<code>Cache-Control header</code>\uff0c\u53d1\u9001 <code>If-Modified-Since</code>\uff0c\u7b49\u3002<strong>\u5c31\u50cf\u5e38\u89c4\u7684 HTTP \u8bf7\u6c42\u90a3\u6837</strong></p> <p><strong>\u4f7f\u7528 <code>cache</code> \u9009\u9879\u53ef\u4ee5\u5ffd\u7565 HTTP \u7f13\u5b58\u6216\u8005\u5bf9\u5176\u7528\u6cd5\u8fdb\u884c\u5fae\u8c03\uff1a</strong></p> <ul> <li><code>default</code> \u2014\u2014 <code>fetch</code> \u4f7f\u7528\u6807\u51c6\u7684 HTTP \u7f13\u5b58\u89c4\u5219\u548c header</li> <li><code>no-store</code> \u2014\u2014 \u5b8c\u5168\u5ffd\u7565 HTTP \u7f13\u5b58\uff0c\u5982\u679c\u6211\u4eec\u8bbe\u7f6e header <code>If-Modified-Since</code>\uff0c<code>If-None-Match</code>\uff0c<code>If-Unmodified-Since</code>\uff0c<code>If-Match</code>\uff0c\u6216 <code>If-Range</code>\uff0c\u5219\u6b64\u6a21\u5f0f\u4f1a\u6210\u4e3a\u9ed8\u8ba4\u6a21\u5f0f</li> <li><code>reload</code> \u2014\u2014 \u4e0d\u4ece HTTP \u7f13\u5b58\u4e2d\u83b7\u53d6\u7ed3\u679c\uff08\u5982\u679c\u6709\uff09\uff0c\u800c\u662f\u4f7f\u7528\u54cd\u5e94\u586b\u5145\u7f13\u5b58\uff08\u5982\u679c <code>response header</code> \u5141\u8bb8\u6b64\u64cd\u4f5c\uff09</li> <li><code>no-cache</code> \u2014\u2014 \u5982\u679c\u6709\u4e00\u4e2a\u5df2\u7f13\u5b58\u7684\u54cd\u5e94\uff0c\u5219\u521b\u5efa\u4e00\u4e2a\u6709\u6761\u4ef6\u7684\u8bf7\u6c42\uff0c\u5426\u5219\u521b\u5efa\u4e00\u4e2a\u666e\u901a\u7684\u8bf7\u6c42\u3002\u4f7f\u7528\u54cd\u5e94\u586b\u5145 HTTP \u7f13\u5b58</li> <li><code>force-cache</code> \u2014\u2014 \u4f7f\u7528\u6765\u81ea HTTP \u7f13\u5b58\u7684\u54cd\u5e94\uff0c\u5373\u4f7f\u8be5\u54cd\u5e94\u5df2\u8fc7\u65f6\uff08stale\uff09\u3002\u5982\u679c HTTP \u7f13\u5b58\u4e2d\u6ca1\u6709\u54cd\u5e94\uff0c\u5219\u521b\u5efa\u4e00\u4e2a\u5e38\u89c4\u7684 HTTP \u8bf7\u6c42\uff0c\u884c\u4e3a\u50cf\u6b63\u5e38\u90a3\u6837</li> <li><code>only-if-cached</code> \u2014\u2014 \u4f7f\u7528\u6765\u81ea HTTP \u7f13\u5b58\u7684\u54cd\u5e94\uff0c\u5373\u4f7f\u8be5\u54cd\u5e94\u5df2\u8fc7\u65f6\uff08stale\uff09\u3002\u5982\u679c HTTP \u7f13\u5b58\u4e2d\u6ca1\u6709\u54cd\u5e94\uff0c\u5219\u62a5\u9519\u3002\u53ea\u6709\u5f53 mode \u4e3a same-origin \u65f6\u751f\u6548</li> </ul> <h3 id="65-redirect">6.5 redirect</h3> <p>\u901a\u5e38\u6765\u8bf4\uff0c<code>fetch</code> \u900f\u660e\u5730\u9075\u5faa HTTP \u91cd\u5b9a\u5411\uff0c\u4f8b\u5982 <code>301</code>\uff0c<code>302</code> \u7b49</p> <p><strong><code>redirect</code> \u9009\u9879\u5141\u8bb8\u5bf9\u91cd\u5b9a\u5411\u8fdb\u884c\u66f4\u6539\uff1a</strong></p> <ul> <li><code>follow</code> \u2014\u2014 \u9ed8\u8ba4\u503c\uff0c\u9075\u5faa HTTP \u91cd\u5b9a\u5411</li> <li><code>error</code> \u2014\u2014 HTTP \u91cd\u5b9a\u5411\u65f6\u62a5\u9519</li> <li><code>manual</code> \u2014\u2014 \u5141\u8bb8\u624b\u52a8\u5904\u7406 HTTP \u91cd\u5b9a\u5411\u3002\u5728\u91cd\u5b9a\u5411\u7684\u60c5\u51b5\u4e0b\uff0c\u6211\u4eec\u5c06\u83b7\u5f97\u4e00\u4e2a\u7279\u6b8a\u7684\u54cd\u5e94\u5bf9\u8c61\uff0c\u5176\u4e2d\u5305\u542b <code>response.type=&quot;opaqueredirect&quot;</code> \u548c\u5f52\u96f6/\u7a7a\u72b6\u6001\u4ee5\u53ca\u5927\u591a\u6570\u5176\u4ed6\u5c5e\u6027</li> </ul> <h3 id="66-integrity">6.6 integrity</h3> <p><code>integrity</code> \u9009\u9879\u5141\u8bb8\u68c0\u67e5\u54cd\u5e94\u662f\u5426\u4e0e\u5df2\u77e5\u7684\u9884\u5148\u6821\u9a8c\u548c\u76f8\u5339\u914d</p> <p>\u652f\u6301\u7684\u54c8\u5e0c\u51fd\u6570\u6709 <code>SHA-256</code>\uff0c<code>SHA-384</code>\uff0c\u548c <code>SHA-512</code>\uff0c\u53ef\u80fd\u8fd8\u6709\u5176\u4ed6\u7684\uff0c\u8fd9\u53d6\u51b3\u4e8e\u6d4f\u89c8\u5668</p> <p>\u4f8b\u5982\uff0c\u6211\u4eec\u4e0b\u8f7d\u4e00\u4e2a\u6587\u4ef6\uff0c\u5047\u5982\u5b83\u7684 <code>SHA-256</code> \u6821\u9a8c\u548c\u4e3a \u201cabcdef\u201d</p> <p>\u6211\u4eec\u53ef\u4ee5\u5c06\u5176\u653e\u5728 integrity \u9009\u9879\u4e2d\uff0c\u5c31\u50cf\u8fd9\u6837:</p> <pre><code class="language-js">fetch(&#39;http://site.com/file&#39;, {\n  integrity: &#39;sha256-abcdef&#39;\n});</code></pre> <p>\u7136\u540e <code>fetch</code> \u5c06\u81ea\u884c\u8ba1\u7b97 <code>SHA-256</code> \u5e76\u5c06\u5176\u4e0e\u6211\u4eec\u7684\u5b57\u7b26\u4e32\u8fdb\u884c\u6bd4\u8f83\u3002\u5982\u679c\u4e0d\u5339\u914d\uff0c\u5219\u4f1a\u89e6\u53d1\u9519\u8bef</p> <h3 id="67-keepalive">6.7 keepalive</h3> <p><code>keepalive</code> \u9009\u9879\u8868\u793a\u8be5\u8bf7\u6c42\u53ef\u80fd\u4f1a\u5728<strong>\u7f51\u9875\u5173\u95ed</strong>\u540e\u7ee7\u7eed\u5b58\u5728</p> <p>\u4f8b\u5982\uff0c\u6211\u4eec\u6536\u96c6\u6709\u5173\u5f53\u524d\u8bbf\u95ee\u8005\u662f\u5982\u4f55\u4f7f\u7528\u6211\u4eec\u7684\u9875\u9762\uff08\u9f20\u6807\u70b9\u51fb\uff0c\u4ed6\u67e5\u770b\u7684\u9875\u9762\u7247\u6bb5\uff09\u7684\u7edf\u8ba1\u4fe1\u606f\uff0c\u4ee5\u5206\u6790\u548c\u6539\u5584\u7528\u6237\u4f53\u9a8c\u3002\u5f53\u8bbf\u95ee\u8005\u79bb\u5f00\u6211\u4eec\u7684\u7f51\u9875\u65f6 \u2014\u2014 \u6211\u4eec\u5e0c\u671b\u80fd\u591f\u5c06\u6570\u636e\u4fdd\u5b58\u5230\u6211\u4eec\u7684\u670d\u52a1\u5668\u4e0a</p> <p>\u6211\u4eec\u53ef\u4ee5\u4f7f\u7528 <code>window.onunload</code> \u4e8b\u4ef6\u6765\u5b9e\u73b0\uff1a</p> <pre><code class="language-js">window.onunload = function() {\n  fetch(&#39;/analytics&#39;, {\n    method: &#39;POST&#39;,\n    body: &#39;statistics&#39;,\n    keepalive: true\n  });\n};</code></pre> <blockquote> <p>\u901a\u5e38\uff0c\u5f53\u4e00\u4e2a\u6587\u6863\u88ab\u5378\u8f7d\u65f6\uff08unloaded\uff09\uff0c\u6240\u6709\u76f8\u5173\u7684\u7f51\u7edc\u8bf7\u6c42\u90fd\u4f1a\u88ab\u4e2d\u6b62\u3002\u4f46\u662f\uff0c<code>keepalive</code> \u9009\u9879\u544a\u8bc9\u6d4f\u89c8\u5668\uff0c\u5373\u4f7f\u5728\u79bb\u5f00\u9875\u9762\u540e\uff0c\u4e5f\u8981\u5728\u540e\u53f0\u6267\u884c\u8bf7\u6c42</p> </blockquote> <p><strong><code>keepalive</code> \u6709\u4e00\u4e9b\u9650\u5236\uff1a</strong></p> <ul> <li>\u6211\u4eec\u65e0\u6cd5\u53d1\u9001\u5146\u5b57\u8282\u7684\u6570\u636e\uff1a<code>keepalive</code> \u8bf7\u6c42\u7684 <code>body</code> \u9650\u5236\u4e3a <code>64KB</code><ul> <li>\u5982\u679c\u6211\u4eec\u9700\u8981\u6536\u96c6\u6709\u5173\u8bbf\u95ee\u7684\u5927\u91cf\u7edf\u8ba1\u4fe1\u606f\uff0c\u6211\u4eec\u5219\u5e94\u8be5\u5c06\u5176\u5b9a\u671f\u4ee5\u6570\u636e\u5305\u7684\u5f62\u5f0f\u53d1\u9001\u51fa\u53bb\uff0c\u8fd9\u6837\u5c31\u4e0d\u4f1a\u7559\u4e0b\u592a\u591a\u6570\u636e\u7ed9\u6700\u540e\u7684 <code>onunload</code> \u8bf7\u6c42\u4e86</li> <li>\u6b64\u9650\u5236\u662f\u88ab\u5e94\u7528\u4e8e\u5f53\u524d\u6240\u6709 <code>keepalive</code> \u8bf7\u6c42\u7684\u603b\u548c\u7684\u3002\u6362\u53e5\u8bdd\u8bf4\uff0c\u6211\u4eec\u53ef\u4ee5\u5e76\u884c\u6267\u884c\u591a\u4e2a <code>keepalive</code> \u8bf7\u6c42\uff0c\u4f46\u5b83\u4eec\u7684 <code>body</code> \u957f\u5ea6\u4e4b\u548c\u4e0d\u5f97\u8d85\u8fc7 <code>64KB</code></li> </ul> </li> <li>\u5982\u679c\u6587\u6863\uff08document\uff09\u5df2\u5378\u8f7d\uff08unloaded\uff09\uff0c\u6211\u4eec\u5c31\u65e0\u6cd5\u5904\u7406\u670d\u52a1\u5668\u54cd\u5e94\u3002\u56e0\u6b64\uff0c\u5728\u6211\u4eec\u7684\u793a\u4f8b\u4e2d\uff0c<code>fetch</code> \u4f1a\u6210\u529f\uff0c\u4f46\u662f\u540e\u7eed\u7684\u51fd\u6570\u5c06\u65e0\u6cd5\u6b63\u5e38\u5de5\u4f5c<ul> <li>\u5728\u5927\u591a\u6570\u60c5\u51b5\u4e0b\uff0c\u4f8b\u5982\u53d1\u9001\u7edf\u8ba1\u4fe1\u606f\uff0c\u8fd9\u4e0d\u662f\u95ee\u9898\uff0c\u56e0\u4e3a\u670d\u52a1\u5668\u53ea\u63a5\u6536\u6570\u636e\uff0c\u5e76\u901a\u5e38\u5411\u6b64\u7c7b\u8bf7\u6c42\u53d1\u9001\u7a7a\u7684\u54cd\u5e94</li> </ul> </li> </ul> <h2 id="7-url-\u5bf9\u8c61">7 URL \u5bf9\u8c61</h2> <h3 id="71-\u521b\u5efa-url-\u5bf9\u8c61">7.1 \u521b\u5efa URL \u5bf9\u8c61</h3> <p>\u521b\u5efa\u4e00\u4e2a\u65b0 URL \u5bf9\u8c61\u7684\u8bed\u6cd5\uff1a</p> <pre><code class="language-js">new URL(url, [base]);</code></pre> <ul> <li><code>url</code> \u2014\u2014 \u5b8c\u6574\u7684 <code>URL</code>\uff0c\u6216\u8005\u4ec5\u8def\u5f84\uff08\u5982\u679c\u8bbe\u7f6e\u4e86 base\uff09</li> <li><code>base</code> \u2014\u2014 \u53ef\u9009\u7684 <code>base URL</code>\uff1a\u5982\u679c\u8bbe\u7f6e\u4e86\u6b64\u53c2\u6570\uff0c\u4e14\u53c2\u6570 <code>url</code> \u53ea\u6709\u8def\u5f84\uff0c\u5219\u4f1a\u6839\u636e\u8fd9\u4e2a <code>base</code> \u751f\u6210 <code>URL</code></li> </ul> <p><strong>\u4f8b\u5982\uff1a</strong></p> <pre><code class="language-js">let url1 = new URL(&#39;https://javascript.info/profile/admin&#39;);\nlet url2 = new URL(&#39;/profile/admin&#39;, &#39;https://javascript.info&#39;);\n\nalert(url1); // https://javascript.info/profile/admin\nalert(url2); // https://javascript.info/profile/admin</code></pre> <p><strong>\u89e3\u6790 url\uff1a</strong></p> <pre><code class="language-js">let url = new URL(&#39;https://javascript.info/url&#39;);\n\nalert(url.protocol); // https:\nalert(url.host); // javascript.info\nalert(url.pathname); // /url</code></pre> <p><strong>URL \u7684\u5c5e\u6027\uff1a</strong></p> <p><img src="'+r+'" alt=""></p> <ul> <li><code>href</code> \u662f\u5b8c\u6574\u7684 URL\uff0c\u4e0e <code>url.toString()</code> \u76f8\u540c</li> <li><code>protocol</code> \u4ee5\u5192\u53f7\u5b57\u7b26 <code>:</code> \u7ed3\u5c3e</li> <li><code>search</code> \u2014\u2014 \u4ee5\u95ee\u53f7 <code>?</code> \u5f00\u5934\u7684\u4e00\u4e32\u53c2\u6570</li> <li><code>hash</code> \u4ee5\u54c8\u5e0c\u5b57\u7b26 <code>#</code> \u5f00\u5934</li> <li>\u5982\u679c\u5b58\u5728 <code>HTTP</code> \u8eab\u4efd\u9a8c\u8bc1\uff0c\u5219\u8fd9\u91cc\u53ef\u80fd\u8fd8\u4f1a\u6709 <code>user</code> \u548c <code>password</code> \u5c5e\u6027\uff1a<code>http://login:password@site.com</code>\uff08\u5f88\u5c11\u88ab\u7528\u5230\uff09</li> </ul> <blockquote> <p>\u6211\u4eec\u53ef\u4ee5\u5728 <code>fetch</code> \u6216 <code>XMLHttpRequest</code> \u4e2d\u4f7f\u7528 <code>URL</code> \u5bf9\u8c61\uff0c\u51e0\u4e4e\u53ef\u4ee5\u5728\u4efb\u4f55\u9700\u8981 <code>URL</code> \u5b57\u7b26\u4e32\u7684\u5730\u65b9\u90fd\u80fd\u4f7f\u7528 <code>URL</code> \u5bf9\u8c61</p> </blockquote> <h3 id="72-searchparams">7.2 SearchParams</h3> <p>\u5047\u8bbe\uff0c\u6211\u4eec\u60f3\u8981\u521b\u5efa\u4e00\u4e2a\u5177\u6709\u7ed9\u5b9a\u641c\u7d22\u53c2\u6570\u7684 <code>url</code>\uff0c\u4f8b\u5982\uff1a<code>https://google.com/search?query=JavaScript</code></p> <p>\u6211\u4eec\u53ef\u4ee5\u5728 <code>URL</code> \u5b57\u7b26\u4e32\u4e2d\u63d0\u4f9b\u5b83\u4eec\u3002\u4f46\u662f\uff0c\u5982\u679c\u53c2\u6570\u4e2d\u5305\u542b\u7a7a\u683c\uff0c\u975e\u62c9\u4e01\u5b57\u6bcd\u7b49\uff0c\u53c2\u6570\u5c31\u9700\u8981\u88ab\u7f16\u7801</p> <p>\u56e0\u6b64\uff0c\u6709\u4e00\u4e2a <code>URL</code> \u5c5e\u6027\u7528\u4e8e\u89e3\u51b3\u8fd9\u4e2a\u95ee\u9898\uff1a<code>url.searchParams</code>\uff0c<code>URLSearchParams</code> \u7c7b\u578b\u7684\u5bf9\u8c61</p> <p><strong>\u5b83\u4e3a\u641c\u7d22\u53c2\u6570\u63d0\u4f9b\u4e86\u7b80\u4fbf\u7684\u65b9\u6cd5\uff1a</strong></p> <ul> <li><code>append(name, value)</code> \u2014\u2014 \u6309\u7167 <code>name</code> \u6dfb\u52a0\u53c2\u6570</li> <li><code>delete(name)</code> \u2014\u2014 \u6309\u7167 <code>name</code> \u79fb\u9664\u53c2\u6570</li> <li><code>get(name)</code> \u2014\u2014 \u6309\u7167 <code>name</code> \u83b7\u53d6\u53c2\u6570</li> <li><code>getAll(name)</code> \u2014\u2014 \u83b7\u53d6\u76f8\u540c <code>name</code> \u7684\u6240\u6709\u53c2\u6570\uff08\u4f8b\u5982 <code>?user=John&amp;user=Pete</code>\uff09</li> <li><code>has(name)</code> \u2014\u2014 \u6309\u7167 <code>name</code> \u68c0\u67e5\u53c2\u6570\u662f\u5426\u5b58\u5728</li> <li><code>set(name, value)</code> \u2014\u2014 <code>set/replace</code> \u53c2\u6570</li> <li><code>sort()</code> \u2014\u2014 \u6309 <code>name</code> \u5bf9\u53c2\u6570\u8fdb\u884c\u6392\u5e8f\uff0c\u5f88\u5c11\u4f7f\u7528</li> <li>\u5b83\u662f\u53ef\u8fed\u4ee3\u7684\uff0c\u7c7b\u4f3c\u4e8e <code>Map</code></li> </ul> <p><strong>\u793a\u4f8b\uff1a</strong></p> <pre><code class="language-js">let url = new URL(&#39;https://google.com/search&#39;);\n\nurl.searchParams.set(&#39;q&#39;, &#39;test me!&#39;); // \u6dfb\u52a0\u5e26\u6709\u4e00\u4e2a\u7a7a\u683c\u548c\u4e00\u4e2a ! \u7684\u53c2\u6570\nalert(url); // https://google.com/search?q=test+me%21\n\nurl.searchParams.set(&#39;tbs&#39;, &#39;qdr:y&#39;); // \u6dfb\u52a0\u5e26\u6709\u4e00\u4e2a\u5192\u53f7 : \u7684\u53c2\u6570\nalert(url); // https://google.com/search?q=test+me%21&amp;tbs=qdr%3Ay\n\n// \u904d\u5386\u641c\u7d22\u53c2\u6570\uff08\u88ab\u89e3\u7801\uff09\nfor (let [name, value] of url.searchParams) {\n  alert(`${name}=${value}`); // q=test me!\uff0c\u7136\u540e\u662f tbs=qdr:y\n}</code></pre> <h3 id="73-\u7f16\u7801">7.3 \u7f16\u7801</h3> <p><code>RFC3986</code> \u6807\u51c6\u5b9a\u4e49\u4e86 <code>URL</code> \u4e2d\u5141\u8bb8\u54ea\u4e9b\u5b57\u7b26\uff0c\u4e0d\u5141\u8bb8\u54ea\u4e9b\u5b57\u7b26</p> <p>\u90a3\u4e9b\u4e0d\u88ab\u5141\u8bb8\u7684\u5b57\u7b26\u5fc5\u987b\u88ab\u7f16\u7801\uff0c\u4f8b\u5982\u975e\u62c9\u4e01\u5b57\u6bcd\u548c\u7a7a\u683c \u2014\u2014 \u7528\u5176 <code>UTF-8</code> \u4ee3\u7801\u4ee3\u66ff\uff0c\u524d\u7f00\u4e3a <code>%</code>\uff0c\u4f8b\u5982 <code>%20</code>\uff08\u7a7a\u683c\u53ef\u4ee5\u7528 <code>+</code> \u7f16\u7801\uff0c\u4f46\u8fd9\u662f\u4e00\u4e2a\u4f8b\u5916\uff09</p> <p><strong>\u7f16\u7801\u5b57\u7b26\u4e32</strong></p> <p>\u5728\u51fa\u73b0 <code>URL</code> \u5bf9\u8c61\u4e4b\u524d\uff0c\u4f7f\u7528\u5b57\u7b26\u4e32\u4f5c\u4e3a <code>URL</code>\u3002</p> <p>\u5982\u679c\u4f7f\u7528\u5b57\u7b26\u4e32\uff0c\u5219\u9700\u8981\u624b\u52a8\u7f16\u7801/\u89e3\u7801\u7279\u6b8a\u5b57\u7b26</p> <p><strong>\u7528\u4e8e\u7f16\u7801/\u89e3\u7801 URL \u7684\u5185\u5efa\u51fd\u6570\uff1a</strong></p> <ul> <li><code>encodeURI</code> \u2014\u2014 \u7f16\u7801\u6574\u4e2a <code>URL</code></li> <li><code>decodeURI</code> \u2014\u2014 \u89e3\u7801\u4e3a\u7f16\u7801\u524d\u7684\u72b6\u6001</li> <li><code>encodeURIComponent</code> \u2014\u2014 \u7f16\u7801 <code>URL</code> \u7ec4\u4ef6\uff0c\u4f8b\u5982\u641c\u7d22\u53c2\u6570\uff0c\u6216\u8005 <code>hash</code>\uff0c\u6216\u8005 <code>pathname</code></li> <li><code>decodeURIComponent</code> \u2014\u2014 \u89e3\u7801\u4e3a\u7f16\u7801\u524d\u7684\u72b6\u6001</li> </ul> <p><code>encodeURIComponent</code> \u548c <code>encodeURI</code> \u4e4b\u95f4\u6709\u4ec0\u4e48\u533a\u522b\uff1f\u6211\u4eec\u4ec0\u4e48\u65f6\u5019\u5e94\u8be5\u4f7f\u7528\u54ea\u4e2a\uff1f</p> <ul> <li><code>encodeURI</code> \u4ec5\u7f16\u7801 <code>URL</code> \u4e2d\u5b8c\u5168\u7981\u6b62\u7684\u5b57\u7b26</li> <li><code>encodeURIComponent</code> \u4e5f\u7f16\u7801\u8fd9\u7c7b\u5b57\u7b26\uff0c\u6b64\u5916\uff0c\u8fd8\u7f16\u7801 <code># $ &amp; + , / : ; = ? @</code> \u5b57\u7b26</li> </ul> <p><strong><code>encode*</code> \u4e0e <code>URL</code> \u4e4b\u95f4\u7684\u7f16\u7801\u5dee\u5f02:</strong></p> <p><code>URL</code> \u548c <code>URLSearchParams</code> \u57fa\u4e8e\u6700\u65b0\u7684 <code>URL</code> \u89c4\u8303\uff1a<code>RFC3986</code>\uff0c\u800c <code>encode*</code> \u51fd\u6570\u662f\u57fa\u4e8e\u8fc7\u65f6\u7684 <code>RFC2396</code></p> <p>\u5b83\u4eec\u4e4b\u95f4\u6709\u4e00\u4e9b\u533a\u522b\uff0c\u4f8b\u5982\u5bf9 <code>IPv6</code> \u5730\u5740\u7684\u7f16\u7801\u65b9\u5f0f\u4e0d\u540c\uff1a</p> <pre><code class="language-js">// IPv6 \u5730\u5740\u7684\u5408\u6cd5 url\nlet url = &#39;http://[2607:f8b0:4005:802::1007]/&#39;;\n\nalert(encodeURI(url)); // http://%5B2607:f8b0:4005:802::1007%5D/\nalert(new URL(url)); // http://[2607:f8b0:4005:802::1007]/</code></pre> <p><code>encodeURI</code> \u66ff\u6362\u4e86\u65b9\u62ec\u53f7 <code>[]</code>\uff0c\u8fd9\u662f\u4e0d\u6b63\u786e\u7684\u3002\u56e0\u4e3a\u5728 <code>RFC2396</code> (August 1998) \u65f6\u4ee3\uff0c\u8fd8\u4e0d\u5b58\u5728 <code>IPv6</code> url</p> <h2 id="8-xmlhttprequest">8 XMLHttpRequest</h2> <p><code>XMLHttpRequest</code> \u662f\u4e00\u4e2a\u5185\u5efa\u7684\u6d4f\u89c8\u5668\u5bf9\u8c61\uff0c\u5b83\u5141\u8bb8\u4f7f\u7528 <code>JavaScript</code> \u53d1\u9001 <code>HTTP</code> \u8bf7\u6c42</p> <p>\u5728\u73b0\u4ee3 <code>Web</code> \u5f00\u53d1\u4e2d\uff0c\u51fa\u4e8e\u4ee5\u4e0b\u4e09\u79cd\u539f\u56e0\uff0c\u6211\u4eec\u8fd8\u5728\u4f7f\u7528 <code>XMLHttpRequest</code>\uff1a</p> <ol> <li>\u5386\u53f2\u539f\u56e0\uff1a\u6211\u4eec\u9700\u8981\u652f\u6301\u73b0\u6709\u7684\u4f7f\u7528\u4e86 <code>XMLHttpRequest</code> \u7684\u811a\u672c</li> <li>\u6211\u4eec\u9700\u8981\u517c\u5bb9\u65e7\u6d4f\u89c8\u5668\uff0c\u5e76\u4e14\u4e0d\u60f3\u7528 <code>polyfill</code>\uff08\u4f8b\u5982\u4e3a\u4e86\u4f7f\u811a\u672c\u66f4\u5c0f\uff09</li> <li>\u6211\u4eec\u9700\u8981\u505a\u4e00\u4e9b <code>fetch</code> \u76ee\u524d\u65e0\u6cd5\u505a\u5230\u7684\u4e8b\u60c5\uff0c\u4f8b\u5982\u8ddf\u8e2a\u4e0a\u4f20\u8fdb\u5ea6</li> </ol> <h3 id="81-xmlhttprequest-\u57fa\u7840">8.1 XMLHttpRequest \u57fa\u7840</h3> <p><strong>\u8bf7\u6c42\u793a\u4f8b\uff1a</strong></p> <pre><code class="language-js">// 1. \u521b\u5efa\u4e00\u4e2a new XMLHttpRequest \u5bf9\u8c61\nlet xhr = new XMLHttpRequest();\n\n// 2. \u914d\u7f6e\u5b83\uff1a\u4ece URL /article/.../load GET-request\nxhr.open(&#39;GET&#39;, &#39;/article/xmlhttprequest/example/load&#39;);\n\n// 3. \u901a\u8fc7\u7f51\u7edc\u53d1\u9001\u8bf7\u6c42\nxhr.send();\n\n// 4. \u5f53\u63a5\u6536\u5230\u54cd\u5e94\u540e\uff0c\u5c06\u8c03\u7528\u6b64\u51fd\u6570\nxhr.onload = function() {\n  if (xhr.status != 200) {\n    // \u5206\u6790\u54cd\u5e94\u7684 HTTP \u72b6\u6001\n    alert(`Error ${xhr.status}: ${xhr.statusText}`); // \u4f8b\u5982 404: Not Found\n  } else {\n    // \u663e\u793a\u7ed3\u679c\n    alert(`Done, got ${xhr.response.length} bytes`); // response \u662f\u670d\u52a1\u5668\u54cd\u5e94\n  }\n};\n\nxhr.onprogress = function(event) {\n  if (event.lengthComputable) {\n    alert(`Received ${event.loaded} of ${event.total} bytes`);\n  } else {\n    alert(`Received ${event.loaded} bytes`); // \u6ca1\u6709 Content-Length\n  }\n};\n\nxhr.onerror = function() {\n  alert(&#39;Request failed&#39;);\n};</code></pre> <p><strong>xhr.open\uff1a</strong></p> <pre><code class="language-js">xhr.open(method, URL, [async, user, password]);</code></pre> <ul> <li><code>method</code> \u2014\u2014 <code>HTTP</code> \u65b9\u6cd5\u3002\u901a\u5e38\u662f <code>GET</code> \u6216 <code>POST</code></li> <li><code>URL</code> \u2014\u2014 \u8981\u8bf7\u6c42\u7684 URL\uff0c\u901a\u5e38\u662f\u4e00\u4e2a\u5b57\u7b26\u4e32\uff0c\u4e5f\u53ef\u4ee5\u662f <code>URL</code> \u5bf9\u8c61</li> <li><code>async</code> \u2014\u2014 \u5982\u679c\u663e\u5f0f\u5730\u8bbe\u7f6e\u4e3a <code>false</code>\uff0c\u90a3\u4e48\u8bf7\u6c42\u5c06\u4f1a\u4ee5\u540c\u6b65\u7684\u65b9\u5f0f\u5904\u7406\uff0c\u6211\u4eec\u7a0d\u540e\u4f1a\u8bb2\u5230\u5b83</li> <li><code>user\uff0cpassword</code> \u2014\u2014 <code>HTTP</code> \u57fa\u672c\u8eab\u4efd\u9a8c\u8bc1\uff08\u5982\u679c\u9700\u8981\u7684\u8bdd\uff09\u7684\u767b\u5f55\u540d\u548c\u5bc6\u7801</li> </ul> <p><strong>xhr \u4e8b\u4ef6\uff1a</strong></p> <ul> <li><code>load</code> \u2014\u2014 \u5f53\u8bf7\u6c42\u5b8c\u6210\uff08\u5373\u4f7f HTTP \u72b6\u6001\u4e3a 400 \u6216 500 \u7b49\uff09\uff0c\u5e76\u4e14\u54cd\u5e94\u5df2\u5b8c\u5168\u4e0b\u8f7d</li> <li><code>error</code> \u2014\u2014 \u5f53\u65e0\u6cd5\u53d1\u51fa\u8bf7\u6c42\uff0c\u4f8b\u5982\u7f51\u7edc\u4e2d\u65ad\u6216\u8005\u65e0\u6548\u7684 URL</li> <li><code>progress</code> \u2014\u2014 \u5728\u4e0b\u8f7d\u54cd\u5e94\u671f\u95f4\u5b9a\u671f\u89e6\u53d1\uff0c\u62a5\u544a\u5df2\u7ecf\u4e0b\u8f7d\u4e86\u591a\u5c11</li> </ul> <p><strong>status\uff1a</strong></p> <p><code>HTTP</code> \u72b6\u6001\u7801\uff08\u4e00\u4e2a\u6570\u5b57\uff09\uff1a<code>200</code>\uff0c<code>404</code>\uff0c<code>403</code> \u7b49\uff0c\u5982\u679c\u51fa\u73b0\u975e <code>HTTP</code> \u9519\u8bef\uff0c\u5219\u4e3a <code>0</code></p> <p><strong>statusText\uff1a</strong></p> <p><code>HTTP</code> \u72b6\u6001\u6d88\u606f\uff08\u4e00\u4e2a\u5b57\u7b26\u4e32\uff09\uff1a\u72b6\u6001\u7801\u4e3a <code>200</code> \u5bf9\u5e94\u4e8e <code>OK</code>\uff0c<code>404</code> \u5bf9\u5e94\u4e8e <code>Not Found</code>\uff0c<code>403</code> \u5bf9\u5e94\u4e8e <code>Forbidden</code></p> <p><strong>response\uff1a</strong></p> <p>\u670d\u52a1\u5668 <code>response body</code></p> <p><strong>\u8d85\u65f6\u65f6\u95f4\uff1a</strong></p> <pre><code class="language-js">xhr.timeout = 10000; // timeout \u5355\u4f4d\u662f ms\uff0c\u6b64\u5904\u5373 10 \u79d2</code></pre> <p>\u5982\u679c\u5728\u7ed9\u5b9a\u65f6\u95f4\u5185\u8bf7\u6c42\u6ca1\u6709\u6210\u529f\u6267\u884c\uff0c\u8bf7\u6c42\u5c31\u4f1a\u88ab\u53d6\u6d88\uff0c\u5e76\u4e14\u89e6\u53d1 <code>timeout</code> \u4e8b\u4ef6</p> <h3 id="82-\u54cd\u5e94\u7c7b\u578b">8.2 \u54cd\u5e94\u7c7b\u578b</h3> <p><strong>\u6211\u4eec\u53ef\u4ee5\u4f7f\u7528 <code>xhr.responseType</code> \u5c5e\u6027\u6765\u8bbe\u7f6e\u54cd\u5e94\u683c\u5f0f\uff1a</strong></p> <ul> <li><code>&quot;&quot;</code>\uff08\u9ed8\u8ba4\uff09\u2014\u2014 \u54cd\u5e94\u683c\u5f0f\u4e3a\u5b57\u7b26\u4e32</li> <li><code>&quot;text&quot;</code> \u2014\u2014 \u54cd\u5e94\u683c\u5f0f\u4e3a\u5b57\u7b26\u4e32</li> <li><code>&quot;arraybuffer&quot;</code> \u2014\u2014 \u54cd\u5e94\u683c\u5f0f\u4e3a <code>ArrayBuffer</code>\uff08\u5bf9\u4e8e\u4e8c\u8fdb\u5236\u6570\u636e\uff0c\u8bf7\u53c2\u89c1 <code>ArrayBuffer</code>\uff0c\u4e8c\u8fdb\u5236\u6570\u7ec4\uff09</li> <li><code>&quot;blob&quot;</code> \u2014\u2014 \u54cd\u5e94\u683c\u5f0f\u4e3a <code>Blob</code>\uff08\u5bf9\u4e8e\u4e8c\u8fdb\u5236\u6570\u636e\uff0c\u8bf7\u53c2\u89c1 <code>Blob</code>\uff09</li> <li><code>&quot;document&quot;</code> \u2014\u2014 \u54cd\u5e94\u683c\u5f0f\u4e3a <code>XML document</code>\uff08\u53ef\u4ee5\u4f7f\u7528 <code>XPath</code> \u548c\u5176\u4ed6 <code>XML</code> \u65b9\u6cd5\uff09</li> <li><code>&quot;json&quot;</code> \u2014\u2014 \u54cd\u5e94\u683c\u5f0f\u4e3a <code>JSON</code>\uff08\u81ea\u52a8\u89e3\u6790\uff09</li> </ul> <h3 id="83-readystate">8.3 readyState</h3> <pre><code class="language-js">UNSENT = 0; // \u521d\u59cb\u72b6\u6001\nOPENED = 1; // open \u88ab\u8c03\u7528\nHEADERS_RECEIVED = 2; // \u63a5\u6536\u5230 response header\nLOADING = 3; // \u54cd\u5e94\u6b63\u5728\u88ab\u52a0\u8f7d\uff08\u63a5\u6536\u5230\u4e00\u4e2a\u6570\u636e\u5305\uff09\nDONE = 4; // \u8bf7\u6c42\u5b8c\u6210</code></pre> <p>\u6211\u4eec\u53ef\u4ee5\u4f7f\u7528 <code>readystatechange</code> \u4e8b\u4ef6\u6765\u8ddf\u8e2a\u5b83\u4eec\uff1a</p> <pre><code class="language-js">xhr.onreadystatechange = function() {\n  if (xhr.readyState == 3) {\n    // \u52a0\u8f7d\u4e2d\n  }\n  if (xhr.readyState == 4) {\n    // \u8bf7\u6c42\u5b8c\u6210\n  }\n};</code></pre> <blockquote> <p>\u4f60\u53ef\u80fd\u5728\u975e\u5e38\u8001\u7684\u4ee3\u7801\u4e2d\u627e\u5230 <code>readystatechange</code> \u8fd9\u6837\u7684\u4e8b\u4ef6\u76d1\u542c\u5668\uff0c\u5b83\u7684\u5b58\u5728\u662f\u6709\u5386\u53f2\u539f\u56e0\u7684\uff0c\u56e0\u4e3a\u66fe\u7ecf\u6709\u5f88\u957f\u4e00\u6bb5\u65f6\u95f4\u90fd\u6ca1\u6709 <code>load</code> \u4ee5\u53ca\u5176\u4ed6\u4e8b\u4ef6\u3002\u5982\u4eca\uff0c\u5b83\u5df2\u88ab <code>load/error/progress</code> \u4e8b\u4ef6\u5904\u7406\u7a0b\u5e8f\u6240\u66ff\u4ee3</p> </blockquote> <h3 id="84-\u4e2d\u6b62\u8bf7\u6c42">8.4 \u4e2d\u6b62\u8bf7\u6c42</h3> <p><strong>\u6211\u4eec\u53ef\u4ee5\u968f\u65f6\u7ec8\u6b62\u8bf7\u6c42\u3002\u8c03\u7528 <code>xhr.abort()</code> \u5373\u53ef\uff1a</strong></p> <pre><code class="language-js">xhr.abort(); // \u7ec8\u6b62\u8bf7\u6c42</code></pre> <p>\u5b83\u4f1a\u89e6\u53d1 <code>abort</code> \u4e8b\u4ef6\uff0c\u4e14 <code>xhr.status</code> \u53d8\u4e3a <code>0</code></p> <h3 id="85-http-header">8.5 HTTP-header</h3> <p><code>XMLHttpRequest</code> \u5141\u8bb8\u53d1\u9001\u81ea\u5b9a\u4e49 <code>header</code>\uff0c\u5e76\u4e14\u53ef\u4ee5\u4ece\u54cd\u5e94\u4e2d\u8bfb\u53d6 <code>header</code></p> <pre><code class="language-js">setRequestHeader(name, value);\n\nxhr.setRequestHeader(&#39;Content-Type&#39;, &#39;application/json&#39;);\n\ngetResponseHeader(name); // \u83b7\u53d6\u5177\u6709\u7ed9\u5b9a name \u7684 header\ngetAllResponseHeaders(); // \u8fd4\u56de\u9664 Set-Cookie \u548c Set-Cookie2 \u5916\u7684\u6240\u6709 response header</code></pre> <h3 id="86-formdata">8.6 FormData</h3> <p>\u8981\u5efa\u7acb\u4e00\u4e2a <code>POST</code> \u8bf7\u6c42\uff0c\u6211\u4eec\u53ef\u4ee5\u4f7f\u7528\u5185\u5efa\u7684 <code>FormData</code> \u5bf9\u8c61</p> <pre><code class="language-js">// \u53d1\u9001 FormData\nlet formData = new FormData([form]); // \u521b\u5efa\u4e00\u4e2a\u5bf9\u8c61\uff0c\u53ef\u4ee5\u9009\u62e9\u4ece &lt;form&gt; \u4e2d\u83b7\u53d6\u6570\u636e\n\nformData.append(name, value); // \u9644\u52a0\u4e00\u4e2a\u5b57\u6bb5\n\nxhr.send(formData);</code></pre> <pre><code class="language-js">// \u53d1\u9001 JSON \u5b57\u7b26\u4e32\nlet xhr = new XMLHttpRequest();\n\nlet json = JSON.stringify({\n  name: &#39;John&#39;,\n  surname: &#39;Smith&#39;\n});\n\nxhr.open(&#39;POST&#39;, &#39;/submit&#39;);\nxhr.setRequestHeader(&#39;Content-type&#39;, &#39;application/json; charset=utf-8&#39;);\n\nxhr.send(json);</code></pre> <h3 id="87-\u4e0a\u4f20\u8fdb\u5ea6">8.7 \u4e0a\u4f20\u8fdb\u5ea6</h3> <p><code>progress</code> \u4e8b\u4ef6\u4ec5\u5728\u4e0b\u8f7d\u9636\u6bb5\u89e6\u53d1</p> <p>\u5982\u679c\u6211\u4eec\u8981\u4e0a\u4f20\u7684\u4e1c\u897f\u5f88\u5927\uff0c\u90a3\u4e48\u6211\u4eec\u80af\u5b9a\u4f1a\u5bf9\u8ddf\u8e2a\u4e0a\u4f20\u8fdb\u5ea6\u611f\u5174\u8da3\u3002\u4f46\u662f <code>xhr.onprogress</code> \u5728\u8fd9\u91cc\u5e76\u4e0d\u8d77\u4f5c\u7528</p> <p>\u8fd9\u91cc\u6709\u53e6\u4e00\u4e2a\u5bf9\u8c61\uff0c\u5b83\u6ca1\u6709\u65b9\u6cd5\uff0c\u5b83\u4e13\u95e8\u7528\u4e8e\u8ddf\u8e2a\u4e0a\u4f20\u4e8b\u4ef6\uff1a<code>xhr.upload</code></p> <p>\u5b83\u4f1a\u751f\u6210\u4e8b\u4ef6\uff0c\u7c7b\u4f3c\u4e8e <code>xhr</code>\uff0c\u4f46\u662f <code>xhr.upload</code> \u4ec5\u5728\u4e0a\u4f20\u65f6\u89e6\u53d1\u5b83\u4eec\uff1a</p> <ul> <li><code>loadstart</code> \u2014\u2014 \u4e0a\u4f20\u5f00\u59cb</li> <li><code>progress</code> \u2014\u2014 \u4e0a\u4f20\u671f\u95f4\u5b9a\u671f\u89e6\u53d1</li> <li><code>abort</code> \u2014\u2014 \u4e0a\u4f20\u4e2d\u6b62</li> <li><code>error</code> \u2014\u2014 \u975e <code>HTTP</code> \u9519\u8bef</li> <li><code>load</code> \u2014\u2014 \u4e0a\u4f20\u6210\u529f\u5b8c\u6210</li> <li><code>timeout</code> \u2014\u2014 \u4e0a\u4f20\u8d85\u65f6\uff08\u5982\u679c\u8bbe\u7f6e\u4e86 <code>timeout</code> \u5c5e\u6027\uff09</li> <li><code>loadend</code> \u2014\u2014 \u4e0a\u4f20\u5b8c\u6210\uff0c\u65e0\u8bba\u6210\u529f\u8fd8\u662f <code>error</code></li> </ul> <pre><code class="language-js">xhr.upload.onprogress = function(event) {\n  alert(`Uploaded ${event.loaded} of ${event.total} bytes`);\n};\n\nxhr.upload.onload = function() {\n  alert(`Upload finished successfully.`);\n};\n\nxhr.upload.onerror = function() {\n  alert(`Error during the upload: ${xhr.status}`);\n};</code></pre> <h3 id="88-\u8de8\u6e90\u8bf7\u6c42">8.8 \u8de8\u6e90\u8bf7\u6c42</h3> <p><code>XMLHttpRequest</code> \u53ef\u4ee5\u4f7f\u7528\u548c <code>fetch</code> \u76f8\u540c\u7684 <code>CORS</code> \u7b56\u7565\u8fdb\u884c\u8de8\u6e90\u8bf7\u6c42\u3002</p> <p>\u5c31\u50cf <code>fetch</code> \u4e00\u6837\uff0c\u9ed8\u8ba4\u60c5\u51b5\u4e0b\u4e0d\u4f1a\u5c06 <code>cookie</code> \u548c <code>HTTP</code> \u6388\u6743\u53d1\u9001\u5230\u5176\u4ed6\u57df\u3002\u8981\u542f\u7528\u5b83\u4eec\uff0c\u53ef\u4ee5\u5c06 <code>xhr.withCredentials</code> \u8bbe\u7f6e\u4e3a <code>true</code></p> <pre><code class="language-js">let xhr = new XMLHttpRequest();\nxhr.withCredentials = true;\n\nxhr.open(&#39;POST&#39;, &#39;http://anywhere.com/request&#39;);</code></pre> <h2 id="9-\u53ef\u6062\u590d\u7684\u6587\u4ef6\u4e0a\u4f20">9 \u53ef\u6062\u590d\u7684\u6587\u4ef6\u4e0a\u4f20</h2> <h3 id="91-\u6982\u5ff5">9.1 \u6982\u5ff5</h3> <p>\u5bf9\u4e8e\u5927\u6587\u4ef6\uff0c\u53ef\u6062\u590d\u7684\u4e0a\u4f20\u5e94\u8be5\u5e26\u6709\u4e0a\u4f20\u8fdb\u5ea6\u63d0\u793a\u3002\u7531\u4e8e fetch \u4e0d\u5141\u8bb8\u8ddf\u8e2a\u4e0a\u4f20\u8fdb\u5ea6\uff0c\u6211\u4eec\u5c06\u4f1a\u4f7f\u7528 <code>XMLHttpRequest</code></p> <p><strong>\u8981\u6062\u590d\u4e0a\u4f20\uff0c\u6211\u4eec\u9700\u8981\u77e5\u9053\u5728\u8fde\u63a5\u65ad\u5f00\u524d\u5df2\u7ecf\u4e0a\u4f20\u4e86\u591a\u5c11</strong></p> <p>\u6211\u4eec\u6709 <code>xhr.upload.onprogress</code> \u6765\u8ddf\u8e2a\u4e0a\u4f20\u8fdb\u5ea6</p> <p>\u4f46\u662f\u5b83\u4e0d\u4f1a\u5e2e\u52a9\u6211\u4eec\u5728\u6b64\u5904\u6062\u590d\u4e0a\u4f20\uff0c\u56e0\u4e3a\u5b83\u4f1a\u5728\u6570\u636e\u88ab\u53d1\u9001\u65f6\u89e6\u53d1\uff0c\u4f46\u662f\u670d\u52a1\u5668\u662f\u5426\u63a5\u6536\u5230\u4e86\uff1f\u6d4f\u89c8\u5668\u5e76\u4e0d\u77e5\u9053</p> <p>\u8981\u6062\u590d\u4e0a\u4f20\uff0c\u6211\u4eec\u9700\u8981<strong>\u786e\u5207\u5730</strong>\u77e5\u9053\u670d\u52a1\u5668\u63a5\u6536\u7684\u5b57\u8282\u6570\u3002\u56e0\u6b64\uff0c\u9700\u8981\u53d1\u9001\u8bf7\u6c42\u8be2\u95ee\u670d\u52a1\u5668</p> <h3 id="92-\u5b9e\u73b0">9.2 \u5b9e\u73b0</h3> <p>\u9996\u5148\uff0c\u521b\u5efa\u4e00\u4e2a\u6587\u4ef6 <code>id</code>\uff0c\u4ee5\u552f\u4e00\u5730\u6807\u8bc6\u6211\u4eec\u8981\u4e0a\u4f20\u7684\u6587\u4ef6\uff1a</p> <pre><code class="language-js">let fileId = file.name + &#39;-&#39; + file.size + &#39;-&#39; + file.lastModified;</code></pre> <p>\u5411\u670d\u52a1\u5668\u53d1\u9001\u4e00\u4e2a\u8bf7\u6c42\uff0c\u8be2\u95ee\u5b83\u5df2\u7ecf\u6709\u4e86\u591a\u5c11\u5b57\u8282\uff1a</p> <pre><code class="language-js">let response = await fetch(&#39;status&#39;, {\n  headers: {\n    &#39;X-File-Id&#39;: fileId\n  }\n});\n\n// \u670d\u52a1\u5668\u5df2\u6709\u7684\u5b57\u8282\u6570\nlet startByte = +(await response.text());</code></pre> <p>\u7136\u540e\uff0c\u6211\u4eec\u53ef\u4ee5\u4f7f\u7528 <code>Blob</code> \u548c <code>slice</code> \u65b9\u6cd5\u6765\u53d1\u9001\u4ece <code>startByte</code> \u5f00\u59cb\u7684\u6587\u4ef6\uff1a</p> <pre><code class="language-js">xhr.open(&#39;POST&#39;, &#39;upload&#39;, true);\n\n// \u6587\u4ef6 id\uff0c\u4ee5\u4fbf\u670d\u52a1\u5668\u77e5\u9053\u6211\u4eec\u8981\u6062\u590d\u7684\u662f\u54ea\u4e2a\u6587\u4ef6\nxhr.setRequestHeader(&#39;X-File-Id&#39;, fileId);\n\n// \u53d1\u9001\u6211\u4eec\u8981\u4ece\u54ea\u4e2a\u5b57\u8282\u5f00\u59cb\u6062\u590d\uff0c\u56e0\u6b64\u670d\u52a1\u5668\u77e5\u9053\u6211\u4eec\u6b63\u5728\u6062\u590d\nxhr.setRequestHeader(&#39;X-Start-Byte&#39;, startByte);\n\nxhr.upload.onprogress = e =&gt; {\n  console.log(`Uploaded ${startByte + e.loaded} of ${startByte + e.total}`);\n};\n\n// \u6587\u4ef6\u53ef\u4ee5\u662f\u6765\u81ea input.files[0]\uff0c\u6216\u8005\u53e6\u4e00\u4e2a\u6e90\nxhr.send(file.slice(startByte));</code></pre> <p>\u670d\u52a1\u5668\u5e94\u8be5\u68c0\u67e5\u5176\u8bb0\u5f55\uff0c\u5982\u679c\u6709\u4e00\u4e2a\u4e0a\u4f20\u7684\u8be5\u6587\u4ef6\uff0c\u5e76\u4e14\u5f53\u524d\u5df2\u4e0a\u4f20\u7684\u6587\u4ef6\u5927\u5c0f\u6070\u597d\u662f <code>X-Start-Byte</code>\uff0c\u90a3\u4e48\u5c31\u5c06\u6570\u636e\u9644\u52a0\u5230\u8be5\u6587\u4ef6</p> <p><a href="https://zh.javascript.info/resume-upload">\u5b8c\u6574\u5b9e\u4f8b</a></p> <h2 id="10-\u957f\u8f6e\u8be2">10 \u957f\u8f6e\u8be2</h2> <p>\u957f\u8f6e\u8be2\u662f\u4e0e\u670d\u52a1\u5668\u4fdd\u6301\u6301\u4e45\u8fde\u63a5\u7684\u6700\u7b80\u5355\u7684\u65b9\u5f0f\uff0c\u5b83\u4e0d\u4f7f\u7528\u4efb\u4f55\u7279\u5b9a\u7684\u534f\u8bae\uff0c\u4f8b\u5982 <code>WebSocket</code> \u6216\u8005 <code>Server Sent Event</code></p> <h3 id="101-\u5e38\u89c4\u8f6e\u8be2">10.1 \u5e38\u89c4\u8f6e\u8be2</h3> <p>\u4ece\u670d\u52a1\u5668\u83b7\u53d6\u65b0\u4fe1\u606f\u7684\u6700\u7b80\u5355\u7684\u65b9\u5f0f\u662f\u5b9a\u671f\u8f6e\u8be2\u3002\u4f8b\u5982\uff0c\u6bcf 10 \u79d2\u4e00\u6b21</p> <p><strong>\u7f3a\u70b9\uff1a</strong></p> <ul> <li>\u6d88\u606f\u4f20\u9012\u7684\u5ef6\u8fdf\u6700\u591a\u4e3a 10 \u79d2\uff08\u4e24\u4e2a\u8bf7\u6c42\u4e4b\u95f4\uff09</li> <li>\u5373\u4f7f\u6ca1\u6709\u6d88\u606f\uff0c\u670d\u52a1\u5668\u4e5f\u4f1a\u6bcf\u9694 10 \u79d2\u88ab\u8bf7\u6c42\u8f70\u70b8\u4e00\u6b21\uff0c\u5373\u4f7f\u7528\u6237\u5207\u6362\u5230\u5176\u4ed6\u5730\u65b9\u6216\u8005\u5904\u4e8e\u4f11\u7720\u72b6\u6001\uff0c\u4e5f\u662f\u5982\u6b64\u3002\u5c31\u6027\u80fd\u800c\u8a00\uff0c\u8fd9\u662f\u4e00\u4e2a\u5f88\u5927\u7684\u8d1f\u62c5</li> </ul> <h3 id="102-\u957f\u8f6e\u8be2">10.2 \u957f\u8f6e\u8be2</h3> <p>\u957f\u8f6e\u8be2\u662f\u8f6e\u8be2\u670d\u52a1\u5668\u7684\u4e00\u79cd\u66f4\u597d\u7684\u65b9\u5f0f</p> <p>\u5b83\u4e5f\u5f88\u5bb9\u6613\u5b9e\u73b0\uff0c\u5e76\u4e14\u53ef\u4ee5\u65e0\u5ef6\u8fdf\u5730\u4f20\u9012\u6d88\u606f</p> <p><strong>\u6d41\u7a0b\uff1a</strong></p> <ul> <li>\u8bf7\u6c42\u53d1\u9001\u5230\u670d\u52a1\u5668</li> <li>\u670d\u52a1\u5668\u5728\u6709\u6d88\u606f\u4e4b\u524d\u4e0d\u4f1a\u5173\u95ed\u8fde\u63a5</li> <li>\u5f53\u6d88\u606f\u51fa\u73b0\u65f6 \u2014\u2014 \u670d\u52a1\u5668\u5c06\u5bf9\u5176\u8bf7\u6c42\u4f5c\u51fa\u54cd\u5e94</li> <li>\u6d4f\u89c8\u5668\u7acb\u5373\u53d1\u51fa\u4e00\u4e2a\u65b0\u7684\u8bf7\u6c42</li> </ul> <p>\u5982\u679c\u8fde\u63a5\u4e22\u5931\uff0c\u53ef\u80fd\u662f\u56e0\u4e3a\u7f51\u7edc\u9519\u8bef\uff0c\u6d4f\u89c8\u5668\u4f1a\u7acb\u5373\u53d1\u9001\u4e00\u4e2a\u65b0\u8bf7\u6c42</p> <p><strong>\u793a\u4f8b\uff1a</strong></p> <pre><code class="language-js">async function subscribe() {\n  let response = await fetch(&#39;/subscribe&#39;);\n\n  if (response.status == 502) {\n    // \u72b6\u6001 502 \u662f\u8fde\u63a5\u8d85\u65f6\u9519\u8bef\uff0c\n    // \u8fde\u63a5\u6302\u8d77\u65f6\u95f4\u8fc7\u957f\u65f6\u53ef\u80fd\u4f1a\u53d1\u751f\uff0c\n    // \u8fdc\u7a0b\u670d\u52a1\u5668\u6216\u4ee3\u7406\u4f1a\u5173\u95ed\u5b83\n    // \u8ba9\u6211\u4eec\u91cd\u65b0\u8fde\u63a5\n    await subscribe();\n  } else if (response.status != 200) {\n    // \u4e00\u4e2a error \u2014\u2014 \u8ba9\u6211\u4eec\u663e\u793a\u5b83\n    showMessage(response.statusText);\n    // \u4e00\u79d2\u540e\u91cd\u65b0\u8fde\u63a5\n    await new Promise(resolve =&gt; setTimeout(resolve, 1000));\n    await subscribe();\n  } else {\n    // \u83b7\u53d6\u5e76\u663e\u793a\u6d88\u606f\n    let message = await response.text();\n    showMessage(message);\n    // \u518d\u6b21\u8c03\u7528 subscribe() \u4ee5\u83b7\u53d6\u4e0b\u4e00\u6761\u6d88\u606f\n    await subscribe();\n  }\n}\n\nsubscribe();</code></pre> <blockquote> <p>\u670d\u52a1\u5668\u67b6\u6784\u5fc5\u987b\u80fd\u591f\u5904\u7406\u8bb8\u591a\u6302\u8d77\u7684\u8fde\u63a5\u3002\u67d0\u4e9b\u670d\u52a1\u5668\u67b6\u6784\u662f\u6bcf\u4e2a\u8fde\u63a5\u5bf9\u5e94\u4e00\u4e2a\u8fdb\u7a0b\uff0c\u5bfc\u81f4\u8fdb\u7a0b\u6570\u548c\u8fde\u63a5\u6570\u4e00\u6837\u591a\uff0c\u800c\u6bcf\u4e2a\u8fdb\u7a0b\u90fd\u4f1a\u6d88\u8017\u76f8\u5f53\u591a\u7684\u5185\u5b58\u3002\u56e0\u6b64\uff0c\u8fc7\u591a\u7684\u8fde\u63a5\u4f1a\u6d88\u8017\u6389\u5168\u90e8\u5185\u5b58\u3002\u4f7f\u7528\u50cf <code>PHP</code> \u548c <code>Ruby</code> \u8bed\u8a00\u7f16\u5199\u7684\u540e\u7aef\u7a0b\u5e8f\u4f1a\u7ecf\u5e38\u9047\u5230\u8fd9\u4e2a\u95ee\u9898\u3002\u4f7f\u7528 <code>Node.js</code> \u7f16\u5199\u7684\u670d\u52a1\u7aef\u7a0b\u5e8f\u901a\u5e38\u4e0d\u4f1a\u51fa\u73b0\u6b64\u7c7b\u95ee\u9898</p> </blockquote> <h3 id="103-\u4f7f\u7528\u573a\u666f">10.3 \u4f7f\u7528\u573a\u666f</h3> <p>\u5728\u6d88\u606f\u5f88\u5c11\u7684\u60c5\u51b5\u4e0b\uff0c\u957f\u8f6e\u8be2\u5f88\u6709\u6548</p> <p>\u5982\u679c\u6d88\u606f\u6bd4\u8f83\u9891\u7e41\u5c31\u4e0d\u9002\u5408\uff0c\u6bcf\u4e2a\u6d88\u606f\u90fd\u662f\u4e00\u4e2a\u5355\u72ec\u7684\u8bf7\u6c42\uff0c\u5e76\u5e26\u6709 <code>header</code>\uff0c\u8eab\u4efd\u9a8c\u8bc1\u5f00\u9500\uff08authentication overhead\uff09\u7b49</p> <p>\u56e0\u6b64\uff0c\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u9996\u9009\u53e6\u4e00\u79cd\u65b9\u6cd5\uff0c\u4f8b\u5982\uff1a<code>Websocket</code> \u6216 <code>Server Sent Events</code></p> <h2 id="11-websocket">11 WebSocket</h2> <h3 id="111-websocket-\u57fa\u7840">11.1 WebSocket \u57fa\u7840</h3> <p><code>WebSocket</code> \u534f\u8bae\u63d0\u4f9b\u4e86\u4e00\u79cd\u5728\u6d4f\u89c8\u5668\u548c\u670d\u52a1\u5668\u4e4b\u95f4\u5efa\u7acb\u6301\u4e45\u8fde\u63a5\u6765\u4ea4\u6362\u6570\u636e\u7684\u65b9\u6cd5\u3002\u6570\u636e\u53ef\u4ee5\u4f5c\u4e3a\u201c\u6570\u636e\u5305\u201d\u5728\u4e24\u4e2a\u65b9\u5411\u4e0a\u4f20\u9012\uff0c\u800c\u4e0d\u4f1a\u65ad\u5f00\u8fde\u63a5\u548c\u5176\u4ed6 <code>HTTP</code> \u8bf7\u6c42</p> <p>\u540c\u6837\u4e5f\u6709\u4e00\u4e2a\u52a0\u5bc6\u7684 <code>wss://</code> \u534f\u8bae\u3002\u7c7b\u4f3c\u4e8e <code>WebSocket</code> \u4e2d\u7684 <code>HTTPS</code></p> <blockquote> <p><strong>\u59cb\u7ec8\u4f7f\u7528 wss://</strong>\u3002<code>wss://</code> \u534f\u8bae\u4e0d\u4ec5\u662f\u88ab\u52a0\u5bc6\u7684\uff0c\u800c\u4e14\u66f4\u53ef\u9760</p> </blockquote> <pre><code class="language-js">let socket = new WebSocket(&#39;ws://javascript.info&#39;);</code></pre> <p><strong>socket \u4e0a\u7684\u4e8b\u4ef6\uff1a</strong></p> <ul> <li><strong>open</strong> \u2014\u2014 \u8fde\u63a5\u5df2\u5efa\u7acb</li> <li><strong>message</strong> \u2014\u2014 \u63a5\u6536\u5230\u6570\u636e</li> <li><strong>error</strong> \u2014\u2014 <code>WebSocket</code> \u9519\u8bef</li> <li><strong>close</strong> \u2014\u2014 \u8fde\u63a5\u5df2\u5173\u95ed</li> </ul> <p><code>socket.send(data)</code> \u53d1\u9001\u6570\u636e</p> <p><strong>\u793a\u4f8b\uff1a</strong></p> <pre><code class="language-js">let socket = new WebSocket(&#39;wss://javascript.info/article/websocket/demo/hello&#39;);\n\nsocket.onopen = function(e) {\n  alert(&#39;[open] Connection established&#39;);\n  alert(&#39;Sending to server&#39;);\n  socket.send(&#39;My name is John&#39;);\n};\n\nsocket.onmessage = function(event) {\n  alert(`[message] Data received from server: ${event.data}`);\n};\n\nsocket.onclose = function(event) {\n  if (event.wasClean) {\n    alert(`[close] Connection closed cleanly, code=${event.code} reason=${event.reason}`);\n  } else {\n    // \u4f8b\u5982\u670d\u52a1\u5668\u8fdb\u7a0b\u88ab\u6740\u6b7b\u6216\u7f51\u7edc\u4e2d\u65ad\n    // \u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0cevent.code \u901a\u5e38\u4e3a 1006\n    alert(&#39;[close] Connection died&#39;);\n  }\n};\n\nsocket.onerror = function(error) {\n  alert(`[error] ${error.message}`);\n};</code></pre> <h3 id="112-\u5efa\u7acb-websocket">11.2 \u5efa\u7acb WebSocket</h3> <p>\u5f53 <code>new WebSocket(url)</code> \u88ab\u521b\u5efa\u540e\uff0c\u5b83\u5c06\u7acb\u5373\u5f00\u59cb\u8fde\u63a5</p> <p><img src="'+l+'" alt=""></p> <p>\u8fd9\u662f\u7531 <code>new WebSocket(&quot;wss://javascript.info/chat&quot;)</code> \u53d1\u51fa\u7684\u8bf7\u6c42\u7684\u6d4f\u89c8\u5668 header \u793a\u4f8b:</p> <pre><code>GET /chat\nHost: javascript.info\nOrigin: https://javascript.info\nConnection: Upgrade\nUpgrade: websocket\nSec-WebSocket-Key: Iv8io/9s+lYFgZWcXczP8Q==\nSec-WebSocket-Version: 13</code></pre><ul> <li><code>Origin</code> \u2014\u2014 \u5ba2\u6237\u7aef\u9875\u9762\u7684\u6e90\uff0c\u4f8b\u5982 <code>https://javascript.info</code>\u3002<code>WebSocket</code> \u5bf9\u8c61\u662f\u539f\u751f\u652f\u6301\u8de8\u6e90\u7684\u3002\u6ca1\u6709\u7279\u6b8a\u7684 <code>header</code> \u6216\u5176\u4ed6\u9650\u5236\u3002\u65e7\u7684\u670d\u52a1\u5668\u65e0\u6cd5\u5904\u7406 <code>WebSocket</code>\uff0c\u56e0\u6b64\u4e0d\u5b58\u5728\u517c\u5bb9\u6027\u95ee\u9898\u3002\u4f46\u662f <code>Origin header</code> \u5f88\u91cd\u8981\uff0c\u56e0\u4e3a\u5b83\u5141\u8bb8\u670d\u52a1\u5668\u51b3\u5b9a\u662f\u5426\u4f7f\u7528 <code>WebSocket</code> \u4e0e\u8be5\u7f51\u7ad9\u901a\u4fe1</li> <li><code>Connection: Upgrade</code> \u2014\u2014 \u8868\u793a\u5ba2\u6237\u7aef\u60f3\u8981\u66f4\u6539\u534f\u8bae</li> <li><code>Upgrade: websocket</code> \u2014\u2014 \u8bf7\u6c42\u7684\u534f\u8bae\u662f <code>websocket</code></li> <li><code>Sec-WebSocket-Key</code> \u2014\u2014 \u6d4f\u89c8\u5668\u968f\u673a\u751f\u6210\u7684\u5b89\u5168\u5bc6\u94a5</li> <li><code>Sec-WebSocket-Version</code> \u2014\u2014 <code>WebSocket</code> \u534f\u8bae\u7248\u672c\uff0c\u5f53\u524d\u4e3a 13</li> </ul> <blockquote> <p>\u65e0\u6cd5\u6a21\u62df <code>WebSocket</code> \u63e1\u624b\u3002\u4e0d\u80fd\u4f7f\u7528 <code>XMLHttpRequest</code> \u6216 <code>fetch</code> \u6765\u8fdb\u884c\u8fd9\u79cd <code>HTTP</code> \u8bf7\u6c42\uff0c\u56e0\u4e3a\u4e0d\u5141\u8bb8 JavaScript \u8bbe\u7f6e\u8fd9\u4e9b <code>header</code></p> </blockquote> <p>\u5982\u679c\u670d\u52a1\u5668\u540c\u610f\u5207\u6362\u4e3a <code>WebSocket</code> \u534f\u8bae\uff0c\u670d\u52a1\u5668\u5e94\u8be5\u8fd4\u56de\u54cd\u5e94\u7801 <code>101</code>\uff1a</p> <pre><code>101 Switching Protocols\nUpgrade: websocket\nConnection: Upgrade\nSec-WebSocket-Accept: hsBlbuDTkk24srzEOTBUlZAlC2g=</code></pre><p>\u8fd9\u91cc <code>Sec-WebSocket-Accept</code> \u662f <code>Sec-WebSocket-Key</code>\uff0c\u662f\u4f7f\u7528\u7279\u6b8a\u7684\u7b97\u6cd5\u91cd\u65b0\u7f16\u7801\u7684\u3002\u6d4f\u89c8\u5668\u4f7f\u7528\u5b83\u6765\u786e\u4fdd\u54cd\u5e94\u4e0e\u8bf7\u6c42\u76f8\u5bf9\u5e94</p> <p>\u7136\u540e\uff0c\u5c31\u4f7f\u7528 WebSocket \u534f\u8bae\u4f20\u8f93\u6570\u636e\uff0c\u6211\u4eec\u5f88\u5feb\u5c31\u4f1a\u770b\u5230\u5b83\u7684\u7ed3\u6784\uff08\u201cframes\u201d\uff09\u3002\u5b83\u6839\u672c\u4e0d\u662f HTTP\u3002</p> <p><strong>\u6269\u5c55\u548c\u5b50\u534f\u8bae</strong></p> <p><code>Sec-WebSocket-Extensions: deflate-frame</code> \u8868\u793a\u6d4f\u89c8\u5668\u652f\u6301\u6570\u636e\u538b\u7f29\u3002\u6269\u5c55\u4e0e\u4f20\u8f93\u6570\u636e\u6709\u5173\uff0c\u6269\u5c55\u4e86 <code>WebSocket</code> \u534f\u8bae\u7684\u529f\u80fd\u3002<code>Sec-WebSocket-Extensions</code> header \u7531\u6d4f\u89c8\u5668\u81ea\u52a8\u53d1\u9001\uff0c\u5176\u4e2d\u5305\u542b\u5176\u652f\u6301\u7684\u6240\u6709\u6269\u5c55\u7684\u5217\u8868</p> <p><code>Sec-WebSocket-Protocol: soap, wamp</code> \u8868\u793a\u6211\u4eec\u4e0d\u4ec5\u8981\u4f20\u8f93\u4efb\u4f55\u6570\u636e\uff0c\u8fd8\u8981\u4f20\u8f93 <code>SOAP</code> \u6216 <code>WAMP</code>\uff08\u201cThe WebSocket Application Messaging Protocol\u201d\uff09\u534f\u8bae\u4e2d\u7684\u6570\u636e\u3002<code>WebSocket</code> \u5b50\u534f\u8bae\u5df2\u7ecf\u5728 <code>IANA catalogue</code> \u4e2d\u6ce8\u518c</p> <p>\u8fd9\u4e2a\u53ef\u9009\u7684 header \u662f\u4f7f\u7528 <code>new WebSocket</code> \u7684\u7b2c\u4e8c\u4e2a\u53c2\u6570\u8bbe\u7f6e\u7684\u3002\u5b83\u662f\u5b50\u534f\u8bae\u6570\u7ec4</p> <pre><code class="language-js">let socket = new WebSocket(&#39;wss://javascript.info/chat&#39;, [&#39;soap&#39;, &#39;wamp&#39;]);</code></pre> <p>\u670d\u52a1\u5668\u5e94\u8be5\u4f7f\u7528\u540c\u610f\u4f7f\u7528\u7684\u534f\u8bae\u548c\u6269\u5c55\u7684\u5217\u8868\u8fdb\u884c\u54cd\u5e94</p> <h3 id="113-\u6570\u636e\u4f20\u8f93">11.3 \u6570\u636e\u4f20\u8f93</h3> <p><code>WebSocket</code> \u901a\u4fe1\u7531 frames\uff08\u5373\u6570\u636e\u7247\u6bb5\uff09\u7ec4\u6210\uff0c\u53ef\u4ee5\u4ece\u4efb\u4f55\u4e00\u65b9\u53d1\u9001\uff0c\u5e76\u4e14\u6709\u4ee5\u4e0b\u51e0\u79cd\u7c7b\u578b\uff1a</p> <ul> <li><code>text frames</code> \u2014\u2014 \u5305\u542b\u5404\u65b9\u53d1\u9001\u7ed9\u5f7c\u6b64\u7684\u6587\u672c\u6570\u636e</li> <li><code>binary data frames</code> \u2014\u2014 \u5305\u542b\u5404\u65b9\u53d1\u9001\u7ed9\u5f7c\u6b64\u7684\u4e8c\u8fdb\u5236\u6570\u636e</li> <li><code>ping/pong frames</code> \u88ab\u7528\u4e8e\u68c0\u67e5\u4ece\u670d\u52a1\u5668\u53d1\u9001\u7684\u8fde\u63a5\uff0c\u6d4f\u89c8\u5668\u4f1a\u81ea\u52a8\u54cd\u5e94\u5b83\u4eec</li> <li><code>connection close frame</code> \u4ee5\u53ca\u5176\u4ed6\u670d\u52a1 <code>frames</code></li> </ul> <p><code>socket.send(body)</code> \u8c03\u7528\u5141\u8bb8 <code>body</code> \u662f\u5b57\u7b26\u4e32\u6216\u4e8c\u8fdb\u5236\u683c\u5f0f\uff0c\u5305\u62ec <code>Blob</code>\uff0c<code>ArrayBuffer</code> \u7b49\u3002\u4e0d\u9700\u8981\u989d\u5916\u7684\u8bbe\u7f6e\uff0c\u53ef\u4ee5\u76f4\u63a5\u53d1\u9001</p> <p>\u5f53\u6211\u4eec\u6536\u5230\u6570\u636e\u65f6\uff0c\u6587\u672c\u603b\u662f\u4ee5\u5b57\u7b26\u4e32\u5f62\u5f0f\u5448\u73b0\u3002\u800c\u5bf9\u4e8e\u4e8c\u8fdb\u5236\u6570\u636e\uff0c\u6211\u4eec\u53ef\u4ee5\u5728 Blob \u548c ArrayBuffer \u683c\u5f0f\u4e4b\u95f4\u8fdb\u884c\u9009\u62e9\u3002</p> <p>\u4e8c\u8fdb\u5236\u6570\u636e\u683c\u5f0f\u53ef\u4ee5\u7531 <code>socket.binaryType</code> \u5c5e\u6027\u8bbe\u7f6e\u7684\uff0c\u9ed8\u8ba4\u4e3a <code>blob</code></p> <pre><code class="language-js">socket.binaryType = &#39;arraybuffer&#39;;\nsocket.onmessage = event =&gt; {\n  // event.data \u53ef\u4ee5\u662f\u6587\u672c\uff08\u5982\u679c\u662f\u6587\u672c\uff09\uff0c\u4e5f\u53ef\u4ee5\u662f arraybuffer\uff08\u5982\u679c\u662f\u4e8c\u8fdb\u5236\u6570\u636e\uff09\n};</code></pre> <h3 id="114-\u9650\u901f">11.4 \u9650\u901f</h3> <p>\u6211\u4eec\u53ef\u4ee5\u53cd\u590d\u5730\u8c03\u7528 <code>socket.send(data)</code>\u3002\u4f46\u662f\u6570\u636e\u5c06\u4f1a\u7f13\u51b2\uff08\u50a8\u5b58\uff09\u5728\u5185\u5b58\u4e2d\uff0c\u5e76\u4e14\u53ea\u80fd\u5728\u7f51\u901f\u5141\u8bb8\u7684\u60c5\u51b5\u4e0b\u5c3d\u5feb\u5c06\u6570\u636e\u53d1\u9001\u51fa\u53bb</p> <p><code>socket.bufferedAmount</code> \u5c5e\u6027\u50a8\u5b58\u4e86\u76ee\u524d\u5df2\u7f13\u51b2\u7684\u5b57\u8282\u6570\uff0c\u7b49\u5f85\u901a\u8fc7\u7f51\u7edc\u53d1\u9001</p> <p>\u53ef\u4ee5\u68c0\u67e5\u5b83\u7684\u72b6\u6001\u6765\u63a7\u5236\u662f\u5426\u53d1\u9001\u6570\u636e\uff1a</p> <pre><code class="language-js">// \u6bcf 100ms \u68c0\u67e5\u4e00\u6b21 socket\n// \u4ec5\u5f53\u6240\u6709\u73b0\u6709\u7684\u6570\u636e\u90fd\u5df2\u88ab\u53d1\u9001\u51fa\u53bb\u65f6\uff0c\u518d\u53d1\u9001\u66f4\u591a\u6570\u636e\nsetInterval(() =&gt; {\n  if (socket.bufferedAmount == 0) {\n    socket.send(moreData());\n  }\n}, 100);</code></pre> <h3 id="115-\u8fde\u63a5\u5173\u95ed">11.5 \u8fde\u63a5\u5173\u95ed</h3> <p>\u901a\u5e38\uff0c\u5f53\u4e00\u65b9\u60f3\u8981\u5173\u95ed\u8fde\u63a5\u65f6\uff08\u6d4f\u89c8\u5668\u548c\u670d\u52a1\u5668\u90fd\u5177\u6709\u76f8\u540c\u7684\u6743\u9650\uff09\uff0c\u5b83\u4eec\u4f1a\u53d1\u9001\u4e00\u4e2a\u5e26\u6709\u6570\u5b57\u7801\uff08numeric code\uff09\u548c\u6587\u672c\u5f62\u5f0f\u7684\u539f\u56e0\u7684 <code>connection close frame</code></p> <pre><code class="language-js">socket.close([code], [reason]);</code></pre> <ul> <li><code>code</code> \u662f\u4e00\u4e2a\u7279\u6b8a\u7684 WebSocket \u5173\u95ed\u7801\uff08\u53ef\u9009\uff09</li> <li><code>reason</code> \u662f\u4e00\u4e2a\u63cf\u8ff0\u5173\u95ed\u539f\u56e0\u7684\u5b57\u7b26\u4e32\uff08\u53ef\u9009\uff09</li> </ul> <p>\u7136\u540e\uff0c\u53e6\u5916\u4e00\u65b9\u901a\u8fc7 close \u4e8b\u4ef6\u5904\u7406\u5668\u83b7\u53d6\u4e86\u5173\u95ed\u7801\u548c\u5173\u95ed\u539f\u56e0\uff0c\u4f8b\u5982\uff1a</p> <pre><code class="language-js">// \u5173\u95ed\u65b9\uff1a\nsocket.close(1000, &#39;Work complete&#39;);\n\n// \u53e6\u4e00\u65b9\nsocket.onclose = event =&gt; {\n  // event.code === 1000\n  // event.reason === &quot;Work complete&quot;\n  // event.wasClean === true (clean close)\n};</code></pre> <p><strong>\u5e38\u89c1\u7684\u6570\u5b57\u7801\uff1a</strong></p> <ul> <li><code>1000</code> \u2014\u2014 \u9ed8\u8ba4\uff0c\u6b63\u5e38\u5173\u95ed\uff08\u5982\u679c\u6ca1\u6709\u6307\u660e code \u65f6\u4f7f\u7528\u5b83\uff09</li> <li><code>1006</code> \u2014\u2014 \u6ca1\u6709\u529e\u6cd5\u624b\u52a8\u8bbe\u5b9a\u8fd9\u4e2a\u6570\u5b57\u7801\uff0c\u8868\u793a\u8fde\u63a5\u4e22\u5931\uff08\u6ca1\u6709 close frame\uff09</li> <li><code>1001</code> \u2014\u2014 \u4e00\u65b9\u6b63\u5728\u79bb\u5f00\uff0c\u4f8b\u5982\u670d\u52a1\u5668\u6b63\u5728\u5173\u95ed\uff0c\u6216\u8005\u6d4f\u89c8\u5668\u79bb\u5f00\u4e86\u8be5\u9875\u9762</li> <li><code>1009</code> \u2014\u2014 \u6d88\u606f\u592a\u5927\uff0c\u65e0\u6cd5\u5904\u7406</li> <li><code>1011</code> \u2014\u2014 \u670d\u52a1\u5668\u4e0a\u53d1\u751f\u610f\u5916\u9519\u8bef</li> </ul> <p>\u5c0f\u4e8e 1000 \u7684\u7801\u90fd\u662f\u88ab\u4fdd\u7559\u7684\uff0c\u5982\u679c\u6211\u4eec\u5c1d\u8bd5\u8bbe\u7f6e\u8fd9\u6837\u7684\u7801\uff0c\u5c06\u4f1a\u51fa\u73b0\u9519\u8bef</p> <h3 id="116-\u8fde\u63a5\u72b6\u6001">11.6 \u8fde\u63a5\u72b6\u6001</h3> <p>\u8981\u83b7\u53d6\u8fde\u63a5\u72b6\u6001\uff0c\u53ef\u4ee5\u901a\u8fc7\u5e26\u6709\u503c\u7684 <code>socket.readyState</code> \u5c5e\u6027\uff1a</p> <ul> <li><code>0</code> \u2014\u2014 \u201cCONNECTING\u201d\uff1a\u8fde\u63a5\u8fd8\u672a\u5efa\u7acb</li> <li><code>1</code> \u2014\u2014 \u201cOPEN\u201d\uff1a\u901a\u4fe1\u4e2d</li> <li><code>2</code> \u2014\u2014 \u201cCLOSING\u201d\uff1a\u8fde\u63a5\u5173\u95ed\u4e2d</li> <li><code>3</code> \u2014\u2014 \u201cCLOSED\u201d\uff1a\u8fde\u63a5\u5df2\u5173\u95ed</li> </ul> <h2 id="12-server-sent-events">12 Server Sent Events</h2> <p>Server-Sent Events \u89c4\u8303\u63cf\u8ff0\u4e86\u4e00\u4e2a\u5185\u5efa\u7684\u7c7b <code>EventSource</code>\uff0c\u5b83\u80fd\u4fdd\u6301\u4e0e\u670d\u52a1\u5668\u7684\u8fde\u63a5\uff0c\u5e76\u5141\u8bb8\u4ece\u4e2d\u63a5\u6536\u4e8b\u4ef6</p> <p>\u4e0e <code>WebSocket</code> \u7c7b\u4f3c\uff0c\u5176\u8fde\u63a5\u662f<strong>\u6301\u4e45\u7684</strong></p> <p><strong>\u4f46\u662f\u4e24\u8005\u4e4b\u95f4\u6709\u51e0\u4e2a\u91cd\u8981\u7684\u533a\u522b\uff1a</strong></p> <table> <thead> <tr> <th>WebSocket</th> <th>EventSource</th> </tr> </thead> <tbody><tr> <td>\u53cc\u5411\uff1a\u5ba2\u6237\u7aef\u548c\u670d\u52a1\u7aef\u90fd\u80fd\u4ea4\u6362\u6d88\u606f</td> <td>\u5355\u5411\uff1a\u4ec5\u670d\u52a1\u7aef\u80fd\u53d1\u9001\u6d88\u606f</td> </tr> <tr> <td>\u4e8c\u8fdb\u5236\u548c\u6587\u672c\u6570\u636e</td> <td>\u4ec5\u6587\u672c\u6570\u636e</td> </tr> <tr> <td>WebSocket \u534f\u8bae</td> <td>\u5e38\u89c4 HTTP \u534f\u8bae</td> </tr> </tbody></table> <blockquote> <p>\u4e0e <code>WebSocket</code> \u76f8\u6bd4\uff0c<code>EventSource</code> \u662f\u4e0e\u670d\u52a1\u5668\u901a\u4fe1\u7684\u4e00\u79cd\u4e0d\u90a3\u4e48\u5f3a\u5927\u7684\u65b9\u5f0f\u3002\u6211\u4eec\u4e3a\u4ec0\u4e48\u8981\u4f7f\u7528\u5b83\uff1f\u4e3b\u8981\u539f\u56e0\u662f\u7b80\u5355\u3002\u5728\u5f88\u591a\u5e94\u7528\u4e2d\uff0c<code>WebSocket</code> \u6709\u70b9\u5927\u6750\u5c0f\u7528</p> </blockquote> <p>\u6211\u4eec\u9700\u8981\u4ece\u670d\u52a1\u5668\u63a5\u6536\u4e00\u4e2a\u6570\u636e\u6d41\uff1a\u53ef\u80fd\u662f\u804a\u5929\u6d88\u606f\u6216\u8005\u5e02\u573a\u4ef7\u683c\u7b49\u3002\u8fd9\u6b63\u662f <code>EventSource</code> \u6240\u64c5\u957f\u7684\u3002\u5b83\u8fd8\u652f\u6301\u81ea\u52a8\u91cd\u65b0\u8fde\u63a5\uff0c\u800c\u5728 <code>WebSocket</code> \u4e2d\u8fd9\u4e2a\u529f\u80fd\u9700\u8981\u6211\u4eec\u624b\u52a8\u5b9e\u73b0\u3002\u6b64\u5916\uff0c\u5b83\u662f\u4e00\u4e2a\u666e\u901a\u7684\u65e7\u7684 HTTP\uff0c\u4e0d\u662f\u4e00\u4e2a\u65b0\u534f\u8bae</p> <h3 id="121-\u83b7\u53d6\u6d88\u606f">12.1 \u83b7\u53d6\u6d88\u606f</h3> <p>\u521b\u5efa <code>new EventSource(url)</code> \u5373\u53ef\u5f00\u59cb\u63a5\u6536\u6d88\u606f</p> <p>\u6d4f\u89c8\u5668\u5c06\u4f1a\u8fde\u63a5\u5230 <code>url</code> \u5e76\u4fdd\u6301\u8fde\u63a5\u6253\u5f00\uff0c\u7b49\u5f85\u4e8b\u4ef6</p> <p>\u670d\u52a1\u5668\u54cd\u5e94\u72b6\u6001\u7801\u5e94\u8be5\u4e3a <code>200</code>\uff0c<code>header</code> \u4e3a <code>Content-Type: text/event-stream</code>\uff0c\u7136\u540e\u4fdd\u6301\u6b64\u8fde\u63a5\u5e76\u4ee5\u4e00\u79cd\u7279\u6b8a\u7684\u683c\u5f0f\u5199\u5165\u6d88\u606f</p> <pre><code>data: Message 1\n\ndata: \u540e\u4e3a\u6d88\u606f\u6587\u672c\uff0c\u5192\u53f7\u540e\u9762\u7684\u7a7a\u683c\u662f\u53ef\u9009\u7684\n\ndata: {&quot;user&quot;:&quot;John&quot;,&quot;message&quot;:&quot;First line\\n Second line&quot;}</code></pre><p><strong>\u6d88\u606f\u4f1a\u751f\u6210 <code>message</code> \u4e8b\u4ef6\uff1a</strong></p> <pre><code class="language-js">let eventSource = new EventSource(&#39;/events/subscribe&#39;);\n\neventSource.onmessage = function(event) {\n  console.log(&#39;New message&#39;, event.data);\n};\n\n// \u6216 eventSource.addEventListener(&#39;message&#39;, ...)</code></pre> <p><strong>\u8de8\u6e90\u8bf7\u6c42\uff1a</strong></p> <p><code>EventSource</code> \u652f\u6301\u8de8\u6e90\u8bf7\u6c42\uff0c\u5c31\u50cf <code>fetch</code> \u548c\u4efb\u4f55\u5176\u4ed6\u7f51\u7edc\u65b9\u6cd5</p> <p>\u8fdc\u7a0b\u670d\u52a1\u5668\u5c06\u4f1a\u83b7\u53d6\u5230 Origin header\uff0c\u5e76\u4e14\u5fc5\u987b\u4ee5 <code>Access-Control-Allow-Origin</code> \u54cd\u5e94\u6765\u5904\u7406</p> <p>\u8981\u4f20\u9012\u51ed\u8bc1\uff08credentials\uff09\uff0c\u8bbe\u7f6e\u9644\u52a0\u9009\u9879 <code>withCredentials</code>\uff1a</p> <pre><code class="language-js">let source = new EventSource(&#39;https://another-site.com/events&#39;, {\n  withCredentials: true\n});</code></pre> <h3 id="122-\u91cd\u65b0\u8fde\u63a5">12.2 \u91cd\u65b0\u8fde\u63a5</h3> <p>\u65ad\u5f00\u8fde\u63a5\u540e\u4f1a\u81ea\u52a8\u91cd\u8fde\uff0c\u6211\u4eec\u4e0d\u7528\u53bb\u5173\u5fc3\u91cd\u65b0\u8fde\u63a5\u7684\u4e8b\u60c5</p> <p>\u6bcf\u6b21\u91cd\u65b0\u8fde\u63a5\u4e4b\u95f4\u6709\u4e00\u70b9\u5c0f\u7684\u5ef6\u8fdf\uff0c\u670d\u52a1\u5668\u53ef\u4ee5\u4f7f\u7528 <code>retry:</code> \u6765\u8bbe\u7f6e\u9700\u8981\u7684\u5ef6\u8fdf\u54cd\u5e94\u65f6\u95f4\uff08\u4ee5\u6beb\u79d2\u4e3a\u5355\u4f4d\uff09</p> <pre><code>retry: 15000\ndata: Hello, I set the reconnection delay to 15 seconds\nretry: \u65e2\u53ef\u4ee5\u4e0e\u67d0\u4e9b\u6570\u636e\u4e00\u8d77\u51fa\u73b0\uff0c\u4e5f\u53ef\u4ee5\u4f5c\u4e3a\u72ec\u7acb\u7684\u6d88\u606f\u51fa\u73b0</code></pre><ul> <li>\u670d\u52a1\u5668\u60f3\u8981\u6d4f\u89c8\u5668\u505c\u6b62\u91cd\u65b0\u8fde\u63a5\uff0c\u90a3\u4e48\u5b83\u5e94\u8be5\u4f7f\u7528 HTTP \u72b6\u6001\u7801 <code>204</code> \u8fdb\u884c\u54cd\u5e94</li> <li>\u6d4f\u89c8\u5668\u60f3\u8981\u5173\u95ed\u8fde\u63a5\uff0c\u5219\u5e94\u8be5\u8c03\u7528 <code>eventSource.close()</code></li> </ul> <p>\u5e76\u4e14\uff0c\u5982\u679c\u54cd\u5e94\u5177\u6709\u4e0d\u6b63\u786e\u7684 <code>Content-Type</code> \u6216\u8005\u5176 HTTP \u72b6\u6001\u7801\u4e0d\u662f 301\uff0c307\uff0c200 \u548c 204\uff0c\u5219\u4e0d\u4f1a\u8fdb\u884c\u91cd\u65b0\u8fde\u63a5\u3002\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u5c06\u4f1a\u53d1\u51fa <code>error</code> \u4e8b\u4ef6\uff0c\u5e76\u4e14\u6d4f\u89c8\u5668\u4e0d\u4f1a\u91cd\u65b0\u8fde\u63a5</p> <blockquote> <p>\u6ce8\u610f\uff1a\u5f53\u8fde\u63a5\u6700\u7ec8\u88ab\u5173\u95ed\u65f6\uff0c\u5c31\u65e0\u6cd5\u91cd\u65b0\u6253\u5f00\u3002\u5982\u679c\u6211\u4eec\u60f3\u8981\u518d\u6b21\u8fde\u63a5\uff0c\u9700\u8981\u521b\u5efa\u4e00\u4e2a\u65b0\u7684 <code>EventSource</code></p> </blockquote> <h3 id="123-\u6d88\u606f-id">12.3 \u6d88\u606f id</h3> <p>\u5f53\u4e00\u4e2a\u8fde\u63a5\u7531\u4e8e\u7f51\u7edc\u95ee\u9898\u800c\u4e2d\u65ad\u65f6\uff0c\u5ba2\u6237\u7aef\u548c\u670d\u52a1\u5668\u90fd\u65e0\u6cd5\u786e\u5b9a\u54ea\u4e9b\u6d88\u606f\u5df2\u7ecf\u6536\u5230\u54ea\u4e9b\u6ca1\u6709\u6536\u5230</p> <p>\u4e3a\u4e86\u6b63\u786e\u5730\u6062\u590d\u8fde\u63a5\uff0c\u6bcf\u6761\u6d88\u606f\u90fd\u5e94\u8be5\u6709\u4e00\u4e2a <code>id</code> \u5b57\u6bb5\uff0c\u5c31\u50cf\u8fd9\u6837\uff1a</p> <pre><code>data: Message 1\nid: 1\n\ndata: Message 2\nid: 2\n\ndata: Message 3\ndata: of two lines\nid: 3</code></pre><p>\u5f53\u6536\u5230\u5177\u6709 id \u7684\u6d88\u606f\u65f6\uff0c\u6d4f\u89c8\u5668\u4f1a\uff1a</p> <ul> <li>\u5c06\u5c5e\u6027 <code>eventSource.lastEventId</code> \u8bbe\u7f6e\u4e3a\u5176\u503c</li> <li>\u91cd\u65b0\u8fde\u63a5\u540e\uff0c\u53d1\u9001\u5e26\u6709 <code>id</code> \u7684 header <code>Last-Event-ID</code>\uff0c\u4ee5\u4fbf\u670d\u52a1\u5668\u53ef\u4ee5\u91cd\u65b0\u53d1\u9001\u540e\u9762\u7684\u6d88\u606f</li> </ul> <blockquote> <p>\u6ce8\u610f\uff1a\u670d\u52a1\u5668\u5e94\u8be5\u5c06 <code>id</code> \u9644\u52a0\u5230 <code>data</code> \u6d88\u606f\u540e\uff0c\u4ee5\u786e\u4fdd\u5728\u6536\u5230\u6d88\u606f\u540e <code>lastEventId</code> \u4f1a\u88ab\u66f4\u65b0</p> </blockquote> <h3 id="124-\u8fde\u63a5\u72b6\u6001-readystate">12.4 \u8fde\u63a5\u72b6\u6001 readyState</h3> <p><code>EventSource</code> \u5bf9\u8c61\u6709 <code>readyState</code> \u5c5e\u6027\uff0c\u8be5\u5c5e\u6027\u5177\u6709\u4e0b\u5217\u503c\u4e4b\u4e00\uff1a</p> <pre><code class="language-js">EventSource.CONNECTING = 0; // \u8fde\u63a5\u4e2d\u6216\u8005\u91cd\u8fde\u4e2d\nEventSource.OPEN = 1; // \u5df2\u8fde\u63a5\nEventSource.CLOSED = 2; // \u8fde\u63a5\u5df2\u5173\u95ed</code></pre> <p>\u5bf9\u8c61\u521b\u5efa\u5b8c\u6210\u6216\u8005\u8fde\u63a5\u65ad\u5f00\u540e\uff0c\u5b83\u59cb\u7ec8\u662f <code>EventSource.CONNECTING</code>\uff08\u7b49\u4e8e 0\uff09</p> <h3 id="125-event-\u7c7b\u578b">12.5 Event \u7c7b\u578b</h3> <p>\u9ed8\u8ba4\u60c5\u51b5\u4e0b <code>EventSource</code> \u5bf9\u8c61\u751f\u6210\u4e09\u4e2a\u4e8b\u4ef6\uff1a</p> <ul> <li><code>message</code> \u2014\u2014 \u6536\u5230\u6d88\u606f\uff0c\u53ef\u4ee5\u7528 <code>event.data</code> \u8bbf\u95ee</li> <li><code>open</code> \u2014\u2014 \u8fde\u63a5\u5df2\u6253\u5f00</li> <li><code>error</code> \u2014\u2014 \u65e0\u6cd5\u5efa\u7acb\u8fde\u63a5\uff0c\u4f8b\u5982\uff0c\u670d\u52a1\u5668\u8fd4\u56de HTTP 500 \u72b6\u6001\u7801</li> </ul> <p>\u670d\u52a1\u5668\u53ef\u4ee5\u5728\u4e8b\u4ef6\u5f00\u59cb\u65f6\u4f7f\u7528 <code>event: ...</code> \u6307\u5b9a\u53e6\u4e00\u79cd\u7c7b\u578b\u4e8b\u4ef6</p> <pre><code>event: join\ndata: Bob\n\ndata: Hello\n\nevent: leave\ndata: Bob</code></pre><p>\u8981\u5904\u7406\u81ea\u5b9a\u4e49\u4e8b\u4ef6\uff0c\u6211\u4eec\u5fc5\u987b\u4f7f\u7528 <code>addEventListener</code>\uff1a</p> <pre><code class="language-js">eventSource.addEventListener(&#39;join&#39;, event =&gt; {\n  alert(`Joined ${event.data}`);\n});\n\neventSource.addEventListener(&#39;message&#39;, event =&gt; {\n  alert(`Said: ${event.data}`);\n});\n\neventSource.addEventListener(&#39;leave&#39;, event =&gt; {\n  alert(`Left ${event.data}`);\n});</code></pre> ';e.exports=s}}]);