(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["docs/es6/class"],{Ze3q:function(e,o){var n='<h1 id="class-\u7684\u57fa\u672c\u8bed\u6cd5">Class \u7684\u57fa\u672c\u8bed\u6cd5</h1> <h2 id="\u7b80\u4ecb">\u7b80\u4ecb</h2> <p>JavaScript \u8bed\u8a00\u4e2d\uff0c\u751f\u6210\u5b9e\u4f8b\u5bf9\u8c61\u7684\u4f20\u7edf\u65b9\u6cd5\u662f\u901a\u8fc7\u6784\u9020\u51fd\u6570\u3002\u4e0b\u9762\u662f\u4e00\u4e2a\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">function Point(x, y) {\n  this.x = x;\n  this.y = y;\n}\n\nPoint.prototype.toString = function () {\n  return &#39;(&#39; + this.x + &#39;, &#39; + this.y + &#39;)&#39;;\n};\n\nvar p = new Point(1, 2);</code></pre> <p>\u4e0a\u9762\u8fd9\u79cd\u5199\u6cd5\u8ddf\u4f20\u7edf\u7684\u9762\u5411\u5bf9\u8c61\u8bed\u8a00\uff08\u6bd4\u5982 C++ \u548c Java\uff09\u5dee\u5f02\u5f88\u5927\uff0c\u5f88\u5bb9\u6613\u8ba9\u65b0\u5b66\u4e60\u8fd9\u95e8\u8bed\u8a00\u7684\u7a0b\u5e8f\u5458\u611f\u5230\u56f0\u60d1\u3002</p> <p>ES6 \u63d0\u4f9b\u4e86\u66f4\u63a5\u8fd1\u4f20\u7edf\u8bed\u8a00\u7684\u5199\u6cd5\uff0c\u5f15\u5165\u4e86 Class\uff08\u7c7b\uff09\u8fd9\u4e2a\u6982\u5ff5\uff0c\u4f5c\u4e3a\u5bf9\u8c61\u7684\u6a21\u677f\u3002\u901a\u8fc7<code>class</code>\u5173\u952e\u5b57\uff0c\u53ef\u4ee5\u5b9a\u4e49\u7c7b\u3002</p> <p>\u57fa\u672c\u4e0a\uff0cES6 \u7684<code>class</code>\u53ef\u4ee5\u770b\u4f5c\u53ea\u662f\u4e00\u4e2a\u8bed\u6cd5\u7cd6\uff0c\u5b83\u7684\u7edd\u5927\u90e8\u5206\u529f\u80fd\uff0cES5 \u90fd\u53ef\u4ee5\u505a\u5230\uff0c\u65b0\u7684<code>class</code>\u5199\u6cd5\u53ea\u662f\u8ba9\u5bf9\u8c61\u539f\u578b\u7684\u5199\u6cd5\u66f4\u52a0\u6e05\u6670\u3001\u66f4\u50cf\u9762\u5411\u5bf9\u8c61\u7f16\u7a0b\u7684\u8bed\u6cd5\u800c\u5df2\u3002\u4e0a\u9762\u7684\u4ee3\u7801\u7528 ES6 \u7684<code>class</code>\u6539\u5199\uff0c\u5c31\u662f\u4e0b\u9762\u8fd9\u6837\u3002</p> <pre><code class="language-javascript">//\u5b9a\u4e49\u7c7b\nclass Point {\n  constructor(x, y) {\n    this.x = x;\n    this.y = y;\n  }\n\n  toString() {\n    return &#39;(&#39; + this.x + &#39;, &#39; + this.y + &#39;)&#39;;\n  }\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u5b9a\u4e49\u4e86\u4e00\u4e2a\u201c\u7c7b\u201d\uff0c\u53ef\u4ee5\u770b\u5230\u91cc\u9762\u6709\u4e00\u4e2a<code>constructor</code>\u65b9\u6cd5\uff0c\u8fd9\u5c31\u662f\u6784\u9020\u65b9\u6cd5\uff0c\u800c<code>this</code>\u5173\u952e\u5b57\u5219\u4ee3\u8868\u5b9e\u4f8b\u5bf9\u8c61\u3002\u4e5f\u5c31\u662f\u8bf4\uff0cES5 \u7684\u6784\u9020\u51fd\u6570<code>Point</code>\uff0c\u5bf9\u5e94 ES6 \u7684<code>Point</code>\u7c7b\u7684\u6784\u9020\u65b9\u6cd5\u3002</p> <p><code>Point</code>\u7c7b\u9664\u4e86\u6784\u9020\u65b9\u6cd5\uff0c\u8fd8\u5b9a\u4e49\u4e86\u4e00\u4e2a<code>toString</code>\u65b9\u6cd5\u3002\u6ce8\u610f\uff0c\u5b9a\u4e49\u201c\u7c7b\u201d\u7684\u65b9\u6cd5\u7684\u65f6\u5019\uff0c\u524d\u9762\u4e0d\u9700\u8981\u52a0\u4e0a<code>function</code>\u8fd9\u4e2a\u5173\u952e\u5b57\uff0c\u76f4\u63a5\u628a\u51fd\u6570\u5b9a\u4e49\u653e\u8fdb\u53bb\u4e86\u5c31\u53ef\u4ee5\u4e86\u3002\u53e6\u5916\uff0c\u65b9\u6cd5\u4e4b\u95f4\u4e0d\u9700\u8981\u9017\u53f7\u5206\u9694\uff0c\u52a0\u4e86\u4f1a\u62a5\u9519\u3002</p> <p>ES6 \u7684\u7c7b\uff0c\u5b8c\u5168\u53ef\u4ee5\u770b\u4f5c\u6784\u9020\u51fd\u6570\u7684\u53e6\u4e00\u79cd\u5199\u6cd5\u3002</p> <pre><code class="language-javascript">class Point {\n  // ...\n}\n\ntypeof Point // &quot;function&quot;\nPoint === Point.prototype.constructor // true</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u8868\u660e\uff0c\u7c7b\u7684\u6570\u636e\u7c7b\u578b\u5c31\u662f\u51fd\u6570\uff0c\u7c7b\u672c\u8eab\u5c31\u6307\u5411\u6784\u9020\u51fd\u6570\u3002</p> <p>\u4f7f\u7528\u7684\u65f6\u5019\uff0c\u4e5f\u662f\u76f4\u63a5\u5bf9\u7c7b\u4f7f\u7528<code>new</code>\u547d\u4ee4\uff0c\u8ddf\u6784\u9020\u51fd\u6570\u7684\u7528\u6cd5\u5b8c\u5168\u4e00\u81f4\u3002</p> <pre><code class="language-javascript">class Bar {\n  doStuff() {\n    console.log(&#39;stuff&#39;);\n  }\n}\n\nvar b = new Bar();\nb.doStuff() // &quot;stuff&quot;</code></pre> <p>\u6784\u9020\u51fd\u6570\u7684<code>prototype</code>\u5c5e\u6027\uff0c\u5728 ES6 \u7684\u201c\u7c7b\u201d\u4e0a\u9762\u7ee7\u7eed\u5b58\u5728\u3002\u4e8b\u5b9e\u4e0a\uff0c\u7c7b\u7684\u6240\u6709\u65b9\u6cd5\u90fd\u5b9a\u4e49\u5728\u7c7b\u7684<code>prototype</code>\u5c5e\u6027\u4e0a\u9762\u3002</p> <pre><code class="language-javascript">class Point {\n  constructor() {\n    // ...\n  }\n\n  toString() {\n    // ...\n  }\n\n  toValue() {\n    // ...\n  }\n}\n\n// \u7b49\u540c\u4e8e\n\nPoint.prototype = {\n  constructor() {},\n  toString() {},\n  toValue() {},\n};</code></pre> <p>\u5728\u7c7b\u7684\u5b9e\u4f8b\u4e0a\u9762\u8c03\u7528\u65b9\u6cd5\uff0c\u5176\u5b9e\u5c31\u662f\u8c03\u7528\u539f\u578b\u4e0a\u7684\u65b9\u6cd5\u3002</p> <pre><code class="language-javascript">class B {}\nlet b = new B();\n\nb.constructor === B.prototype.constructor // true</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>b</code>\u662f<code>B</code>\u7c7b\u7684\u5b9e\u4f8b\uff0c\u5b83\u7684<code>constructor</code>\u65b9\u6cd5\u5c31\u662f<code>B</code>\u7c7b\u539f\u578b\u7684<code>constructor</code>\u65b9\u6cd5\u3002</p> <p>\u7531\u4e8e\u7c7b\u7684\u65b9\u6cd5\u90fd\u5b9a\u4e49\u5728<code>prototype</code>\u5bf9\u8c61\u4e0a\u9762\uff0c\u6240\u4ee5\u7c7b\u7684\u65b0\u65b9\u6cd5\u53ef\u4ee5\u6dfb\u52a0\u5728<code>prototype</code>\u5bf9\u8c61\u4e0a\u9762\u3002<code>Object.assign</code>\u65b9\u6cd5\u53ef\u4ee5\u5f88\u65b9\u4fbf\u5730\u4e00\u6b21\u5411\u7c7b\u6dfb\u52a0\u591a\u4e2a\u65b9\u6cd5\u3002</p> <pre><code class="language-javascript">class Point {\n  constructor(){\n    // ...\n  }\n}\n\nObject.assign(Point.prototype, {\n  toString(){},\n  toValue(){}\n});</code></pre> <p><code>prototype</code>\u5bf9\u8c61\u7684<code>constructor</code>\u5c5e\u6027\uff0c\u76f4\u63a5\u6307\u5411\u201c\u7c7b\u201d\u7684\u672c\u8eab\uff0c\u8fd9\u4e0e ES5 \u7684\u884c\u4e3a\u662f\u4e00\u81f4\u7684\u3002</p> <pre><code class="language-javascript">Point.prototype.constructor === Point // true</code></pre> <p>\u53e6\u5916\uff0c\u7c7b\u7684\u5185\u90e8\u6240\u6709\u5b9a\u4e49\u7684\u65b9\u6cd5\uff0c\u90fd\u662f\u4e0d\u53ef\u679a\u4e3e\u7684\uff08non-enumerable\uff09\u3002</p> <pre><code class="language-javascript">class Point {\n  constructor(x, y) {\n    // ...\n  }\n\n  toString() {\n    // ...\n  }\n}\n\nObject.keys(Point.prototype)\n// []\nObject.getOwnPropertyNames(Point.prototype)\n// [&quot;constructor&quot;,&quot;toString&quot;]</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>toString</code>\u65b9\u6cd5\u662f<code>Point</code>\u7c7b\u5185\u90e8\u5b9a\u4e49\u7684\u65b9\u6cd5\uff0c\u5b83\u662f\u4e0d\u53ef\u679a\u4e3e\u7684\u3002\u8fd9\u4e00\u70b9\u4e0e ES5 \u7684\u884c\u4e3a\u4e0d\u4e00\u81f4\u3002</p> <pre><code class="language-javascript">var Point = function (x, y) {\n  // ...\n};\n\nPoint.prototype.toString = function() {\n  // ...\n};\n\nObject.keys(Point.prototype)\n// [&quot;toString&quot;]\nObject.getOwnPropertyNames(Point.prototype)\n// [&quot;constructor&quot;,&quot;toString&quot;]</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u91c7\u7528 ES5 \u7684\u5199\u6cd5\uff0c<code>toString</code>\u65b9\u6cd5\u5c31\u662f\u53ef\u679a\u4e3e\u7684\u3002</p> <p>\u7c7b\u7684\u5c5e\u6027\u540d\uff0c\u53ef\u4ee5\u91c7\u7528\u8868\u8fbe\u5f0f\u3002</p> <pre><code class="language-javascript">let methodName = &#39;getArea&#39;;\n\nclass Square {\n  constructor(length) {\n    // ...\n  }\n\n  [methodName]() {\n    // ...\n  }\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>Square</code>\u7c7b\u7684\u65b9\u6cd5\u540d<code>getArea</code>\uff0c\u662f\u4ece\u8868\u8fbe\u5f0f\u5f97\u5230\u7684\u3002</p> <h2 id="\u4e25\u683c\u6a21\u5f0f">\u4e25\u683c\u6a21\u5f0f</h2> <p>\u7c7b\u548c\u6a21\u5757\u7684\u5185\u90e8\uff0c\u9ed8\u8ba4\u5c31\u662f\u4e25\u683c\u6a21\u5f0f\uff0c\u6240\u4ee5\u4e0d\u9700\u8981\u4f7f\u7528<code>use strict</code>\u6307\u5b9a\u8fd0\u884c\u6a21\u5f0f\u3002\u53ea\u8981\u4f60\u7684\u4ee3\u7801\u5199\u5728\u7c7b\u6216\u6a21\u5757\u4e4b\u4e2d\uff0c\u5c31\u53ea\u6709\u4e25\u683c\u6a21\u5f0f\u53ef\u7528\u3002</p> <p>\u8003\u8651\u5230\u672a\u6765\u6240\u6709\u7684\u4ee3\u7801\uff0c\u5176\u5b9e\u90fd\u662f\u8fd0\u884c\u5728\u6a21\u5757\u4e4b\u4e2d\uff0c\u6240\u4ee5 ES6 \u5b9e\u9645\u4e0a\u628a\u6574\u4e2a\u8bed\u8a00\u5347\u7ea7\u5230\u4e86\u4e25\u683c\u6a21\u5f0f\u3002</p> <h2 id="constructor-\u65b9\u6cd5">constructor \u65b9\u6cd5</h2> <p><code>constructor</code>\u65b9\u6cd5\u662f\u7c7b\u7684\u9ed8\u8ba4\u65b9\u6cd5\uff0c\u901a\u8fc7<code>new</code>\u547d\u4ee4\u751f\u6210\u5bf9\u8c61\u5b9e\u4f8b\u65f6\uff0c\u81ea\u52a8\u8c03\u7528\u8be5\u65b9\u6cd5\u3002\u4e00\u4e2a\u7c7b\u5fc5\u987b\u6709<code>constructor</code>\u65b9\u6cd5\uff0c\u5982\u679c\u6ca1\u6709\u663e\u5f0f\u5b9a\u4e49\uff0c\u4e00\u4e2a\u7a7a\u7684<code>constructor</code>\u65b9\u6cd5\u4f1a\u88ab\u9ed8\u8ba4\u6dfb\u52a0\u3002</p> <pre><code class="language-javascript">class Point {\n}\n\n// \u7b49\u540c\u4e8e\nclass Point {\n  constructor() {}\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5b9a\u4e49\u4e86\u4e00\u4e2a\u7a7a\u7684\u7c7b<code>Point</code>\uff0cJavaScript \u5f15\u64ce\u4f1a\u81ea\u52a8\u4e3a\u5b83\u6dfb\u52a0\u4e00\u4e2a\u7a7a\u7684<code>constructor</code>\u65b9\u6cd5\u3002</p> <p><code>constructor</code>\u65b9\u6cd5\u9ed8\u8ba4\u8fd4\u56de\u5b9e\u4f8b\u5bf9\u8c61\uff08\u5373<code>this</code>\uff09\uff0c\u5b8c\u5168\u53ef\u4ee5\u6307\u5b9a\u8fd4\u56de\u53e6\u5916\u4e00\u4e2a\u5bf9\u8c61\u3002</p> <pre><code class="language-javascript">class Foo {\n  constructor() {\n    return Object.create(null);\n  }\n}\n\nnew Foo() instanceof Foo\n// false</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>constructor</code>\u51fd\u6570\u8fd4\u56de\u4e00\u4e2a\u5168\u65b0\u7684\u5bf9\u8c61\uff0c\u7ed3\u679c\u5bfc\u81f4\u5b9e\u4f8b\u5bf9\u8c61\u4e0d\u662f<code>Foo</code>\u7c7b\u7684\u5b9e\u4f8b\u3002</p> <p>\u7c7b\u5fc5\u987b\u4f7f\u7528<code>new</code>\u8c03\u7528\uff0c\u5426\u5219\u4f1a\u62a5\u9519\u3002\u8fd9\u662f\u5b83\u8ddf\u666e\u901a\u6784\u9020\u51fd\u6570\u7684\u4e00\u4e2a\u4e3b\u8981\u533a\u522b\uff0c\u540e\u8005\u4e0d\u7528<code>new</code>\u4e5f\u53ef\u4ee5\u6267\u884c\u3002</p> <pre><code class="language-javascript">class Foo {\n  constructor() {\n    return Object.create(null);\n  }\n}\n\nFoo()\n// TypeError: Class constructor Foo cannot be invoked without &#39;new&#39;</code></pre> <h2 id="\u7c7b\u7684\u5b9e\u4f8b\u5bf9\u8c61">\u7c7b\u7684\u5b9e\u4f8b\u5bf9\u8c61</h2> <p>\u751f\u6210\u7c7b\u7684\u5b9e\u4f8b\u5bf9\u8c61\u7684\u5199\u6cd5\uff0c\u4e0e ES5 \u5b8c\u5168\u4e00\u6837\uff0c\u4e5f\u662f\u4f7f\u7528<code>new</code>\u547d\u4ee4\u3002\u524d\u9762\u8bf4\u8fc7\uff0c\u5982\u679c\u5fd8\u8bb0\u52a0\u4e0a<code>new</code>\uff0c\u50cf\u51fd\u6570\u90a3\u6837\u8c03\u7528<code>Class</code>\uff0c\u5c06\u4f1a\u62a5\u9519\u3002</p> <pre><code class="language-javascript">class Point {\n  // ...\n}\n\n// \u62a5\u9519\nvar point = Point(2, 3);\n\n// \u6b63\u786e\nvar point = new Point(2, 3);</code></pre> <p>\u4e0e ES5 \u4e00\u6837\uff0c\u5b9e\u4f8b\u7684\u5c5e\u6027\u9664\u975e\u663e\u5f0f\u5b9a\u4e49\u5728\u5176\u672c\u8eab\uff08\u5373\u5b9a\u4e49\u5728<code>this</code>\u5bf9\u8c61\u4e0a\uff09\uff0c\u5426\u5219\u90fd\u662f\u5b9a\u4e49\u5728\u539f\u578b\u4e0a\uff08\u5373\u5b9a\u4e49\u5728<code>class</code>\u4e0a\uff09\u3002</p> <pre><code class="language-javascript">//\u5b9a\u4e49\u7c7b\nclass Point {\n\n  constructor(x, y) {\n    this.x = x;\n    this.y = y;\n  }\n\n  toString() {\n    return &#39;(&#39; + this.x + &#39;, &#39; + this.y + &#39;)&#39;;\n  }\n\n}\n\nvar point = new Point(2, 3);\n\npoint.toString() // (2, 3)\n\npoint.hasOwnProperty(&#39;x&#39;) // true\npoint.hasOwnProperty(&#39;y&#39;) // true\npoint.hasOwnProperty(&#39;toString&#39;) // false\npoint.__proto__.hasOwnProperty(&#39;toString&#39;) // true</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>x</code>\u548c<code>y</code>\u90fd\u662f\u5b9e\u4f8b\u5bf9\u8c61<code>point</code>\u81ea\u8eab\u7684\u5c5e\u6027\uff08\u56e0\u4e3a\u5b9a\u4e49\u5728<code>this</code>\u53d8\u91cf\u4e0a\uff09\uff0c\u6240\u4ee5<code>hasOwnProperty</code>\u65b9\u6cd5\u8fd4\u56de<code>true</code>\uff0c\u800c<code>toString</code>\u662f\u539f\u578b\u5bf9\u8c61\u7684\u5c5e\u6027\uff08\u56e0\u4e3a\u5b9a\u4e49\u5728<code>Point</code>\u7c7b\u4e0a\uff09\uff0c\u6240\u4ee5<code>hasOwnProperty</code>\u65b9\u6cd5\u8fd4\u56de<code>false</code>\u3002\u8fd9\u4e9b\u90fd\u4e0e ES5 \u7684\u884c\u4e3a\u4fdd\u6301\u4e00\u81f4\u3002</p> <p>\u4e0e ES5 \u4e00\u6837\uff0c\u7c7b\u7684\u6240\u6709\u5b9e\u4f8b\u5171\u4eab\u4e00\u4e2a\u539f\u578b\u5bf9\u8c61\u3002</p> <pre><code class="language-javascript">var p1 = new Point(2,3);\nvar p2 = new Point(3,2);\n\np1.__proto__ === p2.__proto__\n//true</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>p1</code>\u548c<code>p2</code>\u90fd\u662f<code>Point</code>\u7684\u5b9e\u4f8b\uff0c\u5b83\u4eec\u7684\u539f\u578b\u90fd\u662f<code>Point.prototype</code>\uff0c\u6240\u4ee5<code>__proto__</code>\u5c5e\u6027\u662f\u76f8\u7b49\u7684\u3002</p> <p>\u8fd9\u4e5f\u610f\u5473\u7740\uff0c\u53ef\u4ee5\u901a\u8fc7\u5b9e\u4f8b\u7684<code>__proto__</code>\u5c5e\u6027\u4e3a\u201c\u7c7b\u201d\u6dfb\u52a0\u65b9\u6cd5\u3002</p> <blockquote> <p><code>__proto__</code> \u5e76\u4e0d\u662f\u8bed\u8a00\u672c\u8eab\u7684\u7279\u6027\uff0c\u8fd9\u662f\u5404\u5927\u5382\u5546\u5177\u4f53\u5b9e\u73b0\u65f6\u6dfb\u52a0\u7684\u79c1\u6709\u5c5e\u6027\uff0c\u867d\u7136\u76ee\u524d\u5f88\u591a\u73b0\u4ee3\u6d4f\u89c8\u5668\u7684 JS \u5f15\u64ce\u4e2d\u90fd\u63d0\u4f9b\u4e86\u8fd9\u4e2a\u79c1\u6709\u5c5e\u6027\uff0c\u4f46\u4f9d\u65e7\u4e0d\u5efa\u8bae\u5728\u751f\u4ea7\u4e2d\u4f7f\u7528\u8be5\u5c5e\u6027\uff0c\u907f\u514d\u5bf9\u73af\u5883\u4ea7\u751f\u4f9d\u8d56\u3002\u751f\u4ea7\u73af\u5883\u4e2d\uff0c\u6211\u4eec\u53ef\u4ee5\u4f7f\u7528 <code>Object.getPrototypeOf</code> \u65b9\u6cd5\u6765\u83b7\u53d6\u5b9e\u4f8b\u5bf9\u8c61\u7684\u539f\u578b\uff0c\u7136\u540e\u518d\u6765\u4e3a\u539f\u578b\u6dfb\u52a0\u65b9\u6cd5/\u5c5e\u6027\u3002</p> </blockquote> <pre><code class="language-javascript">var p1 = new Point(2,3);\nvar p2 = new Point(3,2);\n\np1.__proto__.printName = function () { return &#39;Oops&#39; };\n\np1.printName() // &quot;Oops&quot;\np2.printName() // &quot;Oops&quot;\n\nvar p3 = new Point(4,2);\np3.printName() // &quot;Oops&quot;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u5728<code>p1</code>\u7684\u539f\u578b\u4e0a\u6dfb\u52a0\u4e86\u4e00\u4e2a<code>printName</code>\u65b9\u6cd5\uff0c\u7531\u4e8e<code>p1</code>\u7684\u539f\u578b\u5c31\u662f<code>p2</code>\u7684\u539f\u578b\uff0c\u56e0\u6b64<code>p2</code>\u4e5f\u53ef\u4ee5\u8c03\u7528\u8fd9\u4e2a\u65b9\u6cd5\u3002\u800c\u4e14\uff0c\u6b64\u540e\u65b0\u5efa\u7684\u5b9e\u4f8b<code>p3</code>\u4e5f\u53ef\u4ee5\u8c03\u7528\u8fd9\u4e2a\u65b9\u6cd5\u3002\u8fd9\u610f\u5473\u7740\uff0c\u4f7f\u7528\u5b9e\u4f8b\u7684<code>__proto__</code>\u5c5e\u6027\u6539\u5199\u539f\u578b\uff0c\u5fc5\u987b\u76f8\u5f53\u8c28\u614e\uff0c\u4e0d\u63a8\u8350\u4f7f\u7528\uff0c\u56e0\u4e3a\u8fd9\u4f1a\u6539\u53d8\u201c\u7c7b\u201d\u7684\u539f\u59cb\u5b9a\u4e49\uff0c\u5f71\u54cd\u5230\u6240\u6709\u5b9e\u4f8b\u3002</p> <h2 id="class-\u8868\u8fbe\u5f0f">Class \u8868\u8fbe\u5f0f</h2> <p>\u4e0e\u51fd\u6570\u4e00\u6837\uff0c\u7c7b\u4e5f\u53ef\u4ee5\u4f7f\u7528\u8868\u8fbe\u5f0f\u7684\u5f62\u5f0f\u5b9a\u4e49\u3002</p> <pre><code class="language-javascript">const MyClass = class Me {\n  getClassName() {\n    return Me.name;\n  }\n};</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4f7f\u7528\u8868\u8fbe\u5f0f\u5b9a\u4e49\u4e86\u4e00\u4e2a\u7c7b\u3002\u9700\u8981\u6ce8\u610f\u7684\u662f\uff0c\u8fd9\u4e2a\u7c7b\u7684\u540d\u5b57\u662f<code>MyClass</code>\u800c\u4e0d\u662f<code>Me</code>\uff0c<code>Me</code>\u53ea\u5728 Class \u7684\u5185\u90e8\u4ee3\u7801\u53ef\u7528\uff0c\u6307\u4ee3\u5f53\u524d\u7c7b\u3002</p> <pre><code class="language-javascript">let inst = new MyClass();\ninst.getClassName() // Me\nMe.name // ReferenceError: Me is not defined</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u8868\u793a\uff0c<code>Me</code>\u53ea\u5728 Class \u5185\u90e8\u6709\u5b9a\u4e49\u3002</p> <p>\u5982\u679c\u7c7b\u7684\u5185\u90e8\u6ca1\u7528\u5230\u7684\u8bdd\uff0c\u53ef\u4ee5\u7701\u7565<code>Me</code>\uff0c\u4e5f\u5c31\u662f\u53ef\u4ee5\u5199\u6210\u4e0b\u9762\u7684\u5f62\u5f0f\u3002</p> <pre><code class="language-javascript">const MyClass = class { /* ... */ };</code></pre> <p>\u91c7\u7528 Class \u8868\u8fbe\u5f0f\uff0c\u53ef\u4ee5\u5199\u51fa\u7acb\u5373\u6267\u884c\u7684 Class\u3002</p> <pre><code class="language-javascript">let person = new class {\n  constructor(name) {\n    this.name = name;\n  }\n\n  sayName() {\n    console.log(this.name);\n  }\n}(&#39;\u5f20\u4e09&#39;);\n\nperson.sayName(); // &quot;\u5f20\u4e09&quot;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>person</code>\u662f\u4e00\u4e2a\u7acb\u5373\u6267\u884c\u7684\u7c7b\u7684\u5b9e\u4f8b\u3002</p> <h2 id="\u4e0d\u5b58\u5728\u53d8\u91cf\u63d0\u5347">\u4e0d\u5b58\u5728\u53d8\u91cf\u63d0\u5347</h2> <p>\u7c7b\u4e0d\u5b58\u5728\u53d8\u91cf\u63d0\u5347\uff08hoist\uff09\uff0c\u8fd9\u4e00\u70b9\u4e0e ES5 \u5b8c\u5168\u4e0d\u540c\u3002</p> <pre><code class="language-javascript">new Foo(); // ReferenceError\nclass Foo {}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>Foo</code>\u7c7b\u4f7f\u7528\u5728\u524d\uff0c\u5b9a\u4e49\u5728\u540e\uff0c\u8fd9\u6837\u4f1a\u62a5\u9519\uff0c\u56e0\u4e3a ES6 \u4e0d\u4f1a\u628a\u7c7b\u7684\u58f0\u660e\u63d0\u5347\u5230\u4ee3\u7801\u5934\u90e8\u3002\u8fd9\u79cd\u89c4\u5b9a\u7684\u539f\u56e0\u4e0e\u4e0b\u6587\u8981\u63d0\u5230\u7684\u7ee7\u627f\u6709\u5173\uff0c\u5fc5\u987b\u4fdd\u8bc1\u5b50\u7c7b\u5728\u7236\u7c7b\u4e4b\u540e\u5b9a\u4e49\u3002</p> <pre><code class="language-javascript">{\n  let Foo = class {};\n  class Bar extends Foo {\n  }\n}</code></pre> <p>\u4e0a\u9762\u7684\u4ee3\u7801\u4e0d\u4f1a\u62a5\u9519\uff0c\u56e0\u4e3a<code>Bar</code>\u7ee7\u627f<code>Foo</code>\u7684\u65f6\u5019\uff0c<code>Foo</code>\u5df2\u7ecf\u6709\u5b9a\u4e49\u4e86\u3002\u4f46\u662f\uff0c\u5982\u679c\u5b58\u5728<code>class</code>\u7684\u63d0\u5347\uff0c\u4e0a\u9762\u4ee3\u7801\u5c31\u4f1a\u62a5\u9519\uff0c\u56e0\u4e3a<code>class</code>\u4f1a\u88ab\u63d0\u5347\u5230\u4ee3\u7801\u5934\u90e8\uff0c\u800c<code>let</code>\u547d\u4ee4\u662f\u4e0d\u63d0\u5347\u7684\uff0c\u6240\u4ee5\u5bfc\u81f4<code>Bar</code>\u7ee7\u627f<code>Foo</code>\u7684\u65f6\u5019\uff0c<code>Foo</code>\u8fd8\u6ca1\u6709\u5b9a\u4e49\u3002</p> <h2 id="\u79c1\u6709\u65b9\u6cd5">\u79c1\u6709\u65b9\u6cd5</h2> <p>\u79c1\u6709\u65b9\u6cd5\u662f\u5e38\u89c1\u9700\u6c42\uff0c\u4f46 ES6 \u4e0d\u63d0\u4f9b\uff0c\u53ea\u80fd\u901a\u8fc7\u53d8\u901a\u65b9\u6cd5\u6a21\u62df\u5b9e\u73b0\u3002</p> <p>\u4e00\u79cd\u505a\u6cd5\u662f\u5728\u547d\u540d\u4e0a\u52a0\u4ee5\u533a\u522b\u3002</p> <pre><code class="language-javascript">class Widget {\n\n  // \u516c\u6709\u65b9\u6cd5\n  foo (baz) {\n    this._bar(baz);\n  }\n\n  // \u79c1\u6709\u65b9\u6cd5\n  _bar(baz) {\n    return this.snaf = baz;\n  }\n\n  // ...\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>_bar</code>\u65b9\u6cd5\u524d\u9762\u7684\u4e0b\u5212\u7ebf\uff0c\u8868\u793a\u8fd9\u662f\u4e00\u4e2a\u53ea\u9650\u4e8e\u5185\u90e8\u4f7f\u7528\u7684\u79c1\u6709\u65b9\u6cd5\u3002\u4f46\u662f\uff0c\u8fd9\u79cd\u547d\u540d\u662f\u4e0d\u4fdd\u9669\u7684\uff0c\u5728\u7c7b\u7684\u5916\u90e8\uff0c\u8fd8\u662f\u53ef\u4ee5\u8c03\u7528\u5230\u8fd9\u4e2a\u65b9\u6cd5\u3002</p> <p>\u53e6\u4e00\u79cd\u65b9\u6cd5\u5c31\u662f\u7d22\u6027\u5c06\u79c1\u6709\u65b9\u6cd5\u79fb\u51fa\u6a21\u5757\uff0c\u56e0\u4e3a\u6a21\u5757\u5185\u90e8\u7684\u6240\u6709\u65b9\u6cd5\u90fd\u662f\u5bf9\u5916\u53ef\u89c1\u7684\u3002</p> <pre><code class="language-javascript">class Widget {\n  foo (baz) {\n    bar.call(this, baz);\n  }\n\n  // ...\n}\n\nfunction bar(baz) {\n  return this.snaf = baz;\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>foo</code>\u662f\u516c\u6709\u65b9\u6cd5\uff0c\u5185\u90e8\u8c03\u7528\u4e86<code>bar.call(this, baz)</code>\u3002\u8fd9\u4f7f\u5f97<code>bar</code>\u5b9e\u9645\u4e0a\u6210\u4e3a\u4e86\u5f53\u524d\u6a21\u5757\u7684\u79c1\u6709\u65b9\u6cd5\u3002</p> <p>\u8fd8\u6709\u4e00\u79cd\u65b9\u6cd5\u662f\u5229\u7528<code>Symbol</code>\u503c\u7684\u552f\u4e00\u6027\uff0c\u5c06\u79c1\u6709\u65b9\u6cd5\u7684\u540d\u5b57\u547d\u540d\u4e3a\u4e00\u4e2a<code>Symbol</code>\u503c\u3002</p> <pre><code class="language-javascript">const bar = Symbol(&#39;bar&#39;);\nconst snaf = Symbol(&#39;snaf&#39;);\n\nexport default class myClass{\n\n  // \u516c\u6709\u65b9\u6cd5\n  foo(baz) {\n    this[bar](baz);\n  }\n\n  // \u79c1\u6709\u65b9\u6cd5\n  [bar](baz) {\n    return this[snaf] = baz;\n  }\n\n  // ...\n};</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>bar</code>\u548c<code>snaf</code>\u90fd\u662f<code>Symbol</code>\u503c\uff0c\u5bfc\u81f4\u7b2c\u4e09\u65b9\u65e0\u6cd5\u83b7\u53d6\u5230\u5b83\u4eec\uff0c\u56e0\u6b64\u8fbe\u5230\u4e86\u79c1\u6709\u65b9\u6cd5\u548c\u79c1\u6709\u5c5e\u6027\u7684\u6548\u679c\u3002</p> <h2 id="\u79c1\u6709\u5c5e\u6027">\u79c1\u6709\u5c5e\u6027</h2> <p>\u4e0e\u79c1\u6709\u65b9\u6cd5\u4e00\u6837\uff0cES6 \u4e0d\u652f\u6301\u79c1\u6709\u5c5e\u6027\u3002\u76ee\u524d\uff0c\u6709\u4e00\u4e2a<a href="https://github.com/tc39/proposal-class-fields#private-fields">\u63d0\u6848</a>\uff0c\u4e3a<code>class</code>\u52a0\u4e86\u79c1\u6709\u5c5e\u6027\u3002\u65b9\u6cd5\u662f\u5728\u5c5e\u6027\u540d\u4e4b\u524d\uff0c\u4f7f\u7528<code>#</code>\u8868\u793a\u3002</p> <pre><code class="language-javascript">class Point {\n  #x;\n\n  constructor(x = 0) {\n    #x = +x; // \u5199\u6210 this.#x \u4ea6\u53ef\n  }\n\n  get x() { return #x }\n  set x(value) { #x = +value }\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>#x</code>\u5c31\u8868\u793a\u79c1\u6709\u5c5e\u6027<code>x</code>\uff0c\u5728<code>Point</code>\u7c7b\u4e4b\u5916\u662f\u8bfb\u53d6\u4e0d\u5230\u8fd9\u4e2a\u5c5e\u6027\u7684\u3002\u8fd8\u53ef\u4ee5\u770b\u5230\uff0c\u79c1\u6709\u5c5e\u6027\u4e0e\u5b9e\u4f8b\u7684\u5c5e\u6027\u662f\u53ef\u4ee5\u540c\u540d\u7684\uff08\u6bd4\u5982\uff0c<code>#x</code>\u4e0e<code>get x()</code>\uff09\u3002</p> <p>\u79c1\u6709\u5c5e\u6027\u53ef\u4ee5\u6307\u5b9a\u521d\u59cb\u503c\uff0c\u5728\u6784\u9020\u51fd\u6570\u6267\u884c\u65f6\u8fdb\u884c\u521d\u59cb\u5316\u3002</p> <pre><code class="language-javascript">class Point {\n  #x = 0;\n  constructor() {\n    #x; // 0\n  }\n}</code></pre> <p>\u4e4b\u6240\u4ee5\u8981\u5f15\u5165\u4e00\u4e2a\u65b0\u7684\u524d\u7f00<code>#</code>\u8868\u793a\u79c1\u6709\u5c5e\u6027\uff0c\u800c\u6ca1\u6709\u91c7\u7528<code>private</code>\u5173\u952e\u5b57\uff0c\u662f\u56e0\u4e3a JavaScript \u662f\u4e00\u95e8\u52a8\u6001\u8bed\u8a00\uff0c\u4f7f\u7528\u72ec\u7acb\u7684\u7b26\u53f7\u4f3c\u4e4e\u662f\u552f\u4e00\u7684\u53ef\u9760\u65b9\u6cd5\uff0c\u80fd\u591f\u51c6\u786e\u5730\u533a\u5206\u4e00\u79cd\u5c5e\u6027\u662f\u5426\u4e3a\u79c1\u6709\u5c5e\u6027\u3002\u53e6\u5916\uff0cRuby \u8bed\u8a00\u4f7f\u7528<code>@</code>\u8868\u793a\u79c1\u6709\u5c5e\u6027\uff0cES6 \u6ca1\u6709\u7528\u8fd9\u4e2a\u7b26\u53f7\u800c\u4f7f\u7528<code>#</code>\uff0c\u662f\u56e0\u4e3a<code>@</code>\u5df2\u7ecf\u88ab\u7559\u7ed9\u4e86 Decorator\u3002</p> <p>\u8be5\u63d0\u6848\u53ea\u89c4\u5b9a\u4e86\u79c1\u6709\u5c5e\u6027\u7684\u5199\u6cd5\u3002\u4f46\u662f\uff0c\u5f88\u81ea\u7136\u5730\uff0c\u5b83\u4e5f\u53ef\u4ee5\u7528\u6765\u5199\u79c1\u6709\u65b9\u6cd5\u3002</p> <pre><code class="language-javascript">class Foo {\n  #a;\n  #b;\n  #sum() { return #a + #b; }\n  printSum() { console.log(#sum()); }\n  constructor(a, b) { #a = a; #b = b; }\n}</code></pre> <h2 id="this-\u7684\u6307\u5411">this \u7684\u6307\u5411</h2> <p>\u7c7b\u7684\u65b9\u6cd5\u5185\u90e8\u5982\u679c\u542b\u6709<code>this</code>\uff0c\u5b83\u9ed8\u8ba4\u6307\u5411\u7c7b\u7684\u5b9e\u4f8b\u3002\u4f46\u662f\uff0c\u5fc5\u987b\u975e\u5e38\u5c0f\u5fc3\uff0c\u4e00\u65e6\u5355\u72ec\u4f7f\u7528\u8be5\u65b9\u6cd5\uff0c\u5f88\u53ef\u80fd\u62a5\u9519\u3002</p> <pre><code class="language-javascript">class Logger {\n  printName(name = &#39;there&#39;) {\n    this.print(`Hello ${name}`);\n  }\n\n  print(text) {\n    console.log(text);\n  }\n}\n\nconst logger = new Logger();\nconst { printName } = logger;\nprintName(); // TypeError: Cannot read property &#39;print&#39; of undefined</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>printName</code>\u65b9\u6cd5\u4e2d\u7684<code>this</code>\uff0c\u9ed8\u8ba4\u6307\u5411<code>Logger</code>\u7c7b\u7684\u5b9e\u4f8b\u3002\u4f46\u662f\uff0c\u5982\u679c\u5c06\u8fd9\u4e2a\u65b9\u6cd5\u63d0\u53d6\u51fa\u6765\u5355\u72ec\u4f7f\u7528\uff0c<code>this</code>\u4f1a\u6307\u5411\u8be5\u65b9\u6cd5\u8fd0\u884c\u65f6\u6240\u5728\u7684\u73af\u5883\uff0c\u56e0\u4e3a\u627e\u4e0d\u5230<code>print</code>\u65b9\u6cd5\u800c\u5bfc\u81f4\u62a5\u9519\u3002</p> <p>\u4e00\u4e2a\u6bd4\u8f83\u7b80\u5355\u7684\u89e3\u51b3\u65b9\u6cd5\u662f\uff0c\u5728\u6784\u9020\u65b9\u6cd5\u4e2d\u7ed1\u5b9a<code>this</code>\uff0c\u8fd9\u6837\u5c31\u4e0d\u4f1a\u627e\u4e0d\u5230<code>print</code>\u65b9\u6cd5\u4e86\u3002</p> <pre><code class="language-javascript">class Logger {\n  constructor() {\n    this.printName = this.printName.bind(this);\n  }\n\n  // ...\n}</code></pre> <p>\u53e6\u4e00\u79cd\u89e3\u51b3\u65b9\u6cd5\u662f\u4f7f\u7528\u7bad\u5934\u51fd\u6570\u3002</p> <pre><code class="language-javascript">class Logger {\n  constructor() {\n    this.printName = (name = &#39;there&#39;) =&gt; {\n      this.print(`Hello ${name}`);\n    };\n  }\n\n  // ...\n}</code></pre> <p>\u8fd8\u6709\u4e00\u79cd\u89e3\u51b3\u65b9\u6cd5\u662f\u4f7f\u7528<code>Proxy</code>\uff0c\u83b7\u53d6\u65b9\u6cd5\u7684\u65f6\u5019\uff0c\u81ea\u52a8\u7ed1\u5b9a<code>this</code>\u3002</p> <pre><code class="language-javascript">function selfish (target) {\n  const cache = new WeakMap();\n  const handler = {\n    get (target, key) {\n      const value = Reflect.get(target, key);\n      if (typeof value !== &#39;function&#39;) {\n        return value;\n      }\n      if (!cache.has(value)) {\n        cache.set(value, value.bind(target));\n      }\n      return cache.get(value);\n    }\n  };\n  const proxy = new Proxy(target, handler);\n  return proxy;\n}\n\nconst logger = selfish(new Logger());</code></pre> <h2 id="name-\u5c5e\u6027">name \u5c5e\u6027</h2> <p>\u7531\u4e8e\u672c\u8d28\u4e0a\uff0cES6 \u7684\u7c7b\u53ea\u662f ES5 \u7684\u6784\u9020\u51fd\u6570\u7684\u4e00\u5c42\u5305\u88c5\uff0c\u6240\u4ee5\u51fd\u6570\u7684\u8bb8\u591a\u7279\u6027\u90fd\u88ab<code>Class</code>\u7ee7\u627f\uff0c\u5305\u62ec<code>name</code>\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">class Point {}\nPoint.name // &quot;Point&quot;</code></pre> <p><code>name</code>\u5c5e\u6027\u603b\u662f\u8fd4\u56de\u7d27\u8ddf\u5728<code>class</code>\u5173\u952e\u5b57\u540e\u9762\u7684\u7c7b\u540d\u3002</p> <h2 id="class-\u7684\u53d6\u503c\u51fd\u6570\uff08getter\uff09\u548c\u5b58\u503c\u51fd\u6570\uff08setter\uff09">Class \u7684\u53d6\u503c\u51fd\u6570\uff08getter\uff09\u548c\u5b58\u503c\u51fd\u6570\uff08setter\uff09</h2> <p>\u4e0e ES5 \u4e00\u6837\uff0c\u5728\u201c\u7c7b\u201d\u7684\u5185\u90e8\u53ef\u4ee5\u4f7f\u7528<code>get</code>\u548c<code>set</code>\u5173\u952e\u5b57\uff0c\u5bf9\u67d0\u4e2a\u5c5e\u6027\u8bbe\u7f6e\u5b58\u503c\u51fd\u6570\u548c\u53d6\u503c\u51fd\u6570\uff0c\u62e6\u622a\u8be5\u5c5e\u6027\u7684\u5b58\u53d6\u884c\u4e3a\u3002</p> <pre><code class="language-javascript">class MyClass {\n  constructor() {\n    // ...\n  }\n  get prop() {\n    return &#39;getter&#39;;\n  }\n  set prop(value) {\n    console.log(&#39;setter: &#39;+value);\n  }\n}\n\nlet inst = new MyClass();\n\ninst.prop = 123;\n// setter: 123\n\ninst.prop\n// &#39;getter&#39;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>prop</code>\u5c5e\u6027\u6709\u5bf9\u5e94\u7684\u5b58\u503c\u51fd\u6570\u548c\u53d6\u503c\u51fd\u6570\uff0c\u56e0\u6b64\u8d4b\u503c\u548c\u8bfb\u53d6\u884c\u4e3a\u90fd\u88ab\u81ea\u5b9a\u4e49\u4e86\u3002</p> <p>\u5b58\u503c\u51fd\u6570\u548c\u53d6\u503c\u51fd\u6570\u662f\u8bbe\u7f6e\u5728\u5c5e\u6027\u7684 Descriptor \u5bf9\u8c61\u4e0a\u7684\u3002</p> <pre><code class="language-javascript">class CustomHTMLElement {\n  constructor(element) {\n    this.element = element;\n  }\n\n  get html() {\n    return this.element.innerHTML;\n  }\n\n  set html(value) {\n    this.element.innerHTML = value;\n  }\n}\n\nvar descriptor = Object.getOwnPropertyDescriptor(\n  CustomHTMLElement.prototype, &quot;html&quot;\n);\n\n&quot;get&quot; in descriptor  // true\n&quot;set&quot; in descriptor  // true</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5b58\u503c\u51fd\u6570\u548c\u53d6\u503c\u51fd\u6570\u662f\u5b9a\u4e49\u5728<code>html</code>\u5c5e\u6027\u7684\u63cf\u8ff0\u5bf9\u8c61\u4e0a\u9762\uff0c\u8fd9\u4e0e ES5 \u5b8c\u5168\u4e00\u81f4\u3002</p> <h2 id="class-\u7684-generator-\u65b9\u6cd5">Class \u7684 Generator \u65b9\u6cd5</h2> <p>\u5982\u679c\u67d0\u4e2a\u65b9\u6cd5\u4e4b\u524d\u52a0\u4e0a\u661f\u53f7\uff08<code>*</code>\uff09\uff0c\u5c31\u8868\u793a\u8be5\u65b9\u6cd5\u662f\u4e00\u4e2a Generator \u51fd\u6570\u3002</p> <pre><code class="language-javascript">class Foo {\n  constructor(...args) {\n    this.args = args;\n  }\n  * [Symbol.iterator]() {\n    for (let arg of this.args) {\n      yield arg;\n    }\n  }\n}\n\nfor (let x of new Foo(&#39;hello&#39;, &#39;world&#39;)) {\n  console.log(x);\n}\n// hello\n// world</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>Foo</code>\u7c7b\u7684<code>Symbol.iterator</code>\u65b9\u6cd5\u524d\u6709\u4e00\u4e2a\u661f\u53f7\uff0c\u8868\u793a\u8be5\u65b9\u6cd5\u662f\u4e00\u4e2a Generator \u51fd\u6570\u3002<code>Symbol.iterator</code>\u65b9\u6cd5\u8fd4\u56de\u4e00\u4e2a<code>Foo</code>\u7c7b\u7684\u9ed8\u8ba4\u904d\u5386\u5668\uff0c<code>for...of</code>\u5faa\u73af\u4f1a\u81ea\u52a8\u8c03\u7528\u8fd9\u4e2a\u904d\u5386\u5668\u3002</p> <h2 id="class-\u7684\u9759\u6001\u65b9\u6cd5">Class \u7684\u9759\u6001\u65b9\u6cd5</h2> <p>\u7c7b\u76f8\u5f53\u4e8e\u5b9e\u4f8b\u7684\u539f\u578b\uff0c\u6240\u6709\u5728\u7c7b\u4e2d\u5b9a\u4e49\u7684\u65b9\u6cd5\uff0c\u90fd\u4f1a\u88ab\u5b9e\u4f8b\u7ee7\u627f\u3002\u5982\u679c\u5728\u4e00\u4e2a\u65b9\u6cd5\u524d\uff0c\u52a0\u4e0a<code>static</code>\u5173\u952e\u5b57\uff0c\u5c31\u8868\u793a\u8be5\u65b9\u6cd5\u4e0d\u4f1a\u88ab\u5b9e\u4f8b\u7ee7\u627f\uff0c\u800c\u662f\u76f4\u63a5\u901a\u8fc7\u7c7b\u6765\u8c03\u7528\uff0c\u8fd9\u5c31\u79f0\u4e3a\u201c\u9759\u6001\u65b9\u6cd5\u201d\u3002</p> <pre><code class="language-javascript">class Foo {\n  static classMethod() {\n    return &#39;hello&#39;;\n  }\n}\n\nFoo.classMethod() // &#39;hello&#39;\n\nvar foo = new Foo();\nfoo.classMethod()\n// TypeError: foo.classMethod is not a function</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>Foo</code>\u7c7b\u7684<code>classMethod</code>\u65b9\u6cd5\u524d\u6709<code>static</code>\u5173\u952e\u5b57\uff0c\u8868\u660e\u8be5\u65b9\u6cd5\u662f\u4e00\u4e2a\u9759\u6001\u65b9\u6cd5\uff0c\u53ef\u4ee5\u76f4\u63a5\u5728<code>Foo</code>\u7c7b\u4e0a\u8c03\u7528\uff08<code>Foo.classMethod()</code>\uff09\uff0c\u800c\u4e0d\u662f\u5728<code>Foo</code>\u7c7b\u7684\u5b9e\u4f8b\u4e0a\u8c03\u7528\u3002\u5982\u679c\u5728\u5b9e\u4f8b\u4e0a\u8c03\u7528\u9759\u6001\u65b9\u6cd5\uff0c\u4f1a\u629b\u51fa\u4e00\u4e2a\u9519\u8bef\uff0c\u8868\u793a\u4e0d\u5b58\u5728\u8be5\u65b9\u6cd5\u3002</p> <p>\u6ce8\u610f\uff0c\u5982\u679c\u9759\u6001\u65b9\u6cd5\u5305\u542b<code>this</code>\u5173\u952e\u5b57\uff0c\u8fd9\u4e2a<code>this</code>\u6307\u7684\u662f\u7c7b\uff0c\u800c\u4e0d\u662f\u5b9e\u4f8b\u3002</p> <pre><code class="language-javascript">class Foo {\n  static bar () {\n    this.baz();\n  }\n  static baz () {\n    console.log(&#39;hello&#39;);\n  }\n  baz () {\n    console.log(&#39;world&#39;);\n  }\n}\n\nFoo.bar() // hello</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u9759\u6001\u65b9\u6cd5<code>bar</code>\u8c03\u7528\u4e86<code>this.baz</code>\uff0c\u8fd9\u91cc\u7684<code>this</code>\u6307\u7684\u662f<code>Foo</code>\u7c7b\uff0c\u800c\u4e0d\u662f<code>Foo</code>\u7684\u5b9e\u4f8b\uff0c\u7b49\u540c\u4e8e\u8c03\u7528<code>Foo.baz</code>\u3002\u53e6\u5916\uff0c\u4ece\u8fd9\u4e2a\u4f8b\u5b50\u8fd8\u53ef\u4ee5\u770b\u51fa\uff0c\u9759\u6001\u65b9\u6cd5\u53ef\u4ee5\u4e0e\u975e\u9759\u6001\u65b9\u6cd5\u91cd\u540d\u3002</p> <p>\u7236\u7c7b\u7684\u9759\u6001\u65b9\u6cd5\uff0c\u53ef\u4ee5\u88ab\u5b50\u7c7b\u7ee7\u627f\u3002</p> <pre><code class="language-javascript">class Foo {\n  static classMethod() {\n    return &#39;hello&#39;;\n  }\n}\n\nclass Bar extends Foo {\n}\n\nBar.classMethod() // &#39;hello&#39;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u7236\u7c7b<code>Foo</code>\u6709\u4e00\u4e2a\u9759\u6001\u65b9\u6cd5\uff0c\u5b50\u7c7b<code>Bar</code>\u53ef\u4ee5\u8c03\u7528\u8fd9\u4e2a\u65b9\u6cd5\u3002</p> <p>\u9759\u6001\u65b9\u6cd5\u4e5f\u662f\u53ef\u4ee5\u4ece<code>super</code>\u5bf9\u8c61\u4e0a\u8c03\u7528\u7684\u3002</p> <pre><code class="language-javascript">class Foo {\n  static classMethod() {\n    return &#39;hello&#39;;\n  }\n}\n\nclass Bar extends Foo {\n  static classMethod() {\n    return super.classMethod() + &#39;, too&#39;;\n  }\n}\n\nBar.classMethod() // &quot;hello, too&quot;</code></pre> <h2 id="class-\u7684\u9759\u6001\u5c5e\u6027\u548c\u5b9e\u4f8b\u5c5e\u6027">Class \u7684\u9759\u6001\u5c5e\u6027\u548c\u5b9e\u4f8b\u5c5e\u6027</h2> <p>\u9759\u6001\u5c5e\u6027\u6307\u7684\u662f Class \u672c\u8eab\u7684\u5c5e\u6027\uff0c\u5373<code>Class.propName</code>\uff0c\u800c\u4e0d\u662f\u5b9a\u4e49\u5728\u5b9e\u4f8b\u5bf9\u8c61\uff08<code>this</code>\uff09\u4e0a\u7684\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">class Foo {\n}\n\nFoo.prop = 1;\nFoo.prop // 1</code></pre> <p>\u4e0a\u9762\u7684\u5199\u6cd5\u4e3a<code>Foo</code>\u7c7b\u5b9a\u4e49\u4e86\u4e00\u4e2a\u9759\u6001\u5c5e\u6027<code>prop</code>\u3002</p> <p>\u76ee\u524d\uff0c\u53ea\u6709\u8fd9\u79cd\u5199\u6cd5\u53ef\u884c\uff0c\u56e0\u4e3a ES6 \u660e\u786e\u89c4\u5b9a\uff0cClass \u5185\u90e8\u53ea\u6709\u9759\u6001\u65b9\u6cd5\uff0c\u6ca1\u6709\u9759\u6001\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">// \u4ee5\u4e0b\u4e24\u79cd\u5199\u6cd5\u90fd\u65e0\u6548\nclass Foo {\n  // \u5199\u6cd5\u4e00\n  prop: 2\n\n  // \u5199\u6cd5\u4e8c\n  static prop: 2\n}\n\nFoo.prop // undefined</code></pre> <p>\u76ee\u524d\u6709\u4e00\u4e2a\u9759\u6001\u5c5e\u6027\u7684<a href="https://github.com/tc39/proposal-class-fields">\u63d0\u6848</a>\uff0c\u5bf9\u5b9e\u4f8b\u5c5e\u6027\u548c\u9759\u6001\u5c5e\u6027\u90fd\u89c4\u5b9a\u4e86\u65b0\u7684\u5199\u6cd5\u3002</p> <p>\uff081\uff09\u7c7b\u7684\u5b9e\u4f8b\u5c5e\u6027</p> <p>\u7c7b\u7684\u5b9e\u4f8b\u5c5e\u6027\u53ef\u4ee5\u7528\u7b49\u5f0f\uff0c\u5199\u5165\u7c7b\u7684\u5b9a\u4e49\u4e4b\u4e2d\u3002</p> <pre><code class="language-javascript">class MyClass {\n  myProp = 42;\n\n  constructor() {\n    console.log(this.myProp); // 42\n  }\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>myProp</code>\u5c31\u662f<code>MyClass</code>\u7684\u5b9e\u4f8b\u5c5e\u6027\u3002\u5728<code>MyClass</code>\u7684\u5b9e\u4f8b\u4e0a\uff0c\u53ef\u4ee5\u8bfb\u53d6\u8fd9\u4e2a\u5c5e\u6027\u3002</p> <p>\u4ee5\u524d\uff0c\u6211\u4eec\u5b9a\u4e49\u5b9e\u4f8b\u5c5e\u6027\uff0c\u53ea\u80fd\u5199\u5728\u7c7b\u7684<code>constructor</code>\u65b9\u6cd5\u91cc\u9762\u3002</p> <pre><code class="language-javascript">class ReactCounter extends React.Component {\n  constructor(props) {\n    super(props);\n    this.state = {\n      count: 0\n    };\n  }\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u6784\u9020\u65b9\u6cd5<code>constructor</code>\u91cc\u9762\uff0c\u5b9a\u4e49\u4e86<code>this.state</code>\u5c5e\u6027\u3002</p> <p>\u6709\u4e86\u65b0\u7684\u5199\u6cd5\u4ee5\u540e\uff0c\u53ef\u4ee5\u4e0d\u5728<code>constructor</code>\u65b9\u6cd5\u91cc\u9762\u5b9a\u4e49\u3002</p> <pre><code class="language-javascript">class ReactCounter extends React.Component {\n  state = {\n    count: 0\n  };\n}</code></pre> <p>\u8fd9\u79cd\u5199\u6cd5\u6bd4\u4ee5\u524d\u66f4\u6e05\u6670\u3002</p> <p>\u4e3a\u4e86\u53ef\u8bfb\u6027\u7684\u76ee\u7684\uff0c\u5bf9\u4e8e\u90a3\u4e9b\u5728<code>constructor</code>\u91cc\u9762\u5df2\u7ecf\u5b9a\u4e49\u7684\u5b9e\u4f8b\u5c5e\u6027\uff0c\u65b0\u5199\u6cd5\u5141\u8bb8\u76f4\u63a5\u5217\u51fa\u3002</p> <pre><code class="language-javascript">class ReactCounter extends React.Component {\n  state;\n  constructor(props) {\n    super(props);\n    this.state = {\n      count: 0\n    };\n  }\n}</code></pre> <p>\uff082\uff09\u7c7b\u7684\u9759\u6001\u5c5e\u6027</p> <p>\u7c7b\u7684\u9759\u6001\u5c5e\u6027\u53ea\u8981\u5728\u4e0a\u9762\u7684\u5b9e\u4f8b\u5c5e\u6027\u5199\u6cd5\u524d\u9762\uff0c\u52a0\u4e0a<code>static</code>\u5173\u952e\u5b57\u5c31\u53ef\u4ee5\u4e86\u3002</p> <pre><code class="language-javascript">class MyClass {\n  static myStaticProp = 42;\n\n  constructor() {\n    console.log(MyClass.myStaticProp); // 42\n  }\n}</code></pre> <p>\u540c\u6837\u7684\uff0c\u8fd9\u4e2a\u65b0\u5199\u6cd5\u5927\u5927\u65b9\u4fbf\u4e86\u9759\u6001\u5c5e\u6027\u7684\u8868\u8fbe\u3002</p> <pre><code class="language-javascript">// \u8001\u5199\u6cd5\nclass Foo {\n  // ...\n}\nFoo.prop = 1;\n\n// \u65b0\u5199\u6cd5\nclass Foo {\n  static prop = 1;\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u8001\u5199\u6cd5\u7684\u9759\u6001\u5c5e\u6027\u5b9a\u4e49\u5728\u7c7b\u7684\u5916\u90e8\u3002\u6574\u4e2a\u7c7b\u751f\u6210\u4ee5\u540e\uff0c\u518d\u751f\u6210\u9759\u6001\u5c5e\u6027\u3002\u8fd9\u6837\u8ba9\u4eba\u5f88\u5bb9\u6613\u5ffd\u7565\u8fd9\u4e2a\u9759\u6001\u5c5e\u6027\uff0c\u4e5f\u4e0d\u7b26\u5408\u76f8\u5173\u4ee3\u7801\u5e94\u8be5\u653e\u5728\u4e00\u8d77\u7684\u4ee3\u7801\u7ec4\u7ec7\u539f\u5219\u3002\u53e6\u5916\uff0c\u65b0\u5199\u6cd5\u662f\u663e\u5f0f\u58f0\u660e\uff08declarative\uff09\uff0c\u800c\u4e0d\u662f\u8d4b\u503c\u5904\u7406\uff0c\u8bed\u4e49\u66f4\u597d\u3002</p> <h2 id="newtarget-\u5c5e\u6027">new.target \u5c5e\u6027</h2> <p><code>new</code>\u662f\u4ece\u6784\u9020\u51fd\u6570\u751f\u6210\u5b9e\u4f8b\u5bf9\u8c61\u7684\u547d\u4ee4\u3002ES6 \u4e3a<code>new</code>\u547d\u4ee4\u5f15\u5165\u4e86\u4e00\u4e2a<code>new.target</code>\u5c5e\u6027\uff0c\u8be5\u5c5e\u6027\u4e00\u822c\u7528\u5728\u6784\u9020\u51fd\u6570\u4e4b\u4e2d\uff0c\u8fd4\u56de<code>new</code>\u547d\u4ee4\u4f5c\u7528\u4e8e\u7684\u90a3\u4e2a\u6784\u9020\u51fd\u6570\u3002\u5982\u679c\u6784\u9020\u51fd\u6570\u4e0d\u662f\u901a\u8fc7<code>new</code>\u547d\u4ee4\u8c03\u7528\u7684\uff0c<code>new.target</code>\u4f1a\u8fd4\u56de<code>undefined</code>\uff0c\u56e0\u6b64\u8fd9\u4e2a\u5c5e\u6027\u53ef\u4ee5\u7528\u6765\u786e\u5b9a\u6784\u9020\u51fd\u6570\u662f\u600e\u4e48\u8c03\u7528\u7684\u3002</p> <pre><code class="language-javascript">function Person(name) {\n  if (new.target !== undefined) {\n    this.name = name;\n  } else {\n    throw new Error(&#39;\u5fc5\u987b\u4f7f\u7528 new \u547d\u4ee4\u751f\u6210\u5b9e\u4f8b&#39;);\n  }\n}\n\n// \u53e6\u4e00\u79cd\u5199\u6cd5\nfunction Person(name) {\n  if (new.target === Person) {\n    this.name = name;\n  } else {\n    throw new Error(&#39;\u5fc5\u987b\u4f7f\u7528 new \u547d\u4ee4\u751f\u6210\u5b9e\u4f8b&#39;);\n  }\n}\n\nvar person = new Person(&#39;\u5f20\u4e09&#39;); // \u6b63\u786e\nvar notAPerson = Person.call(person, &#39;\u5f20\u4e09&#39;);  // \u62a5\u9519</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u786e\u4fdd\u6784\u9020\u51fd\u6570\u53ea\u80fd\u901a\u8fc7<code>new</code>\u547d\u4ee4\u8c03\u7528\u3002</p> <p>Class \u5185\u90e8\u8c03\u7528<code>new.target</code>\uff0c\u8fd4\u56de\u5f53\u524d Class\u3002</p> <pre><code class="language-javascript">class Rectangle {\n  constructor(length, width) {\n    console.log(new.target === Rectangle);\n    this.length = length;\n    this.width = width;\n  }\n}\n\nvar obj = new Rectangle(3, 4); // \u8f93\u51fa true</code></pre> <p>\u9700\u8981\u6ce8\u610f\u7684\u662f\uff0c\u5b50\u7c7b\u7ee7\u627f\u7236\u7c7b\u65f6\uff0c<code>new.target</code>\u4f1a\u8fd4\u56de\u5b50\u7c7b\u3002</p> <pre><code class="language-javascript">class Rectangle {\n  constructor(length, width) {\n    console.log(new.target === Rectangle);\n    // ...\n  }\n}\n\nclass Square extends Rectangle {\n  constructor(length) {\n    super(length, length);\n  }\n}\n\nvar obj = new Square(3); // \u8f93\u51fa false</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>new.target</code>\u4f1a\u8fd4\u56de\u5b50\u7c7b\u3002</p> <p>\u5229\u7528\u8fd9\u4e2a\u7279\u70b9\uff0c\u53ef\u4ee5\u5199\u51fa\u4e0d\u80fd\u72ec\u7acb\u4f7f\u7528\u3001\u5fc5\u987b\u7ee7\u627f\u540e\u624d\u80fd\u4f7f\u7528\u7684\u7c7b\u3002</p> <pre><code class="language-javascript">class Shape {\n  constructor() {\n    if (new.target === Shape) {\n      throw new Error(&#39;\u672c\u7c7b\u4e0d\u80fd\u5b9e\u4f8b\u5316&#39;);\n    }\n  }\n}\n\nclass Rectangle extends Shape {\n  constructor(length, width) {\n    super();\n    // ...\n  }\n}\n\nvar x = new Shape();  // \u62a5\u9519\nvar y = new Rectangle(3, 4);  // \u6b63\u786e</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>Shape</code>\u7c7b\u4e0d\u80fd\u88ab\u5b9e\u4f8b\u5316\uff0c\u53ea\u80fd\u7528\u4e8e\u7ee7\u627f\u3002</p> <p>\u6ce8\u610f\uff0c\u5728\u51fd\u6570\u5916\u90e8\uff0c\u4f7f\u7528<code>new.target</code>\u4f1a\u62a5\u9519\u3002</p> ';e.exports=n}}]);