(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["docs/es6/iterator"],{"0anx":function(e,o){var n='<h1 id="iterator-\u548c-forof-\u5faa\u73af">Iterator \u548c for...of \u5faa\u73af</h1> <h2 id="iterator\uff08\u904d\u5386\u5668\uff09\u7684\u6982\u5ff5">Iterator\uff08\u904d\u5386\u5668\uff09\u7684\u6982\u5ff5</h2> <p>JavaScript \u539f\u6709\u7684\u8868\u793a\u201c\u96c6\u5408\u201d\u7684\u6570\u636e\u7ed3\u6784\uff0c\u4e3b\u8981\u662f\u6570\u7ec4\uff08<code>Array</code>\uff09\u548c\u5bf9\u8c61\uff08<code>Object</code>\uff09\uff0cES6 \u53c8\u6dfb\u52a0\u4e86<code>Map</code>\u548c<code>Set</code>\u3002\u8fd9\u6837\u5c31\u6709\u4e86\u56db\u79cd\u6570\u636e\u96c6\u5408\uff0c\u7528\u6237\u8fd8\u53ef\u4ee5\u7ec4\u5408\u4f7f\u7528\u5b83\u4eec\uff0c\u5b9a\u4e49\u81ea\u5df1\u7684\u6570\u636e\u7ed3\u6784\uff0c\u6bd4\u5982\u6570\u7ec4\u7684\u6210\u5458\u662f<code>Map</code>\uff0c<code>Map</code>\u7684\u6210\u5458\u662f\u5bf9\u8c61\u3002\u8fd9\u6837\u5c31\u9700\u8981\u4e00\u79cd\u7edf\u4e00\u7684\u63a5\u53e3\u673a\u5236\uff0c\u6765\u5904\u7406\u6240\u6709\u4e0d\u540c\u7684\u6570\u636e\u7ed3\u6784\u3002</p> <p>\u904d\u5386\u5668\uff08Iterator\uff09\u5c31\u662f\u8fd9\u6837\u4e00\u79cd\u673a\u5236\u3002\u5b83\u662f\u4e00\u79cd\u63a5\u53e3\uff0c\u4e3a\u5404\u79cd\u4e0d\u540c\u7684\u6570\u636e\u7ed3\u6784\u63d0\u4f9b\u7edf\u4e00\u7684\u8bbf\u95ee\u673a\u5236\u3002\u4efb\u4f55\u6570\u636e\u7ed3\u6784\u53ea\u8981\u90e8\u7f72 Iterator \u63a5\u53e3\uff0c\u5c31\u53ef\u4ee5\u5b8c\u6210\u904d\u5386\u64cd\u4f5c\uff08\u5373\u4f9d\u6b21\u5904\u7406\u8be5\u6570\u636e\u7ed3\u6784\u7684\u6240\u6709\u6210\u5458\uff09\u3002</p> <p>Iterator \u7684\u4f5c\u7528\u6709\u4e09\u4e2a\uff1a\u4e00\u662f\u4e3a\u5404\u79cd\u6570\u636e\u7ed3\u6784\uff0c\u63d0\u4f9b\u4e00\u4e2a\u7edf\u4e00\u7684\u3001\u7b80\u4fbf\u7684\u8bbf\u95ee\u63a5\u53e3\uff1b\u4e8c\u662f\u4f7f\u5f97\u6570\u636e\u7ed3\u6784\u7684\u6210\u5458\u80fd\u591f\u6309\u67d0\u79cd\u6b21\u5e8f\u6392\u5217\uff1b\u4e09\u662f ES6 \u521b\u9020\u4e86\u4e00\u79cd\u65b0\u7684\u904d\u5386\u547d\u4ee4<code>for...of</code>\u5faa\u73af\uff0cIterator \u63a5\u53e3\u4e3b\u8981\u4f9b<code>for...of</code>\u6d88\u8d39\u3002</p> <p>Iterator \u7684\u904d\u5386\u8fc7\u7a0b\u662f\u8fd9\u6837\u7684\u3002</p> <p>\uff081\uff09\u521b\u5efa\u4e00\u4e2a\u6307\u9488\u5bf9\u8c61\uff0c\u6307\u5411\u5f53\u524d\u6570\u636e\u7ed3\u6784\u7684\u8d77\u59cb\u4f4d\u7f6e\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u904d\u5386\u5668\u5bf9\u8c61\u672c\u8d28\u4e0a\uff0c\u5c31\u662f\u4e00\u4e2a\u6307\u9488\u5bf9\u8c61\u3002</p> <p>\uff082\uff09\u7b2c\u4e00\u6b21\u8c03\u7528\u6307\u9488\u5bf9\u8c61\u7684<code>next</code>\u65b9\u6cd5\uff0c\u53ef\u4ee5\u5c06\u6307\u9488\u6307\u5411\u6570\u636e\u7ed3\u6784\u7684\u7b2c\u4e00\u4e2a\u6210\u5458\u3002</p> <p>\uff083\uff09\u7b2c\u4e8c\u6b21\u8c03\u7528\u6307\u9488\u5bf9\u8c61\u7684<code>next</code>\u65b9\u6cd5\uff0c\u6307\u9488\u5c31\u6307\u5411\u6570\u636e\u7ed3\u6784\u7684\u7b2c\u4e8c\u4e2a\u6210\u5458\u3002</p> <p>\uff084\uff09\u4e0d\u65ad\u8c03\u7528\u6307\u9488\u5bf9\u8c61\u7684<code>next</code>\u65b9\u6cd5\uff0c\u76f4\u5230\u5b83\u6307\u5411\u6570\u636e\u7ed3\u6784\u7684\u7ed3\u675f\u4f4d\u7f6e\u3002</p> <p>\u6bcf\u4e00\u6b21\u8c03\u7528<code>next</code>\u65b9\u6cd5\uff0c\u90fd\u4f1a\u8fd4\u56de\u6570\u636e\u7ed3\u6784\u7684\u5f53\u524d\u6210\u5458\u7684\u4fe1\u606f\u3002\u5177\u4f53\u6765\u8bf4\uff0c\u5c31\u662f\u8fd4\u56de\u4e00\u4e2a\u5305\u542b<code>value</code>\u548c<code>done</code>\u4e24\u4e2a\u5c5e\u6027\u7684\u5bf9\u8c61\u3002\u5176\u4e2d\uff0c<code>value</code>\u5c5e\u6027\u662f\u5f53\u524d\u6210\u5458\u7684\u503c\uff0c<code>done</code>\u5c5e\u6027\u662f\u4e00\u4e2a\u5e03\u5c14\u503c\uff0c\u8868\u793a\u904d\u5386\u662f\u5426\u7ed3\u675f\u3002</p> <p>\u4e0b\u9762\u662f\u4e00\u4e2a\u6a21\u62df<code>next</code>\u65b9\u6cd5\u8fd4\u56de\u503c\u7684\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">var it = makeIterator([&#39;a&#39;, &#39;b&#39;]);\n\nit.next() // { value: &quot;a&quot;, done: false }\nit.next() // { value: &quot;b&quot;, done: false }\nit.next() // { value: undefined, done: true }\n\nfunction makeIterator(array) {\n  var nextIndex = 0;\n  return {\n    next: function() {\n      return nextIndex &lt; array.length ?\n        {value: array[nextIndex++], done: false} :\n        {value: undefined, done: true};\n    }\n  };\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u5b9a\u4e49\u4e86\u4e00\u4e2a<code>makeIterator</code>\u51fd\u6570\uff0c\u5b83\u662f\u4e00\u4e2a\u904d\u5386\u5668\u751f\u6210\u51fd\u6570\uff0c\u4f5c\u7528\u5c31\u662f\u8fd4\u56de\u4e00\u4e2a\u904d\u5386\u5668\u5bf9\u8c61\u3002\u5bf9\u6570\u7ec4<code>[&#39;a&#39;, &#39;b&#39;]</code>\u6267\u884c\u8fd9\u4e2a\u51fd\u6570\uff0c\u5c31\u4f1a\u8fd4\u56de\u8be5\u6570\u7ec4\u7684\u904d\u5386\u5668\u5bf9\u8c61\uff08\u5373\u6307\u9488\u5bf9\u8c61\uff09<code>it</code>\u3002</p> <p>\u6307\u9488\u5bf9\u8c61\u7684<code>next</code>\u65b9\u6cd5\uff0c\u7528\u6765\u79fb\u52a8\u6307\u9488\u3002\u5f00\u59cb\u65f6\uff0c\u6307\u9488\u6307\u5411\u6570\u7ec4\u7684\u5f00\u59cb\u4f4d\u7f6e\u3002\u7136\u540e\uff0c\u6bcf\u6b21\u8c03\u7528<code>next</code>\u65b9\u6cd5\uff0c\u6307\u9488\u5c31\u4f1a\u6307\u5411\u6570\u7ec4\u7684\u4e0b\u4e00\u4e2a\u6210\u5458\u3002\u7b2c\u4e00\u6b21\u8c03\u7528\uff0c\u6307\u5411<code>a</code>\uff1b\u7b2c\u4e8c\u6b21\u8c03\u7528\uff0c\u6307\u5411<code>b</code>\u3002</p> <p><code>next</code>\u65b9\u6cd5\u8fd4\u56de\u4e00\u4e2a\u5bf9\u8c61\uff0c\u8868\u793a\u5f53\u524d\u6570\u636e\u6210\u5458\u7684\u4fe1\u606f\u3002\u8fd9\u4e2a\u5bf9\u8c61\u5177\u6709<code>value</code>\u548c<code>done</code>\u4e24\u4e2a\u5c5e\u6027\uff0c<code>value</code>\u5c5e\u6027\u8fd4\u56de\u5f53\u524d\u4f4d\u7f6e\u7684\u6210\u5458\uff0c<code>done</code>\u5c5e\u6027\u662f\u4e00\u4e2a\u5e03\u5c14\u503c\uff0c\u8868\u793a\u904d\u5386\u662f\u5426\u7ed3\u675f\uff0c\u5373\u662f\u5426\u8fd8\u6709\u5fc5\u8981\u518d\u4e00\u6b21\u8c03\u7528<code>next</code>\u65b9\u6cd5\u3002</p> <p>\u603b\u4e4b\uff0c\u8c03\u7528\u6307\u9488\u5bf9\u8c61\u7684<code>next</code>\u65b9\u6cd5\uff0c\u5c31\u53ef\u4ee5\u904d\u5386\u4e8b\u5148\u7ed9\u5b9a\u7684\u6570\u636e\u7ed3\u6784\u3002</p> <p>\u5bf9\u4e8e\u904d\u5386\u5668\u5bf9\u8c61\u6765\u8bf4\uff0c<code>done: false</code>\u548c<code>value: undefined</code>\u5c5e\u6027\u90fd\u662f\u53ef\u4ee5\u7701\u7565\u7684\uff0c\u56e0\u6b64\u4e0a\u9762\u7684<code>makeIterator</code>\u51fd\u6570\u53ef\u4ee5\u7b80\u5199\u6210\u4e0b\u9762\u7684\u5f62\u5f0f\u3002</p> <pre><code class="language-javascript">function makeIterator(array) {\n  var nextIndex = 0;\n  return {\n    next: function() {\n      return nextIndex &lt; array.length ?\n        {value: array[nextIndex++]} :\n        {done: true};\n    }\n  };\n}</code></pre> <p>\u7531\u4e8e Iterator \u53ea\u662f\u628a\u63a5\u53e3\u89c4\u683c\u52a0\u5230\u6570\u636e\u7ed3\u6784\u4e4b\u4e0a\uff0c\u6240\u4ee5\uff0c\u904d\u5386\u5668\u4e0e\u5b83\u6240\u904d\u5386\u7684\u90a3\u4e2a\u6570\u636e\u7ed3\u6784\uff0c\u5b9e\u9645\u4e0a\u662f\u5206\u5f00\u7684\uff0c\u5b8c\u5168\u53ef\u4ee5\u5199\u51fa\u6ca1\u6709\u5bf9\u5e94\u6570\u636e\u7ed3\u6784\u7684\u904d\u5386\u5668\u5bf9\u8c61\uff0c\u6216\u8005\u8bf4\u7528\u904d\u5386\u5668\u5bf9\u8c61\u6a21\u62df\u51fa\u6570\u636e\u7ed3\u6784\u3002\u4e0b\u9762\u662f\u4e00\u4e2a\u65e0\u9650\u8fd0\u884c\u7684\u904d\u5386\u5668\u5bf9\u8c61\u7684\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">var it = idMaker();\n\nit.next().value // &#39;0&#39;\nit.next().value // &#39;1&#39;\nit.next().value // &#39;2&#39;\n// ...\n\nfunction idMaker() {\n  var index = 0;\n\n  return {\n    next: function() {\n      return {value: index++, done: false};\n    }\n  };\n}</code></pre> <p>\u4e0a\u9762\u7684\u4f8b\u5b50\u4e2d\uff0c\u904d\u5386\u5668\u751f\u6210\u51fd\u6570<code>idMaker</code>\uff0c\u8fd4\u56de\u4e00\u4e2a\u904d\u5386\u5668\u5bf9\u8c61\uff08\u5373\u6307\u9488\u5bf9\u8c61\uff09\u3002\u4f46\u662f\u5e76\u6ca1\u6709\u5bf9\u5e94\u7684\u6570\u636e\u7ed3\u6784\uff0c\u6216\u8005\u8bf4\uff0c\u904d\u5386\u5668\u5bf9\u8c61\u81ea\u5df1\u63cf\u8ff0\u4e86\u4e00\u4e2a\u6570\u636e\u7ed3\u6784\u51fa\u6765\u3002</p> <p>\u5982\u679c\u4f7f\u7528 TypeScript \u7684\u5199\u6cd5\uff0c\u904d\u5386\u5668\u63a5\u53e3\uff08Iterable\uff09\u3001\u6307\u9488\u5bf9\u8c61\uff08Iterator\uff09\u548c<code>next</code>\u65b9\u6cd5\u8fd4\u56de\u503c\u7684\u89c4\u683c\u53ef\u4ee5\u63cf\u8ff0\u5982\u4e0b\u3002</p> <pre><code class="language-javascript">interface Iterable {\n  [Symbol.iterator]() : Iterator,\n}\n\ninterface Iterator {\n  next(value?: any) : IterationResult,\n}\n\ninterface IterationResult {\n  value: any,\n  done: boolean,\n}</code></pre> <h2 id="\u9ed8\u8ba4-iterator-\u63a5\u53e3">\u9ed8\u8ba4 Iterator \u63a5\u53e3</h2> <p>Iterator \u63a5\u53e3\u7684\u76ee\u7684\uff0c\u5c31\u662f\u4e3a\u6240\u6709\u6570\u636e\u7ed3\u6784\uff0c\u63d0\u4f9b\u4e86\u4e00\u79cd\u7edf\u4e00\u7684\u8bbf\u95ee\u673a\u5236\uff0c\u5373<code>for...of</code>\u5faa\u73af\uff08\u8be6\u89c1\u4e0b\u6587\uff09\u3002\u5f53\u4f7f\u7528<code>for...of</code>\u5faa\u73af\u904d\u5386\u67d0\u79cd\u6570\u636e\u7ed3\u6784\u65f6\uff0c\u8be5\u5faa\u73af\u4f1a\u81ea\u52a8\u53bb\u5bfb\u627e Iterator \u63a5\u53e3\u3002</p> <p>\u4e00\u79cd\u6570\u636e\u7ed3\u6784\u53ea\u8981\u90e8\u7f72\u4e86 Iterator \u63a5\u53e3\uff0c\u6211\u4eec\u5c31\u79f0\u8fd9\u79cd\u6570\u636e\u7ed3\u6784\u662f\u201d\u53ef\u904d\u5386\u7684\u201c\uff08iterable\uff09\u3002</p> <p>ES6 \u89c4\u5b9a\uff0c\u9ed8\u8ba4\u7684 Iterator \u63a5\u53e3\u90e8\u7f72\u5728\u6570\u636e\u7ed3\u6784\u7684<code>Symbol.iterator</code>\u5c5e\u6027\uff0c\u6216\u8005\u8bf4\uff0c\u4e00\u4e2a\u6570\u636e\u7ed3\u6784\u53ea\u8981\u5177\u6709<code>Symbol.iterator</code>\u5c5e\u6027\uff0c\u5c31\u53ef\u4ee5\u8ba4\u4e3a\u662f\u201c\u53ef\u904d\u5386\u7684\u201d\uff08iterable\uff09\u3002<code>Symbol.iterator</code>\u5c5e\u6027\u672c\u8eab\u662f\u4e00\u4e2a\u51fd\u6570\uff0c\u5c31\u662f\u5f53\u524d\u6570\u636e\u7ed3\u6784\u9ed8\u8ba4\u7684\u904d\u5386\u5668\u751f\u6210\u51fd\u6570\u3002\u6267\u884c\u8fd9\u4e2a\u51fd\u6570\uff0c\u5c31\u4f1a\u8fd4\u56de\u4e00\u4e2a\u904d\u5386\u5668\u3002\u81f3\u4e8e\u5c5e\u6027\u540d<code>Symbol.iterator</code>\uff0c\u5b83\u662f\u4e00\u4e2a\u8868\u8fbe\u5f0f\uff0c\u8fd4\u56de<code>Symbol</code>\u5bf9\u8c61\u7684<code>iterator</code>\u5c5e\u6027\uff0c\u8fd9\u662f\u4e00\u4e2a\u9884\u5b9a\u4e49\u597d\u7684\u3001\u7c7b\u578b\u4e3a Symbol \u7684\u7279\u6b8a\u503c\uff0c\u6240\u4ee5\u8981\u653e\u5728\u65b9\u62ec\u53f7\u5185\uff08\u53c2\u89c1 Symbol \u4e00\u7ae0\uff09\u3002</p> <pre><code class="language-javascript">const obj = {\n  [Symbol.iterator] : function () {\n    return {\n      next: function () {\n        return {\n          value: 1,\n          done: true\n        };\n      }\n    };\n  }\n};</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5bf9\u8c61<code>obj</code>\u662f\u53ef\u904d\u5386\u7684\uff08iterable\uff09\uff0c\u56e0\u4e3a\u5177\u6709<code>Symbol.iterator</code>\u5c5e\u6027\u3002\u6267\u884c\u8fd9\u4e2a\u5c5e\u6027\uff0c\u4f1a\u8fd4\u56de\u4e00\u4e2a\u904d\u5386\u5668\u5bf9\u8c61\u3002\u8be5\u5bf9\u8c61\u7684\u6839\u672c\u7279\u5f81\u5c31\u662f\u5177\u6709<code>next</code>\u65b9\u6cd5\u3002\u6bcf\u6b21\u8c03\u7528<code>next</code>\u65b9\u6cd5\uff0c\u90fd\u4f1a\u8fd4\u56de\u4e00\u4e2a\u4ee3\u8868\u5f53\u524d\u6210\u5458\u7684\u4fe1\u606f\u5bf9\u8c61\uff0c\u5177\u6709<code>value</code>\u548c<code>done</code>\u4e24\u4e2a\u5c5e\u6027\u3002</p> <p>ES6 \u7684\u6709\u4e9b\u6570\u636e\u7ed3\u6784\u539f\u751f\u5177\u5907 Iterator \u63a5\u53e3\uff08\u6bd4\u5982\u6570\u7ec4\uff09\uff0c\u5373\u4e0d\u7528\u4efb\u4f55\u5904\u7406\uff0c\u5c31\u53ef\u4ee5\u88ab<code>for...of</code>\u5faa\u73af\u904d\u5386\u3002\u539f\u56e0\u5728\u4e8e\uff0c\u8fd9\u4e9b\u6570\u636e\u7ed3\u6784\u539f\u751f\u90e8\u7f72\u4e86<code>Symbol.iterator</code>\u5c5e\u6027\uff08\u8be6\u89c1\u4e0b\u6587\uff09\uff0c\u53e6\u5916\u4e00\u4e9b\u6570\u636e\u7ed3\u6784\u6ca1\u6709\uff08\u6bd4\u5982\u5bf9\u8c61\uff09\u3002\u51e1\u662f\u90e8\u7f72\u4e86<code>Symbol.iterator</code>\u5c5e\u6027\u7684\u6570\u636e\u7ed3\u6784\uff0c\u5c31\u79f0\u4e3a\u90e8\u7f72\u4e86\u904d\u5386\u5668\u63a5\u53e3\u3002\u8c03\u7528\u8fd9\u4e2a\u63a5\u53e3\uff0c\u5c31\u4f1a\u8fd4\u56de\u4e00\u4e2a\u904d\u5386\u5668\u5bf9\u8c61\u3002</p> <p>\u539f\u751f\u5177\u5907 Iterator \u63a5\u53e3\u7684\u6570\u636e\u7ed3\u6784\u5982\u4e0b\u3002</p> <ul> <li>Array</li> <li>Map</li> <li>Set</li> <li>String</li> <li>TypedArray</li> <li>\u51fd\u6570\u7684 arguments \u5bf9\u8c61</li> <li>NodeList \u5bf9\u8c61</li> </ul> <p>\u4e0b\u9762\u7684\u4f8b\u5b50\u662f\u6570\u7ec4\u7684<code>Symbol.iterator</code>\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">let arr = [&#39;a&#39;, &#39;b&#39;, &#39;c&#39;];\nlet iter = arr[Symbol.iterator]();\n\niter.next() // { value: &#39;a&#39;, done: false }\niter.next() // { value: &#39;b&#39;, done: false }\niter.next() // { value: &#39;c&#39;, done: false }\niter.next() // { value: undefined, done: true }</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53d8\u91cf<code>arr</code>\u662f\u4e00\u4e2a\u6570\u7ec4\uff0c\u539f\u751f\u5c31\u5177\u6709\u904d\u5386\u5668\u63a5\u53e3\uff0c\u90e8\u7f72\u5728<code>arr</code>\u7684<code>Symbol.iterator</code>\u5c5e\u6027\u4e0a\u9762\u3002\u6240\u4ee5\uff0c\u8c03\u7528\u8fd9\u4e2a\u5c5e\u6027\uff0c\u5c31\u5f97\u5230\u904d\u5386\u5668\u5bf9\u8c61\u3002</p> <p>\u5bf9\u4e8e\u539f\u751f\u90e8\u7f72 Iterator \u63a5\u53e3\u7684\u6570\u636e\u7ed3\u6784\uff0c\u4e0d\u7528\u81ea\u5df1\u5199\u904d\u5386\u5668\u751f\u6210\u51fd\u6570\uff0c<code>for...of</code>\u5faa\u73af\u4f1a\u81ea\u52a8\u904d\u5386\u5b83\u4eec\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u5176\u4ed6\u6570\u636e\u7ed3\u6784\uff08\u4e3b\u8981\u662f\u5bf9\u8c61\uff09\u7684 Iterator \u63a5\u53e3\uff0c\u90fd\u9700\u8981\u81ea\u5df1\u5728<code>Symbol.iterator</code>\u5c5e\u6027\u4e0a\u9762\u90e8\u7f72\uff0c\u8fd9\u6837\u624d\u4f1a\u88ab<code>for...of</code>\u5faa\u73af\u904d\u5386\u3002</p> <p>\u5bf9\u8c61\uff08Object\uff09\u4e4b\u6240\u4ee5\u6ca1\u6709\u9ed8\u8ba4\u90e8\u7f72 Iterator \u63a5\u53e3\uff0c\u662f\u56e0\u4e3a\u5bf9\u8c61\u7684\u54ea\u4e2a\u5c5e\u6027\u5148\u904d\u5386\uff0c\u54ea\u4e2a\u5c5e\u6027\u540e\u904d\u5386\u662f\u4e0d\u786e\u5b9a\u7684\uff0c\u9700\u8981\u5f00\u53d1\u8005\u624b\u52a8\u6307\u5b9a\u3002\u672c\u8d28\u4e0a\uff0c\u904d\u5386\u5668\u662f\u4e00\u79cd\u7ebf\u6027\u5904\u7406\uff0c\u5bf9\u4e8e\u4efb\u4f55\u975e\u7ebf\u6027\u7684\u6570\u636e\u7ed3\u6784\uff0c\u90e8\u7f72\u904d\u5386\u5668\u63a5\u53e3\uff0c\u5c31\u7b49\u4e8e\u90e8\u7f72\u4e00\u79cd\u7ebf\u6027\u8f6c\u6362\u3002\u4e0d\u8fc7\uff0c\u4e25\u683c\u5730\u8bf4\uff0c\u5bf9\u8c61\u90e8\u7f72\u904d\u5386\u5668\u63a5\u53e3\u5e76\u4e0d\u662f\u5f88\u5fc5\u8981\uff0c\u56e0\u4e3a\u8fd9\u65f6\u5bf9\u8c61\u5b9e\u9645\u4e0a\u88ab\u5f53\u4f5c Map \u7ed3\u6784\u4f7f\u7528\uff0cES5 \u6ca1\u6709 Map \u7ed3\u6784\uff0c\u800c ES6 \u539f\u751f\u63d0\u4f9b\u4e86\u3002</p> <p>\u4e00\u4e2a\u5bf9\u8c61\u5982\u679c\u8981\u5177\u5907\u53ef\u88ab<code>for...of</code>\u5faa\u73af\u8c03\u7528\u7684 Iterator \u63a5\u53e3\uff0c\u5c31\u5fc5\u987b\u5728<code>Symbol.iterator</code>\u7684\u5c5e\u6027\u4e0a\u90e8\u7f72\u904d\u5386\u5668\u751f\u6210\u65b9\u6cd5\uff08\u539f\u578b\u94fe\u4e0a\u7684\u5bf9\u8c61\u5177\u6709\u8be5\u65b9\u6cd5\u4e5f\u53ef\uff09\u3002</p> <pre><code class="language-javascript">class RangeIterator {\n  constructor(start, stop) {\n    this.value = start;\n    this.stop = stop;\n  }\n\n  [Symbol.iterator]() { return this; }\n\n  next() {\n    var value = this.value;\n    if (value &lt; this.stop) {\n      this.value++;\n      return {done: false, value: value};\n    }\n    return {done: true, value: undefined};\n  }\n}\n\nfunction range(start, stop) {\n  return new RangeIterator(start, stop);\n}\n\nfor (var value of range(0, 3)) {\n  console.log(value); // 0, 1, 2\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u662f\u4e00\u4e2a\u7c7b\u90e8\u7f72 Iterator \u63a5\u53e3\u7684\u5199\u6cd5\u3002<code>Symbol.iterator</code>\u5c5e\u6027\u5bf9\u5e94\u4e00\u4e2a\u51fd\u6570\uff0c\u6267\u884c\u540e\u8fd4\u56de\u5f53\u524d\u5bf9\u8c61\u7684\u904d\u5386\u5668\u5bf9\u8c61\u3002</p> <p>\u4e0b\u9762\u662f\u901a\u8fc7\u904d\u5386\u5668\u5b9e\u73b0\u6307\u9488\u7ed3\u6784\u7684\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">function Obj(value) {\n  this.value = value;\n  this.next = null;\n}\n\nObj.prototype[Symbol.iterator] = function() {\n  var iterator = { next: next };\n\n  var current = this;\n\n  function next() {\n    if (current) {\n      var value = current.value;\n      current = current.next;\n      return { done: false, value: value };\n    } else {\n      return { done: true };\n    }\n  }\n  return iterator;\n}\n\nvar one = new Obj(1);\nvar two = new Obj(2);\nvar three = new Obj(3);\n\none.next = two;\ntwo.next = three;\n\nfor (var i of one){\n  console.log(i); // 1, 2, 3\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u9996\u5148\u5728\u6784\u9020\u51fd\u6570\u7684\u539f\u578b\u94fe\u4e0a\u90e8\u7f72<code>Symbol.iterator</code>\u65b9\u6cd5\uff0c\u8c03\u7528\u8be5\u65b9\u6cd5\u4f1a\u8fd4\u56de\u904d\u5386\u5668\u5bf9\u8c61<code>iterator</code>\uff0c\u8c03\u7528\u8be5\u5bf9\u8c61\u7684<code>next</code>\u65b9\u6cd5\uff0c\u5728\u8fd4\u56de\u4e00\u4e2a\u503c\u7684\u540c\u65f6\uff0c\u81ea\u52a8\u5c06\u5185\u90e8\u6307\u9488\u79fb\u5230\u4e0b\u4e00\u4e2a\u5b9e\u4f8b\u3002</p> <p>\u4e0b\u9762\u662f\u53e6\u4e00\u4e2a\u4e3a\u5bf9\u8c61\u6dfb\u52a0 Iterator \u63a5\u53e3\u7684\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">let obj = {\n  data: [ &#39;hello&#39;, &#39;world&#39; ],\n  [Symbol.iterator]() {\n    const self = this;\n    let index = 0;\n    return {\n      next() {\n        if (index &lt; self.data.length) {\n          return {\n            value: self.data[index++],\n            done: false\n          };\n        } else {\n          return { value: undefined, done: true };\n        }\n      }\n    };\n  }\n};</code></pre> <p>\u5bf9\u4e8e\u7c7b\u4f3c\u6570\u7ec4\u7684\u5bf9\u8c61\uff08\u5b58\u5728\u6570\u503c\u952e\u540d\u548c<code>length</code>\u5c5e\u6027\uff09\uff0c\u90e8\u7f72 Iterator \u63a5\u53e3\uff0c\u6709\u4e00\u4e2a\u7b80\u4fbf\u65b9\u6cd5\uff0c\u5c31\u662f<code>Symbol.iterator</code>\u65b9\u6cd5\u76f4\u63a5\u5f15\u7528\u6570\u7ec4\u7684 Iterator \u63a5\u53e3\u3002</p> <pre><code class="language-javascript">NodeList.prototype[Symbol.iterator] = Array.prototype[Symbol.iterator];\n// \u6216\u8005\nNodeList.prototype[Symbol.iterator] = [][Symbol.iterator];\n\n[...document.querySelectorAll(&#39;div&#39;)] // \u53ef\u4ee5\u6267\u884c\u4e86</code></pre> <p>NodeList \u5bf9\u8c61\u662f\u7c7b\u4f3c\u6570\u7ec4\u7684\u5bf9\u8c61\uff0c\u672c\u6765\u5c31\u5177\u6709\u904d\u5386\u63a5\u53e3\uff0c\u53ef\u4ee5\u76f4\u63a5\u904d\u5386\u3002\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u6211\u4eec\u5c06\u5b83\u7684\u904d\u5386\u63a5\u53e3\u6539\u6210\u6570\u7ec4\u7684<code>Symbol.iterator</code>\u5c5e\u6027\uff0c\u53ef\u4ee5\u770b\u5230\u6ca1\u6709\u4efb\u4f55\u5f71\u54cd\u3002</p> <p>\u4e0b\u9762\u662f\u53e6\u4e00\u4e2a\u7c7b\u4f3c\u6570\u7ec4\u7684\u5bf9\u8c61\u8c03\u7528\u6570\u7ec4\u7684<code>Symbol.iterator</code>\u65b9\u6cd5\u7684\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">let iterable = {\n  0: &#39;a&#39;,\n  1: &#39;b&#39;,\n  2: &#39;c&#39;,\n  length: 3,\n  [Symbol.iterator]: Array.prototype[Symbol.iterator]\n};\nfor (let item of iterable) {\n  console.log(item); // &#39;a&#39;, &#39;b&#39;, &#39;c&#39;\n}</code></pre> <p>\u6ce8\u610f\uff0c\u666e\u901a\u5bf9\u8c61\u90e8\u7f72\u6570\u7ec4\u7684<code>Symbol.iterator</code>\u65b9\u6cd5\uff0c\u5e76\u65e0\u6548\u679c\u3002</p> <pre><code class="language-javascript">let iterable = {\n  a: &#39;a&#39;,\n  b: &#39;b&#39;,\n  c: &#39;c&#39;,\n  length: 3,\n  [Symbol.iterator]: Array.prototype[Symbol.iterator]\n};\nfor (let item of iterable) {\n  console.log(item); // undefined, undefined, undefined\n}</code></pre> <p>\u5982\u679c<code>Symbol.iterator</code>\u65b9\u6cd5\u5bf9\u5e94\u7684\u4e0d\u662f\u904d\u5386\u5668\u751f\u6210\u51fd\u6570\uff08\u5373\u4f1a\u8fd4\u56de\u4e00\u4e2a\u904d\u5386\u5668\u5bf9\u8c61\uff09\uff0c\u89e3\u91ca\u5f15\u64ce\u5c06\u4f1a\u62a5\u9519\u3002</p> <pre><code class="language-javascript">var obj = {};\n\nobj[Symbol.iterator] = () =&gt; 1;\n\n[...obj] // TypeError: [] is not a function</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53d8\u91cf<code>obj</code>\u7684<code>Symbol.iterator</code>\u65b9\u6cd5\u5bf9\u5e94\u7684\u4e0d\u662f\u904d\u5386\u5668\u751f\u6210\u51fd\u6570\uff0c\u56e0\u6b64\u62a5\u9519\u3002</p> <p>\u6709\u4e86\u904d\u5386\u5668\u63a5\u53e3\uff0c\u6570\u636e\u7ed3\u6784\u5c31\u53ef\u4ee5\u7528<code>for...of</code>\u5faa\u73af\u904d\u5386\uff08\u8be6\u89c1\u4e0b\u6587\uff09\uff0c\u4e5f\u53ef\u4ee5\u4f7f\u7528<code>while</code>\u5faa\u73af\u904d\u5386\u3002</p> <pre><code class="language-javascript">var $iterator = ITERABLE[Symbol.iterator]();\nvar $result = $iterator.next();\nwhile (!$result.done) {\n  var x = $result.value;\n  // ...\n  $result = $iterator.next();\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>ITERABLE</code>\u4ee3\u8868\u67d0\u79cd\u53ef\u904d\u5386\u7684\u6570\u636e\u7ed3\u6784\uff0c<code>$iterator</code>\u662f\u5b83\u7684\u904d\u5386\u5668\u5bf9\u8c61\u3002\u904d\u5386\u5668\u5bf9\u8c61\u6bcf\u6b21\u79fb\u52a8\u6307\u9488\uff08<code>next</code>\u65b9\u6cd5\uff09\uff0c\u90fd\u68c0\u67e5\u4e00\u4e0b\u8fd4\u56de\u503c\u7684<code>done</code>\u5c5e\u6027\uff0c\u5982\u679c\u904d\u5386\u8fd8\u6ca1\u7ed3\u675f\uff0c\u5c31\u79fb\u52a8\u904d\u5386\u5668\u5bf9\u8c61\u7684\u6307\u9488\u5230\u4e0b\u4e00\u6b65\uff08<code>next</code>\u65b9\u6cd5\uff09\uff0c\u4e0d\u65ad\u5faa\u73af\u3002</p> <h2 id="\u8c03\u7528-iterator-\u63a5\u53e3\u7684\u573a\u5408">\u8c03\u7528 Iterator \u63a5\u53e3\u7684\u573a\u5408</h2> <p>\u6709\u4e00\u4e9b\u573a\u5408\u4f1a\u9ed8\u8ba4\u8c03\u7528 Iterator \u63a5\u53e3\uff08\u5373<code>Symbol.iterator</code>\u65b9\u6cd5\uff09\uff0c\u9664\u4e86\u4e0b\u6587\u4f1a\u4ecb\u7ecd\u7684<code>for...of</code>\u5faa\u73af\uff0c\u8fd8\u6709\u51e0\u4e2a\u522b\u7684\u573a\u5408\u3002</p> <p><strong>\uff081\uff09\u89e3\u6784\u8d4b\u503c</strong></p> <p>\u5bf9\u6570\u7ec4\u548c Set \u7ed3\u6784\u8fdb\u884c\u89e3\u6784\u8d4b\u503c\u65f6\uff0c\u4f1a\u9ed8\u8ba4\u8c03\u7528<code>Symbol.iterator</code>\u65b9\u6cd5\u3002</p> <pre><code class="language-javascript">let set = new Set().add(&#39;a&#39;).add(&#39;b&#39;).add(&#39;c&#39;);\n\nlet [x,y] = set;\n// x=&#39;a&#39;; y=&#39;b&#39;\n\nlet [first, ...rest] = set;\n// first=&#39;a&#39;; rest=[&#39;b&#39;,&#39;c&#39;];</code></pre> <p><strong>\uff082\uff09\u6269\u5c55\u8fd0\u7b97\u7b26</strong></p> <p>\u6269\u5c55\u8fd0\u7b97\u7b26\uff08...\uff09\u4e5f\u4f1a\u8c03\u7528\u9ed8\u8ba4\u7684 Iterator \u63a5\u53e3\u3002</p> <pre><code class="language-javascript">// \u4f8b\u4e00\nvar str = &#39;hello&#39;;\n[...str] //  [&#39;h&#39;,&#39;e&#39;,&#39;l&#39;,&#39;l&#39;,&#39;o&#39;]\n\n// \u4f8b\u4e8c\nlet arr = [&#39;b&#39;, &#39;c&#39;];\n[&#39;a&#39;, ...arr, &#39;d&#39;]\n// [&#39;a&#39;, &#39;b&#39;, &#39;c&#39;, &#39;d&#39;]</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u7684\u6269\u5c55\u8fd0\u7b97\u7b26\u5185\u90e8\u5c31\u8c03\u7528 Iterator \u63a5\u53e3\u3002</p> <p>\u5b9e\u9645\u4e0a\uff0c\u8fd9\u63d0\u4f9b\u4e86\u4e00\u79cd\u7b80\u4fbf\u673a\u5236\uff0c\u53ef\u4ee5\u5c06\u4efb\u4f55\u90e8\u7f72\u4e86 Iterator \u63a5\u53e3\u7684\u6570\u636e\u7ed3\u6784\uff0c\u8f6c\u4e3a\u6570\u7ec4\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u53ea\u8981\u67d0\u4e2a\u6570\u636e\u7ed3\u6784\u90e8\u7f72\u4e86 Iterator \u63a5\u53e3\uff0c\u5c31\u53ef\u4ee5\u5bf9\u5b83\u4f7f\u7528\u6269\u5c55\u8fd0\u7b97\u7b26\uff0c\u5c06\u5176\u8f6c\u4e3a\u6570\u7ec4\u3002</p> <pre><code class="language-javascript">let arr = [...iterable];</code></pre> <p><em>*\uff083\uff09yield\\</em> **</p> <p><code>yield*</code>\u540e\u9762\u8ddf\u7684\u662f\u4e00\u4e2a\u53ef\u904d\u5386\u7684\u7ed3\u6784\uff0c\u5b83\u4f1a\u8c03\u7528\u8be5\u7ed3\u6784\u7684\u904d\u5386\u5668\u63a5\u53e3\u3002</p> <pre><code class="language-javascript">let generator = function* () {\n  yield 1;\n  yield* [2,3,4];\n  yield 5;\n};\n\nvar iterator = generator();\n\niterator.next() // { value: 1, done: false }\niterator.next() // { value: 2, done: false }\niterator.next() // { value: 3, done: false }\niterator.next() // { value: 4, done: false }\niterator.next() // { value: 5, done: false }\niterator.next() // { value: undefined, done: true }</code></pre> <p><strong>\uff084\uff09\u5176\u4ed6\u573a\u5408</strong></p> <p>\u7531\u4e8e\u6570\u7ec4\u7684\u904d\u5386\u4f1a\u8c03\u7528\u904d\u5386\u5668\u63a5\u53e3\uff0c\u6240\u4ee5\u4efb\u4f55\u63a5\u53d7\u6570\u7ec4\u4f5c\u4e3a\u53c2\u6570\u7684\u573a\u5408\uff0c\u5176\u5b9e\u90fd\u8c03\u7528\u4e86\u904d\u5386\u5668\u63a5\u53e3\u3002\u4e0b\u9762\u662f\u4e00\u4e9b\u4f8b\u5b50\u3002</p> <ul> <li>for...of</li> <li>Array.from()</li> <li>Map(), Set(), WeakMap(), WeakSet()\uff08\u6bd4\u5982<code>new Map([[&#39;a&#39;,1],[&#39;b&#39;,2]])</code>\uff09</li> <li>Promise.all()</li> <li>Promise.race()</li> </ul> <h2 id="\u5b57\u7b26\u4e32\u7684-iterator-\u63a5\u53e3">\u5b57\u7b26\u4e32\u7684 Iterator \u63a5\u53e3</h2> <p>\u5b57\u7b26\u4e32\u662f\u4e00\u4e2a\u7c7b\u4f3c\u6570\u7ec4\u7684\u5bf9\u8c61\uff0c\u4e5f\u539f\u751f\u5177\u6709 Iterator \u63a5\u53e3\u3002</p> <pre><code class="language-javascript">var someString = &quot;hi&quot;;\ntypeof someString[Symbol.iterator]\n// &quot;function&quot;\n\nvar iterator = someString[Symbol.iterator]();\n\niterator.next()  // { value: &quot;h&quot;, done: false }\niterator.next()  // { value: &quot;i&quot;, done: false }\niterator.next()  // { value: undefined, done: true }</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u8c03\u7528<code>Symbol.iterator</code>\u65b9\u6cd5\u8fd4\u56de\u4e00\u4e2a\u904d\u5386\u5668\u5bf9\u8c61\uff0c\u5728\u8fd9\u4e2a\u904d\u5386\u5668\u4e0a\u53ef\u4ee5\u8c03\u7528 next \u65b9\u6cd5\uff0c\u5b9e\u73b0\u5bf9\u4e8e\u5b57\u7b26\u4e32\u7684\u904d\u5386\u3002</p> <p>\u53ef\u4ee5\u8986\u76d6\u539f\u751f\u7684<code>Symbol.iterator</code>\u65b9\u6cd5\uff0c\u8fbe\u5230\u4fee\u6539\u904d\u5386\u5668\u884c\u4e3a\u7684\u76ee\u7684\u3002</p> <pre><code class="language-javascript">var str = new String(&quot;hi&quot;);\n\n[...str] // [&quot;h&quot;, &quot;i&quot;]\n\nstr[Symbol.iterator] = function() {\n  return {\n    next: function() {\n      if (this._first) {\n        this._first = false;\n        return { value: &quot;bye&quot;, done: false };\n      } else {\n        return { done: true };\n      }\n    },\n    _first: true\n  };\n};\n\n[...str] // [&quot;bye&quot;]\nstr // &quot;hi&quot;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5b57\u7b26\u4e32 str \u7684<code>Symbol.iterator</code>\u65b9\u6cd5\u88ab\u4fee\u6539\u4e86\uff0c\u6240\u4ee5\u6269\u5c55\u8fd0\u7b97\u7b26\uff08<code>...</code>\uff09\u8fd4\u56de\u7684\u503c\u53d8\u6210\u4e86<code>bye</code>\uff0c\u800c\u5b57\u7b26\u4e32\u672c\u8eab\u8fd8\u662f<code>hi</code>\u3002</p> <h2 id="iterator-\u63a5\u53e3\u4e0e-generator-\u51fd\u6570">Iterator \u63a5\u53e3\u4e0e Generator \u51fd\u6570</h2> <p><code>Symbol.iterator</code>\u65b9\u6cd5\u7684\u6700\u7b80\u5355\u5b9e\u73b0\uff0c\u8fd8\u662f\u4f7f\u7528\u4e0b\u4e00\u7ae0\u8981\u4ecb\u7ecd\u7684 Generator \u51fd\u6570\u3002</p> <pre><code class="language-javascript">var myIterable = {};\n\nmyIterable[Symbol.iterator] = function* () {\n  yield 1;\n  yield 2;\n  yield 3;\n};\n[...myIterable] // [1, 2, 3]\n\n// \u6216\u8005\u91c7\u7528\u4e0b\u9762\u7684\u7b80\u6d01\u5199\u6cd5\n\nlet obj = {\n  * [Symbol.iterator]() {\n    yield &#39;hello&#39;;\n    yield &#39;world&#39;;\n  }\n};\n\nfor (let x of obj) {\n  console.log(x);\n}\n// hello\n// world</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>Symbol.iterator</code>\u65b9\u6cd5\u51e0\u4e4e\u4e0d\u7528\u90e8\u7f72\u4efb\u4f55\u4ee3\u7801\uff0c\u53ea\u8981\u7528 yield \u547d\u4ee4\u7ed9\u51fa\u6bcf\u4e00\u6b65\u7684\u8fd4\u56de\u503c\u5373\u53ef\u3002</p> <h2 id="\u904d\u5386\u5668\u5bf9\u8c61\u7684-return\uff0cthrow">\u904d\u5386\u5668\u5bf9\u8c61\u7684 return()\uff0cthrow()</h2> <p>\u904d\u5386\u5668\u5bf9\u8c61\u9664\u4e86\u5177\u6709<code>next</code>\u65b9\u6cd5\uff0c\u8fd8\u53ef\u4ee5\u5177\u6709<code>return</code>\u65b9\u6cd5\u548c<code>throw</code>\u65b9\u6cd5\u3002\u5982\u679c\u4f60\u81ea\u5df1\u5199\u904d\u5386\u5668\u5bf9\u8c61\u751f\u6210\u51fd\u6570\uff0c\u90a3\u4e48<code>next</code>\u65b9\u6cd5\u662f\u5fc5\u987b\u90e8\u7f72\u7684\uff0c<code>return</code>\u65b9\u6cd5\u548c<code>throw</code>\u65b9\u6cd5\u662f\u5426\u90e8\u7f72\u662f\u53ef\u9009\u7684\u3002</p> <p><code>return</code>\u65b9\u6cd5\u7684\u4f7f\u7528\u573a\u5408\u662f\uff0c\u5982\u679c<code>for...of</code>\u5faa\u73af\u63d0\u524d\u9000\u51fa\uff08\u901a\u5e38\u662f\u56e0\u4e3a\u51fa\u9519\uff0c\u6216\u8005\u6709<code>break</code>\u8bed\u53e5\u6216<code>continue</code>\u8bed\u53e5\uff09\uff0c\u5c31\u4f1a\u8c03\u7528<code>return</code>\u65b9\u6cd5\u3002\u5982\u679c\u4e00\u4e2a\u5bf9\u8c61\u5728\u5b8c\u6210\u904d\u5386\u524d\uff0c\u9700\u8981\u6e05\u7406\u6216\u91ca\u653e\u8d44\u6e90\uff0c\u5c31\u53ef\u4ee5\u90e8\u7f72<code>return</code>\u65b9\u6cd5\u3002</p> <pre><code class="language-javascript">function readLinesSync(file) {\n  return {\n    next() {\n      return { done: false };\n    },\n    return() {\n      file.close();\n      return { done: true };\n    },\n  };\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u51fd\u6570<code>readLinesSync</code>\u63a5\u53d7\u4e00\u4e2a\u6587\u4ef6\u5bf9\u8c61\u4f5c\u4e3a\u53c2\u6570\uff0c\u8fd4\u56de\u4e00\u4e2a\u904d\u5386\u5668\u5bf9\u8c61\uff0c\u5176\u4e2d\u9664\u4e86<code>next</code>\u65b9\u6cd5\uff0c\u8fd8\u90e8\u7f72\u4e86<code>return</code>\u65b9\u6cd5\u3002\u4e0b\u9762\u7684\u4e09\u79cd\u60c5\u51b5\uff0c\u90fd\u4f1a\u89e6\u53d1\u6267\u884c<code>return</code>\u65b9\u6cd5\u3002</p> <pre><code class="language-javascript">// \u60c5\u51b5\u4e00\nfor (let line of readLinesSync(fileName)) {\n  console.log(line);\n  break;\n}\n\n// \u60c5\u51b5\u4e8c\nfor (let line of readLinesSync(fileName)) {\n  console.log(line);\n  continue;\n}\n\n// \u60c5\u51b5\u4e09\nfor (let line of readLinesSync(fileName)) {\n  console.log(line);\n  throw new Error();\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u60c5\u51b5\u4e00\u8f93\u51fa\u6587\u4ef6\u7684\u7b2c\u4e00\u884c\u4ee5\u540e\uff0c\u5c31\u4f1a\u6267\u884c<code>return</code>\u65b9\u6cd5\uff0c\u5173\u95ed\u8fd9\u4e2a\u6587\u4ef6\uff1b\u60c5\u51b5\u4e8c\u8f93\u51fa\u6240\u6709\u884c\u4ee5\u540e\uff0c\u6267\u884c<code>return</code>\u65b9\u6cd5\uff0c\u5173\u95ed\u8be5\u6587\u4ef6\uff1b\u60c5\u51b5\u4e09\u4f1a\u5728\u6267\u884c<code>return</code>\u65b9\u6cd5\u5173\u95ed\u6587\u4ef6\u4e4b\u540e\uff0c\u518d\u629b\u51fa\u9519\u8bef\u3002</p> <p>\u6ce8\u610f\uff0c<code>return</code>\u65b9\u6cd5\u5fc5\u987b\u8fd4\u56de\u4e00\u4e2a\u5bf9\u8c61\uff0c\u8fd9\u662f Generator \u89c4\u683c\u51b3\u5b9a\u7684\u3002</p> <p><code>throw</code>\u65b9\u6cd5\u4e3b\u8981\u662f\u914d\u5408 Generator \u51fd\u6570\u4f7f\u7528\uff0c\u4e00\u822c\u7684\u904d\u5386\u5668\u5bf9\u8c61\u7528\u4e0d\u5230\u8fd9\u4e2a\u65b9\u6cd5\u3002\u8bf7\u53c2\u9605\u300aGenerator \u51fd\u6570\u300b\u4e00\u7ae0\u3002</p> <h2 id="forof-\u5faa\u73af">for...of \u5faa\u73af</h2> <p>ES6 \u501f\u9274 C++\u3001Java\u3001C# \u548c Python \u8bed\u8a00\uff0c\u5f15\u5165\u4e86<code>for...of</code>\u5faa\u73af\uff0c\u4f5c\u4e3a\u904d\u5386\u6240\u6709\u6570\u636e\u7ed3\u6784\u7684\u7edf\u4e00\u7684\u65b9\u6cd5\u3002</p> <p>\u4e00\u4e2a\u6570\u636e\u7ed3\u6784\u53ea\u8981\u90e8\u7f72\u4e86<code>Symbol.iterator</code>\u5c5e\u6027\uff0c\u5c31\u88ab\u89c6\u4e3a\u5177\u6709 iterator \u63a5\u53e3\uff0c\u5c31\u53ef\u4ee5\u7528<code>for...of</code>\u5faa\u73af\u904d\u5386\u5b83\u7684\u6210\u5458\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c<code>for...of</code>\u5faa\u73af\u5185\u90e8\u8c03\u7528\u7684\u662f\u6570\u636e\u7ed3\u6784\u7684<code>Symbol.iterator</code>\u65b9\u6cd5\u3002</p> <p><code>for...of</code>\u5faa\u73af\u53ef\u4ee5\u4f7f\u7528\u7684\u8303\u56f4\u5305\u62ec\u6570\u7ec4\u3001Set \u548c Map \u7ed3\u6784\u3001\u67d0\u4e9b\u7c7b\u4f3c\u6570\u7ec4\u7684\u5bf9\u8c61\uff08\u6bd4\u5982<code>arguments</code>\u5bf9\u8c61\u3001DOM NodeList \u5bf9\u8c61\uff09\u3001\u540e\u6587\u7684 Generator \u5bf9\u8c61\uff0c\u4ee5\u53ca\u5b57\u7b26\u4e32\u3002</p> <h3 id="\u6570\u7ec4">\u6570\u7ec4</h3> <p>\u6570\u7ec4\u539f\u751f\u5177\u5907<code>iterator</code>\u63a5\u53e3\uff08\u5373\u9ed8\u8ba4\u90e8\u7f72\u4e86<code>Symbol.iterator</code>\u5c5e\u6027\uff09\uff0c<code>for...of</code>\u5faa\u73af\u672c\u8d28\u4e0a\u5c31\u662f\u8c03\u7528\u8fd9\u4e2a\u63a5\u53e3\u4ea7\u751f\u7684\u904d\u5386\u5668\uff0c\u53ef\u4ee5\u7528\u4e0b\u9762\u7684\u4ee3\u7801\u8bc1\u660e\u3002</p> <pre><code class="language-javascript">const arr = [&#39;red&#39;, &#39;green&#39;, &#39;blue&#39;];\n\nfor(let v of arr) {\n  console.log(v); // red green blue\n}\n\nconst obj = {};\nobj[Symbol.iterator] = arr[Symbol.iterator].bind(arr);\n\nfor(let v of obj) {\n  console.log(v); // red green blue\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u7a7a\u5bf9\u8c61<code>obj</code>\u90e8\u7f72\u4e86\u6570\u7ec4<code>arr</code>\u7684<code>Symbol.iterator</code>\u5c5e\u6027\uff0c\u7ed3\u679c<code>obj</code>\u7684<code>for...of</code>\u5faa\u73af\uff0c\u4ea7\u751f\u4e86\u4e0e<code>arr</code>\u5b8c\u5168\u4e00\u6837\u7684\u7ed3\u679c\u3002</p> <p><code>for...of</code>\u5faa\u73af\u53ef\u4ee5\u4ee3\u66ff\u6570\u7ec4\u5b9e\u4f8b\u7684<code>forEach</code>\u65b9\u6cd5\u3002</p> <pre><code class="language-javascript">const arr = [&#39;red&#39;, &#39;green&#39;, &#39;blue&#39;];\n\narr.forEach(function (element, index) {\n  console.log(element); // red green blue\n  console.log(index);   // 0 1 2\n});</code></pre> <p>JavaScript \u539f\u6709\u7684<code>for...in</code>\u5faa\u73af\uff0c\u53ea\u80fd\u83b7\u5f97\u5bf9\u8c61\u7684\u952e\u540d\uff0c\u4e0d\u80fd\u76f4\u63a5\u83b7\u53d6\u952e\u503c\u3002ES6 \u63d0\u4f9b<code>for...of</code>\u5faa\u73af\uff0c\u5141\u8bb8\u904d\u5386\u83b7\u5f97\u952e\u503c\u3002</p> <pre><code class="language-javascript">var arr = [&#39;a&#39;, &#39;b&#39;, &#39;c&#39;, &#39;d&#39;];\n\nfor (let a in arr) {\n  console.log(a); // 0 1 2 3\n}\n\nfor (let a of arr) {\n  console.log(a); // a b c d\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u8868\u660e\uff0c<code>for...in</code>\u5faa\u73af\u8bfb\u53d6\u952e\u540d\uff0c<code>for...of</code>\u5faa\u73af\u8bfb\u53d6\u952e\u503c\u3002\u5982\u679c\u8981\u901a\u8fc7<code>for...of</code>\u5faa\u73af\uff0c\u83b7\u53d6\u6570\u7ec4\u7684\u7d22\u5f15\uff0c\u53ef\u4ee5\u501f\u52a9\u6570\u7ec4\u5b9e\u4f8b\u7684<code>entries</code>\u65b9\u6cd5\u548c<code>keys</code>\u65b9\u6cd5\uff0c\u53c2\u89c1\u300a\u6570\u7ec4\u7684\u6269\u5c55\u300b\u7ae0\u8282\u3002</p> <p><code>for...of</code>\u5faa\u73af\u8c03\u7528\u904d\u5386\u5668\u63a5\u53e3\uff0c\u6570\u7ec4\u7684\u904d\u5386\u5668\u63a5\u53e3\u53ea\u8fd4\u56de\u5177\u6709\u6570\u5b57\u7d22\u5f15\u7684\u5c5e\u6027\u3002\u8fd9\u4e00\u70b9\u8ddf<code>for...in</code>\u5faa\u73af\u4e5f\u4e0d\u4e00\u6837\u3002</p> <pre><code class="language-javascript">let arr = [3, 5, 7];\narr.foo = &#39;hello&#39;;\n\nfor (let i in arr) {\n  console.log(i); // &quot;0&quot;, &quot;1&quot;, &quot;2&quot;, &quot;foo&quot;\n}\n\nfor (let i of arr) {\n  console.log(i); //  &quot;3&quot;, &quot;5&quot;, &quot;7&quot;\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>for...of</code>\u5faa\u73af\u4e0d\u4f1a\u8fd4\u56de\u6570\u7ec4<code>arr</code>\u7684<code>foo</code>\u5c5e\u6027\u3002</p> <h3 id="set-\u548c-map-\u7ed3\u6784">Set \u548c Map \u7ed3\u6784</h3> <p>Set \u548c Map \u7ed3\u6784\u4e5f\u539f\u751f\u5177\u6709 Iterator \u63a5\u53e3\uff0c\u53ef\u4ee5\u76f4\u63a5\u4f7f\u7528<code>for...of</code>\u5faa\u73af\u3002</p> <pre><code class="language-javascript">var engines = new Set([&quot;Gecko&quot;, &quot;Trident&quot;, &quot;Webkit&quot;, &quot;Webkit&quot;]);\nfor (var e of engines) {\n  console.log(e);\n}\n// Gecko\n// Trident\n// Webkit\n\nvar es6 = new Map();\nes6.set(&quot;edition&quot;, 6);\nes6.set(&quot;committee&quot;, &quot;TC39&quot;);\nes6.set(&quot;standard&quot;, &quot;ECMA-262&quot;);\nfor (var [name, value] of es6) {\n  console.log(name + &quot;: &quot; + value);\n}\n// edition: 6\n// committee: TC39\n// standard: ECMA-262</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u6f14\u793a\u4e86\u5982\u4f55\u904d\u5386 Set \u7ed3\u6784\u548c Map \u7ed3\u6784\u3002\u503c\u5f97\u6ce8\u610f\u7684\u5730\u65b9\u6709\u4e24\u4e2a\uff0c\u9996\u5148\uff0c\u904d\u5386\u7684\u987a\u5e8f\u662f\u6309\u7167\u5404\u4e2a\u6210\u5458\u88ab\u6dfb\u52a0\u8fdb\u6570\u636e\u7ed3\u6784\u7684\u987a\u5e8f\u3002\u5176\u6b21\uff0cSet \u7ed3\u6784\u904d\u5386\u65f6\uff0c\u8fd4\u56de\u7684\u662f\u4e00\u4e2a\u503c\uff0c\u800c Map \u7ed3\u6784\u904d\u5386\u65f6\uff0c\u8fd4\u56de\u7684\u662f\u4e00\u4e2a\u6570\u7ec4\uff0c\u8be5\u6570\u7ec4\u7684\u4e24\u4e2a\u6210\u5458\u5206\u522b\u4e3a\u5f53\u524d Map \u6210\u5458\u7684\u952e\u540d\u548c\u952e\u503c\u3002</p> <pre><code class="language-javascript">let map = new Map().set(&#39;a&#39;, 1).set(&#39;b&#39;, 2);\nfor (let pair of map) {\n  console.log(pair);\n}\n// [&#39;a&#39;, 1]\n// [&#39;b&#39;, 2]\n\nfor (let [key, value] of map) {\n  console.log(key + &#39; : &#39; + value);\n}\n// a : 1\n// b : 2</code></pre> <h3 id="\u8ba1\u7b97\u751f\u6210\u7684\u6570\u636e\u7ed3\u6784">\u8ba1\u7b97\u751f\u6210\u7684\u6570\u636e\u7ed3\u6784</h3> <p>\u6709\u4e9b\u6570\u636e\u7ed3\u6784\u662f\u5728\u73b0\u6709\u6570\u636e\u7ed3\u6784\u7684\u57fa\u7840\u4e0a\uff0c\u8ba1\u7b97\u751f\u6210\u7684\u3002\u6bd4\u5982\uff0cES6 \u7684\u6570\u7ec4\u3001Set\u3001Map \u90fd\u90e8\u7f72\u4e86\u4ee5\u4e0b\u4e09\u4e2a\u65b9\u6cd5\uff0c\u8c03\u7528\u540e\u90fd\u8fd4\u56de\u904d\u5386\u5668\u5bf9\u8c61\u3002</p> <ul> <li><code>entries()</code> \u8fd4\u56de\u4e00\u4e2a\u904d\u5386\u5668\u5bf9\u8c61\uff0c\u7528\u6765\u904d\u5386<code>[\u952e\u540d, \u952e\u503c]</code>\u7ec4\u6210\u7684\u6570\u7ec4\u3002\u5bf9\u4e8e\u6570\u7ec4\uff0c\u952e\u540d\u5c31\u662f\u7d22\u5f15\u503c\uff1b\u5bf9\u4e8e Set\uff0c\u952e\u540d\u4e0e\u952e\u503c\u76f8\u540c\u3002Map \u7ed3\u6784\u7684 Iterator \u63a5\u53e3\uff0c\u9ed8\u8ba4\u5c31\u662f\u8c03\u7528<code>entries</code>\u65b9\u6cd5\u3002</li> <li><code>keys()</code> \u8fd4\u56de\u4e00\u4e2a\u904d\u5386\u5668\u5bf9\u8c61\uff0c\u7528\u6765\u904d\u5386\u6240\u6709\u7684\u952e\u540d\u3002</li> <li><code>values()</code> \u8fd4\u56de\u4e00\u4e2a\u904d\u5386\u5668\u5bf9\u8c61\uff0c\u7528\u6765\u904d\u5386\u6240\u6709\u7684\u952e\u503c\u3002</li> </ul> <p>\u8fd9\u4e09\u4e2a\u65b9\u6cd5\u8c03\u7528\u540e\u751f\u6210\u7684\u904d\u5386\u5668\u5bf9\u8c61\uff0c\u6240\u904d\u5386\u7684\u90fd\u662f\u8ba1\u7b97\u751f\u6210\u7684\u6570\u636e\u7ed3\u6784\u3002</p> <pre><code class="language-javascript">let arr = [&#39;a&#39;, &#39;b&#39;, &#39;c&#39;];\nfor (let pair of arr.entries()) {\n  console.log(pair);\n}\n// [0, &#39;a&#39;]\n// [1, &#39;b&#39;]\n// [2, &#39;c&#39;]</code></pre> <h3 id="\u7c7b\u4f3c\u6570\u7ec4\u7684\u5bf9\u8c61">\u7c7b\u4f3c\u6570\u7ec4\u7684\u5bf9\u8c61</h3> <p>\u7c7b\u4f3c\u6570\u7ec4\u7684\u5bf9\u8c61\u5305\u62ec\u597d\u51e0\u7c7b\u3002\u4e0b\u9762\u662f<code>for...of</code>\u5faa\u73af\u7528\u4e8e\u5b57\u7b26\u4e32\u3001DOM NodeList \u5bf9\u8c61\u3001<code>arguments</code>\u5bf9\u8c61\u7684\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">// \u5b57\u7b26\u4e32\nlet str = &quot;hello&quot;;\n\nfor (let s of str) {\n  console.log(s); // h e l l o\n}\n\n// DOM NodeList\u5bf9\u8c61\nlet paras = document.querySelectorAll(&quot;p&quot;);\n\nfor (let p of paras) {\n  p.classList.add(&quot;test&quot;);\n}\n\n// arguments\u5bf9\u8c61\nfunction printArgs() {\n  for (let x of arguments) {\n    console.log(x);\n  }\n}\nprintArgs(&#39;a&#39;, &#39;b&#39;);\n// &#39;a&#39;\n// &#39;b&#39;</code></pre> <p>\u5bf9\u4e8e\u5b57\u7b26\u4e32\u6765\u8bf4\uff0c<code>for...of</code>\u5faa\u73af\u8fd8\u6709\u4e00\u4e2a\u7279\u70b9\uff0c\u5c31\u662f\u4f1a\u6b63\u786e\u8bc6\u522b 32 \u4f4d UTF-16 \u5b57\u7b26\u3002</p> <pre><code class="language-javascript">for (let x of &#39;a\\uD83D\\uDC0A&#39;) {\n  console.log(x);\n}\n// &#39;a&#39;\n// &#39;\\uD83D\\uDC0A&#39;</code></pre> <p>\u5e76\u4e0d\u662f\u6240\u6709\u7c7b\u4f3c\u6570\u7ec4\u7684\u5bf9\u8c61\u90fd\u5177\u6709 Iterator \u63a5\u53e3\uff0c\u4e00\u4e2a\u7b80\u4fbf\u7684\u89e3\u51b3\u65b9\u6cd5\uff0c\u5c31\u662f\u4f7f\u7528<code>Array.from</code>\u65b9\u6cd5\u5c06\u5176\u8f6c\u4e3a\u6570\u7ec4\u3002</p> <pre><code class="language-javascript">let arrayLike = { length: 2, 0: &#39;a&#39;, 1: &#39;b&#39; };\n\n// \u62a5\u9519\nfor (let x of arrayLike) {\n  console.log(x);\n}\n\n// \u6b63\u786e\nfor (let x of Array.from(arrayLike)) {\n  console.log(x);\n}</code></pre> <h3 id="\u5bf9\u8c61">\u5bf9\u8c61</h3> <p>\u5bf9\u4e8e\u666e\u901a\u7684\u5bf9\u8c61\uff0c<code>for...of</code>\u7ed3\u6784\u4e0d\u80fd\u76f4\u63a5\u4f7f\u7528\uff0c\u4f1a\u62a5\u9519\uff0c\u5fc5\u987b\u90e8\u7f72\u4e86 Iterator \u63a5\u53e3\u540e\u624d\u80fd\u4f7f\u7528\u3002\u4f46\u662f\uff0c\u8fd9\u6837\u60c5\u51b5\u4e0b\uff0c<code>for...in</code>\u5faa\u73af\u4f9d\u7136\u53ef\u4ee5\u7528\u6765\u904d\u5386\u952e\u540d\u3002</p> <pre><code class="language-javascript">let es6 = {\n  edition: 6,\n  committee: &quot;TC39&quot;,\n  standard: &quot;ECMA-262&quot;\n};\n\nfor (let e in es6) {\n  console.log(e);\n}\n// edition\n// committee\n// standard\n\nfor (let e of es6) {\n  console.log(e);\n}\n// TypeError: es6[Symbol.iterator] is not a function</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u8868\u793a\uff0c\u5bf9\u4e8e\u666e\u901a\u7684\u5bf9\u8c61\uff0c<code>for...in</code>\u5faa\u73af\u53ef\u4ee5\u904d\u5386\u952e\u540d\uff0c<code>for...of</code>\u5faa\u73af\u4f1a\u62a5\u9519\u3002</p> <p>\u4e00\u79cd\u89e3\u51b3\u65b9\u6cd5\u662f\uff0c\u4f7f\u7528<code>Object.keys</code>\u65b9\u6cd5\u5c06\u5bf9\u8c61\u7684\u952e\u540d\u751f\u6210\u4e00\u4e2a\u6570\u7ec4\uff0c\u7136\u540e\u904d\u5386\u8fd9\u4e2a\u6570\u7ec4\u3002</p> <pre><code class="language-javascript">for (var key of Object.keys(someObject)) {\n  console.log(key + &#39;: &#39; + someObject[key]);\n}</code></pre> <p>\u53e6\u4e00\u4e2a\u65b9\u6cd5\u662f\u4f7f\u7528 Generator \u51fd\u6570\u5c06\u5bf9\u8c61\u91cd\u65b0\u5305\u88c5\u4e00\u4e0b\u3002</p> <pre><code class="language-javascript">function* entries(obj) {\n  for (let key of Object.keys(obj)) {\n    yield [key, obj[key]];\n  }\n}\n\nfor (let [key, value] of entries(obj)) {\n  console.log(key, &#39;-&gt;&#39;, value);\n}\n// a -&gt; 1\n// b -&gt; 2\n// c -&gt; 3</code></pre> <h3 id="\u4e0e\u5176\u4ed6\u904d\u5386\u8bed\u6cd5\u7684\u6bd4\u8f83">\u4e0e\u5176\u4ed6\u904d\u5386\u8bed\u6cd5\u7684\u6bd4\u8f83</h3> <p>\u4ee5\u6570\u7ec4\u4e3a\u4f8b\uff0cJavaScript \u63d0\u4f9b\u591a\u79cd\u904d\u5386\u8bed\u6cd5\u3002\u6700\u539f\u59cb\u7684\u5199\u6cd5\u5c31\u662f<code>for</code>\u5faa\u73af\u3002</p> <pre><code class="language-javascript">for (var index = 0; index &lt; myArray.length; index++) {\n  console.log(myArray[index]);\n}</code></pre> <p>\u8fd9\u79cd\u5199\u6cd5\u6bd4\u8f83\u9ebb\u70e6\uff0c\u56e0\u6b64\u6570\u7ec4\u63d0\u4f9b\u5185\u7f6e\u7684<code>forEach</code>\u65b9\u6cd5\u3002</p> <pre><code class="language-javascript">myArray.forEach(function (value) {\n  console.log(value);\n});</code></pre> <p>\u8fd9\u79cd\u5199\u6cd5\u7684\u95ee\u9898\u5728\u4e8e\uff0c\u65e0\u6cd5\u4e2d\u9014\u8df3\u51fa<code>forEach</code>\u5faa\u73af\uff0c<code>break</code>\u547d\u4ee4\u6216<code>return</code>\u547d\u4ee4\u90fd\u4e0d\u80fd\u594f\u6548\u3002</p> <p><code>for...in</code>\u5faa\u73af\u53ef\u4ee5\u904d\u5386\u6570\u7ec4\u7684\u952e\u540d\u3002</p> <pre><code class="language-javascript">for (var index in myArray) {\n  console.log(myArray[index]);\n}</code></pre> <p><code>for...in</code>\u5faa\u73af\u6709\u51e0\u4e2a\u7f3a\u70b9\u3002</p> <ul> <li>\u6570\u7ec4\u7684\u952e\u540d\u662f\u6570\u5b57\uff0c\u4f46\u662f<code>for...in</code>\u5faa\u73af\u662f\u4ee5\u5b57\u7b26\u4e32\u4f5c\u4e3a\u952e\u540d\u201c0\u201d\u3001\u201c1\u201d\u3001\u201c2\u201d\u7b49\u7b49\u3002</li> <li><code>for...in</code>\u5faa\u73af\u4e0d\u4ec5\u904d\u5386\u6570\u5b57\u952e\u540d\uff0c\u8fd8\u4f1a\u904d\u5386\u624b\u52a8\u6dfb\u52a0\u7684\u5176\u4ed6\u952e\uff0c\u751a\u81f3\u5305\u62ec\u539f\u578b\u94fe\u4e0a\u7684\u952e\u3002</li> <li>\u67d0\u4e9b\u60c5\u51b5\u4e0b\uff0c<code>for...in</code>\u5faa\u73af\u4f1a\u4ee5\u4efb\u610f\u987a\u5e8f\u904d\u5386\u952e\u540d\u3002</li> </ul> <p>\u603b\u4e4b\uff0c<code>for...in</code>\u5faa\u73af\u4e3b\u8981\u662f\u4e3a\u904d\u5386\u5bf9\u8c61\u800c\u8bbe\u8ba1\u7684\uff0c\u4e0d\u9002\u7528\u4e8e\u904d\u5386\u6570\u7ec4\u3002</p> <p><code>for...of</code>\u5faa\u73af\u76f8\u6bd4\u4e0a\u9762\u51e0\u79cd\u505a\u6cd5\uff0c\u6709\u4e00\u4e9b\u663e\u8457\u7684\u4f18\u70b9\u3002</p> <pre><code class="language-javascript">for (let value of myArray) {\n  console.log(value);\n}</code></pre> <ul> <li>\u6709\u7740\u540c<code>for...in</code>\u4e00\u6837\u7684\u7b80\u6d01\u8bed\u6cd5\uff0c\u4f46\u662f\u6ca1\u6709<code>for...in</code>\u90a3\u4e9b\u7f3a\u70b9\u3002</li> <li>\u4e0d\u540c\u4e8e<code>forEach</code>\u65b9\u6cd5\uff0c\u5b83\u53ef\u4ee5\u4e0e<code>break</code>\u3001<code>continue</code>\u548c<code>return</code>\u914d\u5408\u4f7f\u7528\u3002</li> <li>\u63d0\u4f9b\u4e86\u904d\u5386\u6240\u6709\u6570\u636e\u7ed3\u6784\u7684\u7edf\u4e00\u64cd\u4f5c\u63a5\u53e3\u3002</li> </ul> <p>\u4e0b\u9762\u662f\u4e00\u4e2a\u4f7f\u7528 break \u8bed\u53e5\uff0c\u8df3\u51fa<code>for...of</code>\u5faa\u73af\u7684\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">for (var n of fibonacci) {\n  if (n &gt; 1000)\n    break;\n  console.log(n);\n}</code></pre> <p>\u4e0a\u9762\u7684\u4f8b\u5b50\uff0c\u4f1a\u8f93\u51fa\u6590\u6ce2\u7eb3\u5951\u6570\u5217\u5c0f\u4e8e\u7b49\u4e8e 1000 \u7684\u9879\u3002\u5982\u679c\u5f53\u524d\u9879\u5927\u4e8e 1000\uff0c\u5c31\u4f1a\u4f7f\u7528<code>break</code>\u8bed\u53e5\u8df3\u51fa<code>for...of</code>\u5faa\u73af\u3002</p> ';e.exports=n}}]);