(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["docs/es6/module"],{"8ece":function(e,o){var c='<h1 id="module-\u7684\u8bed\u6cd5">Module \u7684\u8bed\u6cd5</h1> <h2 id="\u6982\u8ff0">\u6982\u8ff0</h2> <p>\u5386\u53f2\u4e0a\uff0cJavaScript \u4e00\u76f4\u6ca1\u6709\u6a21\u5757\uff08module\uff09\u4f53\u7cfb\uff0c\u65e0\u6cd5\u5c06\u4e00\u4e2a\u5927\u7a0b\u5e8f\u62c6\u5206\u6210\u4e92\u76f8\u4f9d\u8d56\u7684\u5c0f\u6587\u4ef6\uff0c\u518d\u7528\u7b80\u5355\u7684\u65b9\u6cd5\u62fc\u88c5\u8d77\u6765\u3002\u5176\u4ed6\u8bed\u8a00\u90fd\u6709\u8fd9\u9879\u529f\u80fd\uff0c\u6bd4\u5982 Ruby \u7684<code>require</code>\u3001Python \u7684<code>import</code>\uff0c\u751a\u81f3\u5c31\u8fde CSS \u90fd\u6709<code>@import</code>\uff0c\u4f46\u662f JavaScript \u4efb\u4f55\u8fd9\u65b9\u9762\u7684\u652f\u6301\u90fd\u6ca1\u6709\uff0c\u8fd9\u5bf9\u5f00\u53d1\u5927\u578b\u7684\u3001\u590d\u6742\u7684\u9879\u76ee\u5f62\u6210\u4e86\u5de8\u5927\u969c\u788d\u3002</p> <p>\u5728 ES6 \u4e4b\u524d\uff0c\u793e\u533a\u5236\u5b9a\u4e86\u4e00\u4e9b\u6a21\u5757\u52a0\u8f7d\u65b9\u6848\uff0c\u6700\u4e3b\u8981\u7684\u6709 CommonJS \u548c AMD \u4e24\u79cd\u3002\u524d\u8005\u7528\u4e8e\u670d\u52a1\u5668\uff0c\u540e\u8005\u7528\u4e8e\u6d4f\u89c8\u5668\u3002ES6 \u5728\u8bed\u8a00\u6807\u51c6\u7684\u5c42\u9762\u4e0a\uff0c\u5b9e\u73b0\u4e86\u6a21\u5757\u529f\u80fd\uff0c\u800c\u4e14\u5b9e\u73b0\u5f97\u76f8\u5f53\u7b80\u5355\uff0c\u5b8c\u5168\u53ef\u4ee5\u53d6\u4ee3 CommonJS \u548c AMD \u89c4\u8303\uff0c\u6210\u4e3a\u6d4f\u89c8\u5668\u548c\u670d\u52a1\u5668\u901a\u7528\u7684\u6a21\u5757\u89e3\u51b3\u65b9\u6848\u3002</p> <p>ES6 \u6a21\u5757\u7684\u8bbe\u8ba1\u601d\u60f3\uff0c\u662f\u5c3d\u91cf\u7684\u9759\u6001\u5316\uff0c\u4f7f\u5f97\u7f16\u8bd1\u65f6\u5c31\u80fd\u786e\u5b9a\u6a21\u5757\u7684\u4f9d\u8d56\u5173\u7cfb\uff0c\u4ee5\u53ca\u8f93\u5165\u548c\u8f93\u51fa\u7684\u53d8\u91cf\u3002CommonJS \u548c AMD \u6a21\u5757\uff0c\u90fd\u53ea\u80fd\u5728\u8fd0\u884c\u65f6\u786e\u5b9a\u8fd9\u4e9b\u4e1c\u897f\u3002\u6bd4\u5982\uff0cCommonJS \u6a21\u5757\u5c31\u662f\u5bf9\u8c61\uff0c\u8f93\u5165\u65f6\u5fc5\u987b\u67e5\u627e\u5bf9\u8c61\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">// CommonJS\u6a21\u5757\nlet { stat, exists, readFile } = require(&#39;fs&#39;);\n\n// \u7b49\u540c\u4e8e\nlet _fs = require(&#39;fs&#39;);\nlet stat = _fs.stat;\nlet exists = _fs.exists;\nlet readfile = _fs.readfile;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u7684\u5b9e\u8d28\u662f\u6574\u4f53\u52a0\u8f7d<code>fs</code>\u6a21\u5757\uff08\u5373\u52a0\u8f7d<code>fs</code>\u7684\u6240\u6709\u65b9\u6cd5\uff09\uff0c\u751f\u6210\u4e00\u4e2a\u5bf9\u8c61\uff08<code>_fs</code>\uff09\uff0c\u7136\u540e\u518d\u4ece\u8fd9\u4e2a\u5bf9\u8c61\u4e0a\u9762\u8bfb\u53d6 3 \u4e2a\u65b9\u6cd5\u3002\u8fd9\u79cd\u52a0\u8f7d\u79f0\u4e3a\u201c\u8fd0\u884c\u65f6\u52a0\u8f7d\u201d\uff0c\u56e0\u4e3a\u53ea\u6709\u8fd0\u884c\u65f6\u624d\u80fd\u5f97\u5230\u8fd9\u4e2a\u5bf9\u8c61\uff0c\u5bfc\u81f4\u5b8c\u5168\u6ca1\u529e\u6cd5\u5728\u7f16\u8bd1\u65f6\u505a\u201c\u9759\u6001\u4f18\u5316\u201d\u3002</p> <p>ES6 \u6a21\u5757\u4e0d\u662f\u5bf9\u8c61\uff0c\u800c\u662f\u901a\u8fc7<code>export</code>\u547d\u4ee4\u663e\u5f0f\u6307\u5b9a\u8f93\u51fa\u7684\u4ee3\u7801\uff0c\u518d\u901a\u8fc7<code>import</code>\u547d\u4ee4\u8f93\u5165\u3002</p> <pre><code class="language-javascript">// ES6\u6a21\u5757\nimport { stat, exists, readFile } from &#39;fs&#39;;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u7684\u5b9e\u8d28\u662f\u4ece<code>fs</code>\u6a21\u5757\u52a0\u8f7d 3 \u4e2a\u65b9\u6cd5\uff0c\u5176\u4ed6\u65b9\u6cd5\u4e0d\u52a0\u8f7d\u3002\u8fd9\u79cd\u52a0\u8f7d\u79f0\u4e3a\u201c\u7f16\u8bd1\u65f6\u52a0\u8f7d\u201d\u6216\u8005\u9759\u6001\u52a0\u8f7d\uff0c\u5373 ES6 \u53ef\u4ee5\u5728\u7f16\u8bd1\u65f6\u5c31\u5b8c\u6210\u6a21\u5757\u52a0\u8f7d\uff0c\u6548\u7387\u8981\u6bd4 CommonJS \u6a21\u5757\u7684\u52a0\u8f7d\u65b9\u5f0f\u9ad8\u3002\u5f53\u7136\uff0c\u8fd9\u4e5f\u5bfc\u81f4\u4e86\u6ca1\u6cd5\u5f15\u7528 ES6 \u6a21\u5757\u672c\u8eab\uff0c\u56e0\u4e3a\u5b83\u4e0d\u662f\u5bf9\u8c61\u3002</p> <p>\u7531\u4e8e ES6 \u6a21\u5757\u662f\u7f16\u8bd1\u65f6\u52a0\u8f7d\uff0c\u4f7f\u5f97\u9759\u6001\u5206\u6790\u6210\u4e3a\u53ef\u80fd\u3002\u6709\u4e86\u5b83\uff0c\u5c31\u80fd\u8fdb\u4e00\u6b65\u62d3\u5bbd JavaScript \u7684\u8bed\u6cd5\uff0c\u6bd4\u5982\u5f15\u5165\u5b8f\uff08macro\uff09\u548c\u7c7b\u578b\u68c0\u9a8c\uff08type system\uff09\u8fd9\u4e9b\u53ea\u80fd\u9760\u9759\u6001\u5206\u6790\u5b9e\u73b0\u7684\u529f\u80fd\u3002</p> <p>\u9664\u4e86\u9759\u6001\u52a0\u8f7d\u5e26\u6765\u7684\u5404\u79cd\u597d\u5904\uff0cES6 \u6a21\u5757\u8fd8\u6709\u4ee5\u4e0b\u597d\u5904\u3002</p> <ul> <li>\u4e0d\u518d\u9700\u8981<code>UMD</code>\u6a21\u5757\u683c\u5f0f\u4e86\uff0c\u5c06\u6765\u670d\u52a1\u5668\u548c\u6d4f\u89c8\u5668\u90fd\u4f1a\u652f\u6301 ES6 \u6a21\u5757\u683c\u5f0f\u3002\u76ee\u524d\uff0c\u901a\u8fc7\u5404\u79cd\u5de5\u5177\u5e93\uff0c\u5176\u5b9e\u5df2\u7ecf\u505a\u5230\u4e86\u8fd9\u4e00\u70b9\u3002</li> <li>\u5c06\u6765\u6d4f\u89c8\u5668\u7684\u65b0 API \u5c31\u80fd\u7528\u6a21\u5757\u683c\u5f0f\u63d0\u4f9b\uff0c\u4e0d\u518d\u5fc5\u987b\u505a\u6210\u5168\u5c40\u53d8\u91cf\u6216\u8005<code>navigator</code>\u5bf9\u8c61\u7684\u5c5e\u6027\u3002</li> <li>\u4e0d\u518d\u9700\u8981\u5bf9\u8c61\u4f5c\u4e3a\u547d\u540d\u7a7a\u95f4\uff08\u6bd4\u5982<code>Math</code>\u5bf9\u8c61\uff09\uff0c\u672a\u6765\u8fd9\u4e9b\u529f\u80fd\u53ef\u4ee5\u901a\u8fc7\u6a21\u5757\u63d0\u4f9b\u3002</li> </ul> <p>\u672c\u7ae0\u4ecb\u7ecd ES6 \u6a21\u5757\u7684\u8bed\u6cd5\uff0c\u4e0b\u4e00\u7ae0\u4ecb\u7ecd\u5982\u4f55\u5728\u6d4f\u89c8\u5668\u548c Node \u4e4b\u4e2d\uff0c\u52a0\u8f7d ES6 \u6a21\u5757\u3002</p> <h2 id="\u4e25\u683c\u6a21\u5f0f">\u4e25\u683c\u6a21\u5f0f</h2> <p>ES6 \u7684\u6a21\u5757\u81ea\u52a8\u91c7\u7528\u4e25\u683c\u6a21\u5f0f\uff0c\u4e0d\u7ba1\u4f60\u6709\u6ca1\u6709\u5728\u6a21\u5757\u5934\u90e8\u52a0\u4e0a<code>&quot;use strict&quot;;</code>\u3002</p> <p>\u4e25\u683c\u6a21\u5f0f\u4e3b\u8981\u6709\u4ee5\u4e0b\u9650\u5236\u3002</p> <ul> <li>\u53d8\u91cf\u5fc5\u987b\u58f0\u660e\u540e\u518d\u4f7f\u7528</li> <li>\u51fd\u6570\u7684\u53c2\u6570\u4e0d\u80fd\u6709\u540c\u540d\u5c5e\u6027\uff0c\u5426\u5219\u62a5\u9519</li> <li>\u4e0d\u80fd\u4f7f\u7528<code>with</code>\u8bed\u53e5</li> <li>\u4e0d\u80fd\u5bf9\u53ea\u8bfb\u5c5e\u6027\u8d4b\u503c\uff0c\u5426\u5219\u62a5\u9519</li> <li>\u4e0d\u80fd\u4f7f\u7528\u524d\u7f00 0 \u8868\u793a\u516b\u8fdb\u5236\u6570\uff0c\u5426\u5219\u62a5\u9519</li> <li>\u4e0d\u80fd\u5220\u9664\u4e0d\u53ef\u5220\u9664\u7684\u5c5e\u6027\uff0c\u5426\u5219\u62a5\u9519</li> <li>\u4e0d\u80fd\u5220\u9664\u53d8\u91cf<code>delete prop</code>\uff0c\u4f1a\u62a5\u9519\uff0c\u53ea\u80fd\u5220\u9664\u5c5e\u6027<code>delete global[prop]</code></li> <li><code>eval</code>\u4e0d\u4f1a\u5728\u5b83\u7684\u5916\u5c42\u4f5c\u7528\u57df\u5f15\u5165\u53d8\u91cf</li> <li><code>eval</code>\u548c<code>arguments</code>\u4e0d\u80fd\u88ab\u91cd\u65b0\u8d4b\u503c</li> <li><code>arguments</code>\u4e0d\u4f1a\u81ea\u52a8\u53cd\u6620\u51fd\u6570\u53c2\u6570\u7684\u53d8\u5316</li> <li>\u4e0d\u80fd\u4f7f\u7528<code>arguments.callee</code></li> <li>\u4e0d\u80fd\u4f7f\u7528<code>arguments.caller</code></li> <li>\u7981\u6b62<code>this</code>\u6307\u5411\u5168\u5c40\u5bf9\u8c61</li> <li>\u4e0d\u80fd\u4f7f\u7528<code>fn.caller</code>\u548c<code>fn.arguments</code>\u83b7\u53d6\u51fd\u6570\u8c03\u7528\u7684\u5806\u6808</li> <li>\u589e\u52a0\u4e86\u4fdd\u7559\u5b57\uff08\u6bd4\u5982<code>protected</code>\u3001<code>static</code>\u548c<code>interface</code>\uff09</li> </ul> <p>\u4e0a\u9762\u8fd9\u4e9b\u9650\u5236\uff0c\u6a21\u5757\u90fd\u5fc5\u987b\u9075\u5b88\u3002\u7531\u4e8e\u4e25\u683c\u6a21\u5f0f\u662f ES5 \u5f15\u5165\u7684\uff0c\u4e0d\u5c5e\u4e8e ES6\uff0c\u6240\u4ee5\u8bf7\u53c2\u9605\u76f8\u5173 ES5 \u4e66\u7c4d\uff0c\u672c\u4e66\u4e0d\u518d\u8be6\u7ec6\u4ecb\u7ecd\u4e86\u3002</p> <p>\u5176\u4e2d\uff0c\u5c24\u5176\u9700\u8981\u6ce8\u610f<code>this</code>\u7684\u9650\u5236\u3002ES6 \u6a21\u5757\u4e4b\u4e2d\uff0c\u9876\u5c42\u7684<code>this</code>\u6307\u5411<code>undefined</code>\uff0c\u5373\u4e0d\u5e94\u8be5\u5728\u9876\u5c42\u4ee3\u7801\u4f7f\u7528<code>this</code>\u3002</p> <h2 id="export-\u547d\u4ee4">export \u547d\u4ee4</h2> <p>\u6a21\u5757\u529f\u80fd\u4e3b\u8981\u7531\u4e24\u4e2a\u547d\u4ee4\u6784\u6210\uff1a<code>export</code>\u548c<code>import</code>\u3002<code>export</code>\u547d\u4ee4\u7528\u4e8e\u89c4\u5b9a\u6a21\u5757\u7684\u5bf9\u5916\u63a5\u53e3\uff0c<code>import</code>\u547d\u4ee4\u7528\u4e8e\u8f93\u5165\u5176\u4ed6\u6a21\u5757\u63d0\u4f9b\u7684\u529f\u80fd\u3002</p> <p>\u4e00\u4e2a\u6a21\u5757\u5c31\u662f\u4e00\u4e2a\u72ec\u7acb\u7684\u6587\u4ef6\u3002\u8be5\u6587\u4ef6\u5185\u90e8\u7684\u6240\u6709\u53d8\u91cf\uff0c\u5916\u90e8\u65e0\u6cd5\u83b7\u53d6\u3002\u5982\u679c\u4f60\u5e0c\u671b\u5916\u90e8\u80fd\u591f\u8bfb\u53d6\u6a21\u5757\u5185\u90e8\u7684\u67d0\u4e2a\u53d8\u91cf\uff0c\u5c31\u5fc5\u987b\u4f7f\u7528<code>export</code>\u5173\u952e\u5b57\u8f93\u51fa\u8be5\u53d8\u91cf\u3002\u4e0b\u9762\u662f\u4e00\u4e2a JS \u6587\u4ef6\uff0c\u91cc\u9762\u4f7f\u7528<code>export</code>\u547d\u4ee4\u8f93\u51fa\u53d8\u91cf\u3002</p> <pre><code class="language-javascript">// profile.js\nexport var firstName = &#39;Michael&#39;;\nexport var lastName = &#39;Jackson&#39;;\nexport var year = 1958;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u662f<code>profile.js</code>\u6587\u4ef6\uff0c\u4fdd\u5b58\u4e86\u7528\u6237\u4fe1\u606f\u3002ES6 \u5c06\u5176\u89c6\u4e3a\u4e00\u4e2a\u6a21\u5757\uff0c\u91cc\u9762\u7528<code>export</code>\u547d\u4ee4\u5bf9\u5916\u90e8\u8f93\u51fa\u4e86\u4e09\u4e2a\u53d8\u91cf\u3002</p> <p><code>export</code>\u7684\u5199\u6cd5\uff0c\u9664\u4e86\u50cf\u4e0a\u9762\u8fd9\u6837\uff0c\u8fd8\u6709\u53e6\u5916\u4e00\u79cd\u3002</p> <pre><code class="language-javascript">// profile.js\nvar firstName = &#39;Michael&#39;;\nvar lastName = &#39;Jackson&#39;;\nvar year = 1958;\n\nexport {firstName, lastName, year};</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u5728<code>export</code>\u547d\u4ee4\u540e\u9762\uff0c\u4f7f\u7528\u5927\u62ec\u53f7\u6307\u5b9a\u6240\u8981\u8f93\u51fa\u7684\u4e00\u7ec4\u53d8\u91cf\u3002\u5b83\u4e0e\u524d\u4e00\u79cd\u5199\u6cd5\uff08\u76f4\u63a5\u653e\u7f6e\u5728<code>var</code>\u8bed\u53e5\u524d\uff09\u662f\u7b49\u4ef7\u7684\uff0c\u4f46\u662f\u5e94\u8be5\u4f18\u5148\u8003\u8651\u4f7f\u7528\u8fd9\u79cd\u5199\u6cd5\u3002\u56e0\u4e3a\u8fd9\u6837\u5c31\u53ef\u4ee5\u5728\u811a\u672c\u5c3e\u90e8\uff0c\u4e00\u773c\u770b\u6e05\u695a\u8f93\u51fa\u4e86\u54ea\u4e9b\u53d8\u91cf\u3002</p> <p><code>export</code>\u547d\u4ee4\u9664\u4e86\u8f93\u51fa\u53d8\u91cf\uff0c\u8fd8\u53ef\u4ee5\u8f93\u51fa\u51fd\u6570\u6216\u7c7b\uff08class\uff09\u3002</p> <pre><code class="language-javascript">export function multiply(x, y) {\n  return x * y;\n};</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u5bf9\u5916\u8f93\u51fa\u4e00\u4e2a\u51fd\u6570<code>multiply</code>\u3002</p> <p>\u901a\u5e38\u60c5\u51b5\u4e0b\uff0c<code>export</code>\u8f93\u51fa\u7684\u53d8\u91cf\u5c31\u662f\u672c\u6765\u7684\u540d\u5b57\uff0c\u4f46\u662f\u53ef\u4ee5\u4f7f\u7528<code>as</code>\u5173\u952e\u5b57\u91cd\u547d\u540d\u3002</p> <pre><code class="language-javascript">function v1() { ... }\nfunction v2() { ... }\n\nexport {\n  v1 as streamV1,\n  v2 as streamV2,\n  v2 as streamLatestVersion\n};</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4f7f\u7528<code>as</code>\u5173\u952e\u5b57\uff0c\u91cd\u547d\u540d\u4e86\u51fd\u6570<code>v1</code>\u548c<code>v2</code>\u7684\u5bf9\u5916\u63a5\u53e3\u3002\u91cd\u547d\u540d\u540e\uff0c<code>v2</code>\u53ef\u4ee5\u7528\u4e0d\u540c\u7684\u540d\u5b57\u8f93\u51fa\u4e24\u6b21\u3002</p> <p>\u9700\u8981\u7279\u522b\u6ce8\u610f\u7684\u662f\uff0c<code>export</code>\u547d\u4ee4\u89c4\u5b9a\u7684\u662f\u5bf9\u5916\u7684\u63a5\u53e3\uff0c\u5fc5\u987b\u4e0e\u6a21\u5757\u5185\u90e8\u7684\u53d8\u91cf\u5efa\u7acb\u4e00\u4e00\u5bf9\u5e94\u5173\u7cfb\u3002</p> <pre><code class="language-javascript">// \u62a5\u9519\nexport 1;\n\n// \u62a5\u9519\nvar m = 1;\nexport m;</code></pre> <p>\u4e0a\u9762\u4e24\u79cd\u5199\u6cd5\u90fd\u4f1a\u62a5\u9519\uff0c\u56e0\u4e3a\u6ca1\u6709\u63d0\u4f9b\u5bf9\u5916\u7684\u63a5\u53e3\u3002\u7b2c\u4e00\u79cd\u5199\u6cd5\u76f4\u63a5\u8f93\u51fa 1\uff0c\u7b2c\u4e8c\u79cd\u5199\u6cd5\u901a\u8fc7\u53d8\u91cf<code>m</code>\uff0c\u8fd8\u662f\u76f4\u63a5\u8f93\u51fa 1\u3002<code>1</code>\u53ea\u662f\u4e00\u4e2a\u503c\uff0c\u4e0d\u662f\u63a5\u53e3\u3002\u6b63\u786e\u7684\u5199\u6cd5\u662f\u4e0b\u9762\u8fd9\u6837\u3002</p> <pre><code class="language-javascript">// \u5199\u6cd5\u4e00\nexport var m = 1;\n\n// \u5199\u6cd5\u4e8c\nvar m = 1;\nexport {m};\n\n// \u5199\u6cd5\u4e09\nvar n = 1;\nexport {n as m};</code></pre> <p>\u4e0a\u9762\u4e09\u79cd\u5199\u6cd5\u90fd\u662f\u6b63\u786e\u7684\uff0c\u89c4\u5b9a\u4e86\u5bf9\u5916\u7684\u63a5\u53e3<code>m</code>\u3002\u5176\u4ed6\u811a\u672c\u53ef\u4ee5\u901a\u8fc7\u8fd9\u4e2a\u63a5\u53e3\uff0c\u53d6\u5230\u503c<code>1</code>\u3002\u5b83\u4eec\u7684\u5b9e\u8d28\u662f\uff0c\u5728\u63a5\u53e3\u540d\u4e0e\u6a21\u5757\u5185\u90e8\u53d8\u91cf\u4e4b\u95f4\uff0c\u5efa\u7acb\u4e86\u4e00\u4e00\u5bf9\u5e94\u7684\u5173\u7cfb\u3002</p> <p>\u540c\u6837\u7684\uff0c<code>function</code>\u548c<code>class</code>\u7684\u8f93\u51fa\uff0c\u4e5f\u5fc5\u987b\u9075\u5b88\u8fd9\u6837\u7684\u5199\u6cd5\u3002</p> <pre><code class="language-javascript">// \u62a5\u9519\nfunction f() {}\nexport f;\n\n// \u6b63\u786e\nexport function f() {};\n\n// \u6b63\u786e\nfunction f() {}\nexport {f};</code></pre> <p>\u53e6\u5916\uff0c<code>export</code>\u8bed\u53e5\u8f93\u51fa\u7684\u63a5\u53e3\uff0c\u4e0e\u5176\u5bf9\u5e94\u7684\u503c\u662f\u52a8\u6001\u7ed1\u5b9a\u5173\u7cfb\uff0c\u5373\u901a\u8fc7\u8be5\u63a5\u53e3\uff0c\u53ef\u4ee5\u53d6\u5230\u6a21\u5757\u5185\u90e8\u5b9e\u65f6\u7684\u503c\u3002</p> <pre><code class="language-javascript">export var foo = &#39;bar&#39;;\nsetTimeout(() =&gt; foo = &#39;baz&#39;, 500);</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u8f93\u51fa\u53d8\u91cf<code>foo</code>\uff0c\u503c\u4e3a<code>bar</code>\uff0c500 \u6beb\u79d2\u4e4b\u540e\u53d8\u6210<code>baz</code>\u3002</p> <p>\u8fd9\u4e00\u70b9\u4e0e CommonJS \u89c4\u8303\u5b8c\u5168\u4e0d\u540c\u3002CommonJS \u6a21\u5757\u8f93\u51fa\u7684\u662f\u503c\u7684\u7f13\u5b58\uff0c\u4e0d\u5b58\u5728\u52a8\u6001\u66f4\u65b0\uff0c\u8be6\u89c1\u4e0b\u6587\u300aModule \u7684\u52a0\u8f7d\u5b9e\u73b0\u300b\u4e00\u8282\u3002</p> <p>\u6700\u540e\uff0c<code>export</code>\u547d\u4ee4\u53ef\u4ee5\u51fa\u73b0\u5728\u6a21\u5757\u7684\u4efb\u4f55\u4f4d\u7f6e\uff0c\u53ea\u8981\u5904\u4e8e\u6a21\u5757\u9876\u5c42\u5c31\u53ef\u4ee5\u3002\u5982\u679c\u5904\u4e8e\u5757\u7ea7\u4f5c\u7528\u57df\u5185\uff0c\u5c31\u4f1a\u62a5\u9519\uff0c\u4e0b\u4e00\u8282\u7684<code>import</code>\u547d\u4ee4\u4e5f\u662f\u5982\u6b64\u3002\u8fd9\u662f\u56e0\u4e3a\u5904\u4e8e\u6761\u4ef6\u4ee3\u7801\u5757\u4e4b\u4e2d\uff0c\u5c31\u6ca1\u6cd5\u505a\u9759\u6001\u4f18\u5316\u4e86\uff0c\u8fdd\u80cc\u4e86 ES6 \u6a21\u5757\u7684\u8bbe\u8ba1\u521d\u8877\u3002</p> <pre><code class="language-javascript">function foo() {\n  export default &#39;bar&#39; // SyntaxError\n}\nfoo()</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>export</code>\u8bed\u53e5\u653e\u5728\u51fd\u6570\u4e4b\u4e2d\uff0c\u7ed3\u679c\u62a5\u9519\u3002</p> <h2 id="import-\u547d\u4ee4">import \u547d\u4ee4</h2> <p>\u4f7f\u7528<code>export</code>\u547d\u4ee4\u5b9a\u4e49\u4e86\u6a21\u5757\u7684\u5bf9\u5916\u63a5\u53e3\u4ee5\u540e\uff0c\u5176\u4ed6 JS \u6587\u4ef6\u5c31\u53ef\u4ee5\u901a\u8fc7<code>import</code>\u547d\u4ee4\u52a0\u8f7d\u8fd9\u4e2a\u6a21\u5757\u3002</p> <pre><code class="language-javascript">// main.js\nimport {firstName, lastName, year} from &#39;./profile&#39;;\n\nfunction setName(element) {\n  element.textContent = firstName + &#39; &#39; + lastName;\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u7684<code>import</code>\u547d\u4ee4\uff0c\u7528\u4e8e\u52a0\u8f7d<code>profile.js</code>\u6587\u4ef6\uff0c\u5e76\u4ece\u4e2d\u8f93\u5165\u53d8\u91cf\u3002<code>import</code>\u547d\u4ee4\u63a5\u53d7\u4e00\u5bf9\u5927\u62ec\u53f7\uff0c\u91cc\u9762\u6307\u5b9a\u8981\u4ece\u5176\u4ed6\u6a21\u5757\u5bfc\u5165\u7684\u53d8\u91cf\u540d\u3002\u5927\u62ec\u53f7\u91cc\u9762\u7684\u53d8\u91cf\u540d\uff0c\u5fc5\u987b\u4e0e\u88ab\u5bfc\u5165\u6a21\u5757\uff08<code>profile.js</code>\uff09\u5bf9\u5916\u63a5\u53e3\u7684\u540d\u79f0\u76f8\u540c\u3002</p> <p>\u5982\u679c\u60f3\u4e3a\u8f93\u5165\u7684\u53d8\u91cf\u91cd\u65b0\u53d6\u4e00\u4e2a\u540d\u5b57\uff0c<code>import</code>\u547d\u4ee4\u8981\u4f7f\u7528<code>as</code>\u5173\u952e\u5b57\uff0c\u5c06\u8f93\u5165\u7684\u53d8\u91cf\u91cd\u547d\u540d\u3002</p> <pre><code class="language-javascript">import { lastName as surname } from &#39;./profile&#39;;</code></pre> <p><code>import</code>\u540e\u9762\u7684<code>from</code>\u6307\u5b9a\u6a21\u5757\u6587\u4ef6\u7684\u4f4d\u7f6e\uff0c\u53ef\u4ee5\u662f\u76f8\u5bf9\u8def\u5f84\uff0c\u4e5f\u53ef\u4ee5\u662f\u7edd\u5bf9\u8def\u5f84\uff0c<code>.js</code>\u540e\u7f00\u53ef\u4ee5\u7701\u7565\u3002\u5982\u679c\u53ea\u662f\u6a21\u5757\u540d\uff0c\u4e0d\u5e26\u6709\u8def\u5f84\uff0c\u90a3\u4e48\u5fc5\u987b\u6709\u914d\u7f6e\u6587\u4ef6\uff0c\u544a\u8bc9 JavaScript \u5f15\u64ce\u8be5\u6a21\u5757\u7684\u4f4d\u7f6e\u3002</p> <pre><code class="language-javascript">import {myMethod} from &#39;util&#39;;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>util</code>\u662f\u6a21\u5757\u6587\u4ef6\u540d\uff0c\u7531\u4e8e\u4e0d\u5e26\u6709\u8def\u5f84\uff0c\u5fc5\u987b\u901a\u8fc7\u914d\u7f6e\uff0c\u544a\u8bc9\u5f15\u64ce\u600e\u4e48\u53d6\u5230\u8fd9\u4e2a\u6a21\u5757\u3002</p> <p>\u6ce8\u610f\uff0c<code>import</code>\u547d\u4ee4\u5177\u6709\u63d0\u5347\u6548\u679c\uff0c\u4f1a\u63d0\u5347\u5230\u6574\u4e2a\u6a21\u5757\u7684\u5934\u90e8\uff0c\u9996\u5148\u6267\u884c\u3002</p> <pre><code class="language-javascript">foo();\n\nimport { foo } from &#39;my_module&#39;;</code></pre> <p>\u4e0a\u9762\u7684\u4ee3\u7801\u4e0d\u4f1a\u62a5\u9519\uff0c\u56e0\u4e3a<code>import</code>\u7684\u6267\u884c\u65e9\u4e8e<code>foo</code>\u7684\u8c03\u7528\u3002\u8fd9\u79cd\u884c\u4e3a\u7684\u672c\u8d28\u662f\uff0c<code>import</code>\u547d\u4ee4\u662f\u7f16\u8bd1\u9636\u6bb5\u6267\u884c\u7684\uff0c\u5728\u4ee3\u7801\u8fd0\u884c\u4e4b\u524d\u3002</p> <p>\u7531\u4e8e<code>import</code>\u662f\u9759\u6001\u6267\u884c\uff0c\u6240\u4ee5\u4e0d\u80fd\u4f7f\u7528\u8868\u8fbe\u5f0f\u548c\u53d8\u91cf\uff0c\u8fd9\u4e9b\u53ea\u6709\u5728\u8fd0\u884c\u65f6\u624d\u80fd\u5f97\u5230\u7ed3\u679c\u7684\u8bed\u6cd5\u7ed3\u6784\u3002</p> <pre><code class="language-javascript">// \u62a5\u9519\nimport { &#39;f&#39; + &#39;oo&#39; } from &#39;my_module&#39;;\n\n// \u62a5\u9519\nlet module = &#39;my_module&#39;;\nimport { foo } from module;\n\n// \u62a5\u9519\nif (x === 1) {\n  import { foo } from &#39;module1&#39;;\n} else {\n  import { foo } from &#39;module2&#39;;\n}</code></pre> <p>\u4e0a\u9762\u4e09\u79cd\u5199\u6cd5\u90fd\u4f1a\u62a5\u9519\uff0c\u56e0\u4e3a\u5b83\u4eec\u7528\u5230\u4e86\u8868\u8fbe\u5f0f\u3001\u53d8\u91cf\u548c<code>if</code>\u7ed3\u6784\u3002\u5728\u9759\u6001\u5206\u6790\u9636\u6bb5\uff0c\u8fd9\u4e9b\u8bed\u6cd5\u90fd\u662f\u6ca1\u6cd5\u5f97\u5230\u503c\u7684\u3002</p> <p>\u6700\u540e\uff0c<code>import</code>\u8bed\u53e5\u4f1a\u6267\u884c\u6240\u52a0\u8f7d\u7684\u6a21\u5757\uff0c\u56e0\u6b64\u53ef\u4ee5\u6709\u4e0b\u9762\u7684\u5199\u6cd5\u3002</p> <pre><code class="language-javascript">import &#39;lodash&#39;;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4ec5\u4ec5\u6267\u884c<code>lodash</code>\u6a21\u5757\uff0c\u4f46\u662f\u4e0d\u8f93\u5165\u4efb\u4f55\u503c\u3002</p> <p>\u5982\u679c\u591a\u6b21\u91cd\u590d\u6267\u884c\u540c\u4e00\u53e5<code>import</code>\u8bed\u53e5\uff0c\u90a3\u4e48\u53ea\u4f1a\u6267\u884c\u4e00\u6b21\uff0c\u800c\u4e0d\u4f1a\u6267\u884c\u591a\u6b21\u3002</p> <pre><code class="language-javascript">import &#39;lodash&#39;;\nimport &#39;lodash&#39;;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u52a0\u8f7d\u4e86\u4e24\u6b21<code>lodash</code>\uff0c\u4f46\u662f\u53ea\u4f1a\u6267\u884c\u4e00\u6b21\u3002</p> <pre><code class="language-javascript">import { foo } from &#39;my_module&#39;;\nimport { bar } from &#39;my_module&#39;;\n\n// \u7b49\u540c\u4e8e\nimport { foo, bar } from &#39;my_module&#39;;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u867d\u7136<code>foo</code>\u548c<code>bar</code>\u5728\u4e24\u4e2a\u8bed\u53e5\u4e2d\u52a0\u8f7d\uff0c\u4f46\u662f\u5b83\u4eec\u5bf9\u5e94\u7684\u662f\u540c\u4e00\u4e2a<code>my_module</code>\u5b9e\u4f8b\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c<code>import</code>\u8bed\u53e5\u662f Singleton \u6a21\u5f0f\u3002</p> <p>\u76ee\u524d\u9636\u6bb5\uff0c\u901a\u8fc7 Babel \u8f6c\u7801\uff0cCommonJS \u6a21\u5757\u7684<code>require</code>\u547d\u4ee4\u548c ES6 \u6a21\u5757\u7684<code>import</code>\u547d\u4ee4\uff0c\u53ef\u4ee5\u5199\u5728\u540c\u4e00\u4e2a\u6a21\u5757\u91cc\u9762\uff0c\u4f46\u662f\u6700\u597d\u4e0d\u8981\u8fd9\u6837\u505a\u3002\u56e0\u4e3a<code>import</code>\u5728\u9759\u6001\u89e3\u6790\u9636\u6bb5\u6267\u884c\uff0c\u6240\u4ee5\u5b83\u662f\u4e00\u4e2a\u6a21\u5757\u4e4b\u4e2d\u6700\u65e9\u6267\u884c\u7684\u3002\u4e0b\u9762\u7684\u4ee3\u7801\u53ef\u80fd\u4e0d\u4f1a\u5f97\u5230\u9884\u671f\u7ed3\u679c\u3002</p> <pre><code class="language-javascript">require(&#39;core-js/modules/es6.symbol&#39;);\nrequire(&#39;core-js/modules/es6.promise&#39;);\nimport React from &#39;React&#39;;</code></pre> <h2 id="\u6a21\u5757\u7684\u6574\u4f53\u52a0\u8f7d">\u6a21\u5757\u7684\u6574\u4f53\u52a0\u8f7d</h2> <p>\u9664\u4e86\u6307\u5b9a\u52a0\u8f7d\u67d0\u4e2a\u8f93\u51fa\u503c\uff0c\u8fd8\u53ef\u4ee5\u4f7f\u7528\u6574\u4f53\u52a0\u8f7d\uff0c\u5373\u7528\u661f\u53f7\uff08<code>*</code>\uff09\u6307\u5b9a\u4e00\u4e2a\u5bf9\u8c61\uff0c\u6240\u6709\u8f93\u51fa\u503c\u90fd\u52a0\u8f7d\u5728\u8fd9\u4e2a\u5bf9\u8c61\u4e0a\u9762\u3002</p> <p>\u4e0b\u9762\u662f\u4e00\u4e2a<code>circle.js</code>\u6587\u4ef6\uff0c\u5b83\u8f93\u51fa\u4e24\u4e2a\u65b9\u6cd5<code>area</code>\u548c<code>circumference</code>\u3002</p> <pre><code class="language-javascript">// circle.js\n\nexport function area(radius) {\n  return Math.PI * radius * radius;\n}\n\nexport function circumference(radius) {\n  return 2 * Math.PI * radius;\n}</code></pre> <p>\u73b0\u5728\uff0c\u52a0\u8f7d\u8fd9\u4e2a\u6a21\u5757\u3002</p> <pre><code class="language-javascript">// main.js\n\nimport { area, circumference } from &#39;./circle&#39;;\n\nconsole.log(&#39;\u5706\u9762\u79ef\uff1a&#39; + area(4));\nconsole.log(&#39;\u5706\u5468\u957f\uff1a&#39; + circumference(14));</code></pre> <p>\u4e0a\u9762\u5199\u6cd5\u662f\u9010\u4e00\u6307\u5b9a\u8981\u52a0\u8f7d\u7684\u65b9\u6cd5\uff0c\u6574\u4f53\u52a0\u8f7d\u7684\u5199\u6cd5\u5982\u4e0b\u3002</p> <pre><code class="language-javascript">import * as circle from &#39;./circle&#39;;\n\nconsole.log(&#39;\u5706\u9762\u79ef\uff1a&#39; + circle.area(4));\nconsole.log(&#39;\u5706\u5468\u957f\uff1a&#39; + circle.circumference(14));</code></pre> <p>\u6ce8\u610f\uff0c\u6a21\u5757\u6574\u4f53\u52a0\u8f7d\u6240\u5728\u7684\u90a3\u4e2a\u5bf9\u8c61\uff08\u4e0a\u4f8b\u662f<code>circle</code>\uff09\uff0c\u5e94\u8be5\u662f\u53ef\u4ee5\u9759\u6001\u5206\u6790\u7684\uff0c\u6240\u4ee5\u4e0d\u5141\u8bb8\u8fd0\u884c\u65f6\u6539\u53d8\u3002\u4e0b\u9762\u7684\u5199\u6cd5\u90fd\u662f\u4e0d\u5141\u8bb8\u7684\u3002</p> <pre><code class="language-javascript">import * as circle from &#39;./circle&#39;;\n\n// \u4e0b\u9762\u4e24\u884c\u90fd\u662f\u4e0d\u5141\u8bb8\u7684\ncircle.foo = &#39;hello&#39;;\ncircle.area = function () {};</code></pre> <h2 id="export-default-\u547d\u4ee4">export default \u547d\u4ee4</h2> <p>\u4ece\u524d\u9762\u7684\u4f8b\u5b50\u53ef\u4ee5\u770b\u51fa\uff0c\u4f7f\u7528<code>import</code>\u547d\u4ee4\u7684\u65f6\u5019\uff0c\u7528\u6237\u9700\u8981\u77e5\u9053\u6240\u8981\u52a0\u8f7d\u7684\u53d8\u91cf\u540d\u6216\u51fd\u6570\u540d\uff0c\u5426\u5219\u65e0\u6cd5\u52a0\u8f7d\u3002\u4f46\u662f\uff0c\u7528\u6237\u80af\u5b9a\u5e0c\u671b\u5feb\u901f\u4e0a\u624b\uff0c\u672a\u5fc5\u613f\u610f\u9605\u8bfb\u6587\u6863\uff0c\u53bb\u4e86\u89e3\u6a21\u5757\u6709\u54ea\u4e9b\u5c5e\u6027\u548c\u65b9\u6cd5\u3002</p> <p>\u4e3a\u4e86\u7ed9\u7528\u6237\u63d0\u4f9b\u65b9\u4fbf\uff0c\u8ba9\u4ed6\u4eec\u4e0d\u7528\u9605\u8bfb\u6587\u6863\u5c31\u80fd\u52a0\u8f7d\u6a21\u5757\uff0c\u5c31\u8981\u7528\u5230<code>export default</code>\u547d\u4ee4\uff0c\u4e3a\u6a21\u5757\u6307\u5b9a\u9ed8\u8ba4\u8f93\u51fa\u3002</p> <pre><code class="language-javascript">// export-default.js\nexport default function () {\n  console.log(&#39;foo&#39;);\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u662f\u4e00\u4e2a\u6a21\u5757\u6587\u4ef6<code>export-default.js</code>\uff0c\u5b83\u7684\u9ed8\u8ba4\u8f93\u51fa\u662f\u4e00\u4e2a\u51fd\u6570\u3002</p> <p>\u5176\u4ed6\u6a21\u5757\u52a0\u8f7d\u8be5\u6a21\u5757\u65f6\uff0c<code>import</code>\u547d\u4ee4\u53ef\u4ee5\u4e3a\u8be5\u533f\u540d\u51fd\u6570\u6307\u5b9a\u4efb\u610f\u540d\u5b57\u3002</p> <pre><code class="language-javascript">// import-default.js\nimport customName from &#39;./export-default&#39;;\ncustomName(); // &#39;foo&#39;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u7684<code>import</code>\u547d\u4ee4\uff0c\u53ef\u4ee5\u7528\u4efb\u610f\u540d\u79f0\u6307\u5411<code>export-default.js</code>\u8f93\u51fa\u7684\u65b9\u6cd5\uff0c\u8fd9\u65f6\u5c31\u4e0d\u9700\u8981\u77e5\u9053\u539f\u6a21\u5757\u8f93\u51fa\u7684\u51fd\u6570\u540d\u3002\u9700\u8981\u6ce8\u610f\u7684\u662f\uff0c\u8fd9\u65f6<code>import</code>\u547d\u4ee4\u540e\u9762\uff0c\u4e0d\u4f7f\u7528\u5927\u62ec\u53f7\u3002</p> <p><code>export default</code>\u547d\u4ee4\u7528\u5728\u975e\u533f\u540d\u51fd\u6570\u524d\uff0c\u4e5f\u662f\u53ef\u4ee5\u7684\u3002</p> <pre><code class="language-javascript">// export-default.js\nexport default function foo() {\n  console.log(&#39;foo&#39;);\n}\n\n// \u6216\u8005\u5199\u6210\n\nfunction foo() {\n  console.log(&#39;foo&#39;);\n}\n\nexport default foo;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>foo</code>\u51fd\u6570\u7684\u51fd\u6570\u540d<code>foo</code>\uff0c\u5728\u6a21\u5757\u5916\u90e8\u662f\u65e0\u6548\u7684\u3002\u52a0\u8f7d\u7684\u65f6\u5019\uff0c\u89c6\u540c\u533f\u540d\u51fd\u6570\u52a0\u8f7d\u3002</p> <p>\u4e0b\u9762\u6bd4\u8f83\u4e00\u4e0b\u9ed8\u8ba4\u8f93\u51fa\u548c\u6b63\u5e38\u8f93\u51fa\u3002</p> <pre><code class="language-javascript">// \u7b2c\u4e00\u7ec4\nexport default function crc32() { // \u8f93\u51fa\n  // ...\n}\n\nimport crc32 from &#39;crc32&#39;; // \u8f93\u5165\n\n// \u7b2c\u4e8c\u7ec4\nexport function crc32() { // \u8f93\u51fa\n  // ...\n};\n\nimport {crc32} from &#39;crc32&#39;; // \u8f93\u5165</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u7684\u4e24\u7ec4\u5199\u6cd5\uff0c\u7b2c\u4e00\u7ec4\u662f\u4f7f\u7528<code>export default</code>\u65f6\uff0c\u5bf9\u5e94\u7684<code>import</code>\u8bed\u53e5\u4e0d\u9700\u8981\u4f7f\u7528\u5927\u62ec\u53f7\uff1b\u7b2c\u4e8c\u7ec4\u662f\u4e0d\u4f7f\u7528<code>export default</code>\u65f6\uff0c\u5bf9\u5e94\u7684<code>import</code>\u8bed\u53e5\u9700\u8981\u4f7f\u7528\u5927\u62ec\u53f7\u3002</p> <p><code>export default</code>\u547d\u4ee4\u7528\u4e8e\u6307\u5b9a\u6a21\u5757\u7684\u9ed8\u8ba4\u8f93\u51fa\u3002\u663e\u7136\uff0c\u4e00\u4e2a\u6a21\u5757\u53ea\u80fd\u6709\u4e00\u4e2a\u9ed8\u8ba4\u8f93\u51fa\uff0c\u56e0\u6b64<code>export default</code>\u547d\u4ee4\u53ea\u80fd\u4f7f\u7528\u4e00\u6b21\u3002\u6240\u4ee5\uff0c<code>import</code>\u547d\u4ee4\u540e\u9762\u624d\u4e0d\u7528\u52a0\u5927\u62ec\u53f7\uff0c\u56e0\u4e3a\u53ea\u53ef\u80fd\u5bf9\u5e94\u4e00\u4e2a\u65b9\u6cd5\u3002</p> <p>\u672c\u8d28\u4e0a\uff0c<code>export default</code>\u5c31\u662f\u8f93\u51fa\u4e00\u4e2a\u53eb\u505a<code>default</code>\u7684\u53d8\u91cf\u6216\u65b9\u6cd5\uff0c\u7136\u540e\u7cfb\u7edf\u5141\u8bb8\u4f60\u4e3a\u5b83\u53d6\u4efb\u610f\u540d\u5b57\u3002\u6240\u4ee5\uff0c\u4e0b\u9762\u7684\u5199\u6cd5\u662f\u6709\u6548\u7684\u3002</p> <pre><code class="language-javascript">// modules.js\nfunction add(x, y) {\n  return x * y;\n}\nexport {add as default};\n// \u7b49\u540c\u4e8e\n// export default add;\n\n// app.js\nimport { default as foo } from &#39;modules&#39;;\n// \u7b49\u540c\u4e8e\n// import foo from &#39;modules&#39;;</code></pre> <p>\u6b63\u662f\u56e0\u4e3a<code>export default</code>\u547d\u4ee4\u5176\u5b9e\u53ea\u662f\u8f93\u51fa\u4e00\u4e2a\u53eb\u505a<code>default</code>\u7684\u53d8\u91cf\uff0c\u6240\u4ee5\u5b83\u540e\u9762\u4e0d\u80fd\u8ddf\u53d8\u91cf\u58f0\u660e\u8bed\u53e5\u3002</p> <pre><code class="language-javascript">// \u6b63\u786e\nexport var a = 1;\n\n// \u6b63\u786e\nvar a = 1;\nexport default a;\n\n// \u9519\u8bef\nexport default var a = 1;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>export default a</code>\u7684\u542b\u4e49\u662f\u5c06\u53d8\u91cf<code>a</code>\u7684\u503c\u8d4b\u7ed9\u53d8\u91cf<code>default</code>\u3002\u6240\u4ee5\uff0c\u6700\u540e\u4e00\u79cd\u5199\u6cd5\u4f1a\u62a5\u9519\u3002</p> <p>\u540c\u6837\u5730\uff0c\u56e0\u4e3a<code>export default</code>\u672c\u8d28\u662f\u5c06\u8be5\u547d\u4ee4\u540e\u9762\u7684\u503c\uff0c\u8d4b\u7ed9<code>default</code>\u53d8\u91cf\u4ee5\u540e\u518d\u9ed8\u8ba4\uff0c\u6240\u4ee5\u76f4\u63a5\u5c06\u4e00\u4e2a\u503c\u5199\u5728<code>export default</code>\u4e4b\u540e\u3002</p> <pre><code class="language-javascript">// \u6b63\u786e\nexport default 42;\n\n// \u62a5\u9519\nexport 42;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u540e\u4e00\u53e5\u62a5\u9519\u662f\u56e0\u4e3a\u6ca1\u6709\u6307\u5b9a\u5bf9\u5916\u7684\u63a5\u53e3\uff0c\u800c\u524d\u4e00\u53e5\u6307\u5b9a\u5916\u5bf9\u63a5\u53e3\u4e3a<code>default</code>\u3002</p> <p>\u6709\u4e86<code>export default</code>\u547d\u4ee4\uff0c\u8f93\u5165\u6a21\u5757\u65f6\u5c31\u975e\u5e38\u76f4\u89c2\u4e86\uff0c\u4ee5\u8f93\u5165 lodash \u6a21\u5757\u4e3a\u4f8b\u3002</p> <pre><code class="language-javascript">import _ from &#39;lodash&#39;;</code></pre> <p>\u5982\u679c\u60f3\u5728\u4e00\u6761<code>import</code>\u8bed\u53e5\u4e2d\uff0c\u540c\u65f6\u8f93\u5165\u9ed8\u8ba4\u65b9\u6cd5\u548c\u5176\u4ed6\u63a5\u53e3\uff0c\u53ef\u4ee5\u5199\u6210\u4e0b\u9762\u8fd9\u6837\u3002</p> <pre><code class="language-javascript">import _, { each, each as forEach } from &#39;lodash&#39;;</code></pre> <p>\u5bf9\u5e94\u4e0a\u9762\u4ee3\u7801\u7684<code>export</code>\u8bed\u53e5\u5982\u4e0b\u3002</p> <pre><code class="language-javascript">export default function (obj) {\n  // \xb7\xb7\xb7\n}\n\nexport function each(obj, iterator, context) {\n  // \xb7\xb7\xb7\n}\n\nexport { each as forEach };</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u7684\u6700\u540e\u4e00\u884c\u7684\u610f\u601d\u662f\uff0c\u66b4\u9732\u51fa<code>forEach</code>\u63a5\u53e3\uff0c\u9ed8\u8ba4\u6307\u5411<code>each</code>\u63a5\u53e3\uff0c\u5373<code>forEach</code>\u548c<code>each</code>\u6307\u5411\u540c\u4e00\u4e2a\u65b9\u6cd5\u3002</p> <p><code>export default</code>\u4e5f\u53ef\u4ee5\u7528\u6765\u8f93\u51fa\u7c7b\u3002</p> <pre><code class="language-javascript">// MyClass.js\nexport default class { ... }\n\n// main.js\nimport MyClass from &#39;MyClass&#39;;\nlet o = new MyClass();</code></pre> <h2 id="export-\u4e0e-import-\u7684\u590d\u5408\u5199\u6cd5">export \u4e0e import \u7684\u590d\u5408\u5199\u6cd5</h2> <p>\u5982\u679c\u5728\u4e00\u4e2a\u6a21\u5757\u4e4b\u4e2d\uff0c\u5148\u8f93\u5165\u540e\u8f93\u51fa\u540c\u4e00\u4e2a\u6a21\u5757\uff0c<code>import</code>\u8bed\u53e5\u53ef\u4ee5\u4e0e<code>export</code>\u8bed\u53e5\u5199\u5728\u4e00\u8d77\u3002</p> <pre><code class="language-javascript">export { foo, bar } from &#39;my_module&#39;;\n\n// \u7b49\u540c\u4e8e\nimport { foo, bar } from &#39;my_module&#39;;\nexport { foo, bar };</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>export</code>\u548c<code>import</code>\u8bed\u53e5\u53ef\u4ee5\u7ed3\u5408\u5728\u4e00\u8d77\uff0c\u5199\u6210\u4e00\u884c\u3002</p> <p>\u6a21\u5757\u7684\u63a5\u53e3\u6539\u540d\u548c\u6574\u4f53\u8f93\u51fa\uff0c\u4e5f\u53ef\u4ee5\u91c7\u7528\u8fd9\u79cd\u5199\u6cd5\u3002</p> <pre><code class="language-javascript">// \u63a5\u53e3\u6539\u540d\nexport { foo as myFoo } from &#39;my_module&#39;;\n\n// \u6574\u4f53\u8f93\u51fa\nexport * from &#39;my_module&#39;;</code></pre> <p>\u9ed8\u8ba4\u63a5\u53e3\u7684\u5199\u6cd5\u5982\u4e0b\u3002</p> <pre><code class="language-javascript">export { default } from &#39;foo&#39;;</code></pre> <p>\u5177\u540d\u63a5\u53e3\u6539\u4e3a\u9ed8\u8ba4\u63a5\u53e3\u7684\u5199\u6cd5\u5982\u4e0b\u3002</p> <pre><code class="language-javascript">export { es6 as default } from &#39;./someModule&#39;;\n\n// \u7b49\u540c\u4e8e\nimport { es6 } from &#39;./someModule&#39;;\nexport default es6;</code></pre> <p>\u540c\u6837\u5730\uff0c\u9ed8\u8ba4\u63a5\u53e3\u4e5f\u53ef\u4ee5\u6539\u540d\u4e3a\u5177\u540d\u63a5\u53e3\u3002</p> <pre><code class="language-javascript">export { default as es6 } from &#39;./someModule&#39;;</code></pre> <p>\u4e0b\u9762\u4e09\u79cd<code>import</code>\u8bed\u53e5\uff0c\u6ca1\u6709\u5bf9\u5e94\u7684\u590d\u5408\u5199\u6cd5\u3002</p> <pre><code class="language-javascript">import * as someIdentifier from &quot;someModule&quot;;\nimport someIdentifier from &quot;someModule&quot;;\nimport someIdentifier, { namedIdentifier } from &quot;someModule&quot;;</code></pre> <p>\u4e3a\u4e86\u505a\u5230\u5f62\u5f0f\u7684\u5bf9\u79f0\uff0c\u73b0\u5728\u6709<a href="https://github.com/leebyron/ecmascript-export-default-from">\u63d0\u6848</a>\uff0c\u63d0\u51fa\u8865\u4e0a\u8fd9\u4e09\u79cd\u590d\u5408\u5199\u6cd5\u3002</p> <pre><code class="language-javascript">export * as someIdentifier from &quot;someModule&quot;;\nexport someIdentifier from &quot;someModule&quot;;\nexport someIdentifier, { namedIdentifier } from &quot;someModule&quot;;</code></pre> <h2 id="\u6a21\u5757\u7684\u7ee7\u627f">\u6a21\u5757\u7684\u7ee7\u627f</h2> <p>\u6a21\u5757\u4e4b\u95f4\u4e5f\u53ef\u4ee5\u7ee7\u627f\u3002</p> <p>\u5047\u8bbe\u6709\u4e00\u4e2a<code>circleplus</code>\u6a21\u5757\uff0c\u7ee7\u627f\u4e86<code>circle</code>\u6a21\u5757\u3002</p> <pre><code class="language-javascript">// circleplus.js\n\nexport * from &#39;circle&#39;;\nexport var e = 2.71828182846;\nexport default function(x) {\n  return Math.exp(x);\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\u7684<code>export *</code>\uff0c\u8868\u793a\u518d\u8f93\u51fa<code>circle</code>\u6a21\u5757\u7684\u6240\u6709\u5c5e\u6027\u548c\u65b9\u6cd5\u3002\u6ce8\u610f\uff0c<code>export *</code>\u547d\u4ee4\u4f1a\u5ffd\u7565<code>circle</code>\u6a21\u5757\u7684<code>default</code>\u65b9\u6cd5\u3002\u7136\u540e\uff0c\u4e0a\u9762\u4ee3\u7801\u53c8\u8f93\u51fa\u4e86\u81ea\u5b9a\u4e49\u7684<code>e</code>\u53d8\u91cf\u548c\u9ed8\u8ba4\u65b9\u6cd5\u3002</p> <p>\u8fd9\u65f6\uff0c\u4e5f\u53ef\u4ee5\u5c06<code>circle</code>\u7684\u5c5e\u6027\u6216\u65b9\u6cd5\uff0c\u6539\u540d\u540e\u518d\u8f93\u51fa\u3002</p> <pre><code class="language-javascript">// circleplus.js\n\nexport { area as circleArea } from &#39;circle&#39;;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u8868\u793a\uff0c\u53ea\u8f93\u51fa<code>circle</code>\u6a21\u5757\u7684<code>area</code>\u65b9\u6cd5\uff0c\u4e14\u5c06\u5176\u6539\u540d\u4e3a<code>circleArea</code>\u3002</p> <p>\u52a0\u8f7d\u4e0a\u9762\u6a21\u5757\u7684\u5199\u6cd5\u5982\u4e0b\u3002</p> <pre><code class="language-javascript">// main.js\n\nimport * as math from &#39;circleplus&#39;;\nimport exp from &#39;circleplus&#39;;\nconsole.log(exp(math.e));</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\u7684<code>import exp</code>\u8868\u793a\uff0c\u5c06<code>circleplus</code>\u6a21\u5757\u7684\u9ed8\u8ba4\u65b9\u6cd5\u52a0\u8f7d\u4e3a<code>exp</code>\u65b9\u6cd5\u3002</p> <h2 id="\u8de8\u6a21\u5757\u5e38\u91cf">\u8de8\u6a21\u5757\u5e38\u91cf</h2> <p>\u672c\u4e66\u4ecb\u7ecd<code>const</code>\u547d\u4ee4\u7684\u65f6\u5019\u8bf4\u8fc7\uff0c<code>const</code>\u58f0\u660e\u7684\u5e38\u91cf\u53ea\u5728\u5f53\u524d\u4ee3\u7801\u5757\u6709\u6548\u3002\u5982\u679c\u60f3\u8bbe\u7f6e\u8de8\u6a21\u5757\u7684\u5e38\u91cf\uff08\u5373\u8de8\u591a\u4e2a\u6587\u4ef6\uff09\uff0c\u6216\u8005\u8bf4\u4e00\u4e2a\u503c\u8981\u88ab\u591a\u4e2a\u6a21\u5757\u5171\u4eab\uff0c\u53ef\u4ee5\u91c7\u7528\u4e0b\u9762\u7684\u5199\u6cd5\u3002</p> <pre><code class="language-javascript">// constants.js \u6a21\u5757\nexport const A = 1;\nexport const B = 3;\nexport const C = 4;\n\n// test1.js \u6a21\u5757\nimport * as constants from &#39;./constants&#39;;\nconsole.log(constants.A); // 1\nconsole.log(constants.B); // 3\n\n// test2.js \u6a21\u5757\nimport {A, B} from &#39;./constants&#39;;\nconsole.log(A); // 1\nconsole.log(B); // 3</code></pre> <p>\u5982\u679c\u8981\u4f7f\u7528\u7684\u5e38\u91cf\u975e\u5e38\u591a\uff0c\u53ef\u4ee5\u5efa\u4e00\u4e2a\u4e13\u95e8\u7684<code>constants</code>\u76ee\u5f55\uff0c\u5c06\u5404\u79cd\u5e38\u91cf\u5199\u5728\u4e0d\u540c\u7684\u6587\u4ef6\u91cc\u9762\uff0c\u4fdd\u5b58\u5728\u8be5\u76ee\u5f55\u4e0b\u3002</p> <pre><code class="language-javascript">// constants/db.js\nexport const db = {\n  url: &#39;http://my.couchdbserver.local:5984&#39;,\n  admin_username: &#39;admin&#39;,\n  admin_password: &#39;admin password&#39;\n};\n\n// constants/user.js\nexport const users = [&#39;root&#39;, &#39;admin&#39;, &#39;staff&#39;, &#39;ceo&#39;, &#39;chief&#39;, &#39;moderator&#39;];</code></pre> <p>\u7136\u540e\uff0c\u5c06\u8fd9\u4e9b\u6587\u4ef6\u8f93\u51fa\u7684\u5e38\u91cf\uff0c\u5408\u5e76\u5728<code>index.js</code>\u91cc\u9762\u3002</p> <pre><code class="language-javascript">// constants/index.js\nexport {db} from &#39;./db&#39;;\nexport {users} from &#39;./users&#39;;</code></pre> <p>\u4f7f\u7528\u7684\u65f6\u5019\uff0c\u76f4\u63a5\u52a0\u8f7d<code>index.js</code>\u5c31\u53ef\u4ee5\u4e86\u3002</p> <pre><code class="language-javascript">// script.js\nimport {db, users} from &#39;./index&#39;;</code></pre> <h2 id="import">import()</h2> <h3 id="\u7b80\u4ecb">\u7b80\u4ecb</h3> <p>\u524d\u9762\u4ecb\u7ecd\u8fc7\uff0c<code>import</code>\u547d\u4ee4\u4f1a\u88ab JavaScript \u5f15\u64ce\u9759\u6001\u5206\u6790\uff0c\u5148\u4e8e\u6a21\u5757\u5185\u7684\u5176\u4ed6\u6a21\u5757\u6267\u884c\uff08\u53eb\u505a\u201d\u8fde\u63a5\u201c\u66f4\u5408\u9002\uff09\u3002\u6240\u4ee5\uff0c\u4e0b\u9762\u7684\u4ee3\u7801\u4f1a\u62a5\u9519\u3002</p> <pre><code class="language-javascript">// \u62a5\u9519\nif (x === 2) {\n  import MyModual from &#39;./myModual&#39;;\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5f15\u64ce\u5904\u7406<code>import</code>\u8bed\u53e5\u662f\u5728\u7f16\u8bd1\u65f6\uff0c\u8fd9\u65f6\u4e0d\u4f1a\u53bb\u5206\u6790\u6216\u6267\u884c<code>if</code>\u8bed\u53e5\uff0c\u6240\u4ee5<code>import</code>\u8bed\u53e5\u653e\u5728<code>if</code>\u4ee3\u7801\u5757\u4e4b\u4e2d\u6beb\u65e0\u610f\u4e49\uff0c\u56e0\u6b64\u4f1a\u62a5\u53e5\u6cd5\u9519\u8bef\uff0c\u800c\u4e0d\u662f\u6267\u884c\u65f6\u9519\u8bef\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c<code>import</code>\u548c<code>export</code>\u547d\u4ee4\u53ea\u80fd\u5728\u6a21\u5757\u7684\u9876\u5c42\uff0c\u4e0d\u80fd\u5728\u4ee3\u7801\u5757\u4e4b\u4e2d\uff08\u6bd4\u5982\uff0c\u5728<code>if</code>\u4ee3\u7801\u5757\u4e4b\u4e2d\uff0c\u6216\u5728\u51fd\u6570\u4e4b\u4e2d\uff09\u3002</p> <p>\u8fd9\u6837\u7684\u8bbe\u8ba1\uff0c\u56fa\u7136\u6709\u5229\u4e8e\u7f16\u8bd1\u5668\u63d0\u9ad8\u6548\u7387\uff0c\u4f46\u4e5f\u5bfc\u81f4\u65e0\u6cd5\u5728\u8fd0\u884c\u65f6\u52a0\u8f7d\u6a21\u5757\u3002\u5728\u8bed\u6cd5\u4e0a\uff0c\u6761\u4ef6\u52a0\u8f7d\u5c31\u4e0d\u53ef\u80fd\u5b9e\u73b0\u3002\u5982\u679c<code>import</code>\u547d\u4ee4\u8981\u53d6\u4ee3 Node \u7684<code>require</code>\u65b9\u6cd5\uff0c\u8fd9\u5c31\u5f62\u6210\u4e86\u4e00\u4e2a\u969c\u788d\u3002\u56e0\u4e3a<code>require</code>\u662f\u8fd0\u884c\u65f6\u52a0\u8f7d\u6a21\u5757\uff0c<code>import</code>\u547d\u4ee4\u65e0\u6cd5\u53d6\u4ee3<code>require</code>\u7684\u52a8\u6001\u52a0\u8f7d\u529f\u80fd\u3002</p> <pre><code class="language-javascript">const path = &#39;./&#39; + fileName;\nconst myModual = require(path);</code></pre> <p>\u4e0a\u9762\u7684\u8bed\u53e5\u5c31\u662f\u52a8\u6001\u52a0\u8f7d\uff0c<code>require</code>\u5230\u5e95\u52a0\u8f7d\u54ea\u4e00\u4e2a\u6a21\u5757\uff0c\u53ea\u6709\u8fd0\u884c\u65f6\u624d\u77e5\u9053\u3002<code>import</code>\u8bed\u53e5\u505a\u4e0d\u5230\u8fd9\u4e00\u70b9\u3002</p> <p>\u56e0\u6b64\uff0c\u6709\u4e00\u4e2a<a href="https://github.com/tc39/proposal-dynamic-import">\u63d0\u6848</a>\uff0c\u5efa\u8bae\u5f15\u5165<code>import()</code>\u51fd\u6570\uff0c\u5b8c\u6210\u52a8\u6001\u52a0\u8f7d\u3002</p> <pre><code class="language-javascript">import(specifier)</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>import</code>\u51fd\u6570\u7684\u53c2\u6570<code>specifier</code>\uff0c\u6307\u5b9a\u6240\u8981\u52a0\u8f7d\u7684\u6a21\u5757\u7684\u4f4d\u7f6e\u3002<code>import</code>\u547d\u4ee4\u80fd\u591f\u63a5\u53d7\u4ec0\u4e48\u53c2\u6570\uff0c<code>import()</code>\u51fd\u6570\u5c31\u80fd\u63a5\u53d7\u4ec0\u4e48\u53c2\u6570\uff0c\u4e24\u8005\u533a\u522b\u4e3b\u8981\u662f\u540e\u8005\u4e3a\u52a8\u6001\u52a0\u8f7d\u3002</p> <p><code>import()</code>\u8fd4\u56de\u4e00\u4e2a Promise \u5bf9\u8c61\u3002\u4e0b\u9762\u662f\u4e00\u4e2a\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">const main = document.querySelector(&#39;main&#39;);\n\nimport(`./section-modules/${someVariable}.js`)\n  .then(module =&gt; {\n    module.loadPageInto(main);\n  })\n  .catch(err =&gt; {\n    main.textContent = err.message;\n  });</code></pre> <p><code>import()</code>\u51fd\u6570\u53ef\u4ee5\u7528\u5728\u4efb\u4f55\u5730\u65b9\uff0c\u4e0d\u4ec5\u4ec5\u662f\u6a21\u5757\uff0c\u975e\u6a21\u5757\u7684\u811a\u672c\u4e5f\u53ef\u4ee5\u4f7f\u7528\u3002\u5b83\u662f\u8fd0\u884c\u65f6\u6267\u884c\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u4ec0\u4e48\u65f6\u5019\u8fd0\u884c\u5230\u8fd9\u4e00\u53e5\uff0c\u4e5f\u4f1a\u52a0\u8f7d\u6307\u5b9a\u7684\u6a21\u5757\u3002\u53e6\u5916\uff0c<code>import()</code>\u51fd\u6570\u4e0e\u6240\u52a0\u8f7d\u7684\u6a21\u5757\u6ca1\u6709\u9759\u6001\u8fde\u63a5\u5173\u7cfb\uff0c\u8fd9\u70b9\u4e5f\u662f\u4e0e<code>import</code>\u8bed\u53e5\u4e0d\u76f8\u540c\u3002</p> <p><code>import()</code>\u7c7b\u4f3c\u4e8e Node \u7684<code>require</code>\u65b9\u6cd5\uff0c\u533a\u522b\u4e3b\u8981\u662f\u524d\u8005\u662f\u5f02\u6b65\u52a0\u8f7d\uff0c\u540e\u8005\u662f\u540c\u6b65\u52a0\u8f7d\u3002</p> <h3 id="\u9002\u7528\u573a\u5408">\u9002\u7528\u573a\u5408</h3> <p>\u4e0b\u9762\u662f<code>import()</code>\u7684\u4e00\u4e9b\u9002\u7528\u573a\u5408\u3002</p> <p>\uff081\uff09\u6309\u9700\u52a0\u8f7d\u3002</p> <p><code>import()</code>\u53ef\u4ee5\u5728\u9700\u8981\u7684\u65f6\u5019\uff0c\u518d\u52a0\u8f7d\u67d0\u4e2a\u6a21\u5757\u3002</p> <pre><code class="language-javascript">button.addEventListener(&#39;click&#39;, event =&gt; {\n  import(&#39;./dialogBox.js&#39;)\n  .then(dialogBox =&gt; {\n    dialogBox.open();\n  })\n  .catch(error =&gt; {\n    /* Error handling */\n  })\n});</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>import()</code>\u65b9\u6cd5\u653e\u5728<code>click</code>\u4e8b\u4ef6\u7684\u76d1\u542c\u51fd\u6570\u4e4b\u4e2d\uff0c\u53ea\u6709\u7528\u6237\u70b9\u51fb\u4e86\u6309\u94ae\uff0c\u624d\u4f1a\u52a0\u8f7d\u8fd9\u4e2a\u6a21\u5757\u3002</p> <p>\uff082\uff09\u6761\u4ef6\u52a0\u8f7d</p> <p><code>import()</code>\u53ef\u4ee5\u653e\u5728<code>if</code>\u4ee3\u7801\u5757\uff0c\u6839\u636e\u4e0d\u540c\u7684\u60c5\u51b5\uff0c\u52a0\u8f7d\u4e0d\u540c\u7684\u6a21\u5757\u3002</p> <pre><code class="language-javascript">if (condition) {\n  import(&#39;moduleA&#39;).then(...);\n} else {\n  import(&#39;moduleB&#39;).then(...);\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5982\u679c\u6ee1\u8db3\u6761\u4ef6\uff0c\u5c31\u52a0\u8f7d\u6a21\u5757 A\uff0c\u5426\u5219\u52a0\u8f7d\u6a21\u5757 B\u3002</p> <p>\uff083\uff09\u52a8\u6001\u7684\u6a21\u5757\u8def\u5f84</p> <p><code>import()</code>\u5141\u8bb8\u6a21\u5757\u8def\u5f84\u52a8\u6001\u751f\u6210\u3002</p> <pre><code class="language-javascript">import(f())\n.then(...);</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u6839\u636e\u51fd\u6570<code>f</code>\u7684\u8fd4\u56de\u7ed3\u679c\uff0c\u52a0\u8f7d\u4e0d\u540c\u7684\u6a21\u5757\u3002</p> <h3 id="\u6ce8\u610f\u70b9">\u6ce8\u610f\u70b9</h3> <p><code>import()</code>\u52a0\u8f7d\u6a21\u5757\u6210\u529f\u4ee5\u540e\uff0c\u8fd9\u4e2a\u6a21\u5757\u4f1a\u4f5c\u4e3a\u4e00\u4e2a\u5bf9\u8c61\uff0c\u5f53\u4f5c<code>then</code>\u65b9\u6cd5\u7684\u53c2\u6570\u3002\u56e0\u6b64\uff0c\u53ef\u4ee5\u4f7f\u7528\u5bf9\u8c61\u89e3\u6784\u8d4b\u503c\u7684\u8bed\u6cd5\uff0c\u83b7\u53d6\u8f93\u51fa\u63a5\u53e3\u3002</p> <pre><code class="language-javascript">import(&#39;./myModule.js&#39;)\n.then(({export1, export2}) =&gt; {\n  // ...\xb7\n});</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>export1</code>\u548c<code>export2</code>\u90fd\u662f<code>myModule.js</code>\u7684\u8f93\u51fa\u63a5\u53e3\uff0c\u53ef\u4ee5\u89e3\u6784\u83b7\u5f97\u3002</p> <p>\u5982\u679c\u6a21\u5757\u6709<code>default</code>\u8f93\u51fa\u63a5\u53e3\uff0c\u53ef\u4ee5\u7528\u53c2\u6570\u76f4\u63a5\u83b7\u5f97\u3002</p> <pre><code class="language-javascript">import(&#39;./myModule.js&#39;)\n.then(myModule =&gt; {\n  console.log(myModule.default);\n});</code></pre> <p>\u4e0a\u9762\u7684\u4ee3\u7801\u4e5f\u53ef\u4ee5\u4f7f\u7528\u5177\u540d\u8f93\u5165\u7684\u5f62\u5f0f\u3002</p> <pre><code class="language-javascript">import(&#39;./myModule.js&#39;)\n.then(({default: theDefault}) =&gt; {\n  console.log(theDefault);\n});</code></pre> <p>\u5982\u679c\u60f3\u540c\u65f6\u52a0\u8f7d\u591a\u4e2a\u6a21\u5757\uff0c\u53ef\u4ee5\u91c7\u7528\u4e0b\u9762\u7684\u5199\u6cd5\u3002</p> <pre><code class="language-javascript">Promise.all([\n  import(&#39;./module1.js&#39;),\n  import(&#39;./module2.js&#39;),\n  import(&#39;./module3.js&#39;),\n])\n.then(([module1, module2, module3]) =&gt; {\n   \xb7\xb7\xb7\n});</code></pre> <p><code>import()</code>\u4e5f\u53ef\u4ee5\u7528\u5728 async \u51fd\u6570\u4e4b\u4e2d\u3002</p> <pre><code class="language-javascript">async function main() {\n  const myModule = await import(&#39;./myModule.js&#39;);\n  const {export1, export2} = await import(&#39;./myModule.js&#39;);\n  const [module1, module2, module3] =\n    await Promise.all([\n      import(&#39;./module1.js&#39;),\n      import(&#39;./module2.js&#39;),\n      import(&#39;./module3.js&#39;),\n    ]);\n}\nmain();</code></pre> ';e.exports=c}}]);