(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["docs/es6/object"],{CIo7:function(e,o){var c='<h1 id="\u5bf9\u8c61\u7684\u6269\u5c55">\u5bf9\u8c61\u7684\u6269\u5c55</h1> <h2 id="\u5c5e\u6027\u7684\u7b80\u6d01\u8868\u793a\u6cd5">\u5c5e\u6027\u7684\u7b80\u6d01\u8868\u793a\u6cd5</h2> <p>ES6 \u5141\u8bb8\u76f4\u63a5\u5199\u5165\u53d8\u91cf\u548c\u51fd\u6570\uff0c\u4f5c\u4e3a\u5bf9\u8c61\u7684\u5c5e\u6027\u548c\u65b9\u6cd5\u3002\u8fd9\u6837\u7684\u4e66\u5199\u66f4\u52a0\u7b80\u6d01\u3002</p> <pre><code class="language-javascript">const foo = &#39;bar&#39;;\nconst baz = {foo};\nbaz // {foo: &quot;bar&quot;}\n\n// \u7b49\u540c\u4e8e\nconst baz = {foo: foo};</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u8868\u660e\uff0cES6 \u5141\u8bb8\u5728\u5bf9\u8c61\u4e4b\u4e2d\uff0c\u76f4\u63a5\u5199\u53d8\u91cf\u3002\u8fd9\u65f6\uff0c\u5c5e\u6027\u540d\u4e3a\u53d8\u91cf\u540d, \u5c5e\u6027\u503c\u4e3a\u53d8\u91cf\u7684\u503c\u3002\u4e0b\u9762\u662f\u53e6\u4e00\u4e2a\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">function f(x, y) {\n  return {x, y};\n}\n\n// \u7b49\u540c\u4e8e\n\nfunction f(x, y) {\n  return {x: x, y: y};\n}\n\nf(1, 2) // Object {x: 1, y: 2}</code></pre> <p>\u9664\u4e86\u5c5e\u6027\u7b80\u5199\uff0c\u65b9\u6cd5\u4e5f\u53ef\u4ee5\u7b80\u5199\u3002</p> <pre><code class="language-javascript">const o = {\n  method() {\n    return &quot;Hello!&quot;;\n  }\n};\n\n// \u7b49\u540c\u4e8e\n\nconst o = {\n  method: function() {\n    return &quot;Hello!&quot;;\n  }\n};</code></pre> <p>\u4e0b\u9762\u662f\u4e00\u4e2a\u5b9e\u9645\u7684\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">let birth = &#39;2000/01/01&#39;;\n\nconst Person = {\n\n  name: &#39;\u5f20\u4e09&#39;,\n\n  //\u7b49\u540c\u4e8ebirth: birth\n  birth,\n\n  // \u7b49\u540c\u4e8ehello: function ()...\n  hello() { console.log(&#39;\u6211\u7684\u540d\u5b57\u662f&#39;, this.name); }\n\n};</code></pre> <p>\u8fd9\u79cd\u5199\u6cd5\u7528\u4e8e\u51fd\u6570\u7684\u8fd4\u56de\u503c\uff0c\u5c06\u4f1a\u975e\u5e38\u65b9\u4fbf\u3002</p> <pre><code class="language-javascript">function getPoint() {\n  const x = 1;\n  const y = 10;\n  return {x, y};\n}\n\ngetPoint()\n// {x:1, y:10}</code></pre> <p>CommonJS \u6a21\u5757\u8f93\u51fa\u4e00\u7ec4\u53d8\u91cf\uff0c\u5c31\u975e\u5e38\u5408\u9002\u4f7f\u7528\u7b80\u6d01\u5199\u6cd5\u3002</p> <pre><code class="language-javascript">let ms = {};\n\nfunction getItem (key) {\n  return key in ms ? ms[key] : null;\n}\n\nfunction setItem (key, value) {\n  ms[key] = value;\n}\n\nfunction clear () {\n  ms = {};\n}\n\nmodule.exports = { getItem, setItem, clear };\n// \u7b49\u540c\u4e8e\nmodule.exports = {\n  getItem: getItem,\n  setItem: setItem,\n  clear: clear\n};</code></pre> <p>\u5c5e\u6027\u7684\u8d4b\u503c\u5668\uff08setter\uff09\u548c\u53d6\u503c\u5668\uff08getter\uff09\uff0c\u4e8b\u5b9e\u4e0a\u4e5f\u662f\u91c7\u7528\u8fd9\u79cd\u5199\u6cd5\u3002</p> <pre><code class="language-javascript">const cart = {\n  _wheels: 4,\n\n  get wheels () {\n    return this._wheels;\n  },\n\n  set wheels (value) {\n    if (value &lt; this._wheels) {\n      throw new Error(&#39;\u6570\u503c\u592a\u5c0f\u4e86\uff01&#39;);\n    }\n    this._wheels = value;\n  }\n}</code></pre> <p>\u6ce8\u610f\uff0c\u7b80\u6d01\u5199\u6cd5\u7684\u5c5e\u6027\u540d\u603b\u662f\u5b57\u7b26\u4e32\uff0c\u8fd9\u4f1a\u5bfc\u81f4\u4e00\u4e9b\u770b\u4e0a\u53bb\u6bd4\u8f83\u5947\u602a\u7684\u7ed3\u679c\u3002</p> <pre><code class="language-javascript">const obj = {\n  class () {}\n};\n\n// \u7b49\u540c\u4e8e\n\nvar obj = {\n  &#39;class&#39;: function() {}\n};</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>class</code>\u662f\u5b57\u7b26\u4e32\uff0c\u6240\u4ee5\u4e0d\u4f1a\u56e0\u4e3a\u5b83\u5c5e\u4e8e\u5173\u952e\u5b57\uff0c\u800c\u5bfc\u81f4\u8bed\u6cd5\u89e3\u6790\u62a5\u9519\u3002</p> <p>\u5982\u679c\u67d0\u4e2a\u65b9\u6cd5\u7684\u503c\u662f\u4e00\u4e2a Generator \u51fd\u6570\uff0c\u524d\u9762\u9700\u8981\u52a0\u4e0a\u661f\u53f7\u3002</p> <pre><code class="language-javascript">const obj = {\n  * m() {\n    yield &#39;hello world&#39;;\n  }\n};</code></pre> <h2 id="\u5c5e\u6027\u540d\u8868\u8fbe\u5f0f">\u5c5e\u6027\u540d\u8868\u8fbe\u5f0f</h2> <p>JavaScript \u5b9a\u4e49\u5bf9\u8c61\u7684\u5c5e\u6027\uff0c\u6709\u4e24\u79cd\u65b9\u6cd5\u3002</p> <pre><code class="language-javascript">// \u65b9\u6cd5\u4e00\nobj.foo = true;\n\n// \u65b9\u6cd5\u4e8c\nobj[&#39;a&#39; + &#39;bc&#39;] = 123;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u7684\u65b9\u6cd5\u4e00\u662f\u76f4\u63a5\u7528\u6807\u8bc6\u7b26\u4f5c\u4e3a\u5c5e\u6027\u540d\uff0c\u65b9\u6cd5\u4e8c\u662f\u7528\u8868\u8fbe\u5f0f\u4f5c\u4e3a\u5c5e\u6027\u540d\uff0c\u8fd9\u65f6\u8981\u5c06\u8868\u8fbe\u5f0f\u653e\u5728\u65b9\u62ec\u53f7\u4e4b\u5185\u3002</p> <p>\u4f46\u662f\uff0c\u5982\u679c\u4f7f\u7528\u5b57\u9762\u91cf\u65b9\u5f0f\u5b9a\u4e49\u5bf9\u8c61\uff08\u4f7f\u7528\u5927\u62ec\u53f7\uff09\uff0c\u5728 ES5 \u4e2d\u53ea\u80fd\u4f7f\u7528\u65b9\u6cd5\u4e00\uff08\u6807\u8bc6\u7b26\uff09\u5b9a\u4e49\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">var obj = {\n  foo: true,\n  abc: 123\n};</code></pre> <p>ES6 \u5141\u8bb8\u5b57\u9762\u91cf\u5b9a\u4e49\u5bf9\u8c61\u65f6\uff0c\u7528\u65b9\u6cd5\u4e8c\uff08\u8868\u8fbe\u5f0f\uff09\u4f5c\u4e3a\u5bf9\u8c61\u7684\u5c5e\u6027\u540d\uff0c\u5373\u628a\u8868\u8fbe\u5f0f\u653e\u5728\u65b9\u62ec\u53f7\u5185\u3002</p> <pre><code class="language-javascript">let propKey = &#39;foo&#39;;\n\nlet obj = {\n  [propKey]: true,\n  [&#39;a&#39; + &#39;bc&#39;]: 123\n};</code></pre> <p>\u4e0b\u9762\u662f\u53e6\u4e00\u4e2a\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">let lastWord = &#39;last word&#39;;\n\nconst a = {\n  &#39;first word&#39;: &#39;hello&#39;,\n  [lastWord]: &#39;world&#39;\n};\n\na[&#39;first word&#39;] // &quot;hello&quot;\na[lastWord] // &quot;world&quot;\na[&#39;last word&#39;] // &quot;world&quot;</code></pre> <p>\u8868\u8fbe\u5f0f\u8fd8\u53ef\u4ee5\u7528\u4e8e\u5b9a\u4e49\u65b9\u6cd5\u540d\u3002</p> <pre><code class="language-javascript">let obj = {\n  [&#39;h&#39; + &#39;ello&#39;]() {\n    return &#39;hi&#39;;\n  }\n};\n\nobj.hello() // hi</code></pre> <p>\u6ce8\u610f\uff0c\u5c5e\u6027\u540d\u8868\u8fbe\u5f0f\u4e0e\u7b80\u6d01\u8868\u793a\u6cd5\uff0c\u4e0d\u80fd\u540c\u65f6\u4f7f\u7528\uff0c\u4f1a\u62a5\u9519\u3002</p> <pre><code class="language-javascript">// \u62a5\u9519\nconst foo = &#39;bar&#39;;\nconst bar = &#39;abc&#39;;\nconst baz = { [foo] };\n\n// \u6b63\u786e\nconst foo = &#39;bar&#39;;\nconst baz = { [foo]: &#39;abc&#39;};</code></pre> <p>\u6ce8\u610f\uff0c\u5c5e\u6027\u540d\u8868\u8fbe\u5f0f\u5982\u679c\u662f\u4e00\u4e2a\u5bf9\u8c61\uff0c\u9ed8\u8ba4\u60c5\u51b5\u4e0b\u4f1a\u81ea\u52a8\u5c06\u5bf9\u8c61\u8f6c\u4e3a\u5b57\u7b26\u4e32<code>[object Object]</code>\uff0c\u8fd9\u4e00\u70b9\u8981\u7279\u522b\u5c0f\u5fc3\u3002</p> <pre><code class="language-javascript">const keyA = {a: 1};\nconst keyB = {b: 2};\n\nconst myObject = {\n  [keyA]: &#39;valueA&#39;,\n  [keyB]: &#39;valueB&#39;\n};\n\nmyObject // Object {[object Object]: &quot;valueB&quot;}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>[keyA]</code>\u548c<code>[keyB]</code>\u5f97\u5230\u7684\u90fd\u662f<code>[object Object]</code>\uff0c\u6240\u4ee5<code>[keyB]</code>\u4f1a\u628a<code>[keyA]</code>\u8986\u76d6\u6389\uff0c\u800c<code>myObject</code>\u6700\u540e\u53ea\u6709\u4e00\u4e2a<code>[object Object]</code>\u5c5e\u6027\u3002</p> <h2 id="\u65b9\u6cd5\u7684-name-\u5c5e\u6027">\u65b9\u6cd5\u7684 name \u5c5e\u6027</h2> <p>\u51fd\u6570\u7684<code>name</code>\u5c5e\u6027\uff0c\u8fd4\u56de\u51fd\u6570\u540d\u3002\u5bf9\u8c61\u65b9\u6cd5\u4e5f\u662f\u51fd\u6570\uff0c\u56e0\u6b64\u4e5f\u6709<code>name</code>\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">const person = {\n  sayName() {\n    console.log(&#39;hello!&#39;);\n  },\n};\n\nperson.sayName.name   // &quot;sayName&quot;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u65b9\u6cd5\u7684<code>name</code>\u5c5e\u6027\u8fd4\u56de\u51fd\u6570\u540d\uff08\u5373\u65b9\u6cd5\u540d\uff09\u3002</p> <p>\u5982\u679c\u5bf9\u8c61\u7684\u65b9\u6cd5\u4f7f\u7528\u4e86\u53d6\u503c\u51fd\u6570\uff08<code>getter</code>\uff09\u548c\u5b58\u503c\u51fd\u6570\uff08<code>setter</code>\uff09\uff0c\u5219<code>name</code>\u5c5e\u6027\u4e0d\u662f\u5728\u8be5\u65b9\u6cd5\u4e0a\u9762\uff0c\u800c\u662f\u8be5\u65b9\u6cd5\u7684\u5c5e\u6027\u7684\u63cf\u8ff0\u5bf9\u8c61\u7684<code>get</code>\u548c<code>set</code>\u5c5e\u6027\u4e0a\u9762\uff0c\u8fd4\u56de\u503c\u662f\u65b9\u6cd5\u540d\u524d\u52a0\u4e0a<code>get</code>\u548c<code>set</code>\u3002</p> <pre><code class="language-javascript">const obj = {\n  get foo() {},\n  set foo(x) {}\n};\n\nobj.foo.name\n// TypeError: Cannot read property &#39;name&#39; of undefined\n\nconst descriptor = Object.getOwnPropertyDescriptor(obj, &#39;foo&#39;);\n\ndescriptor.get.name // &quot;get foo&quot;\ndescriptor.set.name // &quot;set foo&quot;</code></pre> <p>\u6709\u4e24\u79cd\u7279\u6b8a\u60c5\u51b5\uff1a<code>bind</code>\u65b9\u6cd5\u521b\u9020\u7684\u51fd\u6570\uff0c<code>name</code>\u5c5e\u6027\u8fd4\u56de<code>bound</code>\u52a0\u4e0a\u539f\u51fd\u6570\u7684\u540d\u5b57\uff1b<code>Function</code>\u6784\u9020\u51fd\u6570\u521b\u9020\u7684\u51fd\u6570\uff0c<code>name</code>\u5c5e\u6027\u8fd4\u56de<code>anonymous</code>\u3002</p> <pre><code class="language-javascript">(new Function()).name // &quot;anonymous&quot;\n\nvar doSomething = function() {\n  // ...\n};\ndoSomething.bind().name // &quot;bound doSomething&quot;</code></pre> <p>\u5982\u679c\u5bf9\u8c61\u7684\u65b9\u6cd5\u662f\u4e00\u4e2a Symbol \u503c\uff0c\u90a3\u4e48<code>name</code>\u5c5e\u6027\u8fd4\u56de\u7684\u662f\u8fd9\u4e2a Symbol \u503c\u7684\u63cf\u8ff0\u3002</p> <pre><code class="language-javascript">const key1 = Symbol(&#39;description&#39;);\nconst key2 = Symbol();\nlet obj = {\n  [key1]() {},\n  [key2]() {},\n};\nobj[key1].name // &quot;[description]&quot;\nobj[key2].name // &quot;&quot;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>key1</code>\u5bf9\u5e94\u7684 Symbol \u503c\u6709\u63cf\u8ff0\uff0c<code>key2</code>\u6ca1\u6709\u3002</p> <h2 id="objectis">Object.is()</h2> <p>ES5 \u6bd4\u8f83\u4e24\u4e2a\u503c\u662f\u5426\u76f8\u7b49\uff0c\u53ea\u6709\u4e24\u4e2a\u8fd0\u7b97\u7b26\uff1a\u76f8\u7b49\u8fd0\u7b97\u7b26\uff08<code>==</code>\uff09\u548c\u4e25\u683c\u76f8\u7b49\u8fd0\u7b97\u7b26\uff08<code>===</code>\uff09\u3002\u5b83\u4eec\u90fd\u6709\u7f3a\u70b9\uff0c\u524d\u8005\u4f1a\u81ea\u52a8\u8f6c\u6362\u6570\u636e\u7c7b\u578b\uff0c\u540e\u8005\u7684<code>NaN</code>\u4e0d\u7b49\u4e8e\u81ea\u8eab\uff0c\u4ee5\u53ca<code>+0</code>\u7b49\u4e8e<code>-0</code>\u3002JavaScript \u7f3a\u4e4f\u4e00\u79cd\u8fd0\u7b97\uff0c\u5728\u6240\u6709\u73af\u5883\u4e2d\uff0c\u53ea\u8981\u4e24\u4e2a\u503c\u662f\u4e00\u6837\u7684\uff0c\u5b83\u4eec\u5c31\u5e94\u8be5\u76f8\u7b49\u3002</p> <p>ES6 \u63d0\u51fa\u201cSame-value equality\u201d\uff08\u540c\u503c\u76f8\u7b49\uff09\u7b97\u6cd5\uff0c\u7528\u6765\u89e3\u51b3\u8fd9\u4e2a\u95ee\u9898\u3002<code>Object.is</code>\u5c31\u662f\u90e8\u7f72\u8fd9\u4e2a\u7b97\u6cd5\u7684\u65b0\u65b9\u6cd5\u3002\u5b83\u7528\u6765\u6bd4\u8f83\u4e24\u4e2a\u503c\u662f\u5426\u4e25\u683c\u76f8\u7b49\uff0c\u4e0e\u4e25\u683c\u6bd4\u8f83\u8fd0\u7b97\u7b26\uff08===\uff09\u7684\u884c\u4e3a\u57fa\u672c\u4e00\u81f4\u3002</p> <pre><code class="language-javascript">Object.is(&#39;foo&#39;, &#39;foo&#39;)\n// true\nObject.is({}, {})\n// false</code></pre> <p>\u4e0d\u540c\u4e4b\u5904\u53ea\u6709\u4e24\u4e2a\uff1a\u4e00\u662f<code>+0</code>\u4e0d\u7b49\u4e8e<code>-0</code>\uff0c\u4e8c\u662f<code>NaN</code>\u7b49\u4e8e\u81ea\u8eab\u3002</p> <pre><code class="language-javascript">+0 === -0 //true\nNaN === NaN // false\n\nObject.is(+0, -0) // false\nObject.is(NaN, NaN) // true</code></pre> <p>ES5 \u53ef\u4ee5\u901a\u8fc7\u4e0b\u9762\u7684\u4ee3\u7801\uff0c\u90e8\u7f72<code>Object.is</code>\u3002</p> <pre><code class="language-javascript">Object.defineProperty(Object, &#39;is&#39;, {\n  value: function(x, y) {\n    if (x === y) {\n      // \u9488\u5bf9+0 \u4e0d\u7b49\u4e8e -0\u7684\u60c5\u51b5\n      return x !== 0 || 1 / x === 1 / y;\n    }\n    // \u9488\u5bf9NaN\u7684\u60c5\u51b5\n    return x !== x &amp;&amp; y !== y;\n  },\n  configurable: true,\n  enumerable: false,\n  writable: true\n});</code></pre> <h2 id="objectassign">Object.assign()</h2> <h3 id="\u57fa\u672c\u7528\u6cd5">\u57fa\u672c\u7528\u6cd5</h3> <p><code>Object.assign</code>\u65b9\u6cd5\u7528\u4e8e\u5bf9\u8c61\u7684\u5408\u5e76\uff0c\u5c06\u6e90\u5bf9\u8c61\uff08source\uff09\u7684\u6240\u6709\u53ef\u679a\u4e3e\u5c5e\u6027\uff0c\u590d\u5236\u5230\u76ee\u6807\u5bf9\u8c61\uff08target\uff09\u3002</p> <pre><code class="language-javascript">const target = { a: 1 };\n\nconst source1 = { b: 2 };\nconst source2 = { c: 3 };\n\nObject.assign(target, source1, source2);\ntarget // {a:1, b:2, c:3}</code></pre> <p><code>Object.assign</code>\u65b9\u6cd5\u7684\u7b2c\u4e00\u4e2a\u53c2\u6570\u662f\u76ee\u6807\u5bf9\u8c61\uff0c\u540e\u9762\u7684\u53c2\u6570\u90fd\u662f\u6e90\u5bf9\u8c61\u3002</p> <p>\u6ce8\u610f\uff0c\u5982\u679c\u76ee\u6807\u5bf9\u8c61\u4e0e\u6e90\u5bf9\u8c61\u6709\u540c\u540d\u5c5e\u6027\uff0c\u6216\u591a\u4e2a\u6e90\u5bf9\u8c61\u6709\u540c\u540d\u5c5e\u6027\uff0c\u5219\u540e\u9762\u7684\u5c5e\u6027\u4f1a\u8986\u76d6\u524d\u9762\u7684\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">const target = { a: 1, b: 1 };\n\nconst source1 = { b: 2, c: 2 };\nconst source2 = { c: 3 };\n\nObject.assign(target, source1, source2);\ntarget // {a:1, b:2, c:3}</code></pre> <p>\u5982\u679c\u53ea\u6709\u4e00\u4e2a\u53c2\u6570\uff0c<code>Object.assign</code>\u4f1a\u76f4\u63a5\u8fd4\u56de\u8be5\u53c2\u6570\u3002</p> <pre><code class="language-javascript">const obj = {a: 1};\nObject.assign(obj) === obj // true</code></pre> <p>\u5982\u679c\u8be5\u53c2\u6570\u4e0d\u662f\u5bf9\u8c61\uff0c\u5219\u4f1a\u5148\u8f6c\u6210\u5bf9\u8c61\uff0c\u7136\u540e\u8fd4\u56de\u3002</p> <pre><code class="language-javascript">typeof Object.assign(2) // &quot;object&quot;</code></pre> <p>\u7531\u4e8e<code>undefined</code>\u548c<code>null</code>\u65e0\u6cd5\u8f6c\u6210\u5bf9\u8c61\uff0c\u6240\u4ee5\u5982\u679c\u5b83\u4eec\u4f5c\u4e3a\u53c2\u6570\uff0c\u5c31\u4f1a\u62a5\u9519\u3002</p> <pre><code class="language-javascript">Object.assign(undefined) // \u62a5\u9519\nObject.assign(null) // \u62a5\u9519</code></pre> <p>\u5982\u679c\u975e\u5bf9\u8c61\u53c2\u6570\u51fa\u73b0\u5728\u6e90\u5bf9\u8c61\u7684\u4f4d\u7f6e\uff08\u5373\u975e\u9996\u53c2\u6570\uff09\uff0c\u90a3\u4e48\u5904\u7406\u89c4\u5219\u6709\u6240\u4e0d\u540c\u3002\u9996\u5148\uff0c\u8fd9\u4e9b\u53c2\u6570\u90fd\u4f1a\u8f6c\u6210\u5bf9\u8c61\uff0c\u5982\u679c\u65e0\u6cd5\u8f6c\u6210\u5bf9\u8c61\uff0c\u5c31\u4f1a\u8df3\u8fc7\u3002\u8fd9\u610f\u5473\u7740\uff0c\u5982\u679c<code>undefined</code>\u548c<code>null</code>\u4e0d\u5728\u9996\u53c2\u6570\uff0c\u5c31\u4e0d\u4f1a\u62a5\u9519\u3002</p> <pre><code class="language-javascript">let obj = {a: 1};\nObject.assign(obj, undefined) === obj // true\nObject.assign(obj, null) === obj // true</code></pre> <p>\u5176\u4ed6\u7c7b\u578b\u7684\u503c\uff08\u5373\u6570\u503c\u3001\u5b57\u7b26\u4e32\u548c\u5e03\u5c14\u503c\uff09\u4e0d\u5728\u9996\u53c2\u6570\uff0c\u4e5f\u4e0d\u4f1a\u62a5\u9519\u3002\u4f46\u662f\uff0c\u9664\u4e86\u5b57\u7b26\u4e32\u4f1a\u4ee5\u6570\u7ec4\u5f62\u5f0f\uff0c\u62f7\u8d1d\u5165\u76ee\u6807\u5bf9\u8c61\uff0c\u5176\u4ed6\u503c\u90fd\u4e0d\u4f1a\u4ea7\u751f\u6548\u679c\u3002</p> <pre><code class="language-javascript">const v1 = &#39;abc&#39;;\nconst v2 = true;\nconst v3 = 10;\n\nconst obj = Object.assign({}, v1, v2, v3);\nconsole.log(obj); // { &quot;0&quot;: &quot;a&quot;, &quot;1&quot;: &quot;b&quot;, &quot;2&quot;: &quot;c&quot; }</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>v1</code>\u3001<code>v2</code>\u3001<code>v3</code>\u5206\u522b\u662f\u5b57\u7b26\u4e32\u3001\u5e03\u5c14\u503c\u548c\u6570\u503c\uff0c\u7ed3\u679c\u53ea\u6709\u5b57\u7b26\u4e32\u5408\u5165\u76ee\u6807\u5bf9\u8c61\uff08\u4ee5\u5b57\u7b26\u6570\u7ec4\u7684\u5f62\u5f0f\uff09\uff0c\u6570\u503c\u548c\u5e03\u5c14\u503c\u90fd\u4f1a\u88ab\u5ffd\u7565\u3002\u8fd9\u662f\u56e0\u4e3a\u53ea\u6709\u5b57\u7b26\u4e32\u7684\u5305\u88c5\u5bf9\u8c61\uff0c\u4f1a\u4ea7\u751f\u53ef\u679a\u4e3e\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">Object(true) // {[[PrimitiveValue]]: true}\nObject(10)  //  {[[PrimitiveValue]]: 10}\nObject(&#39;abc&#39;) // {0: &quot;a&quot;, 1: &quot;b&quot;, 2: &quot;c&quot;, length: 3, [[PrimitiveValue]]: &quot;abc&quot;}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5e03\u5c14\u503c\u3001\u6570\u503c\u3001\u5b57\u7b26\u4e32\u5206\u522b\u8f6c\u6210\u5bf9\u5e94\u7684\u5305\u88c5\u5bf9\u8c61\uff0c\u53ef\u4ee5\u770b\u5230\u5b83\u4eec\u7684\u539f\u59cb\u503c\u90fd\u5728\u5305\u88c5\u5bf9\u8c61\u7684\u5185\u90e8\u5c5e\u6027<code>[[PrimitiveValue]]</code>\u4e0a\u9762\uff0c\u8fd9\u4e2a\u5c5e\u6027\u662f\u4e0d\u4f1a\u88ab<code>Object.assign</code>\u62f7\u8d1d\u7684\u3002\u53ea\u6709\u5b57\u7b26\u4e32\u7684\u5305\u88c5\u5bf9\u8c61\uff0c\u4f1a\u4ea7\u751f\u53ef\u679a\u4e3e\u7684\u5b9e\u4e49\u5c5e\u6027\uff0c\u90a3\u4e9b\u5c5e\u6027\u5219\u4f1a\u88ab\u62f7\u8d1d\u3002</p> <p><code>Object.assign</code>\u62f7\u8d1d\u7684\u5c5e\u6027\u662f\u6709\u9650\u5236\u7684\uff0c\u53ea\u62f7\u8d1d\u6e90\u5bf9\u8c61\u7684\u81ea\u8eab\u5c5e\u6027\uff08\u4e0d\u62f7\u8d1d\u7ee7\u627f\u5c5e\u6027\uff09\uff0c\u4e5f\u4e0d\u62f7\u8d1d\u4e0d\u53ef\u679a\u4e3e\u7684\u5c5e\u6027\uff08<code>enumerable: false</code>\uff09\u3002</p> <pre><code class="language-javascript">Object.assign({b: &#39;c&#39;},\n  Object.defineProperty({}, &#39;invisible&#39;, {\n    enumerable: false,\n    value: &#39;hello&#39;\n  })\n)\n// { b: &#39;c&#39; }</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>Object.assign</code>\u8981\u62f7\u8d1d\u7684\u5bf9\u8c61\u53ea\u6709\u4e00\u4e2a\u4e0d\u53ef\u679a\u4e3e\u5c5e\u6027<code>invisible</code>\uff0c\u8fd9\u4e2a\u5c5e\u6027\u5e76\u6ca1\u6709\u88ab\u62f7\u8d1d\u8fdb\u53bb\u3002</p> <p>\u5c5e\u6027\u540d\u4e3a Symbol \u503c\u7684\u5c5e\u6027\uff0c\u4e5f\u4f1a\u88ab<code>Object.assign</code>\u62f7\u8d1d\u3002</p> <pre><code class="language-javascript">Object.assign({ a: &#39;b&#39; }, { [Symbol(&#39;c&#39;)]: &#39;d&#39; })\n// { a: &#39;b&#39;, Symbol(c): &#39;d&#39; }</code></pre> <h3 id="\u6ce8\u610f\u70b9">\u6ce8\u610f\u70b9</h3> <p><strong>\uff081\uff09\u6d45\u62f7\u8d1d</strong></p> <p><code>Object.assign</code>\u65b9\u6cd5\u5b9e\u884c\u7684\u662f\u6d45\u62f7\u8d1d\uff0c\u800c\u4e0d\u662f\u6df1\u62f7\u8d1d\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u5982\u679c\u6e90\u5bf9\u8c61\u67d0\u4e2a\u5c5e\u6027\u7684\u503c\u662f\u5bf9\u8c61\uff0c\u90a3\u4e48\u76ee\u6807\u5bf9\u8c61\u62f7\u8d1d\u5f97\u5230\u7684\u662f\u8fd9\u4e2a\u5bf9\u8c61\u7684\u5f15\u7528\u3002</p> <pre><code class="language-javascript">const obj1 = {a: {b: 1}};\nconst obj2 = Object.assign({}, obj1);\n\nobj1.a.b = 2;\nobj2.a.b // 2</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u6e90\u5bf9\u8c61<code>obj1</code>\u7684<code>a</code>\u5c5e\u6027\u7684\u503c\u662f\u4e00\u4e2a\u5bf9\u8c61\uff0c<code>Object.assign</code>\u62f7\u8d1d\u5f97\u5230\u7684\u662f\u8fd9\u4e2a\u5bf9\u8c61\u7684\u5f15\u7528\u3002\u8fd9\u4e2a\u5bf9\u8c61\u7684\u4efb\u4f55\u53d8\u5316\uff0c\u90fd\u4f1a\u53cd\u6620\u5230\u76ee\u6807\u5bf9\u8c61\u4e0a\u9762\u3002</p> <p><strong>\uff082\uff09\u540c\u540d\u5c5e\u6027\u7684\u66ff\u6362</strong></p> <p>\u5bf9\u4e8e\u8fd9\u79cd\u5d4c\u5957\u7684\u5bf9\u8c61\uff0c\u4e00\u65e6\u9047\u5230\u540c\u540d\u5c5e\u6027\uff0c<code>Object.assign</code>\u7684\u5904\u7406\u65b9\u6cd5\u662f\u66ff\u6362\uff0c\u800c\u4e0d\u662f\u6dfb\u52a0\u3002</p> <pre><code class="language-javascript">const target = { a: { b: &#39;c&#39;, d: &#39;e&#39; } }\nconst source = { a: { b: &#39;hello&#39; } }\nObject.assign(target, source)\n// { a: { b: &#39;hello&#39; } }</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>target</code>\u5bf9\u8c61\u7684<code>a</code>\u5c5e\u6027\u88ab<code>source</code>\u5bf9\u8c61\u7684<code>a</code>\u5c5e\u6027\u6574\u4e2a\u66ff\u6362\u6389\u4e86\uff0c\u800c\u4e0d\u4f1a\u5f97\u5230<code>{ a: { b: &#39;hello&#39;, d: &#39;e&#39; } }</code>\u7684\u7ed3\u679c\u3002\u8fd9\u901a\u5e38\u4e0d\u662f\u5f00\u53d1\u8005\u60f3\u8981\u7684\uff0c\u9700\u8981\u7279\u522b\u5c0f\u5fc3\u3002</p> <p>\u4e00\u4e9b\u51fd\u6570\u5e93\u63d0\u4f9b<code>Object.assign</code>\u7684\u5b9a\u5236\u7248\u672c\uff08\u6bd4\u5982 Lodash \u7684<code>_.defaultsDeep</code>\u65b9\u6cd5\uff09\uff0c\u53ef\u4ee5\u5f97\u5230\u6df1\u62f7\u8d1d\u7684\u5408\u5e76\u3002</p> <p><strong>\uff083\uff09\u6570\u7ec4\u7684\u5904\u7406</strong></p> <p><code>Object.assign</code>\u53ef\u4ee5\u7528\u6765\u5904\u7406\u6570\u7ec4\uff0c\u4f46\u662f\u4f1a\u628a\u6570\u7ec4\u89c6\u4e3a\u5bf9\u8c61\u3002</p> <pre><code class="language-javascript">Object.assign([1, 2, 3], [4, 5])\n// [4, 5, 3]</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>Object.assign</code>\u628a\u6570\u7ec4\u89c6\u4e3a\u5c5e\u6027\u540d\u4e3a 0\u30011\u30012 \u7684\u5bf9\u8c61\uff0c\u56e0\u6b64\u6e90\u6570\u7ec4\u7684 0 \u53f7\u5c5e\u6027<code>4</code>\u8986\u76d6\u4e86\u76ee\u6807\u6570\u7ec4\u7684 0 \u53f7\u5c5e\u6027<code>1</code>\u3002</p> <p><strong>\uff084\uff09\u53d6\u503c\u51fd\u6570\u7684\u5904\u7406</strong></p> <p><code>Object.assign</code>\u53ea\u80fd\u8fdb\u884c\u503c\u7684\u590d\u5236\uff0c\u5982\u679c\u8981\u590d\u5236\u7684\u503c\u662f\u4e00\u4e2a\u53d6\u503c\u51fd\u6570\uff0c\u90a3\u4e48\u5c06\u6c42\u503c\u540e\u518d\u590d\u5236\u3002</p> <pre><code class="language-javascript">const source = {\n  get foo() { return 1 }\n};\nconst target = {};\n\nObject.assign(target, source)\n// { foo: 1 }</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>source</code>\u5bf9\u8c61\u7684<code>foo</code>\u5c5e\u6027\u662f\u4e00\u4e2a\u53d6\u503c\u51fd\u6570\uff0c<code>Object.assign</code>\u4e0d\u4f1a\u590d\u5236\u8fd9\u4e2a\u53d6\u503c\u51fd\u6570\uff0c\u53ea\u4f1a\u62ff\u5230\u503c\u4ee5\u540e\uff0c\u5c06\u8fd9\u4e2a\u503c\u590d\u5236\u8fc7\u53bb\u3002</p> <h3 id="\u5e38\u89c1\u7528\u9014">\u5e38\u89c1\u7528\u9014</h3> <p><code>Object.assign</code>\u65b9\u6cd5\u6709\u5f88\u591a\u7528\u5904\u3002</p> <p><strong>\uff081\uff09\u4e3a\u5bf9\u8c61\u6dfb\u52a0\u5c5e\u6027</strong></p> <pre><code class="language-javascript">class Point {\n  constructor(x, y) {\n    Object.assign(this, {x, y});\n  }\n}</code></pre> <p>\u4e0a\u9762\u65b9\u6cd5\u901a\u8fc7<code>Object.assign</code>\u65b9\u6cd5\uff0c\u5c06<code>x</code>\u5c5e\u6027\u548c<code>y</code>\u5c5e\u6027\u6dfb\u52a0\u5230<code>Point</code>\u7c7b\u7684\u5bf9\u8c61\u5b9e\u4f8b\u3002</p> <p><strong>\uff082\uff09\u4e3a\u5bf9\u8c61\u6dfb\u52a0\u65b9\u6cd5</strong></p> <pre><code class="language-javascript">Object.assign(SomeClass.prototype, {\n  someMethod(arg1, arg2) {\n    \xb7\xb7\xb7\n  },\n  anotherMethod() {\n    \xb7\xb7\xb7\n  }\n});\n\n// \u7b49\u540c\u4e8e\u4e0b\u9762\u7684\u5199\u6cd5\nSomeClass.prototype.someMethod = function (arg1, arg2) {\n  \xb7\xb7\xb7\n};\nSomeClass.prototype.anotherMethod = function () {\n  \xb7\xb7\xb7\n};</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4f7f\u7528\u4e86\u5bf9\u8c61\u5c5e\u6027\u7684\u7b80\u6d01\u8868\u793a\u6cd5\uff0c\u76f4\u63a5\u5c06\u4e24\u4e2a\u51fd\u6570\u653e\u5728\u5927\u62ec\u53f7\u4e2d\uff0c\u518d\u4f7f\u7528<code>assign</code>\u65b9\u6cd5\u6dfb\u52a0\u5230<code>SomeClass.prototype</code>\u4e4b\u4e2d\u3002</p> <p><strong>\uff083\uff09\u514b\u9686\u5bf9\u8c61</strong></p> <pre><code class="language-javascript">function clone(origin) {\n  return Object.assign({}, origin);\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u5c06\u539f\u59cb\u5bf9\u8c61\u62f7\u8d1d\u5230\u4e00\u4e2a\u7a7a\u5bf9\u8c61\uff0c\u5c31\u5f97\u5230\u4e86\u539f\u59cb\u5bf9\u8c61\u7684\u514b\u9686\u3002</p> <p>\u4e0d\u8fc7\uff0c\u91c7\u7528\u8fd9\u79cd\u65b9\u6cd5\u514b\u9686\uff0c\u53ea\u80fd\u514b\u9686\u539f\u59cb\u5bf9\u8c61\u81ea\u8eab\u7684\u503c\uff0c\u4e0d\u80fd\u514b\u9686\u5b83\u7ee7\u627f\u7684\u503c\u3002\u5982\u679c\u60f3\u8981\u4fdd\u6301\u7ee7\u627f\u94fe\uff0c\u53ef\u4ee5\u91c7\u7528\u4e0b\u9762\u7684\u4ee3\u7801\u3002</p> <pre><code class="language-javascript">function clone(origin) {\n  let originProto = Object.getPrototypeOf(origin);\n  return Object.assign(Object.create(originProto), origin);\n}</code></pre> <p><strong>\uff084\uff09\u5408\u5e76\u591a\u4e2a\u5bf9\u8c61</strong></p> <p>\u5c06\u591a\u4e2a\u5bf9\u8c61\u5408\u5e76\u5230\u67d0\u4e2a\u5bf9\u8c61\u3002</p> <pre><code class="language-javascript">const merge =\n  (target, ...sources) =&gt; Object.assign(target, ...sources);</code></pre> <p>\u5982\u679c\u5e0c\u671b\u5408\u5e76\u540e\u8fd4\u56de\u4e00\u4e2a\u65b0\u5bf9\u8c61\uff0c\u53ef\u4ee5\u6539\u5199\u4e0a\u9762\u51fd\u6570\uff0c\u5bf9\u4e00\u4e2a\u7a7a\u5bf9\u8c61\u5408\u5e76\u3002</p> <pre><code class="language-javascript">const merge =\n  (...sources) =&gt; Object.assign({}, ...sources);</code></pre> <p><strong>\uff085\uff09\u4e3a\u5c5e\u6027\u6307\u5b9a\u9ed8\u8ba4\u503c</strong></p> <pre><code class="language-javascript">const DEFAULTS = {\n  logLevel: 0,\n  outputFormat: &#39;html&#39;\n};\n\nfunction processContent(options) {\n  options = Object.assign({}, DEFAULTS, options);\n  console.log(options);\n  // ...\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>DEFAULTS</code>\u5bf9\u8c61\u662f\u9ed8\u8ba4\u503c\uff0c<code>options</code>\u5bf9\u8c61\u662f\u7528\u6237\u63d0\u4f9b\u7684\u53c2\u6570\u3002<code>Object.assign</code>\u65b9\u6cd5\u5c06<code>DEFAULTS</code>\u548c<code>options</code>\u5408\u5e76\u6210\u4e00\u4e2a\u65b0\u5bf9\u8c61\uff0c\u5982\u679c\u4e24\u8005\u6709\u540c\u540d\u5c5e\u6027\uff0c\u5219<code>option</code>\u7684\u5c5e\u6027\u503c\u4f1a\u8986\u76d6<code>DEFAULTS</code>\u7684\u5c5e\u6027\u503c\u3002</p> <p>\u6ce8\u610f\uff0c\u7531\u4e8e\u5b58\u5728\u6d45\u62f7\u8d1d\u7684\u95ee\u9898\uff0c<code>DEFAULTS</code>\u5bf9\u8c61\u548c<code>options</code>\u5bf9\u8c61\u7684\u6240\u6709\u5c5e\u6027\u7684\u503c\uff0c\u6700\u597d\u90fd\u662f\u7b80\u5355\u7c7b\u578b\uff0c\u4e0d\u8981\u6307\u5411\u53e6\u4e00\u4e2a\u5bf9\u8c61\u3002\u5426\u5219\uff0c<code>DEFAULTS</code>\u5bf9\u8c61\u7684\u8be5\u5c5e\u6027\u5f88\u53ef\u80fd\u4e0d\u8d77\u4f5c\u7528\u3002</p> <pre><code class="language-javascript">const DEFAULTS = {\n  url: {\n    host: &#39;example.com&#39;,\n    port: 7070\n  },\n};\n\nprocessContent({ url: {port: 8000} })\n// {\n//   url: {port: 8000}\n// }</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u7684\u539f\u610f\u662f\u5c06<code>url.port</code>\u6539\u6210 8000\uff0c<code>url.host</code>\u4e0d\u53d8\u3002\u5b9e\u9645\u7ed3\u679c\u5374\u662f<code>options.url</code>\u8986\u76d6\u6389<code>DEFAULTS.url</code>\uff0c\u6240\u4ee5<code>url.host</code>\u5c31\u4e0d\u5b58\u5728\u4e86\u3002</p> <h2 id="\u5c5e\u6027\u7684\u53ef\u679a\u4e3e\u6027\u548c\u904d\u5386">\u5c5e\u6027\u7684\u53ef\u679a\u4e3e\u6027\u548c\u904d\u5386</h2> <h3 id="\u53ef\u679a\u4e3e\u6027">\u53ef\u679a\u4e3e\u6027</h3> <p>\u5bf9\u8c61\u7684\u6bcf\u4e2a\u5c5e\u6027\u90fd\u6709\u4e00\u4e2a\u63cf\u8ff0\u5bf9\u8c61\uff08Descriptor\uff09\uff0c\u7528\u6765\u63a7\u5236\u8be5\u5c5e\u6027\u7684\u884c\u4e3a\u3002<code>Object.getOwnPropertyDescriptor</code>\u65b9\u6cd5\u53ef\u4ee5\u83b7\u53d6\u8be5\u5c5e\u6027\u7684\u63cf\u8ff0\u5bf9\u8c61\u3002</p> <pre><code class="language-javascript">let obj = { foo: 123 };\nObject.getOwnPropertyDescriptor(obj, &#39;foo&#39;)\n//  {\n//    value: 123,\n//    writable: true,\n//    enumerable: true,\n//    configurable: true\n//  }</code></pre> <p>\u63cf\u8ff0\u5bf9\u8c61\u7684<code>enumerable</code>\u5c5e\u6027\uff0c\u79f0\u4e3a\u201d\u53ef\u679a\u4e3e\u6027\u201c\uff0c\u5982\u679c\u8be5\u5c5e\u6027\u4e3a<code>false</code>\uff0c\u5c31\u8868\u793a\u67d0\u4e9b\u64cd\u4f5c\u4f1a\u5ffd\u7565\u5f53\u524d\u5c5e\u6027\u3002</p> <p>\u76ee\u524d\uff0c\u6709\u56db\u4e2a\u64cd\u4f5c\u4f1a\u5ffd\u7565<code>enumerable</code>\u4e3a<code>false</code>\u7684\u5c5e\u6027\u3002</p> <ul> <li><code>for...in</code>\u5faa\u73af\uff1a\u53ea\u904d\u5386\u5bf9\u8c61\u81ea\u8eab\u7684\u548c\u7ee7\u627f\u7684\u53ef\u679a\u4e3e\u7684\u5c5e\u6027\u3002</li> <li><code>Object.keys()</code>\uff1a\u8fd4\u56de\u5bf9\u8c61\u81ea\u8eab\u7684\u6240\u6709\u53ef\u679a\u4e3e\u7684\u5c5e\u6027\u7684\u952e\u540d\u3002</li> <li><code>JSON.stringify()</code>\uff1a\u53ea\u4e32\u884c\u5316\u5bf9\u8c61\u81ea\u8eab\u7684\u53ef\u679a\u4e3e\u7684\u5c5e\u6027\u3002</li> <li><code>Object.assign()</code>\uff1a \u5ffd\u7565<code>enumerable</code>\u4e3a<code>false</code>\u7684\u5c5e\u6027\uff0c\u53ea\u62f7\u8d1d\u5bf9\u8c61\u81ea\u8eab\u7684\u53ef\u679a\u4e3e\u7684\u5c5e\u6027\u3002</li> </ul> <p>\u8fd9\u56db\u4e2a\u64cd\u4f5c\u4e4b\u4e2d\uff0c\u524d\u4e09\u4e2a\u662f ES5 \u5c31\u6709\u7684\uff0c\u6700\u540e\u4e00\u4e2a<code>Object.assign()</code>\u662f ES6 \u65b0\u589e\u7684\u3002\u5176\u4e2d\uff0c\u53ea\u6709<code>for...in</code>\u4f1a\u8fd4\u56de\u7ee7\u627f\u7684\u5c5e\u6027\uff0c\u5176\u4ed6\u4e09\u4e2a\u65b9\u6cd5\u90fd\u4f1a\u5ffd\u7565\u7ee7\u627f\u7684\u5c5e\u6027\uff0c\u53ea\u5904\u7406\u5bf9\u8c61\u81ea\u8eab\u7684\u5c5e\u6027\u3002\u5b9e\u9645\u4e0a\uff0c\u5f15\u5165\u201c\u53ef\u679a\u4e3e\u201d\uff08<code>enumerable</code>\uff09\u8fd9\u4e2a\u6982\u5ff5\u7684\u6700\u521d\u76ee\u7684\uff0c\u5c31\u662f\u8ba9\u67d0\u4e9b\u5c5e\u6027\u53ef\u4ee5\u89c4\u907f\u6389<code>for...in</code>\u64cd\u4f5c\uff0c\u4e0d\u7136\u6240\u6709\u5185\u90e8\u5c5e\u6027\u548c\u65b9\u6cd5\u90fd\u4f1a\u88ab\u904d\u5386\u5230\u3002\u6bd4\u5982\uff0c\u5bf9\u8c61\u539f\u578b\u7684<code>toString</code>\u65b9\u6cd5\uff0c\u4ee5\u53ca\u6570\u7ec4\u7684<code>length</code>\u5c5e\u6027\uff0c\u5c31\u901a\u8fc7\u201c\u53ef\u679a\u4e3e\u6027\u201d\uff0c\u4ece\u800c\u907f\u514d\u88ab<code>for...in</code>\u904d\u5386\u5230\u3002</p> <pre><code class="language-javascript">Object.getOwnPropertyDescriptor(Object.prototype, &#39;toString&#39;).enumerable\n// false\n\nObject.getOwnPropertyDescriptor([], &#39;length&#39;).enumerable\n// false</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>toString</code>\u548c<code>length</code>\u5c5e\u6027\u7684<code>enumerable</code>\u90fd\u662f<code>false</code>\uff0c\u56e0\u6b64<code>for...in</code>\u4e0d\u4f1a\u904d\u5386\u5230\u8fd9\u4e24\u4e2a\u7ee7\u627f\u81ea\u539f\u578b\u7684\u5c5e\u6027\u3002</p> <p>\u53e6\u5916\uff0cES6 \u89c4\u5b9a\uff0c\u6240\u6709 Class \u7684\u539f\u578b\u7684\u65b9\u6cd5\u90fd\u662f\u4e0d\u53ef\u679a\u4e3e\u7684\u3002</p> <pre><code class="language-javascript">Object.getOwnPropertyDescriptor(class {foo() {}}.prototype, &#39;foo&#39;).enumerable\n// false</code></pre> <p>\u603b\u7684\u6765\u8bf4\uff0c\u64cd\u4f5c\u4e2d\u5f15\u5165\u7ee7\u627f\u7684\u5c5e\u6027\u4f1a\u8ba9\u95ee\u9898\u590d\u6742\u5316\uff0c\u5927\u591a\u6570\u65f6\u5019\uff0c\u6211\u4eec\u53ea\u5173\u5fc3\u5bf9\u8c61\u81ea\u8eab\u7684\u5c5e\u6027\u3002\u6240\u4ee5\uff0c\u5c3d\u91cf\u4e0d\u8981\u7528<code>for...in</code>\u5faa\u73af\uff0c\u800c\u7528<code>Object.keys()</code>\u4ee3\u66ff\u3002</p> <h3 id="\u5c5e\u6027\u7684\u904d\u5386">\u5c5e\u6027\u7684\u904d\u5386</h3> <p>ES6 \u4e00\u5171\u6709 5 \u79cd\u65b9\u6cd5\u53ef\u4ee5\u904d\u5386\u5bf9\u8c61\u7684\u5c5e\u6027\u3002</p> <p><strong>\uff081\uff09for...in</strong></p> <p><code>for...in</code>\u5faa\u73af\u904d\u5386\u5bf9\u8c61\u81ea\u8eab\u7684\u548c\u7ee7\u627f\u7684\u53ef\u679a\u4e3e\u5c5e\u6027\uff08\u4e0d\u542b Symbol \u5c5e\u6027\uff09\u3002</p> <p><strong>\uff082\uff09Object.keys(obj)</strong></p> <p><code>Object.keys</code>\u8fd4\u56de\u4e00\u4e2a\u6570\u7ec4\uff0c\u5305\u62ec\u5bf9\u8c61\u81ea\u8eab\u7684\uff08\u4e0d\u542b\u7ee7\u627f\u7684\uff09\u6240\u6709\u53ef\u679a\u4e3e\u5c5e\u6027\uff08\u4e0d\u542b Symbol \u5c5e\u6027\uff09\u7684\u952e\u540d\u3002</p> <p><strong>\uff083\uff09Object.getOwnPropertyNames(obj)</strong></p> <p><code>Object.getOwnPropertyNames</code>\u8fd4\u56de\u4e00\u4e2a\u6570\u7ec4\uff0c\u5305\u542b\u5bf9\u8c61\u81ea\u8eab\u7684\u6240\u6709\u5c5e\u6027\uff08\u4e0d\u542b Symbol \u5c5e\u6027\uff0c\u4f46\u662f\u5305\u62ec\u4e0d\u53ef\u679a\u4e3e\u5c5e\u6027\uff09\u7684\u952e\u540d\u3002</p> <p><strong>\uff084\uff09Object.getOwnPropertySymbols(obj)</strong></p> <p><code>Object.getOwnPropertySymbols</code>\u8fd4\u56de\u4e00\u4e2a\u6570\u7ec4\uff0c\u5305\u542b\u5bf9\u8c61\u81ea\u8eab\u7684\u6240\u6709 Symbol \u5c5e\u6027\u7684\u952e\u540d\u3002</p> <p><strong>\uff085\uff09Reflect.ownKeys(obj)</strong></p> <p><code>Reflect.ownKeys</code>\u8fd4\u56de\u4e00\u4e2a\u6570\u7ec4\uff0c\u5305\u542b\u5bf9\u8c61\u81ea\u8eab\u7684\u6240\u6709\u952e\u540d\uff0c\u4e0d\u7ba1\u952e\u540d\u662f Symbol \u6216\u5b57\u7b26\u4e32\uff0c\u4e5f\u4e0d\u7ba1\u662f\u5426\u53ef\u679a\u4e3e\u3002</p> <p>\u4ee5\u4e0a\u7684 5 \u79cd\u65b9\u6cd5\u904d\u5386\u5bf9\u8c61\u7684\u952e\u540d\uff0c\u90fd\u9075\u5b88\u540c\u6837\u7684\u5c5e\u6027\u904d\u5386\u7684\u6b21\u5e8f\u89c4\u5219\u3002</p> <ul> <li>\u9996\u5148\u904d\u5386\u6240\u6709\u6570\u503c\u952e\uff0c\u6309\u7167\u6570\u503c\u5347\u5e8f\u6392\u5217\u3002</li> <li>\u5176\u6b21\u904d\u5386\u6240\u6709\u5b57\u7b26\u4e32\u952e\uff0c\u6309\u7167\u52a0\u5165\u65f6\u95f4\u5347\u5e8f\u6392\u5217\u3002</li> <li>\u6700\u540e\u904d\u5386\u6240\u6709 Symbol \u952e\uff0c\u6309\u7167\u52a0\u5165\u65f6\u95f4\u5347\u5e8f\u6392\u5217\u3002</li> </ul> <pre><code class="language-javascript">Reflect.ownKeys({ [Symbol()]:0, b:0, 10:0, 2:0, a:0 })\n// [&#39;2&#39;, &#39;10&#39;, &#39;b&#39;, &#39;a&#39;, Symbol()]</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>Reflect.ownKeys</code>\u65b9\u6cd5\u8fd4\u56de\u4e00\u4e2a\u6570\u7ec4\uff0c\u5305\u542b\u4e86\u53c2\u6570\u5bf9\u8c61\u7684\u6240\u6709\u5c5e\u6027\u3002\u8fd9\u4e2a\u6570\u7ec4\u7684\u5c5e\u6027\u6b21\u5e8f\u662f\u8fd9\u6837\u7684\uff0c\u9996\u5148\u662f\u6570\u503c\u5c5e\u6027<code>2</code>\u548c<code>10</code>\uff0c\u5176\u6b21\u662f\u5b57\u7b26\u4e32\u5c5e\u6027<code>b</code>\u548c<code>a</code>\uff0c\u6700\u540e\u662f Symbol \u5c5e\u6027\u3002</p> <h2 id="objectgetownpropertydescriptors">Object.getOwnPropertyDescriptors()</h2> <p>\u524d\u9762\u8bf4\u8fc7\uff0c<code>Object.getOwnPropertyDescriptor</code>\u65b9\u6cd5\u4f1a\u8fd4\u56de\u67d0\u4e2a\u5bf9\u8c61\u5c5e\u6027\u7684\u63cf\u8ff0\u5bf9\u8c61\uff08descriptor\uff09\u3002ES2017 \u5f15\u5165\u4e86<code>Object.getOwnPropertyDescriptors</code>\u65b9\u6cd5\uff0c\u8fd4\u56de\u6307\u5b9a\u5bf9\u8c61\u6240\u6709\u81ea\u8eab\u5c5e\u6027\uff08\u975e\u7ee7\u627f\u5c5e\u6027\uff09\u7684\u63cf\u8ff0\u5bf9\u8c61\u3002</p> <pre><code class="language-javascript">const obj = {\n  foo: 123,\n  get bar() { return &#39;abc&#39; }\n};\n\nObject.getOwnPropertyDescriptors(obj)\n// { foo:\n//    { value: 123,\n//      writable: true,\n//      enumerable: true,\n//      configurable: true },\n//   bar:\n//    { get: [Function: bar],\n//      set: undefined,\n//      enumerable: true,\n//      configurable: true } }</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>Object.getOwnPropertyDescriptors</code>\u65b9\u6cd5\u8fd4\u56de\u4e00\u4e2a\u5bf9\u8c61\uff0c\u6240\u6709\u539f\u5bf9\u8c61\u7684\u5c5e\u6027\u540d\u90fd\u662f\u8be5\u5bf9\u8c61\u7684\u5c5e\u6027\u540d\uff0c\u5bf9\u5e94\u7684\u5c5e\u6027\u503c\u5c31\u662f\u8be5\u5c5e\u6027\u7684\u63cf\u8ff0\u5bf9\u8c61\u3002</p> <p>\u8be5\u65b9\u6cd5\u7684\u5b9e\u73b0\u975e\u5e38\u5bb9\u6613\u3002</p> <pre><code class="language-javascript">function getOwnPropertyDescriptors(obj) {\n  const result = {};\n  for (let key of Reflect.ownKeys(obj)) {\n    result[key] = Object.getOwnPropertyDescriptor(obj, key);\n  }\n  return result;\n}</code></pre> <p>\u8be5\u65b9\u6cd5\u7684\u5f15\u5165\u76ee\u7684\uff0c\u4e3b\u8981\u662f\u4e3a\u4e86\u89e3\u51b3<code>Object.assign()</code>\u65e0\u6cd5\u6b63\u786e\u62f7\u8d1d<code>get</code>\u5c5e\u6027\u548c<code>set</code>\u5c5e\u6027\u7684\u95ee\u9898\u3002</p> <pre><code class="language-javascript">const source = {\n  set foo(value) {\n    console.log(value);\n  }\n};\n\nconst target1 = {};\nObject.assign(target1, source);\n\nObject.getOwnPropertyDescriptor(target1, &#39;foo&#39;)\n// { value: undefined,\n//   writable: true,\n//   enumerable: true,\n//   configurable: true }</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>source</code>\u5bf9\u8c61\u7684<code>foo</code>\u5c5e\u6027\u7684\u503c\u662f\u4e00\u4e2a\u8d4b\u503c\u51fd\u6570\uff0c<code>Object.assign</code>\u65b9\u6cd5\u5c06\u8fd9\u4e2a\u5c5e\u6027\u62f7\u8d1d\u7ed9<code>target1</code>\u5bf9\u8c61\uff0c\u7ed3\u679c\u8be5\u5c5e\u6027\u7684\u503c\u53d8\u6210\u4e86<code>undefined</code>\u3002\u8fd9\u662f\u56e0\u4e3a<code>Object.assign</code>\u65b9\u6cd5\u603b\u662f\u62f7\u8d1d\u4e00\u4e2a\u5c5e\u6027\u7684\u503c\uff0c\u800c\u4e0d\u4f1a\u62f7\u8d1d\u5b83\u80cc\u540e\u7684\u8d4b\u503c\u65b9\u6cd5\u6216\u53d6\u503c\u65b9\u6cd5\u3002</p> <p>\u8fd9\u65f6\uff0c<code>Object.getOwnPropertyDescriptors</code>\u65b9\u6cd5\u914d\u5408<code>Object.defineProperties</code>\u65b9\u6cd5\uff0c\u5c31\u53ef\u4ee5\u5b9e\u73b0\u6b63\u786e\u62f7\u8d1d\u3002</p> <pre><code class="language-javascript">const source = {\n  set foo(value) {\n    console.log(value);\n  }\n};\n\nconst target2 = {};\nObject.defineProperties(target2, Object.getOwnPropertyDescriptors(source));\nObject.getOwnPropertyDescriptor(target2, &#39;foo&#39;)\n// { get: undefined,\n//   set: [Function: foo],\n//   enumerable: true,\n//   configurable: true }</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u4e24\u4e2a\u5bf9\u8c61\u5408\u5e76\u7684\u903b\u8f91\u53ef\u4ee5\u5199\u6210\u4e00\u4e2a\u51fd\u6570\u3002</p> <pre><code class="language-javascript">const shallowMerge = (target, source) =&gt; Object.defineProperties(\n  target,\n  Object.getOwnPropertyDescriptors(source)\n);</code></pre> <p><code>Object.getOwnPropertyDescriptors</code>\u65b9\u6cd5\u7684\u53e6\u4e00\u4e2a\u7528\u5904\uff0c\u662f\u914d\u5408<code>Object.create</code>\u65b9\u6cd5\uff0c\u5c06\u5bf9\u8c61\u5c5e\u6027\u514b\u9686\u5230\u4e00\u4e2a\u65b0\u5bf9\u8c61\u3002\u8fd9\u5c5e\u4e8e\u6d45\u62f7\u8d1d\u3002</p> <pre><code class="language-javascript">const clone = Object.create(Object.getPrototypeOf(obj),\n  Object.getOwnPropertyDescriptors(obj));\n\n// \u6216\u8005\n\nconst shallowClone = (obj) =&gt; Object.create(\n  Object.getPrototypeOf(obj),\n  Object.getOwnPropertyDescriptors(obj)\n);</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4f1a\u514b\u9686\u5bf9\u8c61<code>obj</code>\u3002</p> <p>\u53e6\u5916\uff0c<code>Object.getOwnPropertyDescriptors</code>\u65b9\u6cd5\u53ef\u4ee5\u5b9e\u73b0\u4e00\u4e2a\u5bf9\u8c61\u7ee7\u627f\u53e6\u4e00\u4e2a\u5bf9\u8c61\u3002\u4ee5\u524d\uff0c\u7ee7\u627f\u53e6\u4e00\u4e2a\u5bf9\u8c61\uff0c\u5e38\u5e38\u5199\u6210\u4e0b\u9762\u8fd9\u6837\u3002</p> <pre><code class="language-javascript">const obj = {\n  __proto__: prot,\n  foo: 123,\n};</code></pre> <p>ES6 \u89c4\u5b9a<code>__proto__</code>\u53ea\u6709\u6d4f\u89c8\u5668\u8981\u90e8\u7f72\uff0c\u5176\u4ed6\u73af\u5883\u4e0d\u7528\u90e8\u7f72\u3002\u5982\u679c\u53bb\u9664<code>__proto__</code>\uff0c\u4e0a\u9762\u4ee3\u7801\u5c31\u8981\u6539\u6210\u4e0b\u9762\u8fd9\u6837\u3002</p> <pre><code class="language-javascript">const obj = Object.create(prot);\nobj.foo = 123;\n\n// \u6216\u8005\n\nconst obj = Object.assign(\n  Object.create(prot),\n  {\n    foo: 123,\n  }\n);</code></pre> <p>\u6709\u4e86<code>Object.getOwnPropertyDescriptors</code>\uff0c\u6211\u4eec\u5c31\u6709\u4e86\u53e6\u4e00\u79cd\u5199\u6cd5\u3002</p> <pre><code class="language-javascript">const obj = Object.create(\n  prot,\n  Object.getOwnPropertyDescriptors({\n    foo: 123,\n  })\n);</code></pre> <p><code>Object.getOwnPropertyDescriptors</code>\u4e5f\u53ef\u4ee5\u7528\u6765\u5b9e\u73b0 Mixin\uff08\u6df7\u5165\uff09\u6a21\u5f0f\u3002</p> <pre><code class="language-javascript">let mix = (object) =&gt; ({\n  with: (...mixins) =&gt; mixins.reduce(\n    (c, mixin) =&gt; Object.create(\n      c, Object.getOwnPropertyDescriptors(mixin)\n    ), object)\n});\n\n// multiple mixins example\nlet a = {a: &#39;a&#39;};\nlet b = {b: &#39;b&#39;};\nlet c = {c: &#39;c&#39;};\nlet d = mix(c).with(a, b);\n\nd.c // &quot;c&quot;\nd.b // &quot;b&quot;\nd.a // &quot;a&quot;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u8fd4\u56de\u4e00\u4e2a\u65b0\u7684\u5bf9\u8c61<code>d</code>\uff0c\u4ee3\u8868\u4e86\u5bf9\u8c61<code>a</code>\u548c<code>b</code>\u88ab\u6df7\u5165\u4e86\u5bf9\u8c61<code>c</code>\u7684\u64cd\u4f5c\u3002</p> <p>\u51fa\u4e8e\u5b8c\u6574\u6027\u7684\u8003\u8651\uff0c<code>Object.getOwnPropertyDescriptors</code>\u8fdb\u5165\u6807\u51c6\u4ee5\u540e\uff0c\u4ee5\u540e\u8fd8\u4f1a\u65b0\u589e<code>Reflect.getOwnPropertyDescriptors</code>\u65b9\u6cd5\u3002</p> <h2 id="__proto__\u5c5e\u6027\uff0cobjectsetprototypeof\uff0cobjectgetprototypeof"><code>__proto__</code>\u5c5e\u6027\uff0cObject.setPrototypeOf()\uff0cObject.getPrototypeOf()</h2> <p>JavaScript \u8bed\u8a00\u7684\u5bf9\u8c61\u7ee7\u627f\u662f\u901a\u8fc7\u539f\u578b\u94fe\u5b9e\u73b0\u7684\u3002ES6 \u63d0\u4f9b\u4e86\u66f4\u591a\u539f\u578b\u5bf9\u8c61\u7684\u64cd\u4f5c\u65b9\u6cd5\u3002</p> <h3 id="__proto__\u5c5e\u6027"><code>__proto__</code>\u5c5e\u6027</h3> <p><code>__proto__</code>\u5c5e\u6027\uff08\u524d\u540e\u5404\u4e24\u4e2a\u4e0b\u5212\u7ebf\uff09\uff0c\u7528\u6765\u8bfb\u53d6\u6216\u8bbe\u7f6e\u5f53\u524d\u5bf9\u8c61\u7684<code>prototype</code>\u5bf9\u8c61\u3002\u76ee\u524d\uff0c\u6240\u6709\u6d4f\u89c8\u5668\uff08\u5305\u62ec IE11\uff09\u90fd\u90e8\u7f72\u4e86\u8fd9\u4e2a\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">// es6 \u7684\u5199\u6cd5\nconst obj = {\n  method: function() { ... }\n};\nobj.__proto__ = someOtherObj;\n\n// es5 \u7684\u5199\u6cd5\nvar obj = Object.create(someOtherObj);\nobj.method = function() { ... };</code></pre> <p>\u8be5\u5c5e\u6027\u6ca1\u6709\u5199\u5165 ES6 \u7684\u6b63\u6587\uff0c\u800c\u662f\u5199\u5165\u4e86\u9644\u5f55\uff0c\u539f\u56e0\u662f<code>__proto__</code>\u524d\u540e\u7684\u53cc\u4e0b\u5212\u7ebf\uff0c\u8bf4\u660e\u5b83\u672c\u8d28\u4e0a\u662f\u4e00\u4e2a\u5185\u90e8\u5c5e\u6027\uff0c\u800c\u4e0d\u662f\u4e00\u4e2a\u6b63\u5f0f\u7684\u5bf9\u5916\u7684 API\uff0c\u53ea\u662f\u7531\u4e8e\u6d4f\u89c8\u5668\u5e7f\u6cdb\u652f\u6301\uff0c\u624d\u88ab\u52a0\u5165\u4e86 ES6\u3002\u6807\u51c6\u660e\u786e\u89c4\u5b9a\uff0c\u53ea\u6709\u6d4f\u89c8\u5668\u5fc5\u987b\u90e8\u7f72\u8fd9\u4e2a\u5c5e\u6027\uff0c\u5176\u4ed6\u8fd0\u884c\u73af\u5883\u4e0d\u4e00\u5b9a\u9700\u8981\u90e8\u7f72\uff0c\u800c\u4e14\u65b0\u7684\u4ee3\u7801\u6700\u597d\u8ba4\u4e3a\u8fd9\u4e2a\u5c5e\u6027\u662f\u4e0d\u5b58\u5728\u7684\u3002\u56e0\u6b64\uff0c\u65e0\u8bba\u4ece\u8bed\u4e49\u7684\u89d2\u5ea6\uff0c\u8fd8\u662f\u4ece\u517c\u5bb9\u6027\u7684\u89d2\u5ea6\uff0c\u90fd\u4e0d\u8981\u4f7f\u7528\u8fd9\u4e2a\u5c5e\u6027\uff0c\u800c\u662f\u4f7f\u7528\u4e0b\u9762\u7684<code>Object.setPrototypeOf()</code>\uff08\u5199\u64cd\u4f5c\uff09\u3001<code>Object.getPrototypeOf()</code>\uff08\u8bfb\u64cd\u4f5c\uff09\u3001<code>Object.create()</code>\uff08\u751f\u6210\u64cd\u4f5c\uff09\u4ee3\u66ff\u3002</p> <p>\u5b9e\u73b0\u4e0a\uff0c<code>__proto__</code>\u8c03\u7528\u7684\u662f<code>Object.prototype.__proto__</code>\uff0c\u5177\u4f53\u5b9e\u73b0\u5982\u4e0b\u3002</p> <pre><code class="language-javascript">Object.defineProperty(Object.prototype, &#39;__proto__&#39;, {\n  get() {\n    let _thisObj = Object(this);\n    return Object.getPrototypeOf(_thisObj);\n  },\n  set(proto) {\n    if (this === undefined || this === null) {\n      throw new TypeError();\n    }\n    if (!isObject(this)) {\n      return undefined;\n    }\n    if (!isObject(proto)) {\n      return undefined;\n    }\n    let status = Reflect.setPrototypeOf(this, proto);\n    if (!status) {\n      throw new TypeError();\n    }\n  },\n});\n\nfunction isObject(value) {\n  return Object(value) === value;\n}</code></pre> <p>\u5982\u679c\u4e00\u4e2a\u5bf9\u8c61\u672c\u8eab\u90e8\u7f72\u4e86<code>__proto__</code>\u5c5e\u6027\uff0c\u8be5\u5c5e\u6027\u7684\u503c\u5c31\u662f\u5bf9\u8c61\u7684\u539f\u578b\u3002</p> <pre><code class="language-javascript">Object.getPrototypeOf({ __proto__: null })\n// null</code></pre> <h3 id="objectsetprototypeof">Object.setPrototypeOf()</h3> <p><code>Object.setPrototypeOf</code>\u65b9\u6cd5\u7684\u4f5c\u7528\u4e0e<code>__proto__</code>\u76f8\u540c\uff0c\u7528\u6765\u8bbe\u7f6e\u4e00\u4e2a\u5bf9\u8c61\u7684<code>prototype</code>\u5bf9\u8c61\uff0c\u8fd4\u56de\u53c2\u6570\u5bf9\u8c61\u672c\u8eab\u3002\u5b83\u662f ES6 \u6b63\u5f0f\u63a8\u8350\u7684\u8bbe\u7f6e\u539f\u578b\u5bf9\u8c61\u7684\u65b9\u6cd5\u3002</p> <pre><code class="language-javascript">// \u683c\u5f0f\nObject.setPrototypeOf(object, prototype)\n\n// \u7528\u6cd5\nconst o = Object.setPrototypeOf({}, null);</code></pre> <p>\u8be5\u65b9\u6cd5\u7b49\u540c\u4e8e\u4e0b\u9762\u7684\u51fd\u6570\u3002</p> <pre><code class="language-javascript">function (obj, proto) {\n  obj.__proto__ = proto;\n  return obj;\n}</code></pre> <p>\u4e0b\u9762\u662f\u4e00\u4e2a\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">let proto = {};\nlet obj = { x: 10 };\nObject.setPrototypeOf(obj, proto);\n\nproto.y = 20;\nproto.z = 40;\n\nobj.x // 10\nobj.y // 20\nobj.z // 40</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u5c06<code>proto</code>\u5bf9\u8c61\u8bbe\u4e3a<code>obj</code>\u5bf9\u8c61\u7684\u539f\u578b\uff0c\u6240\u4ee5\u4ece<code>obj</code>\u5bf9\u8c61\u53ef\u4ee5\u8bfb\u53d6<code>proto</code>\u5bf9\u8c61\u7684\u5c5e\u6027\u3002</p> <p>\u5982\u679c\u7b2c\u4e00\u4e2a\u53c2\u6570\u4e0d\u662f\u5bf9\u8c61\uff0c\u4f1a\u81ea\u52a8\u8f6c\u4e3a\u5bf9\u8c61\u3002\u4f46\u662f\u7531\u4e8e\u8fd4\u56de\u7684\u8fd8\u662f\u7b2c\u4e00\u4e2a\u53c2\u6570\uff0c\u6240\u4ee5\u8fd9\u4e2a\u64cd\u4f5c\u4e0d\u4f1a\u4ea7\u751f\u4efb\u4f55\u6548\u679c\u3002</p> <pre><code class="language-javascript">Object.setPrototypeOf(1, {}) === 1 // true\nObject.setPrototypeOf(&#39;foo&#39;, {}) === &#39;foo&#39; // true\nObject.setPrototypeOf(true, {}) === true // true</code></pre> <p>\u7531\u4e8e<code>undefined</code>\u548c<code>null</code>\u65e0\u6cd5\u8f6c\u4e3a\u5bf9\u8c61\uff0c\u6240\u4ee5\u5982\u679c\u7b2c\u4e00\u4e2a\u53c2\u6570\u662f<code>undefined</code>\u6216<code>null</code>\uff0c\u5c31\u4f1a\u62a5\u9519\u3002</p> <pre><code class="language-javascript">Object.setPrototypeOf(undefined, {})\n// TypeError: Object.setPrototypeOf called on null or undefined\n\nObject.setPrototypeOf(null, {})\n// TypeError: Object.setPrototypeOf called on null or undefined</code></pre> <h3 id="objectgetprototypeof">Object.getPrototypeOf()</h3> <p>\u8be5\u65b9\u6cd5\u4e0e<code>Object.setPrototypeOf</code>\u65b9\u6cd5\u914d\u5957\uff0c\u7528\u4e8e\u8bfb\u53d6\u4e00\u4e2a\u5bf9\u8c61\u7684\u539f\u578b\u5bf9\u8c61\u3002</p> <pre><code class="language-javascript">Object.getPrototypeOf(obj);</code></pre> <p>\u4e0b\u9762\u662f\u4e00\u4e2a\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">function Rectangle() {\n  // ...\n}\n\nconst rec = new Rectangle();\n\nObject.getPrototypeOf(rec) === Rectangle.prototype\n// true\n\nObject.setPrototypeOf(rec, Object.prototype);\nObject.getPrototypeOf(rec) === Rectangle.prototype\n// false</code></pre> <p>\u5982\u679c\u53c2\u6570\u4e0d\u662f\u5bf9\u8c61\uff0c\u4f1a\u88ab\u81ea\u52a8\u8f6c\u4e3a\u5bf9\u8c61\u3002</p> <pre><code class="language-javascript">// \u7b49\u540c\u4e8e Object.getPrototypeOf(Number(1))\nObject.getPrototypeOf(1)\n// Number {[[PrimitiveValue]]: 0}\n\n// \u7b49\u540c\u4e8e Object.getPrototypeOf(String(&#39;foo&#39;))\nObject.getPrototypeOf(&#39;foo&#39;)\n// String {length: 0, [[PrimitiveValue]]: &quot;&quot;}\n\n// \u7b49\u540c\u4e8e Object.getPrototypeOf(Boolean(true))\nObject.getPrototypeOf(true)\n// Boolean {[[PrimitiveValue]]: false}\n\nObject.getPrototypeOf(1) === Number.prototype // true\nObject.getPrototypeOf(&#39;foo&#39;) === String.prototype // true\nObject.getPrototypeOf(true) === Boolean.prototype // true</code></pre> <p>\u5982\u679c\u53c2\u6570\u662f<code>undefined</code>\u6216<code>null</code>\uff0c\u5b83\u4eec\u65e0\u6cd5\u8f6c\u4e3a\u5bf9\u8c61\uff0c\u6240\u4ee5\u4f1a\u62a5\u9519\u3002</p> <pre><code class="language-javascript">Object.getPrototypeOf(null)\n// TypeError: Cannot convert undefined or null to object\n\nObject.getPrototypeOf(undefined)\n// TypeError: Cannot convert undefined or null to object</code></pre> <h2 id="super-\u5173\u952e\u5b57">super \u5173\u952e\u5b57</h2> <p>\u6211\u4eec\u77e5\u9053\uff0c<code>this</code>\u5173\u952e\u5b57\u603b\u662f\u6307\u5411\u51fd\u6570\u6240\u5728\u7684\u5f53\u524d\u5bf9\u8c61\uff0cES6 \u53c8\u65b0\u589e\u4e86\u53e6\u4e00\u4e2a\u7c7b\u4f3c\u7684\u5173\u952e\u5b57<code>super</code>\uff0c\u6307\u5411\u5f53\u524d\u5bf9\u8c61\u7684\u539f\u578b\u5bf9\u8c61\u3002</p> <pre><code class="language-javascript">const proto = {\n  foo: &#39;hello&#39;\n};\n\nconst obj = {\n  find() {\n    return super.foo;\n  }\n};\n\nObject.setPrototypeOf(obj, proto);\nobj.find() // &quot;hello&quot;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5bf9\u8c61<code>obj</code>\u7684<code>find</code>\u65b9\u6cd5\u4e4b\u4e2d\uff0c\u901a\u8fc7<code>super.foo</code>\u5f15\u7528\u4e86\u539f\u578b\u5bf9\u8c61<code>proto</code>\u7684<code>foo</code>\u5c5e\u6027\u3002</p> <p>\u6ce8\u610f\uff0c<code>super</code>\u5173\u952e\u5b57\u8868\u793a\u539f\u578b\u5bf9\u8c61\u65f6\uff0c\u53ea\u80fd\u7528\u5728\u5bf9\u8c61\u7684\u65b9\u6cd5\u4e4b\u4e2d\uff0c\u7528\u5728\u5176\u4ed6\u5730\u65b9\u90fd\u4f1a\u62a5\u9519\u3002</p> <pre><code class="language-javascript">// \u62a5\u9519\nconst obj = {\n  foo: super.foo\n}\n\n// \u62a5\u9519\nconst obj = {\n  foo: () =&gt; super.foo\n}\n\n// \u62a5\u9519\nconst obj = {\n  foo: function () {\n    return super.foo\n  }\n}</code></pre> <p>\u4e0a\u9762\u4e09\u79cd<code>super</code>\u7684\u7528\u6cd5\u90fd\u4f1a\u62a5\u9519\uff0c\u56e0\u4e3a\u5bf9\u4e8e JavaScript \u5f15\u64ce\u6765\u8bf4\uff0c\u8fd9\u91cc\u7684<code>super</code>\u90fd\u6ca1\u6709\u7528\u5728\u5bf9\u8c61\u7684\u65b9\u6cd5\u4e4b\u4e2d\u3002\u7b2c\u4e00\u79cd\u5199\u6cd5\u662f<code>super</code>\u7528\u5728\u5c5e\u6027\u91cc\u9762\uff0c\u7b2c\u4e8c\u79cd\u548c\u7b2c\u4e09\u79cd\u5199\u6cd5\u662f<code>super</code>\u7528\u5728\u4e00\u4e2a\u51fd\u6570\u91cc\u9762\uff0c\u7136\u540e\u8d4b\u503c\u7ed9<code>foo</code>\u5c5e\u6027\u3002\u76ee\u524d\uff0c\u53ea\u6709\u5bf9\u8c61\u65b9\u6cd5\u7684\u7b80\u5199\u6cd5\u53ef\u4ee5\u8ba9 JavaScript \u5f15\u64ce\u786e\u8ba4\uff0c\u5b9a\u4e49\u7684\u662f\u5bf9\u8c61\u7684\u65b9\u6cd5\u3002</p> <p>JavaScript \u5f15\u64ce\u5185\u90e8\uff0c<code>super.foo</code>\u7b49\u540c\u4e8e<code>Object.getPrototypeOf(this).foo</code>\uff08\u5c5e\u6027\uff09\u6216<code>Object.getPrototypeOf(this).foo.call(this)</code>\uff08\u65b9\u6cd5\uff09\u3002</p> <pre><code class="language-javascript">const proto = {\n  x: &#39;hello&#39;,\n  foo() {\n    console.log(this.x);\n  },\n};\n\nconst obj = {\n  x: &#39;world&#39;,\n  foo() {\n    super.foo();\n  }\n}\n\nObject.setPrototypeOf(obj, proto);\n\nobj.foo() // &quot;world&quot;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>super.foo</code>\u6307\u5411\u539f\u578b\u5bf9\u8c61<code>proto</code>\u7684<code>foo</code>\u65b9\u6cd5\uff0c\u4f46\u662f\u7ed1\u5b9a\u7684<code>this</code>\u5374\u8fd8\u662f\u5f53\u524d\u5bf9\u8c61<code>obj</code>\uff0c\u56e0\u6b64\u8f93\u51fa\u7684\u5c31\u662f<code>world</code>\u3002</p> <h2 id="objectkeys\uff0cobjectvalues\uff0cobjectentries">Object.keys()\uff0cObject.values()\uff0cObject.entries()</h2> <h3 id="objectkeys">Object.keys()</h3> <p>ES5 \u5f15\u5165\u4e86<code>Object.keys</code>\u65b9\u6cd5\uff0c\u8fd4\u56de\u4e00\u4e2a\u6570\u7ec4\uff0c\u6210\u5458\u662f\u53c2\u6570\u5bf9\u8c61\u81ea\u8eab\u7684\uff08\u4e0d\u542b\u7ee7\u627f\u7684\uff09\u6240\u6709\u53ef\u904d\u5386\uff08enumerable\uff09\u5c5e\u6027\u7684\u952e\u540d\u3002</p> <pre><code class="language-javascript">var obj = { foo: &#39;bar&#39;, baz: 42 };\nObject.keys(obj)\n// [&quot;foo&quot;, &quot;baz&quot;]</code></pre> <p>ES2017 <a href="https://github.com/tc39/proposal-object-values-entries">\u5f15\u5165</a>\u4e86\u8ddf<code>Object.keys</code>\u914d\u5957\u7684<code>Object.values</code>\u548c<code>Object.entries</code>\uff0c\u4f5c\u4e3a\u904d\u5386\u4e00\u4e2a\u5bf9\u8c61\u7684\u8865\u5145\u624b\u6bb5\uff0c\u4f9b<code>for...of</code>\u5faa\u73af\u4f7f\u7528\u3002</p> <pre><code class="language-javascript">let {keys, values, entries} = Object;\nlet obj = { a: 1, b: 2, c: 3 };\n\nfor (let key of keys(obj)) {\n  console.log(key); // &#39;a&#39;, &#39;b&#39;, &#39;c&#39;\n}\n\nfor (let value of values(obj)) {\n  console.log(value); // 1, 2, 3\n}\n\nfor (let [key, value] of entries(obj)) {\n  console.log([key, value]); // [&#39;a&#39;, 1], [&#39;b&#39;, 2], [&#39;c&#39;, 3]\n}</code></pre> <h3 id="objectvalues">Object.values()</h3> <p><code>Object.values</code>\u65b9\u6cd5\u8fd4\u56de\u4e00\u4e2a\u6570\u7ec4\uff0c\u6210\u5458\u662f\u53c2\u6570\u5bf9\u8c61\u81ea\u8eab\u7684\uff08\u4e0d\u542b\u7ee7\u627f\u7684\uff09\u6240\u6709\u53ef\u904d\u5386\uff08enumerable\uff09\u5c5e\u6027\u7684\u952e\u503c\u3002</p> <pre><code class="language-javascript">const obj = { foo: &#39;bar&#39;, baz: 42 };\nObject.values(obj)\n// [&quot;bar&quot;, 42]</code></pre> <p>\u8fd4\u56de\u6570\u7ec4\u7684\u6210\u5458\u987a\u5e8f\uff0c\u4e0e\u672c\u7ae0\u7684\u300a\u5c5e\u6027\u7684\u904d\u5386\u300b\u90e8\u5206\u4ecb\u7ecd\u7684\u6392\u5217\u89c4\u5219\u4e00\u81f4\u3002</p> <pre><code class="language-javascript">const obj = { 100: &#39;a&#39;, 2: &#39;b&#39;, 7: &#39;c&#39; };\nObject.values(obj)\n// [&quot;b&quot;, &quot;c&quot;, &quot;a&quot;]</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5c5e\u6027\u540d\u4e3a\u6570\u503c\u7684\u5c5e\u6027\uff0c\u662f\u6309\u7167\u6570\u503c\u5927\u5c0f\uff0c\u4ece\u5c0f\u5230\u5927\u904d\u5386\u7684\uff0c\u56e0\u6b64\u8fd4\u56de\u7684\u987a\u5e8f\u662f<code>b</code>\u3001<code>c</code>\u3001<code>a</code>\u3002</p> <p><code>Object.values</code>\u53ea\u8fd4\u56de\u5bf9\u8c61\u81ea\u8eab\u7684\u53ef\u904d\u5386\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">const obj = Object.create({}, {p: {value: 42}});\nObject.values(obj) // []</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>Object.create</code>\u65b9\u6cd5\u7684\u7b2c\u4e8c\u4e2a\u53c2\u6570\u6dfb\u52a0\u7684\u5bf9\u8c61\u5c5e\u6027\uff08\u5c5e\u6027<code>p</code>\uff09\uff0c\u5982\u679c\u4e0d\u663e\u5f0f\u58f0\u660e\uff0c\u9ed8\u8ba4\u662f\u4e0d\u53ef\u904d\u5386\u7684\uff0c\u56e0\u4e3a<code>p</code>\u7684\u5c5e\u6027\u63cf\u8ff0\u5bf9\u8c61\u7684<code>enumerable</code>\u9ed8\u8ba4\u662f<code>false</code>\uff0c<code>Object.values</code>\u4e0d\u4f1a\u8fd4\u56de\u8fd9\u4e2a\u5c5e\u6027\u3002\u53ea\u8981\u628a<code>enumerable</code>\u6539\u6210<code>true</code>\uff0c<code>Object.values</code>\u5c31\u4f1a\u8fd4\u56de\u5c5e\u6027<code>p</code>\u7684\u503c\u3002</p> <pre><code class="language-javascript">const obj = Object.create({}, {p:\n  {\n    value: 42,\n    enumerable: true\n  }\n});\nObject.values(obj) // [42]</code></pre> <p><code>Object.values</code>\u4f1a\u8fc7\u6ee4\u5c5e\u6027\u540d\u4e3a Symbol \u503c\u7684\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">Object.values({ [Symbol()]: 123, foo: &#39;abc&#39; });\n// [&#39;abc&#39;]</code></pre> <p>\u5982\u679c<code>Object.values</code>\u65b9\u6cd5\u7684\u53c2\u6570\u662f\u4e00\u4e2a\u5b57\u7b26\u4e32\uff0c\u4f1a\u8fd4\u56de\u5404\u4e2a\u5b57\u7b26\u7ec4\u6210\u7684\u4e00\u4e2a\u6570\u7ec4\u3002</p> <pre><code class="language-javascript">Object.values(&#39;foo&#39;)\n// [&#39;f&#39;, &#39;o&#39;, &#39;o&#39;]</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5b57\u7b26\u4e32\u4f1a\u5148\u8f6c\u6210\u4e00\u4e2a\u7c7b\u4f3c\u6570\u7ec4\u7684\u5bf9\u8c61\u3002\u5b57\u7b26\u4e32\u7684\u6bcf\u4e2a\u5b57\u7b26\uff0c\u5c31\u662f\u8be5\u5bf9\u8c61\u7684\u4e00\u4e2a\u5c5e\u6027\u3002\u56e0\u6b64\uff0c<code>Object.values</code>\u8fd4\u56de\u6bcf\u4e2a\u5c5e\u6027\u7684\u952e\u503c\uff0c\u5c31\u662f\u5404\u4e2a\u5b57\u7b26\u7ec4\u6210\u7684\u4e00\u4e2a\u6570\u7ec4\u3002</p> <p>\u5982\u679c\u53c2\u6570\u4e0d\u662f\u5bf9\u8c61\uff0c<code>Object.values</code>\u4f1a\u5148\u5c06\u5176\u8f6c\u4e3a\u5bf9\u8c61\u3002\u7531\u4e8e\u6570\u503c\u548c\u5e03\u5c14\u503c\u7684\u5305\u88c5\u5bf9\u8c61\uff0c\u90fd\u4e0d\u4f1a\u4e3a\u5b9e\u4f8b\u6dfb\u52a0\u975e\u7ee7\u627f\u7684\u5c5e\u6027\u3002\u6240\u4ee5\uff0c<code>Object.values</code>\u4f1a\u8fd4\u56de\u7a7a\u6570\u7ec4\u3002</p> <pre><code class="language-javascript">Object.values(42) // []\nObject.values(true) // []</code></pre> <h3 id="objectentries">Object.entries</h3> <p><code>Object.entries</code>\u65b9\u6cd5\u8fd4\u56de\u4e00\u4e2a\u6570\u7ec4\uff0c\u6210\u5458\u662f\u53c2\u6570\u5bf9\u8c61\u81ea\u8eab\u7684\uff08\u4e0d\u542b\u7ee7\u627f\u7684\uff09\u6240\u6709\u53ef\u904d\u5386\uff08enumerable\uff09\u5c5e\u6027\u7684\u952e\u503c\u5bf9\u6570\u7ec4\u3002</p> <pre><code class="language-javascript">const obj = { foo: &#39;bar&#39;, baz: 42 };\nObject.entries(obj)\n// [ [&quot;foo&quot;, &quot;bar&quot;], [&quot;baz&quot;, 42] ]</code></pre> <p>\u9664\u4e86\u8fd4\u56de\u503c\u4e0d\u4e00\u6837\uff0c\u8be5\u65b9\u6cd5\u7684\u884c\u4e3a\u4e0e<code>Object.values</code>\u57fa\u672c\u4e00\u81f4\u3002</p> <p>\u5982\u679c\u539f\u5bf9\u8c61\u7684\u5c5e\u6027\u540d\u662f\u4e00\u4e2a Symbol \u503c\uff0c\u8be5\u5c5e\u6027\u4f1a\u88ab\u5ffd\u7565\u3002</p> <pre><code class="language-javascript">Object.entries({ [Symbol()]: 123, foo: &#39;abc&#39; });\n// [ [ &#39;foo&#39;, &#39;abc&#39; ] ]</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u539f\u5bf9\u8c61\u6709\u4e24\u4e2a\u5c5e\u6027\uff0c<code>Object.entries</code>\u53ea\u8f93\u51fa\u5c5e\u6027\u540d\u975e Symbol \u503c\u7684\u5c5e\u6027\u3002\u5c06\u6765\u53ef\u80fd\u4f1a\u6709<code>Reflect.ownEntries()</code>\u65b9\u6cd5\uff0c\u8fd4\u56de\u5bf9\u8c61\u81ea\u8eab\u7684\u6240\u6709\u5c5e\u6027\u3002</p> <p><code>Object.entries</code>\u7684\u57fa\u672c\u7528\u9014\u662f\u904d\u5386\u5bf9\u8c61\u7684\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">let obj = { one: 1, two: 2 };\nfor (let [k, v] of Object.entries(obj)) {\n  console.log(\n    `${JSON.stringify(k)}: ${JSON.stringify(v)}`\n  );\n}\n// &quot;one&quot;: 1\n// &quot;two&quot;: 2</code></pre> <p><code>Object.entries</code>\u65b9\u6cd5\u7684\u53e6\u4e00\u4e2a\u7528\u5904\u662f\uff0c\u5c06\u5bf9\u8c61\u8f6c\u4e3a\u771f\u6b63\u7684<code>Map</code>\u7ed3\u6784\u3002</p> <pre><code class="language-javascript">const obj = { foo: &#39;bar&#39;, baz: 42 };\nconst map = new Map(Object.entries(obj));\nmap // Map { foo: &quot;bar&quot;, baz: 42 }</code></pre> <p>\u81ea\u5df1\u5b9e\u73b0<code>Object.entries</code>\u65b9\u6cd5\uff0c\u975e\u5e38\u7b80\u5355\u3002</p> <pre><code class="language-javascript">// Generator\u51fd\u6570\u7684\u7248\u672c\nfunction* entries(obj) {\n  for (let key of Object.keys(obj)) {\n    yield [key, obj[key]];\n  }\n}\n\n// \u975eGenerator\u51fd\u6570\u7684\u7248\u672c\nfunction entries(obj) {\n  let arr = [];\n  for (let key of Object.keys(obj)) {\n    arr.push([key, obj[key]]);\n  }\n  return arr;\n}</code></pre> <h2 id="\u5bf9\u8c61\u7684\u6269\u5c55\u8fd0\u7b97\u7b26">\u5bf9\u8c61\u7684\u6269\u5c55\u8fd0\u7b97\u7b26</h2> <p>\u300a\u6570\u7ec4\u7684\u6269\u5c55\u300b\u4e00\u7ae0\u4e2d\uff0c\u5df2\u7ecf\u4ecb\u7ecd\u8fc7\u6269\u5c55\u8fd0\u7b97\u7b26\uff08<code>...</code>\uff09\u3002</p> <pre><code class="language-javascript">const [a, ...b] = [1, 2, 3];\na // 1\nb // [2, 3]</code></pre> <p>ES2017 \u5c06\u8fd9\u4e2a\u8fd0\u7b97\u7b26<a href="https://github.com/sebmarkbage/ecmascript-rest-spread">\u5f15\u5165</a>\u4e86\u5bf9\u8c61\u3002</p> <p><strong>\uff081\uff09\u89e3\u6784\u8d4b\u503c</strong></p> <p>\u5bf9\u8c61\u7684\u89e3\u6784\u8d4b\u503c\u7528\u4e8e\u4ece\u4e00\u4e2a\u5bf9\u8c61\u53d6\u503c\uff0c\u76f8\u5f53\u4e8e\u5c06\u6240\u6709\u53ef\u904d\u5386\u7684\u3001\u4f46\u5c1a\u672a\u88ab\u8bfb\u53d6\u7684\u5c5e\u6027\uff0c\u5206\u914d\u5230\u6307\u5b9a\u7684\u5bf9\u8c61\u4e0a\u9762\u3002\u6240\u6709\u7684\u952e\u548c\u5b83\u4eec\u7684\u503c\uff0c\u90fd\u4f1a\u62f7\u8d1d\u5230\u65b0\u5bf9\u8c61\u4e0a\u9762\u3002</p> <pre><code class="language-javascript">let { x, y, ...z } = { x: 1, y: 2, a: 3, b: 4 };\nx // 1\ny // 2\nz // { a: 3, b: 4 }</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53d8\u91cf<code>z</code>\u662f\u89e3\u6784\u8d4b\u503c\u6240\u5728\u7684\u5bf9\u8c61\u3002\u5b83\u83b7\u53d6\u7b49\u53f7\u53f3\u8fb9\u7684\u6240\u6709\u5c1a\u672a\u8bfb\u53d6\u7684\u952e\uff08<code>a</code>\u548c<code>b</code>\uff09\uff0c\u5c06\u5b83\u4eec\u8fde\u540c\u503c\u4e00\u8d77\u62f7\u8d1d\u8fc7\u6765\u3002</p> <p>\u7531\u4e8e\u89e3\u6784\u8d4b\u503c\u8981\u6c42\u7b49\u53f7\u53f3\u8fb9\u662f\u4e00\u4e2a\u5bf9\u8c61\uff0c\u6240\u4ee5\u5982\u679c\u7b49\u53f7\u53f3\u8fb9\u662f<code>undefined</code>\u6216<code>null</code>\uff0c\u5c31\u4f1a\u62a5\u9519\uff0c\u56e0\u4e3a\u5b83\u4eec\u65e0\u6cd5\u8f6c\u4e3a\u5bf9\u8c61\u3002</p> <pre><code class="language-javascript">let { x, y, ...z } = null; // \u8fd0\u884c\u65f6\u9519\u8bef\nlet { x, y, ...z } = undefined; // \u8fd0\u884c\u65f6\u9519\u8bef</code></pre> <p>\u89e3\u6784\u8d4b\u503c\u5fc5\u987b\u662f\u6700\u540e\u4e00\u4e2a\u53c2\u6570\uff0c\u5426\u5219\u4f1a\u62a5\u9519\u3002</p> <pre><code class="language-javascript">let { ...x, y, z } = obj; // \u53e5\u6cd5\u9519\u8bef\nlet { x, ...y, ...z } = obj; // \u53e5\u6cd5\u9519\u8bef</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u89e3\u6784\u8d4b\u503c\u4e0d\u662f\u6700\u540e\u4e00\u4e2a\u53c2\u6570\uff0c\u6240\u4ee5\u4f1a\u62a5\u9519\u3002</p> <p>\u6ce8\u610f\uff0c\u89e3\u6784\u8d4b\u503c\u7684\u62f7\u8d1d\u662f\u6d45\u62f7\u8d1d\uff0c\u5373\u5982\u679c\u4e00\u4e2a\u952e\u7684\u503c\u662f\u590d\u5408\u7c7b\u578b\u7684\u503c\uff08\u6570\u7ec4\u3001\u5bf9\u8c61\u3001\u51fd\u6570\uff09\u3001\u90a3\u4e48\u89e3\u6784\u8d4b\u503c\u62f7\u8d1d\u7684\u662f\u8fd9\u4e2a\u503c\u7684\u5f15\u7528\uff0c\u800c\u4e0d\u662f\u8fd9\u4e2a\u503c\u7684\u526f\u672c\u3002</p> <pre><code class="language-javascript">let obj = { a: { b: 1 } };\nlet { ...x } = obj;\nobj.a.b = 2;\nx.a.b // 2</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>x</code>\u662f\u89e3\u6784\u8d4b\u503c\u6240\u5728\u7684\u5bf9\u8c61\uff0c\u62f7\u8d1d\u4e86\u5bf9\u8c61<code>obj</code>\u7684<code>a</code>\u5c5e\u6027\u3002<code>a</code>\u5c5e\u6027\u5f15\u7528\u4e86\u4e00\u4e2a\u5bf9\u8c61\uff0c\u4fee\u6539\u8fd9\u4e2a\u5bf9\u8c61\u7684\u503c\uff0c\u4f1a\u5f71\u54cd\u5230\u89e3\u6784\u8d4b\u503c\u5bf9\u5b83\u7684\u5f15\u7528\u3002</p> <p>\u53e6\u5916\uff0c\u6269\u5c55\u8fd0\u7b97\u7b26\u7684\u89e3\u6784\u8d4b\u503c\uff0c\u4e0d\u80fd\u590d\u5236\u7ee7\u627f\u81ea\u539f\u578b\u5bf9\u8c61\u7684\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">let o1 = { a: 1 };\nlet o2 = { b: 2 };\no2.__proto__ = o1;\nlet { ...o3 } = o2;\no3 // { b: 2 }\no3.a // undefined</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5bf9\u8c61<code>o3</code>\u590d\u5236\u4e86<code>o2</code>\uff0c\u4f46\u662f\u53ea\u590d\u5236\u4e86<code>o2</code>\u81ea\u8eab\u7684\u5c5e\u6027\uff0c\u6ca1\u6709\u590d\u5236\u5b83\u7684\u539f\u578b\u5bf9\u8c61<code>o1</code>\u7684\u5c5e\u6027\u3002</p> <p>\u4e0b\u9762\u662f\u53e6\u4e00\u4e2a\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">const o = Object.create({ x: 1, y: 2 });\no.z = 3;\n\nlet { x, ...{ y, z } } = o;\nx // 1\ny // undefined\nz // 3</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53d8\u91cf<code>x</code>\u662f\u5355\u7eaf\u7684\u89e3\u6784\u8d4b\u503c\uff0c\u6240\u4ee5\u53ef\u4ee5\u8bfb\u53d6\u5bf9\u8c61<code>o</code>\u7ee7\u627f\u7684\u5c5e\u6027\uff1b\u53d8\u91cf<code>y</code>\u548c<code>z</code>\u662f\u6269\u5c55\u8fd0\u7b97\u7b26\u7684\u89e3\u6784\u8d4b\u503c\uff0c\u53ea\u80fd\u8bfb\u53d6\u5bf9\u8c61<code>o</code>\u81ea\u8eab\u7684\u5c5e\u6027\uff0c\u6240\u4ee5\u53d8\u91cf<code>z</code>\u53ef\u4ee5\u8d4b\u503c\u6210\u529f\uff0c\u53d8\u91cf<code>y</code>\u53d6\u4e0d\u5230\u503c\u3002</p> <p>\u89e3\u6784\u8d4b\u503c\u7684\u4e00\u4e2a\u7528\u5904\uff0c\u662f\u6269\u5c55\u67d0\u4e2a\u51fd\u6570\u7684\u53c2\u6570\uff0c\u5f15\u5165\u5176\u4ed6\u64cd\u4f5c\u3002</p> <pre><code class="language-javascript">function baseFunction({ a, b }) {\n  // ...\n}\nfunction wrapperFunction({ x, y, ...restConfig }) {\n  // \u4f7f\u7528x\u548cy\u53c2\u6570\u8fdb\u884c\u64cd\u4f5c\n  // \u5176\u4f59\u53c2\u6570\u4f20\u7ed9\u539f\u59cb\u51fd\u6570\n  return baseFunction(restConfig);\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u539f\u59cb\u51fd\u6570<code>baseFunction</code>\u63a5\u53d7<code>a</code>\u548c<code>b</code>\u4f5c\u4e3a\u53c2\u6570\uff0c\u51fd\u6570<code>wrapperFunction</code>\u5728<code>baseFunction</code>\u7684\u57fa\u7840\u4e0a\u8fdb\u884c\u4e86\u6269\u5c55\uff0c\u80fd\u591f\u63a5\u53d7\u591a\u4f59\u7684\u53c2\u6570\uff0c\u5e76\u4e14\u4fdd\u7559\u539f\u59cb\u51fd\u6570\u7684\u884c\u4e3a\u3002</p> <p><strong>\uff082\uff09\u6269\u5c55\u8fd0\u7b97\u7b26</strong></p> <p>\u6269\u5c55\u8fd0\u7b97\u7b26\uff08<code>...</code>\uff09\u7528\u4e8e\u53d6\u51fa\u53c2\u6570\u5bf9\u8c61\u7684\u6240\u6709\u53ef\u904d\u5386\u5c5e\u6027\uff0c\u62f7\u8d1d\u5230\u5f53\u524d\u5bf9\u8c61\u4e4b\u4e2d\u3002</p> <pre><code class="language-javascript">let z = { a: 3, b: 4 };\nlet n = { ...z };\nn // { a: 3, b: 4 }</code></pre> <p>\u8fd9\u7b49\u540c\u4e8e\u4f7f\u7528<code>Object.assign</code>\u65b9\u6cd5\u3002</p> <pre><code class="language-javascript">let aClone = { ...a };\n// \u7b49\u540c\u4e8e\nlet aClone = Object.assign({}, a);</code></pre> <p>\u4e0a\u9762\u7684\u4f8b\u5b50\u53ea\u662f\u62f7\u8d1d\u4e86\u5bf9\u8c61\u5b9e\u4f8b\u7684\u5c5e\u6027\uff0c\u5982\u679c\u60f3\u5b8c\u6574\u514b\u9686\u4e00\u4e2a\u5bf9\u8c61\uff0c\u8fd8\u62f7\u8d1d\u5bf9\u8c61\u539f\u578b\u7684\u5c5e\u6027\uff0c\u53ef\u4ee5\u91c7\u7528\u4e0b\u9762\u7684\u5199\u6cd5\u3002</p> <pre><code class="language-javascript">// \u5199\u6cd5\u4e00\nconst clone1 = {\n  __proto__: Object.getPrototypeOf(obj),\n  ...obj\n};\n\n// \u5199\u6cd5\u4e8c\nconst clone2 = Object.assign(\n  Object.create(Object.getPrototypeOf(obj)),\n  obj\n);\n\n// \u5199\u6cd5\u4e09\nconst clone3 = Object.create(\n  Object.getPrototypeOf(obj),\n  Object.getOwnPropertyDescriptors(obj)\n)</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5199\u6cd5\u4e00\u7684<code>__proto__</code>\u5c5e\u6027\u5728\u975e\u6d4f\u89c8\u5668\u7684\u73af\u5883\u4e0d\u4e00\u5b9a\u90e8\u7f72\uff0c\u56e0\u6b64\u63a8\u8350\u4f7f\u7528\u5199\u6cd5\u4e8c\u548c\u5199\u6cd5\u4e09\u3002</p> <p>\u6269\u5c55\u8fd0\u7b97\u7b26\u53ef\u4ee5\u7528\u4e8e\u5408\u5e76\u4e24\u4e2a\u5bf9\u8c61\u3002</p> <pre><code class="language-javascript">let ab = { ...a, ...b };\n// \u7b49\u540c\u4e8e\nlet ab = Object.assign({}, a, b);</code></pre> <p>\u5982\u679c\u7528\u6237\u81ea\u5b9a\u4e49\u7684\u5c5e\u6027\uff0c\u653e\u5728\u6269\u5c55\u8fd0\u7b97\u7b26\u540e\u9762\uff0c\u5219\u6269\u5c55\u8fd0\u7b97\u7b26\u5185\u90e8\u7684\u540c\u540d\u5c5e\u6027\u4f1a\u88ab\u8986\u76d6\u6389\u3002</p> <pre><code class="language-javascript">let aWithOverrides = { ...a, x: 1, y: 2 };\n// \u7b49\u540c\u4e8e\nlet aWithOverrides = { ...a, ...{ x: 1, y: 2 } };\n// \u7b49\u540c\u4e8e\nlet x = 1, y = 2, aWithOverrides = { ...a, x, y };\n// \u7b49\u540c\u4e8e\nlet aWithOverrides = Object.assign({}, a, { x: 1, y: 2 });</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>a</code>\u5bf9\u8c61\u7684<code>x</code>\u5c5e\u6027\u548c<code>y</code>\u5c5e\u6027\uff0c\u62f7\u8d1d\u5230\u65b0\u5bf9\u8c61\u540e\u4f1a\u88ab\u8986\u76d6\u6389\u3002</p> <p>\u8fd9\u7528\u6765\u4fee\u6539\u73b0\u6709\u5bf9\u8c61\u90e8\u5206\u7684\u5c5e\u6027\u5c31\u5f88\u65b9\u4fbf\u4e86\u3002</p> <pre><code class="language-javascript">let newVersion = {\n  ...previousVersion,\n  name: &#39;New Name&#39; // Override the name property\n};</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>newVersion</code>\u5bf9\u8c61\u81ea\u5b9a\u4e49\u4e86<code>name</code>\u5c5e\u6027\uff0c\u5176\u4ed6\u5c5e\u6027\u5168\u90e8\u590d\u5236\u81ea<code>previousVersion</code>\u5bf9\u8c61\u3002</p> <p>\u5982\u679c\u628a\u81ea\u5b9a\u4e49\u5c5e\u6027\u653e\u5728\u6269\u5c55\u8fd0\u7b97\u7b26\u524d\u9762\uff0c\u5c31\u53d8\u6210\u4e86\u8bbe\u7f6e\u65b0\u5bf9\u8c61\u7684\u9ed8\u8ba4\u5c5e\u6027\u503c\u3002</p> <pre><code class="language-javascript">let aWithDefaults = { x: 1, y: 2, ...a };\n// \u7b49\u540c\u4e8e\nlet aWithDefaults = Object.assign({}, { x: 1, y: 2 }, a);\n// \u7b49\u540c\u4e8e\nlet aWithDefaults = Object.assign({ x: 1, y: 2 }, a);</code></pre> <p>\u4e0e\u6570\u7ec4\u7684\u6269\u5c55\u8fd0\u7b97\u7b26\u4e00\u6837\uff0c\u5bf9\u8c61\u7684\u6269\u5c55\u8fd0\u7b97\u7b26\u540e\u9762\u53ef\u4ee5\u8ddf\u8868\u8fbe\u5f0f\u3002</p> <pre><code class="language-javascript">const obj = {\n  ...(x &gt; 1 ? {a: 1} : {}),\n  b: 2,\n};</code></pre> <p>\u5982\u679c\u6269\u5c55\u8fd0\u7b97\u7b26\u540e\u9762\u662f\u4e00\u4e2a\u7a7a\u5bf9\u8c61\uff0c\u5219\u6ca1\u6709\u4efb\u4f55\u6548\u679c\u3002</p> <pre><code class="language-javascript">{...{}, a: 1}\n// { a: 1 }</code></pre> <p>\u5982\u679c\u6269\u5c55\u8fd0\u7b97\u7b26\u7684\u53c2\u6570\u662f<code>null</code>\u6216<code>undefined</code>\uff0c\u8fd9\u4e24\u4e2a\u503c\u4f1a\u88ab\u5ffd\u7565\uff0c\u4e0d\u4f1a\u62a5\u9519\u3002</p> <pre><code class="language-javascript">let emptyObject = { ...null, ...undefined }; // \u4e0d\u62a5\u9519</code></pre> <p>\u6269\u5c55\u8fd0\u7b97\u7b26\u7684\u53c2\u6570\u5bf9\u8c61\u4e4b\u4e2d\uff0c\u5982\u679c\u6709\u53d6\u503c\u51fd\u6570<code>get</code>\uff0c\u8fd9\u4e2a\u51fd\u6570\u662f\u4f1a\u6267\u884c\u7684\u3002</p> <pre><code class="language-javascript">// \u5e76\u4e0d\u4f1a\u629b\u51fa\u9519\u8bef\uff0c\u56e0\u4e3a x \u5c5e\u6027\u53ea\u662f\u88ab\u5b9a\u4e49\uff0c\u4f46\u6ca1\u6267\u884c\nlet aWithXGetter = {\n  ...a,\n  get x() {\n    throw new Error(&#39;not throw yet&#39;);\n  }\n};\n\n// \u4f1a\u629b\u51fa\u9519\u8bef\uff0c\u56e0\u4e3a x \u5c5e\u6027\u88ab\u6267\u884c\u4e86\nlet runtimeError = {\n  ...a,\n  ...{\n    get x() {\n      throw new Error(&#39;throw now&#39;);\n    }\n  }\n};</code></pre> <h2 id="null-\u4f20\u5bfc\u8fd0\u7b97\u7b26">Null \u4f20\u5bfc\u8fd0\u7b97\u7b26</h2> <p>\u7f16\u7a0b\u5b9e\u52a1\u4e2d\uff0c\u5982\u679c\u8bfb\u53d6\u5bf9\u8c61\u5185\u90e8\u7684\u67d0\u4e2a\u5c5e\u6027\uff0c\u5f80\u5f80\u9700\u8981\u5224\u65ad\u4e00\u4e0b\u8be5\u5bf9\u8c61\u662f\u5426\u5b58\u5728\u3002\u6bd4\u5982\uff0c\u8981\u8bfb\u53d6<code>message.body.user.firstName</code>\uff0c\u5b89\u5168\u7684\u5199\u6cd5\u662f\u5199\u6210\u4e0b\u9762\u8fd9\u6837\u3002</p> <pre><code class="language-javascript">const firstName = (message\n  &amp;&amp; message.body\n  &amp;&amp; message.body.user\n  &amp;&amp; message.body.user.firstName) || &#39;default&#39;;</code></pre> <p>\u8fd9\u6837\u7684\u5c42\u5c42\u5224\u65ad\u975e\u5e38\u9ebb\u70e6\uff0c\u56e0\u6b64\u73b0\u5728\u6709\u4e00\u4e2a<a href="https://github.com/claudepache/es-optional-chaining">\u63d0\u6848</a>\uff0c\u5f15\u5165\u4e86\u201cNull \u4f20\u5bfc\u8fd0\u7b97\u7b26\u201d\uff08null propagation operator\uff09<code>?.</code>\uff0c\u7b80\u5316\u4e0a\u9762\u7684\u5199\u6cd5\u3002</p> <pre><code class="language-javascript">const firstName = message?.body?.user?.firstName || &#39;default&#39;;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u6709\u4e09\u4e2a<code>?.</code>\u8fd0\u7b97\u7b26\uff0c\u53ea\u8981\u5176\u4e2d\u4e00\u4e2a\u8fd4\u56de<code>null</code>\u6216<code>undefined</code>\uff0c\u5c31\u4e0d\u518d\u5f80\u4e0b\u8fd0\u7b97\uff0c\u800c\u662f\u8fd4\u56de<code>undefined</code>\u3002</p> <p>\u201cNull \u4f20\u5bfc\u8fd0\u7b97\u7b26\u201d\u6709\u56db\u79cd\u7528\u6cd5\u3002</p> <ul> <li><code>obj?.prop</code> // \u8bfb\u53d6\u5bf9\u8c61\u5c5e\u6027</li> <li><code>obj?.[expr]</code> // \u540c\u4e0a</li> <li><code>func?.(...args)</code> // \u51fd\u6570\u6216\u5bf9\u8c61\u65b9\u6cd5\u7684\u8c03\u7528</li> <li><code>new C?.(...args)</code> // \u6784\u9020\u51fd\u6570\u7684\u8c03\u7528</li> </ul> <p>\u4f20\u5bfc\u8fd0\u7b97\u7b26\u4e4b\u6240\u4ee5\u5199\u6210<code>obj?.prop</code>\uff0c\u800c\u4e0d\u662f<code>obj?prop</code>\uff0c\u662f\u4e3a\u4e86\u65b9\u4fbf\u7f16\u8bd1\u5668\u80fd\u591f\u533a\u5206\u4e09\u5143\u8fd0\u7b97\u7b26<code>?:</code>\uff08\u6bd4\u5982<code>obj?prop:123</code>\uff09\u3002</p> <p>\u4e0b\u9762\u662f\u66f4\u591a\u7684\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">// \u5982\u679c a \u662f null \u6216 undefined, \u8fd4\u56de undefined\n// \u5426\u5219\u8fd4\u56de a.b.c().d\na?.b.c().d\n\n// \u5982\u679c a \u662f null \u6216 undefined\uff0c\u4e0b\u9762\u7684\u8bed\u53e5\u4e0d\u4ea7\u751f\u4efb\u4f55\u6548\u679c\n// \u5426\u5219\u6267\u884c a.b = 42\na?.b = 42\n\n// \u5982\u679c a \u662f null \u6216 undefined\uff0c\u4e0b\u9762\u7684\u8bed\u53e5\u4e0d\u4ea7\u751f\u4efb\u4f55\u6548\u679c\ndelete a?.b</code></pre> ';e.exports=c}}]);