(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["docs/es6/string"],{"7qgQ":function(e,t){var o='<h1 id="\u5b57\u7b26\u4e32\u7684\u6269\u5c55">\u5b57\u7b26\u4e32\u7684\u6269\u5c55</h1> <p>ES6 \u52a0\u5f3a\u4e86\u5bf9 Unicode \u7684\u652f\u6301\uff0c\u5e76\u4e14\u6269\u5c55\u4e86\u5b57\u7b26\u4e32\u5bf9\u8c61\u3002</p> <h2 id="\u5b57\u7b26\u7684-unicode-\u8868\u793a\u6cd5">\u5b57\u7b26\u7684 Unicode \u8868\u793a\u6cd5</h2> <p>JavaScript \u5141\u8bb8\u91c7\u7528<code>\\uxxxx</code>\u5f62\u5f0f\u8868\u793a\u4e00\u4e2a\u5b57\u7b26\uff0c\u5176\u4e2d<code>xxxx</code>\u8868\u793a\u5b57\u7b26\u7684 Unicode \u7801\u70b9\u3002</p> <pre><code class="language-javascript">&quot;\\u0061&quot;\n// &quot;a&quot;</code></pre> <p>\u4f46\u662f\uff0c\u8fd9\u79cd\u8868\u793a\u6cd5\u53ea\u9650\u4e8e\u7801\u70b9\u5728<code>\\u0000</code>~<code>\\uFFFF</code>\u4e4b\u95f4\u7684\u5b57\u7b26\u3002\u8d85\u51fa\u8fd9\u4e2a\u8303\u56f4\u7684\u5b57\u7b26\uff0c\u5fc5\u987b\u7528\u4e24\u4e2a\u53cc\u5b57\u8282\u7684\u5f62\u5f0f\u8868\u793a\u3002</p> <pre><code class="language-javascript">&quot;\\uD842\\uDFB7&quot;\n// &quot;\ud842\udfb7&quot;\n\n&quot;\\u20BB7&quot;\n// &quot; 7&quot;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u8868\u793a\uff0c\u5982\u679c\u76f4\u63a5\u5728<code>\\u</code>\u540e\u9762\u8ddf\u4e0a\u8d85\u8fc7<code>0xFFFF</code>\u7684\u6570\u503c\uff08\u6bd4\u5982<code>\\u20BB7</code>\uff09\uff0cJavaScript \u4f1a\u7406\u89e3\u6210<code>\\u20BB+7</code>\u3002\u7531\u4e8e<code>\\u20BB</code>\u662f\u4e00\u4e2a\u4e0d\u53ef\u6253\u5370\u5b57\u7b26\uff0c\u6240\u4ee5\u53ea\u4f1a\u663e\u793a\u4e00\u4e2a\u7a7a\u683c\uff0c\u540e\u9762\u8ddf\u7740\u4e00\u4e2a<code>7</code>\u3002</p> <p>ES6 \u5bf9\u8fd9\u4e00\u70b9\u505a\u51fa\u4e86\u6539\u8fdb\uff0c\u53ea\u8981\u5c06\u7801\u70b9\u653e\u5165\u5927\u62ec\u53f7\uff0c\u5c31\u80fd\u6b63\u786e\u89e3\u8bfb\u8be5\u5b57\u7b26\u3002</p> <pre><code class="language-javascript">&quot;\\u{20BB7}&quot;\n// &quot;\ud842\udfb7&quot;\n\n&quot;\\u{41}\\u{42}\\u{43}&quot;\n// &quot;ABC&quot;\n\nlet hello = 123;\nhell\\u{6F} // 123\n\n&#39;\\u{1F680}&#39; === &#39;\\uD83D\\uDE80&#39;\n// true</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u6700\u540e\u4e00\u4e2a\u4f8b\u5b50\u8868\u660e\uff0c\u5927\u62ec\u53f7\u8868\u793a\u6cd5\u4e0e\u56db\u5b57\u8282\u7684 UTF-16 \u7f16\u7801\u662f\u7b49\u4ef7\u7684\u3002</p> <p>\u6709\u4e86\u8fd9\u79cd\u8868\u793a\u6cd5\u4e4b\u540e\uff0cJavaScript \u5171\u6709 6 \u79cd\u65b9\u6cd5\u53ef\u4ee5\u8868\u793a\u4e00\u4e2a\u5b57\u7b26\u3002</p> <pre><code class="language-javascript">&#39;\\z&#39; === &#39;z&#39;  // true\n&#39;\\172&#39; === &#39;z&#39; // true\n&#39;\\x7A&#39; === &#39;z&#39; // true\n&#39;\\u007A&#39; === &#39;z&#39; // true\n&#39;\\u{7A}&#39; === &#39;z&#39; // true</code></pre> <h2 id="codepointat">codePointAt()</h2> <p>JavaScript \u5185\u90e8\uff0c\u5b57\u7b26\u4ee5 UTF-16 \u7684\u683c\u5f0f\u50a8\u5b58\uff0c\u6bcf\u4e2a\u5b57\u7b26\u56fa\u5b9a\u4e3a<code>2</code>\u4e2a\u5b57\u8282\u3002\u5bf9\u4e8e\u90a3\u4e9b\u9700\u8981<code>4</code>\u4e2a\u5b57\u8282\u50a8\u5b58\u7684\u5b57\u7b26\uff08Unicode \u7801\u70b9\u5927\u4e8e<code>0xFFFF</code>\u7684\u5b57\u7b26\uff09\uff0cJavaScript \u4f1a\u8ba4\u4e3a\u5b83\u4eec\u662f\u4e24\u4e2a\u5b57\u7b26\u3002</p> <pre><code class="language-javascript">var s = &quot;\ud842\udfb7&quot;;\n\ns.length // 2\ns.charAt(0) // &#39;&#39;\ns.charAt(1) // &#39;&#39;\ns.charCodeAt(0) // 55362\ns.charCodeAt(1) // 57271</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u6c49\u5b57\u201c\ud842\udfb7\u201d\uff08\u6ce8\u610f\uff0c\u8fd9\u4e2a\u5b57\u4e0d\u662f\u201c\u5409\u7965\u201d\u7684\u201c\u5409\u201d\uff09\u7684\u7801\u70b9\u662f<code>0x20BB7</code>\uff0cUTF-16 \u7f16\u7801\u4e3a<code>0xD842 0xDFB7</code>\uff08\u5341\u8fdb\u5236\u4e3a<code>55362 57271</code>\uff09\uff0c\u9700\u8981<code>4</code>\u4e2a\u5b57\u8282\u50a8\u5b58\u3002\u5bf9\u4e8e\u8fd9\u79cd<code>4</code>\u4e2a\u5b57\u8282\u7684\u5b57\u7b26\uff0cJavaScript \u4e0d\u80fd\u6b63\u786e\u5904\u7406\uff0c\u5b57\u7b26\u4e32\u957f\u5ea6\u4f1a\u8bef\u5224\u4e3a<code>2</code>\uff0c\u800c\u4e14<code>charAt</code>\u65b9\u6cd5\u65e0\u6cd5\u8bfb\u53d6\u6574\u4e2a\u5b57\u7b26\uff0c<code>charCodeAt</code>\u65b9\u6cd5\u53ea\u80fd\u5206\u522b\u8fd4\u56de\u524d\u4e24\u4e2a\u5b57\u8282\u548c\u540e\u4e24\u4e2a\u5b57\u8282\u7684\u503c\u3002</p> <p>ES6 \u63d0\u4f9b\u4e86<code>codePointAt</code>\u65b9\u6cd5\uff0c\u80fd\u591f\u6b63\u786e\u5904\u7406 4 \u4e2a\u5b57\u8282\u50a8\u5b58\u7684\u5b57\u7b26\uff0c\u8fd4\u56de\u4e00\u4e2a\u5b57\u7b26\u7684\u7801\u70b9\u3002</p> <pre><code class="language-javascript">let s = &#39;\ud842\udfb7a&#39;;\n\ns.codePointAt(0) // 134071\ns.codePointAt(1) // 57271\n\ns.codePointAt(2) // 97</code></pre> <p><code>codePointAt</code>\u65b9\u6cd5\u7684\u53c2\u6570\uff0c\u662f\u5b57\u7b26\u5728\u5b57\u7b26\u4e32\u4e2d\u7684\u4f4d\u7f6e\uff08\u4ece 0 \u5f00\u59cb\uff09\u3002\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0cJavaScript \u5c06\u201c\ud842\udfb7a\u201d\u89c6\u4e3a\u4e09\u4e2a\u5b57\u7b26\uff0ccodePointAt \u65b9\u6cd5\u5728\u7b2c\u4e00\u4e2a\u5b57\u7b26\u4e0a\uff0c\u6b63\u786e\u5730\u8bc6\u522b\u4e86\u201c\ud842\udfb7\u201d\uff0c\u8fd4\u56de\u4e86\u5b83\u7684\u5341\u8fdb\u5236\u7801\u70b9 134071\uff08\u5373\u5341\u516d\u8fdb\u5236\u7684<code>20BB7</code>\uff09\u3002\u5728\u7b2c\u4e8c\u4e2a\u5b57\u7b26\uff08\u5373\u201c\ud842\udfb7\u201d\u7684\u540e\u4e24\u4e2a\u5b57\u8282\uff09\u548c\u7b2c\u4e09\u4e2a\u5b57\u7b26\u201ca\u201d\u4e0a\uff0c<code>codePointAt</code>\u65b9\u6cd5\u7684\u7ed3\u679c\u4e0e<code>charCodeAt</code>\u65b9\u6cd5\u76f8\u540c\u3002</p> <p>\u603b\u4e4b\uff0c<code>codePointAt</code>\u65b9\u6cd5\u4f1a\u6b63\u786e\u8fd4\u56de 32 \u4f4d\u7684 UTF-16 \u5b57\u7b26\u7684\u7801\u70b9\u3002\u5bf9\u4e8e\u90a3\u4e9b\u4e24\u4e2a\u5b57\u8282\u50a8\u5b58\u7684\u5e38\u89c4\u5b57\u7b26\uff0c\u5b83\u7684\u8fd4\u56de\u7ed3\u679c\u4e0e<code>charCodeAt</code>\u65b9\u6cd5\u76f8\u540c\u3002</p> <p><code>codePointAt</code>\u65b9\u6cd5\u8fd4\u56de\u7684\u662f\u7801\u70b9\u7684\u5341\u8fdb\u5236\u503c\uff0c\u5982\u679c\u60f3\u8981\u5341\u516d\u8fdb\u5236\u7684\u503c\uff0c\u53ef\u4ee5\u4f7f\u7528<code>toString</code>\u65b9\u6cd5\u8f6c\u6362\u4e00\u4e0b\u3002</p> <pre><code class="language-javascript">let s = &#39;\ud842\udfb7a&#39;;\n\ns.codePointAt(0).toString(16) // &quot;20bb7&quot;\ns.codePointAt(2).toString(16) // &quot;61&quot;</code></pre> <p>\u4f60\u53ef\u80fd\u6ce8\u610f\u5230\u4e86\uff0c<code>codePointAt</code>\u65b9\u6cd5\u7684\u53c2\u6570\uff0c\u4ecd\u7136\u662f\u4e0d\u6b63\u786e\u7684\u3002\u6bd4\u5982\uff0c\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5b57\u7b26<code>a</code>\u5728\u5b57\u7b26\u4e32<code>s</code>\u7684\u6b63\u786e\u4f4d\u7f6e\u5e8f\u53f7\u5e94\u8be5\u662f 1\uff0c\u4f46\u662f\u5fc5\u987b\u5411<code>codePointAt</code>\u65b9\u6cd5\u4f20\u5165 2\u3002\u89e3\u51b3\u8fd9\u4e2a\u95ee\u9898\u7684\u4e00\u4e2a\u529e\u6cd5\u662f\u4f7f\u7528<code>for...of</code>\u5faa\u73af\uff0c\u56e0\u4e3a\u5b83\u4f1a\u6b63\u786e\u8bc6\u522b 32 \u4f4d\u7684 UTF-16 \u5b57\u7b26\u3002</p> <pre><code class="language-javascript">let s = &#39;\ud842\udfb7a&#39;;\nfor (let ch of s) {\n  console.log(ch.codePointAt(0).toString(16));\n}\n// 20bb7\n// 61</code></pre> <p><code>codePointAt</code>\u65b9\u6cd5\u662f\u6d4b\u8bd5\u4e00\u4e2a\u5b57\u7b26\u7531\u4e24\u4e2a\u5b57\u8282\u8fd8\u662f\u7531\u56db\u4e2a\u5b57\u8282\u7ec4\u6210\u7684\u6700\u7b80\u5355\u65b9\u6cd5\u3002</p> <pre><code class="language-javascript">function is32Bit(c) {\n  return c.codePointAt(0) &gt; 0xFFFF;\n}\n\nis32Bit(&quot;\ud842\udfb7&quot;) // true\nis32Bit(&quot;a&quot;) // false</code></pre> <h2 id="stringfromcodepoint">String.fromCodePoint()</h2> <p>ES5 \u63d0\u4f9b<code>String.fromCharCode</code>\u65b9\u6cd5\uff0c\u7528\u4e8e\u4ece\u7801\u70b9\u8fd4\u56de\u5bf9\u5e94\u5b57\u7b26\uff0c\u4f46\u662f\u8fd9\u4e2a\u65b9\u6cd5\u4e0d\u80fd\u8bc6\u522b 32 \u4f4d\u7684 UTF-16 \u5b57\u7b26\uff08Unicode \u7f16\u53f7\u5927\u4e8e<code>0xFFFF</code>\uff09\u3002</p> <pre><code class="language-javascript">String.fromCharCode(0x20BB7)\n// &quot;\u0bb7&quot;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>String.fromCharCode</code>\u4e0d\u80fd\u8bc6\u522b\u5927\u4e8e<code>0xFFFF</code>\u7684\u7801\u70b9\uff0c\u6240\u4ee5<code>0x20BB7</code>\u5c31\u53d1\u751f\u4e86\u6ea2\u51fa\uff0c\u6700\u9ad8\u4f4d<code>2</code>\u88ab\u820d\u5f03\u4e86\uff0c\u6700\u540e\u8fd4\u56de\u7801\u70b9<code>U+0BB7</code>\u5bf9\u5e94\u7684\u5b57\u7b26\uff0c\u800c\u4e0d\u662f\u7801\u70b9<code>U+20BB7</code>\u5bf9\u5e94\u7684\u5b57\u7b26\u3002</p> <p>ES6 \u63d0\u4f9b\u4e86<code>String.fromCodePoint</code>\u65b9\u6cd5\uff0c\u53ef\u4ee5\u8bc6\u522b\u5927\u4e8e<code>0xFFFF</code>\u7684\u5b57\u7b26\uff0c\u5f25\u8865\u4e86<code>String.fromCharCode</code>\u65b9\u6cd5\u7684\u4e0d\u8db3\u3002\u5728\u4f5c\u7528\u4e0a\uff0c\u6b63\u597d\u4e0e<code>codePointAt</code>\u65b9\u6cd5\u76f8\u53cd\u3002</p> <pre><code class="language-javascript">String.fromCodePoint(0x20BB7)\n// &quot;\ud842\udfb7&quot;\nString.fromCodePoint(0x78, 0x1f680, 0x79) === &#39;x\\uD83D\\uDE80y&#39;\n// true</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5982\u679c<code>String.fromCodePoint</code>\u65b9\u6cd5\u6709\u591a\u4e2a\u53c2\u6570\uff0c\u5219\u5b83\u4eec\u4f1a\u88ab\u5408\u5e76\u6210\u4e00\u4e2a\u5b57\u7b26\u4e32\u8fd4\u56de\u3002</p> <p>\u6ce8\u610f\uff0c<code>fromCodePoint</code>\u65b9\u6cd5\u5b9a\u4e49\u5728<code>String</code>\u5bf9\u8c61\u4e0a\uff0c\u800c<code>codePointAt</code>\u65b9\u6cd5\u5b9a\u4e49\u5728\u5b57\u7b26\u4e32\u7684\u5b9e\u4f8b\u5bf9\u8c61\u4e0a\u3002</p> <h2 id="\u5b57\u7b26\u4e32\u7684\u904d\u5386\u5668\u63a5\u53e3">\u5b57\u7b26\u4e32\u7684\u904d\u5386\u5668\u63a5\u53e3</h2> <p>ES6 \u4e3a\u5b57\u7b26\u4e32\u6dfb\u52a0\u4e86\u904d\u5386\u5668\u63a5\u53e3\uff08\u8be6\u89c1\u300aIterator\u300b\u4e00\u7ae0\uff09\uff0c\u4f7f\u5f97\u5b57\u7b26\u4e32\u53ef\u4ee5\u88ab<code>for...of</code>\u5faa\u73af\u904d\u5386\u3002</p> <pre><code class="language-javascript">for (let codePoint of &#39;foo&#39;) {\n  console.log(codePoint)\n}\n// &quot;f&quot;\n// &quot;o&quot;\n// &quot;o&quot;</code></pre> <p>\u9664\u4e86\u904d\u5386\u5b57\u7b26\u4e32\uff0c\u8fd9\u4e2a\u904d\u5386\u5668\u6700\u5927\u7684\u4f18\u70b9\u662f\u53ef\u4ee5\u8bc6\u522b\u5927\u4e8e<code>0xFFFF</code>\u7684\u7801\u70b9\uff0c\u4f20\u7edf\u7684<code>for</code>\u5faa\u73af\u65e0\u6cd5\u8bc6\u522b\u8fd9\u6837\u7684\u7801\u70b9\u3002</p> <pre><code class="language-javascript">let text = String.fromCodePoint(0x20BB7);\n\nfor (let i = 0; i &lt; text.length; i++) {\n  console.log(text[i]);\n}\n// &quot; &quot;\n// &quot; &quot;\n\nfor (let i of text) {\n  console.log(i);\n}\n// &quot;\ud842\udfb7&quot;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u5b57\u7b26\u4e32<code>text</code>\u53ea\u6709\u4e00\u4e2a\u5b57\u7b26\uff0c\u4f46\u662f<code>for</code>\u5faa\u73af\u4f1a\u8ba4\u4e3a\u5b83\u5305\u542b\u4e24\u4e2a\u5b57\u7b26\uff08\u90fd\u4e0d\u53ef\u6253\u5370\uff09\uff0c\u800c<code>for...of</code>\u5faa\u73af\u4f1a\u6b63\u786e\u8bc6\u522b\u51fa\u8fd9\u4e00\u4e2a\u5b57\u7b26\u3002</p> <h2 id="at">at()</h2> <p>ES5 \u5bf9\u5b57\u7b26\u4e32\u5bf9\u8c61\u63d0\u4f9b<code>charAt</code>\u65b9\u6cd5\uff0c\u8fd4\u56de\u5b57\u7b26\u4e32\u7ed9\u5b9a\u4f4d\u7f6e\u7684\u5b57\u7b26\u3002\u8be5\u65b9\u6cd5\u4e0d\u80fd\u8bc6\u522b\u7801\u70b9\u5927\u4e8e<code>0xFFFF</code>\u7684\u5b57\u7b26\u3002</p> <pre><code class="language-javascript">&#39;abc&#39;.charAt(0) // &quot;a&quot;\n&#39;\ud842\udfb7&#39;.charAt(0) // &quot;\\uD842&quot;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>charAt</code>\u65b9\u6cd5\u8fd4\u56de\u7684\u662f UTF-16 \u7f16\u7801\u7684\u7b2c\u4e00\u4e2a\u5b57\u8282\uff0c\u5b9e\u9645\u4e0a\u662f\u65e0\u6cd5\u663e\u793a\u7684\u3002</p> <p>\u76ee\u524d\uff0c\u6709\u4e00\u4e2a\u63d0\u6848\uff0c\u63d0\u51fa\u5b57\u7b26\u4e32\u5b9e\u4f8b\u7684<code>at</code>\u65b9\u6cd5\uff0c\u53ef\u4ee5\u8bc6\u522b Unicode \u7f16\u53f7\u5927\u4e8e<code>0xFFFF</code>\u7684\u5b57\u7b26\uff0c\u8fd4\u56de\u6b63\u786e\u7684\u5b57\u7b26\u3002</p> <pre><code class="language-javascript">&#39;abc&#39;.at(0) // &quot;a&quot;\n&#39;\ud842\udfb7&#39;.at(0) // &quot;\ud842\udfb7&quot;</code></pre> <p>\u8fd9\u4e2a\u65b9\u6cd5\u53ef\u4ee5\u901a\u8fc7<a href="https://github.com/es-shims/String.prototype.at">\u57ab\u7247\u5e93</a>\u5b9e\u73b0\u3002</p> <h2 id="normalize">normalize()</h2> <p>\u8bb8\u591a\u6b27\u6d32\u8bed\u8a00\u6709\u8bed\u8c03\u7b26\u53f7\u548c\u91cd\u97f3\u7b26\u53f7\u3002\u4e3a\u4e86\u8868\u793a\u5b83\u4eec\uff0cUnicode \u63d0\u4f9b\u4e86\u4e24\u79cd\u65b9\u6cd5\u3002\u4e00\u79cd\u662f\u76f4\u63a5\u63d0\u4f9b\u5e26\u91cd\u97f3\u7b26\u53f7\u7684\u5b57\u7b26\uff0c\u6bd4\u5982<code>\u01d1</code>\uff08\\u01D1\uff09\u3002\u53e6\u4e00\u79cd\u662f\u63d0\u4f9b\u5408\u6210\u7b26\u53f7\uff08combining character\uff09\uff0c\u5373\u539f\u5b57\u7b26\u4e0e\u91cd\u97f3\u7b26\u53f7\u7684\u5408\u6210\uff0c\u4e24\u4e2a\u5b57\u7b26\u5408\u6210\u4e00\u4e2a\u5b57\u7b26\uff0c\u6bd4\u5982<code>O</code>\uff08\\u004F\uff09\u548c<code>\u02c7</code>\uff08\\u030C\uff09\u5408\u6210<code>\u01d1</code>\uff08\\u004F\\u030C\uff09\u3002</p> <p>\u8fd9\u4e24\u79cd\u8868\u793a\u65b9\u6cd5\uff0c\u5728\u89c6\u89c9\u548c\u8bed\u4e49\u4e0a\u90fd\u7b49\u4ef7\uff0c\u4f46\u662f JavaScript \u4e0d\u80fd\u8bc6\u522b\u3002</p> <pre><code class="language-javascript">&#39;\\u01D1&#39;===&#39;\\u004F\\u030C&#39; //false\n\n&#39;\\u01D1&#39;.length // 1\n&#39;\\u004F\\u030C&#39;.length // 2</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u8868\u793a\uff0cJavaScript \u5c06\u5408\u6210\u5b57\u7b26\u89c6\u4e3a\u4e24\u4e2a\u5b57\u7b26\uff0c\u5bfc\u81f4\u4e24\u79cd\u8868\u793a\u65b9\u6cd5\u4e0d\u76f8\u7b49\u3002</p> <p>ES6 \u63d0\u4f9b\u5b57\u7b26\u4e32\u5b9e\u4f8b\u7684<code>normalize()</code>\u65b9\u6cd5\uff0c\u7528\u6765\u5c06\u5b57\u7b26\u7684\u4e0d\u540c\u8868\u793a\u65b9\u6cd5\u7edf\u4e00\u4e3a\u540c\u6837\u7684\u5f62\u5f0f\uff0c\u8fd9\u79f0\u4e3a Unicode \u6b63\u89c4\u5316\u3002</p> <pre><code class="language-javascript">&#39;\\u01D1&#39;.normalize() === &#39;\\u004F\\u030C&#39;.normalize()\n// true</code></pre> <p><code>normalize</code>\u65b9\u6cd5\u53ef\u4ee5\u63a5\u53d7\u4e00\u4e2a\u53c2\u6570\u6765\u6307\u5b9a<code>normalize</code>\u7684\u65b9\u5f0f\uff0c\u53c2\u6570\u7684\u56db\u4e2a\u53ef\u9009\u503c\u5982\u4e0b\u3002</p> <ul> <li><code>NFC</code>\uff0c\u9ed8\u8ba4\u53c2\u6570\uff0c\u8868\u793a\u201c\u6807\u51c6\u7b49\u4ef7\u5408\u6210\u201d\uff08Normalization Form Canonical Composition\uff09\uff0c\u8fd4\u56de\u591a\u4e2a\u7b80\u5355\u5b57\u7b26\u7684\u5408\u6210\u5b57\u7b26\u3002\u6240\u8c13\u201c\u6807\u51c6\u7b49\u4ef7\u201d\u6307\u7684\u662f\u89c6\u89c9\u548c\u8bed\u4e49\u4e0a\u7684\u7b49\u4ef7\u3002</li> <li><code>NFD</code>\uff0c\u8868\u793a\u201c\u6807\u51c6\u7b49\u4ef7\u5206\u89e3\u201d\uff08Normalization Form Canonical Decomposition\uff09\uff0c\u5373\u5728\u6807\u51c6\u7b49\u4ef7\u7684\u524d\u63d0\u4e0b\uff0c\u8fd4\u56de\u5408\u6210\u5b57\u7b26\u5206\u89e3\u7684\u591a\u4e2a\u7b80\u5355\u5b57\u7b26\u3002</li> <li><code>NFKC</code>\uff0c\u8868\u793a\u201c\u517c\u5bb9\u7b49\u4ef7\u5408\u6210\u201d\uff08Normalization Form Compatibility Composition\uff09\uff0c\u8fd4\u56de\u5408\u6210\u5b57\u7b26\u3002\u6240\u8c13\u201c\u517c\u5bb9\u7b49\u4ef7\u201d\u6307\u7684\u662f\u8bed\u4e49\u4e0a\u5b58\u5728\u7b49\u4ef7\uff0c\u4f46\u89c6\u89c9\u4e0a\u4e0d\u7b49\u4ef7\uff0c\u6bd4\u5982\u201c\u56cd\u201d\u548c\u201c\u559c\u559c\u201d\u3002\uff08\u8fd9\u53ea\u662f\u7528\u6765\u4e3e\u4f8b\uff0c<code>normalize</code>\u65b9\u6cd5\u4e0d\u80fd\u8bc6\u522b\u4e2d\u6587\u3002\uff09</li> <li><code>NFKD</code>\uff0c\u8868\u793a\u201c\u517c\u5bb9\u7b49\u4ef7\u5206\u89e3\u201d\uff08Normalization Form Compatibility Decomposition\uff09\uff0c\u5373\u5728\u517c\u5bb9\u7b49\u4ef7\u7684\u524d\u63d0\u4e0b\uff0c\u8fd4\u56de\u5408\u6210\u5b57\u7b26\u5206\u89e3\u7684\u591a\u4e2a\u7b80\u5355\u5b57\u7b26\u3002</li> </ul> <pre><code class="language-javascript">&#39;\\u004F\\u030C&#39;.normalize(&#39;NFC&#39;).length // 1\n&#39;\\u004F\\u030C&#39;.normalize(&#39;NFD&#39;).length // 2</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u8868\u793a\uff0c<code>NFC</code>\u53c2\u6570\u8fd4\u56de\u5b57\u7b26\u7684\u5408\u6210\u5f62\u5f0f\uff0c<code>NFD</code>\u53c2\u6570\u8fd4\u56de\u5b57\u7b26\u7684\u5206\u89e3\u5f62\u5f0f\u3002</p> <p>\u4e0d\u8fc7\uff0c<code>normalize</code>\u65b9\u6cd5\u76ee\u524d\u4e0d\u80fd\u8bc6\u522b\u4e09\u4e2a\u6216\u4e09\u4e2a\u4ee5\u4e0a\u5b57\u7b26\u7684\u5408\u6210\u3002\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u8fd8\u662f\u53ea\u80fd\u4f7f\u7528\u6b63\u5219\u8868\u8fbe\u5f0f\uff0c\u901a\u8fc7 Unicode \u7f16\u53f7\u533a\u95f4\u5224\u65ad\u3002</p> <h2 id="includes-startswith-endswith">includes(), startsWith(), endsWith()</h2> <p>\u4f20\u7edf\u4e0a\uff0cJavaScript \u53ea\u6709<code>indexOf</code>\u65b9\u6cd5\uff0c\u53ef\u4ee5\u7528\u6765\u786e\u5b9a\u4e00\u4e2a\u5b57\u7b26\u4e32\u662f\u5426\u5305\u542b\u5728\u53e6\u4e00\u4e2a\u5b57\u7b26\u4e32\u4e2d\u3002ES6 \u53c8\u63d0\u4f9b\u4e86\u4e09\u79cd\u65b0\u65b9\u6cd5\u3002</p> <ul> <li><strong>includes()</strong>\uff1a\u8fd4\u56de\u5e03\u5c14\u503c\uff0c\u8868\u793a\u662f\u5426\u627e\u5230\u4e86\u53c2\u6570\u5b57\u7b26\u4e32\u3002</li> <li><strong>startsWith()</strong>\uff1a\u8fd4\u56de\u5e03\u5c14\u503c\uff0c\u8868\u793a\u53c2\u6570\u5b57\u7b26\u4e32\u662f\u5426\u5728\u539f\u5b57\u7b26\u4e32\u7684\u5934\u90e8\u3002</li> <li><strong>endsWith()</strong>\uff1a\u8fd4\u56de\u5e03\u5c14\u503c\uff0c\u8868\u793a\u53c2\u6570\u5b57\u7b26\u4e32\u662f\u5426\u5728\u539f\u5b57\u7b26\u4e32\u7684\u5c3e\u90e8\u3002</li> </ul> <pre><code class="language-javascript">let s = &#39;Hello world!&#39;;\n\ns.startsWith(&#39;Hello&#39;) // true\ns.endsWith(&#39;!&#39;) // true\ns.includes(&#39;o&#39;) // true</code></pre> <p>\u8fd9\u4e09\u4e2a\u65b9\u6cd5\u90fd\u652f\u6301\u7b2c\u4e8c\u4e2a\u53c2\u6570\uff0c\u8868\u793a\u5f00\u59cb\u641c\u7d22\u7684\u4f4d\u7f6e\u3002</p> <pre><code class="language-javascript">let s = &#39;Hello world!&#39;;\n\ns.startsWith(&#39;world&#39;, 6) // true\ns.endsWith(&#39;Hello&#39;, 5) // true\ns.includes(&#39;Hello&#39;, 6) // false</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u8868\u793a\uff0c\u4f7f\u7528\u7b2c\u4e8c\u4e2a\u53c2\u6570<code>n</code>\u65f6\uff0c<code>endsWith</code>\u7684\u884c\u4e3a\u4e0e\u5176\u4ed6\u4e24\u4e2a\u65b9\u6cd5\u6709\u6240\u4e0d\u540c\u3002\u5b83\u9488\u5bf9\u524d<code>n</code>\u4e2a\u5b57\u7b26\uff0c\u800c\u5176\u4ed6\u4e24\u4e2a\u65b9\u6cd5\u9488\u5bf9\u4ece\u7b2c<code>n</code>\u4e2a\u4f4d\u7f6e\u76f4\u5230\u5b57\u7b26\u4e32\u7ed3\u675f\u3002</p> <h2 id="repeat">repeat()</h2> <p><code>repeat</code>\u65b9\u6cd5\u8fd4\u56de\u4e00\u4e2a\u65b0\u5b57\u7b26\u4e32\uff0c\u8868\u793a\u5c06\u539f\u5b57\u7b26\u4e32\u91cd\u590d<code>n</code>\u6b21\u3002</p> <pre><code class="language-javascript">&#39;x&#39;.repeat(3) // &quot;xxx&quot;\n&#39;hello&#39;.repeat(2) // &quot;hellohello&quot;\n&#39;na&#39;.repeat(0) // &quot;&quot;</code></pre> <p>\u53c2\u6570\u5982\u679c\u662f\u5c0f\u6570\uff0c\u4f1a\u88ab\u53d6\u6574\u3002</p> <pre><code class="language-javascript">&#39;na&#39;.repeat(2.9) // &quot;nana&quot;</code></pre> <p>\u5982\u679c<code>repeat</code>\u7684\u53c2\u6570\u662f\u8d1f\u6570\u6216\u8005<code>Infinity</code>\uff0c\u4f1a\u62a5\u9519\u3002</p> <pre><code class="language-javascript">&#39;na&#39;.repeat(Infinity)\n// RangeError\n&#39;na&#39;.repeat(-1)\n// RangeError</code></pre> <p>\u4f46\u662f\uff0c\u5982\u679c\u53c2\u6570\u662f 0 \u5230-1 \u4e4b\u95f4\u7684\u5c0f\u6570\uff0c\u5219\u7b49\u540c\u4e8e 0\uff0c\u8fd9\u662f\u56e0\u4e3a\u4f1a\u5148\u8fdb\u884c\u53d6\u6574\u8fd0\u7b97\u30020 \u5230-1 \u4e4b\u95f4\u7684\u5c0f\u6570\uff0c\u53d6\u6574\u4ee5\u540e\u7b49\u4e8e<code>-0</code>\uff0c<code>repeat</code>\u89c6\u540c\u4e3a 0\u3002</p> <pre><code class="language-javascript">&#39;na&#39;.repeat(-0.9) // &quot;&quot;</code></pre> <p>\u53c2\u6570<code>NaN</code>\u7b49\u540c\u4e8e 0\u3002</p> <pre><code class="language-javascript">&#39;na&#39;.repeat(NaN) // &quot;&quot;</code></pre> <p>\u5982\u679c<code>repeat</code>\u7684\u53c2\u6570\u662f\u5b57\u7b26\u4e32\uff0c\u5219\u4f1a\u5148\u8f6c\u6362\u6210\u6570\u5b57\u3002</p> <pre><code class="language-javascript">&#39;na&#39;.repeat(&#39;na&#39;) // &quot;&quot;\n&#39;na&#39;.repeat(&#39;3&#39;) // &quot;nanana&quot;</code></pre> <h2 id="padstart\uff0cpadend">padStart()\uff0cpadEnd()</h2> <p>ES2017 \u5f15\u5165\u4e86\u5b57\u7b26\u4e32\u8865\u5168\u957f\u5ea6\u7684\u529f\u80fd\u3002\u5982\u679c\u67d0\u4e2a\u5b57\u7b26\u4e32\u4e0d\u591f\u6307\u5b9a\u957f\u5ea6\uff0c\u4f1a\u5728\u5934\u90e8\u6216\u5c3e\u90e8\u8865\u5168\u3002<code>padStart()</code>\u7528\u4e8e\u5934\u90e8\u8865\u5168\uff0c<code>padEnd()</code>\u7528\u4e8e\u5c3e\u90e8\u8865\u5168\u3002</p> <pre><code class="language-javascript">&#39;x&#39;.padStart(5, &#39;ab&#39;) // &#39;ababx&#39;\n&#39;x&#39;.padStart(4, &#39;ab&#39;) // &#39;abax&#39;\n\n&#39;x&#39;.padEnd(5, &#39;ab&#39;) // &#39;xabab&#39;\n&#39;x&#39;.padEnd(4, &#39;ab&#39;) // &#39;xaba&#39;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>padStart</code>\u548c<code>padEnd</code>\u4e00\u5171\u63a5\u53d7\u4e24\u4e2a\u53c2\u6570\uff0c\u7b2c\u4e00\u4e2a\u53c2\u6570\u7528\u6765\u6307\u5b9a\u5b57\u7b26\u4e32\u7684\u6700\u5c0f\u957f\u5ea6\uff0c\u7b2c\u4e8c\u4e2a\u53c2\u6570\u662f\u7528\u6765\u8865\u5168\u7684\u5b57\u7b26\u4e32\u3002</p> <p>\u5982\u679c\u539f\u5b57\u7b26\u4e32\u7684\u957f\u5ea6\uff0c\u7b49\u4e8e\u6216\u5927\u4e8e\u6307\u5b9a\u7684\u6700\u5c0f\u957f\u5ea6\uff0c\u5219\u8fd4\u56de\u539f\u5b57\u7b26\u4e32\u3002</p> <pre><code class="language-javascript">&#39;xxx&#39;.padStart(2, &#39;ab&#39;) // &#39;xxx&#39;\n&#39;xxx&#39;.padEnd(2, &#39;ab&#39;) // &#39;xxx&#39;</code></pre> <p>\u5982\u679c\u7528\u6765\u8865\u5168\u7684\u5b57\u7b26\u4e32\u4e0e\u539f\u5b57\u7b26\u4e32\uff0c\u4e24\u8005\u7684\u957f\u5ea6\u4e4b\u548c\u8d85\u8fc7\u4e86\u6307\u5b9a\u7684\u6700\u5c0f\u957f\u5ea6\uff0c\u5219\u4f1a\u622a\u53bb\u8d85\u51fa\u4f4d\u6570\u7684\u8865\u5168\u5b57\u7b26\u4e32\u3002</p> <pre><code class="language-javascript">&#39;abc&#39;.padStart(10, &#39;0123456789&#39;)\n// &#39;0123456abc&#39;</code></pre> <p>\u5982\u679c\u7701\u7565\u7b2c\u4e8c\u4e2a\u53c2\u6570\uff0c\u9ed8\u8ba4\u4f7f\u7528\u7a7a\u683c\u8865\u5168\u957f\u5ea6\u3002</p> <pre><code class="language-javascript">&#39;x&#39;.padStart(4) // &#39;   x&#39;\n&#39;x&#39;.padEnd(4) // &#39;x   &#39;</code></pre> <p><code>padStart</code>\u7684\u5e38\u89c1\u7528\u9014\u662f\u4e3a\u6570\u503c\u8865\u5168\u6307\u5b9a\u4f4d\u6570\u3002\u4e0b\u9762\u4ee3\u7801\u751f\u6210 10 \u4f4d\u7684\u6570\u503c\u5b57\u7b26\u4e32\u3002</p> <pre><code class="language-javascript">&#39;1&#39;.padStart(10, &#39;0&#39;) // &quot;0000000001&quot;\n&#39;12&#39;.padStart(10, &#39;0&#39;) // &quot;0000000012&quot;\n&#39;123456&#39;.padStart(10, &#39;0&#39;) // &quot;0000123456&quot;</code></pre> <p>\u53e6\u4e00\u4e2a\u7528\u9014\u662f\u63d0\u793a\u5b57\u7b26\u4e32\u683c\u5f0f\u3002</p> <pre><code class="language-javascript">&#39;12&#39;.padStart(10, &#39;YYYY-MM-DD&#39;) // &quot;YYYY-MM-12&quot;\n&#39;09-12&#39;.padStart(10, &#39;YYYY-MM-DD&#39;) // &quot;YYYY-09-12&quot;</code></pre> <h2 id="\u6a21\u677f\u5b57\u7b26\u4e32">\u6a21\u677f\u5b57\u7b26\u4e32</h2> <p>\u4f20\u7edf\u7684 JavaScript \u8bed\u8a00\uff0c\u8f93\u51fa\u6a21\u677f\u901a\u5e38\u662f\u8fd9\u6837\u5199\u7684\u3002</p> <pre><code class="language-javascript">$(&#39;#result&#39;).append(\n  &#39;There are &lt;b&gt;&#39; + basket.count + &#39;&lt;/b&gt; &#39; +\n  &#39;items in your basket, &#39; +\n  &#39;&lt;em&gt;&#39; + basket.onSale +\n  &#39;&lt;/em&gt; are on sale!&#39;\n);</code></pre> <p>\u4e0a\u9762\u8fd9\u79cd\u5199\u6cd5\u76f8\u5f53\u7e41\u7410\u4e0d\u65b9\u4fbf\uff0cES6 \u5f15\u5165\u4e86\u6a21\u677f\u5b57\u7b26\u4e32\u89e3\u51b3\u8fd9\u4e2a\u95ee\u9898\u3002</p> <pre><code class="language-javascript">$(&#39;#result&#39;).append(`\n  There are &lt;b&gt;${basket.count}&lt;/b&gt; items\n   in your basket, &lt;em&gt;${basket.onSale}&lt;/em&gt;\n  are on sale!\n`);</code></pre> <p>\u6a21\u677f\u5b57\u7b26\u4e32\uff08template string\uff09\u662f\u589e\u5f3a\u7248\u7684\u5b57\u7b26\u4e32\uff0c\u7528\u53cd\u5f15\u53f7\uff08&#96;\uff09\u6807\u8bc6\u3002\u5b83\u53ef\u4ee5\u5f53\u4f5c\u666e\u901a\u5b57\u7b26\u4e32\u4f7f\u7528\uff0c\u4e5f\u53ef\u4ee5\u7528\u6765\u5b9a\u4e49\u591a\u884c\u5b57\u7b26\u4e32\uff0c\u6216\u8005\u5728\u5b57\u7b26\u4e32\u4e2d\u5d4c\u5165\u53d8\u91cf\u3002</p> <pre><code class="language-javascript">// \u666e\u901a\u5b57\u7b26\u4e32\n`In JavaScript &#39;\\n&#39; is a line-feed.`\n\n// \u591a\u884c\u5b57\u7b26\u4e32\n`In JavaScript this is\n not legal.`\n\nconsole.log(`string text line 1\nstring text line 2`);\n\n// \u5b57\u7b26\u4e32\u4e2d\u5d4c\u5165\u53d8\u91cf\nlet name = &quot;Bob&quot;, time = &quot;today&quot;;\n`Hello ${name}, how are you ${time}?`</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\u7684\u6a21\u677f\u5b57\u7b26\u4e32\uff0c\u90fd\u662f\u7528\u53cd\u5f15\u53f7\u8868\u793a\u3002\u5982\u679c\u5728\u6a21\u677f\u5b57\u7b26\u4e32\u4e2d\u9700\u8981\u4f7f\u7528\u53cd\u5f15\u53f7\uff0c\u5219\u524d\u9762\u8981\u7528\u53cd\u659c\u6760\u8f6c\u4e49\u3002</p> <pre><code class="language-javascript">let greeting = `\\`Yo\\` World!`;</code></pre> <p>\u5982\u679c\u4f7f\u7528\u6a21\u677f\u5b57\u7b26\u4e32\u8868\u793a\u591a\u884c\u5b57\u7b26\u4e32\uff0c\u6240\u6709\u7684\u7a7a\u683c\u548c\u7f29\u8fdb\u90fd\u4f1a\u88ab\u4fdd\u7559\u5728\u8f93\u51fa\u4e4b\u4e2d\u3002</p> <pre><code class="language-javascript">$(&#39;#list&#39;).html(`\n&lt;ul&gt;\n  &lt;li&gt;first&lt;/li&gt;\n  &lt;li&gt;second&lt;/li&gt;\n&lt;/ul&gt;\n`);</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u6240\u6709\u6a21\u677f\u5b57\u7b26\u4e32\u7684\u7a7a\u683c\u548c\u6362\u884c\uff0c\u90fd\u662f\u88ab\u4fdd\u7559\u7684\uff0c\u6bd4\u5982<code>&lt;ul&gt;</code>\u6807\u7b7e\u524d\u9762\u4f1a\u6709\u4e00\u4e2a\u6362\u884c\u3002\u5982\u679c\u4f60\u4e0d\u60f3\u8981\u8fd9\u4e2a\u6362\u884c\uff0c\u53ef\u4ee5\u4f7f\u7528<code>trim</code>\u65b9\u6cd5\u6d88\u9664\u5b83\u3002</p> <pre><code class="language-javascript">$(&#39;#list&#39;).html(`\n&lt;ul&gt;\n  &lt;li&gt;first&lt;/li&gt;\n  &lt;li&gt;second&lt;/li&gt;\n&lt;/ul&gt;\n`.trim());</code></pre> <p>\u6a21\u677f\u5b57\u7b26\u4e32\u4e2d\u5d4c\u5165\u53d8\u91cf\uff0c\u9700\u8981\u5c06\u53d8\u91cf\u540d\u5199\u5728<code>${}</code>\u4e4b\u4e2d\u3002</p> <pre><code class="language-javascript">function authorize(user, action) {\n  if (!user.hasPrivilege(action)) {\n    throw new Error(\n      // \u4f20\u7edf\u5199\u6cd5\u4e3a\n      // &#39;User &#39;\n      // + user.name\n      // + &#39; is not authorized to do &#39;\n      // + action\n      // + &#39;.&#39;\n      `User ${user.name} is not authorized to do ${action}.`);\n  }\n}</code></pre> <p>\u5927\u62ec\u53f7\u5185\u90e8\u53ef\u4ee5\u653e\u5165\u4efb\u610f\u7684 JavaScript \u8868\u8fbe\u5f0f\uff0c\u53ef\u4ee5\u8fdb\u884c\u8fd0\u7b97\uff0c\u4ee5\u53ca\u5f15\u7528\u5bf9\u8c61\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">let x = 1;\nlet y = 2;\n\n`${x} + ${y} = ${x + y}`\n// &quot;1 + 2 = 3&quot;\n\n`${x} + ${y * 2} = ${x + y * 2}`\n// &quot;1 + 4 = 5&quot;\n\nlet obj = {x: 1, y: 2};\n`${obj.x + obj.y}`\n// &quot;3&quot;</code></pre> <p>\u6a21\u677f\u5b57\u7b26\u4e32\u4e4b\u4e2d\u8fd8\u80fd\u8c03\u7528\u51fd\u6570\u3002</p> <pre><code class="language-javascript">function fn() {\n  return &quot;Hello World&quot;;\n}\n\n`foo ${fn()} bar`\n// foo Hello World bar</code></pre> <p>\u5982\u679c\u5927\u62ec\u53f7\u4e2d\u7684\u503c\u4e0d\u662f\u5b57\u7b26\u4e32\uff0c\u5c06\u6309\u7167\u4e00\u822c\u7684\u89c4\u5219\u8f6c\u4e3a\u5b57\u7b26\u4e32\u3002\u6bd4\u5982\uff0c\u5927\u62ec\u53f7\u4e2d\u662f\u4e00\u4e2a\u5bf9\u8c61\uff0c\u5c06\u9ed8\u8ba4\u8c03\u7528\u5bf9\u8c61\u7684<code>toString</code>\u65b9\u6cd5\u3002</p> <p>\u5982\u679c\u6a21\u677f\u5b57\u7b26\u4e32\u4e2d\u7684\u53d8\u91cf\u6ca1\u6709\u58f0\u660e\uff0c\u5c06\u62a5\u9519\u3002</p> <pre><code class="language-javascript">// \u53d8\u91cfplace\u6ca1\u6709\u58f0\u660e\nlet msg = `Hello, ${place}`;\n// \u62a5\u9519</code></pre> <p>\u7531\u4e8e\u6a21\u677f\u5b57\u7b26\u4e32\u7684\u5927\u62ec\u53f7\u5185\u90e8\uff0c\u5c31\u662f\u6267\u884c JavaScript \u4ee3\u7801\uff0c\u56e0\u6b64\u5982\u679c\u5927\u62ec\u53f7\u5185\u90e8\u662f\u4e00\u4e2a\u5b57\u7b26\u4e32\uff0c\u5c06\u4f1a\u539f\u6837\u8f93\u51fa\u3002</p> <pre><code class="language-javascript">`Hello ${&#39;World&#39;}`\n// &quot;Hello World&quot;</code></pre> <p>\u6a21\u677f\u5b57\u7b26\u4e32\u751a\u81f3\u8fd8\u80fd\u5d4c\u5957\u3002</p> <pre><code class="language-javascript">const tmpl = addrs =&gt; `\n  &lt;table&gt;\n  ${addrs.map(addr =&gt; `\n    &lt;tr&gt;&lt;td&gt;${addr.first}&lt;/td&gt;&lt;/tr&gt;\n    &lt;tr&gt;&lt;td&gt;${addr.last}&lt;/td&gt;&lt;/tr&gt;\n  `).join(&#39;&#39;)}\n  &lt;/table&gt;\n`;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u6a21\u677f\u5b57\u7b26\u4e32\u7684\u53d8\u91cf\u4e4b\u4e2d\uff0c\u53c8\u5d4c\u5165\u4e86\u53e6\u4e00\u4e2a\u6a21\u677f\u5b57\u7b26\u4e32\uff0c\u4f7f\u7528\u65b9\u6cd5\u5982\u4e0b\u3002</p> <pre><code class="language-javascript">const data = [\n    { first: &#39;&lt;Jane&gt;&#39;, last: &#39;Bond&#39; },\n    { first: &#39;Lars&#39;, last: &#39;&lt;Croft&gt;&#39; },\n];\n\nconsole.log(tmpl(data));\n// &lt;table&gt;\n//\n//   &lt;tr&gt;&lt;td&gt;&lt;Jane&gt;&lt;/td&gt;&lt;/tr&gt;\n//   &lt;tr&gt;&lt;td&gt;Bond&lt;/td&gt;&lt;/tr&gt;\n//\n//   &lt;tr&gt;&lt;td&gt;Lars&lt;/td&gt;&lt;/tr&gt;\n//   &lt;tr&gt;&lt;td&gt;&lt;Croft&gt;&lt;/td&gt;&lt;/tr&gt;\n//\n// &lt;/table&gt;</code></pre> <p>\u5982\u679c\u9700\u8981\u5f15\u7528\u6a21\u677f\u5b57\u7b26\u4e32\u672c\u8eab\uff0c\u5728\u9700\u8981\u65f6\u6267\u884c\uff0c\u53ef\u4ee5\u50cf\u4e0b\u9762\u8fd9\u6837\u5199\u3002</p> <pre><code class="language-javascript">// \u5199\u6cd5\u4e00\nlet str = &#39;return &#39; + &#39;`Hello ${name}!`&#39;;\nlet func = new Function(&#39;name&#39;, str);\nfunc(&#39;Jack&#39;) // &quot;Hello Jack!&quot;\n\n// \u5199\u6cd5\u4e8c\nlet str = &#39;(name) =&gt; `Hello ${name}!`&#39;;\nlet func = eval.call(null, str);\nfunc(&#39;Jack&#39;) // &quot;Hello Jack!&quot;</code></pre> <h2 id="\u5b9e\u4f8b\uff1a\u6a21\u677f\u7f16\u8bd1">\u5b9e\u4f8b\uff1a\u6a21\u677f\u7f16\u8bd1</h2> <p>\u4e0b\u9762\uff0c\u6211\u4eec\u6765\u770b\u4e00\u4e2a\u901a\u8fc7\u6a21\u677f\u5b57\u7b26\u4e32\uff0c\u751f\u6210\u6b63\u5f0f\u6a21\u677f\u7684\u5b9e\u4f8b\u3002</p> <pre><code class="language-javascript">let template = `\n&lt;ul&gt;\n  &lt;% for(let i=0; i &lt; data.supplies.length; i++) { %&gt;\n    &lt;li&gt;&lt;%= data.supplies[i] %&gt;&lt;/li&gt;\n  &lt;% } %&gt;\n&lt;/ul&gt;\n`;</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u5728\u6a21\u677f\u5b57\u7b26\u4e32\u4e4b\u4e2d\uff0c\u653e\u7f6e\u4e86\u4e00\u4e2a\u5e38\u89c4\u6a21\u677f\u3002\u8be5\u6a21\u677f\u4f7f\u7528<code>&lt;%...%&gt;</code>\u653e\u7f6e JavaScript \u4ee3\u7801\uff0c\u4f7f\u7528<code>&lt;%= ... %&gt;</code>\u8f93\u51fa JavaScript \u8868\u8fbe\u5f0f\u3002</p> <p>\u600e\u4e48\u7f16\u8bd1\u8fd9\u4e2a\u6a21\u677f\u5b57\u7b26\u4e32\u5462\uff1f</p> <p>\u4e00\u79cd\u601d\u8def\u662f\u5c06\u5176\u8f6c\u6362\u4e3a JavaScript \u8868\u8fbe\u5f0f\u5b57\u7b26\u4e32\u3002</p> <pre><code class="language-javascript">echo(&#39;&lt;ul&gt;&#39;);\nfor(let i=0; i &lt; data.supplies.length; i++) {\n  echo(&#39;&lt;li&gt;&#39;);\n  echo(data.supplies[i]);\n  echo(&#39;&lt;/li&gt;&#39;);\n};\necho(&#39;&lt;/ul&gt;&#39;);</code></pre> <p>\u8fd9\u4e2a\u8f6c\u6362\u4f7f\u7528\u6b63\u5219\u8868\u8fbe\u5f0f\u5c31\u884c\u4e86\u3002</p> <pre><code class="language-javascript">let evalExpr = /&lt;%=(.+?)%&gt;/g;\nlet expr = /&lt;%([\\s\\S]+?)%&gt;/g;\n\ntemplate = template\n  .replace(evalExpr, &#39;`); \\n  echo( $1 ); \\n  echo(`&#39;)\n  .replace(expr, &#39;`); \\n $1 \\n  echo(`&#39;);\n\ntemplate = &#39;echo(`&#39; + template + &#39;`);&#39;;</code></pre> <p>\u7136\u540e\uff0c\u5c06<code>template</code>\u5c01\u88c5\u5728\u4e00\u4e2a\u51fd\u6570\u91cc\u9762\u8fd4\u56de\uff0c\u5c31\u53ef\u4ee5\u4e86\u3002</p> <pre><code class="language-javascript">let script =\n`(function parse(data){\n  let output = &quot;&quot;;\n\n  function echo(html){\n    output += html;\n  }\n\n  ${ template }\n\n  return output;\n})`;\n\nreturn script;</code></pre> <p>\u5c06\u4e0a\u9762\u7684\u5185\u5bb9\u62fc\u88c5\u6210\u4e00\u4e2a\u6a21\u677f\u7f16\u8bd1\u51fd\u6570<code>compile</code>\u3002</p> <pre><code class="language-javascript">function compile(template){\n  const evalExpr = /&lt;%=(.+?)%&gt;/g;\n  const expr = /&lt;%([\\s\\S]+?)%&gt;/g;\n\n  template = template\n    .replace(evalExpr, &#39;`); \\n  echo( $1 ); \\n  echo(`&#39;)\n    .replace(expr, &#39;`); \\n $1 \\n  echo(`&#39;);\n\n  template = &#39;echo(`&#39; + template + &#39;`);&#39;;\n\n  let script =\n  `(function parse(data){\n    let output = &quot;&quot;;\n\n    function echo(html){\n      output += html;\n    }\n\n    ${ template }\n\n    return output;\n  })`;\n\n  return script;\n}</code></pre> <p><code>compile</code>\u51fd\u6570\u7684\u7528\u6cd5\u5982\u4e0b\u3002</p> <pre><code class="language-javascript">let parse = eval(compile(template));\ndiv.innerHTML = parse({ supplies: [ &quot;broom&quot;, &quot;mop&quot;, &quot;cleaner&quot; ] });\n//   &lt;ul&gt;\n//     &lt;li&gt;broom&lt;/li&gt;\n//     &lt;li&gt;mop&lt;/li&gt;\n//     &lt;li&gt;cleaner&lt;/li&gt;\n//   &lt;/ul&gt;</code></pre> <h2 id="\u6807\u7b7e\u6a21\u677f">\u6807\u7b7e\u6a21\u677f</h2> <p>\u6a21\u677f\u5b57\u7b26\u4e32\u7684\u529f\u80fd\uff0c\u4e0d\u4ec5\u4ec5\u662f\u4e0a\u9762\u8fd9\u4e9b\u3002\u5b83\u53ef\u4ee5\u7d27\u8ddf\u5728\u4e00\u4e2a\u51fd\u6570\u540d\u540e\u9762\uff0c\u8be5\u51fd\u6570\u5c06\u88ab\u8c03\u7528\u6765\u5904\u7406\u8fd9\u4e2a\u6a21\u677f\u5b57\u7b26\u4e32\u3002\u8fd9\u88ab\u79f0\u4e3a\u201c\u6807\u7b7e\u6a21\u677f\u201d\u529f\u80fd\uff08tagged template\uff09\u3002</p> <pre><code class="language-javascript">alert`123`\n// \u7b49\u540c\u4e8e\nalert(123)</code></pre> <p>\u6807\u7b7e\u6a21\u677f\u5176\u5b9e\u4e0d\u662f\u6a21\u677f\uff0c\u800c\u662f\u51fd\u6570\u8c03\u7528\u7684\u4e00\u79cd\u7279\u6b8a\u5f62\u5f0f\u3002\u201c\u6807\u7b7e\u201d\u6307\u7684\u5c31\u662f\u51fd\u6570\uff0c\u7d27\u8ddf\u5728\u540e\u9762\u7684\u6a21\u677f\u5b57\u7b26\u4e32\u5c31\u662f\u5b83\u7684\u53c2\u6570\u3002</p> <p>\u4f46\u662f\uff0c\u5982\u679c\u6a21\u677f\u5b57\u7b26\u91cc\u9762\u6709\u53d8\u91cf\uff0c\u5c31\u4e0d\u662f\u7b80\u5355\u7684\u8c03\u7528\u4e86\uff0c\u800c\u662f\u4f1a\u5c06\u6a21\u677f\u5b57\u7b26\u4e32\u5148\u5904\u7406\u6210\u591a\u4e2a\u53c2\u6570\uff0c\u518d\u8c03\u7528\u51fd\u6570\u3002</p> <pre><code class="language-javascript">let a = 5;\nlet b = 10;\n\ntag`Hello ${ a + b } world ${ a * b }`;\n// \u7b49\u540c\u4e8e\ntag([&#39;Hello &#39;, &#39; world &#39;, &#39;&#39;], 15, 50);</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u6a21\u677f\u5b57\u7b26\u4e32\u524d\u9762\u6709\u4e00\u4e2a\u6807\u8bc6\u540d<code>tag</code>\uff0c\u5b83\u662f\u4e00\u4e2a\u51fd\u6570\u3002\u6574\u4e2a\u8868\u8fbe\u5f0f\u7684\u8fd4\u56de\u503c\uff0c\u5c31\u662f<code>tag</code>\u51fd\u6570\u5904\u7406\u6a21\u677f\u5b57\u7b26\u4e32\u540e\u7684\u8fd4\u56de\u503c\u3002</p> <p>\u51fd\u6570<code>tag</code>\u4f9d\u6b21\u4f1a\u63a5\u6536\u5230\u591a\u4e2a\u53c2\u6570\u3002</p> <pre><code class="language-javascript">function tag(stringArr, value1, value2){\n  // ...\n}\n\n// \u7b49\u540c\u4e8e\n\nfunction tag(stringArr, ...values){\n  // ...\n}</code></pre> <p><code>tag</code>\u51fd\u6570\u7684\u7b2c\u4e00\u4e2a\u53c2\u6570\u662f\u4e00\u4e2a\u6570\u7ec4\uff0c\u8be5\u6570\u7ec4\u7684\u6210\u5458\u662f\u6a21\u677f\u5b57\u7b26\u4e32\u4e2d\u90a3\u4e9b\u6ca1\u6709\u53d8\u91cf\u66ff\u6362\u7684\u90e8\u5206\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u53d8\u91cf\u66ff\u6362\u53ea\u53d1\u751f\u5728\u6570\u7ec4\u7684\u7b2c\u4e00\u4e2a\u6210\u5458\u4e0e\u7b2c\u4e8c\u4e2a\u6210\u5458\u4e4b\u95f4\u3001\u7b2c\u4e8c\u4e2a\u6210\u5458\u4e0e\u7b2c\u4e09\u4e2a\u6210\u5458\u4e4b\u95f4\uff0c\u4ee5\u6b64\u7c7b\u63a8\u3002</p> <p><code>tag</code>\u51fd\u6570\u7684\u5176\u4ed6\u53c2\u6570\uff0c\u90fd\u662f\u6a21\u677f\u5b57\u7b26\u4e32\u5404\u4e2a\u53d8\u91cf\u88ab\u66ff\u6362\u540e\u7684\u503c\u3002\u7531\u4e8e\u672c\u4f8b\u4e2d\uff0c\u6a21\u677f\u5b57\u7b26\u4e32\u542b\u6709\u4e24\u4e2a\u53d8\u91cf\uff0c\u56e0\u6b64<code>tag</code>\u4f1a\u63a5\u53d7\u5230<code>value1</code>\u548c<code>value2</code>\u4e24\u4e2a\u53c2\u6570\u3002</p> <p><code>tag</code>\u51fd\u6570\u6240\u6709\u53c2\u6570\u7684\u5b9e\u9645\u503c\u5982\u4e0b\u3002</p> <ul> <li>\u7b2c\u4e00\u4e2a\u53c2\u6570\uff1a<code>[&#39;Hello &#39;, &#39; world &#39;, &#39;&#39;]</code></li> <li>\u7b2c\u4e8c\u4e2a\u53c2\u6570: 15</li> <li>\u7b2c\u4e09\u4e2a\u53c2\u6570\uff1a50</li> </ul> <p>\u4e5f\u5c31\u662f\u8bf4\uff0c<code>tag</code>\u51fd\u6570\u5b9e\u9645\u4e0a\u4ee5\u4e0b\u9762\u7684\u5f62\u5f0f\u8c03\u7528\u3002</p> <pre><code class="language-javascript">tag([&#39;Hello &#39;, &#39; world &#39;, &#39;&#39;], 15, 50)</code></pre> <p>\u6211\u4eec\u53ef\u4ee5\u6309\u7167\u9700\u8981\u7f16\u5199<code>tag</code>\u51fd\u6570\u7684\u4ee3\u7801\u3002\u4e0b\u9762\u662f<code>tag</code>\u51fd\u6570\u7684\u4e00\u79cd\u5199\u6cd5\uff0c\u4ee5\u53ca\u8fd0\u884c\u7ed3\u679c\u3002</p> <pre><code class="language-javascript">let a = 5;\nlet b = 10;\n\nfunction tag(s, v1, v2) {\n  console.log(s[0]);\n  console.log(s[1]);\n  console.log(s[2]);\n  console.log(v1);\n  console.log(v2);\n\n  return &quot;OK&quot;;\n}\n\ntag`Hello ${ a + b } world ${ a * b}`;\n// &quot;Hello &quot;\n// &quot; world &quot;\n// &quot;&quot;\n// 15\n// 50\n// &quot;OK&quot;</code></pre> <p>\u4e0b\u9762\u662f\u4e00\u4e2a\u66f4\u590d\u6742\u7684\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">let total = 30;\nlet msg = passthru`The total is ${total} (${total*1.05} with tax)`;\n\nfunction passthru(literals) {\n  let result = &#39;&#39;;\n  let i = 0;\n\n  while (i &lt; literals.length) {\n    result += literals[i++];\n    if (i &lt; arguments.length) {\n      result += arguments[i];\n    }\n  }\n\n  return result;\n}\n\nmsg // &quot;The total is 30 (31.5 with tax)&quot;</code></pre> <p>\u4e0a\u9762\u8fd9\u4e2a\u4f8b\u5b50\u5c55\u793a\u4e86\uff0c\u5982\u4f55\u5c06\u5404\u4e2a\u53c2\u6570\u6309\u7167\u539f\u6765\u7684\u4f4d\u7f6e\u62fc\u5408\u56de\u53bb\u3002</p> <p><code>passthru</code>\u51fd\u6570\u91c7\u7528 rest \u53c2\u6570\u7684\u5199\u6cd5\u5982\u4e0b\u3002</p> <pre><code class="language-javascript">function passthru(literals, ...values) {\n  let output = &quot;&quot;;\n  let index;\n  for (index = 0; index &lt; values.length; index++) {\n    output += literals[index] + values[index];\n  }\n\n  output += literals[index]\n  return output;\n}</code></pre> <p>\u201c\u6807\u7b7e\u6a21\u677f\u201d\u7684\u4e00\u4e2a\u91cd\u8981\u5e94\u7528\uff0c\u5c31\u662f\u8fc7\u6ee4 HTML \u5b57\u7b26\u4e32\uff0c\u9632\u6b62\u7528\u6237\u8f93\u5165\u6076\u610f\u5185\u5bb9\u3002</p> <pre><code class="language-javascript">let message =\n  SaferHTML`&lt;p&gt;${sender} has sent you a message.&lt;/p&gt;`;\n\nfunction SaferHTML(templateData) {\n  let s = templateData[0];\n  for (let i = 1; i &lt; arguments.length; i++) {\n    let arg = String(arguments[i]);\n\n    // Escape special characters in the substitution.\n    s += arg.replace(/&amp;/g, &quot;&amp;amp;&quot;)\n            .replace(/&lt;/g, &quot;&amp;lt;&quot;)\n            .replace(/&gt;/g, &quot;&amp;gt;&quot;);\n\n    // Don&#39;t escape special characters in the template.\n    s += templateData[i];\n  }\n  return s;\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>sender</code>\u53d8\u91cf\u5f80\u5f80\u662f\u7528\u6237\u63d0\u4f9b\u7684\uff0c\u7ecf\u8fc7<code>SaferHTML</code>\u51fd\u6570\u5904\u7406\uff0c\u91cc\u9762\u7684\u7279\u6b8a\u5b57\u7b26\u90fd\u4f1a\u88ab\u8f6c\u4e49\u3002</p> <pre><code class="language-javascript">let sender = &#39;&lt;script&gt;alert(&quot;abc&quot;)&lt;/script&gt;&#39;; // \u6076\u610f\u4ee3\u7801\nlet message = SaferHTML`&lt;p&gt;${sender} has sent you a message.&lt;/p&gt;`;\n\nmessage\n// &lt;p&gt;&amp;lt;script&amp;gt;alert(&quot;abc&quot;)&amp;lt;/script&amp;gt; has sent you a message.&lt;/p&gt;</code></pre> <p>\u6807\u7b7e\u6a21\u677f\u7684\u53e6\u4e00\u4e2a\u5e94\u7528\uff0c\u5c31\u662f\u591a\u8bed\u8a00\u8f6c\u6362\uff08\u56fd\u9645\u5316\u5904\u7406\uff09\u3002</p> <pre><code class="language-javascript">i18n`Welcome to ${siteName}, you are visitor number ${visitorNumber}!`\n// &quot;\u6b22\u8fce\u8bbf\u95eexxx\uff0c\u60a8\u662f\u7b2cxxxx\u4f4d\u8bbf\u95ee\u8005\uff01&quot;</code></pre> <p>\u6a21\u677f\u5b57\u7b26\u4e32\u672c\u8eab\u5e76\u4e0d\u80fd\u53d6\u4ee3 Mustache \u4e4b\u7c7b\u7684\u6a21\u677f\u5e93\uff0c\u56e0\u4e3a\u6ca1\u6709\u6761\u4ef6\u5224\u65ad\u548c\u5faa\u73af\u5904\u7406\u529f\u80fd\uff0c\u4f46\u662f\u901a\u8fc7\u6807\u7b7e\u51fd\u6570\uff0c\u4f60\u53ef\u4ee5\u81ea\u5df1\u6dfb\u52a0\u8fd9\u4e9b\u529f\u80fd\u3002</p> <pre><code class="language-javascript">// \u4e0b\u9762\u7684hashTemplate\u51fd\u6570\n// \u662f\u4e00\u4e2a\u81ea\u5b9a\u4e49\u7684\u6a21\u677f\u5904\u7406\u51fd\u6570\nlet libraryHtml = hashTemplate`\n  &lt;ul&gt;\n    #for book in ${myBooks}\n      &lt;li&gt;&lt;i&gt;#{book.title}&lt;/i&gt; by #{book.author}&lt;/li&gt;\n    #end\n  &lt;/ul&gt;\n`;</code></pre> <p>\u9664\u6b64\u4e4b\u5916\uff0c\u4f60\u751a\u81f3\u53ef\u4ee5\u4f7f\u7528\u6807\u7b7e\u6a21\u677f\uff0c\u5728 JavaScript \u8bed\u8a00\u4e4b\u4e2d\u5d4c\u5165\u5176\u4ed6\u8bed\u8a00\u3002</p> <pre><code class="language-javascript">jsx`\n  &lt;div&gt;\n    &lt;input\n      ref=&#39;input&#39;\n      onChange=&#39;${this.handleChange}&#39;\n      defaultValue=&#39;${this.state.value}&#39; /&gt;\n      ${this.state.value}\n   &lt;/div&gt;\n`</code></pre> <p>\u4e0a\u9762\u7684\u4ee3\u7801\u901a\u8fc7<code>jsx</code>\u51fd\u6570\uff0c\u5c06\u4e00\u4e2a DOM \u5b57\u7b26\u4e32\u8f6c\u4e3a React \u5bf9\u8c61\u3002\u4f60\u53ef\u4ee5\u5728 Github \u627e\u5230<code>jsx</code>\u51fd\u6570\u7684<a href="https://gist.github.com/lygaret/a68220defa69174bdec5">\u5177\u4f53\u5b9e\u73b0</a>\u3002</p> <p>\u4e0b\u9762\u5219\u662f\u4e00\u4e2a\u5047\u60f3\u7684\u4f8b\u5b50\uff0c\u901a\u8fc7<code>java</code>\u51fd\u6570\uff0c\u5728 JavaScript \u4ee3\u7801\u4e4b\u4e2d\u8fd0\u884c Java \u4ee3\u7801\u3002</p> <pre><code class="language-javascript">java`\nclass HelloWorldApp {\n  public static void main(String[] args) {\n    System.out.println(\u201cHello World!\u201d); // Display the string.\n  }\n}\n`\nHelloWorldApp.main();</code></pre> <p>\u6a21\u677f\u5904\u7406\u51fd\u6570\u7684\u7b2c\u4e00\u4e2a\u53c2\u6570\uff08\u6a21\u677f\u5b57\u7b26\u4e32\u6570\u7ec4\uff09\uff0c\u8fd8\u6709\u4e00\u4e2a<code>raw</code>\u5c5e\u6027\u3002</p> <pre><code class="language-javascript">console.log`123`\n// [&quot;123&quot;, raw: Array[1]]</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>console.log</code>\u63a5\u53d7\u7684\u53c2\u6570\uff0c\u5b9e\u9645\u4e0a\u662f\u4e00\u4e2a\u6570\u7ec4\u3002\u8be5\u6570\u7ec4\u6709\u4e00\u4e2a<code>raw</code>\u5c5e\u6027\uff0c\u4fdd\u5b58\u7684\u662f\u8f6c\u4e49\u540e\u7684\u539f\u5b57\u7b26\u4e32\u3002</p> <p>\u8bf7\u770b\u4e0b\u9762\u7684\u4f8b\u5b50\u3002</p> <pre><code class="language-javascript">tag`First line\\nSecond line`\n\nfunction tag(strings) {\n  console.log(strings.raw[0]);\n  // strings.raw[0] \u4e3a &quot;First line\\\\nSecond line&quot;\n  // \u6253\u5370\u8f93\u51fa &quot;First line\\nSecond line&quot;\n}</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c<code>tag</code>\u51fd\u6570\u7684\u7b2c\u4e00\u4e2a\u53c2\u6570<code>strings</code>\uff0c\u6709\u4e00\u4e2a<code>raw</code>\u5c5e\u6027\uff0c\u4e5f\u6307\u5411\u4e00\u4e2a\u6570\u7ec4\u3002\u8be5\u6570\u7ec4\u7684\u6210\u5458\u4e0e<code>strings</code>\u6570\u7ec4\u5b8c\u5168\u4e00\u81f4\u3002\u6bd4\u5982\uff0c<code>strings</code>\u6570\u7ec4\u662f<code>[&quot;First line\\nSecond line&quot;]</code>\uff0c\u90a3\u4e48<code>strings.raw</code>\u6570\u7ec4\u5c31\u662f<code>[&quot;First line\\\\nSecond line&quot;]</code>\u3002\u4e24\u8005\u552f\u4e00\u7684\u533a\u522b\uff0c\u5c31\u662f\u5b57\u7b26\u4e32\u91cc\u9762\u7684\u659c\u6760\u90fd\u88ab\u8f6c\u4e49\u4e86\u3002\u6bd4\u5982\uff0cstrings.raw \u6570\u7ec4\u4f1a\u5c06<code>\\n</code>\u89c6\u4e3a<code>\\\\</code>\u548c<code>n</code>\u4e24\u4e2a\u5b57\u7b26\uff0c\u800c\u4e0d\u662f\u6362\u884c\u7b26\u3002\u8fd9\u662f\u4e3a\u4e86\u65b9\u4fbf\u53d6\u5f97\u8f6c\u4e49\u4e4b\u524d\u7684\u539f\u59cb\u6a21\u677f\u800c\u8bbe\u8ba1\u7684\u3002</p> <h2 id="stringraw">String.raw()</h2> <p>ES6 \u8fd8\u4e3a\u539f\u751f\u7684 String \u5bf9\u8c61\uff0c\u63d0\u4f9b\u4e86\u4e00\u4e2a<code>raw</code>\u65b9\u6cd5\u3002</p> <p><code>String.raw</code>\u65b9\u6cd5\uff0c\u5f80\u5f80\u7528\u6765\u5145\u5f53\u6a21\u677f\u5b57\u7b26\u4e32\u7684\u5904\u7406\u51fd\u6570\uff0c\u8fd4\u56de\u4e00\u4e2a\u659c\u6760\u90fd\u88ab\u8f6c\u4e49\uff08\u5373\u659c\u6760\u524d\u9762\u518d\u52a0\u4e00\u4e2a\u659c\u6760\uff09\u7684\u5b57\u7b26\u4e32\uff0c\u5bf9\u5e94\u4e8e\u66ff\u6362\u53d8\u91cf\u540e\u7684\u6a21\u677f\u5b57\u7b26\u4e32\u3002</p> <pre><code class="language-javascript">String.raw`Hi\\n${2+3}!`;\n// &quot;Hi\\\\n5!&quot;\n\nString.raw`Hi\\u000A!`;\n// &#39;Hi\\\\u000A!&#39;</code></pre> <p>\u5982\u679c\u539f\u5b57\u7b26\u4e32\u7684\u659c\u6760\u5df2\u7ecf\u8f6c\u4e49\uff0c\u90a3\u4e48<code>String.raw</code>\u4e0d\u4f1a\u505a\u4efb\u4f55\u5904\u7406\u3002</p> <pre><code class="language-javascript">String.raw`Hi\\\\n`\n// &quot;Hi\\\\n&quot;</code></pre> <p><code>String.raw</code>\u7684\u4ee3\u7801\u57fa\u672c\u5982\u4e0b\u3002</p> <pre><code class="language-javascript">String.raw = function (strings, ...values) {\n  let output = &quot;&quot;;\n  let index;\n  for (index = 0; index &lt; values.length; index++) {\n    output += strings.raw[index] + values[index];\n  }\n\n  output += strings.raw[index]\n  return output;\n}</code></pre> <p><code>String.raw</code>\u65b9\u6cd5\u53ef\u4ee5\u4f5c\u4e3a\u5904\u7406\u6a21\u677f\u5b57\u7b26\u4e32\u7684\u57fa\u672c\u65b9\u6cd5\uff0c\u5b83\u4f1a\u5c06\u6240\u6709\u53d8\u91cf\u66ff\u6362\uff0c\u800c\u4e14\u5bf9\u659c\u6760\u8fdb\u884c\u8f6c\u4e49\uff0c\u65b9\u4fbf\u4e0b\u4e00\u6b65\u4f5c\u4e3a\u5b57\u7b26\u4e32\u6765\u4f7f\u7528\u3002</p> <p><code>String.raw</code>\u65b9\u6cd5\u4e5f\u53ef\u4ee5\u4f5c\u4e3a\u6b63\u5e38\u7684\u51fd\u6570\u4f7f\u7528\u3002\u8fd9\u65f6\uff0c\u5b83\u7684\u7b2c\u4e00\u4e2a\u53c2\u6570\uff0c\u5e94\u8be5\u662f\u4e00\u4e2a\u5177\u6709<code>raw</code>\u5c5e\u6027\u7684\u5bf9\u8c61\uff0c\u4e14<code>raw</code>\u5c5e\u6027\u7684\u503c\u5e94\u8be5\u662f\u4e00\u4e2a\u6570\u7ec4\u3002</p> <pre><code class="language-javascript">String.raw({ raw: &#39;test&#39; }, 0, 1, 2);\n// &#39;t0e1s2t&#39;\n\n// \u7b49\u540c\u4e8e\nString.raw({ raw: [&#39;t&#39;,&#39;e&#39;,&#39;s&#39;,&#39;t&#39;] }, 0, 1, 2);</code></pre> <h2 id="\u6a21\u677f\u5b57\u7b26\u4e32\u7684\u9650\u5236">\u6a21\u677f\u5b57\u7b26\u4e32\u7684\u9650\u5236</h2> <p>\u524d\u9762\u63d0\u5230\u6807\u7b7e\u6a21\u677f\u91cc\u9762\uff0c\u53ef\u4ee5\u5185\u5d4c\u5176\u4ed6\u8bed\u8a00\u3002\u4f46\u662f\uff0c\u6a21\u677f\u5b57\u7b26\u4e32\u9ed8\u8ba4\u4f1a\u5c06\u5b57\u7b26\u4e32\u8f6c\u4e49\uff0c\u5bfc\u81f4\u65e0\u6cd5\u5d4c\u5165\u5176\u4ed6\u8bed\u8a00\u3002</p> <p>\u4e3e\u4f8b\u6765\u8bf4\uff0c\u6807\u7b7e\u6a21\u677f\u91cc\u9762\u53ef\u4ee5\u5d4c\u5165 LaTEX \u8bed\u8a00\u3002</p> <pre><code class="language-javascript">function latex(strings) {\n  // ...\n}\n\nlet document = latex`\n\\newcommand{\\fun}{\\textbf{Fun!}}  // \u6b63\u5e38\u5de5\u4f5c\n\\newcommand{\\unicode}{\\textbf{Unicode!}} // \u62a5\u9519\n\\newcommand{\\xerxes}{\\textbf{King!}} // \u62a5\u9519\n\nBreve over the h goes \\u{h}ere // \u62a5\u9519\n`</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u53d8\u91cf<code>document</code>\u5185\u5d4c\u7684\u6a21\u677f\u5b57\u7b26\u4e32\uff0c\u5bf9\u4e8e LaTEX \u8bed\u8a00\u6765\u8bf4\u5b8c\u5168\u662f\u5408\u6cd5\u7684\uff0c\u4f46\u662f JavaScript \u5f15\u64ce\u4f1a\u62a5\u9519\u3002\u539f\u56e0\u5c31\u5728\u4e8e\u5b57\u7b26\u4e32\u7684\u8f6c\u4e49\u3002</p> <p>\u6a21\u677f\u5b57\u7b26\u4e32\u4f1a\u5c06<code>\\u00FF</code>\u548c<code>\\u{42}</code>\u5f53\u4f5c Unicode \u5b57\u7b26\u8fdb\u884c\u8f6c\u4e49\uff0c\u6240\u4ee5<code>\\unicode</code>\u89e3\u6790\u65f6\u62a5\u9519\uff1b\u800c<code>\\x56</code>\u4f1a\u88ab\u5f53\u4f5c\u5341\u516d\u8fdb\u5236\u5b57\u7b26\u4e32\u8f6c\u4e49\uff0c\u6240\u4ee5<code>\\xerxes</code>\u4f1a\u62a5\u9519\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c<code>\\u</code>\u548c<code>\\x</code>\u5728 LaTEX \u91cc\u9762\u6709\u7279\u6b8a\u542b\u4e49\uff0c\u4f46\u662f JavaScript \u5c06\u5b83\u4eec\u8f6c\u4e49\u4e86\u3002</p> <p>\u4e3a\u4e86\u89e3\u51b3\u8fd9\u4e2a\u95ee\u9898\uff0c\u73b0\u5728\u6709\u4e00\u4e2a<a href="https://tc39.github.io/proposal-template-literal-revision/">\u63d0\u6848</a>\uff0c\u653e\u677e\u5bf9\u6807\u7b7e\u6a21\u677f\u91cc\u9762\u7684\u5b57\u7b26\u4e32\u8f6c\u4e49\u7684\u9650\u5236\u3002\u5982\u679c\u9047\u5230\u4e0d\u5408\u6cd5\u7684\u5b57\u7b26\u4e32\u8f6c\u4e49\uff0c\u5c31\u8fd4\u56de<code>undefined</code>\uff0c\u800c\u4e0d\u662f\u62a5\u9519\uff0c\u5e76\u4e14\u4ece<code>raw</code>\u5c5e\u6027\u4e0a\u9762\u53ef\u4ee5\u5f97\u5230\u539f\u59cb\u5b57\u7b26\u4e32\u3002</p> <pre><code class="language-javascript">function tag(strs) {\n  strs[0] === undefined\n  strs.raw[0] === &quot;\\\\unicode and \\\\u{55}&quot;;\n}\ntag`\\unicode and \\u{55}`</code></pre> <p>\u4e0a\u9762\u4ee3\u7801\u4e2d\uff0c\u6a21\u677f\u5b57\u7b26\u4e32\u539f\u672c\u662f\u5e94\u8be5\u62a5\u9519\u7684\uff0c\u4f46\u662f\u7531\u4e8e\u653e\u677e\u4e86\u5bf9\u5b57\u7b26\u4e32\u8f6c\u4e49\u7684\u9650\u5236\uff0c\u6240\u4ee5\u4e0d\u62a5\u9519\u4e86\uff0cJavaScript \u5f15\u64ce\u5c06\u7b2c\u4e00\u4e2a\u5b57\u7b26\u8bbe\u7f6e\u4e3a<code>undefined</code>\uff0c\u4f46\u662f<code>raw</code>\u5c5e\u6027\u4f9d\u7136\u53ef\u4ee5\u5f97\u5230\u539f\u59cb\u5b57\u7b26\u4e32\uff0c\u56e0\u6b64<code>tag</code>\u51fd\u6570\u8fd8\u662f\u53ef\u4ee5\u5bf9\u539f\u5b57\u7b26\u4e32\u8fdb\u884c\u5904\u7406\u3002</p> <p>\u6ce8\u610f\uff0c\u8fd9\u79cd\u5bf9\u5b57\u7b26\u4e32\u8f6c\u4e49\u7684\u653e\u677e\uff0c\u53ea\u5728\u6807\u7b7e\u6a21\u677f\u89e3\u6790\u5b57\u7b26\u4e32\u65f6\u751f\u6548\uff0c\u4e0d\u662f\u6807\u7b7e\u6a21\u677f\u7684\u573a\u5408\uff0c\u4f9d\u7136\u4f1a\u62a5\u9519\u3002</p> <pre><code class="language-javascript">let bad = `bad escape sequence: \\unicode`; // \u62a5\u9519</code></pre> ';e.exports=o}}]);