(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["docs/js/dataType"],{rQ6S:function(e,o){var t='<h1 id="\u6570\u636e\u7c7b\u578b">\u6570\u636e\u7c7b\u578b</h1> <h2 id="1-\u539f\u59cb\u7c7b\u578b\u7684\u65b9\u6cd5">1 \u539f\u59cb\u7c7b\u578b\u7684\u65b9\u6cd5</h2> <h3 id="11-\u6982\u5ff5">1.1 \u6982\u5ff5</h3> <p><strong>\u539f\u59cb\u503c\uff1a</strong></p> <ul> <li>\u662f\u539f\u59cb\u7c7b\u578b\u4e2d\u7684\u4e00\u79cd\u503c</li> <li>\u5728 JavaScript \u4e2d\u6709 7 \u79cd\u539f\u59cb\u7c7b\u578b\uff1astring\uff0cnumber\uff0cbigint\uff0cboolean\uff0csymbol\uff0cnull \u548c undefined</li> </ul> <p><strong>\u5bf9\u8c61\uff1a</strong></p> <ul> <li>\u80fd\u591f\u5b58\u50a8\u591a\u4e2a\u503c\u4f5c\u4e3a\u5c5e\u6027</li> <li>\u53ef\u4ee5\u4f7f\u7528\u5927\u62ec\u53f7 <code>{}</code> \u521b\u5efa\u5bf9\u8c61\uff0c\u4f8b\u5982\uff1a<code>{name: &quot;John&quot;, age: 30}</code>\u3002JavaScript \u4e2d\u8fd8\u6709\u5176\u4ed6\u79cd\u7c7b\u7684\u5bf9\u8c61\uff0c\u4f8b\u5982\u51fd\u6570\u5c31\u662f\u5bf9\u8c61</li> </ul> <h3 id="12-\u5f53\u4f5c\u5bf9\u8c61\u7684\u539f\u59cb\u7c7b\u578b">1.2 \u5f53\u4f5c\u5bf9\u8c61\u7684\u539f\u59cb\u7c7b\u578b</h3> <p>JavaScript \u5141\u8bb8\u6211\u4eec\u50cf\u4f7f\u7528\u5bf9\u8c61\u4e00\u6837\u4f7f\u7528\u539f\u59cb\u7c7b\u578b\uff08\u5b57\u7b26\u4e32\uff0c\u6570\u5b57\u7b49\uff09</p> <ul> <li>\u5bf9\u8bf8\u5982\u5b57\u7b26\u4e32\u6216\u6570\u5b57\u4e4b\u7c7b\u7684\u539f\u59cb\u7c7b\u578b\u6267\u884c\u4e00\u4e9b\u64cd\u4f5c\u3002\u6700\u597d\u5c06\u5b83\u4eec\u4f5c\u4e3a\u65b9\u6cd5\u6765\u8bbf\u95ee</li> <li>\u539f\u59cb\u7c7b\u578b\u5fc5\u987b\u5c3d\u53ef\u80fd\u7684\u7b80\u5355\u8f7b\u91cf</li> <li>\u539f\u59cb\u7c7b\u578b\u4ecd\u7136\u662f\u539f\u59cb\u7684\u3002\u4e0e\u9884\u671f\u76f8\u540c\uff0c\u63d0\u4f9b\u5355\u4e2a\u503c</li> <li>JavaScript \u5141\u8bb8\u8bbf\u95ee\u5b57\u7b26\u4e32\uff0c\u6570\u5b57\uff0c\u5e03\u5c14\u503c\u548c symbol \u7684\u65b9\u6cd5\u548c\u5c5e\u6027</li> <li>\u4e3a\u4e86\u4f7f\u5b83\u4eec\u8d77\u4f5c\u7528\uff0c\u521b\u5efa\u4e86\u63d0\u4f9b\u989d\u5916\u529f\u80fd\u7684\u7279\u6b8a\u201c\u5bf9\u8c61\u5305\u88c5\u5668\u201d\uff0c\u4f7f\u7528\u540e\u5373\u88ab\u9500\u6bc1</li> </ul> <p>\u201c\u5bf9\u8c61\u5305\u88c5\u5668\u201d\u5bf9\u4e8e\u6bcf\u79cd\u539f\u59cb\u7c7b\u578b\u90fd\u662f\u4e0d\u540c\u7684\uff0c\u5b83\u4eec\u88ab\u79f0\u4e3a String\u3001Number\u3001Boolean \u548c Symbol</p> <h3 id="13-\u6784\u9020\u5668\u4ec5\u4f9b\u5185\u90e8\u4f7f\u7528">1.3 \u6784\u9020\u5668\u4ec5\u4f9b\u5185\u90e8\u4f7f\u7528</h3> <blockquote> <p>\u4e0d\u76f4\u63a5\u4f7f\u7528 <code>new Number(10)</code>\uff0c\u8fd9\u4e2a\u7528\u6cd5\u4ec5\u4f9b\u5185\u90e8\u4f7f\u7528</p> </blockquote> <blockquote> <p><code>Number(&#39;10&#39;)</code>; <code>String(10)</code>; <code>Boolean(0)</code>; \u53ef\u7528\u4e8e\u7c7b\u578b\u8f6c\u6362</p> </blockquote> <pre><code class="language-javascript">let val1 = 10;\nlet val2 = Number(10);\nlet val3 = new Number(10);\n\nval1 === val2; // true\nval1 === val3; // false\nval1 == val2; // true\n\ntypeof val3; // object</code></pre> <h2 id="2-\u6570\u5b57\u7c7b\u578b">2 \u6570\u5b57\u7c7b\u578b</h2> <h3 id="21-\u6570\u5b57\u7684\u8868\u793a\u65b9\u6cd5">2.1 \u6570\u5b57\u7684\u8868\u793a\u65b9\u6cd5</h3> <pre><code class="language-javascript">let billion = 1e9; // 10 \u4ebf\uff0c\u5b57\u9762\u610f\u601d\uff1a\u6570\u5b57 1 \u540e\u9762\u8ddf 9 \u4e2a 0\n\n// &quot;e&quot; \u628a\u6570\u5b57\u4e58\u4ee5 1 \u540e\u9762\u8ddf\u7740\u7ed9\u5b9a\u6570\u91cf\u7684 0 \u7684\u6570\u5b57\n1e3 === 1 * 1000;\n1.23e6 === 1.23 * 1000000;\n\n// \u5c0f\u6570\nlet ms = 0.000001;\nlet ms = 1e-6; // 1 \u7684\u5de6\u8fb9\u6709 6 \u4e2a 0\n1e-3 === 1 / 1000; // 0.001\n\n// \u5341\u516d\u8fdb\u5236\nalert(0xff); // 255\nalert(0xff); // 255\uff08\u4e00\u6837\uff0c\u5927\u5c0f\u5199\u6ca1\u5f71\u54cd\n\n// \u4e8c\u8fdb\u5236\u548c\u516b\u8fdb\u5236\u6570\u5b57\u7cfb\u7edf\u5f88\u5c11\u4f7f\u7528\uff0c\u4f46\u4e5f\u652f\u6301\u4f7f\u7528 0b \u548c 0o \u524d\u7f00\nlet a = 0b11111111; // \u4e8c\u8fdb\u5236\u5f62\u5f0f\u7684 255\nlet b = 0o377; // \u516b\u8fdb\u5236\u5f62\u5f0f\u7684 255\nalert(a == b); // true\uff0c\u4e24\u8fb9\u662f\u76f8\u540c\u7684\u6570\u5b57\uff0c\u90fd\u662f 255</code></pre> <p>\u53ea\u6709\u8fd9\u4e09\u79cd\u8fdb\u5236\u652f\u6301\u8fd9\u79cd\u5199\u6cd5\u3002\u5bf9\u4e8e\u5176\u4ed6\u8fdb\u5236\uff0c\u6211\u4eec\u5e94\u8be5\u4f7f\u7528\u51fd\u6570 <code>parseInt</code></p> <h3 id="22-tostringbase">2.2 toString(base)</h3> <p>base \u7684\u8303\u56f4\u53ef\u4ee5\u4ece 2 \u5230 36\u3002\u9ed8\u8ba4\u60c5\u51b5\u4e0b\u662f 10</p> <pre><code class="language-javascript">let num = 255;\n\nalert(num.toString(16)); // ff\nalert(num.toString(2)); // 11111111</code></pre> <blockquote> <p><code>123456..toString(36)</code> \u4e2d\u6709\u4e24\u4e2a\u70b9\u3002\u63a5\u5728\u4e00\u4e2a\u6570\u5b57\u4e0a\u8c03\u7528\u4e00\u4e2a\u65b9\u6cd5\uff0c\u5c31\u9700\u8981\u5728\u5b83\u540e\u9762\u653e\u7f6e\u4e24\u4e2a\u70b9 <code>..</code></p> </blockquote> <h3 id="23-\u7cbe\u5ea6\u95ee\u9898">2.3 \u7cbe\u5ea6\u95ee\u9898</h3> <p>\u5728\u5185\u90e8\uff0c\u6570\u5b57\u662f\u4ee5 64 \u4f4d\u683c\u5f0f IEEE-754 \u8868\u793a\u7684\uff0c\u6240\u4ee5\u6b63\u597d\u6709 64 \u4f4d\u53ef\u4ee5\u5b58\u50a8\u4e00\u4e2a\u6570\u5b57\uff1a\u5176\u4e2d 52 \u4f4d\u88ab\u7528\u4e8e\u5b58\u50a8\u8fd9\u4e9b\u6570\u5b57\uff0c\u5176\u4e2d 11 \u4f4d\u7528\u4e8e\u5b58\u50a8\u5c0f\u6570\u70b9\u7684\u4f4d\u7f6e\uff08\u5bf9\u4e8e\u6574\u6570\uff0c\u5b83\u4eec\u4e3a\u96f6\uff09\uff0c\u800c 1 \u4f4d\u7528\u4e8e\u7b26\u53f7</p> <pre><code class="language-javascript">alert(0.1 + 0.2 == 0.3); // false\n\nalert(0.1 + 0.2); // 0.30000000000000004</code></pre> <p>\u4e00\u4e2a\u6570\u5b57\u4ee5\u5176\u4e8c\u8fdb\u5236\u7684\u5f62\u5f0f\u5b58\u50a8\u5728\u5185\u5b58\u4e2d\uff0c\u4e00\u4e2a 1 \u548c 0 \u7684\u5e8f\u5217\u3002\u4e8c\u8fdb\u5236\u5f62\u5f0f\u4e2d 0.1\uff0c0.2 \u8fd9\u6837\u7684\u5c0f\u6570\u662f\u65e0\u9650\u5faa\u73af\u5c0f\u6570</p> <p>\u4f7f\u7528\u4e8c\u8fdb\u5236\u6570\u5b57\u7cfb\u7edf\u65e0\u6cd5 \u7cbe\u786e \u5b58\u50a8 0.1 \u6216 0.2\uff0c\u5c31\u50cf\u6ca1\u6709\u529e\u6cd5\u5c06\u4e09\u5206\u4e4b\u4e00\u5b58\u50a8\u4e3a\u5341\u8fdb\u5236\u5c0f\u6570\u4e00\u6837</p> <p><strong>\u5341\u8fdb\u5236\u8f6c\u4e8c\u8fdb\u5236\uff1a</strong></p> <pre><code>// \u5341\u8fdb\u5236\u6574\u6570\u8f6c\u4e8c\u8fdb\u5236\uff1a\u9664 2 \u53d6\u4f59 \u9006\u5e8f\n11 =&gt; 1011\n\n// \u5341\u8fdb\u5236\u5c0f\u6570\u8f6c\u4e8c\u8fdb\u5236\uff1a\u4e58\u4e8c\u53d6\u6574\n0.75 =&gt; 0.11\n\n// \u4e8c\u8fdb\u5236\u8f6c\u5341\u8fdb\u5236\uff1a\n110.11 = 1 * 2^2 + 1 * 2^1 + 0 * 2^0 + 1 * 2^-1 + 1 * 2^-2\n110.11 = 4 + 2 + 0 + 0.5 + 0.25 = 6.75</code></pre><p><strong>\u89e3\u51b3\u65b9\u6848\uff1atoFixed</strong></p> <pre><code class="language-javascript">let sum = 0.1 + 0.2;\nalert(+sum.toFixed(2)); // 0.3</code></pre> <h3 id="24-isfinite-\u548c-isnan">2.4 isFinite \u548c isNaN</h3> <p><code>isNaN(value)</code> \u5c06\u5176\u53c2\u6570\u8f6c\u6362\u4e3a\u6570\u5b57\uff0c\u7136\u540e\u6d4b\u8bd5\u5b83\u662f\u5426\u4e3a <code>NaN</code>\uff1a</p> <pre><code class="language-js">// \u56e0\u4e3a NaN \u4e0d\u7b49\u4e8e\u4efb\u4f55\u503c\uff0c\u5305\u62ec\u5b83\u81ea\u8eab\uff0c\u6240\u4ee5\u9700\u8981\u7528\u8fd9\u4e2a\u65b9\u6cd5\u5224\u65ad\nalert(NaN === NaN); // false\n\nalert(isNaN(NaN)); // true\nalert(isNaN(&#39;str&#39;)); // true</code></pre> <p><code>isFinite(value)</code> \u5c06\u5176\u53c2\u6570\u8f6c\u6362\u4e3a\u6570\u5b57\uff0c\u5982\u679c\u662f\u5e38\u89c4\u6570\u5b57\uff0c\u5219\u8fd4\u56de <code>true</code>\uff1a</p> <pre><code class="language-js">alert(isFinite(&#39;15&#39;)); // true\nalert(isFinite(&#39;str&#39;)); // false\uff0c\u56e0\u4e3a\u662f\u4e00\u4e2a\u7279\u6b8a\u7684\u503c\uff1aNaN\nalert(isFinite(Infinity)); // false\uff0c\u56e0\u4e3a\u662f\u4e00\u4e2a\u7279\u6b8a\u7684\u503c\uff1aInfinity</code></pre> <blockquote> <p>\u5728\u6240\u6709\u6570\u5b57\u51fd\u6570\u4e2d\uff0c\u5305\u62ec <code>isFinite</code>\uff0c\u7a7a\u5b57\u7b26\u4e32\u6216\u4ec5\u6709\u7a7a\u683c\u7684\u5b57\u7b26\u4e32\u5747\u88ab\u89c6\u4e3a 0</p> </blockquote> <h3 id="25-parseint-\u548c-parsefloat">2.5 parseInt \u548c parseFloat</h3> <p>\u4f7f\u7528\u52a0\u53f7 <code>+</code> \u6216 <code>Number()</code> \u7684\u6570\u5b57\u8f6c\u6362\u662f\u4e25\u683c\u7684\u3002\u5982\u679c\u4e00\u4e2a\u503c\u4e0d\u5b8c\u5168\u662f\u4e00\u4e2a\u6570\u5b57\uff0c\u5c31\u4f1a\u5931\u8d25</p> <p><code>parseInt</code> \u548c <code>parseFloat</code> \u53ef\u4ee5\u4ece\u5b57\u7b26\u4e32\u4e2d\u201c\u8bfb\u53d6\u201d\u6570\u5b57\uff0c\u76f4\u5230\u65e0\u6cd5\u8bfb\u53d6\u4e3a\u6b62\u3002\u5982\u679c\u53d1\u751f <code>error</code>\uff0c\u5219\u8fd4\u56de\u6536\u96c6\u5230\u7684\u6570\u5b57</p> <pre><code class="language-javascript">alert(parseInt(&#39;100px&#39;)); // 100\nalert(parseFloat(&#39;12.5em&#39;)); // 12.5\n\nalert(parseInt(&#39;12.3&#39;)); // 12\uff0c\u53ea\u6709\u6574\u6570\u90e8\u5206\u88ab\u8fd4\u56de\u4e86\nalert(parseFloat(&#39;12.3.4&#39;)); // 12.3\uff0c\u5728\u7b2c\u4e8c\u4e2a\u70b9\u51fa\u505c\u6b62\u4e86\u8bfb\u53d6\n\nalert(parseInt(&#39;a123&#39;)); // NaN\uff0c\u7b2c\u4e00\u4e2a\u7b26\u53f7\u505c\u6b62\u4e86\u8bfb\u53d6</code></pre> <p><code>parseInt()</code> \u51fd\u6570\u5177\u6709\u53ef\u9009\u7684\u7b2c\u4e8c\u4e2a\u53c2\u6570\u3002\u5b83\u6307\u5b9a\u4e86\u6570\u5b57\u7cfb\u7edf\u7684\u57fa\u6570</p> <pre><code class="language-javascript">alert(parseInt(&#39;0xff&#39;, 16)); // 255\nalert(parseInt(&#39;ff&#39;, 16)); // 255\uff0c\u6ca1\u6709 0x \u4ecd\u7136\u6709\u6548\n\nalert(parseInt(&#39;2n9c&#39;, 36)); // 123456</code></pre> <h2 id="3-\u5b57\u7b26\u4e32">3 \u5b57\u7b26\u4e32</h2> <h3 id="31-\u7279\u6b8a\u5b57\u7b26">3.1 \u7279\u6b8a\u5b57\u7b26</h3> <table> <thead> <tr> <th>\u5b57\u7b26</th> <th>\u63cf\u8ff0</th> </tr> </thead> <tbody><tr> <td><code>\\n</code></td> <td>\u6362\u884c</td> </tr> <tr> <td><code>\\r</code></td> <td>\u56de\u8f66\uff1a\u4e0d\u5355\u72ec\u4f7f\u7528\u3002Windows \u6587\u672c\u6587\u4ef6\u4f7f\u7528\u4e24\u4e2a\u5b57\u7b26 \\r\\n \u7684\u7ec4\u5408\u6765\u8868\u793a\u6362\u884c</td> </tr> <tr> <td><code>\\&#39;</code>, <code>\\&quot;</code></td> <td>\u5f15\u53f7</td> </tr> <tr> <td><code>\\\\</code></td> <td>\u53cd\u659c\u7ebf</td> </tr> <tr> <td><code>\\t</code></td> <td>\u5236\u8868\u7b26</td> </tr> <tr> <td><code>\\b</code>, <code>\\f</code>, <code>\\v</code></td> <td>\u9000\u683c\uff0c\u6362\u9875\uff0c\u5782\u76f4\u6807\u7b7e \u2014\u2014 \u4e3a\u4e86\u517c\u5bb9\u6027\uff0c\u73b0\u5728\u5df2\u7ecf\u4e0d\u4f7f\u7528\u4e86</td> </tr> <tr> <td><code>\\xXX</code></td> <td></td> </tr> <tr> <td><code>\\uXXXX</code></td> <td></td> </tr> <tr> <td><code>\\u{X\u2026XXXXXX}</code></td> <td></td> </tr> </tbody></table> <ul> <li><code>\\xXX</code> \u5177\u6709\u7ed9\u5b9a\u5341\u516d\u8fdb\u5236 Unicode XX \u7684 Unicode \u5b57\u7b26\uff0c\u4f8b\u5982\uff1a&#39;\\x7A&#39; \u548c &#39;z&#39; \u76f8\u540c</li> <li><code>\\uXXXX</code> \u4ee5 UTF-16 \u7f16\u7801\u7684\u5341\u516d\u8fdb\u5236\u4ee3\u7801 XXXX \u7684 unicode \u5b57\u7b26\uff0c\u4f8b\u5982 \\u00A9 \u2014\u2014 \u662f\u7248\u6743\u7b26\u53f7 \xa9 \u7684 unicode\u3002\u5b83\u5fc5\u987b\u6b63\u597d\u662f 4 \u4e2a\u5341\u516d\u8fdb\u5236\u6570\u5b57</li> <li><code>\\u{X\u2026XXXXXX}</code> \u5177\u6709\u7ed9\u5b9a UTF-32 \u7f16\u7801\u7684 unicode \u7b26\u53f7\u3002\u4e00\u4e9b\u7f55\u89c1\u7684\u5b57\u7b26\u7528\u4e24\u4e2a unicode \u7b26\u53f7\u7f16\u7801\uff0c\u5360\u7528 4 \u4e2a\u5b57\u8282\u3002\u8fd9\u6837\u6211\u4eec\u5c31\u53ef\u4ee5\u63d2\u5165\u957f\u4ee3\u7801\u4e86</li> </ul> <pre><code class="language-javascript">alert(&#39;\\u00A9&#39;); // \xa9\nalert(&#39;\\u{20331}&#39;); // \u4f6b\uff0c\u7f55\u89c1\u7684\u4e2d\u56fd\u8c61\u5f62\u6587\u5b57\uff08\u957f unicode\uff09\nalert(&#39;\\u{1F60D}&#39;); // \ud83d\ude0d\uff0c\u7b11\u8138\u7b26\u53f7\uff08\u53e6\u4e00\u4e2a\u957f unicode\uff09</code></pre> <h3 id="32-\u8bbf\u95ee\u5b57\u7b26\u4e32">3.2 \u8bbf\u95ee\u5b57\u7b26\u4e32</h3> <p>\u5982\u679c\u6ca1\u6709\u627e\u5230\u5b57\u7b26\uff0c<code>[]</code> \u8fd4\u56de <code>undefined</code>\uff0c\u800c <code>charAt</code> \u8fd4\u56de\u4e00\u4e2a\u7a7a\u5b57\u7b26\u4e32</p> <pre><code class="language-javascript">let str = `Hello`;\n\n// \u7b2c\u4e00\u4e2a\u5b57\u7b26\nalert(str[0]); // H\nalert(str.charAt(0)); // H\n\nalert(str[1000]); // undefined\nalert(str.charAt(1000)); // &#39;&#39;\uff08\u7a7a\u5b57\u7b26\u4e32\uff09</code></pre> <blockquote> <p>\u5b57\u7b26\u4e32\u662f\u4e0d\u53ef\u53d8\u7684</p> </blockquote> <pre><code class="language-javascript">let str = &#39;Hi&#39;;\nstr[0] = &#39;h&#39;; // error</code></pre> <h3 id="33-\u5b57\u7b26\u4e32\u65b9\u6cd5">3.3 \u5b57\u7b26\u4e32\u65b9\u6cd5</h3> <ul> <li><code>toLowerCase</code> \u5c0f\u5199</li> <li><code>toUpperCase</code> \u5927\u5199</li> <li><code>str.indexOf</code> \u67e5\u627e\u5b57\u7b26\u4e32\u4f4d\u7f6e</li> <li><code>includes</code> \u662f\u5426\u5305\u542b xxx \u5b57\u7b26\u4e32</li> <li><code>startsWith</code> \u662f\u5426\u4ee5 xxx \u5f00\u5934</li> <li><code>endsWith</code> \u662f\u5426\u4ee5 xxx \u7ed3\u5c3e</li> <li><code>slice(start [, end])</code> \u8fd4\u56de\u5b57\u7b26\u4e32\u4ece start \u5230\uff08\u4f46\u4e0d\u5305\u62ec\uff09end \u7684\u90e8\u5206</li> <li><code>substring(start [, end])</code> \u8fd4\u56de\u5b57\u7b26\u4e32\u5728 start \u548c end \u4e4b\u95f4 \u7684\u90e8\u5206</li> <li><code>substr(start [, length])</code> \u8fd4\u56de\u5b57\u7b26\u4e32\u4ece start \u5f00\u59cb\u7684\u7ed9\u5b9a length \u7684\u90e8\u5206</li> <li><code>str.codePointAt(pos)</code> \u8fd4\u56de\u5728 pos \u4f4d\u7f6e\u7684\u5b57\u7b26\u4ee3\u7801</li> <li><code>String.fromCodePoint(code)</code> \u901a\u8fc7\u6570\u5b57 code \u521b\u5efa\u5b57\u7b26</li> </ul> <h2 id="4-\u6570\u7ec4">4 \u6570\u7ec4</h2> <h3 id="41-\u5185\u90e8\u673a\u5236">4.1 \u5185\u90e8\u673a\u5236</h3> <p>\u6570\u7ec4\u662f\u4e00\u79cd\u7279\u6b8a\u7684\u5bf9\u8c61\u3002\u4f7f\u7528\u65b9\u62ec\u53f7\u6765\u8bbf\u95ee\u5c5e\u6027 <code>arr[0]</code> \u5b9e\u9645\u4e0a\u662f\u6765\u81ea\u4e8e\u5bf9\u8c61\u7684\u8bed\u6cd5\u3002\u5b83\u5176\u5b9e\u4e0e <code>obj[key]</code> \u76f8\u540c\uff0c\u5176\u4e2d arr \u662f\u5bf9\u8c61\uff0c\u800c\u6570\u5b57\u7528\u4f5c\u952e\uff08key\uff09\u3002</p> <p>\u5b83\u4eec\u6269\u5c55\u4e86\u5bf9\u8c61\uff0c\u63d0\u4f9b\u4e86\u7279\u6b8a\u7684\u65b9\u6cd5\u6765\u5904\u7406\u6709\u5e8f\u7684\u6570\u636e\u96c6\u5408\u4ee5\u53ca length \u5c5e\u6027\u3002\u4f46\u4ece\u672c\u8d28\u4e0a\u8bb2\uff0c\u5b83\u4ecd\u7136\u662f\u4e00\u4e2a\u5bf9\u8c61</p> <blockquote> <p>\u6570\u7ec4\u771f\u6b63\u7279\u6b8a\u7684\u662f\u5b83\u4eec\u7684\u5185\u90e8\u5b9e\u73b0\u3002JavaScript \u5f15\u64ce\u5c1d\u8bd5\u628a\u8fd9\u4e9b\u5143\u7d20\u4e00\u4e2a\u63a5\u4e00\u4e2a\u5730\u5b58\u50a8\u5728\u8fde\u7eed\u7684\u5185\u5b58\u533a\u57df\uff0c\u800c\u4e14\u8fd8\u6709\u4e00\u4e9b\u5176\u5b83\u7684\u4f18\u5316\uff0c\u4ee5\u4f7f\u6570\u7ec4\u8fd0\u884c\u5f97\u975e\u5e38\u5feb</p> </blockquote> <blockquote> <p>\u5982\u679c\u6211\u4eec\u4e0d\u50cf\u201c\u6709\u5e8f\u96c6\u5408\u201d\u90a3\u6837\u4f7f\u7528\u6570\u7ec4\uff0c\u800c\u662f\u50cf\u5e38\u89c4\u5bf9\u8c61\u90a3\u6837\u4f7f\u7528\u6570\u7ec4\uff0c\u5bf9\u5e94\u7684\u4f18\u5316\u5c31\u4f1a\u88ab\u5173\u95ed</p> </blockquote> <p><strong>\u6570\u7ec4\u8bef\u7528\u7684\u51e0\u79cd\u65b9\u5f0f:</strong></p> <ul> <li>\u6dfb\u52a0\u4e00\u4e2a\u975e\u6570\u5b57\u7684\u5c5e\u6027\uff0c\u6bd4\u5982 <code>arr.test = 5</code></li> <li>\u5236\u9020\u7a7a\u6d1e\uff0c\u6bd4\u5982\uff1a\u6dfb\u52a0 <code>arr[0]</code>\uff0c\u7136\u540e\u6dfb\u52a0 <code>arr[1000]</code> (\u5b83\u4eec\u4e2d\u95f4\u4ec0\u4e48\u90fd\u6ca1\u6709)</li> <li>\u4ee5\u5012\u5e8f\u586b\u5145\u6570\u7ec4\uff0c\u6bd4\u5982 <code>arr[1000]</code>\uff0c<code>arr[999]</code> \u7b49\u7b49</li> </ul> <h3 id="42-\u6027\u80fd">4.2 \u6027\u80fd</h3> <p><code>push/pop</code>\u65b9\u6cd5\u8fd0\u884c\u7684\u6bd4\u8f83\u5feb\uff0c\u800c <code>shift/unshift</code> \u6bd4\u8f83\u6162</p> <p><strong><code>shift</code> \u64cd\u4f5c\u5fc5\u987b\u505a\u4e09\u4ef6\u4e8b:</strong></p> <ol> <li>\u79fb\u9664\u7d22\u5f15\u4e3a 0 \u7684\u5143\u7d20</li> <li>\u628a\u6240\u6709\u7684\u5143\u7d20\u5411\u5de6\u79fb\u52a8\uff0c\u628a\u7d22\u5f15 1 \u6539\u6210 0\uff0c2 \u6539\u6210 1 \u4ee5\u6b64\u7c7b\u63a8\uff0c\u5bf9\u5176\u91cd\u65b0\u7f16\u53f7</li> <li>\u66f4\u65b0 length \u5c5e\u6027</li> </ol> <h3 id="43-length">4.3 length</h3> <p>length \u5b9e\u9645\u4e0a\u4e0d\u662f\u6570\u7ec4\u91cc\u5143\u7d20\u7684\u4e2a\u6570\uff0c\u800c\u662f\u6700\u5927\u7684\u6570\u5b57\u7d22\u5f15\u503c\u52a0\u4e00</p> <pre><code class="language-javascript">let fruits = [];\nfruits[123] = &#39;Apple&#39;;\n\nalert(fruits.length); // 124</code></pre> <blockquote> <p>length \u53ef\u66f4\u6539\uff0c\u589e\u52a0 length \u4e0d\u4f1a\u6709\u4ec0\u4e48\u5f71\u54cd\u3002\u51cf\u5c11 length \u6570\u7ec4\u4f1a\u88ab\u622a\u65ad\uff0c\u5e76\u4e14\u662f\u4e0d\u53ef\u9006\u7684</p> </blockquote> <h3 id="44-thisarg">4.4 thisArg</h3> <p>\u51e0\u4e4e\u6240\u6709\u7684\u6570\u7ec4\u65b9\u6cd5 \u2014\u2014 \u6bd4\u5982 find\uff0cfilter\uff0cmap\uff0c\u9664\u4e86 sort \u662f\u4e00\u4e2a\u7279\u4f8b\uff0c\u90fd\u63a5\u53d7\u4e00\u4e2a\u53ef\u9009\u7684\u9644\u52a0\u53c2\u6570 <code>thisArg</code></p> <pre><code class="language-js">arr.find(func, thisArg);\narr.filter(func, thisArg);\narr.map(func, thisArg);</code></pre> <pre><code class="language-js">let army = {\n  minAge: 18,\n  maxAge: 27,\n  canJoin(user) {\n    return user.age &gt;= this.minAge &amp;&amp; user.age &lt; this.maxAge;\n  }\n};\n\nlet users = [{ age: 16 }, { age: 20 }, { age: 23 }, { age: 30 }];\n\n// \u627e\u5230 army.canJoin \u8fd4\u56de true \u7684 user\nlet soldiers = users.filter(army.canJoin, army);\n\nalert(soldiers.length); // 2\nalert(soldiers[0].age); // 20\nalert(soldiers[1].age); // 23</code></pre> <p><code>thisArg</code> \u53c2\u6570\u7684\u503c\u5728 <code>func</code> \u4e2d\u53d8\u4e3a <code>this</code></p> <h2 id="5-iterable-object\uff08\u53ef\u8fed\u4ee3\u5bf9\u8c61\uff09">5 Iterable object\uff08\u53ef\u8fed\u4ee3\u5bf9\u8c61\uff09</h2> <p>\u53ef\u8fed\u4ee3\uff08Iterable\uff09 \u5bf9\u8c61\u662f\u6570\u7ec4\u7684\u6cdb\u5316\u3002\u8fd9\u4e2a\u6982\u5ff5\u662f\u8bf4\u4efb\u4f55\u5bf9\u8c61\u90fd\u53ef\u4ee5\u88ab\u5b9a\u5236\u4e3a\u53ef\u5728 <code>for..of</code> \u5faa\u73af\u4e2d\u4f7f\u7528\u7684\u5bf9\u8c61</p> <blockquote> <p>\u53ef\u8fed\u4ee3\u5bf9\u8c61\u901a\u8fc7\u8c03\u7528 <code>Symbol.iterator</code> \u521b\u5efa\u4e00\u4e2a\u201c\u8fed\u4ee3\u5668\u201d\u5bf9\u8c61\uff0c\u8fed\u4ee3\u5668\u5bf9\u8c61\u6709\u4e00\u4e2a next \u65b9\u6cd5\uff0c\u4e3a\u8fed\u4ee3\u751f\u6210\u503c</p> </blockquote> <h3 id="51-symboliterator">5.1 Symbol.iterator</h3> <p>\u521b\u5efa\u53ef\u8fed\u4ee3\u5bf9\u8c61</p> <ul> <li>\u5f53 <code>for..of</code> \u5faa\u73af\u542f\u52a8\u65f6\uff0c\u5b83\u4f1a\u8c03\u7528\u8fd9\u4e2a\u65b9\u6cd5\uff08\u5982\u679c\u6ca1\u627e\u5230\uff0c\u5c31\u4f1a\u62a5\u9519\uff09\u3002\u8fd9\u4e2a\u65b9\u6cd5\u5fc5\u987b\u8fd4\u56de\u4e00\u4e2a \u8fed\u4ee3\u5668\uff08iterator\uff09 \u2014\u2014 \u4e00\u4e2a\u6709 next \u65b9\u6cd5\u7684\u5bf9\u8c61</li> <li>\u4ece\u6b64\u5f00\u59cb\uff0c<code>for..of</code> \u4ec5\u9002\u7528\u4e8e\u8fd9\u4e2a\u88ab\u8fd4\u56de\u7684\u5bf9\u8c61</li> <li>\u5f53 <code>for..of</code> \u5faa\u73af\u5e0c\u671b\u53d6\u5f97\u4e0b\u4e00\u4e2a\u6570\u503c\uff0c\u5b83\u5c31\u8c03\u7528\u8fd9\u4e2a\u5bf9\u8c61\u7684 <code>next()</code> \u65b9\u6cd5</li> <li><code>next()</code> \u65b9\u6cd5\u8fd4\u56de\u7684\u7ed3\u679c\u7684\u683c\u5f0f\u5fc5\u987b\u662f <code>{done: Boolean, value: any}</code>\uff0c\u5f53 <code>done=true</code> \u65f6\uff0c\u8868\u793a\u8fed\u4ee3\u7ed3\u675f\uff0c\u5426\u5219 <code>value</code> \u662f\u4e0b\u4e00\u4e2a\u503c</li> </ul> <pre><code class="language-js">let range = {\n  from: 1,\n  to: 5,\n\n  // for..of range \u5728\u4e00\u5f00\u59cb\u5c31\u8c03\u7528\u4e00\u6b21\u8fd9\u4e2a\u65b9\u6cd5\n  [Symbol.iterator]() {\n    // ...\u5b83\u8fd4\u56de iterator object\uff1a\n    // \u540e\u7eed\u7684\u64cd\u4f5c\u4e2d\uff0cfor..of \u5c06\u53ea\u9488\u5bf9\u8fd9\u4e2a\u5bf9\u8c61\uff0c\u5e76\u4f7f\u7528 next() \u5411\u5b83\u8bf7\u6c42\u4e0b\u4e00\u4e2a\u503c\n    return {\n      current: this.from,\n      last: this.to,\n\n      // for..of \u5faa\u73af\u5728\u6bcf\u6b21\u8fed\u4ee3\u65f6\u90fd\u4f1a\u8c03\u7528 next()\n      next() {\n        // \u5b83\u5e94\u8be5\u4ee5\u5bf9\u8c61 {done:.., value :...} \u7684\u5f62\u5f0f\u8fd4\u56de\u503c\n        if (this.current &lt;= this.last) {\n          return { done: false, value: this.current++ };\n        } else {\n          return { done: true };\n        }\n      }\n    };\n  }\n};\n\n// \u8fed\u4ee3\u6574\u4e2a range \u5bf9\u8c61\uff0c\u8fd4\u56de\u4ece `range.from` \u5230 `range.to` \u8303\u56f4\u7684\u6240\u6709\u6570\u5b57\nalert([...range]); // 1,2,3,4,5</code></pre> <h3 id="52-arrayfrom">5.2 Array.from</h3> <p>\u6709\u4e00\u4e2a\u5168\u5c40\u65b9\u6cd5 <code>Array.from</code> \u53ef\u4ee5\u63a5\u53d7\u4e00\u4e2a <code>\u53ef\u8fed\u4ee3\u6216\u7c7b\u6570\u7ec4</code> \u7684\u503c\uff0c\u5e76\u4ece\u4e2d\u83b7\u53d6\u4e00\u4e2a\u201c\u771f\u6b63\u7684\u201d\u6570\u7ec4\u3002\u7136\u540e\u6211\u4eec\u5c31\u53ef\u4ee5\u5bf9\u5176\u8c03\u7528\u6570\u7ec4\u65b9\u6cd5\u4e86</p> <pre><code class="language-js">let arrayLike = {\n  0: &#39;Hello&#39;,\n  1: &#39;World&#39;,\n  length: 2\n};\n\nlet arr = Array.from(arrayLike); // (*)\nalert(arr.pop()); // World\uff08pop \u65b9\u6cd5\u6709\u6548\uff09</code></pre> <pre><code class="language-js">// \u5047\u8bbe range \u6765\u81ea\u4e0a\u6587\u7684\u4f8b\u5b50\u4e2d\nlet arr = Array.from(range);\nalert(arr); // 1,2,3,4,5 \uff08\u6570\u7ec4\u7684 toString \u8f6c\u5316\u65b9\u6cd5\u751f\u6548\uff09</code></pre> <h2 id="6-map">6 Map</h2> <h3 id="61-\u6982\u5ff5">6.1 \u6982\u5ff5</h3> <p>Map \u662f\u4e00\u4e2a\u5e26\u952e\u7684\u6570\u636e\u9879\u7684\u96c6\u5408\uff0c\u5c31\u50cf\u4e00\u4e2a <code>Object</code> \u4e00\u6837\u3002 \u4f46\u662f\u5b83\u4eec\u6700\u5927\u7684\u5dee\u522b\u662f <code>Map</code> \u5141\u8bb8\u4efb\u4f55\u7c7b\u578b\u7684\u952e\uff08key\uff09\u3002</p> <p>\u5b83\u7684\u65b9\u6cd5\u548c\u5c5e\u6027\u5982\u4e0b\uff1a</p> <ul> <li><code>new Map()</code> \u2014\u2014 \u521b\u5efa map</li> <li><code>map.set(key, value)</code> \u2014\u2014 \u6839\u636e\u952e\u5b58\u50a8\u503c</li> <li><code>map.get(key)</code> \u2014\u2014 \u6839\u636e\u952e\u6765\u8fd4\u56de\u503c\uff0c\u5982\u679c map \u4e2d\u4e0d\u5b58\u5728\u5bf9\u5e94\u7684 key\uff0c\u5219\u8fd4\u56de undefined</li> <li><code>map.has(key)</code> \u2014\u2014 \u5982\u679c key \u5b58\u5728\u5219\u8fd4\u56de true\uff0c\u5426\u5219\u8fd4\u56de false</li> <li><code>map.delete(key)</code> \u2014\u2014 \u5220\u9664\u6307\u5b9a\u952e\u7684\u503c</li> <li><code>map.clear()</code> \u2014\u2014 \u6e05\u7a7a map</li> <li><code>map.size</code> \u2014\u2014 \u8fd4\u56de\u5f53\u524d\u5143\u7d20\u4e2a\u6570</li> </ul> <p>\u4e0e\u666e\u901a\u5bf9\u8c61 Object \u7684\u4e0d\u540c\u70b9\uff1a</p> <ol> <li>\u4efb\u4f55\u952e\u3001\u5bf9\u8c61\u90fd\u53ef\u4ee5\u4f5c\u4e3a\u952e</li> <li>\u6709\u5176\u4ed6\u7684\u4fbf\u6377\u65b9\u6cd5\uff0c\u5982 size \u5c5e\u6027</li> </ol> <h3 id="62-map-\u8fed\u4ee3">6.2 Map \u8fed\u4ee3</h3> <p>\u5982\u679c\u8981\u5728 map \u91cc\u4f7f\u7528\u5faa\u73af\uff0c\u53ef\u4ee5\u4f7f\u7528\u4ee5\u4e0b\u4e09\u4e2a\u65b9\u6cd5\uff1a</p> <ol> <li><code>map.keys()</code> \u2014\u2014 \u904d\u5386\u5e76\u8fd4\u56de\u6240\u6709\u7684\u952e\uff08returns an iterable for keys\uff09</li> <li><code>map.values()</code> \u2014\u2014 \u904d\u5386\u5e76\u8fd4\u56de\u6240\u6709\u7684\u503c\uff08returns an iterable for values\uff09</li> <li><code>map.entries()</code> \u2014\u2014 \u904d\u5386\u5e76\u8fd4\u56de\u6240\u6709\u7684\u5b9e\u4f53\uff08returns an iterable for entries\uff09<code>[key, value]</code>\uff0c<code>for..of</code> \u5728\u9ed8\u8ba4\u60c5\u51b5\u4e0b\u4f7f\u7528\u7684\u5c31\u662f\u8fd9\u4e2a</li> </ol> <h3 id="63-\u4ece\u5bf9\u8c61\u521b\u5efa-map">6.3 \u4ece\u5bf9\u8c61\u521b\u5efa Map</h3> <p><code>Object.entries</code></p> <p>\u521b\u5efa Map:</p> <pre><code class="language-javascript">// \u952e\u503c\u5bf9 [key, value] \u6570\u7ec4\nlet map = new Map([\n  [&#39;1&#39;, &#39;str1&#39;],\n  [1, &#39;num1&#39;],\n  [true, &#39;bool1&#39;]\n]);\n\nalert(map.get(&#39;1&#39;)); // str1</code></pre> <p>\u4ece\u4e00\u4e2a\u5bf9\u8c61\u521b\u5efa\u4e00\u4e2a Map\uff1a</p> <pre><code class="language-javascript">let obj = {\n  name: &#39;John&#39;,\n  age: 30\n};\n\nlet map = new Map(Object.entries(obj));\n\nalert(map.get(&#39;name&#39;)); // John</code></pre> <p>\u8fd9\u91cc\uff0c<code>Object.entries</code> \u8fd4\u56de\u952e/\u503c\u5bf9\u6570\u7ec4\uff1a<code>[ [&quot;name&quot;,&quot;John&quot;], [&quot;age&quot;, 30] ]</code>\u3002\u8fd9\u5c31\u662f Map \u6240\u9700\u8981\u7684\u683c\u5f0f</p> <h3 id="64-\u4ece-map-\u521b\u5efa\u5bf9\u8c61">6.4 \u4ece Map \u521b\u5efa\u5bf9\u8c61</h3> <p><code>Object.fromEntrie</code></p> <p><code>Object.fromEntries</code> \u65b9\u6cd5\u7684\u4f5c\u7528\u662f\u76f8\u53cd\u7684\uff1a\u7ed9\u5b9a\u4e00\u4e2a\u5177\u6709 <code>[key, value]</code> \u952e\u503c\u5bf9\u7684\u6570\u7ec4\uff0c\u5b83\u4f1a\u6839\u636e\u7ed9\u5b9a\u6570\u7ec4\u521b\u5efa\u4e00\u4e2a\u5bf9\u8c61\uff1a</p> <pre><code class="language-javascript">let prices = Object.fromEntries([\n  [&#39;banana&#39;, 1],\n  [&#39;orange&#39;, 2],\n  [&#39;meat&#39;, 4]\n]);\n\n// \u73b0\u5728 prices = { banana: 1, orange: 2, meat: 4 }\nalert(prices.orange); // 2</code></pre> <p>\u6211\u4eec\u53ef\u4ee5\u4f7f\u7528 <code>Object.fromEntries</code> \u4ece Map \u5f97\u5230\u4e00\u4e2a\u666e\u901a\u5bf9\u8c61<code>\uff08plain object\uff09</code></p> <pre><code class="language-javascript">let map = new Map();\nmap.set(&#39;banana&#39;, 1);\nmap.set(&#39;orange&#39;, 2);\nmap.set(&#39;meat&#39;, 4);\n\nlet obj = Object.fromEntries(map.entries()); // \u521b\u5efa\u4e00\u4e2a\u666e\u901a\u5bf9\u8c61\uff08plain object\uff09(*)\n\n// \u5b8c\u6210\u4e86\uff01\n// obj = { banana: 1, orange: 2, meat: 4 }\n\nalert(obj.orange); // 2</code></pre> <h2 id="7-set">7 Set</h2> <h3 id="71-\u6982\u5ff5">7.1 \u6982\u5ff5</h3> <p>Set \u662f\u4e00\u4e2a\u7279\u6b8a\u7684\u7c7b\u578b\u96c6\u5408 \u2014\u2014 \u201c\u503c\u7684\u96c6\u5408\u201d\uff08\u6ca1\u6709\u952e\uff09\uff0c\u5b83\u7684\u6bcf\u4e00\u4e2a\u503c\u53ea\u80fd\u51fa\u73b0\u4e00\u6b21\u3002</p> <p>\u5b83\u7684\u4e3b\u8981\u65b9\u6cd5\u5982\u4e0b\uff1a</p> <ul> <li><code>new Set(iterable)</code> \u2014\u2014 \u521b\u5efa\u4e00\u4e2a set\uff0c\u5982\u679c\u63d0\u4f9b\u4e86\u4e00\u4e2a iterable \u5bf9\u8c61\uff08\u901a\u5e38\u662f\u6570\u7ec4\uff09\uff0c\u5c06\u4f1a\u4ece\u6570\u7ec4\u91cc\u9762\u590d\u5236\u503c\u5230 set \u4e2d</li> <li><code>set.add(value)</code> \u2014\u2014 \u6dfb\u52a0\u4e00\u4e2a\u503c\uff0c\u8fd4\u56de set \u672c\u8eab</li> <li><code>set.delete(value)</code> \u2014\u2014 \u5220\u9664\u503c\uff0c\u5982\u679c value \u5728\u8fd9\u4e2a\u65b9\u6cd5\u8c03\u7528\u7684\u65f6\u5019\u5b58\u5728\u5219\u8fd4\u56de true \uff0c\u5426\u5219\u8fd4\u56de false</li> <li><code>set.has(value)</code> \u2014\u2014 \u5982\u679c value \u5728 set \u4e2d\uff0c\u8fd4\u56de true\uff0c\u5426\u5219\u8fd4\u56de false</li> <li><code>set.clear()</code> \u2014\u2014 \u6e05\u7a7a set</li> <li><code>set.size</code> \u2014\u2014 \u8fd4\u56de\u5143\u7d20\u4e2a\u6570</li> </ul> <blockquote> <p>Set \u4e3b\u8981\u7279\u70b9\u662f\uff0c\u91cd\u590d\u4f7f\u7528\u540c\u4e00\u4e2a\u503c\u8c03\u7528 <code>set.add(value)</code> \u5e76\u4e0d\u4f1a\u53d1\u751f\u4ec0\u4e48\u6539\u53d8\u3002\u8fd9\u5c31\u662f Set \u91cc\u9762\u7684\u6bcf\u4e00\u4e2a\u503c\u53ea\u51fa\u73b0\u4e00\u6b21\u7684\u539f\u56e0</p> </blockquote> <h3 id="72-set-\u8fed\u4ee3">7.2 Set \u8fed\u4ee3</h3> <p>Map \u4e2d\u7528\u4e8e\u8fed\u4ee3\u7684\u65b9\u6cd5\u5728 Set \u4e2d\u4e5f\u540c\u6837\u652f\u6301\uff1a</p> <ul> <li><code>set.keys()</code> \u2014\u2014 \u904d\u5386\u5e76\u8fd4\u56de\u6240\u6709\u7684\u503c\uff08returns an iterable object for values\uff09</li> <li><code>set.values()</code> \u2014\u2014 \u4e0e <code>set.keys()</code> \u4f5c\u7528\u76f8\u540c\uff0c\u8fd9\u662f\u4e3a\u4e86\u517c\u5bb9 Map</li> <li><code>set.entries()</code> \u2014\u2014 \u904d\u5386\u5e76\u8fd4\u56de\u6240\u6709\u7684\u5b9e\u4f53\uff08returns an iterable object for entries\uff09<code>[value, value]</code>\uff0c\u5b83\u7684\u5b58\u5728\u4e5f\u662f\u4e3a\u4e86\u517c\u5bb9 Map</li> </ul> <h2 id="8-weakmap-weakset">8 WeakMap, WeakSet</h2> <p>\u5982\u679c\u6211\u4eec\u4f7f\u7528\u5bf9\u8c61\u4f5c\u4e3a\u5e38\u89c4 Map \u7684\u952e\uff0c\u90a3\u4e48\u5f53 Map \u5b58\u5728\u65f6\uff0c\u8be5\u5bf9\u8c61\u4e5f\u5c06\u5b58\u5728\u3002\u5b83\u4f1a\u5360\u7528\u5185\u5b58\uff0c\u5e76\u4e14\u5e94\u8be5\u4e0d\u4f1a\u88ab\uff08\u5783\u573e\u56de\u6536\u673a\u5236\uff09\u56de\u6536</p> <pre><code class="language-js">let john = { name: &#39;John&#39; };\n\nlet map = new Map();\nmap.set(john, &#39;...&#39;);\n\njohn = null; // \u8986\u76d6\u5f15\u7528\n\n// john \u88ab\u5b58\u50a8\u5728\u4e86 map \u4e2d\uff0c\u6ca1\u6709\u88ab\u5783\u573e\u56de\u6536\n// \u6211\u4eec\u4ecd\u7136\u53ef\u4ee5\u4f7f\u7528 map.keys() \u6765\u83b7\u53d6\u5b83</code></pre> <p><code>WeakMap</code> \u5728\u8fd9\u65b9\u9762\u6709\u7740\u6839\u672c\u4e0a\u7684\u4e0d\u540c\u3002\u5b83\u4e0d\u4f1a\u963b\u6b62\u5783\u573e\u56de\u6536\u673a\u5236\u5bf9\u4f5c\u4e3a\u952e\u7684\u5bf9\u8c61\uff08key object\uff09\u7684\u56de\u6536</p> <blockquote> <p><code>WeakMap</code> \u548c <code>Map</code> \u7684\u7b2c\u4e00\u4e2a\u4e0d\u540c\u70b9\u5c31\u662f\uff0c<code>WeakMap</code> \u7684\u952e\u5fc5\u987b\u662f\u5bf9\u8c61\uff0c\u4e0d\u80fd\u662f\u539f\u59cb\u503c</p> </blockquote> <blockquote> <p><code>WeakMap</code> \u4e0d\u652f\u6301\u8fed\u4ee3\u4ee5\u53ca <code>keys()</code>\uff0c<code>values()</code> \u548c <code>entries()</code> \u65b9\u6cd5\u3002\u6240\u4ee5\u6ca1\u6709\u529e\u6cd5\u83b7\u53d6 <code>WeakMap</code> \u7684\u6240\u6709\u952e\u6216\u503c</p> </blockquote> <h2 id="8-objectkeys\uff0cvalues\uff0centries">8 Object.keys\uff0cvalues\uff0centries</h2> <ul> <li><code>Object.keys(obj)</code> \u2014\u2014 \u8fd4\u56de\u4e00\u4e2a\u5305\u542b\u8be5\u5bf9\u8c61\u6240\u6709\u7684\u952e\u7684\u6570\u7ec4</li> <li><code>Object.values(obj)</code> \u2014\u2014 \u8fd4\u56de\u4e00\u4e2a\u5305\u542b\u8be5\u5bf9\u8c61\u6240\u6709\u7684\u503c\u7684\u6570\u7ec4</li> <li><code>Object.entries(obj)</code> \u2014\u2014 \u8fd4\u56de\u4e00\u4e2a\u5305\u542b\u8be5\u5bf9\u8c61\u6240\u6709 <code>[key, value]</code> \u952e\u503c\u5bf9\u7684\u6570\u7ec4</li> </ul> <h2 id="9-\u89e3\u6784\u8d4b\u503c">9 \u89e3\u6784\u8d4b\u503c</h2> <h3 id="91-\u6570\u7ec4\u89e3\u6784">9.1 \u6570\u7ec4\u89e3\u6784</h3> <p>\u89e3\u6784\u8d4b\u503c</p> <pre><code class="language-javascript">let arr = [&#39;Ilya&#39;, &#39;Kantor&#39;];\nlet [firstName, surname] = arr;\n\n// \u5ffd\u7565\u67d0\u4e9b\u5143\u7d20\nlet [firstName, , title] = [&#39;Julius&#39;, &#39;Caesar&#39;, &#39;Consul&#39;, &#39;of the Roman Republic&#39;];\nalert(title); // Consul</code></pre> <blockquote> <p>\u7b49\u53f7\u53f3\u4fa7\u53ef\u4ee5\u662f\u4efb\u4f55\u53ef\u8fed\u4ee3\u5bf9\u8c61</p> </blockquote> <pre><code class="language-javascript">// \u6211\u4eec\u53ef\u4ee5\u5c06\u5176\u4e0e\u4efb\u4f55\u53ef\u8fed\u4ee3\u5bf9\u8c61\u4e00\u8d77\u4f7f\u7528\uff0c\u800c\u4e0d\u4ec5\u9650\u4e8e\u6570\u7ec4\nlet [a, b, c] = &#39;abc&#39;; // [&quot;a&quot;, &quot;b&quot;, &quot;c&quot;]\nlet [one, two, three] = new Set([1, 2, 3]);</code></pre> <p>\u4e0e .entries() \u65b9\u6cd5\u8fdb\u884c\u5faa\u73af\u64cd\u4f5c</p> <pre><code class="language-javascript">let user = {\n  name: &#39;John&#39;,\n  age: 30\n};\n\nfor (let [key, value] of Object.entries(user)) {\n  alert(`${key}:${value}`); // name:John, then age:30\n}\n\n// \u904d\u5386 Map\nlet user = new Map();\nuser.set(&#39;name&#39;, &#39;John&#39;);\nuser.set(&#39;age&#39;, &#39;30&#39;);\n\nfor (let [key, value] of user) {\n  alert(`${key}:${value}`); // name:John, then age:30\n}</code></pre> <p>\u5269\u4f59\u7684 <code>...</code></p> <pre><code class="language-javascript">let [name1, name2, ...rest] = [&#39;Julius&#39;, &#39;Caesar&#39;, &#39;Consul&#39;, &#39;of the Roman Republic&#39;];\n// rest \u662f\u6570\u7ec4</code></pre> <p>\u9ed8\u8ba4\u503c</p> <pre><code class="language-javascript">let [name = &#39;Guest&#39;, surname = &#39;Anonymous&#39;] = [&#39;Julius&#39;];\n\nalert(name); // Julius\uff08\u6765\u81ea\u6570\u7ec4\u7684\u503c\uff09\nalert(surname); // Anonymous\uff08\u9ed8\u8ba4\u503c\u88ab\u4f7f\u7528\u4e86</code></pre> <h3 id="92-\u5bf9\u8c61\u89e3\u6784">9.2 \u5bf9\u8c61\u89e3\u6784</h3> <pre><code class="language-javascript">let { height, width, title } = { title: &#39;Menu&#39;, height: 200, width: 100 };\n\n// \u6362\u53d8\u91cf\u540d\nlet options = {\n  title: &#39;Menu&#39;,\n  width: 100,\n  height: 200\n};\nlet { width: w, height: h, title } = options;\n\n// \u9ed8\u8ba4\u503c\uff0c\u5c5e\u6027\u7f3a\u5931\u65f6\u4f7f\u7528\u9ed8\u8ba4\u503c\nlet { width = 100, height = 200, title } = options;\n\n// \u5192\u53f7\u3001\u7b49\u53f7\u7ed3\u5408\nlet { width: w = 100, height: h = 200, title } = options;\n\n// ... \u5269\u4f59\u6a21\u5f0f\nlet { title, ...rest } = options; // rest = \u5b58\u6709\u5269\u4f59\u5c5e\u6027\u7684\u5bf9\u8c61</code></pre> <h3 id="93-\u5d4c\u5957\u89e3\u6784">9.3 \u5d4c\u5957\u89e3\u6784</h3> <pre><code class="language-javascript">let options = {\n  size: {\n    width: 100,\n    height: 200\n  },\n  items: [&#39;Cake&#39;, &#39;Donut&#39;],\n  extra: true\n};\n\nlet {\n  size: {\n    // \u628a size \u8d4b\u503c\u5230\u8fd9\u91cc\n    width,\n    height\n  },\n  items: [item1, item2], // \u628a items \u8d4b\u503c\u5230\u8fd9\u91cc\n  title = &#39;Menu&#39; // \u5728\u5bf9\u8c61\u4e2d\u4e0d\u5b58\u5728\uff08\u4f7f\u7528\u9ed8\u8ba4\u503c\uff09\n} = options;</code></pre> <blockquote> <p>\u6ce8\u610f\uff0csize \u548c items \u6ca1\u6709\u5bf9\u5e94\u7684\u53d8\u91cf\uff0c\u56e0\u4e3a\u6211\u4eec\u53d6\u7684\u662f\u5b83\u4eec\u7684\u5185\u5bb9</p> </blockquote> <h3 id="94-\u51fd\u6570\u53c2\u6570">9.4 \u51fd\u6570\u53c2\u6570</h3> <p>\u548c\u89e3\u6784\u8d4b\u503c\u8bed\u6cd5\u4e00\u6837</p> <pre><code class="language-javascript">let options = {\n  title: &#39;My menu&#39;,\n  items: [&#39;Item1&#39;, &#39;Item2&#39;]\n};\n\nfunction showMenu({\n  title = &#39;Untitled&#39;,\n  width: w = 100, // width goes to w\n  height: h = 200, // height goes to h\n  items: [item1, item2] // items first element goes to item1, second to item2\n}) {\n  alert(`${title} ${w} ${h}`); // My Menu 100 200\n  alert(item1); // Item1\n  alert(item2); // Item2\n}\n\nshowMenu(options);</code></pre> <h2 id="10-\u65e5\u671f\u548c\u65f6\u95f4">10 \u65e5\u671f\u548c\u65f6\u95f4</h2> <h3 id="101-\u521b\u5efa">10.1 \u521b\u5efa</h3> <pre><code class="language-javascript">// new Date() \u521b\u5efa\u4e00\u4e2a\u8868\u793a\u5f53\u524d\u65e5\u671f\u548c\u65f6\u95f4\u7684 Date \u5bf9\u8c61\nlet now = new Date();\n\n// new Date(milliseconds)\nlet Jan01_1970 = new Date(0);\nlet Jan02_1970 = new Date(24 * 3600 * 1000);\n\n// new Date(datestring) \u8be5\u7b97\u6cd5\u4e0e Date.parse \u6240\u4f7f\u7528\u7684\u7b97\u6cd5\u76f8\u540c\nlet date = new Date(&#39;2017-01-26&#39;);\n\n// new Date(year, month, date, hours, minutes, seconds, ms)\nnew Date(2011, 0, 1, 0, 0, 0, 0); // 1 Jan 2011, 00:00:00</code></pre> <blockquote> <p>\u4f20\u5165\u7684\u6574\u6570\u53c2\u6570 <code>milliseconds</code> \u4ee3\u8868\u7684\u662f\u81ea <code>1970-01-01 00:00:00</code> \u4ee5\u6765\u7ecf\u8fc7\u7684\u6beb\u79d2\u6570\uff0c\u8be5\u6574\u6570\u88ab\u79f0\u4e3a\u65f6\u95f4\u6233</p> </blockquote> <blockquote> <p>\u65f6\u95f4\u6233\u53ef\u4ee5\u4e3a\u8d1f\u6570\uff0c\u8868\u793a 1970 \u4e4b\u524d\u7684\u65f6\u95f4</p> </blockquote> <h3 id="102-dateparse">10.2 Date.parse</h3> <pre><code class="language-javascript">let ms = Date.parse(&#39;2012-01-26T13:51:50.417-07:00&#39;);\n\nalert(ms); // 1327611110417  (\u65f6\u95f4\u6233)</code></pre> <p>\u5b57\u7b26\u4e32\u7684\u683c\u5f0f\u5e94\u8be5\u4e3a\uff1a<code>YYYY-MM-DDTHH:mm:ss.sssZ</code>\uff0c\u5176\u4e2d\uff1a</p> <ul> <li><code>YYYY-MM-DD</code> \u2014\u2014 \u65e5\u671f\uff1a\u5e74-\u6708-\u65e5</li> <li>\u5b57\u7b26 &quot;T&quot; \u662f\u4e00\u4e2a\u5206\u9694\u7b26</li> <li><code>HH:mm:ss.sss</code> \u2014\u2014 \u65f6\u95f4\uff1a\u5c0f\u65f6\uff0c\u5206\u949f\uff0c\u79d2\uff0c\u6beb\u79d2</li> <li>\u53ef\u9009\u5b57\u7b26 &#39;Z&#39; \u4e3a <code>+-hh:mm</code> \u683c\u5f0f\u7684\u65f6\u533a\u3002\u5355\u4e2a\u5b57\u7b26 Z \u4ee3\u8868 <code>UTC+0</code> \u65f6\u533a</li> </ul> <h2 id="11-json">11 JSON</h2> <p>JSON \u662f\u8bed\u8a00\u65e0\u5173\u7684\u7eaf\u6570\u636e\u89c4\u8303\uff0c\u56e0\u6b64\u4e00\u4e9b\u7279\u5b9a\u4e8e JavaScript \u7684\u5bf9\u8c61\u5c5e\u6027\u4f1a\u88ab <code>JSON.stringify</code> \u8df3\u8fc7</p> <ul> <li>\u51fd\u6570\u5c5e\u6027\uff08\u65b9\u6cd5\uff09</li> <li>Symbol \u7c7b\u578b\u7684\u5c5e\u6027</li> <li>\u5b58\u50a8 undefined \u7684\u5c5e\u6027</li> </ul> <h3 id="111-stringify">11.1 stringify</h3> <pre><code class="language-javascript">let json = JSON.stringify(value[, replacer, space])</code></pre> <ul> <li><code>replacer</code> \u8981\u7f16\u7801\u7684\u5c5e\u6027\u6570\u7ec4\u6216\u6620\u5c04\u51fd\u6570 <code>function(key, value)</code></li> <li><code>space</code> \u7528\u4e8e\u683c\u5f0f\u5316\u7684\u7a7a\u683c\u6570\u91cf</li> </ul> <pre><code class="language-javascript">let meetup = {\n  title: &#39;Conference&#39;,\n  participants: [{ name: &#39;John&#39; }, { name: &#39;Alice&#39; }],\n  place: room // meetup \u5f15\u7528\u4e86 room\n};\n\nJSON.stringify(meetup, [&#39;title&#39;, &#39;participants&#39;]);\n// {&quot;title&quot;:&quot;Conference&quot;,&quot;participants&quot;:[{},{}]}\n\nJSON.stringify(meetup, function replacer(key, value) {\n  alert(`${key}: ${value}`);\n  return key == &#39;occupiedBy&#39; ? undefined : value;\n});</code></pre> <h3 id="112-tojson">11.2 toJSON</h3> <p>\u5982\u679c\u5bf9\u8c61\u4e0b\u9762\u6709 <code>toJSON</code> \u65b9\u6cd5\uff0c\u4f7f\u7528 <code>JSON.stringify</code> \u65f6\u4f1a\u81ea\u52a8\u8c03\u7528</p> <pre><code class="language-javascript">let room = {\n  number: 23,\n  toJSON() {\n    return this.number;\n  }\n};\n\nJSON.stringify(room); // 23</code></pre> <h3 id="113-parse">11.3 parse</h3> <pre><code class="language-javascript">let value = JSON.parse(str, [reviver]);</code></pre> <ul> <li><code>reviver</code> \u53ef\u9009\u7684\u51fd\u6570 <code>function(key,value)</code>\uff0c\u8be5\u51fd\u6570\u5c06\u4e3a\u6bcf\u4e2a <code>(key, value)</code> \u5bf9\u8c03\u7528\uff0c\u5e76\u53ef\u4ee5\u5bf9\u503c\u8fdb\u884c\u8f6c\u6362</li> </ul> <pre><code class="language-javascript">let str = &#39;{&quot;title&quot;:&quot;Conference&quot;,&quot;date&quot;:&quot;2017-11-30T12:00:00.000Z&quot;}&#39;;\n\nlet meetup = JSON.parse(str, function(key, value) {\n  if (key == &#39;date&#39;) return new Date(value);\n  return value;\n});</code></pre> <h2 id="\u590d\u4e60">\u590d\u4e60</h2> <ul> <li>\u539f\u59cb\u7c7b\u578b <code>\u5bf9\u8c61\u5305\u88c5\u5668</code> <code>String</code>\u3001<code>Number</code>\u3001<code>Boolean</code> \u548c <code>Symbol</code>\uff0c\u8ba9\u539f\u59cb\u7c7b\u578b\u6709\u65b9\u6cd5</li> <li>\u4e0d\u76f4\u63a5\u4f7f\u7528 <code>new Number(10)</code>\uff0c\u8fd9\u4e2a\u7528\u6cd5\u4ec5\u4f9b\u5185\u90e8\u4f7f\u7528\uff0c\u8fd9\u6837\u4f7f\u7528\u4f1a\u6709\u4ec0\u4e48\u95ee\u9898\uff1f</li> <li><code>Number(&#39;10&#39;); String(10); Boolean(0);</code> \u53ef\u7528\u4e8e\u7c7b\u578b\u8f6c\u6362</li> <li>\u6570\u5b57\u7684\u8868\u793a\u65b9\u6cd5\uff1a<code>let billion = 1e9;</code> \u4e8c\u8fdb\u5236\u3001\u516b\u8fdb\u5236\u3001\u5341\u516d\u8fdb\u5236</li> <li>\u7cbe\u5ea6\u95ee\u9898\uff0c\u5982\u4f55\u4fee\u590d\uff0c\u5341\u8fdb\u5236\u8f6c\u4e8c\u8fdb\u5236</li> <li><code>parseInt(&#39;100px&#39;)</code> \u8f93\u51fa\u4ec0\u4e48</li> <li>\u6570\u7ec4\u6027\u80fd</li> <li>\u4ec0\u4e48\u662f\u53ef\u8fed\u4ee3\u5bf9\u8c61\uff1f\u53ef\u8fed\u4ee3\u5bf9\u8c61\u6709\u4ec0\u4e48\u7279\u6027\uff1f\u5199\u4e00\u4e2a\u7b80\u5355\u7684\u53ef\u8fed\u4ee3\u5bf9\u8c61</li> <li><code>Map</code> \u6982\u5ff5\uff0c\u4f7f\u7528\u573a\u666f</li> <li><code>Set</code> \u6982\u5ff5\uff0c\u4f7f\u7528\u573a\u666f</li> <li><code>WeakMap</code>, <code>WeakSet</code> \u7279\u6027\uff0c\u6709\u4ec0\u4e48\u4f5c\u7528</li> <li><code>Object.keys(obj)</code> <code>Object.values(obj)</code> <code>Object.entries(obj)</code></li> <li>\u89e3\u6784\u8d4b\u503c</li> <li><code>JSON.stringify</code> \u4f1a\u8df3\u8fc7\u90a3\u4e9b\u5c5e\u6027\uff1f</li> <li><code>JSON.stringify</code> \u4f1a\u81ea\u52a8\u8c03\u7528 <code>toJSON</code>\uff0c\u5982\u679c\u6709\u7684\u8bdd</li> <li><code>JSON.parse</code> \u7b2c\u4e8c\u4e2a\u53c2\u6570</li> </ul> <pre><code class="language-js">// \u904d\u5386\u5bf9\u8c61\nfor (let [key, value] of Object.entries(user)) {\n  alert(`${key}:${value}`); // name:John, then age:30\n}\n\n// rest \u662f\u6570\u7ec4\nlet [name1, name2, ...rest] = [&#39;Julius&#39;, &#39;Caesar&#39;, &#39;Consul&#39;, &#39;of the Roman Republic&#39;];\n\n// \u9ed8\u8ba4\u503c\nlet [name = &#39;Guest&#39;, surname = &#39;Anonymous&#39;] = [&#39;Julius&#39;];</code></pre> <pre><code class="language-js">// \u5192\u53f7\u3001\u7b49\u53f7\u7ed3\u5408\uff0c\u522b\u540d + \u9ed8\u8ba4\u503c\nlet { width: w = 100, height: h = 200, title } = options;\n\n// \u5d4c\u5957\nlet options = {\n  size: { width: 100, height: 200 },\n  items: [&#39;Cake&#39;, &#39;Donut&#39;],\n  extra: true\n};\n\nlet {\n  size: { width, height },\n  items: [item1, item2], // \u628a items \u8d4b\u503c\u5230\u8fd9\u91cc\n  title = &#39;Menu&#39; // \u5728\u5bf9\u8c61\u4e2d\u4e0d\u5b58\u5728\uff08\u4f7f\u7528\u9ed8\u8ba4\u503c\uff09\n} = options;\n\n// \u6ce8\u610f\uff0csize \u548c items \u6ca1\u6709\u5bf9\u5e94\u7684\u53d8\u91cf\uff0c\u56e0\u4e3a\u6211\u4eec\u53d6\u7684\u662f\u5b83\u4eec\u7684\u5185\u5bb9</code></pre> ';e.exports=t}}]);