(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["docs/js/prototype"],{YhUc:function(o,e,t){var c=t("Rxnk"),r=t("DGZy"),d=t("T4yU"),p=c(r),n=c(d),a='<h1 id="\u539f\u578b\u3001\u7ee7\u627f">\u539f\u578b\u3001\u7ee7\u627f</h1> <h2 id="1-\u539f\u578b\u7ee7\u627f">1 \u539f\u578b\u7ee7\u627f</h2> <p>\u5bf9\u8c61\u6709\u4e00\u4e2a\u7279\u6b8a\u7684\u9690\u85cf\u5c5e\u6027 <code>[[Prototype]]</code>\uff0c\u5b83\u8981\u4e48\u4e3a <code>null</code>\uff0c\u8981\u4e48\u5c31\u662f\u5bf9\u53e6\u4e00\u4e2a\u5bf9\u8c61\u7684\u5f15\u7528\u3002\u8be5\u5bf9\u8c61\u88ab\u79f0\u4e3a\u201c\u539f\u578b\u201d</p> <h3 id="11-__proto__">1.1 __proto__</h3> <blockquote> <p><code>__proto__</code> \u662f <code>[[Prototype]]</code> \u7684\u56e0\u5386\u53f2\u539f\u56e0\u800c\u7559\u4e0b\u6765\u7684 <code>getter/setter</code>\u3002<code>__proto__</code> \u4e0e\u5185\u90e8\u7684 <code>[[Prototype]]</code> \u4e0d\u4e00\u6837\u3002\u73b0\u4ee3\u7f16\u7a0b\u8bed\u8a00\u5efa\u8bae\u4f7f\u7528\u51fd\u6570 <code>Object.getPrototypeOf/Object.setPrototypeOf</code> \u6765\u53d6\u4ee3 <code>__proto__</code> \u53bb <code>get/set</code> \u539f\u578b</p> </blockquote> <pre><code class="language-js">function Person() {}\n\nlet p = new Person();\n\nconsole.log(Person.prototype.constructor === Person); // true\nconsole.log(p.__proto__ === Person.prototype); // true</code></pre> <p><img src="'+p+'" alt=""></p> <pre><code class="language-js">let animal = {\n  eats: true\n};\nlet rabbit = {\n  jumps: true\n};\n\nrabbit.__proto__ = animal; // (*)\n\n// \u73b0\u5728\u8fd9\u4e24\u4e2a\u5c5e\u6027\u6211\u4eec\u90fd\u80fd\u5728 rabbit \u4e2d\u627e\u5230\uff1a\nalert(rabbit.eats); // true (**)\nalert(rabbit.jumps); // true</code></pre> <h3 id="12-this-\u7684\u503c">1.2 this \u7684\u503c</h3> <p><code>this</code> \u4e0d\u53d7\u539f\u578b\u7684\u5f71\u54cd</p> <p>\u65e0\u8bba\u5728\u54ea\u91cc\u627e\u5230\u65b9\u6cd5\uff1a\u5728\u4e00\u4e2a\u5bf9\u8c61\u8fd8\u662f\u5728\u539f\u578b\u4e2d\u3002\u5728\u4e00\u4e2a\u65b9\u6cd5\u8c03\u7528\u4e2d\uff0c<code>this</code> \u59cb\u7ec8\u662f\u70b9\u7b26\u53f7 <code>.</code> \u524d\u9762\u7684\u5bf9\u8c61</p> <pre><code class="language-js">let animal = {\n  walk() {\n    alert(`${this.name} walk`);\n  }\n};\n\nlet rabbit = {\n  name: &#39;rabbit&#39;,\n  __proto__: animal\n};\n\nrabbit.walk(); // rabbit walk</code></pre> <h3 id="13-for-in-\u5faa\u73af">1.3 for in \u5faa\u73af</h3> <p><code>for..in</code> \u5faa\u73af\u4f1a\u8fed\u4ee3\u7ee7\u627f\u7684\u5c5e\u6027</p> <p>\u51e0\u4e4e\u6240\u6709\u5176\u4ed6\u952e/\u503c\u83b7\u53d6\u65b9\u6cd5\uff0c\u4f8b\u5982 <code>Object.keys</code> \u548c <code>Object.values</code> \u7b49\uff0c\u90fd\u4f1a\u5ffd\u7565\u7ee7\u627f\u7684\u5c5e\u6027</p> <h2 id="2-fprototype">2 F.prototype</h2> <blockquote> <p><code>F.prototype</code> \u5c5e\u6027\u4ec5\u5728 <code>new F</code> \u88ab\u8c03\u7528\u65f6\u4f7f\u7528\uff0c\u5b83\u4e3a\u65b0\u5bf9\u8c61\u7684 <code>[[Prototype]]</code> \u8d4b\u503c\u3002\u8fd9\u91cc\u7684 <code>F.prototype</code> \u6307\u7684\u662f <code>F</code> \u7684\u4e00\u4e2a\u540d\u4e3a <code>prototype</code> \u7684\u5e38\u89c4\u5c5e\u6027\u3002\u8fd9\u542c\u8d77\u6765\u4e0e <code>\u539f\u578b</code> \u8fd9\u4e2a\u672f\u8bed\u5f88\u7c7b\u4f3c\uff0c\u4f46\u8fd9\u91cc\u6211\u4eec\u5b9e\u9645\u4e0a\u6307\u7684\u662f\u5177\u6709\u8be5\u540d\u5b57\u7684\u5e38\u89c4\u5c5e\u6027</p> </blockquote> <pre><code class="language-js">function Rabbit(name) {\n  this.name = name;\n}\nRabbit.prototype.eat = true;\n\nlet rabbit = new Rabbit(&#39;White Rabbit&#39;);\nalert(rabbit.eats); // true</code></pre> <p>\u9ed8\u8ba4\u7684 <code>prototype</code> \u662f\u4e00\u4e2a\u53ea\u6709\u5c5e\u6027 <code>constructor</code> \u7684\u5bf9\u8c61\uff0c\u5c5e\u6027 <code>constructor</code> \u6307\u5411\u51fd\u6570\u81ea\u8eab</p> <p>\u4f46\u662f <code>prototype</code> \u53ef\u4ee5\u66f4\u6539\uff1a</p> <pre><code class="language-js">function Rabbit() {}\nRabbit.prototype = {\n  jumps: true\n};\n\nlet rabbit = new Rabbit();\nalert(rabbit.constructor === Rabbit); // false</code></pre> <p>\u4e3a\u4e86\u786e\u4fdd\u6b63\u786e\u7684 <code>constructor</code>\uff0c\u6211\u4eec\u53ef\u4ee5\u9009\u62e9\u6dfb\u52a0/\u5220\u9664\u5c5e\u6027\u5230\u9ed8\u8ba4 <code>prototype</code>\uff0c\u800c\u4e0d\u662f\u5c06\u5176\u6574\u4e2a\u8986\u76d6</p> <pre><code class="language-js">function Rabbit() {}\n\n// \u4e0d\u8981\u5c06 Rabbit.prototype \u6574\u4e2a\u8986\u76d6\n// \u53ef\u4ee5\u5411\u5176\u4e2d\u6dfb\u52a0\u5185\u5bb9\nRabbit.prototype.jumps = true;\n// \u9ed8\u8ba4\u7684 Rabbit.prototype.constructor \u88ab\u4fdd\u7559\u4e86\u4e0b\u6765</code></pre> <h2 id="3-\u539f\u751f\u7684\u539f\u578b">3 \u539f\u751f\u7684\u539f\u578b</h2> <h3 id="31-\u5185\u5efa\u5bf9\u8c61">3.1 \u5185\u5efa\u5bf9\u8c61</h3> <p>\u5185\u5efa\u5bf9\u8c61\uff0c\u50cf <code>Array</code>\u3001<code>Date</code>\u3001<code>Function</code> \u53ca\u5176\u4ed6\uff0c\u90fd\u5728 <code>prototype</code> \u4e0a\u6302\u8f7d\u4e86\u65b9\u6cd5</p> <p>\u6240\u6709\u7684\u5185\u5efa\u539f\u578b\u9876\u7aef\u90fd\u662f <code>Object.prototype</code>\uff0c\u4e00\u5207\u90fd\u4ece\u5bf9\u8c61\u7ee7\u627f\u800c\u6765</p> <p><img src="'+n+'" alt=""></p> <p><strong>\u9a8c\u8bc1\uff1a</strong></p> <pre><code class="language-js">let arr = [1, 2, 3];\n\n// \u5b83\u7ee7\u627f\u81ea Array.prototype\uff1f\nalert(arr.__proto__ === Array.prototype); // true\n\n// \u63a5\u4e0b\u6765\u7ee7\u627f\u81ea Object.prototype\uff1f\nalert(arr.__proto__.__proto__ === Object.prototype); // true\n\n// \u539f\u578b\u94fe\u7684\u9876\u7aef\u4e3a null\u3002\nalert(arr.__proto__.__proto__.__proto__); // null</code></pre> <h3 id="32-\u57fa\u672c\u6570\u636e\u7c7b\u578b">3.2 \u57fa\u672c\u6570\u636e\u7c7b\u578b</h3> <p>\u57fa\u672c\u6570\u636e\u7c7b\u578b\u5e76\u4e0d\u662f\u5bf9\u8c61\u3002\u4f46\u662f\u5982\u679c\u8bd5\u56fe\u8bbf\u95ee\u5b83\u4eec\u7684\u5c5e\u6027\uff0c\u90a3\u4e48\u4e34\u65f6\u5305\u88c5\u5668\u5bf9\u8c61\u5c06\u4f1a\u901a\u8fc7\u5185\u5efa\u7684\u6784\u9020\u5668 <code>String</code>\u3001<code>Number</code> \u548c <code>Boolean</code> \u88ab\u521b\u5efa\u3002\u5b83\u4eec\u63d0\u4f9b\u7ed9\u6211\u4eec\u64cd\u4f5c\u5b57\u7b26\u4e32\u3001\u6570\u5b57\u548c\u5e03\u5c14\u503c\u7684\u65b9\u6cd5\u7136\u540e\u6d88\u5931</p> <blockquote> <p>\u7279\u6b8a\u503c <code>null</code> \u548c <code>undefined</code> \u6bd4\u8f83\u7279\u6b8a\u3002\u5b83\u4eec\u6ca1\u6709\u5bf9\u8c61\u5305\u88c5\u5668\uff0c\u6240\u4ee5\u5b83\u4eec\u6ca1\u6709\u65b9\u6cd5\u548c\u5c5e\u6027\u3002\u5e76\u4e14\u5b83\u4eec\u4e5f\u6ca1\u6709\u76f8\u5e94\u7684\u539f\u578b</p> </blockquote> <h2 id="4-\u539f\u578b\u65b9\u6cd5">4 \u539f\u578b\u65b9\u6cd5</h2> <p><code>__proto__</code> \u88ab\u8ba4\u4e3a\u662f\u8fc7\u65f6\u4e14\u4e0d\u63a8\u8350\u4f7f\u7528\u7684\uff08deprecated\uff09\uff0c<code>__proto__</code> \u662f <code>[[Prototype]]</code> \u7684 <code>getter/setter</code></p> <p><strong>\u73b0\u4ee3\u7684\u65b9\u6cd5\u6709\uff1a</strong></p> <ul> <li><code>Object.create(proto, [descriptors])</code> \u2014\u2014 \u5229\u7528\u7ed9\u5b9a\u7684 <code>proto</code> \u4f5c\u4e3a <code>[[Prototype]]</code> \u548c\u53ef\u9009\u7684\u5c5e\u6027\u63cf\u8ff0\u6765\u521b\u5efa\u4e00\u4e2a\u7a7a\u5bf9\u8c61</li> <li><code>Object.getPrototypeOf(obj)</code> \u2014\u2014 \u8fd4\u56de\u5bf9\u8c61 <code>obj</code> \u7684 <code>[[Prototype]]</code></li> <li><code>Object.setPrototypeOf(obj, proto)</code> \u2014\u2014 \u5c06\u5bf9\u8c61 obj \u7684 [[Prototype]] \u8bbe\u7f6e\u4e3a <code>proto</code></li> </ul> <pre><code class="language-js">let animal = {\n  eats: true\n};\n\n// \u521b\u5efa\u4e00\u4e2a\u4ee5 animal \u4e3a\u539f\u578b\u7684\u65b0\u5bf9\u8c61\nlet rabbit = Object.create(animal);\n\nalert(rabbit.eats); // true\n\nalert(Object.getPrototypeOf(rabbit) === animal); // true\n\nObject.setPrototypeOf(rabbit, {}); // \u5c06 rabbit \u7684\u539f\u578b\u4fee\u6539\u4e3a {}</code></pre> <blockquote> <p>JavaScript \u5f15\u64ce\u5bf9\u539f\u578b\u8fdb\u884c\u4e86\u9ad8\u5ea6\u4f18\u5316\uff0c\u7528 <code>Object.setPrototypeOf</code> \u6216 <code>obj.__proto__=</code> \u201c\u5373\u65f6\u201d\u66f4\u6539\u539f\u578b\u662f\u4e00\u4e2a\u975e\u5e38\u7f13\u6162\u7684\u64cd\u4f5c\uff0c\u56e0\u4e3a\u5b83\u7834\u574f\u4e86\u5bf9\u8c61\u5c5e\u6027\u8bbf\u95ee\u64cd\u4f5c\u7684\u5185\u90e8\u4f18\u5316</p> </blockquote> <h2 id="\u590d\u4e60">\u590d\u4e60</h2> <ul> <li><code>__proto__</code> \u662f\u4ec0\u4e48\uff1f<code>prototype</code> \u662f\u4ec0\u4e48\uff1f</li> <li><code>this</code> \u4e0d\u53d7\u539f\u578b\u7684\u5f71\u54cd\uff0c<code>this</code> \u59cb\u7ec8\u662f\u70b9\u7b26\u53f7 <code>.</code> \u524d\u9762\u7684\u5bf9\u8c61</li> <li><code>for..in</code> \u5faa\u73af\u4f1a\u8fed\u4ee3\u7ee7\u627f\u7684\u5c5e\u6027\u3002\u51e0\u4e4e\u6240\u6709\u5176\u4ed6\u952e/\u503c\u83b7\u53d6\u65b9\u6cd5\uff0c\u4f8b\u5982 <code>Object.keys</code> \u548c <code>Object.values</code> \u7b49\uff0c\u90fd\u4f1a\u5ffd\u7565\u7ee7\u627f\u7684\u5c5e\u6027</li> <li>es5 \u5982\u4f55\u5b9e\u73b0\u7ee7\u627f</li> <li>\u539f\u751f\u7684\u539f\u578b\uff0c\u6240\u6709\u7684\u5185\u5efa\u539f\u578b\u9876\u7aef\u90fd\u662f <code>Object.prototype</code></li> <li><code>Object.create</code> \u521b\u5efa\u5bf9\u8c61\u53ef\u4ee5\u6307\u5b9a\u539f\u578b</li> </ul> ';o.exports=a}}]);